// Dữ liệu
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('1', '01','01', 'Tết dương lịch')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','01', 'Từ ngày 1 đến 15-1-1950, đã diễn ra Đại hội Tổng liên đoàn Lao động Việt Nam lần thứ nhất. Gần 200 đại biểu ưu tú của giai cấp công nhân Việt Nam về dự Đại hội ở chiến khu Việt Bắc. Trong thư gửi Đại hội, Hồ Chủ tịch cǎn dặn: \"Giai cấp công nhân phải là người lãnh đạo... mọi nam nữ công nhân phải cố gắng học hỏi tiến bộ, phải cố gắng xung phong làm kiểu mẫu trong mọi công việc để làm tròn nhiệm vụ vẻ vang của giai cấp mình\". Đại hội đã nghe đồng chí Trường Chinh, Tổng Bí thư Đảng trình bày bản báo cáo: Nhiệm vụ của Công đoàn. Các đại biểu đã kiểm điểm tình hình 3 nǎm tham gia kháng chiến của giai cấp công nhân Việt Nam, đã thảo luận về nhiệm vụ, đường lối của Tổng liên đoàn trong giai đoạn mới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','01', 'Ngày 1-1-1955, nhân dịp đầu nǎm mới, nhân dân Thủ đô chào mừng T.W. Đảng, Chính phủ và Hồ Chủ tịch trở về Thủ đô.\nTrong diễn vǎn đọc trước cuộc mít tinh, Hồ Chủ tịch khẳng định: \"Nam Bắc là một nhà, là anh em ruột thịt, quyết không thể chia cắt được. Chúng ta phải đoàn kết rộng rãi và chặt chẽ từ Bắc đến Nam, phải ủng hộ đồng bào miền Nam đấu tranh giành tự do dân chủ\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','01', 'Ngày 1-1-1960, tại Thủ đô Hà Nội, Hồ Chủ tịch đã ký sắc lệnh công bố Hiến pháp mới. Hiến pháp mới chính thức xác định miền Bắc nước ta đã bước vào thời kỳ quá độ tiến lên CNXH. Hiến pháp mới là cương lĩnh chỉ đạo cuộc đấu tranh Cách mạng và xây dựng đời sống mới của nhân dân ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','01', 'Cǎn cứ vào yêu cầu của thực tiễn cách mạng và theo đề nghị của Đảng bộ miền Nam, T.W Đảng LĐVN đã quyết định Đảng bộ miền Nam lấy tên là \"Đảng nhân dân cách mạng Việt Nam\". Ngày 1-1-1962, Đảng NDCMVN ra tuyên bố và nêu rõ cương lĩnh của mình. Cùng ngày, Đoàn thanh niên NDCM miền Nam được thành lập.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','01', 'Ngày 1-1-1969, nhân dịp nǎm mới Hồ Chủ tịch chúc mừng đồng bào và chiến sĩ cả nước. Và đây cũng là bài thơ chúc Tết cuối cùng vào dịp nǎm mới của Người:\n\"Nǎm qua thắng lợi vẻ vang\nNǎm này tiền tuyến chắc càng thắng to\nVì Độc lập, vì Tự do\nĐánh cho Mỹ cút, đánh cho Nguỵ nhào\nTiến lên chiến sĩ đồng bào\nBắc Nam sum họp xuân nào vui hơn\".\nTrước đó, hàng nǎm vào dịp đầu nǎm mới, Bác Hồ đều làm thơ chúc Tết đồng bào và chiến sĩ cả nước, cũng như bà con Việt kiều ở nước ngoài.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','01', 'Xanđorơ Pêtôphi (Sandor Petofi) nhà thơ - anh hùng dân tộc Hunggari. Ông sinh ngày 1-1-1823.\nThơ ông mang tính trữ tình, ngôn ngữ bình thường của quần chúng, lấy đề tài trong nhân dân và ca ngợi tổ quốc Hunggari. Ông được coi là nhà thơ dân tộc và nhân dân, người chiến sĩ cách mạng trung thành với lý tưởng dân chủ. Tác phẩm nổi tiếng nhất của ông là trường ca Cái búa của địa phương.\nNǎm 1848, ông gia nhập quân đội giải phóng Hunggari. Ông đã hy sinh trong một trận chiến đấu lúc mới 26 tuổi để lại tấm gương yêu nước, yêu dân chủ, một hình ảnh đẹp về người nghệ sĩ sống chết vì tổ quốc, vì nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','02', 'Tháng 2 nǎm 1158, vǎn thần Nguyễn Quốc khuyên Vua Lý Anh Tông nên đặt hòm kín ở triều đình để ai có điều kiện gì cần tấu trình, đề nghị, khiếu nại, tố cáo thì viết giấy bỏ vào. Nhà vua nghe theo và chỉ trong một tháng, đơn, thư, sớ,... đã đầy hòm.\nĐây là một phương thức đầu tiên trong lịch sử nước ta về tiếp nhận ý kiến nhân dân một cách tiện lợi, tiến bộ và có hiệu quả.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','02', 'Ngày 1-2-1961, Thông tấn xã Giải phóng, cơ quan thông tin của Mặt trận dân tộc giải phóng miền Nam được thành lập và truyền đi những bản tin đầu tiên phản ánh phong trào đấu tranh của nhân dân miền Nam chống bọn xâm lược Mỹ và bè lũ tay sai. Thông tấn xã giải phóng đã phát huy tích cực góp phần cho nhân loại tiến bộ toàn thế giới thấy rõ cuộc đấu tranh chính nghĩa của nhân dân ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','02', 'Ngày 1-2-1962, vào hồi 18 giờ 30 phút, Đài phát thanh Giải phóng, tiếng nói của Mặt trận dân tộc giải phóng miền Nam Việt Nam bắt đầu hoạt động. Tại một cǎn cứ trong vùng giải phóng miền Nam, Đài phát thanh Giải phóng đã trở thành vũ khí sắc bén tấn công của Mỹ - Ngụy. Nhân dân miền Nam thêm một lần tin tưởng vào thắng lợi cuối cùng trong cuộc đấu tranh lâu dài và gian khổ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','02', 'Ngày 1-2-1965, Đại đội 13 pháo binh Vĩnh Linh bắn chìm chiếc tàu biệt kích đầu tiên của Mỹ Ngụy, mở đầu bảng vàng lập công, đánh bại cuộc chiến tranh phá hoại miền Bắc bằng hải quân của đế quốc Mỹ và bè lũ tay sai của chúng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','02', 'Nhà thơ Nam Trân, tên thật là Nguyễn Học Sĩ, sinh nǎm 1907, quê ở tỉnh Quảng Nam, qua đời ngày 1-2-1967 tại Hà Nội.\nNǎm 1939, ông cho ra mắt bạn đọc tập thơ đầu tay Huế đẹp và thơ. \nSau cách mạng tháng Tám, ông tham gia kháng chiến tại quê nhà. Nǎm 1959, Nam Trân về công tác tại Viện Vǎn học, phụ trách tiểu ban dịch tập thơ chữ Hán Nhật ký trong tù của Hồ Chí Minh ra tiếng Việt. Ông còn là người chủ trì dịch thơ Đường (nǎm 1962) và là người duyệt cuốn thơ Tống (nǎm 1968).\nVề phương diện dịch thuật, Nam Trân có nhiều đóng góp quý báu. Phần lớn những bản dịch của ông đều được bạn đọc ưa thích.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','02', 'Nguyễn Đình Thạc - nhà vǎn, bút danh Như Phong, sinh ngày 25-10-1917 tại Hà Tây.\nNǎm 1937, ông tham gia hoạt động bí mật, rồi từ nǎm 1942 vận động thành lập Hội vǎn hoá Cứu quốc và được bầu vào Ban chấp hành Hội. Về sau, lần lượt làm các nhiệm vụ: ủy viên Đảng đoàn Hội Vǎn nghệ Việt Nam, ủy viên Ban Biên tập Báo Nhân dân, ủy viên Thường vụ Hội Nhà vǎn Việt Nam, Giám đốc Nhà xuất bản Vǎn học.\nNguyễn Đình Thạc là nhà phê bình và lý luận vǎn học với một ngòi bút sắc nét. Ông mất ngày 1-2-1985, thọ 68 tuổi')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','02', 'Ngày 1-2-1946, Hungaria tuyên bố là nước cộng hoà.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','02', '1-2-1946, Chính trị gia người Na Uy Trygve Lie được bầu là Tổng thư ký đầu tiên của Liên hợp quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','02', 'Ngày 1-2-1958, Nước Cộng hoà A-rập Thống nhất ra đời dựa trên sự hợp nhất của Aicập và Syria.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','02', 'Ngày này năm 1968, Nauru, một hòn đảo nằm ở Thái Bình Dương, giành được độc lập.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','02', 'Ngày 1-2-1971, 18 quốc gia và các tổ chức quốc tế nhóm họp ở Ramsar, Iran đã nhất trí thông qua Hiệp ước Ramsar về Bảo vệ Môi trường Biển. Hiệp ước này đã thúc đẩy sự hợp tác quốc tế về bảo vệ sự cân bằng sinh thái, đặc biệt là ở những vùng đất ẩm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','03', 'Chống lại việc bọn cai ngục bắt tù nhân ǎn \"khô mục\" có dòi, ngày 1-3-1935, 70 tù nhân chính trị bị giam tại nhà tù Côn Đảo đã đấu tranh đòi cải thiện bữa ǎn. Cuộc đấu tranh lan rộng và có 120 tù nhân tuyệt thực đưa ra yêu sách: Cấm ngược đãi, đánh đập - Bớt giờ lao động khổ sai - Phát quần áo - Cải thiện ǎn uống - Điều trị bệnh tật cho tù nhân. Bọn thực dân đã phải nhượng bộ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','03', 'Đầu tháng 3 nǎm 1939, Quân đội phát xít Nhật đánh chiếm quần đảo Hoàng Sa. Đây là phần lãnh thổ đầu tiên của Việt nam bị chủ nghĩa phát xít xâm lược. Đồng thời cũng là dấu hiệu xâm lǎng của phát xít Nhật đối với bán đảo Đông Dương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','03', 'Ngày 1-3-1948 quân ta đã phục kích một đoàn xe địch ở La Ngà (Nam Bộ) diệt 150 tên, phá huỷ 56 xe các loại, thu rất nhiều vũ khí. Trận La Ngà đánh dấu lối đánh táo bạo của quân ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','03', 'Binh chủng Rađa của Quân đội nhân dân Việt Nam là một binh chủng kỹ thuật hiện đại ra đời ngày 1-3-1959. Trong cuộc kháng chiến chống Mỹ, binh chủng Rađa đã góp phần đánh thắng cuộc chiến tranh phá hoại bằng không quân. Cánh sóng Rađa Việt Nam đã thực sự là những con mắt thần cảnh giác, kịp thời và chính xác phát hiện kẻ thù. Trong công cuộc xây dựng đất nước, binh chủng Rađa còn hiệp đồng chặt chẽ, bảo vệ an toàn cho các chuyến bay trong nước và quốc tế. Binh chủng Rađa đã được tuyên dương là Đơn vị Anh hùng lực lượng vũ trang.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','03', 'Ngày 1-3-1960, lần đầu tiên việc điều tra dân số được tiến hành một cách khoa học ở miền Bắc nước ta.\nKết quả điều tra cho biết: dân số miền bắc có 15.916.955 người.\nRiêng Hà Nội có 643.576 người.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','03', 'Nhà bác học Pháp, Hǎngri Béccơren (Antoine Henri Becquerel) đã phát hiện ra Urani vào ngày 1-3-1896. Ông được giải Noben nǎm 1903.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','03', 'Hội nghị nhân dân Đông Dương đã họp tại Phnôm Pênh ngày 1-3-1965. Dự hội nghị có đoàn đại biểu của Mặt trận tổ quốc Việt Nam - Mặt trận dân tộc giải phóng miền Nam Việt Nam - Cộng đồng xã hội bình dân Cǎmpuchia và Neo Lào Hắc Xạt. Nghị quyết chung đã khẳng định tình đoàn kết của nhân dân Đông Dương trong cuộc đấu tranh chống Mỹ xâm lược.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','03', 'Ngày 1-3-1991, Chương trình Kiểm soát Dược phẩm của Liên Hợp quốc, UNDCP, có trụ sở ở Viên chính thức hoạt động')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','03', 'Ngày 1-3-1996 tại Thủ đô Bǎng Cốc (Thái Lan) đã khai mạc cuộc gặp cấp cao Á - Âu (ASEM) lần thứ nhất.\nDự hội nghị gồm: Các nhà lãnh đạo 7 nước ASEAN, Nhật Bản, Trung Quốc, Hàn Quốc cùng các nhà lãnh đạo 15 nước Liên hiệp châu Âu (EU) và chủ tịch Hội đồng châu Âu. Mục đích của cuộc họp nhằm tạo ra một mối liên hệ nǎng động mới và hình thành một sự hợp tác giữa châu Á và châu Âu. Theo mục tiêu hoà bình và hợp tác phát triển.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','03', 'Ngày 1-3-1999, Hiệp ước quốc tế về cấm mìm sát thương được ký kết tại Ottawa, Canada.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','04', 'Ngày 1-4-1922, báo Le Paria (Người cùng khổ) ra số đầu tiên tại Pari. Tờ báo do Hội liên hiệp thuộc địa chủ trương và xuất bản bằng tiếng Pháp, do Nguyễn Ái Quốc làm chủ nhiệm kiêm chủ bút. Là \"Diễn đàn của các dân tộc thuộc địa\" sau đó đổi thành \"Diễn đàn của vô sản thuộc địa\", Báo Le Paria tồn tại cho đến nǎm 1926 - tổng cộng được 38 số, với nội dung chống chủ nghĩa thực dân, kêu gọi đoàn kết các dân tộc thuộc địa. Đặc biệt là có nhiều bài viết đặc sắc của Nguyễn Ái Quốc. Việc sản xuất báo Le Paria là một đòn đánh vào thực dân. Báo đã được bí mật chuyển về Đông Dương và các thuộc địa của Pháp. Như lời kêu gọi của báo trên số đầu tiên: \"Vũ khí chiến đấu với sứ mệnh rõ ràng là giải phóng con người\" - Tờ Le Paria đã làm tròn tôn chỉ và thực sự trở thành bạn của những người cùng khổ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','04', 'Đội danh dự Hà Nội được thành lập ngày 1-4-1945.\nĐây là một đội vũ trang đặc biệt của Đảng trước Cách mạng tháng Tám có nhiệm vụ tuyên truyền xung phong và trừ gian diệt ác.\nĐội đã tổ chức được nhiều cuộc vũ trang tuyên truyền táo bạo ở các trường học, rạp hát, các nhà máy và đã diệt được nhiều tên Việt gian đầu sỏ, gây chấn động dư luận Hà Nội bấy giờ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','04', 'Ngày 1-4-1953, trở thành ngày truyền thống binh chủng pháo cao xạ\nRa đời trong giai đoạn cuối của cuộc kháng chiến chống thực dân Pháp xâm lược, bộ đội pháo cao xạ đã lập nhiều chiến công oanh liệt, góp phần quyết định làm nên chiến thắng Điện Biên Phủ chấn động địa cầu.\nPhát huy truyền thống vẻ vang, trong kháng chiến chống Mỹ cứu nước, binh chủng pháo cao xạ đã chiến đấu dũng cảm mưu trí, lập công xuất sắc, xứng đáng là mũi nhọn trên tuyến \"đất đối không\", nòng cốt trong chiến lược chiến tranh nhân dân, đánh thắng hai cuộc chiến tranh phá hoại không quân và hải quân của đế quốc Mỹ đối với miền Bắc và tham gia giành thắng lợi hoàn toàn cuộc tổng tiến công và nổi dậy mùa xuân 1975, giải phóng miền Nam, thống nhất Tổ quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','04', 'Vào ngày 1-4-1959, Hồ Chủ tịch đã về thǎm các làng cá và bà con ngư dân trên đảo Tuần Châu, Cát Bà, Cát Hải... ở Quảng Ninh và Hải Phòng. Bác đã thǎm già, hỏi trẻ, cǎn dặn cán bộ về tổ chức sản xuất và chǎm lo đời sống của nhân dân vùng biển - Bác nói: \"Biển bạc của ta, do nhân dân ta làm chủ\".\nTừ nǎm 1979, nhân dân, cán bộ, công nhân làm nghề cá đã lấy ngày 1-4 là ngày hội ra quân đánh bắt vụ cá nam và sinh sản các giống cá nuôi.\nNgày 18-3-1995, Thủ tướng Chính phủ đã quyết định lấy ngày 1-4 là ngày truyền thống của ngành thuỷ sản nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','04', 'Giáo sư bác sĩ Đặng Vǎn Ngữ sinh nǎm 1910 tại Huế.\nTrước nǎm 1945, khi đang học tại đại học y khoa Hà Nội, ông được cử sang Nhật Bản nghiên cứu ở Viện vi trùng, ký sinh trùng Tôkiô. Nǎm 1949, ông đã trở về nước tham gia kháng chiến - ông đã nghiên cứu và sản xuất thành công thuốc kháng sinh, nước lọc Pênixilin chế từ giống nấm, cứu chữa cho thương binh bệnh binh.\nÔng đi khảo sát nhiều nơi trên miền Bắc, vạch ra kế hoạch tiêu diệt bệnh sốt rét và thực hiện có kết quả kế hoạch đó.\nNǎm 1965, bác sĩ Đặng Vǎn Ngữ cùng một đoàn thầy thuốc vào miền Nam, ông đã chế thành công loại Vắcxin mới phục vụ cho việc chữa bệnh. Ông mất ngày 1-4-1967 tại chiến trường khu 5 sau một trận bom Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','04', '1-4-1975, sư đoàn 320 A tiến quân theo đường số 7 cùng lực lượng vũ trang tỉnh Phú Yên giải phóng thị xã Tuy Hoà và tỉnh này. Cùng ngày sư đoàn 3 chủ lực quân khu 5 và sư đoàn 968 cơ động trên đường số 19 tiến công địch, đập tan các chốt phòng ngự còn lại của sư đoàn 22 ngụy, phối hợp với lực lượng vũ trang địa phương giải phóng thị xã Quy Nhơn và tỉnh Bình Định.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','04', 'Ngày 1-4-1988, giáo sư Đào Duy Anh đã từ trần. Ông sinh nǎm 1904 ở Thanh Hoá. Là nhà sử học, nhà vǎn hoá lớn của nước ta, ông có nhiều công trình khoa học như: Hán - Việt từ điển (nǎm 1932), Pháp - Việt từ điển (nǎm 1936), Việt Nam vǎn hoá sử cương (nǎm 1936), Lịch sử Việt Nam (nǎm 1955), Từ điển truyện Kiều (nǎm 1974). Học giả Đào Duy Anh thực sự là một nhà bách khoa thư vǎn hoá Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','04', 'Gôgôn - nhà vǎn hiện thực chủ nghĩa lớn của Nga sinh ngày 1-4-1809.\nThuở nhỏ ông sống ở thôn quê, thường xuyên tiếp xúc với những bài ca, điệu vũ dân gian Ukraina và sáng tác vǎn học rất sớm. Những tập truyện ngắn viết về quê hương Ukraina và chùm truyện về Petecxbua là những bức tranh hiện thực, ca ngợi cảnh đẹp thiên nhiên, tinh thần tự do của nhân dân và phê phán cuộc sống ǎn bám, trụy lạc của giai cấp quý tộc. Tác phẩm đặc biệt nổi tiếng của ông là tiểu thuyết Những linh hồn chết.\nÔng mất ngày 4-3-1852. Nhân dân Nga đánh giá cao những đóng góp của ông cho nền vǎn học hiện thực Nga.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','04', 'Ngày 1-4-1946, Singapore trở thành thuộc địa của Anh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','04', 'Ngày này năm 1979, Giáo chủ Ayatollah Khomeini tuyên bố Iran là một nước Cộng hoà Hồi giáo.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','04', '1-4-1989, Lần đầu tiên nội các an ninh trong nước của Israel thông qua nghị quyết 425 của Liên Hợp Quốc kêu gọi quân đội Israel rút khỏi Libǎng, nhưng phía Israel đòi kèm theo điều kiện.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('1', '01','05', 'Ngày Quốc tế Lao động')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','05', 'Trần Phú, Tổng bí thư đầu tiên của Đảng sinh ngày 1-5-1904, quê xã Tùng Anh, huyện Đức Thọ, tỉnh Hà Tĩnh. \nKhi nhỏ học ở Huế, ông là một học sinh giỏi, sáng lập hội Hội tu nghiệp thanh niên. Sau, ông về dạy trường tiểu học Vinh. Nǎm 1925, ông tham gia Hội Phục Việt, rồi tham gia lập Việt Nam Cách mạng Đảng. Có lúc ông sang Lào vận động thành lập các chi bộ. Nǎm 1926 ông sang Trung Quốc liên lạc với Việt Nam Cách mạng đồng chí Hội. Tại đây ông được kết nạp vào Cộng sản đoàn rồi được lãnh tụ Nguyễn Ái Quốc cử về nước hoạt động với tư cách là người Cộng sản. Nǎm 1927 ông được cử sang học tại trường đại học Phương Đông và dự Đại hội lần thứ sáu Quốc tế Cộng sản, sau đó trở về nước hoạt động. \nNǎm 1930 ông được cử vào Ban chấp hành Trung ương Lâm thời Đảng Cộng sản Đông Dương và thảo \"Luận cương chính trị\" đầu tiên của Đảng. Hội nghị lần thứ nhất của Trung ương đã bầu ông làm Tổng bí thư đầu tiên của Đảng. \nÔng là một trong những người góp phần quan trọng vạch ra đường lối tất thắng của Cách mạng Việt Nam. Ông là một chiến sĩ kiên cường đấu tranh thực hiện đường lối đó. Dưới sự lãnh đạo của Trung ương Đảng do ông làm Tổng bí thư; Cách mạng Việt nam đã phát triển sôi nổi mà đỉnh cao lúc bấy giờ là cuộc khởi nghĩa Nghệ Tĩnh. Ông bị đế quốc Pháp bắt và hy sinh ngày 6-9-1931. Sự hy sinh của ông đã để lại cho những lớp Đảng viên sau này một tấm gương sáng chói về khí tiết của người Cộng sản.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','05', 'Nghệ sĩ nhân dân Đinh Ngọc Liên, sinh ngày 1-5-1912, qua đời nǎm 1991. \nÔng là một trong những người đầu tiên có công xây dựng đoàn quân nhạc Việt nam, là nghệ sĩ chỉ huy dàn dựng lâu nǎm giàn nhạc kèn. \nNhạc sĩ Đinh Ngọc Liên còn là tác giả một số hoà tấu kèn nổi tiếng như: Chiến thắng Phủ Thông; Xuân chiến thắng; Hải cảng về ta; Chúng ta có Bác Hồ; Vọng gác tiền tiêu; Hành khúc tang lễ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','05', 'Hàng nǎm, ngày Quốc tế lao động trở thành cơ hội để các tầng lớp nhân dân lao động và các tổ chức Cách mạng biểu dương lực lượng và tuyên truyền Cách mạng dưới nhiều hình thức như mít tinh, biểu tình, giải truyền đơn, bãi công. Nhưng ngày 1-5-1938, lần đầu tiên lễ kỷ niệm được tổ chức công khai, với quy mô chưa từng có ở Hà Nội. \nNhững chiến sĩ cộng sản hoạt động công khai trong nhóm báo \"tin tức\" phối hợp với một số lực lượng tiến bộ, trong đó có chi nhánh Đảng xã hội Pháp, tổ chức cuộc mít tinh công khai này tại khu Đấu Xảo (nay là Cung Vǎn hoá hữu nghị Việt- Xô). Cuộc mít tinh được tiến hành với một ý thức kỷ luật và tổ chức cao, thu hút tới 25.000 người thuộc đủ các ngành, các giới, chia thành 25 đoàn với những huy hiệu biểu tượng riêng cho ngành, giới mình. Cuộc mít tinh đã diễn ra một cách sôi nổi, với các bài diễn thuyết và các khẩu hiệu chiến đấu như \"Đi tới mặt trận Bình dân Đông Dương\", \"Tự do nghiệp đoàn\", \"Chống nạn thất nghiệp\", \"Tự do - Cơm áo - Hoà Bình\" v.v... \nĐây là cuộc biểu dương lực lượng lớn nhất trong thời kỳ Mặt trận dân chủ, đánh dấu uy tín và nghệ thuật tổ chức của những người Cộng sản.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','05', 'Nǎm 1951, Chủ tịch Hồ Chí Minh ra lời kêu gọi anh chị em lao động cả nước nhân ngày Quốc tế Lao động 1-5. Người kêu gọi giai cấp công nhân, nhân dân lao động đẩy mạnh phong trào thi đua ái quốc để chuẩn bị đầy đủ điều kiện đặng chuyển mạnh sang tổng phản công. Trọng tâm thi đua là: \nQuân đội thi đua giết giặc lập công Công nhân thi đua sản xuất lương thực Người trí thức thi đua sáng tạo, phát minh Cán bộ thi đua cần kiệm liêm chính Toàn dân thi đua tích cực tham gia kháng chiến.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','05', 'Từ ngày 1-5 đến 6-5-1952 đã diễn ra Đại hội chiến sỹ thi đua và cán bộ gương mẫu toàn quốc lần thứ nhất. \nTại Đại hội, Hồ Chủ tịch đã nêu rõ mục đích, nội dung, phương pháp và ý nghĩa của việc thi đua yêu nước. Đồng chí Trường Chinh báo cáo \"Phong trào thi đua ái quốc và chủ nghĩa anh hùng mới\", tổng kết phong trào thi đua trong thời gian qua, đề ra phương hướng nhiệm vụ mới. \nĐại hội đã long trọng tuyên dương 7 anh hùng tiêu biểu cho công nông binh và lao động trí óc: Cù Chính Lan, La Vǎn Cầu, Nguyễn Quốc Trị, Nguyễn Thị Chiên, Ngô Gia Khảm, Trần Đại Nghĩa, Hoàng Hanh. \nĐại hội đã nói lên chủ nghĩa anh hùng Cách mạng, anh hùng tập thể của quân và dân ta, có tác dụng mãnh mẽ thúc đẩy phong trào thi đua ái quốc toàn dân tiến lên một bước mới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','05', 'Đơn vị bay đầu tiên của Việt Nam được thành lập vào ngày 1-5-1959 đó là Đoàn 919. \nHơn sáu nǎm sau, vào đêm 15-12-1965, Nguyễn Vǎn Ba và Lê Tiến Phước đều là chiến sĩ lái máy bay của Đoàn 919, đã bắn rơi chiếc C23 chở biệt kích Mỹ - Nguỵ trên vùng trời Tây Bắc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','05', 'Ngày 1-5-1964, Mặt trận Tây Nguyên được thành lập dưới sự lãnh đạo và chỉ huy trực tiếp của Quân uỷ Trung ương và Bộ Quốc phòng. Tổng tư lệnh, đồng thời chịu sự lãnh đạo của khu uỷ 5. Nhiệm vụ của Mặt trận là xây dựng Tây Nguyên thành chiến trường đánh và tiêu diệt địch, có quân chủ lực mạnh làm nòng cốt thúc đẩy lực lượng vũ trang trong ba thứ quân, tiêu diệt tiêu hao nhiều sinh lực địch, thu hút giam chân quân chủ lực địch, tạo điều kiện cho đồng bằng và thành phố nổi dậy. Phối hợp với vũ trang Trị Thiên, Khu 5, Đông Nam Bộ tiến công địch trong thời điểm chiến lược, Tư lệnh Mặt trận là Đại tá Nguyễn Chánh, Chính uỷ Mặt trận là Đại tá Đoàn Khuê.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','05', 'Sáng ngày 1-5-1975, các chiến sĩ Cách mạng bị giam cầm tại nhà tù Côn Đảo đã nổi dậy phá các nhà giam, làm chủ toàn bộ nhà tù Côn Đảo và chuẩn bị đón lực lượng của ta từ trong đất liền ra giải phóng huyện Côn Đảo. \nHàng trǎm chiến sĩ Cách mạng bị giam cầm ở nhà tù Côn Đảo đã được giải phóng. Xoá bỏ chế độ hà khắc của nhà tù; tổ chức lần lượt đưa anh em về đất liền để về với gia đình và đơn vị. \nNhà tù Côn Đảo được Pháp xây dựng từ cuối thế kỷ 19 đến ngày giải phóng là 115 nǎm. Đây là nơi giam cầm nhiều lãnh tụ Cách mạng của Đảng ta, nhưng cũng là nơi đào tạo các cán bộ Cách mạng của Đảng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','05', 'Ngày 1-5-1994, Tổng cục địa Chính Việt nam bắt đầu hoạt động.\nTổng cục này được thành lập trên cơ sở hợp nhất và tổ chức lại Tổng cục Quản lý ruộng đất và Cục Đo đạc và bản đồ Nhà nước. \nTổng cục Địa Chính là cơ quan thuộc Chính phủ, thực hiện chức nǎng Nhà nước về đất đai và đo đạc bản đồ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','05', 'Từ nǎm 1884 tại thành phố công nghiệp Sicagô, Đại hội Liên đoàn lao động Mỹ thông qua nghị quyết nêu rõ rằng: Từ 1-5-1886, ngày lao động của tất cả công nhân sẽ là 8 giờ. Chọn ngày ấy là vì hàng nǎm hợp đồng mới giữa thợ và chủ ký ngày 1-5; và để bọn chủ tư bản biết trước quyết định của công nhân mà không thể kiếm cớ khước từ. \nĐến ngày 1-5-1886 công nhân kắp nơi mang biểu ngữ: Phải thực hiện 8 giờ làm việc, 8 giờ nghỉ ngơi, 8 giờ học tập. Cuộc bãi công đã nổ ra khắp nước Mỹ. Đặc biệt ở Sicagô, cuộc đấu tranh giai cấp diễn ra hết sức ác liệt. Toàn thể công nhân đều xuống đường biểu tình đòi làm việc 8 giờ. Bọn chủ đuổi các công nhân bãi công, thuê người làm ở các phố bên cạnh. Chiều 3-5, khoảng 8.000 công nhân bãi công của nhà máy Mắccóocních đã bị cảnh sát bắn chết 6 người và bắn bị thương 50 người. Sôi sục cǎm phẫn, những người dự mít tinh phản đối sự khủng bố dã man của bọn cầm quyền. Cảnh sát lại bắn chết và làm bị thương hàng trǎm người nữa. Nhiều thủ lĩnh Công đoàn bị bắn và bị án tử hình. Nhưng trước sự đấu tranh mãnh liệt của công nhân, mấy ngày sau bọn cầm quyền buộc phải trả tự do cho những người bị giam giữ và trả lại quyền công dân cho những người bị án tử hình. \nCuộc đấu tranh anh dũng và hy sinh của công nhân Sicagô để giành ngày làm việc 8 giờ có ý nghĩa rất lớn trong lịch sử. Từ đó, ngày 1-5 trở thành ngày đấu tranh của giai cấp công nhân các nước, ngày biểu dương lực lượng, ngày hội của công nhân và lao động thế giới. Ngày đó trở thành ngày quốc tế lao động.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('1', '01','06', 'Ngày Quốc tế Thiếu nhi')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','06', 'Ở Việt Nam, từ sau thắng lợi của cuộc kháng chiến chống thực dân Pháp xâm lược, ngày 1-6 đã được tổ chức hàng nǎm và trở thành ngày hội chǎm sóc và bồi dưỡng thế hệ mǎng non cho Tổ quốc. Nhà nước ta cũng đã ban hành Luật bảo vệ, chǎm sóc và giáo dục trẻ em, coi trách nhiệm vẻ vang ấy là của toàn dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','06', 'Ngày 1-6-1960, Bác Hồ tới dự liên hoan với các cháu nhỏ ở Câu lạc bộ Thiếu niên (nay là Cung thiếu nhi, 38 Lý Thái Tổ, Hà Nội). Bác rất vui, xem các cháu múa hát, trò chuyện và chia quà cho các cháu.\nTừ nǎm 1946 đến nǎm 1965, Bác Hồ đã 7 lần đến dự vui với các cháu thiếu niên, nhi đồng ở Cung thiếu nhi Hà Nội trong dịp Tết Trung thu và ngày quốc tế bảo vệ Thiếu nhi 1-6.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','06', 'Ngày 1-6-1884 Pháp đã ký hiệp ước Pháp - Cǎmpuchia, chính thức đặt ách thống trị của thực dân Pháp lên đất nước này. Bán đảo Đông Dương dần dần trở thành thuộc địa của Pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','06', '11 giờ đêm ngày 9-6-1942, quân phát xít Hítle sục vào làng Liđixe (tiệp Khắc). Chúng bắt dân làng tập trung rồi xả súng giết 92 người, trong đó có 88 trẻ em.\nHai nǎm sau, ngày 10-6-1944, bọn phát xít bao vây Ôrađua (Pháp), dí súng đẩy từng người vào một nhà thờ rồi tưới dầu xǎng thiêu chết trên một nghìn người, trong đó có hơn 200 trẻ em.\nĐể khắc sâu cǎm thù đối với những cuộc thảm sát do bọn phát xít Hítle gây ra ở Liđixe vad Ôrađua trong những ngày tháng 6, và để biểu dương ý chí của các bà mẹ và mọi người quan tâm đến con trẻ, quyết phấn đấu cho tương lai, hoà bình và hạnh phúc của con em mình, tháng 1-1949, Liên đoàn phụ nữ dân chủ thế giới họp ở Mátxcơva (Liên Xô) đã quyết định lấy ngày 1 tháng 6 hàng nǎm làm ngày quốc tế thiếu nhi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','06', 'Nhà giáo dục Henlen Ađam Kelơ (Henlen Adams Keller)sinh ngày 27-6-1880 tại Hoa Kỳ.\nBà bị mù, điếc và câm lúc mới 19 tháng tuổi. Đến 7 tuổi, học ngôn ngữ của người câm điếc, sử dụng máy đánh chữ và nghệ thuật phát âm nǎm 1890. Sau khi tự học, Kenlơ đã tốt nghiệp đại học. Bà viết các tác phẩm: Lịch sử đời tôi (nǎm 1902),')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('4', '01','06', 'Ngày 1-6-1983, tiến sĩ Anna Dêgớc (Anna Seghers) qua đời. Bà vào Đảng Cộng sản khi còn là sinh viên. Bà đã sáng lập ra Viện Hàn lâm Nghệ thuật Đức. Anna Dêgớc là một nhà tiểu thuyết lớn của nước cộng hoà dân chủ Đức và thế giới. Bạn đọc Việt Nam đã quen biết và qua các cuốc tiểu thuyết: \"Cây thập tự thứ bảy\" và \"Những người chết còn trẻ mãi\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','07', 'Nhà thơ Nguyễn Đình Chiểu sinh ngày 1-7-1822, quê ở Tân Khánh, Bình Dương (Gia Định - Thành phố Hồ Chí Minh ngày nay).\nNǎm 1843 ông đỗ tú tài lúc 21 tuổi. Nǎm 1847 ông ra Huế học thêm để chờ khoa thi, bỗng nghe tin mẹ mất. Ông trở về chịu tang, dọc đường về ông bị bệnh rồi mù đôi mắt. Từ ấy ông an phận ở Gia Định dạy học và nhân dân quen gọi ông là Đồ Chiểu. Khi Pháp xâm chiếm, ông lui về Bến Tre dạy học và làm thuốc. Vốn nhiệt tình yêu nước, ông liên hệ mật thiết với các nhóm nghĩa binh của Trương Định. Ông tích cực dùng vǎn chương lòng yêu nước của sĩ phu và nhân dân. Ông làm vǎn tế \"Vong hồn mộ nghĩa\", thơ vǎn thương sót Trương Định, Phan Tòng và xót xa cái chết của Phan Thanh Giản. Ông có ba tác phẩm yêu nước là \"Lục Vân Tiên\", \"Dương Từ Hà Mậu\", \"Ngư tiều y thuật vấn đáp\".\nNguyễn Đình Chiểu không những là một nhà thơ mà còn là một chiến sĩ, một nhà vǎn hoá của nhân dân ta hồi cuối thế kỷ XIX. Ông mất ngày 3-7-1888.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','07', 'Lợi dụng cơ hội một quan chức cao cấp của Nhật gợi ý với bác sĩ Phạm Ngọc Thạch về việc tổ chức một phong trào thanh niên thân Nhật, Xứ uỷ Nam Kỳ chủ trương vận động một phong trào thanh niên công khai để tập hợp lực lượng yêu nước của Nam Bộ và Sài Gòn chuẩn bị cho cuộc tổng khởi nghĩa. Tổ chức ấy lấy tên là \"Thanh niên Tiền phong\". Lễ ra mắt chính thức của \"Thanh niên Tiền phong\" được tổ chức công khai trước Sở thú Sài Gòn vào ngày 1-7-1945.\nTừ Sài Gòn - Chợ Lớn, phong trào \"Thanh niên Tiền phong\" đã làm tan rã các tổ chức thể thao của Pháp. Bằng những công việc như làm vệ sinh, quyên gạo, tiền, tổ chức cứu đói v.v... phong trào đã đi sâu vào đời sống quần chúng để thực hiện chủ trương của Đảng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','07', 'Ngày 1-7-1948, Nha Bình dân học vụ đã phát động một chiến dịch diệt dốt mới.\nChiến dịch này được triển khai đều khắp từ cǎn cứ Việt Bắc đến đồng bằng Liên khu 3, từ Bình Trị Thiên, Liên khu 5 đến các cǎn cứ Đồng Tháp (Nam Bộ).\nTính đến đầu nǎm 1949, hơn 10 triệu người từ 8 tuổi trở lên ở nước ta đã thoát nạn mù chữ. Lúc này số dân nước ta có khoảng 26 triệu người).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','07', 'Tại phiên họp toàn thể lần thứ 119 Hội nghị Pari về Việt Nam ngày 1-7-1971, Đoàn thể đại biểu Chính phủ cách mạng lâm thời cộng hoà miền Nam Việt Nam đưa ra sáng kiến gồm 7 điểm nhằm giải quyết hoà bình về vấn đề Việt Nam. Đó là các vấn đề: Thời hạn rút hết quân Mỹ; Chính quyền ở miền Nam Việt Nam; các lực lượng vũ trang; Hoà bình thống nhất Việt Nam và quan hệ hai miền; Chính sách đối ngoại hoà bình trung lập ở miền Nam Việt Nam; Những thiệt hại do Mỹ gây ra; Tôn trọng và bảo quản quốc tế các hiệp nghị sẽ ký kết.\nSáng kiến hoà bình 7 điểm của Chính phủ cách mạng lâm thời cộng hoà miền Nam Việt Nam được nhân dân trong và thế giới hoan nghênh ủng hộ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','07', 'Đại biểu Đoàn thanh niên nhân dân Cách mạng miền Nam Việt Nam lần thứ hai được tổ chức ngày 1 tháng 7 nǎm 1973, trong vùng giải phóng miền Nam. Có 200 đại biểu về dự đại hội đã nêu bật sự đóng góp to lớn của hàng triệu bạn trẻ trên tiền tuyến lớn anh hùng, và nêu lên nhiệm vụ của Đoàn và phong trào thanh niên miền Nam trong giai đoạn mới.\nĐại hội quyết định từ nay Đoàn mang tên Đoàn Thanh niên nhân dân cách mạng Hồ Chí Minh và đội mang tên Đội Thiếu niên tiền phong Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','07', 'Laibơnít Gốtphríc Vinhem Phôn (Leibniz Gottfried Whilhlm von) sinh ngày 1-7-1646 tại Lepzich (Đức). Nǎm 1666 ông đã viết luận án chuẩn bị thi Tiến sĩ Luật nhưng bị từ chối vì còn quá trẻ để nhận bằng Tiến sĩ. Nhưng nguyên nhân chính là ông biết Luật nhiều hơn số đông giáo sư của trường Đại học Lepzich.\nĐể hiểu biết triết học, ông đi sâu nghiên cứu toán học. Ngay say khi sáng tạo toán học ông đã làm ra máy tính thực hiện được cả bốn phép tính số học, và máy tích phân gần đúng. Công trình lớn nhất của ông là \"Phép tính tích phân và vi phân\". Bằng các phương pháp của phép tính này, ông đã giải quyết hàng loạt vấn đề mà các khoa học khác cùng thời không làm nổi. Ông không chỉ là một nhà toán học lớn. Ông còn là một nhà luật học, nhà thơ, nhà vǎn, nhà sử học. Ông mất ngày 14-11-1716.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','07', 'Ngày 1-7-1994: Sau 27 nǎm sống lưu vong, Yasser Arafat trở về quê hương của người Palestine và đẩy mạnh phong trào thành lập quốc gia độc lập của dân tộc mình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','07', 'Ngày này năm 1997, Hồng Kông thoát khỏi 156 nǎm nằm dưới dự thống trị của Anh khi được chuyển giao chủ quyền cho Trung Quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','08', 'Trần Đường là nhà yêu nước chống Pháp, sinh nǎm 1839, quê ở làng Hiền Lương, Vạn Ninh, tỉnh Khánh Hoà. Ông làm quan cuối triều Tự Đức. Khi thực dân Pháp đánh phá miền Bắc, ông liên hợp với các nhóm Cần Vương, quyết tâm chống giặc Pháp xâm lược. Tôn Thất Thuyết nghe nói về ông, lấy làm khen ngợi, phong ông làm Tổng trấn Vạn Ninh, giữ phía Bắc tỉnh Khánh Hoà. Địa điểm chiến đấu của ông là đèo Dốc Thị phía nam quận lỵ Vạn Ninh. Nǎm 1884 giặc Pháp tiến công mạnh, Vạn Ninh thất thủ, ông rút lui cầm cự được ít lâu thì sa vào tay giặc. Ngày 1-8-1885 ông bị sử chém ở Nha Trang, lúc đó ông mới 46 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','08', 'Báo Việt Nam độc lập, gọi tắt là Việt Lập, do lãnh tụ Nguyễn Ái Quốc sáng lập, ra số đầu tiên vào ngày 1-8-1941. Tờ báo in thạch tại Pắc Pó, mỗi số khoảng 400 bản. Trong suốt thời gian ở Cao Bằng, Người thường xuyên viết bài và trực tiếp chỉ đạo tờ báo.\nKhi Người sang Trung Quốc công tác, tờ Việt Lập được trao lại cho đồng chí Phạm Vǎn Đồng phụ trách. Với mục đích \"cốt làm cho dân ta hết ngu hèn, biết các việc, biết đoàn kết đặng đánh Tây, đánh Nhật, làm cho Việt Nam bình đẳng tự do\", báo Việt Lập thực sự trở thành người tuyên truyền và cổ động cho Mặt trận Việt Minh. Đến ngày Tổng khởi nghĩa tháng Tám nǎm 1945, báo Việt Lập đã ra được 126 số, sau đó tờ báo vẫn tiếp tục được xuất bản và là cơ quan ngôn luận của Cao Bắc Lạng, cuối cùng trở thành tờ báo của tỉnh Cao Bằng.\nBáo Việt Lập được đánh giá là \"Cái hồn của tổ chức cứu quốc... là tờ báo bí mật được quảng đại quần chúng hoan nghênh nhất\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','08', 'Ngày 1-8-1954 những người trí thức yêu nước ở miền Nam, trong đó có luật sư Nguyễn Hữu Thọ, thành lập \"Phong trào bảo vệ hoà bình Sài Gòn - Chợ Lớn\". Mục đích của phong trào được ghi rõ trong bản hiệu triệu: Muốn cho hoà bình ở Đông Dương được củng cố, quyền tự do dân chủ được bảo đảm, nước Việt Nam được thống nhất bằng tuyển cử tự do trong cả nước. \"Phong trào bảo vệ hoà bình Sài Gòn - Chợ Lớn\" là một tổ chức rộng rãi của mọi người Việt Nam yêu nước, tán thành thực hiện các điều khoản của hiệp định Giơnevơ.\nTừ cuối nǎm 1954, Diệm khủng bố, bắt giam 40 sáng lập viên của phong trào. Chúng tra tấn đến chết giáo sư Nguyễn Thị Diệu, bắt giam luật sư Nguyễn Hữu Thọ, trong hai tháng 11 và tháng 12 nǎm 1954, hơn 3000 trí thức, hơn 1000 sinh viên, học sinh và 4000 công thương Sài Gòn đưa kháng nghị đòi Diệm phải trả tự do cho những người trong phong trào hoà bình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','08', 'Ngày 1-8-1955 khánh thành đường sắt liên vận Việt Nam - Trung Quốc. Từ đây hệ thống xe lửa của Việt Nam nối liền với hệ thống đường xe lửa của các nước SNG và Đông Âu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','08', 'Mỏ than cọc 6 (Quảng Ninh) là mỏ khai thác lộ thiên lớn nhất nước ta, được thành lập ngày 1-8-1960. Tại đây có nhiều loại than có chất lượng cao, được khách hàng trong nước và nhiều nước trên thế giới ưa dùng. Mỏ than cọc 6 đã được Nhà nước tặng thưởng một Huân chương Độc lập, 46 Huân chương Lao động, 2839 Huân chương Kháng chiến chống Mỹ, và có hai công nhân được tuyên dương Anh hùng lao động.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','08', 'Hevesy sinh ngày 1-8-1885 tại Buđapét (Hungari). Sau khi tốt nghiệp đại học, ông ra nước ngoài, đến làm việc tại các trung tâm khoa học nổi tiếng ở châu Âu.\nNǎm 1918, ông và Đơcoitơ (Hà Lan) đã phát minh ra nguyên tố 72, gọi theo tên La tinh của Copenhagen là Hafnium. Nǎm 1926, ông nghiên cứu các nguyên tố phổ biến trên trái đất và trong vũ trụ. Nǎm 1927, ông hệ thống hoá các nguyên tố hiếm của đất. Nǎm 1932, ông tìm ra phương pháp pha loãng đồng vị và dùng để xác định hàm lượng chì trong quặng. Với sự phát hiện ra Phốt pho 32, ông dùng nó để xác định sự trao đổi chất trong xương, máu và khối u ác tính. Ông được giải Nôben, là hội viên và viện sĩ nhiều tổ chức khoa học và Viện hàn lâm khoa học thế giới. Ông mất ngày 5-7-1966.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','09', 'Ngày 1-9-1858 Hạm thuyền Pháp và Tây Ban Nha nổ súng tấn công Đà Nẵng.\nTrong cuộc tấn công này, quan quân nhà Nguyễn nhu nhược để thành Đà Nẵng thất thủ. Ngày này được coi là sự kiện mở đầu cho cuộc xâm lược nước ta của thực dân Pháp. Từ đó thực dân Pháp đã đặt ách đô hộ thuộc địa lên đất nước ta gần 100 năm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','09', 'Học giả Trương Vĩnh Ký còn có tên là Pêtruyt Ký, sinh năm 1837, quê ở tỉnh Bến Tre, qua đời ngày 1-9-1898.\nTrương Vĩnh Ký là người đọc và nói giỏi 15 thứ ngoại ngữ, từ ngữ của phương Tây, và biết thông thạo 11 ngoại ngữ phương Đông. Đương thời ông được giới học thuật tư sản liệt vào 18 nhà bác học trên thế giới.\nÔng trước tác nhiều thể loại, còn để lại hơn 100 bộ sách giá trị về nghiên cứu lịch sử, địa lý, ngôn ngữ, biên soạn từ điển, dịch sách ngữ Hán, sưu tầm, phiên âm truyện nôm và tác phẩm cổ Việt Nam, những sáng tác dân gian. Sách của Trương Vĩnh Ký đã có nhiều đóng góp cho một số ngành khoa học đương thời, nhất là khoa học ngôn ngữ và khoa học lịch sử.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','09', '\"Phố Thái\" là biệt danh mà giới nghệ thuật tạo hình nước ta dành cho hoạ sĩ Bùi Xuân Phái. Tranh vẽ về phố phường và cảnh sinh hoạt thường ngày của Hà Nội là bộ phận đáng kể nhất trong sự nghiệp nghệ thuật của ông. Ông sinh ngày 1-9-1921 và qua đời năm 1988.\nTừ năm 1941, khi bức tranh ông vẽ về phố Hàng Phèn được gửi đi triển lãm ở Pháp, cho đến cuối đời, hoạ sĩ vẫn gắn bó với đề tài này.\nNhững ngõ, phố Phất Lộc, Hàng Giầy, Hàng Mã, Hàng Bè... đến Ô Quan Chưởng, Văn Miếu, Hồ Tây... đã đi vào bảo tàng tranh Bùi Xuân Phái để lại hàng nghìn tác phẩm. Tranh của ông được trưng bày ở Bảo tàng Mỹ thuật Việt Nam và nhiều nước trên thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','09', 'Sáng 1-9-1930 đã diễn ra cuộc biểu tình có vũ trang tự vệ của nông dân huyện Thanh Chương, Nghệ An. Trên hai vạn nông dân đã tiến vào huyện lỵ đòi giải quyết các yêu sách như: trả tự do cho những công nhân bị bắt, chia ruộng đất, tự do bãi công, đòi bồi thường... Không giải tán được, quân Pháp đã bắn vào đoàn biểu tình. Quần chúng xông lên mở cửa nhà lao giải phóng tù chính trị, thiêu huỷ huyện đường. Ngày hôm sau chính quyền Xô Viết được thành lập.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','09', 'Lương Khánh Thiện sinh năm 1903 quê ở xã Liêm Chính, Thanh Liêm, Hà Nam. Năm 1925, ông cùng Hoàng Quốc Việt vận động đòi ân xá Phan Bội Châu, bị đuổi học, ông về Nam Định tuyên truyền công nhân tham gia cách mạng. Năm 1929, ông được kết nạp vào Đảng cộng sản Đông Dương. Ngày 1-5-1930 sau cuộc biểu tình, ông bị kết án khổ sai trung thân, đày ra Côn Đảo. Đến 1936 ông được trả tự do, ông tiếp tục hoạt động ở Hà Nội và được cử vào Xứ uỷ Bắc Kỳ, trực tiếp chỉ đạo các cuộc bãi công của công nhân nhà máy xe lửa Gia Lâm. Đầu năm 1940 ông phụ trách Hải Phòng, Kiến An, Quảng Yên, Hòn Gai, Hải Dương, Hưng Yên và trực tiếp làm Bí thư Thành ủy Hải Phòng.\nTháng 11-1940 ông bị bắt. Lần này chúng kết án ông tử hình và bị hành quyết ngày 1-9-1941 tại thị xã Kiến An, lúc đó ông mới 38 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','09', 'Cung văn hoá lao động Hữu nghị Việt Xô được khánh thành ngày 1-9-1985.\nCung được xây dựng trên nền Nhà Đấu Xảo cũ ở Hà Nội và do công nhân lao động Liên Xô giúp. Từ ngày đi vào hoạt động, Cung Văn hoá Lao động Hữu nghị Việt - Xô đã diễn ra nhiều đại hội, hội nghị trong nước và quốc tế. Cung đã thực sự trở thành địa điểm sinh hoạt văn hoá văn nghệ của nhân dân và nhân dân lao động Thủ đô.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','09', 'Ngày 1-9-1939 nước Đức quốc xã tấn công Ba Lan, mở đầu cuộc chiến tranh thế giới lần thứ hai (1939-1945).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','10', 'Nhân sĩ yêu nước Huỳnh Thúc Kháng sinh ngày 1-10-1876 ở huyện Tiên Phước tỉnh Quảng Nam, và từ trần năm 1947 tại tỉnh Quảng Ngãi trên đường đi công tác ở miền Trung.\nCụ đỗ Giải nguyên năm 1900 và đỗ Hoàng giáp năm 1904. Cụ cùng Phan Chu Trinh, Trần Quý Cáp tuyên truyền thuyết Duy tân nên bị thực dân Pháp bắt và đày đi Côn Đảo 13 năm (Từ năm 1908 - 1921). Năm 1920 cụ được bầu làm Viện trưởng Viện Dân biểu Trung Kỳ, nhưng do chống đối khâm sứ Pháp nên từ chức và sáng lập ra tờ báo Tiếng dân ở Huế (Từ năm 1927-1943).\nSau Cách mạng tháng Tám, Chủ tịch Hồ Chí Minh mời cụ Huỳnh Thúc Kháng tham gia Chính phủ Liên hiệp kháng chiến, giữ cương vị Bộ trưởng Bộ Nội vụ. Khi Hồ Chủ tịch đi Pháp năm 1946, cụ được trao quyền Chủ tịch nước.\nCụ còn là một sáng lập viên và là Hội trưởng Hội Liên hiệp quốc dân Việt Nam (tức Liên Việt).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','10', 'Ngày 1-10-1936, lễ đặt mối đường ray cuối cùng nối liền tuyến đường xe lửa xuyên Đông Dương được tổ chức tại ga Hảo Sơn (Tuy Hoà).\nHôm sau, ngày 2-10-1936, chuyến tàu hoả đầu tiên xuất phát từ ga Hà Nội đã tới Sài Gòn, chính thức khai thông đường sắt xuyên Đông Dương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','10', 'Ngày 1-10-1951, theo chủ trương của Trung ương Đảng và Chính phủ, Bộ Giáo dục nước ta đã ra Quyết định chính thức thành lập Khu học xá Trung ương (đặt tại Nam Ninh, Trung Quốc).\nBan Giám đốc Khu học xá gồm có các ông: Võ Thuần Nho, Nguyễn Xiển, Lê Văn Thiêm và Nguyễn Văn Chiển.\nTrong 9 năm tồn tại, nhà trường đã đào tạo được nhiều cán bộ khoa học và giáo viên, sau này đảm nhiệm những chức vụ quan trọng trong các cơ quan của Đảng và Nhà nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','10', 'Ngày 1-10-1962, đoàn 759 (sau này đổi tên là 125) của bộ đội hải quân, tổ chức chuyến tàu đầu tiên chở vũ khí tiếp tế cho miền Nam xuất phát từ bờ biển Hải Phòng. Chiếc tàu này vỏ gỗ, có gắn máy.\nSau 10 ngày vượt biển, tàu tới đích an toàn, cập bến Vàm Lũng (miền Tây Nam Bộ).\nĐến cuối năm 1962, đoàn 759 đã thực hiện được 32 chuyến chở vũ khí chi viện cho các khu 7,8 và 9 ở Nam Bộ.\nThành công của chuyến đi đã mở ra tuyến đường Hồ Chí Minh trên biển.\nĐường Hồ Chí Minh trên biển cùng đường Hồ Chí Minh trên dãy Trường Sơn đã đóng góp phần đặc biệt quan trọng vào chi viện chiến trường Miền Nam đến thắng lợi hoàn toàn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','10', 'Ngày 1-10-1958, tại Xuân Quan, tỉnh Hưng Yên, hệ thống thuỷ nông Bắc - Hưng - Hải được khởi công xây dựng.\nHệ thống bao gồm: Hai cống lớn là Xuân Quan và Nghi Xuyên, lấy nước sông Hồng tưới cho 167 nghìn ha. Cống An Thổ làm nhiệm vụ tiêu nước ra sông Tứ Kỳ và ngăn nước Thuỷ Triều. Cùng với đó là mạng kênh, mương, cầu, cống và trạm bơm nối liền ba cống trên, bao gồm 1900 công trình lớn nhỏ với khối lượng đất phải đào là 63 triệu m3 công trình thuỷ nông Bắc - Hưng - Hải căn bản hoàn thành vào tháng 5-1960.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','10', 'Nhà soạn kịch Trần Hữu Trang, sinh năm 1906 ở Mỹ Tho (tỉnh Tiền Giang) và mất ngày 1-10-1966.\nTrước Cách mạng, ông đi theo các gánh hát, làm thư ký chép vở, rồi dần dần trở thành một soạn giả cải lương nổi tiếng.\nSau năm 1945, ông hoạt động ở nội thành Sài Gòn. Trong kháng chiến chống Mỹ, ông tham gia phong trào hoà bình Sài Gòn - Chợ Lớn. Năm 1960, ông ra vùng giải phóng đảm nhiệm chức Chủ tịch Hội Văn nghệ giải phóng.\nTrần Hữu Trang bắt đầu nổi tiếng từ vở cải lương \"Đời cô Lựu\" với nội dung tố cáo chế độ thực dân trong các đồn điền ở Nam Kỳ có nhiều mâu thuẫn giai cấp và mâu thuẫn dân tộc gay gắt. Ông còn viết các vở: \"Tô Ánh Nguyệt\", \"Tìm hạnh phúc\", \"Mộng hoa Vương\", \"Lan Và Điệp\"...\nVới soạn giả Trần Hữu Trang, cải lương Nam Bộ đã có một bước tiến dài, đạt tới tính văn nghệ thật sự.\nNghệ sĩ Trần Hữu Trang đã được truy tặng Huân chương Thành Đồng và giải thưởng Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','10', 'Nhà giáo Lê Thước sinh năm 1890 tại Hà Tĩnh và qua đời ngày 1-10-1975.\nÔng tốt nghiệp thành chung tại trường Quốc học Huế rồi tốt nghiệp trường Cao đẳng sư phạm Hà Nội. Năm 1918 ông đỗ đầu khoa thi hương cuối cùng.\nTrong nghiên cứu văn học, giáo sư Lê Thước có nhiều công trình nghiên cứu có giá trị về Nguyễn Du, Nguyễn Công Trứ, Tú Xương, Nguyễn Thượng Hiền, v.v...\nLà một nhà yêu nước, ông hăng hái tham gia nhiều công việc của Mặt trận Liên - Việt toàn quốc, và của tỉnh Thanh Hoá.\nHoà bình lập lại (1954), ông Lê Thước tiếp tục làm việc ở Bộ Giáo dục, rồi Bộ Văn hoá, viết nhiều công trình nghiên cứu lịch sử.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','10', 'Ngày 1-10-1949 nước cộng hoà nhân dân Trung Hoa được thành lập do Mao Trạch Đông làm chủ tịch.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','11', 'Hàm Nghi là một ông vua yêu nước, chống giặc Pháp. Tướng Pháp là Đờ Cuôcxi doạ sẽ đem quân bắt nhà vua. Trước tình thế đó, tháng 7 nǎm 1885, đại thần Tôn Thất Thuyết ra lệnh tấn công vào đồn Mang Cá và đồn quân Pháp đóng cạnh toà Khâm xứ ở Huế. Quân ta đánh rất hǎng, song vũ khí quá thô sơ, chỉ huy liên lạc non kém, nên mấy giờ sau, cuộc tấn công bị thất bại. Tôn Thất Thuyết phải đưa vua Hàm Nghi ra Quảng Trị. Tại Tân Sở, nhà vua đã chiếu Cần Vương, kêu gọi nhân dân cả nước chống giặc Pháp.\nĐêm 1-11-1888, vua Hàm Nghi bị bọn phản bội ập đến vây bắt, đem nộp cho Pháp để lĩnh thưởng, lúc đó vua mới 17 tuổi.\nSau đó Pháp đưa Hàm Nghi an trí ở Angiê (nước Angiêri). Hàm Nghi sống ở đó 47 nǎm, giữ vững khí tiết của một ông vua yêu nước. Nǎm 1943, ông mất, thọ 64 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','11', 'Nguyễn Thị Minh Khai sinh ngày 1-11-1910, ở tỉnh Nghệ An.\nNǎm 1927, chị gia nhập Tân Việt cách mạng Đảng, sau đó vào Đảng Cộng sản Việt Nam và được phân công làm việc tại chi nhánh ban Phương Đông của Quốc tế Cộng sản ở Trung Quốc. Tháng 7-1935, chị dự đại hội lần thứ VII Quốc tế Cộng sản. Sau khi về nước, chị tham gia Xứ uỷ Nam Kỳ và là Bí thư Thành uỷ Sài Gòn - Chợ Lớn.\nSau cuộc khởi nghĩa Nam Kỳ nǎm 1940, chị Nguyễn Thị Minh Khai bị địch bắt và sát hại nǎm 1941.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','11', 'Từ cuối nǎm 1963 đến giữa nǎm 1965 do thắng lợi của quân và dân miền Nam và thất bại liên tiếp của địch, nội bộ ngụy quân, ngụy quyền Sài Gòn ngày càng hoang mang, lục đục. Đế quốc Mỹ buộc phải làm nhiều cuộc đảo chính, thay đổi tay sai.\nNgày 1-11-1963, nhóm Dương Vǎn Minh, Trần Vǎn Đôn, Lê Vǎn Kim làm đảo chính, giết anh em Ngô Đình Diệm, Ngô Đình Nhu, lập ngụy quyền theo công thức \"quân sự + dân sự + không đảng phái\". \nNhân cơ hội này, nhân dân ở vùng nông thôn bị kìm kẹp đã nổi dậy phá hàng loạt \"ấp chiến lược\", mở rộng thêm vùng giải phóng. Chỉ trong tháng 11-1963, quân và dân miền Nam đã bức địch rút 354 đồn bốt và phá gần 1.000 \"ấp chiến lược\". Riêng miền núi các tỉnh khu V và ba tỉnh Tây Nguyên, đồng bào đã phá tới 4/5 số \"ấp chiến lược\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','11', 'Ngày 1-11-1964, Khai giảng khoá đầu tiên của trường Đại học Thể dục thể thao. Trường đặt tại Từ Sơn, tỉnh Bắc Ninh, có nhiệm vụ đào tạo huấn luyện các vận động viên, huấn luyện viên các môn thể thao. Nhiều lớp vận động viên đã trưởng thành từ mái trường này và đóng góp vào nền thể dục thể thao nước nhà.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','11', 'Do những thất bại nặng nề ở hai miền nước ta, ngày 1-11-1968, Chính phủ Mỹ phải ngừng không điều kiện việc ném bom đánh phá miền Bắc và phải nói chuyện với đại diện của Mặt trận Dân tộc giải phóng miền Nam tại Hội nghị bốn bên ở Pa-ri, chấm dứt cuộc chiến tranh phá hoại lần thứ nhất bằng không quân của đế quốc Mỹ ở miền Bắc nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','11', 'Ngày 1-11-1981, Nhạc sĩ giáo sư Trần Vǎn Khê, một Việt kiều yêu nước ở Pháp được nhận giải thưởng về âm nhạc nǎm 1981 của Tổ chức giáo dục, khoa học và vǎn hoá của Liên hiệp quốc (UNESCO) tại thành phố Budapét, thủ đô nước Hunggari.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','12', 'Ngày 1-12-1945, tại Pari, thủ đô nước Pháp, phong trào phụ nữ đã họp và tuyên bố thành lập Liên đoàn Phụ nữ dân chủ thế giới. Mục đích của tổ chức này là: Phụ nữ các nước đoàn kết chặt chẽ, không phân biệt già trẻ, tôn giáo, xu hướng chính trị, kiên quyết bảo vệ hoà bình. Liên đoàn điều tra những nhiệm vụ: chống chủ nghĩa phát xít, đòi hoà bình, dân chủ, đảm bảo cho thế hệ con em một tương lai hạnh phúc, thực hiện cho phụ nữ những quyền được ghi trên Hiến chương quốc tế phụ nữ do Liên hiệp quốc đề ra. Liên đoàn quyết định lấy ngày 8-3 hàng nǎm làm ngày phụ nữ đoàn kết đấu tranh để giành những mục tiêu nói trên.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '01','12', 'Ngày 1-12-1958, Mỹ - Diệm đã đầu độc hơn 6000 tù nhân yêu nước ở trại giam Phú Lợi, làm cho 1000 người chết. Một phong trào quần chúng đấu tranh chống địch khủng bố tàn sát nổ ra khắp Sài Gòm và miền Nam, nhất là trong giới công nhân, lao động, kéo dài đến tháng 3-1959.\nỞ Hà Nội và miền Bắc có nhiều cuộc tuần hành, mít tinh lên án tội ác dã man này của Mỹ - Diệm và đòi trả lại tự do cho những người kháng chiến và đồng bào yêu nước ở miền Nam đã bị chúng giam giữ, cầm tù.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','12', 'Nhà Vật lý người Pháp Sáclơ đã tìm ra tất cả những quy tắc của ngành khí cầu hiện đại. Khí cầu do ông nghĩ ra được bơm hiđrô, đã bay lên trời từ vườn Tuyilơri ngày 1-12-1783, và trong giỏ treo có Sáclơ và anh em Rôbe.\nCác khí cầu thể thao hiện nay chỉ khác khí cầu của Sáclơ ở chỗ nó sử dụng khí không cháy (hêli) và ở chất lượng của vỏ bọc khí cầu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','12', 'Gioócdơ Everét (George Everest) sinh nǎm 1799 và mất ngày 1-12-1866.\nÔng là nhà trắc địa nước Anh, làm giám đốc sở trắc địa Ấn Độ, đã lập bản đồ đo đạc cả nước rộng lớn này với độ chính xác cao.\nNǎm 1827, ông gia nhập Hội Khoa học Hoàng gia Anh quốc. Nǎm 1841, ông xác định được toạ độ và độ cao của đỉnh núi cao nhất thế giới mang tên Everét.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','12', 'Liên đoàn phòng cháy, chữa cháy thế giới (viết tắt là FWVFA) được thành lập ngày 1-12-1982, là tổ chức được Liên hiệp quốc công nhận, có trụ sở tại Tôkiô (Nhật Bản).\nNhiệm vụ chủ yếu của Liên đoàn là tổng hợp các nỗ lực nhằm phòng chống các thảm hoạ về cháy nổ, tai nạn trên toàn thế giới, góp phần bảo vệ cuộc sống và tài sản của loài người.\nTính đến nǎm 1995, số lượng thành viên tham gia Liên đoàn phòng cháy chữa cháy thế giới có 145 nước, trong đó có Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '01','12', 'Từ nǎm 1987, tổ chức y tế thế giới quyết định lấy ngày 1-12 hàng nǎm là ngày thế giới phòng chống AIDS do vi rút HIV gây ra.\nTừ tháng 6-1987, Bộ y tế nước ta đã thành lập được uỷ ban AIDS với sự giúp đỡ của chương trình AIDS toàn cầu, thực hiện thành công chương trình ngắn hạn phòng chống AIDS (1989-1990).\nTháng 10-1990, Hội đồng Bộ trưởng (nay là Chính phủ) đã quyết định thành lập Uỷ ban quốc gia phòng chống AIDS với sự tham gia của 14 bộ, ngành và đoàn thể.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','01', 'Ngày 2-1-1963, tại Ấp Bắc, xã Tân Phú, huyện Cai Lậy, nay thuộc tỉnh Tiền Giang, một đơn vị quân giải phóng với số quân ít hơn địch 10 lần, dựa vào xã chiến đấu, đã đập tan cuộc càn quét quy mô lớn của của địch gồm 2.000 tên, có nhiều máy bay, trọng pháo, tàu chiến và xe lội nước yểm trợ.\nTa đã diệt hơn 450 lính Mỹ - Ngụy, bắn rơi 19 máy bay, bắn cháy 3 xe bọc thép, đánh tan chiến thuật, \"trực thǎng vận\" và \"thiết xa vận\" của địch.\nChiến thắng Ấp Bắc đánh dấu sự trưởng thành nhanh chóng về nhiều mặt của lực lượng vũ trang Cách mạng miền Nam, chứng tỏ quân và dân miền Nam hoàn toàn có khả nǎng thắng Mỹ - Ngụy về quân sự trong \"chiến tranh đặc biệt\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','01', 'Ngày 2-1-1985, khánh thành Nhà máy Xi mǎng Bỉm Sơn (Thanh Hoá), nay đổi tên là Công ty Xi mǎng Bỉm Sơn.\nĐây là một công trình lớn do Liên Xô giúp ta khảo sát, thiết kế và xây dựng cùng toàn bộ thiết bị, máy móc và xe thi công. Công suất ban đầu của nhà máy là 1 triệu 200 nghìn tấn xi mǎng một nǎm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','02', 'Đêm 2-2-1937 tại Nhà máy Sợi Nam Định, tên đốc công Forốt vô cớ đánh đập công nhân. Lập tức toàn thể công nhân các phân xưởng dệt, tơ, nhuộm... hãm máy phản đối. Yêu sách của cuộc đấu tranh là: Thi hành luật lao động, bỏ đánh đập, tǎng lương... Lực lượng đấu tranh lên tới 7 nghìn người.\nĐây là cuộc bãi công lớn nhất, dài nhất và giành được kết quả tiêu biểu nhất của công nhân Nhà máy Sợi Nam Định. Thắng lợi của cuộc bãi công đã tǎng thêm khí thế và làm đà cho phong trào cách mạng của công nhân trong tỉnh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','02', 'Ngày 2-2-1973, đã diễn ra phiên họp đầu tiên của các trưởng đoàn đại biểu quân sự bốn bên trong ban liên hợp quân sự Trung ương, họp tại Sài Gòn. Hội nghị đã thảo luận các vấn đề thực hiện ngừng bắn tại chỗ, việc rút quân đội Mỹ và nước ngoài ra khỏi miền Nam Việt Nam, việc tháo gỡ mìn v.v...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','02', 'Nguyễn Đức Cảnh sinh ngày 2-2-1908, quê ở tỉnh Thái Bình.\nThuở nhỏ, ông học tập ở Nam Định, từng tham gia các hoạt động chống Pháp ở địa phương.\nTháng 9-1927 ông sang Trung Quốc và gặp lãnh tụ Nguyễn Ái Quốc và từ đó đi theo Chủ nghĩa Cộng sản. Nǎm 1928 ông được cử vào Kỳ bộ Bắc Kỳ của Việt Nam thanh niên cách mạng đồng chí Hội. Nǎm 1929, ông đứng ra thành lập Tổng công hội Bắc Kỳ. Tháng 2-1930, ông tham gia thành lập Đảng Cộng sản Việt Nam. Cuối nǎm 1930, ông được Trung ương Đảng cử đi công tác ở Trung Kỳ, rồi được bầu vào ban Thường vụ Xứ ủy Trung Kỳ, phụ trách tuyên huấn. Cuối nǎm 1931, Nguyễn Đức Cảnh bị thực dân Pháp bắt tại Vinh.\nTháng 7-1932, ông bị xử chém ở Hải Phòng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','02', 'Ngày 2-2-1950, Việt Nam và Cộng hoà Séc và Slovakia thiết lập quan hệ ngoại giao.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','02', 'Tạ Hiện, tức Tạ Quang Hiện sinh nǎm 1841, quê ở tỉnh Thái Bình.\nÔng đỗ tú tài võ, làm Đốc binh Tuyên Quang, từng cùng với Lưu Vĩnh Phúc dẹp các đám thổ phỉ ở miền núi.\nNǎm 1882, ông được thǎng làm Đô đốc. Đến nǎm 1983, do áp lực của Pháp, triều đình buộc ông bãi binh. Ông không tuân lệnh, tập hợp nghĩa quân nổi dậy kháng chiến, sát cánh cùng Nguyễn Thiện Thuật đánh quân Pháp tại các tỉnh Tuyên Quang, Thái Nguyên.\nNgày 2-2-1887, ông Tạ Hiện bị giặc bắt. Sau đó, ông vượt ngục trốn thoát, tiếp tục lãnh đạo nghĩa quân chiến đấu ở vùng Đông Triều, Móng Cái (thuộc tỉnh Quảng Ninh ngày nay). Sau một thời gian ông mất.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','02', 'Nhà nghiên cứu vǎn học Trần Thanh Mại sinh nǎm 1911, quê ở tỉnh Thừa Thiên Huế, mất ngày 2-2-1965 tại Hà Nội.\nTừ nǎm 1930, ông đã sáng tác vǎn học, cộng tác với nhiều báo chí trong Nam ngoài Bắc. Sau Cách mạng Tháng Tám, ông nhiệt tình tham gia công tác giáo dục. Sau đó, ông về Viện Vǎn học, phụ trách tổ vǎn học Việt Nam cổ cận đại. \nÔng Trần Thanh Mại còn để lại các tác phẩm: \"Trên dòng sông vị\". \"Tuy lý vương\", \"Hàn Mạc Tử\", \"Ngô Vương Quyền\", \"Tú Xương - con người và nhà thơ\", \"Đời vǎn\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','02', 'Ngày 2-2-1912, vua cuối cùng của triều Mãn Thanh là Phổ Nghi tuyên bố thoái vị. Nền quân chủ chuyên chế phong kiến Mãn Thanh sụp đổ sau 268 nǎm tồn tại (1644 - 1912).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','02', 'Sau khi thất bại ở ngay cửa ngõ Mátxcơva, nǎm 1942, Hítle đã mở một chiến dịch quyết định trên mặt trận Xô - Đức. Mục tiêu của Hítle trong chiến dịch này là tiêu diệt lực lượng lớn của Hồng quân. Ngay từ đầu, bộ Tổng tư lệnh tối cao Hồng quân Liên Xô đã nắm được ý đồ của bọn phát xít và chủ động \"giương bẫy\".\nNgày 23-11-1942, lệnh phản công của Hồng quân Liên Xô bắt đầu. Chỉ hơn 1 tháng phản công, ngày 2-2-1943 Hồng quân đã phá tan kế hoạch chiến lược của bọn phát xít Hítle, tiêu diệt 30 sư đoàn tinh nhuệ của địch, bắt làm tù binh 90 nghìn tên lính và 2.500 sĩ quan. Trong đó có nguyên vẹn cả bộ tham mưu mặt trận gồm 1 thống chế và 24 viên tướng Đức.\nChiến thắng Xtalingrat làm nức lòng toàn thể quân dân Xô Viết tiến lên đánh thắng phát xít Đức trên khắp mặt trận, giành thắng lợi hoàn toàn trong cuộc chiến tranh vệ quốc vĩ đại.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','03', 'Ngày 2-3-1926, Học sinh Trường Kỹ nghệ thực hành Huế đã kéo đến toà Khâm sứ Trung kỳ đưa ra 3 yêu sách: Nâng cao chương trình học - Không nhục mạ học sinh - Cải thiện đời sống học sinh. Chính quyền thực dân một mặt hứa hẹn chấp nhận yêu sách, một mặt điều lính đến đàn áp. Các học sinh kiên trì bãi khoá và kéo dài cuộc đấu tranh. Cuối cùng chính quyền thực dân ở đây phải nhượng bộ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','03', 'Ngày 2-3-1943, Bác sĩ Iécxanh qua đời tại Nha Trang. Ông là một nhà vi trùng học nổi tiếng người Pháp gốc Thụy Sĩ. Từ cuối nǎm 1889, ông tới Việt Nam, cuộc đời ông dành hầu hết thời gian và tâm huyết cho các cuộc thám hiểm và nghiên cứu khoa học ở Việt Nam, mà tập trung là Tây Nguyên. Ông đã góp phần phát hiện ra cao nguyên Lang Bian và ra đời thành phố du lịch Đà Lạt. Ông đã có nhiều cống hiến lớn trong ngành vi trùng học, như việc phát hiện ra loại virút dịch hạch. Dưới sự chỉ đạo của ông, hoạt động của viện Paxtơ ở Sài Gòn, Nha Trang được tiến triển. Ông là người chịu trách nhiệm xây dựng trường thuốc ở Hà Nội nǎm 1904, là người lãnh đạo Viện Paxtơ Đông Dương cho tới khi qua đời. Hiện nay, ở Nha Trang, thành phố Hồ Chí Minh và Thủ đô Hà Nội đều có đường phố mang tên ông. Người dân Nha Trang còn lưu giữ nhiều tình cảm và kỷ niệm đẹp về ông.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','03', '8 giờ sáng ngày 2-3-1946 tại Nhà hát lớn thành phố Hà Nội hơn 300 đại biểu đã dự phiên họp đầu tiên của Quốc hội nước Việt Nam Dân chủ Cộng hoà. Một trong những công việc quan trọng của kỳ họp thứ nhất Quốc hội khoá I là việc thành lập Chính phủ liên hiệp kháng chiến. Chính phủ gồm 10 vị bộ trưởng và Bác Hồ được tín nhiệm bầu làm Chủ tịch Chính phủ. Vào lúc 10 gờ 30 phút Chính phủ tuyên thệ (do Chủ tịch Hồ Chí Minh đọc): \"Chúng tôi, Chính phủ kháng chiến nước Việt Nam Dân chủ Cộng hoà, Tối cao cố vấn đoàn và uỷ viên kháng chiến, trước bàn thờ thiêng liêng của Tổ quốc, trước Quốc hội, xin thề cương quyết lãnh đạo nhân dân kháng chiến, thực hiện nền dân chủ Việt Nam, mang lại tự do, hạnh phúc cho dân tộc...\"\nĐúng 11 giờ cùng ngày, phiên họp bế mạc. Trước khi kết thúc, Chủ tịch Hồ Chí Minh đã phát biểu thay mặt Chính phủ và Người đề ra khẩu hiệu sau: Kháng chiến thắng lợi - Kiến quốc thành công - Việt Nam độc lập muôn nǎm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','03', 'Sau khi hiệp định Pari về lập lại hoà bình ở Việt Nam được ký, ngày 2-3-1973, Hội nghị quốc tế về Việt Nam được triệu tập ở Pari. Gồm đại biểu của Liên Xô, Trung Quốc, Anh, Pháp, 4 bên tham gia ký hiệp định và 4 nước trong uỷ ban giám sát quốc tế (Ba Lan, Canađa, Hunggari và Inđônêxia). Tất cả các nước dự Hội nghị đã ký vào bản định ước ghi nhận và bảo đảm hiệp định Pari và các nghị định thư kèm theo được thi hành nghiêm chỉnh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','03', 'Ngày 2-3-1979, Danh mục các thành phần dân tộc Việt Nam được ban hành theo quyết định số 121 - TCTK/PPCA. Danh mục này được xác lập trên cơ sở các đặc điểm sinh hoạt vǎn hoá và ý thức của các dân tộc ở Việt Nam. Hiện nước ta có 54 dân tộc anh em. Mỗi dân tộc có phong tục, tập quán và sắc thái riêng đã góp phần làm rực rỡ vườn hoa dân tộc Việt Nam đủ mầu sắc. Truyền thống Đại đoàn kết các dân tộc ngày nay được tôn trọng và phát huy.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','04', 'Đồng chí Nguyễn Lương Bằng sinh ngày 2-4-1904 tại xã Thanh Tùng, huyện Thanh Miện, tỉnh Hải Dương, trong một gia đình nho giáo thanh bạch. Nǎm 20 tuổi, đồng chí đã tham gia cách mạng, bị thực dân Pháp bắt nhiều lần, 2 lần vượt ngục. Sau Cách mạng Tháng Tám, đồng chí đã giữ nhiều trọng trách: Ủy viên Ban Chấp hành Trung ương Đảng, Trưởng ban kiểm tra Trung ương, Đại sứ Việt Nam đầu tiên tại Liên Xô, Tổng thanh tra của Chính phủ, Phó Chủ tịch nước Cộng hoà XHCN Việt Nam và Quyền Chủ tịch nước CHXHCN Việt Nam. Đồng chí Nguyễn Lương Bằng từ trần nǎm 1979 nhưng để lại một tấm gương sáng về tính mẫu mực và đạo đức Cách mạng. Tình cảm về Anh Cả - đồng chí Sao Đỏ còn in đậm trong tâm trí những người cộng sản Việt Nam.\nỞ Hà Nội có một đường phố mang tên Nguyễn Lương Bằng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','04', 'Huỳnh Vǎn Nghệ sinh ngày 2-4-1914 tại làng Tân Tịch, huyện Tân Uyên, nay thuộc tỉnh Bình Dương, qua đời ngày 5-3-1977 tại thành phố Hồ Chí Minh.\nTừ tuổi thanh niên, ông đã tham gia hoạt động cứu nước - Trong kháng chiến chống Pháp, ông làm phó khu trưởng khu 7 (miền đông Nam Bộ), chỉ huy nhiều trận đánh nổi tiếng với bí danh Tám Ngãi, được phong quân hàm Thiếu tướng.\nSau ngày hoà bình lập lại, ông chuyển ngành, giữ chức Thứ trưởng Bộ Lâm nghiệp.\nThời còn trẻ, Huỳnh Vǎn Nghệ sáng tác nhiều thơ vǎn thể hiện tình yêu nước, nghĩa đồng bào. Ngày nay, nhiều người còn nhớ mấy câu thơ của ông đối với miền Bắc và Thủ đô Hà Nội:\nAi về xứ Bắc, ta theo với\nThǎm lại quê hương giống Lạc Hồng \nTừ thuở mang gươm đi mở nước, \nNgàn nǎm thương nhớ đất Thǎng Long.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','04', 'Với thắng lợi của phong trào bình dân ở bên Pháp, tại Đông Dương, Mặt trận dân chủ ra đời và thực hiện đấu tranh công khai. Ngày 2-4-1938, Báo Tin tức - Cơ quan của Mặt trận dân chủ ra số đầu tiên. Tờ báo do đồng chí Trường Chinh phụ trách ban Tuyên truyền cổ động của TW Đảng Cộng sản Đông Dương đứng ra làm chủ nhiệm. Báo Tin tức đặt trụ sở tại Hà Nội và trở thành trung tâm chỉ đạo phong trào Mặt trận dân chủ ở Bắc Kỳ. Thông qua tờ báo, Đảng Cộng sản Đông Dương tập hợp và chỉ đạo phong trào cả nước cả về tổ chức và tuyên truyền. Báo ra mỗi tuần 2 số với lượng phát hành 7.000 bản/số. Ngày 19-10-1938 Báo Tin tức ngừng ra báo ở số 43. Cũng thời gian này Xứ ủy Bắc Kỳ đã ra báo Đời nay và báo này làm tiếp chức nǎng khi tờ Tin tức ngừng xuất bản.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','04', 'Ngày 2-4-1975, sư đoàn 10 và trung đoàn 25 bộ binh của ta đã tiêu diệt cụm phòng thủ của trung đoàn 40 (thuộc sư đoàn 22 ngụy), giải phóng quận lỵ Khánh Dương, tiêu diệt lữ đoàn 3 (thuộc sư đoàn dù) ở đèo (Phượng Hoàng), mở thông đường xuống Ninh Hoà, rồi theo đường số 21, tiến xuống giải phóng thành phố Nha Trang và quân cảng Cam Ranh. Cùng ngày 2-4-1975 ta còn giải phóng Bình Long.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','04', 'Ngày 2-4-1985, tại thủ đô Phnôm Pênh, Bộ Quốc phòng nước CHND Cam puchia và Hội đồng toàn quốc mặt trận đoàn kết xây dựng và bảo vệ Tổ quốc Campuchia đã tổ chức trọng thể lễ trao tặng Huân chương CHND Campuchia cho binh đoàn 52 và Huân chương Bảo vệ Tổ quốc hạng nhất cho các đoàn 7703, 7706, 9906 quân tình nguyện Việt Nam, nhân dịp các đơn vị này đã hoàn thành xuất sắc nhiệm vụ quốc tế tại Campuchia.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','04', 'Ngày 2-4-1805, Hanxơ Crixtian Anđécxen (Hans Christian Andersen) đã sinh ra ở nước Đan Mạch, và qua đời vào ngày 4-8-1875.\nAnđécxen là nhà vǎn nổi tiếng thế giới về những chuyện kể cho thiếu nhi. Là con một người thợ giầy, từ bé Anđécxen đã ham thích những chuyện cổ dân gian.\nNhững sáng tác của Anđécxen bắt rễ từ đời sống thực tế - ông nói: \"Không có truyện kể nào hay hơn được những điều do chính cuộc sống tạo nên\". Bút pháp Anđécxen vừa trào lộng, vừa trữ tình, vừa lãng mạng, vừa hiện thực.\nTác phẩm của ông thể hiện rõ tính nhân dân sâu sắc.\nỞ Việt Nam, bạn đọc nhỏ tuổi và cả người lớn đều rất thích đọc các truyện của Hanxơ Crixtian Anđécxen, qua tác phẩm \"Truyện cổ Anđécxen\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','04', 'Hồng y Joseph Ratzinger được bầu làm giáo hoàng mới với danh hiệu Benedict XVI sau khi Giáo hoàng John Paul II qua đời ngày 2-4-2005. Sự qua đời của Đức John Paul II - vị giáo hoàng không phải người Italia trong vòng 455 năm qua - đã kết thúc triều đại dài hơi thứ ba (26 năm) trong lịch sử giáo hội Công giáo. Như thể đánh dấu ảnh hưởng to lớn này, hàng triệu người, trong đó có các nguyên thủ các quốc gia lớn trên khắp thế giới đã đến dự lễ tang của Ngài. Đức Hồng y Joseph Ratzinger người Đức đã tiếp tục đường lối của vị tiền nhiệm, trở thành vị giáo hoàng mới và lập tức bỏ qua khoảng thời gian phải chờ đợi thông thường để thúc tiến việc phong thánh cho Đức John Paul II.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','05', 'Trong lịch sử báo chí Việt Nam, tờ báo đầu tiên dành riêng cho phụ nữ và do phụ nữ chủ trương là tờ Nữ giới chung của bà Sương Nguyệt Ánh (Con gái nhà thơ Nguyễn Đình Chiểu) xuất bản tại Sài Gòn nhưng tồn tại không lâu (từ tháng 2 đến tháng 9-1918).\nHơn mười nǎm sau, 2-5-1929 Báo Phụ nữ tân vǎn - Một tờ báo đề cập tới các vấn đề phụ nữ và các vấn đề công tác xã hội - thời sự; ra số đầu tiên. Tờ báo nhanh chóng mở rộng ảnh hưởng và đương thời được coi là tờ báo duy nhất phát hành trên cả 3 kỳ và có đông đảo độc giả. Báo Phụ nữ tân vǎn do bà Nguyễn Đức Nhuận xuất bản tại Sài Gòn. Đánh giá về tờ báo này, tác phẩm Chủ nghĩa Mác và Vǎn hoá Việt Nam đã viết: \"Tờ Phụ nữ tân vǎn có khuynh hướng tiến bộ, đứng trên lập trường tư sản và dân tộc mà đề xướng vấn đề nữ quyền\". Sau Phụ nữ tân vǎn, hầu khắp cả ba kỳ đều có báo dành riêng cho phụ nữ và trẻ em.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','05', 'Từ ngày 2 đến ngày 9-5-1933, toà án Đại hình Sài Gòn đã mở một phiên toà lớn để kết án 120 chiến sĩ cộng sản và nói xấu Đảng ta.\nTên chưởng lý đọc cáo trạng và vu khống những người Cộng sản. Lập tức các đồng chí Ngô Gia Tự, Lê Vǎn Lương, Hà Huy Giáp và nhiều đồng chí đã đứng dậy phản đối, lên án kịch liệt chính sách phản động của đế quốc Pháp. Đuối lý, bọn quan toà ra lệnh cho mật thám, lính gác dùng dùi cui, xông vào đánh đập túi bụi, ngǎn cản không cho các đồng chí nói. Mặc dù không có chứng cứ, bọn quan toà vẫn vu cáo cho những người Cộng sản là \"ǎn cướp, giết người, làm rối loạn trị an\" và tuyên án 8 người tử hình (trong đó có các đồng chí Phạm Hùng, Lê Vǎn Lương...), 19 người bị khổ sai và tù đầy trung thân (trong đó có các đồng chí Ngô Gia Tự, Nguyễn Chí Diểu, Hà Huy Giáp, chị Sáu nhỏ...), 79 người từ 5 đến 20 nǎm, cộng là 970 nǎm tù.\nVừa tuyên án xong, bọn quan toà đã vội vã rút ngay vì chúng hoảng sợ trước sự phản đối mạnh mẽ của các chiến sĩ ta.\nVụ án trấn động dư luận trong nước và dư luận Pháp, sau này được nhà vǎn tiến bộ người Pháp là Viôlét viết trong cuốn \"Đông dương cấp cứu\" đã gọi là \"Vụ án 121\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','05', 'Ngày 2-5-1946, Chính phủ đã quyết định chia ngoại thành Hà Nội ra làm 5 khu: Lãng Bạc, Đại La, Đống Đa. Đề Thám và Mê Linh.\nTiếp đó ngày 14-5-1946, Chính phủ lại có quyết định chia nội thành Hà Nội ra làm 17 khu, gồm có: Trúc Bạch, Đồng Xuân, Thǎng Long, Đông Thành, Đông Kinh Nghĩa Thục, Hoàn Kiếm, Vǎn Miếu, Quán Sứ, Đại Học, Bảy Mẫu, Chợ Hôm, Lò Đúc, Hồng Hà, Long Biên, Đồng Nhân, Vạn Thái và Bạch Mai.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','05', '397 công nhân Cuba trong đội xây dựng mang tên Hồ Chủ tịch do Đảng Cộng sản và Chính phủ Cuba cử sang giúp nhân dân Việt Nam hàn gắn vết thương chiến tranh, đã đến Hà Nội ngày 2-5-1974. Toàn đội Hồ Chí Minh chia làm 5 phân đội mang tên Nguyễn Vǎn Trỗi, Lê Thị Hồng Gấm, Cù Chính Lan, Nguyễn Viết Xuân và Đại hội công đoàn Cuba lần thứ 13. 5 phân độ này đã xây dựng cho nhân dân ta đường sá, bệnh viện, trại chǎn nuôi, khách sạn ở Thủ đô Hà Nội và các tỉnh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','05', 'Nhà vǎn Nguyên Hồng qua đời vào ngày 2-5-1982 ở Nhã Nam, huyện Yên Thế, tỉnh Bắc Giang). Ông sinh ngày 5-11-1918 tại Nam Định.\nNguyên Hồng vào đời sớm, vốn học vấn ở nhà trường ít ỏi, trí tuệ và tài nǎng của ông chủ yếu phát triển trong trường \"Đại học của cuộc sống thực tế\". Ông có niềm tin không gì lay chuyển được ở phía tích cực, phía ánh sáng của tâm hồn con người.\nTác phẩm chính của Nguyên Hồng là các tiểu thuyết: Bỉ Vỏ (1937), Những ngày thơ ấu (1938), Cửa biển (1961-1976).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','05', 'Người đầu tiên trong số những danh hoạ tạo nên nền nghệ thuật tạo hình kỳ diệu của thời phục hưng Italia là Lêôna đờ Vanhxi. Ông tiêu biểu hơn cả cho thời đại đặc biệt này ở tài nǎng đa dạng, nhất là điêu khắc và kiến trúc, ông còn là một nhà thực nghiệm khoa học, nhà toán học kiêm kỹ sư cơ khí. Những thí nghiệm của ông đóng góp phần không nhỏ trong sự phát triển khoa học kỹ thuật của châu Âu. \nLêôna đờ Vanhxi sinh nǎm 1452. Cuộc đời ông có nhiều biến động thǎng trầm. Nǎm 14 tuổi đã tham gia nghiệp đoàn thợ vẽ. Ngay từ những bức vẽ đầu tiên Vanhxi đã bộc lộ tài nǎng. Tranh của ông gần với hiện thực. Từ nǎm 29 tuổi ông chuyển sáng tác. Nổi tiếng là bức hoạ Đức mẹ trong hang, Bữa ǎn cuối cùng và đặc biệt là bức La Giôcôngdơ được mệnh danh là \"nụ cười của các thời đại\". Bức hoạ đã mở đầu cho nghệ thuật chân dung tâm lý trên thế giới sau này. Những nǎm cuối đời, vì những thí nghiệm về khoa học tự nhiên của mình, ông bị nghi là kẻ chống lại tôn giáo. Ngoài những tác phẩm hội hoạ, Lêôna đờ Vanhxi để lại cho hậu thế một công trình lý luận vô giá, đó là tập \"Ghi chép về hội hoạ\" do học trò ông tập hợp lại và xuất bản.\nLêôna đờ Vanhxi mất vào ngày 2-5-1519 tại Pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','05', 'Sáng ngày 2-5-1945, Bộ chỉ huy quân Đức trong thành phố Beclin đầu hàng. Địch hạ vũ khí, trận công phá Beclin kết thúc bằng chiến thắng vinh quang của Hồng quân Liên xô.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','05', 'Toà án Quốc tế xét xử tội ác chiến tranh của đế quốc Mỹ ở Việt Nam đã mở phiên toà chính thức đầu tiên tại Xtốckhôm, thủ đô Thụy Điển, ngày 2-5-1967.\nTrước đó, theo sáng kiến của Huân tước Béctơrǎng Rútxen, ngày 15-11-1966, Toà án quốc tế xét xử tội ác chiến tranh của Mỹ ở Việt Nam được thành lập tại Luân Đôn, Thủ đô nước Anh.\nBéctơrǎng Rútxen là nhà triết học và là chiến sĩ hoà bình nổi tiếng người Anh, đã đấu tranh không mệt mỏi chống cuộc chiến tranh xâm lược của đế quốc Mỹ ở Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','05', 'Vào tháng 5-1981, lần đầu tiên các thầy thuốc đã báo động về một bệnh suy giảm miễn dịch, gọi tắt là AIDS, là mối đe doạ lớn đối với mọi người.\nĐến tháng 1-1988, tuyên ngôn Luân Đôn nhấn mạnh:\n\"Trong lúc chưa có vắcxin hay loại thuốc nào chữa được bệnh AIDS, thì loài người có thể phòng chống sự lan truyền của virut HIV bằng sự ǎn ở có hiểu biết và có trách nhiệm\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','06', 'Lê Quý Đôn là nhà bác học lớn Việt Nam thời hậu Lê. Ông sinh 2-8-1726 ở Diên Hà, Tiên Hưng, Sơn Nam, Thái Bình.\nXuất thân trong một gia đình khoa bảng lâu đời, từ nhỏ ông đã nổi tiếng thông minh, trí nhớ phi thường, được xem là \"thần đồng\". Về triết học, tư tưởng của ông đã có nhân tố duy vật. Về chính trị, ông luôn thể hiện tinh thần độc lập tự chủ, tin tưởng ở khả nǎng cải tạo xã hội và cải tạo thiên nhiên của con người. Để cải tạo xã hội và thiên nhiên, ông chủ trương phải \"biết nhiều, nghe rộng mới làm nên sự nghiệp\".\nLê Quý Đôn am hiểu hầu hết các tri thức về khoa học mà người Việt Nam thế kỷ XVIII có được. Ông hiểu rộng và viết nhiều về triết học, sử học, vǎn học, luật học, viết cả về nông nghiệp (nhất là về cây lúa) và các ngành nghề khác. Ông đã để lại một số tác phẩm có tính chất bách khoa như: \"Vân đài loại ngữ\". Đây là bộ sách tổng hợp, hệ thống hoá tri thức về khoa học, nghệ thuật, triết lý, địa lý, lý luận vǎn học, giáo dục học. Tác phẩm \"Phủ biên tạp lục\" là bộ sách bách khoa thư địa phương. Tác phẩm \"Kiến vǎn tiểu lục\" là bộ bách khoa về Việt Nam từ đất nước, lịch sử đến con người.\nÔng mất ngày 2-6-1784, thọ 58 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','06', 'Nguyễn Huy Oánh, nhà vǎn, nhà thơ đời Lê Hiển Tông. Ông sinh ngày 4-11-1713 ở trường Lưu, Lai Thạch, La Sơn, Hà Tĩnh.\nNǎm 1748 ông đỗ Thám hoa, làm đến Tả thị lạng Bộ Lại và thǎng Đô ngự sử. Ông từng được cử đi sứ, làm đến Thượng thư Bộ Hộ. Anh ông là Nguyễn Quýnh, cũng nổi tiếng vǎn thơ. Con ông là Nguyễn Huy Tự, tác giả truyện thơ \"Hoa tiên\". Cháu ông là Nguyễn Huy Hổ, tác giả truyện thơ \"Mai Đình Mộng Ký\". Các tác phẩm chính của Nguyễn Huy Oánh là: \"Phụng sử yên kinh tập\", \"Bắc dư tập lãm\", \"Sơ học chỉ nam\", \"Phụng sự yên đài tổng ca\", \"Cổ lễ nhạc chương thi vǎn tập\".\nÔng mất ngày 2-6-1789, thọ 76 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','06', 'Ngày 2-6-1897, Toàn quyền Đông Dương lúc bấy giờ là Pôn Dume đa ra nghị định về chính sách thuế thân đối với người dân ở Bắc Kỳ.\nNội dung của chính sách như sau: thuế thân đánh vào người từ 18 tuổi đến 60 tuổi. Tất cả những ai trong diện đóng thuế thân đều phải có thẻ thuế thân. Trên thẻ có chữ ký hoặc điểm chỉ, có dấu triện của Lý trưởng. Mỗi nǎm thay thẻ một lần, màu sắc thẻ cũng phải thay đổi hàng nǎm. Đi đâu cũng phải mang thẻ. Trường hợp không mang thẻ mà bị cảnh sát bắt giữ thì phải nộp tiền để lấy thẻ mới. Nếu dùng thẻ của người khác phải nộp phạt. Người cho mượn thẻ cũng bị phạt.\nVề thực chất, thuế thân là môt thứ thuế coi con người là một loại hàng hoá, đánh vào đông đảo tầng lớp bình dân nghèo khó.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','06', 'Êvarit Galoa (Évariste Galois) là một nhà toán học lừng danh, không chỉ vì một cuộc đời kỳ dị mà chính là do những ý kiến và công trình sâu sắc của ông về lý thuyết phương trình đại số. Những công trình này nay đã có một giá trị rất lớn cho ngành toán học nhân loại.\nGaloa sinh ngày 26-10-1811 gần thủ đô Pari. Ngay từ thuở học sinh, ông đã rất ham mê môn toán học. Nǎm 1829 ông có công trình đầu tiên về \"Lý thuyết chương trình\". Nǎm 1831 ông bị bắt vì lý do cầm đầu một nhóm biểu tình. Trong khi bị giam, ông nghiên cứu về \"Tích phân của hàm đại số\", và đề ra \"Lý thuyết sự mập mờ\".\nGaloa mất khi 20 tuổi, ngày 2-6-1832. Những công trình toán học của ông mở đầu cho một phương hướng mới trong ngành toán học. Vì thế tên tuổi của ông được ghép vào những khái niệm toán, như \"Tập hợp Galoa\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','07', 'Ngày 2-7-1940 Nhật đơn phương đưa nhiều đơn vị giám sát tại các của khẩu Móng Cái, Lạng Sơn, Cao Bằng, Hà Giang và Hải Phòng. Đây là những lực lượng vũ trang đầu tiên của Nhật Bản đặt chân lên Đông Dương tạo ra tiền đề cao cho sự can thiệp và chiếm đóng của phát xít Nhật.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','07', 'Trong hồi ức của mình, đại tướng Võ Nguyên Giáp kể lại \"Sau khi được giao là tổng chỉ huy quân đội Quốc gia, một hôm Hồ Chủ tịch bảo tôi: \"Sẽ có một thanh niên về làm tham mưu\". Người thanh niên ấy là đồng chí Hoàng Vǎn Thái\".\nĐại tướng Hoàng Vǎn Thái quê tại huyện Tiền Hải, tỉnh Thái Bình. Ông tham gia cách mạng từ hồi còn trẻ và được học tại trường Quân sự Hoàng Phố (Trung Quốc). Sau Cách mạng tháng Tám ông được Bác Hồ cử làm Tổng tham mưu trưởng quân đội quốc gia. Nǎm 1948 ông được phong quân hàm Thiếu tướng. Cuộc đời binh nghiệp của ông gắn với các chiến dịch trong kháng chiến chống Pháp và chống Mỹ, lúc làm lãnh đạo cơ quan tham mưu, lúc là chỉ huy chiến đấu. Ở cương vị nào ông cũng đều hoàn thành xuất sắc nhiệm vụ và thực sự là người cầm quân tài giỏi. Đánh giá khả nǎng của ông về công tác tham mưu, giới quân sự xếp ông vào hàng ngũ các danh tướng xuất sắc của Quân đội nhân dân Việt Nam. Ông mất ngày 2-7-1948.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','07', 'Ngày 2-7-1965, 10 vạn nhân dân ở quận Bình Sơn, Sơn Tịnh, Mộ Đức, Đức Phổ tỉnh Quảng Ngãi biểu tình kéo đến trụ sở quân đội Mỹ và tay sai đòi chấm dứt ném bom, chấm dứt khủng bố. Bọn địch dùng súng bắn vào đoàn biểu tình làm chết và bị thương 11 người.\nĐây là cuộc biểu tình lớn nhất và quyết liệt nhất của nhân dân Quảng Ngãi từ trước đến 1965.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','07', 'Nguyễn Thái Bình sinh nǎm 1948 ở tỉnh Long An. Do thông minh, học giỏi, nǎm 1966, sau khi đỗ tú tài, anh được sang học tập ở Mỹ.\nTrong thời gian ở Mỹ, anh đã gửi cho Tổng thống Mỹ Nichxơn, vạch trần những luận điệu hoà bình giả dối, xảo trá, tố cáo tội ác dã man của Mỹ xâm lược Việt Nam. Anh tham gia các cuộc biểu tình ở Mỹ chống chiến tranh ở Việt Nam, viết báo, làm thơ cổ vũ những người Việt Nam sống trên đất Mỹ hướng về Tổ quốc, thuyết phục những người Mỹ yêu chuộng hoà bình và công lý.\nĐầu tháng 2-1972, sau khi cùng các bạn học kéo đến tổng lãnh sự toán của Ngụy quyền miền Nam ở Xanphraxítcô phản đối sự đàn áp chính trị ở miền Nam, đòi trả lại tự do cho những người trong phong trào hoà bình ở Sài Gòn, anh và 6 sinh viên khác bị chính quyền Mỹ trục xuất về nước.\nNgày 2-7-1972, khi máy bay vừa hạ cánh xuống sân bay Tân Sơn Nhất thì chúng dùng súng bắn chết Nguyễn Thái Bình. Cái chết của anh đã làm trấn động dư luận và thổi bùng lên ngọn lửa đấu tranh của học sinh sinh viên miền Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','07', 'Ngày 24-6-1976 tại hội trường Ba Đình lịch sử, kỳ họp đầu tiên của Quốc hội nước Việt Nam thống nhất khai mạc trọng thể. Kỳ họp lịch sử này của Quốc hội có một ý nghĩa rất to lớn là: Ngày 2-7-1976, Quốc hội đã quyết định đặt tên nước là Cộng hoà xã hội chủ nghĩa Việt Nam. Sự ra đời của nước Cộng hoà xã hội chủ nghĩa Việt Nam là một sự kiện đánh dấu bước ngoặt lịch sử của Cách mạng và đời sống xã hội nước ta. Giai đoạn Cách mạng dân tộc dân chủ nhân dân dưới chính thể dân chủ cộng hoà đã kết thúc vẻ vang, giai đoạn Cách mạng xã hội chủ nghĩa trong cả nước dưới chính thể Cộng hoà xã hội chủ nghĩa bắt đầu. Cả nước ta làm nhiệm vụ chiến lược Cách mạng xã hội chủ nghĩa dưới sự lãnh đạo của Đảng cộng sản Việt Nam và sự quản lý của một nhà nước chung: Nhà nước xã hội chủ nghĩa.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','07', 'Ơnít Milơ Hêminhây (Ernest Miller Hemingway) là nhà vǎn Mỹ nổi tiếng. Ông được giải thưởng Nôben vǎn học nǎm 1945.\nLà một bác sĩ, ông chỉ học hết trung học rồi đi viết báo. Đại chiến lần thứ nhất bùng nổ, ông tự nguyện tham gia với tư cách là một người cứu thương, và chính ông lại bị thương ở Italia. Sau chiến tranh, ông làm báo viết sách và sống nhiều nǎm ở châu Âu.\nSáng tác của ông nhiều thể loại: Tiểu thuyết, truyện ngắn, kịch và được viết với một phong cách độc đáo. Những tác phẩm tiêu biểu của ông \"Giã từ vũ khí\", (1929)\"Những ngọn đồi xanh châu Phi\", (1935) \"Chết vào buổi chiều\", (1932 \"Chuộng nguyện hồn ai\" (1940) rồi \"Ông già và biển cả\" v.v... Những nǎm cuối đời ông sống ở Cuba. Nǎm 1961, trong một chuyến trở về Mỹ chữa bệnh ông đã dùng súng sǎn tự sát ở nhà riêng vào ngày 2-7')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','08', 'Đêm mồng 2 rạng ngày 3-8-1909, đồng bào Mường Hoà Bình, dưới sự chỉ huy của thủ lĩnh Kiêm, đã nổi dậy tấn công tỉnh lỵ Hoà Bình, giết lính, phá đồn, phá kho bạc, phá ty thương chính, giải phóng 40 tù nhân, thu 150 khẩu súng trường, 35 nghìn viên đạn và bắt đầu công khai vũ trang tổ chức kháng chiến chống Pháp suốt 5 tháng.\nThủ lĩnh Kiêm (còn gọi là Tổng Kiêm) là xã trưởng xã Mông Hoá. Ông là một trong số thủ lĩnh nghĩa quân hoạt động ở vùng sông Đà, dưới sự chỉ huy của Đốc Ngữ. Thực dân Pháp phản công. Song, nhân dân dân tộc Mường vẫn đoàn kết ủng hộ nghĩa quân. Ngày 30-12-1909, Tổng Kiêm hy sinh trong một trận phục kích của giặc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','08', 'Từ ngày 2 đến 13-8-1952 đã diễn ra Hội nghị Tổ chức toàn quân lần thứ nhất, nhằm tǎng cường công tác chi bộ Đảng lao động Việt Nam trong bộ đội chủ lực.\nTừ Hội nghị này, công tác chi bộ trong quân đội ngày càng có nề nếp, chất lượng đảng viên ngày càng được nâng cao, chi bộ Đảng thực sự là tế bào lãnh đạo của Đảng ở cơ sở.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','08', 'Ngày 2-8-1964, khu trục hạm \"Ma đốc\" của Mỹ trắng trợn xâm phạm vùng biển miền Bắc, khu vực giữa Hòn Mê và Lạch Trường (Thanh Hoá). Trận đầu tiên mặt đối mặt với chiến hạm Mỹ, bộ đội hải quân nhân dân ta đã đánh đuổi chiến khu trục này, buộc nó phải tháo chạy ra khỏi vùng biển nước Việt Nam dân chủ cộng hoà. Sau đó máy bay Mỹ đã cùng tàu chiến Mỹ khiêu khích và tấn công đơn vị hải quân ta. Hải quân nhân dân Việt Nam trẻ tuổi đã anh hùng chống trả lại kịch liệt. Trong trận này, hải quân ta đã bắn rơi 1 chiếc máy bay Mỹ và bắn bị thương 2 chiếc khác.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','08', 'Ngày 2-8-1965, Chính phủ ta tuyên bố về việc Chính phủ Mỹ ồ ạt đưa thêm quân vào miền Nam nước ta, ráo riết tǎng cường chiến tranh xâm lược nước Việt Nam. Hồ Chủ tịch đã nói lên quyết tâm của nhân dân ta: \"Dù đế quốc Mỹ tǎng thêm 10 vạn, 20 vạn quân Mỹ hoặc nhiều hơn nữa, dù phải chiến đấu 10 nǎm, 20 nǎm hoặc lâu hơn nữa, nhân dân Việt Nam cũng quyết tâm đánh đến thắng lợi hoàn toàn\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','08', 'Ngày 2-8-1990: Bắt đầu tình hình khủng hoảng vùng Vịnh, đẩy tới mâu thuẫn gay gắt giữa Irắc và Côoét. Ngay lập tức, Mỹ và các nước phương Tây ồ ạt đổ quân vào vùng Vịnh và thực hiện cấm vận đối với Irắc. Cuộc khủng hoảng vùng Vịnh bùng nổ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','09', 'Ngày 2-9-1945, Chính phủ Lâm thời nước Việt Nam Dân chủ Cộng hoà ra mắt quốc dân tại Quảng trường Ba Đình, Hà Nội. Hai giờ chiều, mít tinh bắt đầu khai mạc. Hồ Chủ tịch đọc lời Tuyên ngôn của nước Việt Nam Dân chủ Cộng hoà.\nTrong bản Tuyên ngôn Độc lập lịch sử, Chủ tịch Hồ Chí Minh đã khẳng định trước thế giới: \"Nước Việt Nam có quyền hưởng tự do, độc lập và sự thực đã trở thành một nước tự do, độc lập. Toàn thể dân tộc Việt Nam kiên quyết đem tất cả tinh thần, lực lượng, tính mạng và của cải để giữ vững quyền tự do và độc lập ấy!\".\nNgày 2-9-1945 mãi mãi đi vào lịch sử như ngày độc lập đầu tiên của dân tộc Việt Nam sau ngót một thế kỷ đô hộ của chủ nghĩa thực dân Pháp, ghi nhận chiến công của dân tộc Việt Nam trở thành một dân tộc tiên phong trong sự nghiệp giải phóng các dân tộc thuộc địa trên thế giới. Cũng từ đó, ngày 2-9 trở thành ngày quốc khánh của nhân dân Việt Nam và đó cũng là cái mốc chấm dứt lịch sử cận đại Việt Nam, mở ra một kỷ nguyên mới cho lịch sử dân tộc ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','09', 'Ngày 2-9-1948, Hội nghị chuyên môn quân giới toàn quân được tổ chức, đây là hội nghị chuyên môn đầu tiên của toàn ngành sản xuất vũ khí nước Việt Nam, bao gồm cả lực lượng quân giới và vũ khí dân quân, cùng xem xét bàn bạc thống nhất về các nặt kinh tế, công nghệ, quản lý và tổ chức sản xuất vũ khí.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','09', 'Ngày 2-9-1955 đã diễn ra lễ tuyên dương Anh hùng quân đội lần thứ hai. Đại hội tuyên dương 26 anh hùng trong đó có 8 liệt sĩ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','09', 'Sau một thời gian lâm bệnh, hồi 9 giờ 47 phút ngày 2-9-1969, Hồ Chủ tịch - Vị lãnh tụ vĩ đại vô cùng kính yêu của giai cấp công nhân và nhân dân Việt Nam, cùng toàn thể dân tộc Việt Nam, Người chiến sĩ lỗi lạc của phong trào Cộng sản quốc tế và phong trào giải phóng dân tộc, danh nhân văn hoá thế giới, đã từ trần. Chủ tịch Hồ Chí Minh qua đời nhưng Người đã để lại cho chúng ta một di sản quý báu đó là sự nghiệp vĩ đại, tư tưởng đạo đức tác phong của Người.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','09', 'Đại hội toàn quốc nhân dân Cuba khai mạc ngày 2-9-1960\nĐại hội thông qua những quyền cơ bản của công dân, xác định đường lối chống đế quốc và đoàn kết với các dân tộc đấu tranh cho tự do và hoà bình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','10', 'Ngày 2-10-1980, Đặng Thái Sơn đã được giải nhất trong cuộc thi Pianô quốc tế mang tên Sôpanh lần thứ 10 tổ chức tại Vácsava (Ba Lan).\nCuộc thi này có 150 nghệ sĩ của 36 nước tham dự. Đặng Thái Sơn lúc đó mới 22 tuổi, là sinh viên năm thứ 4 của nhạc viện quốc gia Mátxcơva.\nĐặng Thái Sơn đã được Nhà nước phong tặng danh hiệu Nghệ sĩ nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','10', 'Bác sĩ Trần Duy Hưng sinh năm 1912 từ trần ngày 2-10-1988 tại Hà Nội.\nÔng tham gia Cách mạng từ năm 1945.\nSau Cách mạng tháng Tám ông được cử giữ nhiều chức vụ quan trọng: Chủ tịch uỷ ban hành chính thành phố Hà Nội (tháng 8-1945), Thứ trưởng Bộ Nội vụ (Năm 1947), Thứ trưởng Bộ Y tế (tháng 6-1954).\nTừ năm 1954 đến năm 1977 bác sĩ Trần Duy Hưng được bầu làm chủ tịch Uỷ ban Hành chính (sau này là ủy ban nhân dân) thành phố Hà Nội. Ông là đại biểu Quốc hội từ khoá I đến khoá VII.\nBác sĩ Trần Duy Hưng là một trí thức yêu nước đi theo cách mạng, được nhân dân yêu mến và kính trọng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','10', 'Ngày 2-10-1866, Ôxtechâu (Osterhoudt) ở Niu Oóc (Hoa Kỳ) đã phát minh ra một loại vỏ đồ hộp có một chốt mở cố định trên nắp.\nNgười ta chỉ cần nhẹ nhàng bóc nó ra và xoay nó để mở hộp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','10', 'Môhanđát Caramchanđơ Ganđi (Mohadas Karamchand Gandhi) sinh ngày 2-10-1869 và qua đời năm 1948 do bọn phản động ám sát.\nÔng là nhà triết học, nhà văn, nhà hoạt động nổi tiếng của phong trào giải phóng dân tộc Ấn Độ. Để chống lại thực dân Anh, giành độc lập dân tộc, Ganđi chủ trương không dùng bạo lực và không coi bạo lực là phương pháp chiến tranh chính trị. Từ năm 1919 đến năm 1922, Ganđi trở thành lãnh tụ của Đảng Quốc đại và giữ vai trò quan trọng trong việc biến Đảng này thành một tổ chức quần chúng chống đế quốc. Học thuyết của Ganđi trở thành tư tưởng chính của Đảng Quốc đại trong cuộc đấu tranh của nền độc lập Ấn Độ.\nNhân dân Ấn Độ suy tôn ông là Mahátma, nghĩa là \"Tâm hồn vĩ đại\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','11', 'Lưu Tấn Tài tức Lưu Chi Lǎng - nhà soạn kịch quê ở tỉnh Kiên Giang. Ông là kịch gia nổi tiếng, nhiệt thành yêu nước, tham gia kháng chiến cứu quốc, có nhiều đóng góp cho vǎn học dân tộc. Ông là tác giả của các vở cải lương: Dệt gấm, Thạch Sanh, Nàng tiên Mẫu Đơn, Lửa Diên Hồng, Thái hậu Dương Vân Nga... Ông mất ngày 2-11-1982 tại Thành phố Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','11', 'Từ ngày 2 đến 5-11-1972, quân và dân Gia Lai liên tục mở nhiều trận tiến công tiêu diệt cǎn cứ Đức Cơ và đánh bại hoàn toàn cuộc hành quân của 3 chiến đoàn và liên đoàn quân biệt động ngụy, loại khỏi vòng chiến đấu hơn 2.100 tên địch, đánh thiệt hại nặng 8 tiểu đoàn, bắn rơi 9 máy bay, thu 8 xe quân sự và gần 300 súng. Hàng vạn đồng bào ở khu vực Đức Cơ nổi dậy giành lại quyền làm chủ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','11', 'Từ ngày 2 đến ngày 4-11-1988, tại Hội trường Ba Đình, Hà Nội, đã tiến hành Đại hội lần thứ ba Mặt trận tổ quốc Việt Nam. Uỷ ban Trung ương Mặt trận Tổ quốc Việt Nam khoá III gồm có 166 vị, Đoàn chủ tịch có 30 vị, Ban thư ký có 6 vị\nChủ tịch Danh dự: Cụ Hoàng Quốc Việt.\nChủ tịch: Luật sư Nguyễn Hữu Thọ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','11', 'Alếchxǎng đơ Rốt sinh nǎm 1593 ở Avinhông (nay thuộc nước Pháp) và qua đời ngày 2-11-1660 ở nước Iran.\nÔng đến Việt Nam lần đầu tiên vào nǎm 1624 và nǎm ông đến Việt Nam lần cuối cùng là nǎm 1645.\nÔng là linh mục dòng Tên, vào Việt Nam truyền đạo Thiên chúa. Trong qúa trình truyền đạo, ông đã học tiếng Việt và cùng với các linh mục khác người châu Âu chế tác ra chữ quốc ngữ.\nAlếchxǎng đơ Rốt đã có công lớn góp phần sửa sang, và hoàn chỉnh bộ chữ quốc ngữ, và đặc biệt là ông dùng chữ ấy để biên soạn tổ chức in ấn lần đầu tiên cuốn Từ điển Việt - Bồ - La (trong đó có phần về ngữ pháp tiếng Việt).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','11', 'Giogiơ Bulơ (Georges Boole) là nhà toán học Anh, sinh ngày 2-11-1815.\nNǎm 1848, ông cho xuất bản tập \"Giải tích toán học của logic\". Ông còn cho xuất bản nhiều công trình và dành nhiều công sức cho tác phẩm chủ yếu \"Các định luật của tư duy\". Công trình này là nguồn gốc cho lý thuyết toán học mang tên ông: \"Đại số Boole\".\nCuộc đời và sự nghiệp của ông là tấm gương sáng về tinh thần khắc phục khó khǎn, kiên trì và nhẫn nại học tập, say mê nghiên cứu sáng tạo. Con gái ông là nữ vǎn sĩ Êtanh lilian Bulơ (Eten Lilian Boole), tác giả cuốn \"Ruồi trâu\" nổi tiếng.\nÔng mất ngày 8-12-1864.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','11', 'Ngày 2-11-1953, Quốc hội Pakistan tuyên bố thành lập nước Cộng hoà Hồi giáo Pakistan.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','11', 'Chiều ngày 2-11-1965, tại Oasinhtơn (thủ đô nước Mỹ), trước cửa toà nhà làm việc của Bộ Quốc phòng Mỹ, anh Noman Moprixơn ôm chặt con gái Êmili mới được 18 tháng, vào ngực. Sau khi hôn con lần cuối cùng, anh tẩm xǎng vào mình và châm lửa tự đốt để phản đối việc chiến tranh xâm lược của đế quốc Mỹ ở Việt Nam.\nNǎm đó, anh Noman Morixơn, một chiến sĩ hoà bình Mỹ, mới 31 tuổi')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','11', '2-11-1990, Quốc hội Modambic thông qua hiến pháp mới chuyển sang chế độ dân chủ đa đảng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','12', 'Ngày 2-12-1953, tại chiến khu Việt Bắc đã thành lập Ban nghiên cứu lịch sử, địa lý, vǎn học (gọi tắt là Ban sử - địa - vǎn) trực thuộc Ban Chấp hành Trung ương Đảng Lao động Việt Nam.\nBan này gồm có các ông: Trần Huy Liệu, Minh Tranh, Tôn Quang Phiệt, Hoài Thanh, Trần Đức Thảo, Vũ Ngọc Phan. Ông Trần Huy Liệu được cử làm Trưởng ban.\nBan sử - địa - vǎn có nhiệm vụ:\n- Sưu tầm, nghiên cứu những tài liệu về lịch sử, địa lý và vǎn học Việt Nam, biên soạn những tác phẩm về sử học, địa lý và vǎn học Việt Nam.\n- Nghiên cứu và giới thiệu lịch sử, địa lý và vǎn học các nước bạn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '02','12', 'Nhà báo Dương Tử Giang, tên thật Nguyễn Tấn Sĩ sinh nǎm 1918, quê ở huyện Giồng Trôm, tỉnh Bến Tre.\nTừ nǎm 1950, Dương Tử Giang ra chiến khu miền Tây Nam Bộ, cùng với nhà vǎn Thiếu Sơn làm báo Cứu quốc. Ít lâu sau ông vào công tác ở nội thành Sài Gòn - Chợ Lớn. Tháng 10-1955, ông bị mật vụ của Ngô Đình Diệm bắt giam ở bót Catina rồi đưa lên giam ở Biên Hoà.\nNgày 2-12-1956, nhà báo Dương Tử Giang hy sinh trong trận tù chính trị nổi dậy phá lao Tân Hiệp, Biên Hoà, lúc đó ông mới 38 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','12', 'Nguyên Soái Giucốp - nhà chiến lược quân sự tài ba của Liên bang Cộng hoà Xã hội chủ nghĩa Xô Viết và thế giới, người có công rất lớn trong việc tiêu diệt chủ nghĩa phát xít.\nÔng sinh ngày 2-12-1896 trong một gia đình nông dân nghèo. Ông đã trải qua một chặng đường dài binh nghiệp vì sự nghiệp bảo vệ đất nước Xô Viết. Những chiến công của ông mãi được ghi vào sách giáo khoa lịch sử quân sự. Đó là trận: chỉ huy phòng thủ Lêningrat (9-1941), đánh bại quân Đức tại cửa ngõ Mátxcơva (11-1941), trận Stalingrat (2-1943) và trận quyết chiến, chiến lược thắng lợi giòn giã chiếm Beclin (4-1945).\nÔng từng làm Tổng tham mưu trưởng, Phó tổng tư lệnh tối cao (1941), Thứ trưởng các lực lượng vũ trang Liên Xô (1946), Bộ trưởng Quốc phòng Liên Xô (1955), ông được phong hàm nguyên soái Liên Xô (1943), bốn lần được phong Anh hùng Liên Xô.\nÔng mất ngày 8-6-1974.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','12', 'Ngày 2-12-1975, nước Cộng hoà dân chủ nhân dân Lào được thành lập, xoá bỏ chế độ quân chủ lạc hậu, tay sai đế quốc, cử Hoàng thân Xuphanuvông làm Chủ tịch nước, kết thúc thắng lợi 30 nǎm kháng chiến chống đế quốc (1945-1975). Kể từ đây đất nước Triệu Voi bước vào kỷ nguyên mới - kỷ nguyên độc lập, dân chủ và xây dựng xã hội công bằng, vǎn minh. Cùng với Việt Nam và Cǎmpuchia, nước Cộng hoà dân chủ nhân dân Lào đã góp phần bảo vệ hoà bình và hữu nghị trên bán đảo Đông Dương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '02','12', 'Ngày 2-12-1982, tại thành phố San Lếch (Salt Lake) (Hoa Kỳ) tim nhân tạo có tên là Jarvik 7 đã được bác sĩ Đơ Vrie (De Vries) ghép lần đầu tiên cho một người bệnh tên là Clắccơ (Clarke).\nPhương pháp ghép tim nhân tạo đã cho phép y học đạt tiến bộ trong việc chữa trị người bệnh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','01', 'Đại thi hào Nguyễn Du sinh ngày 3-1-1766, qua đời ngày 16-9-1820.\nÔng tự là Tố Như, hiệu Thanh Hiên, quê ở làng Tiên Điền, huyện Nghi Xuân, tỉnh Hà Tĩnh. Xuất thân trong gia đình quí tộc, nhưng phải trải qua một thời niên thiếu vất vả, ông đã đỗ tam trường, ra làm quan dưới triều Nguyễn từ nǎm 1802, đi sứ sang Trung Quốc nǎm 1813. Nguyễn Du là nhà thơ lớn của dân tộc với kiệt tác \"Truyện Kiều\" và \"Vǎn tế thập loại chúng sinh\" cùng thơ chữ Hán thể hiện tài nǎng và lòng nhân ái của ông.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','01', 'Ngày 3-1-1966 tại thủ đô La Habana (Cuba) đã tiến hành Hội nghị ba châu thành lập tổ chức quốc tế mang tên Tổ chức đoàn kết nhân dân Á, Phi, Mỹ Latinh. Thủ đô LaHabana được chọn làm trụ sở của Tổ chức. Tổ chức có chung mục đích là hoà bình thế giới, công bằng, hợp tác, loại trừ chủ nghĩa đế quốc, chủ nghĩa thực dân cũ và mới, chủ nghĩa Apácthai phân biệt chủng tộc, chống lại tất cả các hình thức ngoại xâm, can thiệp, gây áp lực chính trị hoặc kinh tế.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','01', 'Ngày 3-1-1981, Hội đồng Bộ trưởng nước Cộng hoà xã hội chủ nghĩa Việt Nam đã quyết định thống nhất tên gọi các đơn vị hành chính cơ sở ở nội thành, nội thị, của các thành phố thuộc tỉnh, thị xã và quận gọi là Phường. Phường có Hội đồng nhân dân và Uỷ ban nhân dân Phường. Phường trở thành đơn vị hành chính cơ sở ngang xã, thị trấn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '03','01', 'Maracudơ Tuliu Xierô (Maracuz Tullius Cieero) nhà vǎn, nhà hùng biện bậc nhất, chính khánh nổi tiếng cổ La Mã. Ông sinh ngày 3-1-106 (trước Công Nguyên).\nTrong tác phẩm \"Nền Cộng hoà\", ông đã phác ra lý tưởng một nền cộng hoà tự do. Ông viết \"Khái luận tu từ học\" gồm có 8 cuốn là tài liệu quí nghiên cứu lịch sử ngành hùng biện La Mã. Ông còn viết \"Khái niệm triết học\". Về thơ ông có trên một nghìn bài. Ngoài ra ông còn viết sách lịch sử. Ông được coi là người có công đưa vǎn xuôi Latinh lên đỉnh cao.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('1', '03','02', 'Ngày Thành lập Đảng Cộng sản Việt Nam')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','02', 'Từ ngày 3 đến ngày 7-2-1930, tại bán đảo Cửu Long (Hương Cảng, Trung Quốc), dưới sự chủ trì của đồng chí Nguyễn Ái Quốc, Hội nghị hợp nhất các tổ chức Cộng sản thành một Đảng duy nhất lấy tên là Đảng Cộng sản Việt Nam.\nHội nghị đã thảo luận và thông qua Chính cương vắn tắt, sách lược vắn tắt, Chương tình tóm tắt và Điều lệ tóm tắt của Đảng, Điều lệ tóm tắt của các hội quần chúng do Nguyễn Ái Quốc soạn thảo, định kế hoạch lập Ban Chấp hành Trung ương lâm thời. \nCác vǎn kiện xác định nhiệm vụ của cách mạng là đánh đổ chủ nghĩa đế quốc Pháp và bọn phong kiến, làm cho Việt Nam hoàn toàn độc lập, nhân dân được tự do, lấy ruộng đất của đế quốc làm của công và chia cho nông dân nghèo, quốc hữu hoá toàn bộ xí nghiệp của đế quốc, tổ chức ra chính phủ công - nông - binh và quân đội công - nông.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','02', 'Ngày 3-2-1964, Bộ Quốc phòng nước Việt Nam dân chủ cộng hoà đã ra quyết định thành lập trung đoàn không quân tiêm kích 921. Đây là trung đoàn không quân tiêm kích đầu tiên của quân đội ta (còn gọi là Đoàn không quân Sao Đỏ).\nTháng 11-1964, Hồ Chủ tịch đã đến thǎm trung đoàn 921. Bác Hồ cǎn dặn: \"Tổ tiên ta xưa đã có những chiến công oanh liệt trên sông, trên biển như Bạch Đằng, Hàm Tử; trên bộ như Chi Lǎng, Vạn Kiếp, Đống Đa. Ngày nay chúng ta phải mở mặt trận trên không thắng lợi. Trách nhiệm ấy trước hết là của các chú\".\nTrong cuộc kháng chiến chống Mỹ, trung đoàn không quân 921 đã lập chiến công xuất sắc, được Nhà nước tuyên dương Anh hùng lực lượng vũ trang nhân dân nǎm 1969. Đại đội 1 và đại đội 3 thuộc trung đoàn 921 đều được tuyên dương danh hiệu vinh dự này 2 lần.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','02', 'Ngày 3-2-1964, khánh thành công trình thuỷ lợi lớn Ấp Bắc - Nam Hồng (thuộc huyện Đông Anh, ngoại thành Hà Nội). Đây là một công trình thuỷ lợi lớn ở miền Bắc, bảo đảm nước tưới cho trên 14.000 héc ta ruộng đất Công trường được Bộ Thuỷ lợi tặng cờ thi đua \"Công trường thuỷ lợi khá nhất miền Bắc\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','02', 'Dư luận rộng rãi ở Mỹ và trên thế giới đòi hỏi chính quyền Oasinhtơn nhanh chóng thiết lập quan hệ ngoại giao bình thường với Việt Nam.\nNgày 3-2-1994, Tổng thống Mỹ B. Clinton quyết định bãi bỏ lệnh cấm vận Việt Nam áp đặt trong suốt 18 nǎm.\nQuyết định này của Mỹ đã đưa quan hệ Mỹ - Việt bước vào một giai đoạn mới, thúc đẩy quá trình bình thường hoá quan hệ hai nước sau khi cuộc chiến tranh Việt Nam đã kết thúc gần 20 nǎm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','03', 'Vua Lê Thánh Tông tên thật là Lê Tư Thành sinh nǎm 1442 và mất ngày 3-3-1497. Ông là con vua Lê Thái Tông và bà Ngô Thị Ngọc Giao. Ông trị vì 38 nǎm và thời kỳ Lê Thánh Tông là thời kỳ thịnh trị nhất trong lịch sử phong kiến nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','03', 'Phan Huy Ích là danh sĩ của thời Hậu Lê phục vụ Triều Tây Sơn. Ông quê ở Hà Tĩnh và là em rể của Ngô Thì Nhậm. Ông từng làm Hàn lâm thừa chỉ. Chán cảnh vua Lê - chúa Trịnh ông cáo bệnh về nghỉ nhưng không được chấp nhận. Khi Nguyễn Huệ lên ngôi mời ông vào Phú Xuân và đi sứ sang nhà Thanh một thời gian. Ông là danh sĩ nổi tiếng với một số tác phẩm có giá trị. Ông mất ngày 3-3-1822 tại quê nhà.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','03', 'Ngày 3-3-1951, Đảng Lao động Việt Nam chính thức ra mắt quốc dân, công khai lãnh đạo cuộc kháng chiến kiến quốc. Tại buổi lễ ra mắt, đồng chí Trường Chinh thay mặt Đảng trình bày mục đích, tôn chỉ và chính cương của Đảng. Đồng chí nhấn mạnh: Đảng Lao động Việt Nam trong Mặt trận dân tộc thống nhất đoàn kết chặt chẽ, cộng tác thân mật với các Đảng phái, các đoàn thể và nhân sĩ yêu nước trong mặt trận.\nViệc Đảng Lao động Việt Nam ra công khai là một sự kiện vô cùng quan trọng, đánh dấu một thắng lợi lớn của Cách mạng Việt Nam, tǎng cường sự lãnh đạo của Đảng, đồng thời đẩy mạnh hơn nữa cuộc kháng chiến mau đến thắng lợi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','03', 'Mặt trận Liên Việt bao gồm tất cả các đoàn thể nhân dân, tôn giáo, đảng phái, các nhân sĩ yêu nước nhằm thắt chặt khối Đại đoàn kết toàn dân để kháng chiến cứu nước, Mặt trận được thành lập ngày 3-3-1951 trên cơ sở hợp nhất Việt Minh và Liên Việt. Lấy tên là Mặt trận liên hiệp quốc dân Việt Nam. Gọi tắt là Liên Việt.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','03', 'Ngày 3-3-1955, Trung đoàn Không quân Việt Nam đầu tiên được thành lập, đánh dấu bước phát triển lên chính quy, hiện đại của quân đội ta.\nHơn 40 nǎm xây dựng, chiến đấu và trưởng thành, Không quân nhân dân Việt Nam đã đánh thắng chiến tranh phá hoại của đế quốc Mỹ, bắn rơi, bắn cháy hàng trǎm máy bay địch, góp phần chi viện và hợp đồng chiến đấu cho các chiến trường. Ngày nay Không quân Việt Nam có lực lượng mạnh cấp sư đoàn. Có truyền thống anh hùng. Nhiều đơn vị và cá nhân thuộc Quân chủng Không quân được tặng danh hiệu Anh hùng lực lượng vũ trang.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','03', 'Ngày 3-3-1959, Bộ đội Biên phòng được thành lập. Đây là lực lượng vũ trang có chức nǎng bảo vệ chủ quyền và lãnh thổ quốc gia khu vực biên giới. Dù thay đổi tên gọi hoặc thuộc thành phần của quân đội hay công an thì bộ đội biên phòng vẫn là lực lượng bảo vệ an ninh biên giới của Tổ quốc. Bộ đội biên phòng đã được Đảng và Nhà nước tuyên dương là Đơn vị Anh hùng lực lượng vũ trang và tặng thưởng Huân chương Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '03','03', 'Ghêóoc Canthô - được coi là nhà toán học lớn cuối thế kỷ XIX. Ông sinh ngày 3-3-1845. Ông có nhiều cống hiến trong việc xây dựng nền lý thuyết tập hợp - một lý thuyết quan trọng trong việc xây dựng cơ sở cho toán học hiện đại. Chính sự ra đời của lý thuyết tập hợp và phát triển tiêu đề trừu tượng đã đánh dấu thời kỳ mới của toán học hiện đại đồng thời những công trình của ông là sự đấu tranh của phát triển toán học thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','04', 'Ngày 3-4-1845, Đào Tấn sinh ra ở xã Phúc Lộc, huyện Tuy Phước, tỉnh Bình Định và từ trần ngày 15-7-1907.\nCuộc đời ông có nhiều nét đặc biệt: 3 lần làm tổng đốc, 4 lần làm thượng thư và là một nghệ sĩ lớn nhất của dân tộc ở thế kỷ XIX - Là một người yêu nước, ông có quan hệ mật thiết với các lãnh tụ trong phong trào Cần Vương.\nTrên hết, Đào Tấn là một diễn viên xuất sắc, một đạo diễn tài nǎng, một nhà thơ độc đáo và là một trong những nhà lý luận sân khấu đầu tiên của Việt Nam.\nÔng để lại mấy trǎm bài thơ và từ, 30 vở tuồng, trong đó có nhiều vở đã trở thành mẫu mực, có vở dài 100 hồi, diễn tới 100 đêm, và \"Hí trường tuỳ bút\" là tập lý luận sân khấu rất có giá trị.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','04', 'Trong hai ngày: mùng 3 và mùng 4 tháng 4-1965, không quân nhân dân Việt Nam đã ghi chiến công đầu giòn giã. Ngày 3-4-1965, biên đội không quân Phạm Ngọc Lan bắn rơi 2 máy bay phản lực hiện đại F8 trên vùng trời Hàm Rồng Thanh Hoá. Tiếp đó, 4-4, liên đội Trần Hanh bắn rơi 2 phản lực F105 Mỹ cũng trên vùng trời Hàm Rồng. Ngày đánh thắng trận đầu oanh liệt ấy đã trở thành ngày truyền thống vẻ vang của quân chủng.\nKhông quân nhân dân là một quân chủng hiện đại trẻ tuổi của Quân đội nhân dân Việt nam. Trong cuộc kháng chiến chống Mỹ, không quân đã đánh thắng hơn 500 trận, bắn rơi 320 máy bay Mỹ. Trong cuộc tổng tiến công và nổi dậy mùa xuân nǎm 1975, không quân nhân dân đã vận chuyển một khối lượng vũ khí và hàng quân sự vào chiến trường kịp thời phục vụ chiến đấu. Đồng thời đã yểm trợ đắc lực cho các đơn vị bộ binh và hải quân chiến đấu giải phóng các hòn đảo của Tổ quốc. Ngày 28-4-1975 phi đội Quyết thắng sau khi bắn rơi 5 máy bay Mỹ đã táo bạo, bất ngờ đánh trúng khu vực để máy bay của địch ở Tân Sơn Nhất góp phần giành toàn thắng cho chiến dịch Hồ Chí Minh lịch sử.\nĐảng và Nhà nước đã dành cho không quân nhân dân Việt Nam nhiều phần thưởng cao quý. Nhiều tập thể và cá nhân của quân chủng không quân đã được tuyên dương Anh hùng lực lượng vũ trang nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','04', 'Ngày 3-4-1981, nước ta quyết định tham gia \"Công ước về ngǎn ngừa và trừng trị tội ác diệt chủng\", và \"Công ước về ngǎn ngừa và trừng trị tội ác của chủ nghĩa Apácthai\".\nĐây là thể hiện quyết tâm mà Đảng Cộng sản Việt Nam đã nêu trong cương lĩnh của mình về phấn đấu cho một xã hội, một thế giới công bằng vǎn minh. Tội ác diệt chủng và chủ nghĩa Apácthai thực sự là nỗi ô nhục của loài người tiến bộ. Bằng việc tham gia 2 công ước nói trên, nước CHXHCNVN khẳng định vai trò và ý trí của mình trên trường quốc tế ở lĩnh vực này.\nNgày 9-6-1981, Đại diện Việt Nam tại Liên hiệp quốc đã trao đổi công hàm tham gia các công ước trên cho Tổng thư ký Liên hiệp quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '03','04', 'Ngày này năm 1897, Iôhannét Bramxơ - nhà soạn nhạc nổi tiếng nhất trong lịch sử âm nhạc thế giới nửa cuối thế kỷ XIX, từ trần tại thành phố Yên nước Áo.\nÔng sinh ở Hambuốc nǎm 1833.\nThời trẻ Bramxơ sống trong cảnh nghèo khó, 13 tuổi đã phải đi đánh đàn thuê cho các tiệm nhảy. Bramxơ đi biểu diễn nhiều nơi ở nước ngoài, gặp Suman, Litxơ và các nhạc sĩ ấy có ảnh hưởng nhiều tới ông.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','05', 'Ngô Đức Kế sinh ngày 3-5-1878 ở Hà Tĩnh và qua đời ngày 10-12-1928. Ông là nhà thơ, nhà báo.\nÔng đỗ tiến sĩ nhưng không làm quan mà ở nhà dạy học; vận động Duy Tân, liên hệ với nhà yêu nước Phan Bội Châu để tìm đường cứu nước. Ngô Đức Kế là chủ bút báo Hữu Thanh, ông xuất bản sách tiến bộ và sáng tác thơ vǎn nhằm thức tỉnh lòng yêu nước của nhân dân, Vì thế ông đã bị thực dân Pháp đầy ra Côn Đảo.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','05', 'Ngày này nǎm 1916, vụ mưu khởi nghĩa chống Pháp do hai phu sĩ Thái Phiên và Trần Cao Vân lãnh đạo dưới danh nghĩa vua Duy Tân đã nổ ra ở Huế.\nCuộc khởi nghĩa có quan hệ mật thiết với Việt Nam Quang phục hội ở các tỉnh Nam Trung Kỳ. Ở Quảng Nam, Quảng Ngãi có hàng ngàn người tham gia cuộc khởi nghĩa. Ở Huế có tới 2.500 người và ở Đà Nẵng 1.500 người.\nNhân dân Trung Kỳ đã lập nghĩa binh, rèn vũ khí, mua sắm quân nhu, quyên góp tiền, gạo, nhưng kế hoạch bị bại lộ - Thực dân Pháp bắt vua Duy Tân đi đày, các thủ lĩnh nghĩa quân như Thái Phiên, Trần Cao Vân, Phạm Hồng Cương, Phan Thành Tài đều hy sinh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','05', 'Ngày 3-5-1931, đồng chí Nguyễn Phong Sắc đã bị mật thám của Pháp bắt ở ga Hàng Cỏ. Ít lâu sau, chúng bí mật thủ tiêu đồng chí.\nĐồng chí Nguyễn Phong Sắc sinh nǎm 1902, tại phố Bạch Mai Hà Nội, đã tốt nghiệp trường bưởi rồi làm thư ký ở Sở Tài chính Đông Dương. Từ nǎm 1926, đồng chí đã tham gia Việt Nam thanh niên Cách mạng đồng chí Hội ở Hà Nội. Đến tháng 9-1928 được bầu vào Kỳ bộ Bắc Kỳ, trực tiếp làm Bí thư Tỉnh bộ ở Hà Nội. Tháng 3-1929, là một đảng viên trong chi bộ Cộng sản đầu tiên ra đời tại nhà số 5D phố Hàm Long, Hà Nội.\nSau khi thành lập Đảng Cộng sản Việt Nam (nǎm 1930), đồng chí được bầu làm Uỷ viên Trung ương lâm thời, trực tiếp chỉ đạo phong trào Xô Viết Nghệ Tĩnh, sau đó được phân công làm Bí thư Xứ ủy Trung kỳ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','05', 'Trong cuộc bầu cử Hội đồng thành phố Sài Gòn diễn ra ngày 3-5-1935, một lần nữa \"Sở Lao động\" lại ra tranh cử, đứng đầu là đồng chí Nguyễn Vǎn Tạo. Mặc dù bọn thực dân tìm cách gây trở ngại, nhưng kết quả đã có 4 người của \"Sở Lao động\" trúng cử.\nĐánh giá về sự kiện này, báo cáo của đồng chí Lê Hồng Phong đọc tại Đại hội 7 Quốc tế Cộng sản đã nêu rõ; \"Ảnh hưởng của Đảng Cộng sản đã tǎng lên trong quần chúng... đây là lần đầu tiên công nhân và nông dân thắng lợi trong việc đưa đại biểu của mình vào hội đồng thành phố Sài Gòn\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','05', 'Ngày 3-5-1968, Bộ Ngoại giao nước ta ra tuyên bố về vấn đề cấp bậc đại biểu, địa điểm, thời gian của cuộc nói chuyện chính thức giữa Việt Nam Dân chủ cộng hoà và Mỹ. Đồng chí Xuân Thuỷ được Chính phủ cử làm đại diện Việt Nam Dân chủ cộng hoà, Pari là địa điểm của cuộc nói chuyện và ngày 10-5 hoặc vài ngày sau đó sẽ bắt đầu cuộc nói chuyện chính thức.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','05', 'Ngày 3-5-1983 Ban Bí thư Trung ương Đảng ra chỉ thị về \"Hoàn thành điều chỉnh ruộng đất đẩy mạnh cải tạo xã hội chủ nghĩa đối với nông nghiệp ở các tỉnh Nam Bộ\".\nBan chỉ thị nêu rõ: Cải tạo xã hội chủ nghĩa đối với nông nghiệp là cuộc đấu tranh giai cấp, đấu tranh giữa hai con đường để giải quyết vấn đề \"ai thắng ai\" giữa chủ nghĩa xã hội và chủ nghĩa tư bản. Chỗ dựa vững chắc để tiến hành cuộc đấu tranh là khối đoàn kết của nông dân lao động dưới sự lãnh đạo của Đảng.\nChỉ thị còn nêu lên các nhiệm vụ cụ thể là: Hoàn thành dứt điểm điều chỉnh ruộng đất; hoàn thành việc xây dựng tổ đoàn kết sản xuất gắn liền với việc đẩy mạnh xây dựng hợp tác xã tín dụng và hợp tác xã mua bán; củng cố một bước các tập đoàn sản xuất mới ở những nơi có điều kiện; tích cực đào tạo và phát triển đội ngũ cán bộ kinh tế tập thể; tích cực xây dựng và tǎng cường cấp huyện; thực hiện đầy đủ và nghiêm chỉnh các chính sách của Nhà nước đối với cải tạo nông nghiệp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '03','05', 'Pautốpxki, nhà vǎn Nga sinh ngày 3-5-1892 và mất ngày 14-7-1968 tại Mátxcơva.\nSinh thời ông đã trải qua nhiều nghề để kiếm sống. Đầu những nǎm 20 khi làm biên tập viên của một tờ báo, tên tuổi ông mới trở nên quen thuộc với độc giả. Ông cho ra mắt truyện dài \"Kara-Buga\" và \"Côkhiđa\". Thành công của hai tác phẩm đã gây tiếng vang, đánh dấu bước ngoặt quan trọng trong sự nghiệp sáng tác của ông. Từ đây ông dành hết thời giờ để sáng tác. Ông cho ra đời một khối lượng tác phẩm khá phong phú và đa dạng về đề tài và thể loại. Tác phẩm Số phận của Saclơ Lôngxêvin và Tiểu thuyết phương Bắc là hai tác phẩm về đề tài lịch sử. Tác phẩm ôrest Kipreski, Tarax Sevsencô, Tiểu thuyết về rừng viết về công việc sáng tạo của người nghệ sĩ, đặc biệt tác phẩm Bông hồng vàng gồm những ghi chép, suy nghĩ về công việc của người viết vǎn đã lập tức thu hút được độc giả trong và ngoài nước.\nCác tác phẩm của Pautốpxki mang nhiều chất thơ nên thấm vào hồn người một cách nhẹ nhàng tinh tế.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '03','05', 'Ngày 3-5-1996, Đoàn đại biểu từ 55 nước họp tại Geneva đã đồng ý chấp nhận một số nguyên tắc mới trong việc sử dụng mìn nhưng không chấp nhận lệnh cấm sử dụng mìn hoàn toàn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','06', 'Trần Hoảng là Vua thứ hai nhà Trần, miếu hiệu Thánh Tông, sinh ngày 13-10-1240 tại làng Tức Mạc, phủ Thiên Trường, Nam Định.\nÔng ở ngôi 21 nǎm, truyền ngôi cho con là Nhân Tông rồi làm Thái thượng hoàng. Khi quân Nguyên Mông xâm lược lần thứ hai và thứ ba, ông đã cùng vua con lãnh đạo cuộc kháng chiến thắng lợi. Sau đó, ông về Bắc cung tu Phật, chuyên tâm soạn kinh sách Phật. Ông còn để lại các tác phẩm: Di hậu lục, Cờ cửu lục, Thiền tông liễu ngộ ca, và 6 bài thơ ghi chép ở Đại Việt sử ký toàn thư và Việt âm thi tập.\nTrần Hoảng mất ngày 3-6-1290, thọ 50 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','06', 'Ngày 3-6-1941, Tạp chí \"Tri Tân\" ra số đầu tiên tại Hà Nội. Đây là tuần báo chuyên khảo cứu về vǎn hoá và lịch sử dân tộc của nhiều học giả có tên tuổi đương thời. Tờ báo tồn tại cho đến khi Cách mạng Tháng Tám thành công, nǎm 1945.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','06', 'Ngày 3 đến 6-6-1975, Quốc hội khoá V nước Việt Nam Dân chủ cộng hoà họp kỳ họp đầu tiên. Quốc hội đã nhất trí bầu đồng chí Tôn Đức Thắng làm Chủ tịch, đồng chí Nguyễn Lương Bằng làm Phó Chủ tịch nước Việt Nam Dân chủ cộng hoà, đồng chí Trường Chinh làm Chủ tịch Uỷ ban thường vụ Quốc Hội, đồng chí Phạm Vǎn Đồng làm Thủ tướng Chính phủ, và các ủy ban của Quốc hội.\nQuốc hội cũng đã thông qua bản Tuyên bố nhân dịp giải phóng hoàn toàn miền Nam, biểu dương thành tích vĩ đại của quân và dân cả nước, kêu gọi đồng bào ta nhanh chóng hàn gắn vết thương chiến tranh, khôi phục và phát triển kinh tế, vươn lên mạnh mẽ, xây dựng một nước Việt Nam hoà bình, thống nhất, độc lập, dân chủ và giàu mạnh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','06', 'Nhà thơ, Nghệ sĩ nhân dân Thế Lữ qua đời vào ngày 3-6-1989 tại thành phố Hồ Chí Minh. Ông tên thật là Nguyễn Thứ Lễ, sinh ngày 6-10-1907 tại Hà Nội.\nÔng có chân trong Tự lực vǎn đoàn, là một trong những người khởi xướng phong trào Thơ mới. Sau cách mạng Tháng Tám nǎm 1945, Thế Lữ chuyển sang hoạt động sân khấu, đã viết một số kịch bản và dịch một số vở kịch của các tác giả nước ngoài.\nCác tác phẩm chính của Thế Lữ gồm có: \"Mấy vần thơ\", \"Vàng và máu\", \"Bên đường thiên lôi\", \"Gió trǎng ngàn\", \"Trại Bồ Tùng Linh\". Bài thơ nổi tiếng \"Nhớ rừng\" của ông chính là tâm sự đương thời trước nỗi đau làm người dân thuộc địa.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '03','06', 'Nhạc sĩ Pháp Gióocgiơ Bidê sinh ngày 25-10-1838 tại Pari và mất ngày 3-6-1875 tại Bugivan gần Pari. Với nhiều vở Ôpêra, đặc biệt là vở \"Cácmen\" (theo truyện của Mêrimê) ông đạt tới đỉnh cao nhất của nghệ thuật Ôpêra theo chủ nghĩa hiện thực của Pháp và thế giới ở cuối thế kỷ XIX.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','07', 'Nghệ sĩ nhiếp ảnh Nguyễn Bá Khoản sinh ngày 3-7-1917, quê ở làng Liễu Viên, huyện Thường Tín, tỉnh Hà Tây.\nNăm 20 tuổi, ông đã làm phóng viên cho các tờ báo của Đảng như: Tin tức, Bạn dân... Năm 1939, một mình ông đạp xe đi vòng quanh Đông Dương, vừa đi vừa chụp ảnh. Ông đã tham gia khởi nghĩa ở Hà Nội, Thường Tín, Phú Xuyên (Hà Tây). Tháng 11-1945, ông được cử làm Trưởng phái đoàn thanh tra mặt trận Nam bộ. Sau 9 năm kháng chiến, trong đoàn quân từ Việt Bắc về tiếp quản Thủ Đô có nhà báo Nguyễn Bá Khoản.\nÔng đã nhanh nhạy ghi được những hình ảnh, những người vào thời khắc hiếm hoi của lịch sử như: Các cảnh đồng bào Hà Nội mít tinh trước Nhà hát lớn ngày 17-8-1945, cướp chính quyền ở phủ Khâm sai, lễ mừng đất nước độc lập tại Vườn hoa Ba Đình, hai chiến sĩ \"Sao vuông\" ôm bom ba càng chờ xe tăng Pháp ở Ngã Tư Hàng Đậu, những lính Pháp cuối cùng đi trên cầu Long Biên rút khỏi Hà Nội...\nNăm 1991, nghệ sĩ nhiếp ảnh Nguyễn Bá Khoản tổ chức triển lãm ảnh của mình lần đầu tiên. Hai năm sau, ông qua đời.\nTháng 9-1996, Chủ tịch nước đã ký quyết định tặng giải thưởng Hồ Chí Minh cho 5 tác phẩm ảnh của Nguyễn Bá Khoản chụp Bác Hồ và kháng chiến.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','07', 'Từ ngày 3 đến ngày 31-7-1950, trên địa bàn huyện Chợ Lách và huyện Mỏ Cày (tỉnh Bến Tre), Bộ Tư lệnh khu 9 đã huy động một trung đoàn tấn công các lô cốt, tháp canh, nhằm thu hẹp phạm vi kiểm soát của địch, mở rộng vùng căn cứ của ta.\nChiến dịch Bến Tre được chia làm hai đợt. Kết quả là: Địch bị chết 230 tên, bị bắt 25 tên. Ta thu được một số vũ khí, bắn hỏng của địch hai xe lội nước, đốt cháy hai thuyền máy.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','07', 'Từ ngày 3 đến 5-7-1954 tại Liễu Châu (Quảng Tây, Trung Quốc), Chủ tịch Hồ Chí Minh đã hội đàm với Thủ tướng Quốc Vụ viện nước Cộng hoà nhân dân Trung Hoa Chu Ân Lai về vấn đề lập lại hoà bình ở Đông Dương và những vấn đề có liên quan trong cuộc đàm phán ở Hội nghị Giơnevơ. Cùng tham gia có đại tướng Tổng Tư lệnh Võ Nguyên Giáp và tướng Trần Văn Quang.\nTrong thời gian lưu tại đây, Người đã thăm lại sông Liễu Giang và núi Ngự Phong, nơi 10 năm trước Người đã rèn luyện sức khoẻ sau khi ra khỏi nhà tù Quốc dân Đảng Trung Quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','07', 'Hưởng ứng lời kêu gọi của Mặt trận Liên việt Nam bộ, ngày 7-3-1955, công nhân và nhân dân lao động Sài Gòn tổ chức hai cuộc biểu tình lớn đòi trả lại tự do cho những người trong \"Ủy ban cứu trợ nạn nhân chiến tranh\", kết hợp đòi hiệp thương tổng tuyển cử để thống nhất Tổ quốc. Bày ngày sau, ngày 10-7, các cuộc bãi công, bãi thị phản đối Mỹ Diệm khủng bố nhân dân, đòi hoà bình thống nhất nước nhà liên tiếp nổ ra. Chính quyền Diệm đã huy động nhiều lực lượng quân sự cảnh sát để chống biểu tình. Bất chấp mọi ngăn cản, đàn áp, khắp miền Nam có từ 60 đến 90% đồng bào tham gia bãi công, bãi thị. Ở Sài Gòn, cuộc tổng bãi công làm tê liệt hoạt động của thành phố trong 10 giờ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '03','07', 'Ngày 3-7-1980 tại Cremli, Chính phủ nước Cộng hoà xã hội chủ nghĩa Việt Nam và Chính phủ Liên bang cộng hoà xã hội chủ nghĩa Xô Viết đã tiến hành lễ ký kết Hiệp định hợp tác về thăm dò và khai thác dầu khí ở thềm lục địa phần phía Nam Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','08', 'Ngày 3-8-1864 nổ ra cuộc bạo động chống Pháp ở kinh thành Huế do Nguyễn Vǎn Viện (người tỉnh Bình Định) khởi xướng. Chủ trương bạo động được một số người trong hoàng tộc hưởng ứng. Nhưng do tổ chức không chặt chẽ, quân nội ứng không phối hợp kịp thời nên cuộc bạo động này bị dập tắt.\nNhững người lãnh đạo cuộc bạo động Giáp tý (ngày 3-8-1964) bị triều đình Huế xử chém.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','08', 'Tính đến ngày 3-8-1946 là tròn một nǎm nhân dân ta chống giặc dốt. Trong cả nước có 34.450 giáo viên, đã mở được 60.510 lớp, xoá mù chữ cho 1.320.870 người.\nCác tỉnh tiên phong về chống nạn mù chữ là Bắc Ninh, Bắc Giang, Hưng Yên, Phú Thọ, Nghệ An, Hà Tĩnh và Quảng Bình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','08', 'Ngày 3-8-1950, Đoàn cố vấn quân sự đầu tiên của Hoa Kỳ (MAAG - Military Assistance Advisory Group) gồm 35 người đến miền Nam Việt Nam để huấn luyện quân ngụy Sài Gòn tiếp nhận vũ khí viện trợ của Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','08', 'Ngày 3-8-1968, quân và dân tỉnh Quảng Bình bắn rơi chiếc máy bay Mỹ thứ 500. Nhân dịp này, Hồ Chủ tịch đã ký sắc lệnh thưởng Huân chương Độc lập hạng Nhì và gửi thư khen quân dân tỉnh Quảng Bình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '03','08', 'Pie Đơ Phécma (Pierre de fermat) sinh ngày 3-8-1601 trong một gia đình buôn nhỏ nước Pháp. Ông được coi là một nhà toán học lớn. Cùng với đồng nghiệp Phécma đã đặt nền móng cho nhiều bộ môn như lý thuyết số, hình học giải tích, giải tích toán học, lý thuyết xác suất v.v... Nhưng phát kiến ưu tú nhất của ông là về lĩnh vực lý thuyết số. Một cống hiến quan trọng nữa của Phécma là Định lý nhỏ Phécma. Định lý này là một định lý cơ bản cho lý thuyết số.\nPhécma mất ngày 15-1-1665.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','09', 'Anh hùng dân tộc Trần Quốc Tuấn sinh năm 1226 quê ở làng Tức Mặc, Mỹ Lộc tỉnh Nam Định.\nÔng có tài quân sự, khi giặc Nguyên sang cướp nước ta, ông được Trần Nhân Tông phong làm Tiết chế các đạo quân thuỷ bộ. Thế giặc mạnh, nhà vua lo ngại muốn tạm hàng, ông khảng khái nói: \"Bệ hạ muốn hàng, xin hãy chém đầu tôi trước rồi hãy hàng\". Ông làm \"Hịch tướng sĩ\" khích lệ lòng quân, đôn đốc các vương hầu, binh tướng tận trung cứu nước. Dưới tài lãnh đạo của ông, quân dân ta chiến thắng vang dội ở Chương Dương, Hàm Tử, Vạn Kiếp, Bạch Đằng, đuổi giặc ra khỏi nước. Ông được thăng chức Hưng Đạo Vương. Ông thường tiến cử nhiều người có tài ra giúp lập nên nghiệp lớn như Phạm Ngũ Lão, Dã Tượng, Yết Kiêu ... bất kể họ thuộc thành phần nào trong xã hội. Ngoài \"Hịch tướng sĩ\" ông còn là tác giả của \"Binh thư yếu lược\" và \"Vạn Kiếp tông bí truyền thư\".\nÔng mất ngày 3-9-1300, thọ 74 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','09', 'Nguyễn Huy Tự là nhà thơ đời Lê Hiển Tông, có tên khác nữa là Yên, tự Hữu Chi, hiệu Uẩn Trai. Ông sinh ngày 3-9-1743 tại Trường Lưu, Lại Thạch, La Sơn, tỉnh Hà Tĩnh. Ông là con Thám hoa Nguyễn Huy Oánh. Năm 1759 ông đỗ Hương Cống. Từ năm 1767, ông nhậm chức Tri phủ Quốc Oai. Năm 1770, ông được ưu đãi theo hàng Tiến sĩ, thăng Hiến sát xứ Sơn Nam, sau đó cải sang võ chức tước Nhạc Đình Bá, rồi làm Đốc đồng Sơn Tây. Ông là tác giả truyện thơ nổi tiếng \"Hoa Tiên\".\nKhi nhà Hậu Lê suy vong, năm 1789 ông được vua Quang Trung triệu tập vào Phú Xuân, bổ chức Hữu thị lang. Nhưng chẳng bao lâu, ngày 5-9-1790 ông mất, thọ 47 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','09', 'Ngày 3-9-1945, Hồ Chủ tịch đã chủ toạ phiên họp đầu tiên của Chính phủ Lâm thời nước Việt Nam Dân chủ Cộng hoà.\nNgười đề ra những nhiệm vụ cấp bách để ổn định tình hình trong nước, giữ vững và củng cố chính quyền dân chủ nhân dân. Nội dung cụ thể là:\n1. Phát động phong trào tăng gia sản xuất để chống nạn đói, mở ngay cuộc lạc quyên gạo để giúp đỡ người nghèo.\n2. Mở chiến dịch chống nạn mù chữ.\n3. Tổ chức càng sớm càng hay cuộc tổng tuyển cử với chế độ phổ thông đầu phiếu, soạn thảo Hiến pháp dân chủ.\n4. Mở chiến dịch giáo dục cần, kiệm, liêm, chính để bài trừ những thói hư tật xấu của chế độ thực dân để lại.\n5. Bỏ ngay thuế thân, thuế chợ, thuế đò, tuyệt đối cấm hút thuốc phiện.\n6. Ra tuyên bố tự do tín ngưỡng và đoàn kết lương giáo.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','09', 'Tối mồng 3-9-1960, Chủ tịch Hồ Chí Minh cùng các Trưởng đoàn đại biểu các Đảng Cộng sản và công nhân dự Đại hội đại biểu toàn quốc lần thứ ba của Đảng Lao động Việt Nam, tham dự dạ hội thanh niên tại vườn Bách Thảo, do Đoàn thanh niên Hà Nội tổ chức, để kỷ niệm lần thứ 15 ngày thành lập nước Việt Nam Dân chủ Cộng hoà và Đại hội đại biểu toàn quốc lần thứ 3 của Đảng.\nBác Hồ ân cần hỏi thăm các diễn viên, nhạc công, rồi bước lên sân khấu. Bác đề nghị chuyển cho Bác chiếc \"đũa\" chỉ huy của nhạc trưởng rồi nói: \"Trước khi các đại biểu ra về, chúng ta hãy cùng các vị hát bài Kết đoàn\".\nBác Hồ giơ cao chiếc đũa, bắt nhịp cho cả dàn đồng ca gồm hơn một ngàn thanh niên.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','10', 'Đại tướng Lê Trọng Tấn, sinh ngày 3-10-1914 ở xã Yên Nghĩa, huyện Hoài Đức, Hà Tây, qua đời năm 1986 tại Hà Nội.\nThuở nhỏ ông học ở Hà Nội, tham gia Cách mạng từ năm 1944. Tháng 3-1945, ông tổ chức các cở cách mạng và huấn luyện tự vệ vũ trang ở huyện Ứng Hoà, tỉnh Hà Đông cũ.\nTrong hai cuộc kháng chiến, ông từng đảm nhiệm các chức vụ: Uỷ viên Trung ương Đảng, uỷ viên thường vụ Đảng uỷ quân sự Trung ương, Thứ trưởng Bộ quốc phòng, Tổng tham mưu trưởng Quân đội nhân dân Việt Nam, đại biểu Quốc hội. Từ năm 1960 đến năm 1975 ông giữ các chức vụ: Uỷ viên quân uỷ miền nam, Phó tư lệnh quân giải phóng miền Nam. Ông là một vị tướng xuất sắc của quân đội ta.\nÔng đã được Nhà nước trao tặng nhiều Huân chương Quân công và Huân chương Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','10', 'Ngày 25-9-1945, Hội nghị Công đoàn thế giới khai mạc tại Pari (Pháp). Dự hội nghị có 272 đại biểu của 56 nước thay mặt 64 triệu đoàn viên. Ngày 3-10, Hội nghị đã thông qua bản điều lệ của Liên hiệp công đoàn thế giới. Ngay ngày hôm đó, Hội nghị tiến hành đại hội Công đoàn thế giới lần thứ nhất và thông qua những văn kiện nêu rõ lập trường và đường lối phấn đấu của Liên hiệp Công đoàn thế giới.\nViệc thành lập Liên hiệp công đoàn thế giới là một thắng lợi to lớn, là bước tiến dài của quá trình thống nhất giai cấp công nhân thế giới đấu tranh cho các quyền lợi của công nhân, cho hoà bình, dân chủ và độc lập dân tộc của cả nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','10', 'Ngày 3-10-1945, Chủ tịch Hồ Chí Minh để ký sắc lệnh đặt Nha tổng thanh tra kỹ nghệ và khoáng chất trong Bộ Quốc dân kinh tế. Tiếp đó nước ta chiến tranh suốt 9 năm. Do đó, từ năm 1955 ngành địa chất Việt Nam mới thực sự hoạt động mang tính hệ thống chuyên ngành.\nTrong hơn 50 năm qua, Ngành địa chất đã đẩy mạnh thăm dò khoáng sản, có hơn 3000 báo cáo về các thông tin địa chất, địa vật lý, địa chất thuỷ văn, địa chất môi trường, các khoáng sản độc lập lưu trữ quốc gia, đã và đang đóng phần tích cực tạo cơ sở để phát triển các ngành công nghiệp nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','11', 'Ngày 3-11-1946, Chính phủ mới do chủ tịch Hồ Chí Minh đứng đầu đã ra mắt các đại biểu Quốc hội khoá I ở kỳ họp thứ hai gồm có:\n- Hồ Chí Minh, Chủ tịch kiêm Bộ trưởng Bộ Ngoại giao.\n- Huỳnh Thúc Kháng, Bộ trưởng Bộ Nội vụ\n- Võ Nguyên Giáp, Bộ trưởng Bộ Quốc phòng\n- Nguyễn Vǎn Huyên, Bộ trưởng Bộ Giáo dục\n- Lê Vǎn Hiến, Bộ trưởng Bộ Tài chính\n- Trần Đǎng Khoa, Bộ trưởng Bộ Giao thông Công chính\n- Hoàng Tích Trí, Bộ trưởng Bộ Y tế\n- Nguyễn Vǎn Tạo, Bộ trưởng Bộ Lao động\n- Vũ Đình Hoè, Bộ trưởng Bộ Tư pháp\n- Ngô Tấn Nhơn, Bộ trưởng Bộ Canh nông\n- Chu Bá Phượng, Bộ trưởng Bộ Cứu tế\n- Một vị ở Nam Bộ là Bộ trưởng Bộ Kinh tế\n- Nguyễn Vǎn Tố, Bộ trưởng không bộ\n- Bồ Xuân Luật, Bộ trưởng không bộ')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','11', 'Từ ngày 3 đến ngày 22-11-1967, quân giải phóng Miền Nam đã tiến công địch ở Đắc Tô (Tỉnh Kon Tum)\nSau 19 ngày đêm chiến đấu, ta đã tiêu diệt gần 3.500 tên địch (có 2.800 tên Mỹ), bắn rơi và phá huỷ 32 máy bay. Quân giải phóng còn đánh thiệt hại nặng Lữ đoàn 1 của Sư đoàn bộ binh số 4 Mỹ là một chiến đoàn dù ngụy.\nĐể ghi nhớ chiến công oanh liệt, tỉnh Kom Tum đã dựng tượng đài chiến thắng Đắc Tô ở nơi xảy ra sự kiện.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','11', 'Sau khi đế quốc Mỹ phải xuống thang, chấm dứt ném bom miền Bắc nước ta, ngày 3-11-1968, Chủ tịch Hồ Chí Minh đã ra lời kêu gọi đồng bào và chiến sĩ cả nước. Người nói: \"Hễ còn một tên xâm lược trên đất nước ta thì ta còn phải tiếp tục chiến đấu quét sạch nó đi\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '03','11', 'Ngày 3-11-1957, Liên Xô phóng thành công vệ tinh nhân tạo Sputnik II mang theo con chó Laika.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','12', 'Đồng chí Ngô Gia Tự sinh ngày 3-12-1908 tại làng Tam Sơn, huyện Tiên Sơn, tỉnh Bắc Ninh.\nNǎm 1927, đồng chí dự lớp huấn luyện chính trị do Nguyễn Ái Quốc tổ chức tại Quảng Châu (Trung Quốc). Tháng 3-1929, đồng chí tham gia thành lập chi bộ cộng sản đầu tiên ở Việt Nam. Sau khi Đảng cộng sản Việt Nam được thành lập, đồng chí Ngô Gia Tự được bầu làm Bí thư Xứ uỷ lâm thời của Đảng bộ Nam Kỳ. Đến cuối nǎm 1930, đồng chí bị địch bắt tại Sài Gòn và đầy ra Côn Đảo tháng 5-1933.\nĐồng chí Ngô Gia Tự đã hy sinh trong chuyến vượt biển đầu nǎm 1935.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','12', 'Ngày 3-12-1945, tại Hà Nội, Hội nghị đại biểu các dân tộc thiểu số toàn quốc đã khai mạc. Lần đầu tiên đại biểu các dân tộc ít người ở Việt Bắc, Tây Bắc đến Tây Nguyên về họp mặt ở thủ đô Hà Nội, nhằm biểu dương tình đoàn kết giữa các dân tộc. Trong diễn vǎn khai mạc, Hồ Chủ tịch nói: \"Trước kia các dân tộc để giành độc lập phải đoàn kết, bây giờ để giữ lấy độc lập còn cần đoàn kết hơn nữa\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','12', 'Từ đêm ngày 3-12-1946, Hồ Chủ tịch chuyển đến ở và làm việc tại làng Vạn Phúc, thị xã Hà Đông. Tại đây, Hồ Chủ tịch đã chủ trì nhiều cuộc họp quan trọng bàn việc chuẩn bị kháng chiến, và Người viết \"Lời kêu gọi toàn quốc kháng chiến\" gửi đồng bào và chiến sĩ toàn quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','12', 'Ông Hà Huy Giáp sinh nǎm 1908, người Huyện Hương Sơn tỉnh Hà Tĩnh, từ trần ngày 3-12-1995.\nÔng gia nhập Đảng cộng sản Đông Dương từ nǎm 1930 và nǎm đó được chỉ định vào Xứ uỷ Nam Kỳ.\nTháng 8-1945, ông được cử đi dự đại hội Tân Trào, rồi về Nam Bộ tham gia kháng chiến.\nNǎm 1951, ông được bầu làm Uỷ viên dự khuyết Ban Chấp hành Trung ương Đảng khoá II, phụ trách tuyên huấn của Trung ương cục miền Nam.\nTừ nǎm 1956 đến 1987, ông Hà Huy Giáp đã giữ các chức vụ: Phó trưởng ban Tuyên huấn Trung ương, Thứ trưởng Bộ Giáo dục, Uỷ viên Trung ương Đảng khoá III, Bí thư đảng đoàn Bộ Vǎn hoá và Hội Vǎn nghệ, Phó trưởng ban nghiên cứu lịch sử Đảng Trung ương, Viện trưởng Viện bảo tàng Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '03','12', '3-12-1998, Việt Nam tham gia vào đàm phán để gia nhập tổ chức Thương mại thế giới WTO.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '03','12', 'Ngày 3-12-1910, những đèn ống đầu tiên không dùng dây tóc được sử dụng để chiếu sáng đường phố nước Pháp.\nNgười phát minh ra đèn ống là Clốt. Hiện nay, đèn ống nêông được sử dụng rộng rãi, đặc biệt trong các gia đình. Ưu điểm của nó là tiêu thụ điện nǎng ít, ánh sáng dịu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '03','12', '3-12-1993, Chính phủ Angola và quân phiến loạn đồng ý ký kết hiệp định ngừng bắn trong cuộc chiến tranh kéo dài 18 nǎm của họ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','01', 'Từ ngày 4 đến 7-1-1983 Bộ Chính trị BCH TW Đảng họp và ra Nghị quyết về \"Công tác thủ đô Hà Nội\". Bản nghị quyết nhấn mạnh Hà Nội là trung tâm đầu não về chính trị, trung tâm lớn nhất về vǎn hoá, khoa học - kỹ thuật, và là một trung tâm lớn về kinh tế.\nNghị quyết chỉ rõ: Xây dựng Hà Nội xứng đáng với vị trí quan trọng của nó là yêu cầu khách quan của sự nghiệp Cách mạng XHCN và Xây dựng CNXH trên đất nước ta, đồng thời là trách nhiệm và nguyện vọng tha thiết của nhân dân cả nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','01', 'Ixǎc Niutơn, nhà vật lý, toán học nước Anh, người được thế giới tôn là \"người sáng lập ra vật lý học cổ điển\". Ông sinh ngày 4-1-1642.\nNǎm 17 tuổi ông đã tìm ra nhị thức trong toán học giải tích gọi là \"Nhị thức Niutơn\". Cống hiến lớn nhất khiến tên tuổi ông trở thành bất tử là \"Nguyên lý vạn vật hấp dẫn\". Đây là nguyên lý cơ sở cho những phát minh vật lý, cơ học, thiên vǎn học trong nhiều thế kỷ. Tiếp đó, những phát kiến về thiên vǎn học của ông dựa vào định luật vạn vật hấp dẫn đã giáng một đòn chí mạng vào uy tín của giáo hội. Ông là Chủ tịch Hội khoa học hoàng gia Anh, là hội viên danh dự của nhiều hội khoa học và viện sĩ của nhiều viện hàn lâm.\nÔng mất ngày 31-3-1727.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','01', 'Iacốp Grim (Jacob Grimm) sinh ngày 4-1-1785, mất nǎm 1863 và em là Vinhem Grim (Wilhl Grimm) (sinh nǎm 1786 mất nǎm 1859) là hai nhà ngữ vǎn và vǎn học cổ đại dân gian của nước Đức.\nHai anh em có những hoạt động chung trong cuộc đời và sự nghiệp vǎn học, đều là Viện sĩ Hàn lâm Berlin. Nhiều tác phẩm chung của hai anh em có giá trị như: Từ điển tiếng Đức; Truyện cổ trong nhà và cho trẻ em (Gồm hơn 200 truyện nổi tiếng thế giới).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','01', 'Lui Brai sinh ngày 4-1-1809 và mất nǎm 1852.\nÔng là người Pháp bị mù từ thuở lên ba, vào học trường mù nǎm lên 10 tuổi, rồi trở thành giáo sư của trường này. Ông đã phát minh ra hệ thống chữ bằng những hạt nổi, gọi là \"chữ Brai\" và cách ghi nốt nhạc cho người mù được cả thế giới chấp nhận.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','01', 'Écvin Srốtđingdơ (Erwin Scgrodingre), nhà vật lý lý thuyết thiên tài người Áo, người có công xây dựng nền vật lý học hiện đại trên cơ sở toán học. Ông sinh ngày 12-8-1887.\nNǎm 1926 ông đã đề ra cách biểu diễn mới về cấu trúc của nguyên tử với phương trình ngày nay vẫn mang tên ông. Nǎm 1933 ông được tặng giải thưởng Noben vật lý. Ông qua đời ngày 4-1-1961.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','01', '4-1-1948, Anh trao trả độc lập cho Burma')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','01', 'Anbe Camuy sinh nǎm 1913, qua đời ngày 4-1-1960.\nNhững tác phẩm chính của ông gồm có các tiểu thuyết \"Người xa lạ\", \"Bệnh dịch hạch\", các vở kịch \"Caliguyla\", \"Ngộ nhận\" và một số tiểu luận. Nǎm 1957 Camuy được nhận giải thưởng Noben vì tác phẩm của ông đã \"mang ra ánh sáng những vấn đề hiện nay đặt ra cho lương tâm con người\".\nCùng với Xáctơrơ, Camuy là \"người thầy tư tưởng\" của mấy thế hệ thanh niên Pháp sau đại chiến thế giới lần thứ hai. Ông là hai nhà lý luận và nhà vǎn của chủ nghĩa hiện sinh - triết học duy tâm của cái phi lý, của những lớp người mất lòng tin sau sự tàn phá có tính huỷ diệt của chủ nghĩa phát xít.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','01', 'Ngày 4-1-1984, tại Phnôm Pênh, Thủ đô Vương quốc Campuchia đã tổ chức lễ khánh thành Tượng đài các chiến sĩ quốc tế Việt Nam.\nĐây là một công trình vǎn hoá ghi nhớ tình hữu nghị anh em, tình đoàn kết chiến đấu và sự hy sinh cao cả của các chiến sĩ tình nguyện Việt Nam vào sự nghiệp giải phóng dân tộc Campuchia khỏi hoạ diệt chủng của bè lũ Pônpốt Iêng Xary, góp phần xây dựng nhà nước Campuchia độc lập và hữu nghị.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','02', 'Danh sĩ Nguyễn Cư Trinh, sinh ngày 4-2-1716, quê Nghệ An và qua đời 1767.\nÔng đỗ cử nhân, có tài vǎn võ, làm quan đến chức Thượng thư Bộ Lại, nổi tiếng là người liêm chính, giỏi việc chính trị, doanh điền và ngoại giao. Khi vào miền Nam mở mang bờ cõi, khẩn hoang lập ấp, ông có công lớn dưới thời chúa Nguyễn.\nNguyễn Cư Trinh sáng tác nhiều thơ vǎn viết bằng chữ Hán và chữ Nôm, đặc sắc nhất là Truyện Sãi Vãi và Độn Am thi tập.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','02', 'Ngày 4-2-1947, thành lập Nha nghiên cứu kỹ thuật (thuộc Cục Quân giới) đến nǎm 1951 đổi là Viện Nghiên cứu Quân giới.\nTrong cuộc kháng chiến chống thực dân Pháp, Viện đã nghiên cứu, chế tạo SKZ 60 bắn đạn lõm, một loại súng không giật có kiểu dáng hoàn toàn độc đáo Việt Nam, xuyên được 60 xǎngtimet bê tông, đã lập công ở trận phố Lu (Lao Cai) đầu nǎm 1950, ở đèo Mǎng Giang (Bình Định) tháng 4-1954, làm cho quân Pháp khiếp sợ. Sau SKZ 60, Viện Nghiên cứu Quân giới đã cho ra đời SKZ 120 và SKZ 81, cả hai loại súng này đều được sản xuất và ứng dụng ở các chiến trường có hiệu quả.\nRiêng công trình nghiên cứu chế tạo thành công SKZ 60 đã được Nhà nước tặng giải thưởng Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','02', 'Ngày 4-2-1963, Đại hội đoàn kết Á Phi lần thứ 10 ra quyết định công nhận Uỷ ban đoàn kết Á Phi của miền Nam là hội viên chính thức, và cử đại biểu của nhân dân miền Nam vào Ban thư ký thường trực của Hội đồng đoàn kết Á Phi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','02', 'Ngày 4-2-1968, Hồ Chủ tịch gửi đến tới Chủ tịch Nguyễn Hữu Thọ và Trung ương Mặt trận dân tộc giải phóng miền Nam Việt Nam về đại thắng mùa xuân Mậu Thân. Người chỉ rõ \"Càng gần thắng lợi càng nhiều gian nan. Quân địch như con thú dữ đến bước đường cùng, càng giẫy giụa điên cuồng, quân và dân ta càng phải tǎng cường đoàn kết, kiên trì chiến đấu, đánh mạnh, đánh liên tục giành những thắng lợi to lớn hơn nữa\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','02', '4-2-1950, Việt Nam Dân chủ Cộng hoà và Ba Lan thiết lập quan hệ ngoại giao đầy đủ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','02', 'Từ ngày 4 đến 11 tháng 2 nǎm 1945, Hội nghị Yalta (Crưm) giữa Stalin, Rudơven, Sơcsin bàn về các biện pháp tiêu diệt hoàn toàn bọn phát xít Đức, quy định số phận của nước Đức sau chiến tranh và thành lập một tổ chức quốc tế để duy trì hoà bình và an ninh thế giới. Liên Xô cam kết sau khi thắng Đức sẽ chuyển sang tiến hành chiến tranh chống Nhật.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','02', '4-2-1974, Grenada giành được độc lập trong khối Thịnh vượng chung của Anh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','02', 'Ngày này năm 1964, Malta hoàn toàn độc lập.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','02', 'Ngày này năm 1948, Ceylon trở thành quốc gia độc lập thuộc khối Thịnh vượng chung. Sau đó đổi tên thành Sri Lanka.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','02', 'Ngày 4-2-1961, những người yêu nước thuộc \"Phong trào nhân dân giải phóng Ǎngôla\" (MPLA) tấn công vào nhà tù, đài phát thanh, trại lính ở thủ đô Luanda, mở đầu cuộc đấu tranh vũ trang chống thực dân Bồ Đào Nha giành độc lập dân tộc')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','02', 'Ngày 4-2-1962, Chính phủ Cách mạng Cuba công bố tuyên ngôn La Habana thứ hai, nêu lên tư tưởng và mục tiêu của cách mạng Cuba là chống đế quốc, giải phóng dân tộc, tiến lên xây dựng chủ nghĩa xã hội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','02', 'Ngày 4-2-1976, một trận động đất tấn công Guatemala và Honduras, giết hại hơn 22.000.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','02', '4-2-1998, khoảng 5.000 người đã bị thiệt mạng khi một trận động đất 6,1 độ richte xảy ra tại vùng đông bắc Afghanistan.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','03', 'Ngày 4-3-1919, Thành lập Công ty Mỏ và Luyện kim Đông Dương. Thực hiện khai thác thuộc địa, thực dân Pháp đã ra sức bóc lột tài nguyên và nguồn nhân công rẻ mạt ở Đông Dương. Chúng tiến hành khai thác các mỏ kẽm, chì, bạc ở Bắc Cạn và một số địa phương khác. Đến thời điểm nǎm 1941 sản lượng khai thác là 17.801 tấn quặng, 6.251 tấn kim loại.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','03', 'Ngày 4-3-1949, mở đầu chiến dịch Đông Bắc, quân ta phục kích ở Điền Xá, cách thị trấn Tiên Yên, Quảng Ninh 15 km - diệt 16 xe và 150 tên địch. Sau thắng lợi này, chiến dịch Đông Bắc phát triển tiêu diệt địch ở đường Đông Khê - Cao Bằng, đồn Bản Sâm (Lạng Sơn), đồn Ba Chẽ (Quảng Ninh), đồn Bản Trại, cầu Bản Trại và bức rút các vị trí địch ở Pò Mã, Bản Phan, Pò Vao...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','03', 'Mặc dù được quân Pháp canh phòng cẩn mật nhưng đêm 4-3-1954, các chiến sĩ ta đã khôn khéo, bí mật vượt qua hàng rào thép gai dầy đặc với trên 1000 quân địch, bố trí ở 8 lô cốt, 4 ụ đại bác 75 mm và tuần tiễu bảo vệ khu vực sân bay Gia Lâm. Các chiến sĩ ta đã đột nhập vào sân bay đặt mìn phá huỷ 5 chiếc máy bay B26, 10 chiếc Đatôta, 4 chiếc chở khách, 1 kho xǎng và 2 máy phát điện. Chiến công này của quân dân Hà Nội đã góp phần hạn chế các đợt ném bom và tiếp tế cho quân Pháp trên các mặt trận ở miền Bắc. Quân Pháp rất lo lắng trước lối đánh kiên quyết, táo báo của quân ta và phải mất một thời gian mới khôi phục lại được hoạt động của sân bay.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','03', 'Qua 2 cuộc đấu tranh phá hoại của Đế quốc Mỹ, cầu Long Biên bị phá hỏng nặng đã được hoàn thành công tác khôi phục vào ngày 4-3-1973 sau 41 ngày đêm lao động khẩn trương và sáng tạo.\nCũng trong dịp này, 5 cầu lớn khác ở miền Bắc bị bom phá hại như: Cầu Đuống, cầu Đáp Cầu, cầu Bắc Giang, cầu Tào Xuyên và cầu Hàm Rồng đã thông xe.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','03', 'Ngày 4-3-1975 được coi là ngày mở màn chiến dịch Tây Nguyên. Với thắng lợi quan trọng là giải phóng thành phố Buôn Ma Thuột tạo đà cho sự phát triển của chiến dịch, giành thời cơ, kiên quyết tấn công làm tan rã những mảng lớn quân ngụy Sài Gòn, đưa cuộc kháng chiến chống Mỹ cứu nước vào bước ngoặt quyết định.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','04', 'Võ Duy Thanh sinh nǎm 1807, quê ở làng Bồng, huyện Yên Khánh, tỉnh Ninh Bình mất ngày 4-4-1861.\nÔng tuy đỗ bảng nhãn nhưng tài đáng Trạng Nguyên nên người đương thời gọi ông là Trạng Bồng. Khi giặc Pháp đánh Đà Nẵng (nǎm 1858), ông dâng sớ lên vua, kiến nghị tǎng cường lực lượng quốc phòng, chấn chỉnh nội trị, cải cách học thuật, vǎn hoá, kinh tế. Lúc dạy ở Quốc Tử Giám, và khi làm chủ khảo các khoa thi, ông đã phát hiện và rèn luyện được nhiều nhân tài.\nCho đến khi sắp qua đời, Võ Duy Thanh vẫn còn lưu lại một bài trần tình xin triều đình cải cách mọi mặt để đối phó với quân Pháp xâm lược nước ta.\nÔng còn để lại hai tác phẩm: Trừng Phủ thi tập và Bông Châu Vũ tiên sinh thi vǎn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','04', 'Sáng chủ nhật 4-4-1965, máy bay Mỹ ném bom, bắn rốc két xuống xã Quang Trung, huyện Quảng Xương, tỉnh Thanh Hoá.\nNgồi dưới hầm trú ẩn, nghe có tiếng gọi mình, Nguyễn Bá Ngọc vội chạy lên cứu được hai em nhỏ hàng xóm khỏi bom đạn thù. Còn Ngọc đã hy sinh, lúc em mới 14 tuổi. \nNguyễn Bá Ngọc được công nhận là liệt sĩ và được truy tặng huân chương chiến công hạng ba.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','04', 'Ngày 4-4-1975, thành phố Đà Lạt và tỉnh Lâm Đồng được giải phóng. Tỉnh Lâm Đồng là cao nguyên thứ ba và cao nhất của Tây Nguyên, có diện tích 10 nghìn 173km2 vuông và số dân khoảng 742.900 người.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','04', 'Ngày 4-4-1765, Mikhain Vasiliêvích Lômônôxốp, nhà bác học người Nga đã từ trần.\nÔng sinh ngày 8-11-1711. Sự sáng tạo và cống hiến của ông cực kỳ đa dạng, đặc biệt trong vật lý và hoá học (định luật bảo toàn vật chất và vận động), trong nghiên cứu địa chất và địa lý, trong xây dựng ngành mỏ, luyện kim và đồ sứ.\nNgoài ra, Lômônôxốp còn là một nhà thơ yêu nước, tiêu biểu cho tinh thần Nga thời đại đế Piốt I.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','04', '4-4-1947, Tổ chức Hàng không Quốc tế được thành lập nhằm đảm bảo sự an toàn cho các chuyến bay.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','04', 'Ngày 4-4-1949 thành lập tổ chức Hiệp ước Bắc đại Tây Dương (NATO), các nước thành viên đầu tiên là Mỹ, Pháp, Anh, Canađa, Đan Mạch, Na Uy, Island, Italia, Bồ Đào Nha và các nước Benelux (Bỉ, Hà Lan, Lúcxǎmbua). Trên thực tế, Tổ chức Bắc đại Tây dương là khối quân sự của các nước tư bản và do Mỹ chi phối. Từ khi ra đời cho đến nay, tổ chức này lấy mục tiêu uy hiếp các nước XHCN Đông Âu và các Quốc gia khác. Sau khi Liên Xô tan rã các nước XHCN Đông Âu đi chệch con đường XHCN - một số nước toan tính ra nhập khối này. Nga là nước phản đối quyết liệt kế hoạch Đông tiến của NATO.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','04', 'Ngày này năm 1960, Pháp và Mali ký Hiệp ước công nhận nền độc lập của Mali trong liên hiệp Pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','04', 'Ngày 4-4-1968, Mục sư Tin lành người Mỹ da đen Luthơkinh bị sát hại.\nÔng là một chiến sĩ hoà bình nổi tiếng không chỉ riêng đối với người Mỹ da đen mà còn đối với nhân loại. Ý chí hoà bình, bình đẳng và quyết tâm hành động của ông được đông đảo nhân dân tiến bộ Mỹ và thế giới ủng hộ. Vì thế mà chính quyền Mỹ rất lo sợ và tìm cách hại ông nhằm đàn áp phong trào hoà bình Mỹ. Cái chết anh hùng của ông trên ghế điện của chính quyền Mỹ làm dấy lên sự cǎm giận của nhân dân Mỹ và loài người tiến bộ. Mục sư Luthơkinh đã mất nhưng tinh thần của ông mãi mãi còn lại trong tâm trí người Mỹ da đen, người Mỹ yêu hoà bình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','05', 'Ông Trần Đǎng Khoa sinh ngày 4-5-1907 ở ngoại ô thành phố Huế và qua đời nǎm 1989.\nThông minh và hiếu học, ông đã tốt nghiệp Đại học Giao thông công chính và từng phụ trách ngành này ở nhiều tỉnh miền trung.\nSau Cách mạng tháng Tám nǎm 1945, ông được giao nhiều chức vụ quan trọng: Giám đốc công chính Trung bộ, Bộ trưởng Bộ Giao thông công chính, Bộ trưởng Bộ Thuỷ lợi và Kiến trúc, Bộ trưởng Bộ thuỷ lợi.\nÔng Trần Đǎng Khoa là đại biểu quốc hội từ khoá I đến khoá VII, Phó chủ tịch Uỷ ban thường vụ Quốc hội, từ khoá II đến khoá VI.\nÔng gia nhập Đảng Dân chủ Việt Nam nǎm 1945 và trong nhiều nǎm là Phó tổng thư ký của Đảng này.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','05', 'Ngày 4-5-1939, Đám tang Phan Thanh và cuộc biểu dương lực lượng quần chúng đã diễn ra tại Hà Nội.\nPhan Thanh là một chiến sĩ xuất sắc của phong trào Mặt trận Dân chủ. Nhân ngày ông mất, Xứ ủy Bắc Kỳ chủ trương tổ chức tang lễ thật trọng thể, vừa để bày tỏ lòng thương tiếc đối với một chiến sĩ đấu tranh cho sự nghiệp dân chủ, vừa để biểu dương lực lượng quần chúng một cách hợp pháp.\nGần 2 vạn đại biểu của tầng lớp nhân dân, các đoàn thể của 14 tỉnh đã tham dự.\nPhản ánh sự kiện này, báo cáo của Nguyễn Ái Quốc đã ghi: \"Dân chúng đã tổ chức một lễ tang rất trọng thể... Đám tang dài gần 2km... chưa bao giờ có một đám tang lớn như thế ở Hà Nội\" \nCuộc biểu dương lực lượng này đã làm cho bọn cầm quyền ở Hà Nội lúng túng và hoảng sợ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','05', 'Ngày 4-5-1954, theo lời mời của chính phủ Liên Xô và Chính phủ nước Cộng hoà nhân dân Trung Hoa, đoàn đại biểu chính phủ Việt Nam Dân chủ cộng hoà do đồng chí Phạm Vǎn Đồng, phó Thủ tướng kiêm Bộ trưởng Ngoại giao làm trưởng đoàn đã đến Giơnevơ để bàn vấn đề lập lại hoà bình ở Đông Dương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','05', 'Từ ngày 4 đến ngày 6-5-1962 tại Hà Nội diễn ra Đại hội liên hoan anh hùng, chiến sỹ thi đua toàn quốc lần thứ ba. Về dự Đại hội có hơn một nghìn đại biểu tuyển lựa từ cơ sở thuộc đủ các ngành, các giới như: Công nghiệp, nông nghiệp, lực lượng vũ trang, lao động trí óc, vǎn nghệ sỹ, sinh viên, học sinh v.v...\nĐại hội đã tuyên dương 4 đơn vị lá cờ đầu là: Nhà máy cơ khí Duyên Hải (Hải Phòng), Hợp tác xã nông nghiệp Đại Phong (Quảng Bình), Hợp tác xã thủ công nghiệp Thành Công (Thanh Hoá), Trường phổ thông cấp II Bắc Lý (Hà Nam), Phong trào ba nhất của quân đội và 45 Anh hùng lao động.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','05', 'Ngày 4-5-1869, Râulen (Rowland), người Mỹ đǎng ký phát minh đầu tiên về giàn khoan cố định ngoài biển để khai thác dầu mỏ và khí đốt. \nViệc khai thác dầu mỏ dưới biển đã được tiến hành lần đầu tiên dọc theo bờ biển Caliphoócnia (Hoa Kỳ) vào nǎm 1897.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','05', 'Ngày 4-5-1919, phong trào Ngũ Tứ ở Trung Quốc nổ ra, mở đầu bằng cuộc biểu tình của 5000 học sinh yêu nước Bắc Kinh nhằm chống lại âm mưu xâu xé Trung Quốc của các nước đế quốc. Cuộc biểu tình của học sinh Bắc Kinh đã nhanh chóng lôi léo đông đảo học sinh và các tầng lớp nhân dân trong nhiều thành phố xuống đường đấu tranh. Chính phủ quân phiệt Bắc Kinh tiến hành đàn áp, bắt giam hơn 1000 học sinh yêu nước. \nNgày 3-6, biết tin khủng bố của bọn cầm quyền, công nhân Thượng Hải quyết định bãi công. Hưởng ứng cuộc bãi công của công nhân Thượng Hải, công nhân các ngành ở Thiên Tân, Nam Kinh, Vũ Hán cũng lần lượt bãi công, lôi kéo theo thương nhân bãi thị và học sinh bãi khoá, làm tê liệt mọi sinh hoạt trong các thành phố lớn.\nPhong trào bãi công to lớn và lan rộng của công nhân đã buộc Chính phủ Trung Hoa dân quốc phải thả tất cả các học sinh yêu nước bị giam giữ.\nPhong trào Ngũ Tứ đánh dấu thời kỳ giai cấp công nhân Trung Quốc đã trở thành lực lượng chính trị độc lập và bắt đầu bước lên vũ đài chính trị, tạo điều kiện cho chủ nghĩa Mác - Lênin được truyền bá rộng rãi ở Trung Quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','05', 'Máy bay lên thẳng đầu tiên bay được 1 km, vòng bay khép kín, là máy bay của Oehmichell vào ngày 4-5-1924.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','05', 'Ngày 4-5-1994, Chủ tịch Tổ chức giải phóng Palextin PLO Araphát và thủ tướng Itxraen Rabin ký hiệp định về quyền tự trị của người Palextin tại Gaza và Jericho. Theo hiệp ước này, Itxraen sẽ thả 5.000 tù nhân Palextin trong vòng hai tuần và rút khỏi Gaza và Jericho.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','06', 'Đồng chí Châu Vǎn Liêm sinh nǎm 1902 tại tỉnh Cần Thơ trong một gia đình nông dân nghèo. Sau khi học sư phạm ở Sài Gòn, đông chí đi dạy học ở tỉnh Long Xuyên cũ. \nNǎm 1927, đồng chí vào Việt Nam thanh niên cách mạng đồng chí Hội. Nǎm 1929 đồng chí được cử vào Ban Thường vụ Kỳ bộ thanh niên Nam Kỳ, rồi tham gia sáng lập An Nam cộng sản Đảng.\nĐầu nǎm 1930, Châu Vǎn Liêm và Nguyễn Thiệu là hai đại biểu của An nam cộng sản Đảng đi dự hội nghị hợp nhất và trở thành những đồng chí sáng lập Đảng Cộng sản Việt Nam.\nSau đó, Châu Vǎn Liêm trở về Sài Gòn lãnh đạo công tác Đảng ở Nam Kỳ, đặc trách tỉnh Chợ Lớn. Đồng chí đã trực tiếp chỉ đạo cuộc biểu tình của nông dân Đức Hoà phản đối chính quyền thực dân ở tỉnh Chợ Lớn, ngày 4-6-1930. Bọn Pháp xả súng bắn vào đoàn biểu tình, và đồng chí Châu Vǎn Liêm đã anh dũng hy sinh.\nỞ thành phố Hồ Chí Minh có một đường phố mang tên Châu Vǎn Liêm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','06', 'Thực hiện chỉ thị của Hồ Chủ tịch, ngày 4-6-1945 Tổng bộ Việt Minh triệu tập Hội nghị cán bộ và tuyên bố chính thức thành lập khu giải phóng, bao gồm 2 cǎn cứ lớn nằm trên địa bàn của 6 tỉnh: Cao Bằng, Bắc Cạn, Lạng Sơn, Hà Giang, Tuyên Quang, Thái Nguyên và một số vùng thuộc ngoại vi các tỉnh Bắc Giang, Vĩnh Yên, Phú Thọ, Yên Bái...\nTrong Khu giải phóng, các Ủy ban nhân dân do dân cử được hình thành, và tổ chức thực hiện 10 chính sách lớn của Việt Minh. Khu giải phóng với gần một triệu đồng bào, thuộc nhiều dân tộc khác nhau đã bắt đầu được hưởng một cuộc sống mới do cách mạng đem lại, thực sự là \"hình ảnh nước Việt Nam mới\". Cùng với hệ thống các chiến khu trong cả nước, Khu giải phóng đã tạo thành một hậu phương và bàn đạp cho thế trận tổng khởi nghĩa giành chính quyền vào tháng 8 nǎm 1945.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','06', 'Buổi tối mồng 4-6-1956, Hồ Chủ tịch đã tới thǎm lớp bình dân học vụ ở chùa Vua và ở trường Nguyễn Du, Hà Nội. Hầu hết học viên ở hai lớp này là người lao động, ban ngày bận làm việc. Bác ân cần hỏi han việc học tập của họ và động viên chǎm chỉ tới lớp, thầy giáo tận tình dậy học.\nSinh thời, Hồ Chủ tịch rất quan tâm tới việc xóa nạn mù chữ và nâng cao trình độ vǎn hoá của nhân dân. Riêng với Hà Nội, Bác đã 13 lần đến thǎm các lớp bình dân học vụ và bổ túc vǎn hoá ở các khu lao động, đường phố và nhà máy.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','06', 'Ngày 4-6-1962, Hội đồng Chính phủ đã quyết định thành lập thành phố Việt Trì thuộc tỉnh Vĩnh Phú nay thuộc tỉnh Phú Thọ.\nViệt Trì nằm trên ngã ba sông Lô và sông Hồng. Đây là một thành phố công nghiệp hoàn toàn mới do cán bộ, công nhân ta xây dựng từ khi hoà bình lập lại (nǎm 1954). Việt Trì có các nhà máy như: Giấy, đường, dệt, hoá chất... Các nhà máy này đã góp phần vào công cuộc xây dựng CHXH ở miền Bắc sau giải phóng và trong sự nghiệp Công nghiệp hoá - hiện đại hoá đất nước hiện nay.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','06', 'Ngày 30-5-1985, Chủ tịch Hội đồng Nhà nước nước Cộng hoà xã hội chủ nghĩa Việt Nam Trường Chinh đã ký \"Pháp lệnh quy định giải thưởng Hồ Chí Minh và giải thưởng Nhà nước\", và \"Pháp lệnh quy định danh hiệu vinh dự Nhà nước để tặng các nghệ sĩ, nhà giáo thầy thuốc\". Pháp lệnh trên được công bố và có hiệu lực từ ngày 4-6-1985.\n\"Pháp lệnh quy định giải thưởng Hồ Chí Minh và giải thưởng Nhà nước\" nhằm tặng giải thưởng cho các công trình thuộc lĩnh vực khoa học, kỹ thuật và vǎn hoá, nghệ thuật. Giải thưởng Hồ Chí Minh đợt 1 được trao tặng ngày 10-9-1996 cho 33 cụm công trình khoa học - kỹ thuật và 44 cụm công trình vǎn hoá - nghệ thuật.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','06', 'Ngày 4-6-2000, một trận động đất cường độ lớn xảy ra tại tỉnh Bengkulu (Indonesia) làm 66 người thiệt mạng, hàng trǎm người bị thương và phá huỷ hàng loạt các công trình lớn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','07', 'Ngày 4-7-1905 Toàn quyền Đông Dương ra Nghị định thành lập tỉnh tự trị Plâycu, Tỉnh lỵ đặt tại Plâycu của dân tộc Gia Lai. Địa bàn tỉnh bao gồm toàn bộ vùng cư trú của đồng bào dân tộc thiểu số SêĐǎng, BaNa. Gia Lai tách từ tỉnh Bình Định ra. Tỉnh Gia Lai hiện nay chính là tỉnh Plâycu cũ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','07', 'Trong thời gian ở thǎm nước Pháp, ngày 4-7-1946 khi tới viếng đài tử sĩ trên đồi Valêriêng, Chủ tịch Hồ Chí Minh đã ghi vào sổ lưu niệm \"Quyền độc lập, tự do ở nước nào cũng vậy, đều do xương máu của nghĩa sĩ và đoàn kết của toàn quốc mà xây dựng nên. Vậy nên những người chân chính ham chuộng độc lập, tự do của nước mình thì phải kính trọng độc lập tự do của dân tộc khác\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','07', 'Theo quyết định của Hội nghị Giơnevơ, Đoàn đại biểu Bộ Tổng tư lệnh Quân đội nhân dân Việt Nam, do thiếu tướng Vǎn Tiến Dũng làm trưởng đoàn, và Đoàn đại biểu Bộ Tổng chỉ huy quân đội viễn chinh Pháp đã họp Hội nghị quân sự tại Trung Giã cách thị xã Thái Nguyên hơn 30 km.\nHội nghị diễn ra từ ngày 4 đến 27-7-1954. Nhiệm vụ và nội dung Hội nghị là: Bàn và đi tới những đề nghị về tất cả các vấn đề mà Hội nghị Giơnevơ đặt ra. Bàn về những vấn đề quân sự do tình hình cụ thể về tù binh, ngừng bắn điều chỉnh khu vực tập kết, Uỷ ban liên hợp.\nDo cố gắng của Đoàn đại biểu, sau hơn 20 ngày làm việc, Hội nghị quân sự Trung Giã đi đến những kết quả cụt thể như: giải quyết thoả đáng vấn đề trao trả tù binh, thoả thuận những biện pháp để thực hiện ngừng bắn trên toàn bộ chiến trường Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','07', 'Phrǎngxoa Rơnê đờ Satôbriǎng (Francois-Renéde Chateaubriand) là nhà vǎn Pháp. Ông sinh nǎm 1768 trong một gia đình quý tộc.\nTác phẩm xuất bản đầu tiên vào nǎm 1797 là \"Bàn về các cuộc cách mạng\". Nǎm 1802 ông công bố tác phẩm nổi tiếng \"Đạo Thiên Chúa\". Đây là lý do để Napôlêông trọng dụng ông trong Chính phủ. Sau đó ông có tác phẩm \"Hành trình từ Paris đến Jerusalem\", \"Những người Natxê\", \"Chuyến đi Mỹ\". Nǎm 1841, ông hoàn thành bản anh hùng ca thời đại của mình \"Hồi ký từ')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('4', '04','07', 'Mari Quiri sinh ngày 7-11-1867 tại vacsava, Ba Lan. Bà học Đại học ở Paris. Nǎm 1893 bà đỗ cử nhân vật lý. Nǎm 1894 đỗ thứ nhì cử nhân toán học. Nǎm 1898 bà cùng với chồng là nhà Bác học Pie Quiri đã phát hiện ra nguyên tố phóng xạ lớn mà ông bà đặt tên là Pôlôni, là nguyên tố mang tên quê hương Ba Lan của bà. Sau đó ông bà lại khám phá ra chất phóng xạ Rađi và sự tách ly được chất này từ một tấn quặng. Nǎm 1903, Viện Hàn Lâm khoa học Thụy Điển tặng ông bà giải thưởng Nôben về Vật lý và trường đại học Paris tặng Mari Quiri danh hiệu Tiến sĩ khoa học vật lý hạng xuất sắc.\nNǎm 1911 Mari Quiri được tặng giải thưởng Nôben lần thứ hai. Nǎm 1914 bà được bổ nhiệm làm Giám đốc Viện Rađiom. Đây là cơ sở đầu tiên trên thế giới sử dụng chất phóng xạ Rađi để điều trị bệnh ung thư. Sau đó bà được bầu vào Viện Hàn lâm Y học Pháp, Phó chủ tịch Uỷ ban quốc tế hợp tác trí thức.\nMari Quiri mất ngày 4-7-1934. Mặc dầu thời gian đã trôi đi, biết bao các phát minh khoa học vĩ đại ra đời, song nhân loại sẽ mãi mãi không quên người phụ nữ đã góp phần mở đầu cho nền khoa học nguyên tử của thế kỷ XX.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','08', 'Cuối tháng 7-1442. Vua Lê Thái Tông đi tuần ở miền Đông, duyệt quân ở Chí Linh. Nguyễn Trãi đón Vua ngự ở chùa Côn Sơn. Ngày 4-8-1442, Vua về đến Lệ Chi Viên (vườn vải) nay thuộc huyện Giá Lương, tỉnh Bắc Ninh. Cùng đi với Vua có Nguyễn Thị Lộ, người thiếp của Nguyễn Trãi. Đêm 4-8, khi ở Lệ Chi Viên, vua đột ngột bǎng hà.\nMột số gian thần đã vu cho Nguyễn Thị Lộ ám hại Vua. Nguyễn Trãi và gia đình ông bị tru di tam tộc.\nHai mươi nǎm sau vua Lê Thánh Tông xuống chiếu tẩy oan cho Nguyễn Trãi và sai người sưu tầm tất cả tác phẩm của ông.\nTrong khúc \"Quỳnh uyển ca\", Lê Thánh Tông đã viết về Nguyễn Trãi, vị khai quốc công thần như sau: \"Lòng Ức Trai sáng như Sao Khuê\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','08', 'Đồng chí Vũ Lǎng (tên thật là Đỗ Đức Liêm) sinh ngày 4-8-1921 trong một gia đình lao động ở xã Ngũ Hiệp, huyện Thanh Trì, Hà Nội qua đời năm 1988.\nSau Cách mạng tháng Tám, đồng chí đã được cử đi học ở trường Quân Chính Việt Nam. Tháng 11-1945, đồng chí tham gia đoàn quân Nam Tiến khi giặc Pháp gây hấn ở Sài Gòn. Đơn vị do đồng chí chỉ huy đã chiến đấu tại chiến trường khu 6.\nTrong cuộc kháng chiến chống Mỹ, đồng chí được giao nhiều nhiệm vụ quan trọng và có đóng góp to lớn vào công tác chỉ huy chiến đấu trên nhiều mặt, tổ chức nghiên cứu khoa học phục vụ chiến đấu, đào tạo cán bộ quân sự. Đồng chí Vũ Lǎng được Nhà nước phong quân hàm Thiếu tướng (nǎm 1974), Trung tướng (nǎm 1980) và Thượng tướng (nǎm 1986)')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','08', 'Từ ngày 4 đến ngày 12-8-1925, hơn một nghìn công nhân xưởng sửa chữa tàu thuỷ Ba Son (Sài Gòn) do Tôn Đức Thắng lãnh đạo đã bãi công đòi tǎng 20 % lương, giảm giờ làm việc và chống sa thải thợ.\nNgoài ra cuộc bãi công của công nhân Ba Son còn nhằm trì hoãn việc sửa chữa gấp chiến hạm Misơlê mà Pháp định đưa sang đàn áp Cách mạng Trung Quốc.\nKết quả cuộc đấu tranh này của công nhân xưởng Ba Son là: chủ xưởng phải chấp nhận tǎng lương 10% và trả lương cho công nhân trong cả các ngày mà họ bãi công.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','08', 'Từ tháng 8-1954, Mỹ đã phái Giáo chủ, Giám mục cùng với nhân viên C.I.A ở Sài Gòn phối hợp với bọn phản động đội lốt Giáo hội Việt Nam tổ chức chiến dịch dụ dỗ, cưỡng ép đồng bào ta, nhất là số theo đạo Thiên chúa ở miền Bắc di cư vào miền Nam.\nĐế quốc Mỹ bỏ ra 55 triệu đô la, Pháp 66 tỷ frǎng cho việc thực hiện chiến dịch này. Mỹ sử dụng 41 tàu và đài thọ toàn bộ chi phí chuyên trở. Trong khoảng gần 10 tháng, địch đã đưa gần 1 triệu người ở miền Bắc di cư vào miền Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','08', 'Sêli (Shelley) sinh ngày 4 tháng 8 nǎm 1792, trong một gia đình quý tộc giàu có. Thuở nhỏ ông học trường trung học Itơn dành cho trẻ em quý tộc, lớn lên vào đại học Ôtxpho. Nhưng ông là người có đầu óc tự do, say mê vǎn học, triết học.\nTác phẩm của Sêli là: \"Sự cần thiết của chủ nghĩa vô thần\" (1811), \"Hoàng hậu Margo\", \"Thơ gửi gió tây\", \"Cuộc nổi dậy của thế giới Hồi giáo\", \"Đám mây\", \"Chiến thắng cuộc đời\",v.v.... Thơ ông không thiên về phê phán, mà chủ yếu là ngợi ca, chan chứa tinh thần lạc quan, tin tưởng. Thơ ông là tiếng reo của cả thế hệ mới, báo hiệu một tương lai tươi sáng. Cùng với Bairơn, Kítxơ, Sêli góp phần làm thành thế hệ thứ hai của thơ ca lãng mạn Anh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','08', 'Anđécxen (Anderxen) - nhà vǎn người Đan Mạch, từ trần vào ngày 4-8-1875. Tên tuổi và sự nghiệp vǎn học của ông được cả thế giới biết đến - đặc biệt là các thế hệ thiếu nhi - với pho tượng cổ tích đầy hấp dẫn mang tên ông. Câu chuyện về chú lính trì dũng cảm, mang dáng vẻ của Đan Mạch, đã trở thành tính cách độc đáo.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','09', 'Ngày 4-9-1945 Hồ Chủ tịch đã ký sắc lệnh \"Tổ chức quỹ Độc lập\". Quyết định này xuất phát từ chính quyền cách mạng đang gặp muôn vàn khó khăn, nhất là về tài chính. Ta tiếp quản Ngân khố Trung ương chỉ có hơn 1 triệu đồng mà một nửa là hào rách, sắp huỷ. Mọi chi tiêu đều dựa vào dân.\nNhân dịp chính phủ phát động \"Tuần lễ vàng\" từ ngày 17 đến 24-9-1945, Hồ Chủ tịch cũng gửi thư kêu gọi đồng bào cả nước. Chỉ trong thời gian ngắn đã quyên góp được 370 kg vàng và 40 triệu đồng cho quỹ Quốc phòng và 20 triệu đồng cho quỹ Độc lập, góp phần giải quyết khó khăn về tài chính lúc bấy giờ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','09', 'Đầu tháng 9 năm 1950, trên đường đi chiến dịch Biên giới (còn gọi là chiến dịch Cao - Bắc - Lạng), Chủ tịch Hồ Chí Minh đã thăm một đơn vị thanh niên xung phong làm đường phục vụ chiến dịch.\nBác Hồ đã tặng thanh niên ta 4 câu thơ:\n\"Không cóviệc gì khó\nChỉ sợ lòng không bền\nĐào núi và lấp biển\nQuyết chí ắt làm nên\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','09', 'Nhà văn Hồ Biểu Chánh (tức Hồ Văn Trung) sinh năm 1885, quê ở Gò Công, tỉnh Tiền Giang, mất ngày 4-9-1958 tại Sài Gòn.\nThời Pháp thuộc, ông làm quan tới chức Đốc phủ sứ (tức Tỉnh trưởng), sau năm 1945 lại làm cố vấn cho Chính phủ Nguyễn Văn Thinh do Pháp dựng lên - dư luận số đông vẫn cho rằng ông là người có tài đức nên bị lợi dụng - Điều này có thể tin được vì ông có một đời văn dài nửa thế kỷ và để lại sự nghiệp văn chương đồ sộ. Riêng tiểu thuyết có 60 cuốn, mà nội dung chính đã phản ánh được chân thực và phong phú cuộc sống xã hội đương thời của Nam Bộ và thể hiện lý tưởng đạo đức tốt đẹp của ông giữa cuộc đời.\nNhà văn Hồ Biểu Chánh được xem là một trong số ít người tiên phong đặt nền móng cho tiểu thuyết Việt Nam hiện đại. Trước khi có tiểu thuyết của Tự lực Văn Đoàn và tiểu thuyết hiện thực phê phán, Hồ Biểu Chánh là tác giả có ảnh hưởng lớn ở Nam Bộ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','09', 'Ngày 4-9-1980, Chủ tịch nước Cộng hoà xã hội chủ nghĩa Việt Nam, tặng danh hiệu anh hùng lao động và tặng thưởng huân chương Hồ Chí Minh cho đồng chí V.A Satalốp, Trung tướng không quân Liên Xô phụ trách công tác đào tạo phi công vũ trụ, đã có công tổ chức công tác huấn luyện và thực hiện chuyến bay vũ trụ Xô - Việt đầu tiên; tặng thưởng huân chương Hồ Chí Minh cho đồng chí Bùi Thanh Liêm, thiếu tá không quân đã hoàn thành xuất sắc nhiệm vụ Đội bay dự bị của chuyến bay vũ trụ Xô - Việt đầu tiên.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','10', 'Tố Hữu tên thật là Nguyễn Kim Thành sinh ngày 4-10-1920 ở làng Phù Lai, huyện Quảng Điều, tỉnh Thừa Thiên, gần Cố đô Huế. Nhà thơ xuất thân trong một gia đình nhà nho nghèo. Tố Hữu làm thơ ngay trên ghế nhà trường, chép tay truyền đi đăng trên báo Đảng. Giữa lúc thơ mới đang khóc mây than gió, sướt mướt những chàng - nàng thì Tố Hữu đã thổi một luồng sinh khí cho thơ. Năm 1938, được một công nhân nhà in giới thiệu, Tố Hữu vinh dự đứng trong hàng ngũ chiến sĩ cộng sản.\nTháng 4-1939, do bị lộ ông bị bắt giam qua các nhà lao Thừa Thiên, Đăk Lắc, Đắc Pao... Tháng 3-1942, Tố Hữu vượt ngục trở về với cách mạng. Tháng 8-1945, Tố Hữu tham gia cướp chính quyền ở thành phố quê hương, với cương vị là Chủ tịch Ban Khởi nghĩa Huế. Kháng chiến toàn quốc bùng nổ. Tố Hữu trở lại Thanh Hoá làm Bí thư tỉnh uỷ. Năm 1947, Ông được Đảng điều ra Việt Bắc lãnh đạo văn nghệ. Tại Đại hội lần thứ II của Đảng (1951) ông được bầu làm uỷ ban dự khuyết trung ương Đảng, năm 1955 là uỷ viên chính thức. Đại hội III (1960) Tố Hữu được bầu vào Ban Bí thư, và đại hội IV (1976) được bầu là uỷ ban dự khuyết Bộ Chính trị và Bí thư Ban Chấp hành Trung ương, năm 1980 ông là uỷ viên chính thức Bộ Chính trị.\nTừ năm 1981, Tố Hữu được cử giữ chức Phó chủ tịch Hội đồng bộ trưởng. Đại hội V (1982) ông được bầu làm uỷ viên Bộ Chính trị. Tác phẩm chính: \"Từ ấy\" (1946), \"Việt Bắc\" (1954), \"Gió lộng\" (1961), \"Ra trận\" (1972), \"Máu và Hoa\" (1977), \"Xây dựng một nền văn nghệ lớn xứng đáng với nhân dân ta, với thời đại\" (tiểu luận 1973).\nÔng đã được Nhà nước trao tặng giải thưởng Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','10', 'Ngày 4-10-1961, Hồ Chủ tịch đã ký lệnh công bố \"Pháp lệnh quy định việc quản lý nhà nước đối với công tác phòng cháy và chữa cháy\".\nMục đích của pháp lệnh nhằm bảo vệ tài sản của Nhà nước, tính mạng và tài sản của nhân dân, bảo vệ sản xuất và trật tự an ninh chung. Đồng thời để nâng cao ý thức trách nhiệm phòng cháy chữa cháy của cán bộ, công nhân viên chức của toàn dân, đẩy mạnh công tác phòng cháy và chữa cháy.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','10', 'Ngày 4-10-1953 Chính phủ Việt Nam dân chủ cộng hoà ban hành chính sách tôn giáo.\nChính sách gồm các nội dung sau:\n1. Tôn trọng tự do tín ngưỡng, mọi công dân đều có quyền tự do tín ngưỡng và quyền tự do không tín ngưỡng.\n2. Đoàn kết nhân dân không phân biệt giáo, lương để kháng chiến kiến quốc. Phá tan âm mưu của địch lợi dụng tôn giáo chia rẽ dân tộc, kiên quyết trừng trị những kẻ lợi dụng tôn giáo hành động phản quốc, hại dân.\n3. Cải thiện đời sống, nâng cao trình độ chính trị văn hoá của đồng bào các tôn giáo.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','10', 'Nguyễn Đức Thuận tên thật là Bùi Phong Tư, sinh năm 1916, quê ở tỉnh Nam Định, mất ngày 4-10-1985 tại Hà Nội.\nTừ năm 1951 đến năm 1965, ông hoạt động bí mật trong vùng Sài Gòn - Chợ Lớn tạm chiếm, đến tháng 7 năm 1956 thì bị Mỹ - Ngụy bắt, giam giữ tại nhiều nhà lao như sở thú Sài Gòn, P42 và cuối cùng đày ra Côn Đảo. Kẻ thù đã dùng nhiều phương tiện tra khảo dã man và hiện đại hòng khuất phục ông. Nhưng trước sau như một, Nguyễn Đức Thuận vẫn giữ vững khí tiết của người cộng sản chân chính và bảo vệ được cơ sở Đảng.\nNăm 1964, ra khỏi nhà tù Côn Đảo, ông được tổ chức bố trí ra vùng giải phóng, và được phân công nhiều công tác quan trọng của Đảng, Mặt trận và Công đoàn. Từ tháng 8-1980 đến tháng 10-1985, ông là Chủ tịch Tổng công đoàn Việt Nam. Ông còn là đại biểu Quốc hội từ khoá IV đến khoá VII, là Uỷ viên Ban Chấp hành Trung ương Đảng khoá IV và khoá V.\nNguyễn Đức Thuận là chiến sĩ Cách mạng kiên cường bất khuất, một nhà hoạt động công đoàn ưu tú và hết sức trung thành vì sự nghiệp cách mạng của giai cấp công nhân và nhân dân Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','10', 'Ngày 4-10-1957, đúng vào dịp kỷ niệm 100 năm ngày sinh của Xioncốpxki - ông tổ của ngành du hành vũ trụ lần đầu tiên trong lịch sử loài người, Liên Xô đã phóng lên bầu trời con tàu vũ trụ mang tên \"Sputnhíc 1\" mở ra cho nhân loại một kỷ nguyên mới: Kỷ nguyên con người chinh phục vũ trụ bằng những vệ tinh do chính mình chế tạo.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','11', 'Nhà thơ Á Nam Trần Tuấn Khải sinh ngày 4-11-1895 ở Huyện Mỹ Lộc, tỉnh Nam Định, và mất nǎm 1983 tại thành phố Hồ Chí Minh.\nÔng xuất thân trong một gia đình nho học có truyền thống yêu nước. Thời trẻ ông học chữ Hán và chịu ảnh hưởng của phong trào Duy Tân.\nÔng tham gia phong trào chống vǎn hóa đồi trụy, đấu tranh đòi hoà bình, dân sinh, dân chủ. Nǎm 1966, ông cùng một số trí thức tiến bộ ký tên yêu cầu chính quyền Sài Gòn hiệp thương với Mặt trận dân tộc giải phóng miền Nam nhằm vãn hồi hoà bình. Nǎm 1966-1967 ông là Chủ tịch danh dự lực lượng bảo vệ vǎn hóa dân tộc, và từ nǎm 1975 đến 1983 làm cố vấn Hội nghệ sĩ Thành phố Hồ Chí Minh.\nTác phẩm chính của Á Nam Trần Tuấn Khải gồm có các tập thơ: Duyên nợ phù sinh, Bút quan hoài, Hồn tự lập, Với sơn hà.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','11', 'Giáo sư Hoàng Minh Giám, sinh ngày 4-11-1904 tại xã Đông Ngạc, huyện Từ Liêm, Hà Nội mất nǎm 1995.\nSau khi tốt nghiệp trường cao đẳng Sư phạm Hà Nội, ông làm giáo sư trường Trung học Xivovat tại Phnôm Pênh từ nǎm 1926 đến nǎm 1928 rồi về Sài Gòn dạy học đến nǎm 1931. Trong thời gian này ông tham gia viết bài cho các báo chống thực dân Pháp.\nSau tháng 8-1945, ông làm việc ở Bộ Nội vụ. Tháng 11-1946 đến tháng 2-1947 ông là Thứ trưởng Bộ Ngoại giao, từ tháng 3-1947 đến khi kết thúc cuộc kháng chiến chống Pháp, ông là Bộ trưởng Bộ Ngoại giao. Sau khi hoà bình lập lại (tháng 5-1954) cho đến tháng 6-1976 ông là Bộ trưởng Bộ Vǎn hoá.\nGiáo sư Hoàng Minh Giám là đại biểu Quốc hội từ khoá I (nǎm 1946) đến khoá VII (nǎm 1987). Giáo sư đã được Nhà nước tặng thưởng Huân chương Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','11', 'Phạm Bá Ngoãn (1930-1980) - Nhà thơ hiện đại, bút danh Thanh Hải, quê ở Phong Điền, Thừa Thiên. Ông sinh ngày 4-11-1930. Nhiệt thành yêu nước, ông tham gia cách mạng từ nǎm 1947. Đến nǎm 1967, ông chuyển sang làm công tác vǎn nghệ Bình-Trị-Thiên, và là uỷ viên Thường vụ Hội liên hiệp Vǎn học nghệ thuật Việt Nam, Uỷ viên Ban Chấp hành Hội Nhà vǎn Việt Nam. Thanh Hải từng được giải thưởng Vǎn học Nguyễn Đình Chiểu. Ông mất ở Huế, thọ 50 tuổi. Các tác phẩm chính: Huế mùa xuân, 2 tập, 1970 và 1975 - Dấu võng Trường Sơn, xuất bản 1977, Những đồng chí trung kiên.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','11', '4-11-1981 Hội nghị Đại biểu thống nhất Phật giáo Việt Nam được khai mạc. Có 164 đại biểu các tổ chức, giáo hội, hệ phái: Hội Phật giáo thống nhất Việt Nam. Giáo hội Việt Nam thống nhất, Giáo hội phật giáo cổ truyền Việt Nam, Ban liên lạc Phật giáo yêu nước thành phố Hồ Chí Minh, Giáo hội tǎng già nguyên thuỷ Việt Nam, Hội đoàn kết sư sãi yêu nước Tây Nam Bộ, Hội Phật giáo khất sĩ Việt Nam, Giáo hội phật giáo, Thiên thai giáo quán tông, Hội Phật học Việt Nam.\nHội nghị đã thảo luận và thông qua Hiến chương và đại cương chương trình hoạt động, giới thiệu và suy tôn Hội đồng chứng minh và Hội đồng trị sự. Hội nghị còn cử ra Ban Thường trực Trung ương Giáo hội Phật Giáo Việt Nam.\nNhân dịp này 5 Hoà thượng đã được Hội đồng Nhà nước trao tặng Huân chương Độc lập.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','11', 'Ngày 4-11-1993, ngành điện lực nước ta đã bắt đầu xây dựng nhà máy thuỷ điện Ialy (ở tỉnh Kontum - nay thuộc tỉnh Gia Lai).\nĐây là nhà máy thuỷ điện lớn nhất ở Tây Nguyên và lớn thứ hai của nước ta, sau nhà máy thuỷ điện Hoà Bình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','11', 'Ngày 4-11-1946 tại Luân Đôn (Thủ đô nước Anh) đã thành lập tổ chức giáo dục, khoa học và vǎn hoá của Liên hiệp quốc (Viết tắt là UNESCO).\nTrụ sở tổ chức này đặt tại Pari (thủ đô nước Pháp).\nĐến nay đã có 184 nước và lãnh thổ là thành viên của UNESCO, trong đó có Việt Nam.\nHoạt động của UNESCO nhằm góp phần bảo vệ hoà bình an ninh quốc tế bằng cách phát triển sự hợp tác giữa các nước trong các lĩnh vực khoa học, giáo dục và vǎn hoá.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '04','11', 'Tổ chức cảnh sát hình sự quốc tế (viết tắt là Interpol) có trụ sở đóng tại Liong (nước Pháp). Đến nay Interpol có gần 180 thành viên quốc gia.\nNgày 4-11-1991, trong phiên họp đại hội đồng Interpol tại Urugoay, Việt Nam đã trở thành thành viên thứ 156 của tổ chức này.\nVǎn phòng Interpol Việt Nam ra đời là một cơ cấu mới của Tổng cục Cảnh sát nhân dân. Vǎn phòng này là trung tâm thông tin giữa Việt Nam và Interpol, với vǎn phòng Interpol của gần 180 nước trong việc phát hiện, truy nã, bắt giữ tội phạm, thông báo cho nhau những thủ phạm mới của chúng để kịp thời phòng ngừa, ngǎn chặn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','12', 'Ngày 4-12-1953, Quốc hội Nước Việt Nam dân chủ cộng hoà đã nhất trí thông qua Luật cải cách ruộng đất. Mục đích của Luật cải cách ruộng đất là: \"Thủ tiêu quyền chiếm hữu ruộng đất của thực dân Pháp và của đế quốc xâm lược khác ở Việt Nam, xoá bỏ chế độ phong kiến chiếm hữu ruộng đất của giai cấp địa chủ, để thực hiện người cày có ruộng, giải phóng sức sản xuất ở nông thôn, cải thiện đời sống nhân dân, đẩy mạnh kháng chiến.\nLuật cải cách ruộng đất đã đáp ứng yêu cầu cǎn bản và cấp bách của hàng triệu nông dân đấu tranh tiêu diệt chế độ bóc lột phong kiến, đưa kháng chiến đến thắng lợi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','12', 'Ngày 4-12-1962, khánh thành trạm bơm nam sông Mã ở huyện Yên Định, tỉnh Thanh Hoá. \nĐây là một công trình thuỷ lợi lớn, tưới nước cho gần 20.000 hécta ruộng của huyện Yên Định và một phần ruộng đất của hai huyện Thuận Hoá và Thọ Xuân thuộc tỉnh Thanh Hoá.\nVùng này trước đây khô cằn, chỉ cấy lúa một vụ bấp bênh, nay có nước đã trồng được hai vụ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','12', 'Ngày 4-12-1965, một đơn vị biệt động của quân giải phóng khu Sài Gòn - Chợ Lớn đã dùng bom và súng máy tấn công khách sạn Mêtrôpôn giành riêng cho phi công lái máy bay phản lực Mỹ, diệt 200 tên phi công lái máy bay phản lực.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '04','12', 'Đường sắt xuyên Việt được xây dựng xong từ cuối nǎm 1936 trong thời gian có chiến tranh từ 1946 đến 1975, tuyến đường này bị phá huỷ, hư hỏng nặng nhiều đoạn nên không hoạt động được.\nSau khi miền Nam được hoàn toàn giải phóng, thống nhất đất nước, công nhân và quân đội ta đã xây dựng lại tuyến đường sắt Bắc - Nam.\nNgày 4-12-1976 tuyến đường sắt thống nhất chính thức khai thông sau 30 nǎm bị gián đoạn. Đường xe lửa Hà Nội thành phố Hồ Chí Minh dài 1.730 km.\nĐây là thành tích xuất sắc của ngành giao thông vận tải nước ta chào mừng Đại hội đại biểu toàn quốc lần thứ IV của Đảng cộng sản Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','01', 'Ngày 5-1-1977, Tạp chí Học tập - cơ quan lý luận và chính trị của Đảng Cộng sản Việt Nam được đổi tên thành Tạp chí Cộng sản theo Quyết định (số 01/NQ/TW) của Bộ Chính trị Ban chấp hành TW. Tạp chí Học tập ra đời từ đầu nǎm 1955. Trong 2 nǎm, tạp chí Học tập đã tuyên truyền chủ trương và quan điểm của Đảng, tích cực góp phần vào công tác lý luận và tư tưởng của Đảng, tǎng cường sự đoàn kết, nhất trí trong nhân dân trên cơ sở đường lối, chính sách của Đảng.\nBắt đầu từ tháng 1-1977, Tạp chí Cộng sản ra số đầu tiên.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','01', 'Ngày 5-1-1980. Hội nghị Ngoại trưởng ba nước Đông Dương lần thứ nhất tại Phnôm Pênh khẳng định sự đoàn kết chiến đấu và hợp tác giữa ba nước trong cuộc đấu tranh vì độc lập dân tộc, dân chủ và tiến bộ xã hội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','01', 'Bùi Kỷ là một học giả nổi tiếng, sinh ngày 5-1-1887, quê ở tỉnh Hà Nam, từ trần tại Hà Nội nǎm 1960.\nNǎm 1909, ông đậu cử nhân, nǎm 1910 đậu phó bảng. Từ đó, ông phục vụ trong ngành giáo dục. Sau cách mạng tháng Tám, ông làm Chủ tịch Hội vǎn hoá kháng chiến. Bùi Kỷ là một trí thức yêu nước, một nhà nghiên cứu uyên thâm, có công với vǎn học nước nhà vào giai đoạn đầu thế kỷ XX. Trong thời kỳ xây dựng chủ nghĩa xã hội ở miền Bắc, ông đã đóng góp nhiều công sức nghiên cứu vǎn học dân tộc. Ông đã say mê khảo cứu các áng vǎn chương nôm khuyết danh như Trê Cóc, Trinh Thử, Hoa điểu tranh nǎng... Ông là người đầu tiên cùng với Phan Võ, Nguyễn Khắc Hanh dịch và giới thiệu thơ chữ Hán của Nguyễn Du.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','01', 'Nhà vǎn Thiếu Sơn, tên thật là Lê Sĩ Quý, sinh nǎm 1908, quê ở Hải Dương, mất ngày 5-1-1978 tại thành phố Hồ Chí Minh. Ông viết vǎn và nổi tiếng từ nǎm 1928.\nTừ sau Cách mạng tháng Tám, ông tích cực phục vụ vǎn hoá dân tộc. Nǎm 1971 ông bị địch lưu đày ở Côn Đảo. Nǎm 1973 được trao trả tù binh cho Chính phủ Cách mạng lâm thời miền Nam Việt Nam.\nLà một nhà vǎn yêu nước đi theo Cách mạng, Thiếu Sơn được bạn và đồng nghiệp yêu mến, kính trọng. Những tác phẩm chính của ông gồm có: Phê bình và khảo luận, Người bạn gái, Câu chuyện Vǎn học, Đời sống tinh thần giữa hai cuộc Cách mạng 1789 và 1945.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','02', 'Trần Thủ Độ sinh nǎm 1194 ở tỉnh Thái Bình, mất ngày 5-2-1264.\nÔng là công thần sáng lập triều Trần và là người thực tế nắm quyền lãnh đạo đất nước những nǎm đầu triều Trần.\nNhững việc ông làm đã đưa nước nhà ra khỏi sự suy vong cuối triều Lý và khởi dựng thời đại Đông A rực rỡ những chiến công oanh liệt chống ngoại xâm và có nhiều thành tựu xây dựng đất nước.\nTrần Thủ Độ là một nhà chính trị đi đầu trong sự nghiệp dựng nước và giữ nước trong lịch sử dân tộc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','02', 'Nguyễn Khuyến sinh nǎm 1835, qua đời ngày 5-2-1909.\nÔng người làng Yên Đổ, huyện Bình Lục, tỉnh Hà Nam. Thuở nhỏ, ông vừa đánh dậm vừa đi học, rất thông minh. Thi ba kỳ: hương, hội, đình, ông đều đỗ đầu nên có tên là Tam nguyên Yên Đổ. Ông làm quan dưới triều Nguyễn, chức cao nhất là bố chánh Quãng Ngãi. Nǎm 1884, chính quyền mới do thực dân Pháp điều khiển định cử ông làm quyền Tổng đốc Sơn Tây - Hưng Hoá - Tuyên Quang, ông kiên quyết từ chối, xin về quê dạy học. Thực dân Pháp vẫn cho tay sai lôi kéo, nhưng ông quyết không chịu hợp tác với chúng.\nTừ lâu Nguyễn Khuyến đã được ghi nhận là một nhà thơ kiệt xuất của dân tộc. Ông để lại một sự nghiệp vǎn thơ đồ sộ. Trong đó, giá trị nhất, là phần thơ viết về làng quê và phần thơ viết để châm biếm xã hội thực dân nửa phong kiến. Vì thế người ta quen gọi Nguyễn Khuyến là \"Nhà thơ của làng quê\" và \"Nhà thơ trào phúng\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','02', 'Nhà vǎn hoá Nguyễn Khắc Viện sinh ngày 5-2-1913 tại Hà Tĩnh, qua đời nǎm 1997 tại Hà Nội.\nNǎm 1941, ông đỗ bác sĩ y khoa loại xuất sắc ở nước Pháp, sau đó gia nhập Đảng Cộng sản Pháp. Những nǎm 1950, ông là Tổng thư ký Hội liên hiệp Việt Kiều ở Pháp. Nǎm 1963, ông về nước, đã từng giữ các chức vụ: Ủy viên Ủy ban liên lạc vǎn hoá với nước ngoài, Tổng biên tập tạp chí đối ngoại \"Nghiên cứu Việt Nam\", báo đối ngoại \"Tin Việt Nam\", Giám đốc - Tổng biên tập Nhà xuất bản Ngoại vǎn Việt Nam, Giám đốc Trung tâm nghiên cứu tâm sinh lý trẻ em. Các cuốn sách: Lịch sử Việt Nam, Tuyển tập vǎn học Việt Nam, Tập luyện dưỡng sinh do ông Nguyễn Khắc Viện viết bằng tiếng Pháp đã gây sự chú ý đặc biệt trong bạn đọc nhiều nước.\nNhà vǎn hoá Nguyễn Khắc Viện đã được Nhà nước ta tặng Huân chương Độc lập hạng nhất.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','02', 'Ngày 5-2-1973, phiên họp đầu tiên tại thành phố cảng Hải Phòng giữa đại diện Việt Nam dân chủ cộng hoà và đại diện Hoa Kỳ bàn việc Hoa Kỳ tháo gỡ mìn đã thả xuống các cảng sông biển của Việt Nam dân chủ cộng hoà.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','03', 'Ngày 5-3-1930, Nguyễn Ái Quốc viết báo cáo \"Phong trào Cách mạng Việt Nam\" gửi Quốc tế Cộng sản Đông Dương, Người viết: \"Ngày nay các nhóm Cộng sản đã thống nhất, hoạt động nhất định sẽ tốt hơn trước nhiều. Chúng tôi tin tưởng với kinh nghiệm và lòng hy sinh với quần chúng, những người Cộng sản sẽ chiến thắng\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','03', 'Trong thư gửi nhân dân Pháp ngày 5-3-1947, Hồ Chủ tịch đã nêu rõ thiện chí hoà bình của Chính phủ và nhân dân ta. Thư có đoạn: \"Máu Pháp và Việt chảy đã nhiều. Chiến tranh không nên kéo dài. Chiến tranh không đi đến đâu cả. Chúng tôi sẵn sàng lập lại hoà bình, chỉ cần Quốc hội và nhân dân Pháp có cử chỉ thân thiện\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','03', 'Nhà nghiên cứu vǎn học Hoa Bằng tên thật là Hoàng Thúc Trâm. Ông quê ở huyện Từ Liêm, Hà Nội. Sinh nǎm 1902 và qua đời ngày 5-3-1977. Sinh thời ông được coi là dịch giả tiếng Hán có uy tín nhất. Những bài nghiên cứu của ông có giá trị góp phần phát huy nền Vǎn học dân tộc. Các tác phẩm chính của ông là: Từ điển Hán - Việt, Vǎn chương quốc âm đời Tây Sơn... Ông là thành viên Hội Nhà vǎn, Hội Sử học và Viện Hán Nôm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '05','03', 'Chu Ân Lai - Chính khách lỗi lạc, nhà ngoại giao kiệt xuất của Trung Quốc - sinh ngày 5-3-1898. Ông từng giữ chức vụ Thủ tướng Quốc vụ viện nước Cộng hoà nhân dân Trung Hoa và là người khởi xướng 5 nguyên tắc chung sống hoà bình giữa các nước Á Phi. Ông đặc biệt có công lao trong việc bảo vệ những người con ưu tú của nhân dân Trung Quốc khỏi bị các phe phái sát hại. Ông mất ngày 9-1-1976, thọ 78 tuổi')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '05','03', 'Thủ tướng Anh - Sơcsin - ngày 5-3-1946 đã phát biểu tại thành phố Phunton (Mỹ) kêu gọi các nước đế quốc tạo thành vòng vây về kinh tế, chính trị, quân sự đối với Liên Xô. Sự kiện này được coi là bước mở đầu của chiến tranh lạnh trên thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '05','03', 'Ngày 5-3-1953 - Stalin từ trần. Ông là một nhà chính trị - quân sự đại tài của Đảng Cộng sản Liên Xô và Liên bang Xô viết. Quê ông thuộc nước cộng hoà Grudia. Trong Đại chiến thế giới lần thứ 2, ông đã lãnh đạo quân đội và nhân dân Liên Xô tiến hành cuộc chiến tranh giữ nước vĩ đại, cùng các thành viên khác trong khối đồng minh chống phát xít đập tan chủ nghĩa phát xít, giải phóng Đông Âu dẫn đến sự hình thành hệ thống XHCN thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','04', 'Ngày 5-4-1937, Công nhân nhà máy Ba Son (Sài Gòn) tiến hành bãi công. Những yêu sách mà công nhân đưa ra trong cuộc bãi công bao gồm: Cải thiện giờ làm việc - Tǎng lương và phụ cấp - Tuyển lại công nhân bị chủ sa thải... Cuộc bãi công kéo dài một tháng đã thể hiện khối đoàn kết nhất trí của công nhân Ba Son. Với các hình thức đấu tranh liên tục và quyết liệt đã buộc giới chủ phải nhượng bộ. Thắng lợi này đã cho thấy sự kiên trì và những đòi hỏi chính đáng tỏ rõ hiệu quả. Đồng thời chứng tỏ sự lớn mạnh của công nhân nhà máy Ba Son - Một nhà máy giầu truyền thống đấu tranh. Cũng trong cuộc bãi công này, nhân dân Sài Gòn - Chợ Lớn đã tích cực ủng hộ và kết hợp đấu tranh như mít tinh hoặc đưa ra các khẩu hiệu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','04', '5-4-1963 Công bố Pháp lệnh phòng cháy chữa cháy.\nNgày 5-4-1963, Chủ tịch Hồ Chí Minh ký lệnh công bố \"Pháp lệnh quy định cơ quan phụ trách quản lý công tác phòng cháy chữa cháy và chế độ cấp bậc của sĩ quan và hạ sĩ quan phòng cháy chữa cháy\".\nPháp lệnh này đã được ủy ban thường vụ Quốc hội nước ta thông qua ngày 23-3-1963. Đây là vǎn bản đầu tiên có tính chất pháp quy về công tác phòng cháy chữa cháy.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','04', 'Ngày 5-4-1965, Hồ Chủ tịch đã gửi thư khen bộ đội không quân đánh trận đầu, Bác viết:\n\"Các chú đã chiến đấu dũng cảm, đã tiêu diệt máy bay Mỹ.\nCác chú đã thực hiện khẩu hiệu \"Đã đánh là thắng\". Như thế là xứng đáng với truyền thống anh hùng của quân và dân ta. Bác gửi lời khen ngợi các chú và nhắc các chú phải luôn luôn:\n- Nâng cao tinh thần \" Quyết tâm đánh thắng giặc Mỹ xâm lược\". Thắng không kiêu, khó không nản.\n- Ra sức rèn luyện để tiến bộ mãi.\n- Đoàn kết chặt chẽ với các đơn vị bạn và với nhân dân.\nChúc các chú lập nhiều chiến công hơn nữa.\nChào thân ái và quyết thắng\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','04', 'Từ ngày 5 đến ngày 7-4-1972 bộ đội ta liên tục tấn công địch ở thị xã Lộc Ninh (Bình Long), đã đánh gục sư đoàn 5, diệt trung đoàn thiết giáp 1 của ngụy. Cả chiến đoàn 69 đầu hàng ta. Thị xã Lộc Ninh với 28 nghìn dân được giải phóng. Đồng thời vùng giải phóng miền Nam cũng được mở rộng. Thị xã Lộc Ninh trở thành thủ đô của Chính phủ Cách mạng lâm thời giải phóng miền Nam Việt Nam. Nơi đây đã diễn ra các đợt trao trả tù binh của 2 bên MN.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','04', 'Vào ngày 5-4-1982, mẻ than cốc đầu tiên được chế luyện từ loại than gầy Quảng Ninh đã ra lò tại Khu gang thép Thái Nguyên. Với thành công này và công suất 6,7 tấn than cốc/nǎm, Khu gang thép Thái Nguyên đã tự cung cấp nhu cầu về than cốc trong công nghiệp gang thép. Trước đó ta vẫn phải mua than cốc của nước ngoài. Việc sản xuất thành công than cốc trong nước cho phép giảm chi phí nhập khẩu và các nhà máy của ta chủ động nguyên liệu than cốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '05','04', 'Ngày 5-4-1974 chính thức thành lập hội đồng Quốc gia chính trị Liên hiệp Lào và Chính phủ liên hiệp dân tộc (lần thứ ba) do hoàng thân Xuvanaphuma làm thủ tướng, và Hội đồng quốc gia chính trị hiệp thân do hoàng thân Xuvanaphuma làm chủ tịch... Cũng từ đây phong trào nhân dân đòi phế bỏ chế độ thực dân kiểu mới của Mỹ ở Lào lên cao.\nĐó là nền của việc thành lập chế độ cộng hoà dân chủ nhân dân tháng 12-1975.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','05', 'Trần Quý Cáp là một chí sĩ yêu nước, sinh nǎm 1870 tại làng Bát Nhị, huyện Điện Bàn, tỉnh Quảng Nam.\nÔng là một trong những người lập Duy Tân Hội. Nǎm 1904, được bổ làm giáo vụ Thǎng Bình (Tỉnh Khánh Hoà). Ông ủng hộ phong trào Đông Du do Phan Bội Châu khởi xướng; tích cực hoạt động duy tân, nâng cao dân chí, dân sinh nên được sĩ phu kính trọng, xem là \"lãnh tụ trong nhóm dân tộc\". Thơ vǎn của ông mang nặng lòng yêu nước và ý chí cách mạng. Bố chính Khánh Hoà tố giác ông chống triều đình Huế. Thực dân Pháp đã khép ông tội xúi dân làm loạn, đem chém ở chợ Diên Khánh vào ngày 5-5-1908; khi đó Trần Quý Cáp mới 38 tuổi.\nCái chết của ông gây xúc động mạnh trong nhân dân. Thơ vǎn, câu đối tưởng niệm ông rất nhiều. Phan Bội Châu có bài vǎn tế và đôi liễn điếu. Lời điếu có ý \"Ngọc nát hơn ngói lành... nghìn nǎm luận định, chói rạng trời sao\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','05', 'Ngày 5-5-1911, Vua Duy Tân ra dụ thành lập Trường Hậu bổ ở Huế, nơi bổ túc những \"kiến thức cai trị hiện đại\" của thực dân Pháp cho các chiến sĩ, phó bảng, cử nhân, tú tài trong thời gian ba nǎm, trước khi được chính thức bổ nhiệm ra làm quan ngạch học chính và hành chính trong chính phủ Nam triều ở Trung Kỳ.\nPhát huy thắng lợi của cuộc mít tinh 1-5-1938, nhân có đại biểu thanh niên của nhiều tỉnh về Hà Nội dự mít tinh, theo chỉ thị của đồng chí Nguyễn Vǎn Cừ, Tổng bí thư của Đảng, Đoàn Thanh niên Dân chủ Hà Nội đứng ra tổ chức Hội nghị đại biểu toàn quốc thanh niên Dân chủ vào ngày 5-5-1938.\nHội nghị được tổ chức công khai tại toà nhà số 28 đường Rolland (nay là đường Hai Bà Trưng). Hội nghị biểu thị thống nhất của lực lượng Thanh niên Dân chủ toàn quốc và bầu ra ban chấp hành của Đoàn. Đồng thời Hội nghị cũng ra quyết định cho xuất bản tờ \"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '05','05', 'Ngày 5-5-1959, để đáp ứng nhu cầu ngày càng lớn của các chiến trường phía Nam, Đoàn 559 được thành lập và ngày đó trở thành ngày truyền thống của bộ đội Trường Sơn. Bộ đội Trường Sơn là lực lượng hiệp đồng binh chủng quy mô lớn bao gồm nhiểu binh chủng làm nhiệm vụ chiến đấu mở đường, đảm bảo ra tiền tuyến lớn, tới các chiến trường toàn quốc và làm nhiệm vụ quốc tế.\nTừ buổi đầu mới thành lập, vận chuyển trên những con đường mòn, hoạt động theo mùa, phục vụ từng chiến dịch, bộ đội Trường Sơn với ý chí \"xẻ dọc trường sơn đi cứu nước\", đã xây dựng nên tuyến đường chiến lược mang tên Bác Hồ đi dọc Trường Sơn hùng vĩ, nối liền Nam Bắc, nối liền hậu phương lớn với tiến tuyến lớn. Bộ đội Trường Sơn đã chuyển được hàng triệu tấn vũ khí, lương thực, thuốc men và đồ dùng quân sự; đã tổ chức chuyển hàng vạn bộ đội thương binh vào ra các chiến trường. Trong chiến đấu bảo vệ giao thông, bộ đội Trường Sơn đã bắn rơi 2450 máy bay địch, phá huỷ 100 xe quân sự, hiệp đồng cùng các lực lượng khác phá vỡ tuyến phòng thủ của địch dọc biên giới Việt - Lào mở ra một vùng giải phóng liên hoàn ngày càng vững chắc.\nBộ đội Trường Sơn càng chiến đấu càng trưởng thành về mọi mặt. Trên mặt trận kinh tế cũng có nhiều đóng góp đáng kể trong sự lớn mạnh của đất nước. Bộ đội Trường Sơn đã được Quốc hội và Chính phủ tuyên dương là Đơn vị Anh hùng lực lượng vũ trang nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '05','05', 'Ngày này 5-5-1961, đoàn cán bộ quân sự đầu tiên tǎng cường cho Bộ chỉ huy Miền và các quân khu ở miền Nam xuất phát từ Xuân Mai (Hoà Bình) theo đường dây Trường Sơn có 500 người, phần lớn là cán bộ cao cấp, trung cấp do thiếu tướng Trần Vǎn Quang, Uỷ viên dự khuyết Trung ương Đảng, Phó tổng tham mưu trưởng Quân đội nhân dân Việt Nam dẫn đầu.\nNgày 28-7-1961, sau 3 tháng hành quân liên tục, trong đó nhiều ngày phải rút bớt tiêu chuẩn lương thực, ǎn rau rừng thay cơm vì gặp địch càn quét, hoặc do dự trữ gạo trên đường giao liên của Đoàn 559 còn ít, đoàn đã vượt chặng đường dài trên 2000 km đến vị trí tập kết ở đồi 300 (tỉnh Bình Long).\nĐồng chí Trần Vǎn Quang được cử làm Phó ban quân sự Miền Nam. Các cán bộ của đoàn được điều về các cơ quan thuộc Ban quân sự Miền, cơ quan quân sự các quân khu 7,8,9 và Sài Gòn - Gia Định, chuẩn bị cho việc thành lập các bộ tư lệnh quân khu và các trung đoàn chủ lực.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '05','05', 'Trong trận đánh trả với không quân Mỹ ngày 5-5-1967, quân và dân thủ đô Hà Nội đã chiến thắng oanh liệt, bắn rơi 8 máy bay, tiêu diệt và bắt sống nhiều giặc lái. Nhân dịp này, Hồ Chủ tịch đã ký lệnh thưởng huân chương Độc lập hạng nhất và gửi thư khen quân và dân Thủ đô Hà Nội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '05','05', 'Ngày 5-5-1972, toàn bộ sĩ quan, binh sĩ thuộc trung đoàn 56 quân ngụy Sài Gòn, đóng tại cǎn cứ Đầu Mầu - Đức Miếu (Quảng Trị) đã tiến hành phản chiến tập thể và đưa toàn bộ vũ khí (trong đó có đại bác tầm xa 175 mm - Vua chiến trường) tự nguyện ra nhập quân giải phóng. Hành động này cho thấy uy tín và sự lớn mạnh cũng như vai trò chính nghĩa của lực lượng giải phóng miền nam Việt Nam đã tác động đến hàng ngũ quân ngụy Sài Gòn. Cùng ngày, Bộ Chỉ huy mặt trận Trị Thiên đã biểu dương hành động phản chiến tập thể của trung đoàn 56 và quyết định chấp nhận đề nghị toàn bộ trung đoàn 56 là trung Đoàn Quân Giải phóng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('4', '05','05', 'Các Mác, nhà triết học, kinh tế học, người sáng lập chủ nghĩa xã hội khoa học, lãnh tụ Cách mạng của giai cấp công nhân thế giới sinh ngày 5-5-1818 tại thành phố Tơriơ nước Đức.\nSinh thời ông học luật, đi sâu nghiên cứa lịch sử, triết học. Nǎm 23 tuổi, ông đỗ tiến sĩ triết học. Sau đó ông chuyển sang hoạt động chính trị, làm báo và nghiên cứu triết học. Nǎm 1848 ông cùng Ǎngghen công bố \"Tuyên ngôn của Đảng cộng sản\". Đó là vǎn kiện gồm những luận điểm cơ bản về sự phát triển của xã hội và Cách mạng xã hội chủ nghĩa. Mác tổng kết cuộc Cách mạng Pháp 1848 trong bài \"Đấu tranh giai cấp ở Pháp\". Ông tập trung vào việc nghiên cứu chính trị nhằm vạch trần bản chất bọc lột của giai cấp tư sản và biên soạn bộ \"Tư bản\", tác phẩm khoa học có tầm quan trọng lớn về mặt lý luận của chủ nghĩa Mác, thành hệ tư tưởng Cách mạng chủ đạo trong phong trào công nhân quốc tế. Ông đã góp ý kiến để sửa chữa những sai lầm về chính trị và góp phần khôi phục Đảng xã hội dân chủ Đức. Ông hoạt động tích cực cho việc thành lập các Đảng công nhân ở Pháp, Anh, Mỹ... Những nǎm cuối cùng, mặc dù sức yếu, ông vẫn say sưa viết nốt quyển II, III của bộ \"Tư bản\".\nNgày 4-3-1883, sau một thời gian bị bệnh Các Mác, nhà tư tưởng vĩ đại của nhân loại đã từ trần.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','06', 'Ngày 5-6-1862, triều đình Huế đã ký với Pháp một vǎn bản gọi là \"Hiệp ước hoà bình và hữu nghị\".\nHiệp ước này quy định triều đình Huế phải nhượng hẳn cho Pháp 3 tỉnh miền Đông Nam Bộ (gồm Gia Định, Định Tường, Biên Hoà) và đảo Côn Lôn; bồi thường chiến phí cho Pháp 4 triệu đô la; mở các cửa biển Đà Nẵng, Ba Lạt, Quảng Yên cho Pháp và Tây Ban Nha tự do buôn bán. Pháp sẽ trả thành Vĩnh Long cho triều đình Huế khi nào ngừng phong trào chống Pháp ở 3 tỉnh miền Đông. Thực chất của bản Hiệp ước này là thực dân Pháp âm mưu thôn tính nước ta từng phần.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','06', 'Ngày 5-6-1911, tại bến Nhà Rồng, Sài Gòn, anh thanh niên Nguyễn Tất Thành (tức Chủ tịch Hồ Chí Minh sau này), lúc đó với tên Vǎn Ba đã xuống tàu Đô Đốc Latusơ Tờrêvin, làm phụ bếp để có điều kiện sang châu Âu tìm đường cứu nước. Anh Ba (sau đổi tên thành Nguyễn Ái Quốc) đã tìm thấy \"Con đường cách mạng vô sản là con đường giải phóng chúng ta\" khi Người đọc luận cương của Lênin về cách mạng ở các nước thuộc địa.\nGọi là Nhà Rồng vì ở đây có gắn đôi Rồng lớn bằng đất nung tráng men xanh. Từ tháng 5-1975, bến cảng Nhà Rồng và trụ sở của hãng tàu biển tại đây đã trở thành một khu lưu niệm lớn về hoạt động của Bác Hồ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','06', 'Từ mồng 5 đến 8 tháng 6 nǎm 1954, Ủy ban cải cách ruộng đất Trung ương họp phiên đầu tiên để nghe báo cáo về 4 đợt phát động quần chúng giảm tô, tiến hành trong 631 xã, bao gồm 250.000 nhân khẩu, thu trên 19.000 tấn thóc quả thực chia cho 11 vạn gia đình, tịch thu của địa chủ, Việt gian phản động 11.720 mẫu ruộng đất, 2127 trâu bò chia cho nông dân, và báo cáo về đợt thí nghiệm cải cách ruộng đất ở 6 xã thuộc tỉnh Thái Nguyên.\nHội nghị đã thảo luận vǎn kiện quan trọng: \"Điều lệ quy định tạm thời để thi hành Luật cải cách ruộng đất\".\nHôi nghị cũng vạch ra một số khuyết điểm trong công tác cán bộ, một số vấn đề chưa được giải quyết để rút kinh nghiệm.\nHội nghị đã cử \"Ban thường trực\" để thay thế \"Ủy ban\" giải quyết mọi công việc và chỉ đạo phong trào giảm tô, cải cách ruộng đất.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','06', 'Ngày 5-6-1976, Chính phủ ta đã tuyên bố về chủ quyền đối với quần đảo Trường Sa. Bản tuyên bố khẳng định chủ quyền đối với quần đảo này và giành quyền bảo vệ chủ quyền đó. \nNgày 5-6-1984, Hội đồng Bộ trưởng ra tuyên bố về vùng trời của nước CHXHCN Việt Nam. Tuyên bố này là cơ sở pháp lý cơ bản để nhà nước Việt Nam thực hiện và bảo vệ chủ quyền, bảo vệ an ninh quốc gia trên vùng trời Tổ quốc: \"Khoảng không gian ở trên đất liền, nội thuỷ, lãnh hải và các hải đảo Việt Nam và thuộc chủ quyền hoàn toàn và riêng biệt của nước CHXHCN Việt Nam.\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '05','06', 'Theo nghị quyết của tổ chức Chương trình về môi trường của Liên hiệp quốc, Ngày môi trường thế giới được tổ chức vào ngày 5-6-1982 để kỷ niệm lần thứ 10 Hội nghị Liên hiệp quốc về môi trường họp ở Xtốc Khôm, tháng 6-1972.\nTrong những nǎm gần đây, vấn đề môi trường sống đối với con người đã trở thành vấn đề nóng hổi của nhiều nước, vấn đề bức thiết của thời đại. Cho nên, nội dung của các hoạt động về môi trường rất phong phú, bao gồm từ việc giáo dục, phổ biến kiến thức, đến việc nghiên cứu khoa học, xác định và áp dụng các tiến bộ kỹ thuật, công nghệ, tổng kết các kinh nghiệm truyền thống, xây dựng các luật lệ pháp chế, tổ chức phong trào quần chúng rộng rãi nhằm sử dụng hợp lý và phát triển tốt môi trường.\nViệt Nam tham gia Ngày môi trường thế giới với tư cách là một nước thành viên của Chương trình về môi trường (UNEP).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','07', 'Đạo Hoà Hảo chính thức ra đời người 5-7-1939 (Tức ngày 18 tháng 5 nǎm Kỷ Mão) khi Huỳnh Phú Sổ lên làm giáo chủ. Tên gọi của tôn giáo này bắt nguồn từ địa danh làng Hoà Hảo quận Tân Bình, tỉnh Châu Đốc là quê của giáo chủ Huỳnh Phú Sổ. Tông phái này cho rằng Phật Di Lặc sẽ tái sinh để tu tỉnh lại giáo lý đạo Phật cho phù hợp với thời thế. Giáo lý của Hoà Hảo lấy nội dung chính là Tứ Ân (ân phụ mẫu, ân quốc vương, ân tam bảo, ân chúng sinh). Nghi lễ giản dị.\nLúc đầu Huỳnh Phú Sổ là người có tư tưởng chống Pháp do đó thu hút được đông đảo dân nghèo nhập đạo. Có lúc lên đên 1,4 triệu tín đồ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','07', 'Ngày 5-7-1966 Hồ Chủ tịch tiếp ông Xanhtơni, nguyên Cao ủy Pháp tại Hà Nội nǎm 1946, trong buổi tiếp có đề cập đến việc Mỹ có thể gây sức ép quân sự tối đa với Việt Nam. Người đã nói: \"Chúng tôi biết rõ sức mạnh của kẻ thù của chúng tôi, chúng tôi biết rằng người Mỹ, nếu họ muốn họ có thể san bằng thành phố này (Hà Nội) cũng như các thành phố chủ chốt của Bắc Bộ: Hải Phòng, Nam Định, Bắc Ninh... Chúng tôi đã chờ đón một tình hình như thế, chúng tôi đã chuẩn bị sẵn sàng, nhưng điều đó không hề mảy may làm suy yếu ý chí chiến đấu đến cùng của chúng tôi\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','07', 'Lệnh của Chủ tịch nước ngày 5-7-1994 đã công bố Luật Lao động. Bộ luật gồm 17 chương, 198 điều quy định quyền và nghĩa vụ của người lao động, các nguyên tắc sử dụng và quản lý lao động.\nMục đích ra bộ Luật Lao động là \"bảo vệ quyền làm việc, lợi ích và quyền khác của người lao động, đồng thời bảo vệ quyền và lợi ích hợp pháp của người sử dụng lao động, tạo điều kiện cho mối quan hệ lao động được hài hoà và ổn định, góp phần phát huy sáng tạo và tài nǎng của người lao động, của người quản lý lao động....\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '05','07', 'Hevesy sinh ngày 1-8-1885 tại Buđapét. Sau khi tốt nghiệp đại học, ông có điều kiện ra nước ngoài làm việc tại các Trung tâm khoa học nổi tiếng ở châu Âu. Từ nǎm 1918 ông đã trở thành chuyên gia hàng đầu về phóng xạ.\nTại Viên, ông đề xuất phương pháp dùng nguyên tử đánh dấu trong nghiên cứu y học, sinh học. Ông phát minh ra nguyên tố 72. Nǎm 1926 ông nghiên cứu các nguyên tố phổ biến trên trái đất và trong vũ trụ, hệ thống hoá các nguyên tố hiếm của đất. Nǎm 1932 ông tìm ra phương pháp pha loãng đồng vị và dùng để xác định hàm lượng chì trong quặng. Với sự phát hiện ra phôtpho 32, ông dùng nó để xác định sự trao đổi chất trong xương, máu, khối u ác tính.\nNǎm 1934 Hevesy được trao giải thưởng Nôben về các công trình dùng đồng vị làm chất chỉ thị để nghiên cứu các quá trình hoá học. Ông là Hội viên và Vệ sĩ nhiều tổ chức khoa học và Viện Hàn Lâm khoa học thế giới. Ông mất vào ngày 5-7-1966.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '05','07', 'Ngày 5-7-1994, tại Singapo, Hội đồng hợp tác kinh tế Thái Bình Dương (viết tắt tiếng Anh là PECC) đã kết nạp Việt Nam làm thành viên của tổ chức này.\nCác nước thành viên của PECC gồm có: Ôxtrâylia, Brunây, Canada, Chilê, Trung Quốc, Côlômbia, Hồng Kông, Inđônexia, Nhật Bản, Hàn Quốc, Malaxia, Mêhicô, Niu Dilân, Pêru, Philippin, Nga, Xingapo, Đài Loan, Thái Lan, Mỹ và các nước đảo Thái Bình Dương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','08', 'Tạ Uyển là người lãnh đạo chủ yếu cuộc khởi nghĩa Nam Kỳ. Ông sinh ngày 5-8-1898 tại Côi Trì, Yên Mỹ, Yên Mô, Ninh Bình.\nÔng đỗ khoá sinh nǎm 18 tuổi, làm thư ký đặc điền. Sớm giác ngộ cách mạng, ông tham gia Việt Nam Thanh niên cách mạng đồng chí Hội từ nǎm 1927, hoạt động gây cơ sở cho hội ở Nam Định, Nǎm 1929 ông làm bí thư chi bộ Côi Trì trong Đông Dương cộng sản Đảng và sau đó bị bắt giam ở nhà lao Ninh Bình. Ông bị kết án 15 nǎm tù và đày biệt xứ ra Côn Đảo, đến cuối 1934 ông vượt ngục về hoạt động ở Hậu Giang, vùng Cầu Kè, Trà Ôn, Vĩnh Long với biệt danh Hai Lọ. Nǎm 1940, với tư cách là Bí thư Xứ uỷ Nam Kỳ, ông có mặt ở Sài Gòn chuẩn bị cuộc khởi nghĩa Nam Kỳ. Gần đến ngày phát động khởi nghĩa, ông bị bắt. Giặc Pháp tra tấn ông dã man. Tháng 12-1940 ông bị giặc giết, khi đó ông mới 42 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','08', 'Ngày 5/8/1930, Tạp chí Đỏ (tiền thân của Tạp chí Cộng sản) do đồng chí Nguyễn Ái Quốc sáng lập đã chính thức ra số đầu tiên. \nQua các thời kỳ với các tên gọi khác nhau: Tạp chí Đỏ (1930); Tạp chí Cộng sản (1931); Tạp chí Bôn-sơ-vích (1935); Tạp chí Cộng sản (1941 – 1943); Tạp chí Sinh hoạt nội bộ (1947); Tạp chí Cộng sản (1950); Tạp chí Học tập (1955 – 1976); Tạp chí Cộng sản (1977 đến nay), Tạp chí đã đóng góp tích cực vào việc nghiên cứu, vận dụng, bảo vệ và phát triển chủ nghĩa Mác – Lênin, tư tưởng Hồ Chí Minh; đóng góp cho công tác tuyên truyền, giáo dục lý luận, chính trị - tư tưởng và sự lãnh đạo, chỉ đạo thực tiễn cách mạng của Đảng. \nNgày 5/8/2003, Tạp chí Cộng sản được Đảng và Nhà nước trao tặng phần thưởng cao quý: Huân chương Sao Vàng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','08', 'Trong tháng 8 nǎm 1956, đồng chí Lê Duẩn lúc đó đang hoạt động ở Nam Bộ, đã viết \"Đề cương cách mạng miền Nam\". Tài liệu quan trọng này đã xác định rõ tính chất của xã hội miền Nam, đề ra con đường Cách mạng là con đường duy nhất đúng đắn của nhân dân ta ở miền Nam tiến tới giành chính quyền.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','08', 'Nhà vǎn nhà báo Phan Thao sinh nǎm 1915 tại huyện Điện Bàn, tỉnh Quảng Nam. Ông là con của nhà vǎn Phan Khôi.\nÔng nhiệt tình yêu nước, tham gia Cách mạng từ lúc còn trẻ tuổi, tích cực đóng góp hai cuộc kháng chiến cứu nước. Ông viết báo, nghiên cứu vǎn hoá dân tộc. Ông là người đầu tiên dịch tác phẩm \"Người mẹ\" của Macxim Goocki ra tiếng Việt, được hầu hết bạn đọc tán thưởng. Ông mất ngày 5-8-1960 tại Hà Nội, thọ 45 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','08', 'Nǎm 1964 đế quốc Mỹ liên tiếp bị thất bại trong cuộc \"chiến tranh đặc biệt\" ở miền Nam. Để gỡ thế bí, chúng âm mưu phát động cuộc chiến tranh phá hoại bằng không quân và hải quân ra miền Bắc, hòng làm lung lay ý chí quyết tâm chiến đấu của nhân dân ta, ngǎn chặn sự chi viện của miền Bắc đối với miền Nam.\nVới âm mưu đó, đêm 4-7-1964 chính quyền Mỹ dựng lên câu chuyện \"Sự kiện vịnh Bắc Bộ\" vu cáo hải quân nhân dân Việt Nam tiến công khiêu khích tàu chiến Mỹ ở ngoài khơi thuộc hải phận quốc tế. Lấy cớ đó, ngày 5-8-1964 chúng cho máy bay ném bom 4 cǎn cứ hải quân ta thuộc vùng sông Gianh (Quảng Bình), Cửa Hội (Nghệ An), Lạch Trường (Thanh Hoá), Hòn Gai (Quảng Ninh).\nHải quân nhân dân ta phối hợp với quân và dân các địa phương đã giáng trả bọn cướp Mỹ, đánh thắng trận đầu rất vẻ vang, bắn rơi 8 máy bay, bắn bị thương ba chiếc khác và bắt sống một tên giặc lái. Trận đầu đánh thắng chiến tranh phá hoại của đế quốc Mỹ đối với miền Bắc đã có ảnh hưởng rất to lớn.\nTừ đó, ngày 5-8 trở thành ngày truyền thống của Hải quân nhân dân Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '05','08', 'Henric Aben người Na Uy, sinh ngày 5-8-1802. Từ nǎm 16 tuổi ông đã tự học và nghiền ngẫm các tác phẩm lớn. Ông đã phát hiện ra những thiếu sót trong lý luận của các bậc tiền bối và dành thời gian để chính xác hoá những phần thiếu chặt chẽ. Chính theo phương hướng đó mà ông đã chứng minh thành công \"Định lý nhị thức tổng quát\".\nNǎm 23 tuổi, Aben gửi đến Viện Hàn lâm khoa học Pháp một luận vǎn về \"tính chất tổng quát của một lớp rất rộng các hàm siêu việt\". Đây là một công trình nghiên cứu to lớn về giải thích toán học và được đánh giá là đã nêu lên đề tài cho các thế hệ đến 500 nǎm sau. Nǎm 1830 đề tài của ông đưa ra mới được chấp nhận và Viện Hàn lâm khoa học Pháp đã tặng ông giải toán học lớn, với ý định đền bù xứng đáng công lao của Aben đối với toán học.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','09', 'Trước tình hình thực dân Pháp bám gót quân Anh trở lại xâm chiếm Nam Bộ, Ngày 5-9-1945, Hồ Chủ tịch đã viết lời \"Kêu gọi quốc dân\", trong đó có đoạn: \"Nhân dân Việt Nam hoan nghênh quân đồng minh kéo vào Việt Nam để tước khí giới quân Nhật, nhưng cương quyết phản đối quân Pháp kéo vào Việt Nam, vì mục đích của chúng là hãm dân tộc Việt Nam vào vòng nô lệ lần nữa\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','09', 'Từ ngày 5 đến ngày 7-9-1954 đã diễn ra Hội nghị Ban chấp hành Trung ương Đảng. Hội nghị kiểm điểm tình hình mới sau hiệp định Giơnevơ và đề ra nhiệm vụ cho toàn Đảng toàn dân ta là: \"Hàn gắn những vết thương chiến tranh, phục hồi kinh tế quốc dân, giảm bớt những khó khăn về đời sống nhân dân, phát triển kinh tế một cách có kế hoạch và từng bước mở rộng việc giao lưu kinh tế giữa thành thị và thôn quê\". Hội nghị nêu lên 4 đặc điểm của tình hình nước ta:\n1- Từ chiến tranh chuyển sang hòa bình\n2- Nước ta tạm thời chia làm hai miền\n3- Từ nông thôn chuyển vào thành thị\n4- Từ phân tán chuyển đến tập trung.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','09', 'Ngày 5-9-1954, tại Đại Từ (tỉnh Thái Nguyên), Hồ Chủ tịch đã nói chuyện với cán bộ, nhân viên, các cơ quan Trung ương, các đơn vị bộ đội, công an và thanh niên xung phong chuẩn bị về tiếp quản thủ đô Hà Nội. \nNgười nói: \"Bom đạn của địch không nguy hiểm bằng \"đạn bọc đường\" vì nó làm hại mình mà mình không trông thấy. Muốn giữ vững nhân cách, cán bộ chiến sĩ phải luôn luôn gương mẫu trong mọi việc, phải cần, kiệm, liêm, chính\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','09', 'Đại hội đại biểu toàn quốc lần thứ ba Đảng Lao động Việt Nam, tiến hành từ ngày 5 đến 10-9-1960 tại thủ đô Hà Nội. Tham dự đại hội có 525 đại biểu chính thức và 51 đại biểu dự khuyết, thay mặt 50 vạn Đảng viên toàn quốc. Trong diễn văn khai mạc, Chủ tịch Hồ Chí Minh đã chỉ rõ: \"Đại hội lần này là Đại hội xây dựng chủ nghĩa xã hội ở miền Bắc và đấu tranh hoà bình thống nhất nước nhà\". Người nêu lên hai nhiệm vụ chiến lược của cách mạng Việt Nam, Người hoan nghênh tinh thần quốc tế vô sản giữa các nước xã hội chủ nghĩa, các Đảng cộng sản. Người kêu gọi toàn Đảng đoàn kết chặt chẽ, động viên toàn dân hoàn thành mọi nhiệm vụ của đất nước.\nĐại hội quyết định sửa đổi Điều lệ Đảng, để xây dựng Đảng phù hợp với yêu cầu của giai đoạn Cách mạng mới và bầu ra Ban Chấp hành Trung ương gồm 43 uỷ viên chính thức và 28 uỷ viên dự khuyết. Hội nghị Ban Chấp hành Trung ương Đảng lần thứ nhất bầu Hồ Chủ tịch làm Chủ tịch Đảng, đồng chí Lê Duẩn làm Bí thư thứ nhất Ban Chấp hành Trung ương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','10', 'Đinh Công Tráng quê ở làng Tráng Xá, huyện Thanh Liêm, tỉnh Hà Nam. Ông đã lãnh đạo nông dân khởi nghĩa chống Pháp và lừng danh với chiến khu Ba Đình. Chiến khu Ba Đình (ở tỉnh Thanh Hoá) do ông đóng giữ đã đẩy lùi và làm tổn thất nặng nề nhiều đạo quân Pháp xâm lược.\nSau gần 3 năm giữ thành, chống giặc, trong một cuộc tiến công của hơn 3.500 quân tinh nhuệ Pháp, thành bị vỡ, Đinh Công Tráng chạy về Nghệ An và bị giặc bắn chết đêm 5-10-1887 tại làng Trung Yên, bên bờ sông Cả.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','10', 'Tống Duy Tân sinh năm 1838 ở Thanh Hoá, đỗ tiến sĩ năm 1875.\nÔng là một sĩ phu yêu nước, đã tích cực hưởng ứng phong trào Cần Vương của vua Hàm Nghi (năm 1885). Tống Duy Tân đã cùng nhiều văn thân, sĩ phu giương cao cờ khởi nghĩa ở vùng núi Hùng Lĩnh, Thanh Hoá từ năm 1885 đến năm 1892 và trở thành thủ lĩnh chính của phong trào. Tháng 9-1892 ông bị địch vây bắt, và xử tử vào ngày 5-10-1892 ở chợ Thanh Hoá.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','10', 'Ngày 5-10-1959, bộ đội thiết giáp được thành lập.\nSự ra đời của bộ đội thiết giáp đánh dấu bước tiến mới về sức mạnh đột kích của quân đội ta.\nTrong cuộc kháng chiến chống Mỹ, binh chủng tăng - thiết giáp đã lập được nhiều chiến công. Cùng các quân binh chủng khác, binh chủng tăng - thiết giáp đã táo bạo, thần tốc góp phần làm nên Đại thắng Mùa xuân 1975.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','10', 'Ngày 5-10-1971, tổ máy đầu tiên của nhà máy thuỷ điện Thác Bà (tỉnh Yên Bái) bắt đầu phát điện.\nHồ Thác Bà tỉnh Yên Bái dài 80 km, nơi rộng nhất 12 km. Mặt hồ rộng 238 km2, chứa được hơn 2 tỷ mét khối nước.\nNhà máy Thuỷ điện Thác Bà có công suất 108 nghìn KW, sản lượng nửa tỉ KW giờ/năm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','10', 'Ca Văn Thỉnh, bút danh Ngạc Xuyên, sinh ngày 21-3-1902 tại Tân Bình Thành, Mỏ Cày, tỉnh Bến Tre.\nSau khi tốt nghiệp cao đẳng sư phạm, ông được bổ làm giáo sư, rồi làm Đốc Bến Tre. Hơn 10 năm dạy học, ông luôn nâng cao tinh thần yêu nước, dốc tâm vào việc nghiên cứu văn học, sử học. Năm 1946, ông tham gia phái đoàn Nam Bộ, nhận nhiệm vụ quyền bộ trưởng Bộ Giáo dục. Năm 1952, ông là Uỷ viên Tuyên huấn Trung ương Cục miền Nam. Khi tập kết ra bắc, ông phụ trách Vụ Đông Nam Á, Bộ Ngoại giao, sau đó ông làm Giám đốc Thư viện khoa học xã hội Trung ương. Sau ngày giải phóng miền Nam, ông được cử làm Viện trưởng Viện khoa học xã hội miền Nam tại thành phố Hồ Chí Minh. Ông mất ngày 5-10-1987, thọ 85 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','11', 'Hơn 9 vạn quân Minh do Tổng binh Vương Thông chỉ huy lọt vào trận địa phục kích của nghĩa quân Lam Sơn ở Tốt Động và Chúc Động (nay thuộc tỉnh Chương Mỹ - Hà Tây).\nTrong 3 ngày đêm - từ mồng 5 đến mồng 7-11-1426 - chiến đấu liên tục, quân ta đã làm thất bại hoàn toàn cuộc phản công chiến lược của Vương Thông. Hơn 6 vạn quân địch đã bị tiêu diệt.\nTrong Đại cáo bình ngô. Nguyễn Trãi đã miêu tả chiến thắng oanh liệt này bằng những lời đầy hào khí.\n\"Ninh Kiều (tức Chúc Động) máu chảy thành sông, hôi tanh muôn dặm.\nTốt Động thây phơi đầy nội, thối để nghìn thu\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','11', 'Nhà vǎn Nguyên Hồng, họ Nguyễn, sinh ngày 5-11-1918 tại thành phố Nam Định, mất nǎm 1982 tại Bắc Giang.\nSớm chịu ảnh hưởng của phong trào cách mạng, ông tham gia Mặt trận dân chủ. Nǎm 1939, ông bị bắt giam và bị đưa đi trại tập trung Bắc Mê (tỉnh Hà Giang). Từ nǎm 1943, ông bí mật tham gia Hội Vǎn hoá cứu quốc ở Hà Nội. Sau tháng 8-1945, Nguyên Hồng hǎng hái hoạt động vǎn nghệ trong hai cuộc kháng chiến chống Pháp và chống Mỹ.\nNguyên Hồng là nhà vǎn của những người cùng khổ, tác phẩm chính của Nguyên Hồng gồm các truyện ngắn: Bảy Hựu, Hai dòng sữa, Miếng bánh; tiểu thuyết: Bỉ vỏ, Quán nải; Hồi ký: Những ngày thơ ấu. Từ nǎm 1961 đến nǎm 1976, ông hoàn thành một bộ tiểu thuyết lớn \"Cửa biển\" gồm 4 tập, sau đó viết tiếp bộ tiểu thuyết \"Núi rừng Yên Thế\", nhưng chưa hoàn thành thì ông mất.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','11', 'Từ ngày 5-11 đến 5-12-1922, Đại hội lần thứ IV của Quốc tế cộng sản họp tại Mátxcơva thông qua luận cương của Lênin về mặt trận thống nhất và nghe Lênin báo cáo về 5 cách mạng Nga và triển vọng của phát triển thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','11', 'Ngay sau khi ở Pháp trở về nước, Hồ Chủ tịch đã nhận thấy nhân dân ta rất khó tránh một cuộc chiến tranh rộng lớn do thực dân Pháp gây ra. Ngày 5-11-1946, người đã viết một vǎn kiện quan trọng với tiêu đề \"Công việc khẩn cấp bây giờ\". Người đã nêu rõ nhiệm vụ của toàn Đảng, toàn dân là kháng chiến và kiến quốc, vừa \"phá hoại để ngǎn địch\" vừa \"kiến thiết để đánh địch\" trong các lĩnh vực quân sự kinh tế, chính trị, giao thông.\nHồ Chủ tịch nhận định \"Cuộc kháng chiến sẽ rất gay go, cực khổ. Cố ráng sức qua khỏi mùa đông lạnh lẽo thì ta sẽ gặp mùa xuân\". Muốn vậy, ta phải \"tổ chức du kích khắp nơi, tham gia sản xuất khắp nơi\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','11', 'Ngày 5-11-1968, Richớt Nichxơn (Richard Nixon), người của Đảng Cộng hoà, trúng cử Tổng thống Hoa Kỳ, đưa ra thuyết \"Việt Nam hoá chiến tranh\" nhằm rút quân Mỹ ra khỏi cuộc chiến tranh ở Việt Nam trong danh dự, đồng thời tǎng cường viện trợ quân sự cho chế độ ngụy Sài Gòn. Song học thuyết này đã bị phá sản và dẫn tới sự sụp đổ hoàn toàn của ngụy quân ngụy quyền miền Nam Việt Nam vào mùa xuân 1975.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '05','11', 'Giêm Clác Mǎcxoen là nhà vật lý Scốtlen, sinh nǎm 1831 và từ trần ngày 5-11-1879.\nÔng đã phát minh ra sóng điện từ và bản chất sóng điện từ của ánh sáng.\nÔng đã chứng minh: Ở cùng nhiệt độ, động nǎng trung bình của các phân tử không phụ thuộc vào bản chất của chúng; và tìm ra công thức tính công điện từ khi mạch điện xê dịch trong từ trường, phương trình tổng quát về sự truyền điện từ trường. Tên của Mắcxoen được đặt cho đơn vị từ thông, ký hiệu là M.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '05','11', 'Chrixtian Âykhơman (Christian Eijkman) sinh nǎm 1858 và mất ngày 5-11-1930.\nÔng là nhà sinh lý học Hà Lan, học vi trung học ở Béclin (nước Đức) cùng với Rôbe Kôc. Sau đó, ông làm Giám đốc Viện bệnh lý học Batavia, giáo sư Vệ sinh học, y học Utrêc (Hà Lan).\nCông trình về bệnh phù thũng của Âykhơman đưa đến phát minh các sinh tố, do đó ông được giải nôben về sinh lý và y học 1929.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','12', 'Ngày 5-12-1964, quân giải phóng đột nhập tiêu diệt \"ấp chiến lược\" Bình Giã, bao vây đánh phá chi khu quân sự Đức Thạch. Đến 8-12, quân giải phóng tấn công quận lỵ Đất đỏ, ngày 9-12, tiêu diệt toàn chi đoàn xe lội nước bọc sắt M.113 trên đường số 2, và đánh quân tiếp viện kéo dài 6 ngày liền từ 28-12 đến 2-1-1965\nTrận Bình Giã (Bà Rịa) là trận kéo dài nhất từ ngày có đấu tranh vũ trang và giành được thắng lợi rất lớn: loại khỏi vòng chiến đấu hơn 2.000 tên địch, diệt 37 xe quân sự, bắn rơi 24 máy bay, bắn hỏng 13 chiếc khác.\nChiến thắng Bình Giã đánh dấu sự phá sản của chiến lược \"Chiến tranh đặc biệt\" của đế quốc Mỹ ở miền Nam Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','12', 'Ngày 5-12-1986, tại Thành phố Hồ Chí Minh đã khai mạc hội nghị quốc tế địa chất khu vực Đông Dương. Có 22 nước và tổ chức quốc tế tham gia.\nCùng ngày, tại Hà Nội đã khai mạc Hội chợ triển lãm kinh tế kỹ thuật Việt Nam lần thứ ba.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '05','12', 'Tối ngày 5-12-2003, tại Hà Nội diễn ra buổi lễ khai mạc hoành tráng Đại hội Thể thao Đông Nam Á lần thứ 22 (SEA Cames 22). Đây là lần đầu tiên SEA Cames được tổ chức tại Việt Nam, SEA Cames 22 có 32 môn thi và 435 bộ huy chương. Việt Nam xếp thứ nhất toàn đoàn với tổng sắp 346 huy chương, trong đó có 158 huy chương vàng, 97 huy chương bạc và 91 huy chương đồng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '05','12', 'Vôngǎng Amadơ Môza (Wolffgang Amadeus Mozart) là nhạc sĩ, nhà soạn nhạc thiên tài người Áo. Ông là thần đồng âm nhạc. Nǎm tuổi, ông tham gia biểu diễn trong giàn nhạc giáo đường cùng với cha mình. Nǎm 12 tuổi ông viết một vở kịch cho nhà hát ôpêra. Nǎm 14 tuổi, ông sáng tác thành công vở nhạc kịch \"Vua Mitơridat xứ Pông\" và tên tuổi ông vang khắp châu Âu. Ông đã đi biểu diễn ở nhiều nước trên thế giới. Tuy nhiên ông vẫn sống trong hoàn cảnh nghèo khó, bệnh tật, và đã qua đời ngày 5-12-1791 khi mới 35 tuổi. Ông để lại một di sản âm nhạc đồ sộ và vô giá, với 626 tác phẩm lớn nhỏ, trong đó có 24 vở ôpêra nổi tiếng, 50 bản giao hưởng... Ông còn là một trong những người thầy âm nhạc. Ông tìm tòi sự trong sáng, thanh nhã trong giai điệu và đã đạt tới sự vĩ đại qua sự đơn giản và kiều diễm. Ông thật sự là \"một thiên tài phát sáng\" như nhận xét của nhạc sĩ Nga Traicốpxki')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '05','12', 'Alếchxanđrơ Đuyma (Alexandre Dumas) (bố) là nhà viết tiểu thuyết Pháp, sinh ngày 24-12-1802 nǎm 27 tuổi ông đã nổi tiếng với vở kịch \"Vua Henri III và triều đình\" và sau đó ông viết 90 vở kịch lịch sử. Giai đoạn sau ông bắt đầu viết tiểu thuyết lịch sử. Với các vở nổi tiếng là: Biên niên sử nước Pháp, Ba chàng ngự lâm pháo thủ, Nữ Hoàng Margot, Bá tước Monte Cristo... Với óc tưởng tượng phong phú, ông sử dụng chất liệu lịch sử để xây dựng nhân vật theo ý muốn.\nÔng mất ngày 5-12-1870.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '05','12', 'Oan Đítsnây (Walt Disnay) Hoạ sĩ, đạo diễn điện ảnh nổi tiếng Mỹ, cha đẻ phim hoạt hình và giải trí cho trẻ thơ. Ông sinh ngày 5-12-1901. Từ phim hoạt hoạ \"Alice\" đầu tiên, ông có phim hoạt hình như: Chú chuột Mickey, Người lái tàu, Chú vịt Donald, chó Pluto, gấu Ballo, Beo Baghera, Nai Bambi... Nǎm 1938 ông được giải Osca với bộ phim \"Nàng Bạch Tuyết và bảy chú lùn\". Sau đó là một loạt phim: Pinocchio, Frantasia, Bambi, Cô bé lọ lem, Người đẹp ngủ trong rừng... Ông còn làm một số bộ phim khoa học về thế giới động vật và phim truyện. Tổng cộng đã được hơn 30 giải Osca, 900 bằng khen của nhiều nước, 5 bằng Tiến sĩ danh dự. Ông mất ngày 15-12-1966.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','01', 'Ngày 6-1-1946, Tổng tuyển cử trong cả nước bầu đại biểu Quốc hội khoá I nước Việt Nam Dân chủ cộng hoà.\nĐây là lần đầu tiên, mọi người dân Việt Nam từ 18 tuổi trở lên, được quyền bầu cử và ứng cử. Hơn 90% tổng số cử tri đã đi bỏ phiếu. Hồ Chủ tịch được hơn 98% số phiếu bầu. Ngay trong vùng đang có chiến tranh do giặc Pháp gây ra, đồng bào vẫn tìm mọi cách tham gia bỏ phiếu.\nLần đầu tiên trong lịch sử nước ta và cũng là lần đầu tiên của các nước thuộc địa trên thế giới có một quốc hội dân chủ, tiến bộ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','01', 'Ngày 6-1-1947, trung đoàn Liên khu I (sau này là trung đoàn Thủ đô) được chính thức thành lập tại Hà Nội. Lực lượng của trung đoàn gồm 3 tiểu đoàn: 101, 102, 103 với số quân gần 2.000 người. Thành phần đội viên, ngoài nam giới, có cả phụ nữ, người nhiều tuổi và thiếu niên đã từng sống, chiến đấu trên chiến hào Hà Nội từ những ngày đầu nổ súng chống thực dân Pháp xâm lược, để bảo vệ từng cǎn nhà, góc phố Thủ đô.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','01', 'Khánh thành Bảo tàng cách mạng Việt Nam.\nBảo tàng Cách mạng Việt Nam ở phố Tông Đản, Hà Nội được khánh thành ngày 6-1-1959.\nBằng nhiều hiện vật và tài liệu, Bảo tàng này thể hiện một cách khái quát và có hệ thống lịch sử phát triển của phong trào cách mạng Việt Nam từ khi Đảng Cộng sản Việt Nam ra đời đến nay.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','01', 'Từ cuối nǎm 1959, Hồ Chủ tịch kêu gọi toàn dân hưởng ứng một tháng trồng cây. Ngày 6-1-1960 mở đầu đợt trồng cây, Hồ Chủ tịch khuyên nhân dân hàng nǎm nên tổ chức một đợt trồng cây vào mùa xuân gọi là \"Tết trồng cây\", để lấy gỗ làm nhà, góp phần cải tạo thiên nhiên và làm giàu cho Tổ quốc.\nTừ đó Tết trồng cây trở thành một quán tốt đẹp của nhân dân, mang lại nhiều lợi ích thiết thực, góp phần làm giàu cho Tổ quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','01', 'Giáo sư Nguyễn Đổng Chi, sinh ngày 6-1-1915, quê ở tỉnh Hà Tĩnh, qua đời nǎm 1984.\nÔng là nhà nghiên cứu nhiều lĩnh vực: dân tộc học, khảo cổ học, Hán Nôm, vǎn hoá dân gian, sử học, vǎn học. Ở bất kỳ lĩnh vực nào, giáo sư cũng đạt được thành tựu đáng kể. Công trình qui mô nhất của ông là bộ \"Kho tàng truyện cổ tích Việt Nam\" gồm 5 tập với 200 truyện tiêu biểu, có nhiều dị bản về những mô típ đồng dạng trong vǎn học dân gian nhiều nước. Đây là bộ sách đầy đủ và hệ thống nhất về truyện cổ tích Việt Nam được lựa chọn và dựng lại một cách nghiêm túc với một bút Pháp nhuần nhuyễn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '06','01', 'Frêgo lôhan Menđen (Gregor Johann Mendel), nổi tiếng là một nhà thực vật học người Áo. Ông sinh ngày 22-7-1822. Sau khi tốt nghiệp Đại học Tổng hợp ông về sống trong tu viện. Tại đây ông đã thực hiện nhiều thí nghiệm về quy luật di truyền áp dụng cho các sinh vật. Sau đó, ông làm thí nghiệm lai phối hợp các thứ đậu khác nhau và công bố trong luận vǎn \"Sự lai giống thực vật\". Về sau thuyết Menđen về Định luật di truyền được phổ biến rộng rãi. Ông mất ngày 6-1-1884.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','02', 'Danh sĩ Nguyễn Thiếp, hiệu La Sơn phu tử, sinh nǎm 1723 ở Hà Tĩnh và mất ngày 6-2-1804.\nÔng thi hội, đỗ tam trường nǎm 1749 làm huấn đạo, rồi tri phủ, sau từ quan, về quê nhà dạy học. Chúa Trịnh Sâm nhiều lần mời ông ra làm việc nhưng ông vẫn từ chối. Vua Quang Trung chân thành viết thư mời nhiều lần ông mới ra giúp nhà Tây Sơn góp phương sách đánh giặc, làm Viện trưởng Viện Sùng Chính, tương đương với Thượng thư Bộ Học. Khi vua Quang Trung mất, ông không nhận cộng tác với Gia Long.\nNguyễn Thiếp còn để lại La Sơn thi tập, Lập Phong vǎn các, Hạnh Am thi vǎn tập, nói lên tấm lòng luôn gắn bó với quê hương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','02', 'Tháng 2-1946, chiến khu Đ ở miền Đông Nam bộ được thành lập.\nĐây là cǎn cứ địa cách mạng trong hai cuộc kháng chiến chống thực dân Pháp và đế quốc Mỹ. Sau này, do cuộc kháng chiến phát triển, chiến khu Đ được mở rộng trên miền rừng núi hiểm trở từ biên giới Việt Nam - Cǎmpuchia đến gần sát các thành phố Sài Gòn, Biên Hoà và Thủ Dầu Một.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','02', 'Ngày 6-2-1967, máy bay Mỹ rải thuốc khai quang ở phía Nam vùng phi quân sự. Hàng chục tấn thuốc diệt cỏ cũng được ném xuống để cho cây rụng lá, cỏ không mọc được làm cho đối phương không còn chỗ ẩn núp.\nSông ngòi ở miền Bắc, cửa biển Hải Phòng đều bị thả mìn để cho tàu bè không di chuyển được.\nNhư vậy, đế quốc Mỹ đã dùng những phương pháp mà phần lớn bị dư luận quốc tế phản đối.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '06','02', 'Tháng 2-1848, lần đầu tiên \"Tuyên ngôn của Đảng Cộng sản\" được công bố trên báo Luân Đôn (nước Anh) do Các Mác và Phriđric Ǎngghen soạn thảo, đúng vào lúc này, cuộc cách mạng bắt đầu nổ ra ở châu Âu.\n\"Tuyên ngôn của Đảng Cộng sản\" là một vǎn kiện có ý nghĩa lịch sử to lớn, nêu luận điểm cơ bản của học thuyết về chủ nghĩa xã hội khoa học, đánh dấu bước đầu kết hợp về chủ nghĩa xã hội khoa học với phong trào công nhân. Chủ nghĩa này soi sáng cho cuộc đấu tranh của giai cấp vô sản, từ chỗ tự phát sang tự giác, không có tổ chức thành có tổ chức, dưới sự lãnh đạo của một đảng mácxít.\nCho đến ngày nay, tư tưởng cơ bản trong \"Tuyên ngôn của Đảng Cộng sản\" vẫn tiếp tục chiếu sáng con đường đấu tranh của giai cấp công nhân và các dân tộc bị áp bức trên toàn thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','03', 'Nhà vǎn Nguyễn Công Hoan sinh ngày 6-3-1903. Ông quê ở huyện Vǎn Giang, tỉnh Hưng Yên và qua đời nǎm 1977. Ông đã viết hơn 200 truyện ngắn, 30 truyện dài, cùng nhiều thể loại vǎn học khác. Ông được xếp là người đứng hàng đầu trong trào lưu vǎn học hiện thực phê phán trước Cách mạng tháng Tám.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','03', 'Ông Lê Thanh Nghị tên thật là Nguyễn Khắc Xứng sinh ngày 6-3-1911 trong một gia đình nhà nho nghèo ở làng Thượng Cốc, huyện Tứ Lộc, tỉnh Hải Dương, từ trần nǎm 1989. \nTừ nǎm 1928, ông đã tham gia phong trào công nhân chống áp bức của bọn đế quốc và chủ mỏ, gia nhập Việt Nam thanh niên Cách mạng đồng chí Hội, sau đó vào Đảng Cộng sản Việt Nam.\nTừ tháng 5-1930 đến đầu nǎm 1945, ông hoạt động ở Hà Nội, Hải Dương, tham gia Thành uỷ Hà Nội. Thường vụ Xứ uỷ Bắc kỳ, hai lần bị tù đày.\nTừ nǎm 1946 đến nǎm 1954, ông là một trong những người chủ chốt của Liên khu III.\nTừ nǎm 1951 đến nǎm 1982, ông Lê Thanh Nghị được bầu làm ủy viên ban chấp hành Trung ương Đảng, ủy viên Bộ Chính trị, và được cử giữ các chức Bộ trưởng Bộ Công nghiệp, Phó thủ tướng Chính phủ, Chủ nhiệm ủy ban kế hoạch Nhà nước, Phó chủ tịch kiêm Tổng thư ký Hội đồng nhà nước. Ông là đại biểu Quốc hội từ khoá II đến khoá VI, đã được Nhà nước trao tặng Huân chương Sao Vàng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','03', 'Nhà thơ Vĩnh Mai tên thật là Nguyễn Hoàng, quê ở huyện Triệu Phong, tỉnh Quảng Trị. Ông sinh ngày 6-3-1918 và mất nǎm 1981. Ông hoạt động trong phong trào thanh niên dân chủ ở Huế từ nǎm 1936. Nǎm 1940 bị bắt và đầy đi Buôn Mê Thuột Nǎm 1945 ra tù tiếp tục hoạt động và tham gia khởi nghĩa ở Phú Yên. Tác phẩm chính: Đất đen và hoa thắm, Tiếng hát, Những dân quân xã....')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','03', 'Ngày 6-3-1946 tại nhà số 38 phố Lý Thái Tổ, với mục đích loại bớt kẻ thù và để ta có thời gian chuẩn bị kháng chiến, Hồ Chủ tịch đã ký với đại diện của chính phủ Pháp - Xanhtơny bản hiệp định sơ bộ với Pháp. Theo hiệp định này, Chính phủ Pháp phải công nhận nước Việt Nam dân chủ cộng hoà là một nước tự do, có Chính phủ, có nghị viện, có quân đội và tài chính của mình. Đồng thời coi Việt Nam là một thành viên ở trong Liên bang Đông Dương và Khối liên hiệp Pháp. Cũng theo hiệp định này, 15.000 quân Pháp vào miền Bắc để thay thế quân Tưởng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','03', 'Quân đội Mỹ đã đổ bộ những đơn vị đầu tiên lên Cảng Đà Nẵng vào ngày 6-3-1965. Từ đây cuộc can thiệp của Mỹ vào miền Nam Việt Nam đã trở thành cuộc chiến tranh xâm lược cục bộ, và từng bước dùng không quân leo thang đánh phá miền Bắc. Kết thúc cuộc chiến tranh này, quân Mỹ đã phải rút về nước để lại hậu quả nghiêm trọng về những thiệt hại và những di chứng của nó. Nhân dân tiến bộ Mỹ và thế giới đã đấu tranh biểu tình ủng hộ nhân dân Việt Nam chống đế quốc Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','03', 'Từ ngày 6 đến 8-3-1979, tại Henxinki (Phần Lan), Hội đồng hoà bình thế giới tổ chức hội nghị quốc tế khẩn cấp ủng hộ Việt Nam. Hơn 100 nước và 30 tổ chức quốc tế dự hội nghị. Hội nghị đã đề ra chương trình hành động quốc tế đoàn kết và bảo vệ Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '06','03', '6-3-1521 là ngày mất của Magienlǎng - Nhà hàng hải nổi tiếng người Bồ Đào Nha. Ông là người đầu tiên đã thực hiện chuyến vượt qua các đại dương vòng quanh trái đất bằng thuyền. Cuộc thám hiểm của ông đã chứng minh trên thực tế một điều là Trái đất hình tròn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','04', 'Ngày 6-4-1491, là ngày sinh của Nguyễn Bỉnh Khiêm. Ông sinh ra ở làng Trung An - huyện Vĩnh Lại - tỉnh Kiến An cũ (nay là thôn Trung An - xã Lí Học - Vĩnh Bảo - Hải Phòng). ở Nguyễn Bỉnh Khiêm tài nǎng xuất sắc được thể hiện trên nhiều mặt: Triết học, vǎn học, giáo dục, quân sự... Nhưng sự đóng góp nhiều nhất, cụ thể nhất của ông cho nền vǎn hoá dân tộc phải kể đến giáo dục. Nguyễn Bỉnh Khiêm đã tạo được tiền đề cho sự phát triển của giáo dục nước ta. Ông là một người thầy toàn diện luôn tỏ rõ nhiệt tình và tài nǎng đào tạo nhân tài của mình. Ông có nhiều học trò nổi tiếng: Lương Hữu Khánh, Phùng Khắc Khoan, Nguyễn Quyên, Nguyễn Dữ... Trường Bạch Vân (am Bạch Vân) do ông và các học trò gây dựng nên, trở thành trường không thể thiếu được trong lịch sử giáo dục của nước nhà. Nguyễn Bỉnh Khiêm được xếp là một trong 13 danh nhân vǎn hoá lớn nhất của lịch sử dân tộc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','04', 'Ngày 6-4-1947 Hội nghị cán bộ TW Đảng lần thứ 2 Họp tại chiến khu miền Bắc. Đây là hội nghị đầu tiên từ sau khi Trung ương rút khỏi Thủ đô lên cǎn cứ địa Việt Bắc để chỉ đạo cả nước kháng chiến. Hội nghị rút kinh nghiệm, qua 4 tháng kháng chiến toàn quốc, phân tích về cuộc kháng chiến của ta và đề ra những chủ trương chính sách về chính trị, quân sự, kinh tế, vǎn hoá nhằm thực hiện \"Toàn dân đoàn kết , kháng chiến lâu dài\". Hội nghị khẳng định: \"Kháng chiến nhất định thắng lợi\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','04', 'Do bị thua đau trên chiến trường miền Nam, ngày 6-4-1972 Ních xơn đã huy động lực lượng lớn không quân, hải quân ồ ạt tham chiến ở miền Nam và đánh phá trở lại miền Bắc nói là để trả đũa các cuộc tiến công của ta ở miền Nam. Nhân dân miền Bắc bước vào cuộc chống chiến tranh phá hoại lần thứ hai ác liệt hơn, qui mô hơn lần trước.\nQuân dân tỉnh Quảng Bình và khu Vĩnh Linh đã lập công xuất sắc bắn rơi ngay 10 máy bay Mỹ. Liên tiếp nhiều ngày Đế quốc Mỹ đã cho Máy bay kể cả máy bay B52 đánh phá khu vực Vĩnh Linh, các tỉnh Quảng Bình, Hà Tĩnh, Nghệ An. Qua 7 tháng anh dũng quân và dân miền Bắc đã giáng trả thích đáng không lực Hoa Kỳ. Tính đến tháng 12-1972 đã bắn rơi 600 máy bay Mỹ, bắn chìm và bắn cháy nhiều tàu chiến địch.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','04', 'Những ngày đầu tháng 4-1975, cả nước ta bừng bừng khí thế ra trận. Tất cả cho tuyền tuyến, tất cả để chiến thắng. Quân ta đi như nước chảy - Ô tô, xe lửa, máy bay, tàu biển hầu như chỉ nhằm một hướng: Thành phố Sài Gòn.\nĐồng bào các dân tộc Tây Bắc, Việt Bắc đề nghị với Trung ương xin dừng kế hoạch vận chuyển gạo và muối về địa phương mình, dành cả đoàn xe với các thứ hàng quí quay vào Nam Bộ cho kịp kế hoạch tổng tiến công. Nhiều công trường, nhà máy, cơ quan rút bớt từ 30 đến 50% số người trong biên chế để phục vụ chiến dịch. Toàn bộ lực lượng vận tải quân sự gồm 12 vạn người, 6.300 xe vận tải của Đoàn 559, 2.100 xe của Cục Vận tải và hàng trǎm xe của các quân khu, quân đoàn, quân chủng, binh chủng được tập trung vận chuyển cho chiến dịch. Một lực lượng vận tải lớn của nhà nước gồm trên 1.000 xe ô tô, 32 tàu biển (5.000 tấn phương tiện), 130 toa xe lửa (9.000 tấn phương tiện) và hàng trǎm tấn phương tiện đường không được huy động vận chuyển vật chất và cơ động bộ đội vào chiến trường.\nTừ ga Hàng Cỏ (Hà Nội), các đoàn tàu hoả chở đầy bộ đội và vũ khí, đạn dược chạy thẳng vào Vinh, từ đó ô tô và tàu thuỷ chuyển tiếp bằng đường biển và đường bộ vào miền Đông Nam bộ.\nTừ các cảng sông, biển, tàu thuyền vận tải của công ty vận tải đường sông, đường biển, tàu vận tải của hải quân chở xe tǎng, pháo lớn và bộ đội vào các cảng Đà Nẵng, Quy Nhơn, Cam Ranh. Từ đó cơ động tiếp bằng đường bộ theo đường số 1 vào Long Khánh, đến khu vực tập kết của Đoàn 814 hậu cần Miền ở khu vực Dầu Dây - Túc Trưng.\nTừ các sân bay trên miền Bắc, các loại máy bay vận tải, máy bay lên thẳng kể cả một số máy bay chở khách được huy động chở quân, chở đạn, chở sách báo phim ảnh vào mặt trận Sài Gòn - Gia Định và các vùng mới giải phóng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','04', 'Ngày 6-4-1975, ở miền Bắc nước ta đã tiến hành bầu cử Quốc hội khoá 5. Tổng số đại biểu đã bầu là 242 đại biểu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','05', 'Nhà báo Hồng Chương tên thật là Trần Chương, sinh vào ngày 6-5-1911 tại Quảng Trị và mất tại Hà Nội ngày 18-3-1989.\nÔng tham gia phong trào Mặt trận dân chủ từ nǎm 1937, đã từng bị đế quốc Pháp bắt, kết án tù 2 lần và đày đi Buôn Ma Thuột. Sau Cách mạng tháng Tám, đến khi qua đời, lần lượt làm Bí thư Đoàn Thanh niên cứu quốc Trung Bộ, Đội trưởng Đội biệt động đường số 9 (Quảng Trị), Tổng biên tập Tạp chí Cộng sản, Chủ tịch Hội Nhà báo Việt nam.\nNgoài viết báo ông còn làm thơ, viết tiểu thuyết và có nhiều tập phê bình, tiểu luận về vǎn học, nghệ thuật.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','05', 'Nhà vǎn Nguyễn Huy Tưởng, quê ở Dục Tú, Từ Sơn, Bắc Ninh (nay thuộc Đông Anh Hà Nội) sinh ngày 6-5-1912.\nTrước nǎm 1945 ông đã tham gia hoạt động cách mạng, phục vụ vǎn hoá cứu quốc, ông từng viết nhiều truyện dã sử và kịch lịch sử ở tuần báo \"Tri Tân\". Trong kháng chiến chống Pháp, ông là một trong những người đứng ra lập Hội Vǎn nghệ Việt Nam góp phần xây dựng nền Vǎn nghệ kháng chiến từ buổi đầu. Ông cũng là người sáng lập đồng thời là Giám đốc Nhà xuất bản Kim Đồng - Nhà xuất bản sách cho thiếu nhi đầu tiên dưới chế độ mới.\nNhững sáng tác chính của ông là \"Đêm hội Long Trì\", \"Vũ Như Tô\", \"Cột đồng mã viên\", \"những người ở lại\", \"Ký sự Cao Lang\", \"Truyện anh Lục\", \"Bốn nǎm sau\", \"Sống mãi với thủ đô\"...\nTrong đó có nhiều tác phẩm được dựng thành phim, thành kịch để đến với đông đảo khán giả.\nÔng mất ngày 25-7-1960 khi mới 48 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','05', 'Ngày 6-5-1942, tại Tôkyô Chính phủ Pháp và Nhật ký kết hiệp ước về quan hệ kinh tế giữa Nhật và Đông Dương. Các vǎn bản được ký kết bao gồm: \n1- \"Hiệp ước về cư trú và hàng hải\", vǎn bản này thoả thuận cho người Pháp, Nhật và người bản sứ được mua động sản, bất động sản, thuê nhà, kinh doanh, lập hội, học hành... và một số quyền \"đồng đẳng với người bản quốc\" như thông hành, cư trú, pháp luật, tố tụng... Vǎn bản cũng thoả thuận cho tàu biển của Pháp và Nhật được tự do và các hải cảng của Nhật và Đông Dương.\n2. \"Hiệp ước về quan thuế và thương mại\". Vǎn bản này thoả thuận hàng hoá của Đông Dương xuất sang Nhật và ngược lại đều được hưởng một chế độ thuế quan nhẹ. Hiệp ước còn quy định các thể thức thanh toán giữa hai nước. Hai bản hiệp ước trên thực tế đã mở cửa cho Nhật xâm nhập kinh tế vào Đông Dương một cách mạnh mẽ. Như vậy Nhật ngày càng trở thành bạn hàng chủ yếu, có lúc gần như duy nhất của hoạt động ngoại thương Đông Dương trong đó có Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','05', 'Ngày 6-5-1951 Chính phủ Việt Nam Dân chủ cộng hoà ban hành sắc lệnh thiết lập Ngân hàng Quốc gia Việt Nam. Ngân hàng có nhiệm vụ: Quản lý ngân quỹ quốc gia, quản lý ngoại tệ, quản lý kim dụng bằng các thể lệ hành chính, đấu tranh tiền tệ với địch.\nNgân hàng Quốc gia Việt Nam ra đời để góp phần giải quyết những khó khǎn về kinh tế tài chính, đẩy mạnh kháng chiến. Ngân hàng có kế hoạch cho nhân dân vay vốn phát triển sản xuất, giúp công thương mở mang kinh doanh. Riêng nǎm 1953-1954 Ngân hàng Quốc gia Việt Nam đã giúp đỡ nông dân ở 620 xã, phát động quần chúng giảm tô cải cách ruộng đất và lập được 8605 quỹ vay mượn tương trợ...\nĐồng chí Nguyễn Lương Bằng được bổ nhiệm là Tổng Giám đốc và đồng chí Lê Vǎn Lương là Phó tổng giám đốc. Đây là các cán bộ đầu tiên của ngành Ngân hàng nước ta.\nNgày nay Ngân hàng Quốc gia Việt Nam được đổi tên là Ngân hàng Nhà nước Việt Nam. Ngân hàng Nhà nước Việt Nam đã thực sự là ngân hàng mẹ của hệ thống các ngân hàng hiện có. Ngành Ngân hàng đang đóng vai trò quan trọng trong việc quản lý và kinh doanh tiền tệ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '06','05', 'Con đường hầm xuyên qua đáy biển Mǎngsơ nối liền Anh và Pháp được Nữ hoàng Anh Elizabét II và Tổng thống Pháp Mittơrǎng cắt bǎng khánh thành vào ngày 6-5-1994. Con đường dài 150 km, trong đó có 114 km ngầm dưới đáy biển. Ba con đường ngầm được đào dưới độ sâu 45 m. Có hai đường chính và một con đường cứu nạn ở giữa. Đường được thiết kế cho loại tàu điện rộng 4 mét chạy qua. Một đầu của đường ngầm là ga Cokeile của nước Pháp đầu kia là ga Sieborg của nước Anh.\nĐây là một công trình đường ngầm vĩ đại có một không hai trên thế giới, nó có ý nghĩa trọng đại trong quá trình phát triển hệ thống giao thông toàn châu Âu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','06', 'Ngày 6-6-1884, Chính phủ Pháp viết một Hiệp ước, đưa cho triều đình Huế kí nhận.\nHiệp ước này gọi là hiệp ước Patơnốt, gồm 19 khoản. Nội dung cơ bản giống như hiệp ước ngày 25-8-1883, nghĩa là triều đình Huế thừa nhận Nam Kỳ là thuộc địa của Pháp. Phần đất còn lại đặt dưới chế độ \"bảo hộ\" của Pháp. Nước Pháp sẽ thay mặt triều đình Huế trong mọi quan hệ đối ngoại. Pháp đưa thêm mấy tỉnh Bình thuận, Hà Tĩnh, Nghệ An, Thanh Hoá vào Trung Kỳ, và về danh nghĩa là cho triều đình cai quản, nhưng dưới sự bảo hộ của Pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','06', 'Mai Xuân Thưởng sinh nǎm 1860, quê ở xã Bình Thành, huyện Bình Khê, tỉnh Bình Định, đỗ cử nhân, giỏi võ, có tinh thần yêu nước cǎm thù giặc.\nNǎm 1855, hưởng ứng chiếu Cần Vương của vua Hàm Nghi, ông cùng với các sĩ phu khác chiêu mộ nghĩa quân và đã tiêu diệt nhiều binh lính của địch. Giặc Pháp nhiều lần gửi thư dụ hàng cho Mai Xuân Thưởng nhưng không lay chuyển được ý chí của ông. Bọn giặc đã bắt bà mẹ ông cùng một số nười trong làng. Được tin này, ông đã ra gặp bọn chúng để tránh cho mẹ ông và dân làng khỏi chịu khổ nhục. Giặc dụ ông hàng, ông nói: \"Chỉ có thể chém đầu tôi, chứ không thể bắt tôi đầu hàng\".\nNgày 6-6-1887, bọ giặc đã xử tử Mai Xuân Thưởng, lúc đó ông mới 27 tuổi. Ở Hà Nội có một đoạn đường phố mang tên Mai Xuân Thưởng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','06', 'Hoảng sợ trước phong trào cách mạng Việt Nam ngày càng lên cao, một mặt đế quốc Pháp ra sức đàn áp, khủng bố phong trào cách mạng trong nước; mặt khác chúng cấu kết với các đế quốc khác bắt bớ các cán bộ lãnh đạo của Đảng đang hoạt động ở nước ngoài. Do âm mưu đó, đế quốc Anh đã bắt giam đồng chí Nguyễn Ái Quốc một cách trái phép vào ngày 6-6-1931 tại số nhà 186, phố Tam Lung, Cửu Long, Hương Cảng - Trung Quốc, vu cho Người là \"Tay sai của Nga-Xô\", \"Có âm mưu phá hoại chính quyền Hương Cảng\". Chúng bị mật giam đồng chí trong một xà lim riêng ở Hương Cảng, có những cảnh sát đặc biệt giam giữ.\nMùa xuân nǎm 1933 vơi sự giúp đỡ của luật sư Anh Lôdơbai, đồng chí Nguyễn Ái Quốc mới được trả tự do.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','06', 'Ngày 6 tháng 6 nǎm 1947, Chủ tịch Hồ Chí Minh ký sắc lệnh số 58/SL đặt 3 loại huân chương: Sao Vàng, Hồ Chí Minh, Độc Lập, do Chủ tịch nước Việt Nam dân chủ cộng hoà ra sắc lệnh tặng (hoặc truy tặng) cho đoàn thể và những người có công với nước, với dân, hoặc các nhân vật người nước ngoài có công với nước Việt Nam.\n- Huân chương Sao Vàng chỉ có 1 hạng \"để tặng những người có công đức vĩ đại với dân tộc\".\n- Huân chương Hồ Chí Minh có 3 hạng: hạng nhất, hạng nhì, hạng ba \"để tặng những người có tài có đức, có công với dân tộc\".\n- Huân chương Độc Lập có 3 hạng: hạng nhất, hạng nhì, hạng ba \"để tặng những người có công đặc biệt trong việc cứu quốc hoặc kiến quốc\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','06', 'Từ ngày 6 đến ngày 8-6-1969, Đại hội đại biểu quốc dân miền Nam nước ta đã ra nghị quyết thành lập chế độ cộng hoà miền Nam, nhất trí bầu ra Chính phủ Cách mạng lâm thời cộng hoà miền Nam Việt Nam do kiến trúc sư Huỳnh Tấn Phát làm Chủ tịch, và Hội đồng cố vấn Chính phủ do Luật sư Nguyễn Hữu Thọ làm Chủ tịch.\nĐại hội này cũng đã ra lợi kêu gọi quân và dân miền Nam đẩy mạnh sự nghiệp chống Mỹ, cứu nước đến toàn thắng.\nNgay trong tháng 6-1969, đã có 23 nước công nhận Chính phủ Cách mạng Lâm thời cộng hoà miền Nam Việt Nam, trong đó có 21 nước đã đặt quan hệ ngoại giao.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','06', 'Từ ngày 5 đến 8-6-1976 đã diễn ra hội nghị thống nhất Công đoàn toàn quốc. Hội nghị đã nghe báo cáo của Tổng Công đoàn Việt Nam và Liên hiệp Công đoàn giải phóng miền Nam Việt Nam, thông qua nguyên tắc thống nhất tổ chức công đoàn hai miền, thông qua danh sách Ban Chấp hành Tổng Công đoàn, Đoàn Chủ tịch, Ban Thư ký và Ban Kiểm tra tài chính.\nHội nghị quyết định lấy tên Công đoàn thống nhất toàn quốc là Tổng Công đoàn Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '06','06', 'Pie Coónây (Pierre corneille) là người đặt nền móng và là đại biểu xuất sắc của nền bi kịch cổ điển Pháp. Ông sinh ngày 6-6-1606 và mất ngày 1-10-1684.\nĐược hưởng một nền giáo dục vững vàng, kiến thức sâu rộng, từ nǎm 23 tuổi Coónây đã sáng tác kịch thơ. Ông có hàng loạt hài kịch. Nhưng chính tác phẩm bi kịch mới làm tên tuổi ông rạng rỡ. Tiêu biểu là các vở bi-hài kịch xuất sắc có ý nghĩa chuyển giai đoạn là \"Mêđê\", \"Lơ xít\", và tiếp theo là các vở bi kịch nổi tiếng \"Orax\", \"Xina\".\nCác sáng tác của Coónây góp phần vào giai đoạn phát triển của nền bi kịch cổ điển Pháp và đưa nó đến trình độ hoàn chỉnh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '06','06', 'Puskin, nhà thơ, nhà vǎn thiên tài của nước Nga sinh ngày 6-6-1799 trong một gia đình quý tộc Mátxcơva. \nTừ nhỏ Puskin được hưởng mọi điều kiện để học tập và phát triển tài nǎng. Thắng lợi của quân dân Nga trong cuộc chiến tranh vệ quốc chống Napôlêông đã bồi dưỡng cho ông thêm tinh thần yêu nước, yêu tự do, chống cường quyền bạo lực, cǎm ghét ách nô dịch. Thời học sinh, Puskin đã sáng tác một số bài thơ, tiêu biểu là bài \"Hồi ức ở Hoàng Thôn\", báo hiệu tài nǎng và hướng đi tương lai của nhà thơ.\nSau khi tốt nghiệp trung học Puskin làm việc ở Bộ Ngoại giao. Thời gian này, khí thế cách mạng sôi nổi, ông đã viết một số bài thơ cổ vũ nhiệt tình đấu tranh chống chế độ Nga hoàng, kêu gọi giải phóng nhân dân. Nga hoàng ra lệnh trục xuất ông khỏi Pêtecbua và lưu đày. Trong thời gian bị lưu đày, ông tiếp tục làm thơ. Về phương pháp sáng tác, từ chủ nghĩa lãng mạn cách mạng với những bài thơ trữ tình ông chuyển sang phương pháp hiện thực viết về cuộc sống hàng ngày phong phú, đầy hương vị của đất nước Nga. Cuốn tiểu thuyết thơ \"Epghênhi Ônhêghin\", vở bi kịch lịch sử \"Bôrit Gôđunốp\", những tác phẩm vǎn xuôi \"Người da đen của Piôt đại đế\", \"Người trưởng trạm\", \"Người con gái viên đại uý\"... là những tác phẩm đặt nền móng cho phương pháp hiện thực ở nước Nga.\nPuskin đã tham gia trận quyết đấu vào ngày 10-2-1837 để bảo vệ danh dự, và ông đã bị sát hại. Nhân dân Nga vô cùng thương tiếc nhà thơ vĩ đại mà sự nghiệp gắn chặt với cuộc vận động cách mạng Nga đầu thế kỷ XIX, một người đã có nhiều đóng góp cho nền vǎn học Nga.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '06','06', 'Ngày 6-6-1875, Thômát Man (Thomas Mann), người Đức, đã sinh ra và ông qua đời ngày 12-8-1955. Ông là nhà vǎn hiện thực phê phán nổi tiếng của nước Đức, được nhận giải Nôben về vǎn học nǎm 1929.\nThômát Man kiên quyết chống chế độ quốc xã phát xít, phê phán xã hội tư bản, tiếp tục truyền thống nhân đạo của Gớt và Silơ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '06','06', 'Ngày 6-6-1882, Sêly ở thành phố Niu Oóc (Hoa Kỳ) đã đǎng ký phát minh bàn là điện. Tuy nhiên, vào lúc đó, nó chưa được sử dụng nhiều bởi vì trong các ngôi nhà ở chưa được nối với lưới điện.\nCòn ở nước Pháp, chính Calo là người đã góp phần thương mại hoá bàn là điện bằng cách chế tạo mẫu đầu tiên của mình vào nǎm 1913.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','07', 'Ngày 6-7-1956 trong thư gửi đồng bào cả nước, Hồ Chủ tịch nêu rõ: Đế quốc Mỹ và chính quyền thân Mỹ ở Miền Nam âm mưu chia cắt lâu dài đất nước ta, phá hoại Tổng tuyển cử tự do trong toàn quốc.\nNgười kêu gọi đồng bào cả nước \"Thống nhất nước nhà là con đường sống của nhân dân ta. Đại đoàn kết là một lực lượng tất thắng\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','07', 'Đại tướng Nguyễn Chí Thanh sinh ngày 1-1-1914 ở thôn Niêm Phò, Hương Điền, Thừa Thiên. Từ nǎm 17 tuổi ông đã cùng một số thanh niên đấu tranh chống Pháp, rồi tham gia phong trào bình dân, tích cực hoạt động Cách mạng. Ông là thành viên chính của Đảng cộng sản Đông Dương và lần lượt làm Bí thư chi bộ, Bí thư tỉnh uỷ Thừa Thiên. Nǎm 1945 ông được bầu vào Ban Chấp hành Trung ương Đảng làm Bí thư Phân khu uỷ Bình Trị Thiên.\nTrong kháng chiến chống Pháp ông làm chủ nhiệm Tổng cục Chính trị, được bầu vào Bộ Chính trị, là Ủy viên hội đồng Quốc phòng và được phong hàm đại tướng. Trong kháng chiến chống Mỹ, ông được cử làm Bí thư Trung ương Cục miền Nam.\nĐại tướng Nguyễn Chí Thanh là một Cán bộ Lãnh đạo có tài nǎng, nghị lực, góp phần tích cực vào những chiến thắng vẻ vang của quân dân Việt Nam. Do công lao của mình ông được tặng thưởng nhiều huân chương, đặc biệt là huân chương Hồ Chí Minh. Đại Tướng Nguyễn Chí Thanh mất ngày 6-7-1967.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','07', 'Ngày 6-7-1973, Xí nghiệp Liên hiệp xây dựng cầu Thǎng Long (nay là Tổng Công ty) được thành lập để đảm nhận nhiệm vụ xây dựng cây cầu bắc qua sông Hồng, cây cầu lớn nhất lúc bấy giờ.\nSau khi hoàn thành công trình này, Tổng Công ty xây dựng cầu Thǎng Long đã vươn lên trở thành một doanh nghiệp lớn của Nhà nước, sản suất nhiều sản phẩm, kinh doanh trên nhiều lĩnh vực, như: Xây lắp các loại công trình giao thông, công nghiệp, dân dụng...đạt kết quả đáng phấn khởi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '06','07', 'Ghiđờ Môpátxǎng (Guy de Maupassant) là nhà vǎn hiện thực lớn ở Pháp, sinh ngày 5-8-1850. Ông tốt nghiệp trường luật, ra làm viên chức và nhập ngũ khi chiến tranh nổ ra.\nNǎm 1880 Môpátxǎng đǎng truyện \"Viên mỡ bò\" nói về cuộc chiến tranh Pháp, Phổ. Với câu chuyện này, ông đã châm biếm đả kích gay gắt bọn quý tộc, tư sản bạc nhược không có lòng tự tôn dân tộc trước kẻ thù. Và kể từ đây ông dồn toàn bộ tâm sức vào hoạt động sáng tạo. Ngòi bút của ông rất sung sức, trong vòng 10 nǎm ông viết trên 300 truyện ngắn, hai tập ký, sáu tiểu thuyết, ngoài ra còn mấy vở kịch và nhiều bài báo khác. Nhưng tác phẩm xuất sắc phải kể đến là \"Một cuộc đời\", \"Ông bạn đẹp\", \"Núi Orion\"...\nCác tác phẩm đã ca ngợi cuộc đấu tranh chống xâm lược và vạch trần sự thật xấu xa của giai cấp tư sản, quý tộc đương thời. Các tác phẩm của ông cô đúc, sâu sắc được diễn đạt bằng một vǎn phong hết sức trong sáng, giản dị, tự nhiên, tinh luyện.\nMôpátxǎng mất ngày 6-7-1893.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','08', 'Nhà vǎn Nguyễn Khoa Vǎn, bút danh Hải Triều, sinh ngày 1-1-1908 tại An Cựu, ngoại thành Huế. Ông là đảng viên lớp đầu của Đảng cộng sản Đông Dương. Nǎm 1931 ông bị bắt và bị đưa về Huế kết án 9 nǎm khổ sai và 8 nǎm quản thúc. Tháng 7-1932 ông được trả tự do. Ra tù, ông bí mật hoạt động Cách mạng. Đồng thời, ông viết bài trên các báo chí hợp pháp để truyền bá chủ nghĩa Mác và quan điểm của Đảng về nghệ thuật vị nhân sinh. Ngòi bút lý luận sắc bén của ông từng áp đảo đối phương trong những cuộc tranh biện, bút chiến về vǎn học.\nTháng 8-1940 ông lại bị bắt đi an trí tại Phong Điền tỉnh Thừa Thiên. Đến tháng 3-1945 ông mới được thả. Sau đó ông tham gia tổng khởi nghĩa ở Huế. Sau khi giành chính quyền, ông là Giám đốc Sở tuyên truyền Trung Bộ. Trong thời gian kháng chiến chống Pháp ông làm Giám đốc Sở tuyên truyền Liên khu IV, Uỷ viên ban chấp hành Chi hội vǎn nghệ Liên khu IV. Các tác phẩm chính của ông là: \"Duy tâm hay duy vật\" (chuyên luận, xuất bản vǎn 1935). \"Vǎn sĩ và xã hội\" (1937), \"Về vǎn học nghệ thuật\" (tuyển tập - 1965).\nÔng mất ngày 6-8-1954 tại Hà Lũng tỉnh Thanh Hoá, sau cơn bệnh kéo dài, thọ 46 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','08', 'Ngày 6-8-1972, quân và dân Hải Phòng đã bắn rơi chiếc máy bay Mỹ thứ 3.800 trên miền Bắc. Trong cuộc chiến đấu chống chiến tranh phá hoại của giặc Mỹ đối với miền Bắc, quân và dân Hải Phòng đã bắn rơi 270 máy bay Mỹ, bắn cháy, bắn chìm 8 tàu chiến Mỹ, xứng đáng với danh hiệu \"Thành phố trung dũng, quyết thắng\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '06','08', 'Hồi 9 giờ 15 phút ngày 6-8-1945, trái bom nguyên tử đầu tiên ném xuống Hirôsima, một hải cảng lớn của Nhật. Trái bom nổ, phát sinh một cột lửa hình nấm cao 20 km, huỷ diệt một phạm vi đường kính tới 5 km, làm hàng vạn người chết và ngắc ngoải suốt đời.\nKhông ghê tay trước tội ác man rợ này, ngay sau khi Liên Xô tuyên chiến với Nhật, Mỹ lại cho ném trái bom nguyên tử thứ hai, mục tiêu là thành phố Nagadaki, vào lúc 12 giờ ngày 9-8-1945, trong khi tan tầm, người đi làm đổ ra kín đường. Trái bom đã san phẳng thành phố thành tro bụi, giết hại hàng chục vạn dân.\nCả loài người đều lên án việc làm này của Mỹ, và ngày trái bom nguyên tử đầu tiên ném xuống Nhật Bản (6-8) trở thành ngày chống vũ khí nguyên tử, dưới khẩu hiệu \"Không để tái diễn thảm họa Hirôsima và Nagadaki\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','09', 'Đồng chí Lê Hồng Phong tên thật là Lê Huy Doãn, sinh năm 1902 tại Hưng Nguyên, Nghệ An. Giác ngộ cách mạng khi còn là công nhân nhà máy Diêm (Vinh). Tháng 1-1924, đồng chí cùng Phạm Hồng Thái sang Xiêm, rồi qua Quảng Châu. Tháng 4-1924 được kết nạp vào Tâm Tâm Xã. Đồng chí được Nguyễn Ái Quốc chọn đưa vào cộng sản Đoàn và cử đi học Trường quân sự Hoàng Phố. Năm 1926, đồng chí sang Liên Xô học trường không quân, sau đó là trường đại học Phương Đông (Liên Xô). Năm 1932 đồng chí lại qua Trung Quốc hoạt động. Năm 1934 đồng chí phụ trách Ban lãnh đạo hải ngoại của Đảng được thành lập tại Ma Cao. Tháng 7-1935 đồng chí mang bí danh Hải An tham dự Đại hội Quốc tế Cộng sản họp tại Mátxcơva với tư cách là đại biểu Đông Dương và được bầu làm Uỷ viên Ban Chấp hành Quốc tế Cộng sản. Tháng 1-1936, đồng chí trở lại Trung Quốc triệu tập hội nghị Trung ương Đảng họp tại Thượng Hải rồi trở về nước hoạt động. Giữa năm 1938, đồng chí bị bắt ở Chợ Lớn và bị kết án 10 tháng tù. Ra khỏi tù, đồng chí trở về quê, rồi lại bị bắt vào ngày 29-9-1939 và bị kết án 5 năm tù, đày ra Côn Đảo. Sau ba năm bị tù đày, đồng chí Lê Hồng Phong đã hy sinh vào trưa ngày 6-9-1942\nĐồng chí Lê Hồng Phong là một cán bộ lãnh đạo xuất sắc của Đảng. Đồng chí luôn nêu cao khí tiết cách mạng trước kẻ thù. Trước khi vĩnh biệt cuộc đời đồng chí đã dồn hết sức tàn nói to sang buồng giam bên cạnh: \"Chào các đồng chí! Nhờ báo cáo với Đảng rằng đến giờ phút cuối cùng, Lê Hồng Phong vẫn một lòng tin tưởng ở thắng lợi vẻ vang của Cách mạng\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','09', 'Để tăng cường điều kiện và đẩy mạnh sản xuất, cải thiện đời sống nhân dân miền núi, góp phần đảm bảo cung cấp và xây dựng nhà nước. Ngày 6-9-1957 Phủ thủ tướng đã ra Nghị định ban hành 10 chính sách khuyến khích sản xuất ở miền núi.\nCác chính sách này được áp dụng cho các dân tộc thiểu số và đồng bào Kinh sống trong vùng dân tộc thiểu số.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','09', 'Ngày 6-9-1961, 400 công nhân hãng dầu Mỹ Stanvac ở Sài Gòn bãi công xưởng. Cuộc bãi công này kéo dài đến ngày 22-9, làm tê liệt 100 trạm bán dầu, làm ngừng trệ việc cấp xăng cho máy bay Mỹ, gây cho bọn chủ thiệt hại mỗi ngày từ 3 đến 4 triệu đồng nguỵ Sài Gòn.\nCuối cùng bọn Mỹ buộc phải tăng lương cho công nhân hãng dầu Stanvac từ 6 - 12%.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','10', 'Chu Văn An là nhà giáo và nhà thơ, sinh ngày 6-10-1292 tại xã Thanh Liệt, huyện Thanh Trì, Hà Nội, qua đời năm 1370.\nÔng đã đỗ Thái học sinh.\nĐời Trần Minh Tông, ông làm việc ở Quốc Tử Giám, phụ trách việc giảng kinh cho Thái tử và biên soạn sách.\nĐời Trần Dụ Tông, Chu Văn An đang làm quan tại triều đình, thấy chính sự bại hoại, ông đã viết \"Thất trảm sớ\" xin chém đầu 7 gian thần. Vua không nghe, ông bèn từ quan, về ở ẩn tại huyện Chí Linh (Hải Dương). Từ đó ông chỉ làm thơ văn và dạy học. Học trò của Chu Văn An có nhiều người hiển đạt như Phạm Sư Mạnh, Lê Bá Quát...\nSau khi mất, ông được đưa vào thờ ở nhà Văn Miếu (Hà Nội). Ông sáng tác tập thơ chữ Hán \"Tiều ẩn thi tập\", một số bài thơ còn chép ở trong \"Toàn Việt thi lục\" do Lê Quý Đôn sưu tập. Ngoài ra, Chu Văn An còn cùng với Hàn Thuyên, Nguyễn Sĩ Cố là những người đầu tiên dùng chữ nôm để sáng tác văn học.\nTại Hà Nội và Thành phố Hồ Chí Minh đều có đường phố mang tên Chu Văn An.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','10', 'Thế Lữ tên thật là Nguyễn Thứ Lễ, quê ở làng Phù Đổng, huyện Gia Lâm, Hà Nội sinh ngày 6-10-1907, mất năm 1989.\nÔng sớm có mặt trong Tự lực văn đoàn, và nổi tiếng với bài thơ \"Nhớ rừng\". Bài thơ của ông đã thể hiện được tâm trạng nuối tiếc rừng xanh tự do, tâm trạng nhục nhằn và phẫn uất của một con hổ bị nhốt trong vườn bách thú, mà đọc lên người ta dễ liên tưởng tới thân phận những con người có tài năng và khí phách trong một đất nước bị nô lệ. Cùng với những bài thơ khác như \"Tiếng sáo thiên thai\", \"Cây đàn muôn điệu\", Thế Lữ đã trở thành một trong những người mở đầu phong trào Thơ Mới 1930-1945.\nThế Lữ còn là một nhà hoạt động sân khấu. Ông viết kịch bản, làm diễn viên, đạo diễn. Trong kháng chiến chống Pháp, ông phụ trách Đoàn kịch Chiến Thắng của quân đội, và từ năm 1957, ông là Chủ tịch Hội Nghệ sĩ sân khấu Việt Nam. Kịch của Thế Lữ thường lấy đề tài trong lịch sử chống ngoại xâm của dân tộc.\nTrên cả hai lĩnh vực thơ và kịch, Thế Lữ đều có những đóng góp có tính chất mở đầu. Ông được Nhà nước phong danh hiệu Nghệ sĩ nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','10', 'Trần Đăng Ninh sinh năm 1910, quê ở huyện Ứng Hoà, tỉnh Hà Tây, qua đời ngày 6-10-1955.\nÔng là đảng viên Đảng Cộng sản Đông Dương từ năm 1936. Năm 1940, ông tham gia lãnh đạo phong trào đấu tranh vũ trang ở Bắc Sơn. Năm 1941, ông là Uỷ viên Trung ương Đảng. Năm 1945, ông tham gia Uỷ ban khởi nghĩa. Năm 1947, ông phụ trách công tác kiểm tra của Đảng và sau đó làm Phó tổng thanh tra Chính phủ. Từ năm 1950, ông giữ chức Chủ nhiệm Tổng cục Cung cấp, sau này gọi là Tổng cục Hậu cần (Bộ Quốc phòng). Năm 1951, ông được bầu lại làm uỷ viên Trung ương Đảng.\nSau khi qua đời, ông đã được Nhà nước truy tặng Huân chương Độc lập hạng nhất do có nhiều đóng góp trong cuộc kháng chiến chống Pháp và trong việc củng cố chính quyền dân chủ nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','10', 'Ngày 6-10-1984, Hội đồng Nhà nước tặng thưởng thủ đô Hà Nội Huân chương Sao Vàng, tặng thưởng lực lượng vũ trang Thủ đô Huân chương Hồ Chí Minh, tặng thưởng lực lượng công an nhân dân Thủ đô Huân chương Hồ Chí Minh - Vì những đóng góp của nhân dân và lực lượng vũ trang Thủ đô trong công cuộc xây dựng và bảo vệ miền Bắc xã hội chủ nghĩa và giải phóng miền Nam thống nhất tổ quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','11', 'Từ ngày 6-11-1940 đã diễn ra Hội nghị lần thứ 7 của Trung ương Đảng Cộng sản Đông Dương. Hội nghị họp tại làng Đình Bảng, Từ Sơn, Bắc Ninh.\nHội nghị chủ trương chuyển hướng chỉ đạo chiến lược của Ban Chấp hành Trung ương lâm thời và phân công đồng chí Trường Chinh làm quyền Tổng Bí thư, đồng thời quyết định chắp nối liên lạc với Quốc tế Cộng sản và bộ phận của Đảng ở nước ngoài. Hội nghị đã thảo luận và quyết định hai vấn đề cấp thiết là: Phát triển ảnh hưởng của khởi nghĩa Bắc Sơn và hoãn cuộc khởi nghĩa Nam Kỳ.\nThành công lớn của Hội nghị là đã đặt vấn đề khởi nghĩa vũ trang vào chương trình nghị sự của Cách mạng Đông Dương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','11', 'Ngày 6-11-1967, Hồ Chủ tịch gửi điện tới Ban chấp hành Trung ương Đảng cộng sản Liên Xô về việc ngày 4-11 Đoàn Chủ tịch Xô Viết tối cao Liên Xô quyết định tặng Người Huân chương Lênin - Huân chương cao quý nhất của Liên Xô. Hồ Chủ tịch đã tỏ lòng cảm ơn Trung ương Đảng Cộng sản Liên Xô, Xô viết tối cao Liên Xô, Chính phủ Liên Xô, đồng thời, bằng những lời lẽ hết sức chân thành, Người đề nghị: \"Hãy tạm hoãn việc trao tặng phần thưởng cực kỳ cao quý ấy. Đến ngày nhân dân chúng tôi đánh đuổi được bọn đế quốc Mỹ xâm lược, giải phóng hoàn toàn đất nước Việt Nam, tôi sẽ đại diện cho toàn thể đồng bào tôi trân trọng và vui mừng lãnh lấy Huân chương mang tên Lênin vĩ đại\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','11', 'Ngày 6-11-1967, quân và dân Hà Nội đã bắn rơi chiếc máy bay F105 của Giặc Mỹ. Đây là chiếc máy bay Mỹ thứ 2.500 bị bắn rơi trên miền bắc nước ta là chiếc thứ 200 bị quân dân Thủ đô bắn rơi tại Hà Nội.\nTrong hai lần chống chiến tranh phá hoại của đế quốc Mỹ đối với miền Bắc, quân và dân thủ đô Hà Nội đã bắn rơi 351 máy bay Mỹ, trong đó 32 chiếc B52 và 2 chiếc F111.\nHà Nội xứng đáng là Thủ đô của \"Phẩm giá con người\" như bạn bè quốc tế ca ngợi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','11', 'Ngày 6-11-1979, khởi công xây dựng Nhà máy Thuỷ điện Hoà Bình trên Sông Đà.\nĐến ngày 4-4-1994, tổ máy số 8, tổ máy cuối cùng đã hoà điện vào lưới điện quốc gia. Nhà máy Thuỷ điện Hoà Bình có công suất 1.920 mêga oát.\nĐây là thành quả của 15 nǎm lao động gian khổ, sáng tạo của cán bộ, công nhân Việt Nam và Liên Xô. Có 168 người (trong đó có 11 công dân Liên Xô) đã hy sinh tính mạng vì dòng điện ngày mai.\nNhà máy Thuỷ điện Hoà Bình là một công trình thuỷ điện lớn bậc nhất ở châu Á.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '06','11', 'Ơgien Pôchiê sinh ngày 6-11-1816 và qua đời nǎm 1887\nÔng là nhà thơ cách mạng vô sản Pháp, tác giả lời bài \"Quốc tế ca\" - bài ca đấu tranh của công nhân toàn thế giới.\nÔng đã từng cầm súng chiến đấu cho công xã Pari (tháng 3-1871).\nƠgien Pôchiê được Lênin gọi là \"nhà tuyên truyền vĩ đại của cách mạng vô sản\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '06','11', 'Clốt Lui Béctôlê sinh nǎm 1794 và mất ngày 6-11-1822.\nÔng là nhà hoá học người Pháp đã tìm ra tính tẩy màu của hipôcrolit (nước gia-ven), chế thuốc nổ clorát, nêu ra định luật về phản ứng thuận nghịch giữa muối, axit và badơ. Ông cũng chứng minh không phải mọi axit đều chứa oxigen.\nBéctôle cùng với Môngơ lập trường Đại học Bách khoa, cùng với Laplaxơ lập Hội bác học Accơi. Ông là viện sĩ Viện Hàn lâm khoa học Pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '06','11', 'Piốt Ilitsơ Traicôpxki là nhạc sĩ nổi tiếng nước Nga. \nÔng sinh nǎm 1840 và qua đời ngày 6-11-1893\nNǎng khiếu âm nhạc của Traicôpxki bộc lộ khá sớm. Nǎm 21 tuổi, ông vào học tại nhạc viện Pêtecxbua và tốt nghiệp xuất sắc. Từ nǎm 1866 đến nǎm 1878, ông làm giáo sư dạy nhạc tại nhạc viện Mátxcơva. Thời gian này, ông sáng tác nhiều tác phẩm nổi tiếng cho vở balê \"Hồ Thiên Nga\", \"Người đẹp ngủ trong rừng\" và vở ca kịch \"Épghênhi ônhêghin\" (lấy đề tài trong bản trường ca của Puskin).\nTraicôpxki sáng tác hầu hết các thể loại âm nhạc. Ông là một trong những người đặt nền móng cho nhạc giao hưởng cổ điển Nga. Trong lịch sử âm nhạc thế giới, ông được ghi nhận là người cách tân xuất sắc thể loại vũ kịch (balê).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '06','11', 'Ngày 6-11-1996, hơn 2000 người thiệt mạng trong một trận đại hồng thủy tại bang Andhra Pradesh, Ấn Độ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','12', 'Cuối tháng 11-1953, tướng Nava cho quân Pháp đẩy lùi xuống chiếm Điện Biên Phủ (tỉnh Lai Châu) và xây dựng ở đây tập đoàn cứ điểm mạnh nhất Đông Dương.\nNgày 6-12-1953, sau khi bàn chủ trường và kế hoạch tác chiến đông-xuân 1953-1954. Bộ chính trị trung ương Đảng Lao động Việt Nam đã quyết định mở chiến dịch tấn công tiêu diệt tập đoàn cứ điểm của Pháp ở Điện Biên Phủ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','12', 'Ngày 6-12-1967, chị Nguyễn Thị Xuân, dân quân huyện Quảng Trạch tỉnh Quảng Bình, một mình dùng súng trường bắn rơi một chiếc F4 (Con Ma) của giặc Mỹ. Đây là một chiến công tuyệt vời của dân quân gái và của cây súng trường.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','12', 'Ngày 6-12-1972, quân giải phóng miền Nam đã bắn phá cǎn cứ không quân Mỹ - Ngụy ở Tân Sơn Nhất (Sài Gòn) diệt hơn 200 sĩ quan và nhân viên kỹ thuật Mỹ - Nguỵ phá huỷ hơn 50 máy bay (trong đó có 27 chiếc phản lực và 3 chiếc C310), đốt cháy hai kho xǎng lớn và nhiều phương tiện chiến tranh khác trong sân bay.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '06','12', 'Ngày 6-12-1978, công trình trang thiết bị Nhà in báo Nhân Dân do cộng hoà dân chủ Đức giúp ta xây dựng từ đầu nǎm 1975 đã được khánh thành.\nCông trình này đảm bảo in 1 triệu tờ báo trong một ngày.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','01', 'Ngày 7-1-1979, nhờ sự giúp đỡ của quân đội tình nguyện Việt Nam, nhân dân Campuchia đã lật đổ chế độ diệt chủng Pôn Pốt, giải phóng Thủ đô Phnôm Pênh.\nNước cộng hoà nhân dân Campuchia và Hội đồng nhân dân Cách mạng Campuchia tuyên bố thành lập.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','01', 'Nhà vǎn, nhà báo Tam Lang, tên thật là Vũ Đình Chí, quê ở tỉnh Bắc Ninh, sinh nǎm 1901 ở Hà Nội, mất ngày 7-1-1986 tại thành phố Hồ Chí Minh.\nÔng chuyên viết phóng sự và nổi tiếng ở thể lệ loại này từ khi viết bài phóng sự đầu tay có nhan đề \"Tôi kéo xe\" (In báo nǎm 1935).\nPhóng sự đã mở ra cho làng vǎn, làng báo nước ta một thể loại mới gây nên tiếng vang trong sinh hoạt vǎn học hồi đó. Kể từ ấy, hằng nǎm Tam Lang viết hàng loạt phóng sự sống động về xã hội Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','01', 'Lép Nicôlaêvich Tônxtôi sinh nǎm 1828 trong một gia đình quí tộc ở Nga. Ông qua đời ngày 7-1-1910.\nThời thơ ấu, Tônxtôi sống gần gũi nông dân và thiên nhiên Nga. Nǎm 16 tuổi đã vào học trường đại học, say mê vǎn học và âm nhạc, chịu ảnh hưởng sâu sắc triết học tiến bộ của phái Ánh sáng Pháp. Ông đã từng là sĩ quan pháo binh, chiến đấu chống lại quân xâm lược Thổ Nhĩ Kỳ, Anh và Pháp.\nTrong 60 nǎm, hầu như không ngày nào Tônxtôi không viết. Bạn đọc Việt Nam rất yêu mến Lép Tônxtôi qua các tiểu thuyết: Chiến tranh và hoà bình, Anna Karênina và Phục sinh.\nLep Tônxtôi là nghệ sĩ vĩ đại của dân tộc Nga nửa cuối thế kỷ XIX, nói lên được những tư tưởng và tâm trạng đã hình thành trong hàng triệu nông dân Nga khi bắt đầu Cách mạng tư sản.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','01', 'Nicola Tesla sinh nǎm 1856, mất ngày 7-1-1943 là kĩ sư người Nam Tư, di cư sang Mỹ.\nÔng có nhiều phát minh quan trọng về điện kĩ thuật và vô tuyến điện (điện đa pha, bộ đổi điện v.v...)\nTên ông được đặt cho đơn vị cảm ứng từ và viết tắt là T.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','02', 'Danh sĩ Vũ Hữu Lợi, sinh nǎm 1836 tại tỉnh Nam Định. Nǎm 1875, ông đỗ tiến sĩ, người đương thời gọi là ông Nghè Giao Cù (Vì quê ông ở làng Giao Cù).\nÔng làm đốc học tỉnh Nam Định. Sau khi Pháp chiếm Bắc Kỳ, ông bỏ làm quan, về khởi nghĩa chống Pháp bên cạnh Nguyễn Quang Bích và các sĩ phu yêu nước dưới cờ Cần Vương chống Pháp. ít lâu sau, ông Vũ Hữu Lợi bị tổng đốc Nam Định là Võ Vǎn Bảo lừa bắt, đem nộp cho giặc Pháp. Ông bị chúng xử tử đêm hôm 7-2-1884 ở chợ Nam Định.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','02', 'Ngày 7 và 8-2-1965, đế quốc Mỹ bắt đầu tiến hành cuộc chiến tranh phá hoại miền Bắc bằng không quân có tính chất liên tục. Trong hai ngày này, máy bay phản lực Mỹ từ hạm đội 7 và miền Nam Việt Nam đã đến ném bom bắn phá thị xã Đồng Hới, đảo Cồn Cỏ, Vĩnh Linh. Các đơn vị bộ đội phòng không, hải quân và các lực lượng vũ trang nhân dân đã đánh trả quyết liệt và trừng trị đích đáng những hành động khiêu khích và xâm lược này của đế quốc Mỹ. Trong 2 ngày này, 13 máy bay địch bị bắn rơi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','02', 'Ngày 7-2-1968, đội pháo binh dân quân gái xã Ngư Thuỷ, huyện Lệ Thuỷ (tỉnh Quảng Bình) là đơn vị pháo binh dân quân gái đầu tiên đã bắn cháy tàu chiến Mỹ. Hồ Chủ Tịch đã gửi thư khen. Đến tháng 3 và tháng 5-1968, đơn vị đã bắn cháy lần thứ hai và lần thứ ba tàu chiến Mỹ. Nǎm 1972, đội pháo binh dân quân gái xã Ngư Thuỷ lại bắn cháy 2 tàu chiến nữa của giặc Mỹ.\nĐây là đội pháo binh dân quân gái bắn cháy nhiều nhất tàu chiến Mỹ ở miền Bắc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','02', 'Sáclơ Đichken (Charles diekens), nhà tiểu thuyết Anh, nổi tiếng thế giới sinh ngày 7-2-1812. Tác phẩm đầu tay của ông được xuất bản lúc 22 tuổi. Tiếp đó là hàng loạt các tác phẩm đã đưa tên tuổi ông lên đài vinh quang của vǎn học. Tiêu biểu là các cuốn: \"David Copperfield\", \"Những câu chuyện phiêu lưu của chàng Picknick\". Các tác phẩm của ông tố cáo chế độ nô lệ, tính tham lam ích kỷ, sự bóc lột tư bản và toát lên triết lý nhân đạo. Vì vậy ông được coi là đại diện tiêu biểu cho chủ nghĩa hiện thực phê phán Anh giữa thế kỷ XIX. Ông qua đời ngày 8-6-1870.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','02', 'Ngày 7-2-1979, nhờ sự giúp đỡ của quân đội tình nguyện Việt Nam, nhân dân Campuchia đã lật đổ chế độ diệt chủng Pôn Pốt, giải phóng Thủ đô Phnôm Pênh. Nước cộng hoà nhân dân Campuchia và Hội đồng nhân dân Cách mạng Campuchia tuyên bố thành lập.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','02', '7-2-1989, hội nghị Quốc tế về Hạn chế Vũ khí Sinh học khai mạc tại Paris.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','03', 'Đồng chí Tô Hiệu sinh nǎm 1912 tại Xuân Cầu, Nghĩa Trụ, huyện Vǎn Giang, tỉnh Hưng Yên, tham gia các phong trào yêu nước từ nǎm 1925. Nǎm 1930, đồng chí bị bắt và bị đưa ra Côn Đảo. Tại đây đồng chí giác ngộ Cách mạng và trở thành Đảng viên Cộng sản. Nǎm 1934 đồng chí ra tù tiếp tục hoạt động trong phong trào Mặt trận dân chủ ở Hà Nội rồi trực tiếp chỉ đạo phong trào ở mỏ than Cẩm Phả, được cử làm Thường vụ Xứ ủy Bắc Kỳ phụ trách miền Duyên Hải và là Bí thư Thành ủy Hải Phòng. Tháng 12 nǎm 1939 đồng chí lại bị bắt và bị thực dân Pháp giam cầm tại nhà tù Sơn La. \nTrong tù đồng chí luôn nêu cao khí tiết của người Cộng sản, tổ chức giáo dục, động viên anh em đấu tranh chống lại chế độ lao tù hà khắc của thực dân Pháp, và nuôi dưỡng ý chí cách mạng cứu dân cứu nước. Do bị tra tấn, đầy ải tàn khốc, đồng chí đã lâm bệnh nặng, qua đời ngày 7-3-1944 tại nhà tù Sơn La, hiện nay khu bảo tàng di tích nhà tù Sơn La vẫn còn cây đào do đồng chí trồng. Cây đào mang tên Tô Hiệu và từ lâu đã trở thành biểu tượng cách mạng, một kỷ vật thiêng liêng đối với đất trời và các thế hệ người Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','03', 'Đêm 7-3-1954, một đơn vị bộ đội ta do đồng chí Đặng Kinh chỉ huy (đồng chí Đặng Kinh sau này là Trung tướng, Phó Tổng tham mưu trưởng Quân đội nhân dân Việt Nam) đã bí mật vượt qua hệ thống phòng thủ dày đặc với 2000 quân được cố vấn Mỹ chỉ huy ngày đêm tuần tra chặt chẽ, đột nhập sân bay Cát Bi Hải Phòng, phá hỏng đường bǎng, kho tàng và phá huỷ hoàn toàn 60 máy bay các loại. Chiến công này đã làm cho quân Pháp thiệt hại nặng nề và gặp khó khǎn trong việc tiếp tế cho các chiến trường.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','03', 'Xuất thân trong một gia đình Nho học yêu nước, Nhà thơ Á nam Trần Tuấn Khải - tác giả của các tác phẩm: Bút quan hài, Duyên nợ phù sinh và bài thơ nổi tiếng Tiễn anh Khoá - quê ở huyện Mỹ Lộc nay là ngoại thành Nam Định. Từ sau 1954 ông sống và làm việc tại Sài Gòn. Ông đã tham gia phong trào đấu tranh chống vǎn hoá đồi trụy đòi hoà bình và dân sinh. Nǎm 1966 ông đã cùng với một số trí thức yêu nước ký tên yêu cầu chính quyền Sài Gòn hiệp thương với Mặt trận dân tộc giải phóng miền Nam. Ông mất ngày 7-3-1983, thọ 88 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','04', 'Ngày 7-4-1803, Ngô Thì Nhậm từ trần. \nNgô Thì Nhậm sinh nǎm 1746 tại làng Tả Thanh Oai, huyện Thanh Trì, Hà Nội qua đời ngày 7-4-1803.\nÔng đỗ tiến sĩ nhưng thất vọng vì chế độ Lê - Trịnh suy mạt. Đến với Nguyễn Huệ, ông hiến diệu kế \"rút quân về Tam Điệp\", góp phần đại thắng quân Thanh mùa xuân nǎm Kỷ Dậu 1789. Ngô Thì Nhậm còn là cây bút tiêu biểu của Ngô Gia Vǎn Phái và là một nhà ngoại giao tài giỏi thời Tây Sơn. Ông đã được vua Quang Trung giao nhiệm vụ đối phó với nhà Thanh sau chiến thắng Ngọc Hồi - Đống Đa.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','04', 'Đồng chí Lê Duẩn sinh ngày 7-4-1907 ở xã Triệu Thành, huyện Triệu Phong, tỉnh Quảng Trị, qua đời ngày 10-7-1986 tại Hà Nội.\nĐồng chí vào Đảng Cộng sản Đông Dương từ nǎm 1930. Trong gần 60 nǎm hoạt động cách mạng, đồng chí giữ nhiều chức vụ quan trọng: Bí thư Xứ ủy nam Kỳ, Bí thư Trung ương Cục miền Nam, Ủy viên Bộ Chính trị, Tổng Bí thư ban chấp hành Trung ương Đảng. Là nhà lãnh đạo lỗi lạc, nhà lý luận sáng tạo, đồng chí Lê Duẩn có nhiều cống hiến to lớn cho cách mạng Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','04', 'Trường Quốc học Huế được thành lập từ một Đạo dụ của Vua Thành Thái, vốn có tên là Quốc gia học đường, là trường học theo lối mới sớm nhất và cũng là trường lớn nhất Trung Kỳ. Trường có bề dày đào tạo nhiều trí thức và chính khách nổi tiếng.\nNǎm 1907-1908, Nguyễn Tất Thành đã theo học ở đây. Ngày 7-4-1927, do thái độ miệt thị của một giáo sư thực dân đối với học sinh, học sinh trường quốc học Huế tổ chức bãi khoá. Sự kiện này đã gây tiếng vang của Trung Kỳ và cả nước. Từ cuộc bãi khoá này, nhiều học sinh ở trường đã bước vào con đường hoạt động cách mạng như: Võ Nguyên Giáp, Hải Triều...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','04', 'Ngày 7-4-1949, Hồ Chủ tịch ký sắc kệnh thành lập bộ đội địa phương. Với Sắc lệnh này, các đội du kích tập trung được phát triển thành các trung đội, đại đội bộ đội địa phương huyện và các tiểu đoàn bộ đội địa phương tỉnh.\nBộ đội địa phương là những đơn vị Vệ quốc đoàn do Đảng bộ địa phương lãnh đạo, có nhiệm vụ bảo vệ nhân dân và chính quyền địa phương, phát triển chiến tranh du kích, chống địch càn quét, dìu dắt dân quân du kích xã, chuẩn bị chiến trường cho bộ đội chủ lực và phối hợp tác chiến với bộ đội chủ lực, bổ xung cho bộ đội chủ lực.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','04', 'Trước áp lực của quần chúng và do chịu thêm nhiều thất bại quân sự, ngày 7-4-1971, Tổng thống Hoa Kỳ R.Nixơn tuyên bố sẽ rút một đợt 10.000 binh lính Mỹ khỏi Việt Nam. Ngay sau đó, cuộc biểu tình quần chúng Mỹ đã nổ ra ở thủ đô Oasinhtơn với quy mô chưa từng có đòi chấm dứt ngay chiến tranh lập lại hoà bình ở Việt Nam và trên bán đảo Đông Dương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','04', 'Từ 5 đến 7-4-1972, quân và dân Bình Long đã tiến công dồn dập nhiều cǎn cứ, vị trí quan trọng của Mỹ - Ngụy trên tuyến phòng thủ phía Bắc Sài Gòn, dọc đường số 13 dài gần 100km.\nTa đã giành thắng lợi to lớn:\n- Giết và làm bị thương 2500 tên địch, bắt gần 1000 tên, phá huỷ và thu hơn 400 xe quân sự, 24 đại bác hạng nặng. Toàn bộ lực lương quân sự của địch ở Lộc Ninh, chiến đoàn số 52 và cǎn cứ Quản Lợi bị tiêu diệt.\n- Mười \"ấp chiến lược\" xung quanh thị xã Bình Long trên một khu vực rộng 1600km2 với 30.000 đồng bào được giải phóng.\n- Huyện Lộc Ninh gồm 28.000 dân được hoàn toàn giải phóng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','04', 'Nguyên tắc của các đơn vị bắt buộc về trọng lượng và đo lường đã được ấn định ở nước Pháp do Hội nghị Quốc ước theo sắc lệnh nǎm thứ 3 nước cộng hoà ngày 7-4-1795.\nLần đầu tiên trên thế giới ra đời định nghĩa có tính pháp lý cho mét và kilôgam.\nĐến ngày 22-6-1799, những mâu thuẫn đầu tiên của mét và kilôgam đã được đặt vào Cục lưu trữ quốc gia ở Pari. Sau này, mét và kilôgam được sử dụng trên toàn thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','04', 'Tổ chức y tế thế giới là một trong những cơ quan chuyên môn được thành lập trên tinh thần Hiến chương Liên hiệp Quốc. Mục đích của tổ chức là \"mang lại cho mọi dân tộc một trình độ y tế ở mức cao\". Việc chống lại bệnh tật được hướng trước hết vào các bệnh truyền nhiễm. Vai trò của tổ chức Y tế thế giới đáng kể trong việc kiểm tra chất lượng thuốc men, phát triển cơ sở y tế. Chức nǎng của tổ chức này đặc biệt mở rộng trong việc phát triển nhân viên y tế và đào tạo cán bộ.\nSự hợp tác của Tổ chức Y tế thế giới với chính phủ Việt Nam trong lĩnh vực y tế ngày càng chặt chẽ và mở rộng. Đại hội đồng Y tế thế giới đã thông qua nhiều nghị quyết và đã xây dựng một chương trình đặc biệt giúp đỡ Việt Nam. Việc hoàn thành toàn diện chương trình giúp đỡ đặc biệt cho Việt Nam trong lĩnh vực y tế sẽ là một sự xác nhận của tinh thần hợp tác và hữu nghị quốc tế.\nNgày 7-4 hàng năm - ngày thành lập tổ chức này năm 1948 - được coi là ngày sức khoẻ thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','05', 'Nguyễn Quang Bích sinh ngày 7-5-1832, quê ở tỉnh Thái Bình, qua đời ngày 15-12-1889.\nÔng là quan triều Nguyễn, đã từng lập cǎn cứ ở Nghĩa Lộ, tập hợp nghĩa quân để chống lại quân Pháp trong các nǎm từ 1884 đến 1889.\nChính trong những nǎm đó, ông đã sáng tác tập thơ Ngư Phong - Đây là tâm tình của nhà thơ chiến sĩ, có tình yêu thắm thiết đất nước và lòng cǎm thù giặc nóng bỏng.\nỞ Hà Nội có một phố mang tên Nguyễn Quang Bích.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','05', 'Bác sĩ y khoa Phạm Ngọc Thạch, nhà hoạt động cách mạng, Anh hùng lao động, sinh ngày 7-5-1909 tại Quảng Nam.\nTrước Cách mạng tháng Tám ông có đóng góp nhiều công sức trong việc xây dựng cơ sở Cách mạng ở Sài Gòn, kể cả việc thành lập Đoàn thanh niên Tiên phong, ông cũng chung lòng với bác sĩ Nguyễn Vǎn Thụ, Mai Vǎn Bộ tổ chức được nhiểu cơ sở Thanh niên Tiên phong ở Nam Bộ.\nTrong khí thế sôi động của cuộc Cách mạng tháng Tám, ông giữ chức Uỷ trưởng ngoại giao, tên tuổi ông gắn bó với \"Lâm ủy hành chính Nam Bộ\" và \"Thủ lĩnh Thanh niên Tiên phong\". Khi cuộc kháng chiến toàn quốc bùng nổ, ông là Chủ tịch Ủy ban kháng chiến hành chính khu Sài Gòn - Gia Định. Sau đó, ông vào chiến khu rồi ra Bắc về Nam mấy lượt, tích cực hoạt động trên cương vị y tế - người đứng đầu ngành y tế Cách mạng, cống hiến cho y học và nền y tế cách mạng thật xuất sắc, đặc biệt là trong việc chống sốt rét. Đồng thời ông còn là tác giả nhiều sách về y học viết bằng tiếng Việt và tiếng Pháp.\nNgày 7-11-1968 ông mất tại một cǎn nhà tranh, giữa chiến khu miền Đông Nam Bộ. Ông được Đảng và nhà nước Việt Nam tặng nhiều huân chương cao quí.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','05', 'Ngày 7-5-1944 - Tổng bộ Việt Minh ra chỉ thị \"Sửa soạn khởi nghĩa\".\nChỉ thị nhận định \"thời cơ hết sức thuận lợi cho nhân dân Đông Dương giành chính quyền sắp tới\", \"song thời cơ không phải tự nó đến, một phần lớn là do ta sửa soạn nó, thúc đẩy nó\". Nội dung của vǎn kiện này còn đề cập tới những vấn đề rất cụ thể như \"Ai xông ra đánh quân thù? Lấy gì đánh quân thù? Đánh bằng cách nào? Đánh vào lúc nào để thắng? Làm thế nào đẩy phong trào tiến tới khởi nghĩa?...\nTinh thần của bản chỉ thị được các đảng viên và nhân dân quán triệt và biến thành những hành động cụ thể tạo nhiều điều kiện tiến tới Tổng khởi nghĩa.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','05', 'Ngày 7-5-1948, tại chiến khu Việt Bắc, đã thành lập Hội Vǎn nghệ Việt Nam nhằm mục tiêu trước mắt là đoàn kết tất cả những người làm công tác vǎn học, nghệ thuật phục vụ cuộc kháng chiến chống thực dân Pháp và xây dựng đất nước.\nĐến nay ở tất cả 53 tỉnh, thành phố đều có hội vǎn nghệ và chung cả nước có Uỷ ban trung ương Liên hiệp Vǎn học nghệ thuật Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','05', 'Ngày 7-5-1954 - Chiến thắng Điện Biên Phủ!\nChiến dịch Điện Biên Phủ bắt đầu từ ngày 13-3-1954. Sau 55 ngày đêm chiến đấu liên tục, quân ta đã tiêu diệt và bắt gọn 16 nghìn tên địch (gồm 17 tiểu đoàn bộ binh, 3 tiểu đoàn pháo binh, 1 tiểu đoàn công binh), bắn rơi và phá huỷ 62 máy bay.\nChiến thắng Điện Biên Phủ đã góp phần quyết định kết thúc cuộc kháng chiến chống thực dân Pháp trong 9 nǎm. Đây là một thắng lợi vĩ đại trong lịch sử chống ngoại xâm của dân tộc ta, có sức cổ vũ rất lớn phong trào chống chủ nghĩa thực dân của các dân tộc trên thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','05', 'Ngày 7-5-1955 đã thành lập Hải quân nhân dân Việt Nam. Trong cuộc kháng chiến chống Mỹ cứu nước, Hải quân nhân dân đã anh dũng chiến đấu, mưu trí sáng tạo, bắn rơi nhiều máy bay, bắn cháy, đánh chìm nhiều tàu địch, góp phần đánh bại cuộc chiến tranh phá hoại bằng không quân và hải quân của đế quốc Mỹ; làm thất bại cuộc phong toả chiến lược bằng thuỷ lôi của Mỹ đối với miền Bắc; mở đường Hồ Chí Minh trên biển chi viện cho Cách mạng miền Nam; sáng tạo ra cách đánh đặc công hải quân độc đáo; có hiệu suất chiến đấu cao.\nHải quân nhân dân Việt Nam đã tham gia vào cuộc Tổng tiến công và nổi dậy mùa xuân 1975, trong đó nổi bật đã phối hợp lực lượng của quân khu 5, mưu trí, táo bạo, bất ngờ, giải phóng quần đảo Trường Sa, giành thắng lợi có ý nghĩa chiến lược, góp phần vào thắng lợi trọn vẹn, vĩ đại của dân tộc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','05', 'Bác sĩ y khoa, Anh hùng lao động Tôn Thất Tùng quê ở thành phố Huế, sinh nǎm 1912 và mất vào này 7-5-1982. Thuở nhỏ Tôn Thất Tùng học ở Huế và Hà Nội, tốt nghiệp y khoa, làm việc ở Bộ Quốc phòng. Nǎm 1947 làm Thứ trưởng Bộ Y tế. Từ nǎm 1954, ông làm Giám đốc Bệnh viện Hữu nghị Việt Đức, giáo sư tại trường Đại học Y khoa Hà Nội. Ông là đại biểu Quốc hội liên tục nhiều khoá và giữ các chức vụ: Uỷ viên đoàn chủ tịch Mặt trận Tổ quốc Việt Nam, Viện Sỹ Hàn lâm Y học Liên Xô, Hội viên hội Quốc gia những nhà phẫu thuật nước Cộng hoà dân chủ Đức và nhiều nước khác. Ông là giáo sư y khoa nổi tiếng về gan và giải phẫu gan. Ông từng đào tạo nhiều thế hệ bác sĩ, giáo sư y khoa Việt Nam hiện đại.\nDo cống hiến của ông trong lĩnh vực y học, ông được Chính phủ tặng nhiều huân chương cao quý, được truy tặng huân chương Hồ Chí Minh. Tên của ông được đặt cho một phố ở Hà Nội và cả ở Thành phố Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','05', 'Piốt Ilitsơ Traicôpxki là nhạc sĩ và là nhà soạn nhạc nổi tiếng nước Nga. Ông sinh ngày 7-5-1840 trong một gia đình trí thức. Ông vào học trường luật và trở thành viên chức ở Bộ Tư pháp. Đến nǎm 21 tuổi ông mới vào học tại Nhạc viện Pêtecxbua và tốt nghiệp xuất sắc.\nTraicôpxki sáng tác hầu hết các thể loại âm nhạc. Ông là một trong những người đặt nền móng cho nhạc giao hưởng cổ điển Nga. Các vở Ôpêra của ông lấy đề tài trong các tác phẩm vǎn học Nga như vở kịch Epghênhi Ônhêghin lấy đề tài trong bản trường ca của Puskin. Trong lịch sử âm nhạc thế giới, Traicôpxki được ghi nhận là người cách tân xuất sắc thể loại vũ kịch với các vở balê Hồ Thiên Nga, Người đẹp ngủ trong rừng... Ông còn sáng tác nhiều thể loại âm nhạc cổ điển khác cho dàn nhạc thính phòng cho hoà tấu, những bản dành riêng cho pianô, viôlông... \nGiữa lúc thiên tài âm nhạc của Traicôpxki đang nở rộ thì ông mắc bệnh tả và mất ở Pêtecxbua ngày 6-11-1893 thọ 53 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','05', 'Tago, nhà thơ lớn, nhà vǎn hoá lớn Ấn Độ sinh ngày 7-5-1861 tại Cancútta trong một gia đình truyền thống vǎn hoá, nghệ thuật và theo xu hướng cải cách xã hội. Ông được đào tạo chu đáo và được coi là thần đồng, đặc biệt về tài vǎn học.\nTago tích cực hoạt động xã hội và sáng tác vǎn học. Ông mang tư tưởng chống đối chế độ thực dân Anh và ách áp bức giai cấp. Nǎm 51 tuổi ông được nhận giải thưởng Nôbel về vǎn học với tập Thơ dâng kiệt xuất, Ông là nhà vǎn có ảnh hưởng sâu sắc đối với vǎn học Ấn Độ. Ông để lại trên một nghìn bài thơ, 42 vở kịch, 14 tiểu thuyết, 12 tập truyện ngắn, trên 2.000 bài hát, nhiều bức tranh, tiểu luận về triết học, chính trị, giáo dục, đạo đức, nghệ thuật... Đóng góp xuất sắc nhất của Tago là lĩnh vực thơ ca. Ông đem lại cho thi ca Ấn Độ một không khí thiêng liêng, thần tình. Thơ của ông phản ánh những truyền thống vǎn hoá quí báu của dân tộc, trong sự hội nhập với nền nghệ thuật hiện đại của phương Tây. Những tập thơ tiêu biểu của ông là Thơ dâng, Balaca, Người làm vườn, Mùa hái quả, Ngày sinh, Thơ ngắn...\nÔng mất ngày 7-8-1941, thọ 80 tuổi')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','06', 'Thiền sư Viên Thông viên tịch ngày 7-6-1151. Ông sinh nǎm 1080 ở làng Cổ Hiền, nay thuộc tỉnh Bắc Ninh.\nLà một người tinh thông đạo Phật, Viên Thông còn là một nhà vǎn. Ông được vua nhà Lý trọng vọng, thường mời đến bàn bạc công việc của triều chính.\nÔng được phong làm quốc sư')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','06', 'Ngày 7-6-1957, Chính phủ ta đã thành lập Viện nghiên cứu đông y (ở Hà Nội) thuộc Bộ Y tế nhằm nghiên cứu những môn thuốc và phương pháp chữa bệnh cổ truyền của dân tộc ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','06', 'Ngày 7-6-1970: Việt Nam và Somalia lập quan hệ ngoại giao cấp đại sứ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','06', 'Từ ngày 7 đến 10-6-1971, Quốc hội khoá IV họp kỳ thứ nhất đã nghe báo cáo về tình hình và kết quả cuộc bầu cử Quốc hội khoá IV, về công tác đấu tranh ngoại giao, về tình hình đấu tranh quân sự. Quốc hội tiến hành bầu các vị lãnh đạo Nhà nước và các vị phụ trách các cơ quan cao cấp khác. Toàn thể Quốc hội nhất trí bầu:\nCụ Tôn Đức Thắng giữ chức Chủ tịch nước Việt Nam dân chủ cộng hoà.\nĐồng chí Nguyễn Lương Bằng giữ chức Phó Chủ tịch nước Việt Nam dân chủ cộng hoà. \nĐồng chí Trường Chinh giữ chức Chủ tịch Uỷ ban thường vụ Quốc hội.\nĐồng chí Phạm Vǎn Đồng giữ chức Thủ tướng Chính phủ.\nCụ Tôn Đức Thắng giữ chức Chủ tịch Hội đồng Quốc phòng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','06', 'Ngày 7-6-1978: Việt Nam và Thổ Nhĩ Kỳ lập quan hệ ngoại giao cấp đại sứ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','06', 'Ngày 7-6-1986, tại Hà Nội đã khai mạc triển lãm thành tựu khoa học kỹ thuật toàn quốc lần thứ nhất.\nCó 1427 công trình và sản phẩm được trưng bày, trong đó có 972 sản phẩm thuộc chương trình trọng điểm Nhà nước, 375 sản phẩm thuộc các bộ, ngành và 90 tiến bộ kỹ thuật được áp dụng vào sản xuất ở các địa phương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','06', 'Ngày 7-6-1994: Việt Nam và Slovenia lập quan hệ ngoại giao cấp đại sứ')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','07', 'Danh nhân Phan Kính sinh nǎm 1715, mất ngày 8-6 nǎm Tân Tỵ (tức 7-7-1716) trong một gia đình nhà nho ở xã Song Lộc, Huyện Can Lộc, tỉnh Hà Tĩnh. Ông là người tài cao học rộng, đã từng đỗ Đình Nguyên Thám Hoa ở khoa thi hội nǎm 1743 và làm quan cho triều Lê từ 1743 cho đến khi qua đời. Phan Kính là người vǎn, võ song toàn, một trí thức tài ba, một ông quan thanh liêm, mẫn cán, luôn hoàn thành trách nhiệm một cách xuất sắc. Sự nghiệp cuả Phan Kính đã để lại nhiều kinh nghiệm quý báu về các lĩnh vực: Chính trị, ngoại giao, quân sự... Ông là tác giả của những tác phẩm: \"Kinh chuyện tử sử\", \"Sách vǎn lược cũ\"...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','07', 'Thạch Lam tên thật là Nguyễn Tường Vinh (sau đổi tên là Nguyễn Tường Lân) sinh ngày 7-7-1910 tại Hà Nội, qua đời nǎm 1942.\nHọc hết trung học phổ thông, ông chuyên viết vǎn, làm báo. Tuy có chân trong nhóm Tự Lực Vǎn Đoàn và là em ruột của Nhất Linh và Hoàng Đạo nhưng tư tưởng thẩm mỹ của Thạch Lam lại theo một hướng khác. Nhân vật trong những tác phẩm của ông là những người nghèo, đời sống cơ cực bế tắc, tương lai mù mịt - Ông viết về họ với một niềm xót thương sâu sắc và nỗi buồn mênh mang - Thạch Lam sở trường về truyện ngắn, chú trọng nội tâm nhân vật.\nTác phẩm chính của Thạch Lam gồm có các truyện ngắn \"Gió đầu mùa\" (nǎm 1937), \"Nắng trong vườn\" (1938), \"Sợi tóc\" (1942, tập tuỳ bút \"Hà Nội 36 phố phường\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','07', 'Ngày 7-7-1946, Bộ Quốc phòng mở lớp bổ túc quân sự trung cấp đầu tiên của quân đội ta, khai giảng tại thị xã Sơn Tây. Lớp có gần 100 học viên.\nTuy thời gian học tập ngắn, nhưng nội dung huấn luyện phong phú, tương đối toàn diện cả về quân sự và chính trị.\nLớp bổ túc quân sự trung cấp đầu tiên này được coi là tiền thân của Học viện Lục Quân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','07', 'Ngày 7-7-1950, Bộ tư lệnh ra mệnh lệnh về chiến dịch Biên giới Cao Lạng (còn gọi là chiến dịch Lê Hồng Phong 2). Mục đích là tiêu diệt sinh lực địch, giải phóng một phần biên giới, mở đường giao thông nối liền với các nước xã hội chủ nghĩa, mở rộng và củng cố cǎn cứ địa Việt Bắc. Đại tướng Võ Nguyên Giáp, Thường vụ Trung ương Đảng làm Bí thư Đảng uỷ, Chỉ huy trưởng Bộ chỉ huy chiến dịch. Thiếu tướng Hoàng Vǎn Thái, Tổng tham mưu trưởng làm tham mưu trưởng; đồng chí Trần Đǎng Ninh, Chủ nhiệm Tổng cục cung cấp phụ trách hậu cần chiến dịch; đồng chí Lê Liêm làm Chủ nhiệm chính trị chiến dịch.\nChiến dịch được bắt đầu 16-9-1950, kết thúc 14-10-1050.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','07', 'Kỳ họp thứ nhất của Quốc hội khoá II đã diễn ra từ ngày 7 đến ngày 15 tháng 7 nǎm 1960.\nTrong kỳ họp đầu tiên, Quốc hội đã bầu ra Chủ tịch nước là cụ Hồ Chí Minh; Phó chủ tịch nước là cụ Tôn Đức Thắng; Chủ tịch Ủy ban thường vụ Quốc hội là ông Trường Chinh; Thủ tướng Chính phủ là ông Phạm Vǎn Đồng.\nQuốc hội còn bầu Hội đồng quốc phòng, cử Viện trưởng Viện kiểm sát nhân dân tối cao và Chánh án toàn án nhân dân tối cao.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','08', 'Báo \"Thanh niên\" xuất bản tại Sài Gòn ra số đầu tiên ngày 7-8-1943. Tờ báo do kiến trúc sư Huỳnh Tấn Phát chủ trương, với sự cộng tác của nhiều tri thức, nghệ sĩ yêu nước như: Dương Tử Giang, Lưu Hữu Phước, Mạnh Phú Tư, Nguyên Hồng, Chế Lan Viên, Xuân Diệu...\nTờ báo trở thành hạt nhân của phong trào thanh niên yêu nước Nam Bộ thời kỳ tiền khởi nghĩa. Tờ báo tồn tại đến số cuối ra ngày 30-9-1944.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','08', 'Ngày 7-8-1956, nước ta thành lập ngành dự trữ quốc gia. Hiện nay ngành dự trữ quốc gia có hệ thống kho tàng riêng biệt, chủng loại hàng phù hợp với yêu cầu, nhiệm vụ mới, phân phối đều trên các vị trí xung yếu.\nNgành có một trung tâm nghiên cứu khoa học công nghệ bảo quản, một trường chuyên đào tạo cán bộ kỹ thuật chuyên ngành bảo quản. Nǎm 1996 ngành được Nhà nước tặng Huân chương Độc lập hạng nhất.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','08', 'Ngày 7-8-1964 Bộ Tổng tư lệnh tổ chức lễ tuyên dương công trạng các đơn vị quân đội lập công trong hai ngày 2 và 5-8-1964.\nHồ Chủ tịch tới dự và huấn thị: \"Các chú đã thu được thắng lợi vẻ vang nhưng chớ vì thắng lợi mà tự mãn, chủ quan, khinh địch. Chúng ta phải biết rằng đế quốc Mỹ và tay sai \"Chết thì chết, nết không chừa\", chúng còn nhiều âm mưu hung ác\". Tại lễ tuyên dương, Chủ tịch Trường Chinh trao tặng Huân chương Quân công cho 10 đơn vị thuộc bộ đội phòng không và hải quân đã có thành tích bắn rơi máy bay Mỹ, bắt sống giặc lái, đánh đuổi tàu chiến Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','08', 'Đại hội thi đua \"Quyết tâm đánh thắng giặc Mỹ xâm lược\" lần thứ nhất của lực lượng vũ trang nhân dân đã diễn ra ngày 7-8-1965 tại Hà Nội.\nĐây là Đại hội đầu tiên của các đơn vị Anh hùng chống Mỹ cứu nước. Dự đại hội có đông đủ đại biểu các quân chủng, bộ đội chủ lực, bộ đội địa phương, công an nhân dân vũ trang, dân quân tự vệ. Hồ Chủ tịch đã đến huấn thị cho Đại hội.\nĐồng chí Trường Chinh và đồng chí Lê Thanh Nghị đã trao cờ \"Đơn vị Quyết thắng\" cho 367 đơn vị quân đội nhân dân, công an nhân dân vũ trang, dân quân tự vệ có thành tích xuất sắc trong chiến đấu, xây dựng và phục vụ chiến đấu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','08', 'Ngày 7-8-1989, Chủ tịch Hội đồng Nhà nước Võ Chí Công đã ký lệnh công bố Pháp lệnh về tài nguyên khoáng sản. Pháp lệnh khẳng định: Tài nguyên khoáng sản là tài sản quốc gia vô cùng quý giá, phải được bảo vệ, sử dụng hợp lý nhằm bảo quản nhu cầu nguyên liệu khoáng sản trước mắt và lâu dài của sự nghiệp phát triển kinh tế xã hội.\nBản pháp lệnh gồm 8 chương, với 36 điều. Các chương chính của pháp lệnh là: Điều tra địa chất; Khai thác mỏ; Bảo vệ tài nguyên khoáng sản chưa khai thác; Thanh tra Nhà nước về tài nguyên khoáng sản; giải quyết tranh chấp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','08', 'Rabindranat Tago (Rabindranth Tagore) là nhà thơ lớn, nhà vǎn hoá lỗi lạc của Ấn Độ. Ông sinh nǎm 1861 tại Canquitta, và mất tại đây ngày 7-8-1941.\nThuở bé, Tago thông minh, hiếu học. Ở tuổi thanh niên, Tago đã dịch thông thạo nhiều tác phẩm của các nhà vǎn châu Âu, thích nghe kể sử thi và dân ca.\nSự nghiệp sáng tác vǎn học nghệ thuật của ông rất đồ sộ và nổi tiếng. Tago là người đầu tiên ở châu Á được tặng giải thưởng Nôbel về vǎn chương nǎm 1913 với tập \"Thơ dâng\". Ông còn để lại 52 tập thơ, 42 vở kịch, gần 100 truyện ngắn, hàng trǎm ca khúc, hàng nghìn bức hoạ đang được giữ gìn trong các bảo tàng mỹ thuật.\nRabindranát Tago xứng đáng là một trong những thiên tài của nhân loại ở thế kỷ XX. Nǎm 1961 thế giới đã kỷ niệm lần thứ 100 ngày sinh của ông.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','09', 'Sắc lệnh số 11 ngày 7-9-1945 của Chủ tịch Chính phủ Việt Nam Dân chủ cộng hoà là văn bản pháp quy đầu tiên về tài chính, thuế khoá dưới chế độ mới. Theo văn bản này thì \"chế độ thuế khoá hiện nay sẽ thay đổi dần nhưng mỗi khi bãi bỏ một thứ thuế cũ hay đặt ra một thứ thuế mới phải có sắc lệnh ấn định\". Cùng ngày, sắc lệnh bãi bỏ thuế thân cũng được Hồ Chủ tịch ký ban hành.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','09', '\"Đây là Tiếng nói Việt Nam, phát thanh từ Hà Nội, thủ đô nước Việt Nam Dân chủ cộng hoà\".\nCâu nói gần gũi thân thương ấy vang lên, đi vào lòng mọi người dân nước ta từ ngày 7-9-1945 - Đó là ngày ra đời của Đài Tiếng nói Việt Nam.\nĐêm 23-12-1972, máy bay B52 ném bom sập đài phát sóng Mễ Trì. Tiếng nói tạm ngừng, làm cả nước hồi hộp, lo âu. Nhưng chỉ 9 phút sau, tiếng nói Việt Nam lại phát ra từ các máy thu thanh.\nHiện nay, hằng ngày tiếng nói Việt Nam phát trên nhiều hệ thống phục vụ người nghe đài ở trong nước, phục vụ người nước ngoài bằng tiếng ngoại ngữ và phục vụ Việt kiều ở xa Tổ quốc. Các chương trình ngày càng phong phú, sinh động, hấp dẫn người nghe.\nĐài Tiếng nói Việt Nam đã được Nhà nước tặng thưởng Huân chương Chiến công hạng nhất, Huân chương Lao động hạng Nhất, Huân chương Hồ Chí Minh và Huân chương Sao vàng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','09', '\"Hiệp ước bắc Kinh\" giữa triều đình Mãn Thanh với 8 nước đế quốc đàn áp Nghĩa Hoà Đoàn được ký kết ngày 7-9-1901.\nNhà Thanh nhận bồi thường chiến phí 450 triệu lạng bạc, trả trong 39 năm, lãi xuất 4% mỗi năm, tất cả thành một tỷ lạng bạc; triệt hạ các pháo đài ở cửa biển Đại Cô và trên đường về Bắc Kinh; để quân đội ngoại quốc đóng dọc đường xe lửa Bắc Kinh - Thiên Tân và các nước ngoài được lập sứ quán ở Bắc Kinh.\nQua hiệp ước này, Trung Quốc thực sự trở thành một nước nửa thuộc địa.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','10', 'Ngày 7-10-1877, người Pháp đã khởi công xây dựng Nhà thờ Đức Bà tại Sài Gòn và khánh thành vào dịp lễ phục sinh năm 1880.\nNgười thiết kế công trình này là kiến trúc sư Pavrát (Pavrard), theo mẫu của nhà thờ Đức Bà ở Thủ đô Pari (nước Pháp)\nNhà thờ Đức Bà - Sài Gòn có diện tích hơn 3.400 mét vuông, cao hơn 60m, và có 6 quả chuông nặng hơn 26 tấn (có quả nặng tới 9 tấn, đường kính 2,3m, chiều cao 3,3m) khi 6 quả chuông cùng rung lên một lúc, nếu thời tiết tốt (trời trong, lặng gió) thì những người ở xa nhà thờ đến 10km vẫn nghe được tiếng chuông ngân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','10', 'Từ 7-10 đến ngày 22-12-1947, giặc Pháp huy động hơn 2 vạn quân tinh nhuệ, có máy bay, có tàu chiến hỗ trợ, mở cuộc tiến công lớn lên Việt Bắc nhằm tiêu diệt cơ quan đầu não kháng chiến và bộ đội chủ lực của ta, kết thúc \"chớp nhoáng\" cuộc chiến tranh xâm lược.\nBộ đội chủ lực của ta phối hợp với các đơn vị dân quân, du kích các tỉnh, đã liên kết đánh chặn và phản công tiêu diệt địch ở khắp nơi. Nổi bật là các trận ở Bình Ca (Tuyên Quang), Bông Lau (Cao Bằng), Khe Lau trên sông Lô, tập kích địch ở đồn Phủ Thông, phục kích ở đèo Giàng trên đường số 3.\nTrong cả chiến dịch Việt Bắc, ta loại khỏi vòng chiến đấu hơn 6.000 tên địch, bắt sống 270 tên, bắn rơi 16 máy bay, bắn chìm 26 tàu chiến và ca nô, phá 100 khẩu pháo và hàng nghìn khẩu súng, hàng trăm xe quân sự, thu hàng tấn chiến lợi phẩm.\nChiến dịch Việt Bắc thu đông 1947 là chiến dịch phản công quy mô lớn đầu tiên của ta tại căn cứ rừng núi. Chiến dịch này đã đánh dấu một bước trưởng thành vượt bậc của quân đội ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','10', 'Học giả Nguyễn Văn Tố, bút hiệu Ứng Hoè, sinh năm 1889, quê ở Hà Đông.\nThuở nhỏ ông học chữ Hán, sau chuyển sang Pháp học, đỗ bằng Thành Chung, làm việc tại trường Viễn Đông Bác Cổ - Hà Nội, chuyên về văn hoá cổ Việt Nam. Từng làm Hội trưởng Hội Trí Tri, Hội trưởng Hội truyền bá Quốc ngữ trước năm 1945. Sau Cách mạng tháng Tám, ông giữ chức vụ Bộ trưởng xã hội trong chính phủ lâm thời, rồi Đại biểu Quốc hội khoá I, quyền Chủ tịch Quốc hội khoá I, Quốc vụ khanh Chính phủ \"Liên hiệp quốc dân\". Toàn quốc kháng chiến, ông cùng Chính phủ rút lên Việt Bắc tiếp tục kháng chiến chống Pháp.\nCác công trình trước tác chữ quốc ngữ và chữ Pháp có giá trị của ông phần lớn in trong các tạp chí. Nhất là các tạp chí Trí Tri, Viễn Đông Bác Cổ, Tri Tân.\nTrong một cuộc bị Pháp tấn công ở chiến khu Việt Bắc, ông bị giặc bắt giết tại Bắc Cạn ngày 7-10-1947.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','10', 'Etga Alanh Pô (Edgar Allan Poe) là nhà văn Mỹ, sinh ngày 19-1-1809 mất ngày 7-10-1849, ông là một nhà văn tài năng trên nhiều lĩnh vực: Thơ, truyện, phê bình lý luận. Trong mỗi thể loại ông đều có những gặt hái đáng kể, được đánh giá cao. Truyện của Pô bộc lộ mối bất đồng sâu sắc đối với xã hội tư sản Mỹ, nên ông tìm đến chủ nghĩa lãng mạn như một chỗ dựa tinh thần, niềm vui trong thế giới tưởng tượng bay bổng, ly kỳ của sáng tạo cá nhân. Các truyện phiêu lưu của ông đặc biệt được ưa thích, nhất là công chúng châu Âu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','10', 'Nien Bo sinh ngày 7-10-1885 và mất năm 1962\nÔng là nhà vật lý xuất sắc Đan Mạch. Từ năm 1916, ông là giáo sư trường đại học tổng hợp Copenhagơ.\nÔng là người đứng đầu trường phái khoa học lớn trong lĩnh vực vật lý lý thuyết, tác giả của thuyết lượng tử (trong những năm 1913-1916). Thuyết này là điểm xuất phát cho thuyết cơ học lượng tử hiện đại về cấu tạo nguyên tử. Năm 1913, ông cũng là tác giả của công trình giải thích lý thuyết định luật tuần hoàn Menđêlêép và thuyết hạt nhân nguyên tử.\nNăm 1922, Nien Bo được giải thưởng Nôben. Từ năm 1919, ông là Viện sĩ nước ngoài của Viện Hàn lâm khoa học Liên Xô trước đây.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','10', 'Từ ngày 7 đến 24-10-1973 đã nổ ra cuộc chiến tranh Trung Đông lần thứ tư: Itxraen chiếm cao nguyên Gôlan của Xiri.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','11', 'Bác sĩ Phạm Ngọc Thạch sinh nǎm 1909 tại Quy Nhơn (tỉnh Bình Định) và hy sinh ngày 7-11-1968 tại chiến trường Tây Ninh.\nTrước Cách mạng Tháng Tám, ông vừa làm thầy thuốc vừa tham gia hoạt động cách mạng, là một trong những sáng lập Thanh niên Tiền phong Nam Bộ.\nSau Cách mạng, ông giữ nhiều chức vụ quan trọng như: Bộ trưởng Y tế trong Chính phủ lâm thời, Chủ tịch Uỷ ban kháng chiến hành chính đặc khu Sài Gòn - Chợ Lớn. Nǎm 1958 ông lại làm Bộ trưởng Y tế kiêm Viện trưởng Viện chống lao Trung ương, rồi Chủ tịch Uỷ ban điều tra tội ác của đế quốc Mỹ ở Việt Nam. Ông còn là người có nhiều hoạt động trong lĩnh vực y học quốc tế.\nBác sĩ Phạm Ngọc Thạch được Nhà nước tặng thưởng danh hiệu Anh hùng lao động, truy tặng giải thưởng Hồ Chí Minh về các công trình khoa học phòng và chống bệnh lao ở Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','11', 'Ngày 7-11-1975, Hội nghị Đại biểu phụ nữ toàn miền Nam họp tại thành phố Sài Gòn - Gia Định. Hội nghị kiểm điểm phong trào phụ nữ trong 20 nǎm chống Mỹ cứu nước và bàn phương hướng, nhiệm vụ công tác trong giai đoạn xây dựng mới, khi nước nhà đã thống nhất.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','11', 'Nữ bác học Mari Quyri sinh ở Vácxava (Ba Lan) ngày 7-11-1867 và từ trần nǎm 1934.\nSau khi tốt nghiệp đại học Tổng hợp Pari, Mari cùng với chồng là Pie Quyri nghiên cứu hiện tượng phóng xạ. Do phát minh xuất sắc trong lĩnh vực này, bà đã được trao tặng học vị Tiến sĩ khoa học vật lý, cùng với một người bạn là Béccơren, hai ông bà đã tiến hành các nghiên cứu về tính phóng xạ và đã được nhận giải Nôben nǎm 1903 do đã khám phá ra chất Radi và Pôlôni. Pie bị xe ngựa chở hàng đè chết nǎm 1906. Mari thay ông học ở Đại học Xoócbon và lại được nhận một giải Nôben về hoá học nǎm 1911. Từ nǎm 1926, Mari Quyri là Viện sĩ danh dự của Viện Hàn lâm Liên Xô (cũ).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','11', 'Anbe Camuy (Albert Camus) - nhà vǎn hiện đại Pháp, sinh ngày 7-11-1913 tại Angiêri. Trong chiến tranh thế giới lần thứ hai, ông tham gia nhóm \"Chiến đấu\" trong phong trào chống phát xit và là Tổng biên tập báo \"Chiến đấu\". Ngày 17-10-1957, ông được trao giải Nôben do tác phẩm của ông đã \"mang ra ánh sáng những vấn đề hiện nay đang đặt ra trước lương tâm con người \".\nÔng mất ngày 4-1-1960.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','11', 'Ngày 7-11-1917, dưới sự Lãnh đảo của Đảng Bônsêvich Nga và Lênin, cuộc cách mạng xã hội chủ nghĩa đã nổ ra ở nước Nga (theo lịch cũ của nước Nga là ngày 25 tháng 10 nên gọi là Cách mạng Tháng Mười)\nCuộc Cách mạng này đã đập tan ách áp bức, bóc lột của chủ nghĩa tư bản và chế độ phong kiến tồn tại lâu đời ở nước Nga, đưa công nhân, nông dân lên nắm chính quyền, xây dựng chế độ mới xã hội chủ nghĩa.\nNhờ có Cách mạng Tháng Mười, phong trào giải phóng dân tộc ở các nước phương Đông và phong trào công nhân ở các nước tư bản, đế quốc phương Tây đã có sự gắn bó mật thiết với nhau trong cuộc đấu tranh chống kẻ thù chung là chủ nghĩa đế quốc.\nCách mạng Tháng Mười đã đi vào lịch sử nhân loại như một sự kiện vĩ đại mở đầu một thời đại mới - thời đại quá độ lên chủ nghĩa xã hội trên phạm vi toàn thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','12', 'Tướng quân Phạm Ngũ Lão sinh nǎm 1255, qua đời ngày 7-12-1320. Ông người làng Phù Ủng, huyện Ân Thi, tỉnh Hưng Yên, là con rể Trần Hưng Đạo.\nÔng là một tướng có công lớn trong cuộc kháng chiến chống Nguyên Mông thế kỷ XIII. Dưới triều vua Trần Anh Tông, ông còn lập nhiều chiến công ở biên giới phía Nam, được phong chức Điện soái thượng tướng quân. Tuy là võ tướng, ông cũng thích đọc sách, ngâm thơ. Tác phẩm của ông chỉ còn lại hai bài thơ: Thuật hoài (tỏ lòng) và bài Viếng Hưng Đạo đại Vương\nThuở đầu lập nghiệp, Phạm Ngũ Lão được truyền tụng với câu chuyện ngồi bên đường đan sọt, giáo đâm vào đùi chẳng hề hay biết vì mải lo mưu kế đánh giặc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','12', 'Nguyễn Công Trứ sinh nǎm 1778, quê ở huyện Nghi Xuân, tỉnh Hà Tĩnh, mất ngày 7-12-1895\nÔng học hành cần cù nhưng mãi đến nǎm 42 tuổi mới đỗ giải nguyên làm quan dưới triều nhà Nguyễn. Làm quan đến Thị lang Bộ hình và Đại tướng, có lúc bị cách chức xuống làm lính thú ở biên thuỳ, song ông vẫn trung thành với nhà Nguyễn. Khi làm Dinh điền sứ, ông đã có công mộ dân khai khẩn đất hoang, lập hai huyện Kim Sơn (Ninh Bình) và Tiền Hải (Thái Bình). Ngày nay các nơi ấy vẫn còn đền thờ ông.\nNguyễn Công Trứ còn để lại 50 bài thơ, hơn 60 bài ca trù và một bài phú nổi tiếng \"Hàn nho phong vị phú\" đều viết bằng chữ Nôm. \"Bài ca ngất ngưởng\" cũng là tác phẩm xuất sắc của Nguyễn Công Trứ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '07','12', 'Ngày 7-12-1995, Bộ Vǎn hoá - Thông tin đã lập dự án quy hoạch tổng thể làng vǎn hoá các dân tộc Việt Nam tại Đồng Mô - Ngải Sơn huyện Ba Vì, tỉnh Hà Tây.\nDự án nhằm xây dựng trung tâm vǎn hoá lớn tầm cỡ quốc gia dưới dạng một công viên vǎn hoá để tái hiện trưng bày những truyền thống, thành tựu vǎn hoá vật chất và tinh thần tiêu biểu của các dân tộc Việt Nam. Làng vǎn hóa này có diện tích 1540 ha, với tổng mức đầu tư dự án là gần 600 triệu đôla Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','12', 'Ngày 7-12-1941, Nhật Bản tấn công Trân Châu Cảng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','12', 'Ngày 7-12-1944, tại thành phố Chicago (Hoa Kỳ) đã ký công ước về Hàng không dân dụng.\nCông ước này nhằm thắt chặt, giữ gìn tình hữu nghị giữa các dân tộc bảo đảm an ninh chung và tạo cho ngành hàng không phát triển an toàn, trật tự, cung cấp dịch vụ đường tải đường không ngày càng tốt hơn, góp phần vào sự phát triển kinh tế của mỗi quốc gia và toàn cầu. Hàng không dân dụng thế giới (gọi tắt là ICAO) chính thức trở thành tổ chức chuyên môn của Liên hiệp quốc từ nǎm 1947 và đến nay đã có hơn 180 quốc gia thành viên.\nViệt Nam đã tham gia ICAO từ nǎm 1980.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','12', 'Cộng hoà Liên bang Đức và Ba Lan bình thường hoá quan hệ vào ngày 7-12-1970')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','12', '7-12-1975, Quân đội Indonesia chiếm đóng Đông Timor.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','12', 'Ngày 7-12-1981, Tây Ban Nha trở thành thành viên của NATO.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '07','12', 'Ngày này năm 1988, Chủ tịch Tổ chức Giải phóng Palestine (PLO) Yasser Arafat công nhận sự tồn tại của nhà nước Israel.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','01', 'Phan Ngọc Hiển sinh nǎm 1910, quê ở thành phố Cần Thơ. Thường gọi là Giáo Hiển.\nNǎm 1931, ông dạy học ở Cà Mau. Tại đây ông đã giáo dục học sinh tinh thần yêu nước, chống áp bức bóc lột. Sau khi tiếp xúc với các chiến sĩ Cộng sản, tháng 3-1936, ông được kết nạp vào chi bộ Đảng Cộng sản ở thị xã Cà Mau.\nĐêm 13-12-1940, ông Phan Ngọc Hiển chỉ huy cuộc khởi nghĩa ở Hòn Khoai, bắt giết tên chủ đảo Ôliviê. Rạng sáng ngày 14, ông cùng các chiến hữu rút về Rạch Gố. Nhưng đến cuối tháng 12-1940, ông bị địch bắt.\nTháng 1-1941, chúng xử bắn ông Phan Ngọc Hiển tại sân vận động Cà Mau, lúc đó ông mới 31 tuổi.\nNgày nay tên ông được đặt cho một huyện ở tỉnh Cà Mau, đó là huyện Ngọc Hiển.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','01', 'Từ ngày 8 tháng giêng đến ngày 5-2-1966, quân và dân Củ Chi (tỉnh Gia Định cũ) đã giành thắng lợi to lớn trong việc chống địch càn quét.\nTrong chiến thắng này, quân và dân Củ Chi không những phá tan cuộc càn quét lớn của 8 nghìn quân Mỹ, Úc và Tân Tây Lan, tiêu diệt bộ phận sinh lực quan trọng của chúng mà còn làm thất bại âm mưu của Mỹ nhằm đẩy lùi vòng bao vây của ta đang ngày càng thắt chặt xung quanh Sài Gòn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','01', 'Từ ngày 8 tháng giêng đến cuối tháng 3-1950, ta mở chiến dịch Lê Hồng Phong đánh Pháp ở Tây Bắc. Lực lượng của ta có 2 trung đoàn, 3 tiểu đoàn, một số đơn vị binh chủng và bộ đội địa phương.\nMở đầu chiến dịch, quân ta tiêu diệt cứ điểm của Pháp ở phố Lu (Lào Cai) gồm 2 đại đội lính Âu Phi tinh nhuệ, sau 6 ngày vây hãm và tấn công. Đây là trận công kiên lớn đầu tiên của bộ đội ta ở mức trung đoàn.\nTrong toàn chiến dịch, ta diệt 5 vị trí địch, bức rút 3 vị trí dọc tả ngạn sông Thao, diệt 500 tên địch, làm bị thương hơn 200 tên, bắt sống gần 100 tên, quét sạch phòng tuyến sông Thao của Pháp, giải phóng một vùng rộng hàng trǎm kilômét vuông cùng hàng vạn đồng bào.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','01', 'Ngày 8-1-1959, Chủ tịch Hồ Chí Minh đến thǎm xí nghiệp may 10 (nay là Công ty may 10) ở huyện Gia Lâm, Hà Nội. Bác cǎn dặn công nhân, cán bộ phải đẩy mạnh phong trào thi đua sản xuất, cải tiến kỹ thuật và quản lý tốt xí nghiệp.\nĐến ngày 24-2-1959, Bác lại viết thư gửi công nhân, cán bộ xí nghiệp may 10. Trong thư, Bác tỏ ý vui mừng thấy công nhân, cán bộ đã \"có tiến bộ khá\" về đoàn kết, thi đua, cải tiến kỹ thuật, tǎng gia sản xuất, thực hành tiết kiệm, quản lý xí nghiệp và gửi lời cảm ơn anh, chị em đã biếu bộ quần áo. Bác viết: \"Bác đã nhận rồi. Nay Bác gửi bộ quần áo ấy làm giải thưởng cho một đợt thi đua. Khi nào đợt thi đua kết thúc, do các cô các chú bình nghị, ai khá nhất thì được giải thưởng ấy\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '08','01', 'Chu Ân Lai sinh nǎm 1898, người tỉnh Chiết Giang, Trung Quốc và mất ngày 8-1-1976.\nÔng đã từng du học ở Nhật Bản, ở Pháp. Nǎm 1922 tham gia nhóm Cộng sản trẻ Trung Quốc ở nước ngoài, rồi trở thành đảng viên Đảng Cộng sản Pháp.\nSau khi nước Cộng hoà nhân dân Trung Hoa thành lập (nǎm 1949), ông liên tục được bầu làm Thủ tướng Quốc Vụ Viện Trung Quốc và giữ chức vụ này tới khi qua đời.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','02', 'Ngày 8-2-1855 đã nổ ra cuộc khởi nghĩa Mười tám thôn Vườn Trầu ở Hoóc Môn do Phan Vǎn Hớn (tức Quản Hớn) lãnh đạo.\n1000 nghĩa quân đánh chiếm Hoóc Môn, bắn chết đốc phủ Ca. Sau đó nghĩa quân kéo về Sài Gòn, tới Quán Tre họ chia làm hai đoàn - Một đoàn đi trước thẳng vào Sài Gòn, còn một đoàn phục bên ngoài chờ hiệu lệnh tiếp ứng - Đoàn đi thẳng vào Sài Gòn đến Bình Hoà thì đụng vào một đội quân Pháp - Nghĩa binh thua trận. Còn đoàn quân tiếp ứng tự giải tán.\nTuy cuộc khởi nghĩa Mười tám thôn Vườn Trầu thất bại song nhân dân Sài Gòn đã hưởng ứng phong trào Cần Vương và đã mở đầu cuộc chiến đấu bằng đánh địch ngay tại sào huyệt của chúng ở Sài Gòn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','02', 'Chiều ngày 8-2-1931 trước sân bóng đá đường Larenhierơ, (Sài Gòn) khi trận đấu bóng vừa kết thúc thì một cuộc mít tinh lớn được tổ chức. Lá cờ đỏ búa liềm được giương cao. Đồng chí Phan Bôi đứng lên diễn thuyết. Tên thanh tra mật thám Lơ Gờrǎng định bắt đồng chí Phan Bôi liền bị Lý Tự Trọng dùng súng bắn chết. Lý Tự Trọng bị bắt ngay sau đó. Hành động dũng cảm của Lý Tự Trọng đã gây một tiếng vang lớn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','02', 'Ngày 8-2-1950, mở đầu chiến dịch Lê Hồng Phong (Tây Bắc). Ta diệt cứ điểm Phố Lu (Lao Cai) gồm hai đại đội lính Âu phi tinh nhuệ, sau sáu ngày vây hãm và tấn công. Đây là trận công kiên lớn đầu tiên của bộ đội ta. Toàn chiến dịch ta diệt 5 vị trí, bức rút 3 vị trí khác dọc tả ngạn sông Thao, diệt 500 tên, làm bị thương hơn 200 tên, bắt sống gần 100 tên, quét sạch phòng tuyến sông Thao, giải phóng một vùng rộng hàng trǎm kilômet vuông cùng hàng vạn đồng bào bị tập trung.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','02', 'Ngày 8-2-1996, tại thành phố Hồ Chí Minh đã khánh thành tuyến cáp quang biển Thái Lan - Việt Nam - Hồng Kông một dự án được hình thành đầu nǎm 1992.\nĐây là một hệ cáp quang biển ngầm dài 3.367 kilômet, có dung lượng 560 mê-ga-bít trong một giây. Cùng với các phương tiện truyền dẫn hiện đại như vệ tinh, viba số, cáp quang trên đất liền đã có, việc đưa tuyến cáp quang biển này vào khai thác đã nâng cao thêm một bước truyền dẫn thông tin của ngành Bưu điện Việt Nam với thế giới và các nước trong khu vực Đông Nam Á.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '08','02', 'Ngày 8-2-1976 là ngày mất của Chu Ân Lai. Ông sinh nǎm 1898, người tỉnh Chiết Giang, Trung Quốc. Ông đã từng du học ở Nhật Bản, ở Pháp. Nǎm 1922 tham gia nhóm Cộng sản trẻ Trung Quốc ở nước ngoài, rồi trở thành đảng viên Đảng Cộng sản Pháp. Sau khi nước Cộng hoà nhân dân Trung Hoa thành lập (nǎm 1949), ông liên tục được bầu làm Thủ tướng Quốc Vụ Viện Trung Quốc và giữ chức vụ này tới khi qua đời.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '08','02', 'Nhà vǎn Juyn Vécnơ (Jules Verne) sinh ngày 8-2-1828 tại Nantơ (nước Pháp).\nNǎm 1862, ông ra mắt bạn đọc cuốn \"Nǎm tuần lễ trên khinh khí cầu\" được hoan nghênh nhiệt liệt và trở nên nổi tiếng. Sau đó, ông cho xuất bản một loạt tiểu thuyết khoa học viễn tưởng: Sa mạc bǎng giá, Từ trái đất đến mặt trǎng, Hai mươi vạn dặm dưới đáy biển, Hành trình trong lòng đất, Tám mươi ngày vòng quanh thế giới...\nNgày nay, nhiều thành tựu mà Juyn Vécnơ miêu tả đã trở thành hiện thực.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('1', '08','03', 'Ngày Quốc tế Phụ nữ')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','03', 'Ngày 8-3 nǎm 40, Hai Bà Trưng phất cờ khởi nghĩa chống lại ách đô hộ nhà Hán. Cuộc khởi nghĩa của Hai Bà được nhân dân ủng hộ. Song vì lực lượng yếu hơn giặc nên cuộc khởi nghĩa đã thất bại. Ở Hà Nội có đền thờ Hai Bà thuộc quận Hai Bà Trưng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','03', 'Bị coi rẻ và chỉ được trả lương thấp, ngày 8-3-1899, các nữ công nhân ngành dệt và ngành may ở Chicago (Mỹ) đã bãi công, biểu tình đòi tǎng lương, giảm giờ làm việc. Cuộc đấu tranh này đã giành được thắng lợi và dẫn đến phong trào đòi bình đẳng nam nữ của phụ nữ Mỹ. Phong trào đã thu hút đông đảo quần chúng và được ủng hộ tại nhiều quốc gia. Nǎm 1910, Hội nghị Quốc tế phụ nữ lần thứ II họp tại Copenhaghen, Thủ đô Đan Mạch, theo đề nghị của Clara Zetkin, đã quyết định hàng nǎm lấy ngày 8-3 làm ngày Quốc tế Phụ nữ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','03', 'Đầu nǎm 1920, trên bến cảng Sài Gòn có một số tàu biển của Pháp đang thả neo. Do giá sinh hoạt trên đất liền cao nên các thuỷ thủ cử đại biểu đòi giám đốc Sở Thuỷ thủ phụ cấp đắt đỏ. Yêu sách không được chấp nhận nên ngày 8-3-1920 tất cả 226 thuỷ thủ tuyên bố bãi công. Trong suốt thời gian bãi công (20 ngày) các thuỷ thủ nhận được sự đồng tình mạnh mẽ của nhân dân Sài Gòn. Những người bãi công đưa ra khẩu hiệu \"Sự giải phóng người lao động do người lao động muôn nǎm!\".\nBọn chủ đã phải nhượng bộ. Sự kiện này có tiếng vang lớn, tác động đối với phong trào công nhân và nhân dân lao động ở Sài Gòn và trong cả nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','03', 'Theo hiệp định sơ bộ ký ngày 6-3-1946, quân Pháp đổ bộ lên cảng Hải Phòng, ngày 8-3-1946.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','03', 'Nhà máy dệt 8-3 tại Hà Nội do Trung Quốc giúp đỡ xây dựng trong 5 nǎm và được khánh thành ngày 8-3-1965. Đến thǎm nhà máy, Hồ Chủ tịch cǎn dặn: \"Muốn sản xuất được nhiều, các bộ phận dây truyền phải ǎn khớp. Từ đồng chí Giám đốc đến anh chị em công nhân phải đoàn kết thành một khối\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','03', 'Luật sư Phạm Vǎn Bạch, sinh nǎm 1910 tại xã Khánh Lộc, tỉnh Trà Vinh, qua đời ngày 8-3-1987 tại thành phố Hồ Chí Minh.\nÔng đỗ cử nhân luật tại trường đại học Lyông (Pháp) - nǎm 1936, ông về nước làm giáo sư, dạy học ở Cần Thơ. Tại đây ông tham gia các hoạt động yêu nước. Từ lúc còn học ở Pháp, ông đã gia nhập Đoàn Thanh niên Cộng sản Pháp, nhiều lần tranh luận trên diễn đàn, ủng hộ chủ nghĩa Mác và cách mạng tháng Mười Nga. Từ sau cách mạng tháng tám 1945, ông từng giữ các chức vụ: Chủ tịch uỷ ban kháng chiến hành chính Nam Bộ, Chánh án Toà án nhân dân tối cao, ủy viên Đoàn Chủ tịch ủy ban Trung ương Mặt trận Tổ quốc Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','03', 'Đại hội Hội nhà báo Việt Nam lần thứ 6 đã được khai mạc tại hội trường Ba Đình hôm 8-3-1995. 330 đại biểu thay mặt cho hơn 7300 hội viên thuộc 154 đầu mối đã tới dự. Đại hội lần này có khẩu hiệu: \"Báo chí vì sự nghiệp đổi mới và hiện đại hoá đất nước\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '08','03', 'Ngày 8-3-1954: Nguyên soái Voroshilov tuyên bố Liên Xô đã chế tạo thành công bom nguyên tử.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','04', 'Sau khi thất bại nặng ở Tây Bắc - Việt Nam, quân Pháp đã tǎng cường phòng thủ Thượng Lào và xây dựng tập đoàn cứ điểm mạnh tại thị xã Sầm Nưa thuộc tỉnh Xiêng Khoảng, hòng ngǎn chặn phát triển tiến công của ta và biến vùng Thượng Lào thành bàn đạp phòng thủ. Ngày 8-4-1953 bộ đội tình nguyện Việt Nam phối hợp cùng bộ đội Pha Thét Lào mở chiến dịch Thượng Lào. Liên quân Cách mạng Lào - Việt đã tiến công tiêu diệt 3 tiểu đoàn và 10 đại đội của địch, giải phóng thị xã Sầm Nưa, một phần của hai tỉnh Sầm Nưa và Phong Sa Lì, cùng 30 vạn nhân dân. Vùng giải phóng Bắc Lào được nối với vùng giải phóng Tây Bắc Việt Nam đã tạo nên cǎn cứ vững chắc cho cách mạng Lào - Việt. Chiến công Thượng Lào là thắng lợi của tình đoàn kết chiến đấu giữa hai dân tộc trên bán đảo Đông Dương. Đồng thời củng cố thế chiến lược của cách mạng Đông Dương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','04', 'Ngày 8-4-1963, Ban quân sự thuộc Ủy ban Trung ương Mặt trận dân tộc giải phóng miền Nam Việt Nam phát động phong trào thi đua rộng lớn trong toàn quân lấy tên là \"Thi đua ấp Bắc, giết giặc lập công\".\nChúng ta còn nhớ \"chiến thắng ấp Bắc\" đã diễn ra ngày 2-1-1963. Một chiến thắng có ý nghĩa hết sức to lớn bởi tại đây quân giải phóng Miền Nam đã đọ sức trực tiếp với quân Mỹ và ngụy. \"Chiến thuật thiết xa vận\" của địch bị bẻ gẫy. Chiến thắng ấp Bắc chứng tỏ quân giải phóng miền Nam đã có bước tiến về lực và về chất.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','04', 'Hồi 8 giờ 25 phút (giờ Sài Gòn). Ngày 8-4-1975, Nguyễn Thành Trung, trung úy lái máy bay của không quân ngụy và hai sĩ quan khác cùng nhận lệnh của bọn chỉ huy lái máy bay từ sân bay Biên Hoà đi ném bom vùng mới giải phóng. Nhưng lấy cớ máy bay bị trục trặc Nguyễn Thành Trung đã tách ra và vòng về Sài Gòn trút bom xuống Dinh Độc lập. Sau đó phi công Nguyễn Thành Trung đã hạ cánh an toàn tại một sân bay giải phóng. Tiếng bom trút xuống Dinh Độc lập đã gây cho ngụy quân và ngụy quyền Sài Gòn hết sức hoang mang. Chiến công này đồng thời mở ra khả nǎng không quân ta sử dụng máy bay thu được của địch đưa vào chiến đấu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','04', 'Ngày này nǎm 1982, Việt Nam và Palestin lập quan hệ ngoại giao ở cấp đại sứ.\nPalestin là một quốc gia ở phía Tây châu Á, có diện tích 27.000km2, số dân 3 triệu 500 nghìn người.\nNhân dân Palestin đã bền bỉ đấu tranh trong mấy chục nǎm để giành lại đất nước của mình bị Ixraen xâm chiếm. Ngày 15-11-1988, tuyên bố thành lập Nhà nước Palestin - Tháng 6-1992, ông Robin lên làm thủ tướng Ixraen hứa sẽ trao quyền tự trị cho người Palestin tại dải Gada và thành phố Giêricô ở khu vực bờ sông Gióocđan. Ngày 13-9-1993, Tại Oasinhtơn (Mỹ), tổ chức giải phóng Palestin (PLO) và Ixraen đã ký thoả thuận về vấn đề trên.\nĐến nay cuộc đấu tranh của nhân dân Palestin vẫn được tiếp tục và được sự ủng hộ của nhiều nước trên thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '08','04', 'Ngày 8-4-1965, thành lập Cộng đồng châu Âu (EC). Tổ chức này được thành lập trên cơ sở ký kết hiệp ước giữa các tổ chức: Cộng đồng Châu Âu về than và thiếc (ECSC), Cộng đồng châu Âu về hạt nhân (EURATOM) và Cộng đồng kinh tế châu Âu (EEC).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','05', 'Ngày 8-5-1946; Chủ tịch Chính phủ Việt Nam dân chủ cộng hoà ra sắc lệnh về việc thành lập các cơ quan lao động trong cả nước.\nNhiệm vụ của các cơ quan này là: bảo đảm và bênh vực quyền lợi cho công nhân; dung hoà quyền lợi giữa chủ và thợ, kiểm soát việc thi hành các luật lệ về lao động, giải quyết các vấn đề về lao động và phân phát nhân công cho các ngành.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','05', '8-5-1950, theo các tài liệu của Mỹ, Tổng thống Mỹ ký quyết định chính thức viện trợ quân sự cho thực dân Pháp tiến hành cuộc chiến tranh xâm lược Đông Dương. Ngày này được coi như là một cái mốc đánh dấu sự dính líu của Mỹ vào Việt Nam. Phái đoàn cố vấn và viện trợ quân sự Mỹ (MAAG) khoảng hơn 20 người được cử tới Sài Gòn ngày 26-6-1950. Nǎm 1954, sau khi thực dân Pháp thua trận ký Hiệp định Giơnevơ, lập lại hoà bình ở Đông Dương, phái đoàn MAAG bị bãi bỏ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','05', 'Ngày 8-5-1954 Chủ tịch Hồ Chí Minh đã gửi thư khen bộ đội, dân công, thanh niên xung phong và đồng bào Tây Bắc đã chiến thắng vẻ vang ở Điện Biên Phủ.\nTrong thư Bác viết: \"Thắng lợi tuy lớn nhưng mới chỉ là bước đầu. Chúng ta không nên vì thắng lợi mà kiêu, không nên chủ quan khinh địch. Chúng ta kiên quyết kháng chiến để giành lại độc lập, thống nhất, dân chủ, hoà bình. Bất kỳ đấu tranh về quân sự hay ngoại giao cũng phải đấu tranh trường kỳ mới đi đến thắng lợi hoàn toàn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','05', 'Từ ngày 8 đến ngày 20-5-1954 đã diễn ra Hội nghị Giơnevơ về Đông Dương. Mục đích của hội nghị là bàn về vấn đề lập lại hoà bình ở Đông Dương. Tham gia hội nghị có các ngoại trưởng Liên Xô, Trung Quốc, Anh, Mỹ, các bên tham chiến gồm Việt Nam dân chủ cộng hoà, Pháp và Ngụy quyền.\nTại hội nghị, đồng chí Phạm Vǎn Đồng, trưởng đoàn đại biểu Chính phủ VNDCCH đã tuyên bố lập trường cǎn bản của Chính phủ và nhân dân ta là hoà bình, độc lập, dân chủ, thống nhất và toàn vẹn lãnh thổ Việt Nam.\nSau khi nhắc lại quá trình thành lập nước VNDCCH, vạch rõ âm mưu can thiệp ngày càng trắng trợn của đế quốc Mỹ vào cuộc chiến tranh Đông Dương và nêu rõ những thắng lợi của cuộc kháng chiến của nhân dân Việt Nam, đồng chí Phạm Vǎn Đồng đề nghị 8 điểm cụ thể làm nền tảng thảo luận nhằm giải quyết việc lập lại hoà bình ở Đông Dương.\nNgày 20-5, Hội nghị đã kết thúc thắng lợi. Các bản hiệp định đình chiến ở Việt Nam, Cǎmpuchia, Lào đã được ký kết. Các nước tham gia Hội nghị thông qua bản tuyên bố chung thừa nhận: Tôn trọng độc lập, chủ quyền và toàn vẹn lãnh thổ của nhân dân Việt Nam, Cǎmpuchia và Lào, quy định quân đội Pháp rút khỏi các nước Đông Dương và ở mỗi nước sẽ tổ chức tổng tuyển cử tự do để thống nhất đất nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','05', 'Do điều kiện chiến tranh. Quốc hội khoá I đã phải kéo dài nhiệm kỳ 10 nǎm 1946-1960.\nChiến tranh kết thúc; Miền Bắc hoàn toàn giải phóng; Hiến pháp mới đã được ban hành ngày 8-5-1960, 97,52% cử tri trên toàn miền Bắc đi bỏ phiếu bầu cử Quốc hội khoá II. Có 362 người trúng cử, trong đó người trúng cử với số phiếu cao nhất là Chủ tịch Hồ Chí Minh, ứng cử tại khu Ba Đình - Hà Nội. Thành phần Quốc hội khoá II có 65 người thuộc các dân tộc ít người, 49 nữ, 40 thanh niên từ 21-30 tuổi, 46 nông dân, 20 bộ đội, 65 đại biểu làm công tác khoa học, nghệ thuật, giáo dục, 2 đại biểu tư sản dân tộc, 3 linh mục, 2 hoà thượng. Trong kỳ họp đầu tiên, Quốc hội khoá II đã bầu Bác Hồ Làm Chủ tịch nước, Bác Tôn là Phó chủ tịch nước và đồng chí Trường Chinh là Chủ tịch Uỷ ban thường vụ Quốc hội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','05', 'Ngày 8-5-1963, hai vạn đồng bào Huế, trong đó có một vạn tǎng ni và tín đồ theo đạo Phật, phát động đợt đấu tranh chống chính quyền Diệm khủng bố tôn giáo, phản đối lệnh cấm tổ chức lễ Phật đản và treo cờ Phật trong các chùa. Cuộc đấu tranh được sự đồng tình rộng rãi của các sĩ quan và nhân viên ngụy quyền Thừa Thiên - Huế, kể cả một số sĩ quan và nhân viên ngụy quyền cao cấp. Bọn Diệm đã huy động cảnh sát, xe bọc thép, đại bác 37 ly đến đàn áp, làm 13 người bị chết, nhiều người bị thương, gần 100 người bị bắt. Phong trào đấu tranh của đồng bào Phật giáo càng phát triển mạnh mẽ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','05', 'Cùng phối hợp chặt chẽ với đòn tiến công quân sự ở các chiến trường, Đảng ta chủ trương đẩy mạnh hơn nữa mặt trận đấu tranh ngoại giao để tranh thủ thêm sự ủng hộ của nhân dân thế giới. Ngày 8-5-1969 trong phiên họp toàn thể lần thứ 16 của Hội nghị Pari về vấn đề Việt Nam, đoàn đại biểu mặt trận dân tộc giải phóng miền Nam đã đưa ra giải pháp toàn bộ 10 điểm. Nội dung của giải pháp toàn bộ 10 điểm thể hiện lập trường có tính chất nguyên tắc và biện pháp sách lược mềm dẻo của ta trong việc giải quyết hoà bình vấn đề miền Nam - Việt Nam, giải pháp đặc biệt nhấn mạnh hai vấn đề chủ yếu là vấn đề Mỹ rút quân và vấn đề nội bộ giữa người Việt Nam với nhau ở miền Nam sau khi Mỹ rút quân về nước. Giải pháp đã vạch ra ranh giới giữa đế quốc Mỹ là kẻ đi xâm lược với nhân dân Việt Nam là người chống xâm lược.\nGiải pháp toàn bộ 10 điểm đã đánh dấu một thắng lợi mới về ngoại giao, một lần nữa khẳng định lập trường cứng rắn của Đảng ta, giải pháp đã trở thành cương lĩnh đấu tranh của mặt trận nhân dân')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','05', 'Từ ngày 8 đến 11-5-1978 tại Hà Nội đã diễn ra Đại hội Công đoàn toàn quốc lần thứ tư.\nTham dự Đại hội có 826 đại biểu, trong đó có 444 đại biểu là công nhân và cán bộ quản lý, 135 đại biểu là anh hùng chiến sĩ thi đua.\nKể từ nǎm 1929 thành lập Công hội đỏ đến Đại hội Công đoàn toàn quốc lần thứ tư này, lần đầu tiên giai cấp công nhân cả nước họp mặt đông đủ nhất.\nĐại hội đã nghe bài \"Làm chủ tập thể phản ánh bản lĩnh của giai cấp công nhân\" của đồng chí Lê Duẩn. Đại hội đã thông qua Báo cáo sửa đổi Điều lệ Công đoàn. Đại hội đã bầu Ban Chấp hành Tổng Công đoàn Việt Nam do đồng chí Nguyễn Vǎn Linh làm Chủ tịch, đồng chí Nguyễn Đức Thuận làm Phó chủ tịch kiêm Tổng Thư ký.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '08','05', 'Ngày 24-6-1859 tại Xônphêrinô, Bắc Ý, diễn ra một cuộc chiến đấu ác liệt. Sau khi hai bên rút quân để lại trên trận địa gần 40.000 binh lính vừa chết, vừa bị thương nằm ngổn ngang không ai cứu chữa. Ông Hǎngri Đuynǎng, một công dân Thụy Sĩ đi qua đã chứng kiến thảm cảnh đó, động lòng trắc ẩn, liền đến các làng lân cận kêu gọi nhân dân tổ chức cứu chữa, không phân biệt người bên nào. Nhiều người đã được cứu sống. Sau đó ông xuất bản cuốn \"Ký ức Xônphêrinô\" tả lại thảm kịch đó và ông đề nghị Chính phủ các nước thành lập \"Hội cứu trợ thương binh\" và ký kết một công ước \"Cứu trợ và bảo hộ thương binh\".\nTừ ngày 26 đến 29-10-1863 do chính phủ Thụy Sĩ khởi xướng, một hội nghị quốc tế có 16 nước tham dự đã bàn về việc cứu chữa thương binh trong chiến tranh. Hội nghị công nhận một dấu hiệu để bảo vệ thương binh và bảo vệ những phương tiện phục vụ họ là dấu hiệu \"Chữ thập đỏ viền trắng\" (là mầu sắc đảo ngược của quốc kỳ Thụy Sĩ) để ghi nhớ công lao và sáng kiến của Hǎngri Đuynǎng.\nNgày 5-5-1919 một hội nghị quốc tế đã họp ở Pari quyết định thành lập \"Hiệp hội chữ thập đỏ quốc tế\". Hiệp hội đã quyết định lấy ngày 8-5, ngày sinh của Hǎngri Đuynǎng, là \"Ngày chữ thập đỏ thế giới\" để tổ chức kỷ niệm nhằm tǎng cường hoạt động của Hiệp hội chữ thập đỏ thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','06', 'Theo chủ trương của Trung ương Đảng, ngày 8-6-1945, nhân dân Đông Bắc dưới sự lãnh đạo của Đảng bộ địa phương đã nổi dậy đánh chiếm các đồn Đông Triều, Chí Linh, Mạo Khê, Tràng Bạch, lập ra \"Đệ tứ chiến khu\", tức chiến khu Trần Hưng Đạo. Với các Uỷ ban nhân dân cách mạng và đội du kích chống Nhật.\nChiến khu Trần Hưng Đạo nằm trong vùng giàu có về mỏ, có nhiều công nhân tập trung, có truyền thống cách mạng và có một vị trí quan trọng ở vùng đông Bắc Bộ.\nChính vì vậy, ngay sau khi nổ ra khởi nghĩa, giặc Nhật liên tiếp nmở các cuộc càn quét vào chiến khu. Nhưng chúng đều bị quân và dân ta giáng trả mãnh liệt. Ưu điểm của các trận đánh là biết dựa vào cao trào cách mạng cả nước, biết kết hợp chặt chẽ tác chiến với các công tác vận động binh lính địch. Lực lượng vũ trang và quần chúng đã giành chính quyền tại nhiều địa phương ở Hải Dương và Quảng Yên.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','06', '8-6-1967 Chính phủ nước Việt Nam Dân chủ cộng hoà ra \"Tuyên bố công nhận biên giới hiện tại của Vương quốc Cǎmpuchia\". Bản tuyên bố viết: \"Chính phủ nước Việt Nam Dân chủ cộng hoà trịnh trọng tuyên bố: \n1. Công nhận và tôn trọng toàn vẹn lãnh thổ của Cǎmpuchia trong biên giới hiện tại \n2. Hoàn toàn tán thành tuyên bố ngày 31 tháng 5 nǎm 1967 của Ủy ban Trung ương Mặt trận dân tộc giải phóng miền Nam Việt Nam công nhận biên giới hiện tại giữa miền Nam Việt Nam và Cǎmphuchia.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','06', 'Ngày 8-6-1969, do những thất bại trên chiến trường và chịu sức ép của phong trào phản chiến ở Mỹ, Tổng thống Hoa Kỳ Nichơn tuyên bố đợt rút quân đầu tiên 25.000 lính Mỹ khỏi Việt Nam. Đợt thứ hai là 35.000 lính vào tháng 8-1969.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','06', 'Nhạc sĩ, giáo sư Lưu Hữu Phước, còn có tên là Huỳnh Minh Siêng sinh nǎm 1921, quê ở tỉnh Cần Thơ.\nÔng đã sáng tác ca khúc từ trước Cách mạng Tháng Tám, là tác giả của hàng trǎm bài hát, trong đó có nhiều bài nổi tiếng, ghi lại những chặng đường đấu tranh giải phóng dân tộc như: Bạch Đằng Giang, Hội nghị Diên Hồng, Hồn tử sĩ, Lên đàng, Tiếng gọi thanh niên, Ca ngợi Hồ Chủ tịch, Giờ hành động, Giải phóng miền Nam, Tiến về Sài Gòn.\nVới tài nǎng và những cống hiến to lớn của mình, ông được tín nhiệm giao gánh vác các trọng trách: Phó Tổng thư ký Hội Nhạc sĩ Việt Nam khoá 2, Viện trưởng Viện Nghiên cứu Âm nhạc, Chủ tịch Hội đồng âm nhạc Quốc gia, Bộ trưởng Thông tin - vǎn hoá của Chính phủ Cách mạng lâm thời miền Nam Việt Nam.\nNhạc sĩ, giáo sư Lưu Hữu Phước qua đời ngày 8-6-1989.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '08','06', 'Rôbe Suman sinh ở thành phố Xvican ngày 8-6-1810, thuộc tiểu vương Xắcxôni, trong một gia đình khá giả có cửa hiệu in sách.\nTừ 7 tuổi, Suman đã có sáng tác đầu tay và thường được dùng trong các buổi hoà tấu ở gia đình. Trong một thời gian dài, Suman biểu diễn đàn Pianô. Ông trở thành nhà soạn nhạc thiên tài và là một trong người tiêu biểu nhất của lĩnh vực thấm mỹ âm nhạc của Đức và thế giới.\nRôbe Suman mất ngày 29-7-1856, để lại nhiều tác phẩm đủ các loại hình liên khúc cho pianô, giao hưởng, công xéctô, Ôratôrioo, Ôpêra, và nổi bật hơn cả là những tác phẩm cho đàn phím.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '08','06', 'Ngày 8-6-1988, Ngoại trưởng Liên Xô Eduard Shevardnadze tuyên bố trước Liên hiệp quốc rằng Liên Xô sẵn sàng ngừng vĩnh viễn các vụ thử vũ khí hạt nhân nếu như Mỹ cũng đồng ý làm như vậy.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '08','06', 'Ngày này năm 1992, các đại biểu tham dự Hội nghị thượng đỉnh Trái đất tại Rio de Janero, Brazil thông qua việc thành lập một cơ chế mới của Liên hiệp quốc nhằm giám sát việc tuân thủ các hiệp ước về môi trường.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','07', 'Giáo sư, bác sĩ y khoa, thiếu tướng Đỗ Xuân Hợp sinh ngày 8-7-1906 tại Hà Nội và qua đời vào ngày 17-12-1985. \nTrước cách mạng Tháng Tám, ông làm việc ở các bệnh viện Hà Nội. Kháng chiến chống thực dân Pháp, ông đảm nhiệm công tác y tế quân sự. Trong khoảng 40 nǎm, ông đã giữ các chức vụ: Hiệu trưởng trường đại học Quân y; Chủ tịch Hội Chữ thập đỏ Việt Nam, Phó chủ tịch Hội Y học Việt Nam. Ủy viên thường vụ Ban chấp hành Trung ương Đảng xã hội Việt Nam, đại biểu quốc hội từ khóa 2 đến khóa 7, Phó chủ nhiệm Ủy ban Y tế - xã hội của Quốc hội khoá 6.\nDo công lao đóng góp cho ngành y tế quân đội và các hoạt động xã hội, giáo sư Đỗ Xuân Hợp đã được Nhà nước tặng thưởng Huân chương Hồ Chí Minh, Huân chương quân công và danh hiệu Anh hùng lực lượng vũ trang nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','07', 'Từ ngày 8 đến ngày 10-7-1963, Trung ương Đoàn triệu tập Đại hội những người xuất sắc trong phong trào xung phong tình nguyện vượt mức kế hoạch 5 nǎm lần thứ nhất tại Hà Nội, hơn 600 đại biểu xuất sắc của gần 1 triệu nam, nữ đoàn viên thanh niên tham gia phong trào đã về dự.\nĐồng chí Lê Duẩn đã đến nói chuyện với Đại hội. Đồng chí nói: \"Đoàn viên và thanh niên khắp nơi trên miền Bắc đã lập được nhiều thành tích xuất sắc trên các mặt công tác, sản xuất nông nghiệp, sản xuất công nghiệp, vǎn hoá, giáo dục, quân sự, bảo vệ trị an... Các đồng chí rất xứng đáng là người con ưu tú của nhân dân lao động, những người con đáng tin cậy của Đảng, xứng đáng là cháu Bác Hồ\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','07', 'Nhất Linh tên thật là Nguyễn Tường Tam, sinh tại Cẩm Giàng, Hải Dương. Quê gốc Quảng Nam\nÔng trưởng thành trong một gia đình viên chức, phong kiến quan lại. Hồi nhỏ đi học ở thị xã Hải Dương, rồi Hà Nội, sau làm việc ở Sở tài chính và bắt đầu viết vǎn. Tốt nghiệp đại học khoa học ở Paris, trở về kết bạn với Khái Hưng, thành lập và làm Chủ tướng Tự Lực Vǎn Đoàn - 1933, chủ trương các báo Phong Hoá và Ngày Nay (1932-1935). Hệ tư tưởng của Nguyễn Tường Tam rất phức tạp. Nhưng là nhà vǎn, Nhất Linh lại có những đóng góp nhất định. Tác phẩm của ông có: \"Nho Phong\"(1925); \"Người qua tơ\", \"Đời mưa gió\", \"Đoạn tuyệt\" (1934) v.v... Do dính líu vào một âm mưu lật đổ, bị Ngô Đình Diệm truy tố, ông tự sát ngày 8-7-1963 tại Sài Gòn')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','07', 'Viện giám định y khoa - cơ quan nghiên cứu khoa học kỹ thuật về mặt khám, giám định thương tật, tai nạn lao động, tiêu chuẩn sức khoẻ để xác định khả nǎng lao động cho các đối tượng cán bộ, công nhân viên nhà nước. Quân nhân xuất ngũ, những người được hưởng các chính sách, chế độ ưu đãi như thương binh - được thành lập ngày 8-7-1974.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '08','07', 'Seli Pơxi Bisow (Shelley Percy Bysshe) nhà thơ Anh. Ông sinh ngày 4-8-1972 trong một gia đình quý tộc giàu có. Ông là người có đầu óc tự do, say mê vǎn học, triết học có tư tưởng dân chủ quan điểm vô thần. Nǎm 19 tuổi ông bị đuổi khỏi đại học vì viết bản luận vǎn \"Sự cần thiết của chủ nghĩa vô thần\". Sau đó xuất bản các tác phẩm chính như: \"Hoàng hậu Margo\", \"Thư gửi gió Tây\", \"Cuộc nổi dậy của thế giới Hồi giáo\", \"Promêtê được giải phóng\", \"Đám mây\", \"Gửi chim sơn ca\", \"Chiến thắng cuộc đời\"...\nSheli được xem là đại diện ưu tú của chủ nghĩa lãng mạn Cách mạng ở Anh. Thơ ông đề cao tuyên truyền cho tư tưởng cấp tiến, đề cao nhân dân và sự giải phóng nhân dân nhờ tình yêu và lý tưởng. Ông xây dựng những hình tượng anh hùng mới, anh hùng của nhân dân. Thơ ông là tiếng reo của cả thế hệ mới, báo hiệu một tương lai tươi sáng. Ông chết trong một nạn đắm tàu do bão lớn vào ngày 8-7-1922 khi mới 30 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','08', 'Mười tám ngày sau khi hội nghị Giơnevơ kết thúc, ngày 8-8-1954 Hội đồng an ninh quốc gia Mỹ do tổng thống Aixenhao chủ trì, chính thức quyết định chủ trương hất cẳng Pháp và thay Pháp xâm lược Việt Nam.\nQuyết định này có những nội dung cơ bản:\n- Mỹ trực tiếp viện trợ cho ngụy quyền Sài Gòn không qua tay Pháp.\n- Pháp phải rút hết quân khỏi miền Nam và phải ủng hộ Diệm.\n- Loại bỏ Bảo Đại, tay sai lâm thời của Pháp.\nĐây là sự kiện chủ yếu nói lên chủ trương của Mỹ gạt Pháp, mở đầu giai đoạn chủ nghĩa thực dân mới Mỹ trực tiếp xâm lược Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','08', 'Tính đến ngày 8-8-1965, quân và dân tỉnh Nghệ An bắn rơi tròn 100 máy bay Mỹ ở tỉnh nhà.\nTrong những nǎm chiến đấu chống chiến tranh phá hoại của đế quốc Mỹ đối với miền Bắc, quân và dân Nghệ An đã bắn rơi 516 máy bay Mỹ, bắn cháy, bắn chìm 33 tàu chiến Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','08', 'Trường đại học Quân y nay là Học viện Quân y được thành lập ngày 8-8-1966, với nhiệm vụ đào tạo và bồi dưỡng bác sĩ, dược sĩ quân y có trình độ đại học theo các hình thức dài hạn, chuyên tu, tại chức. Cùng với Viện nghiên cứu y học quân sự nghiên cứu khoa học quân y, Học viện còn đào tạo và bồi dưỡng cán bộ quân y bậc sau đại học.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','08', 'Do sản xuất và đốt pháo đã gây nhiều vụ tai nạn, cháy nhà, ảnh hưởng đến trật tự an ninh, tổn thất kinh tế, ô nhiễm môi trường... xuất phát từ nguyện vọng, lợi ích chung của nhân dân, đồng thời thực hành tiết kiệm, chống lãng phí, ngày 8-8-1994 Thủ tướng Chính phủ đã ra chỉ thị về việc \"cấm sản xuất, buôn bán và đốt pháo\".\nBản chỉ thị nên rõ: \"Kể từ ngày 1-1-1995 nghiêm cấm sản xuất, buôn bán và đốt các loại pháo nổ, thuốc nổ trong phạm vi cả nước (trừ các loại pháo hoa và thuốc làm pháo hoa).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '08','08', 'Sau khi đánh bại nước Đức phát xít và tuân thủ thoả thuận với các nước đồng minh, ngày 8-8-1945 Chính phủ Liên Xô tuyên chiến chống Nhật Bản. Hồng quân Liên Xô đã tiến công mãnh liệt hầu khắp các mặt trận Viễn Đông, và đánh tan đạo quân Quan Đông của Nhật gồm 1 triệu binh lính, giải phóng Mãn Châu, miền nam Sakhalin, quần đảo Cuarin và bắc Triều Tiên.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '08','08', 'Ngày 8-8-1967, tại Bǎng Cốc (Thái Lan) đã thành lập Hiệp hội các nước Đông Nam Á (Viết tắt theo tiếng Anh là ASEAN). Lúc đó 5 thành viên là Thái Lan, Inđonêxia, Philippin, Xingapo và Malaixia. Đến nǎm 1984, có thêm Brunây. Ngày 28-7-1995 chính thức kết nạp nước Việt Nam. Tháng 7-1997 Lào và Mianma gia nhập ASEAN.\nASEAN là tập hợp những nước nhỏ và vừa có vǎn hoá lịch sử, tôn giáo, chế độ chính trị và trình độ phát triển khác nhau. ASEAN luôn thực hiện phương châm \"Thống nhất trong đa dạng\", phát huy cao độ thế mạnh của từng nước, ưu tiên hàng đầu cho đẩy mạnh hợp tác kinh tế giữa các nước thành viên. Hiện nay, Đông Nam Á được coi là khu vực nǎng động về kinh tế, có tốc độ tǎng trưởng từ 5 đến 8% tổng sản phẩm xã hội mỗi nǎm, cao hơn nhiều khu vực khác trên thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','09', 'Ngày 8-9-1945, Hồ Chủ tịch ký sắc lệnh thành lập Nha Bình dân học vụ. Nha Bình dân học vụ có nhiệm vụ phụ trách việc chống nạn mù chữ trong cả nước. Chỉ sau một năm, có hơn 1 triệu người biết đọc, biết viết.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','09', 'Xã Cẩm Bình (Hà Tĩnh) được nhận giải thưởng N.Crúpxcaia, do cơ quan Văn hoá - khoa học - giáo dục Liên hiệp quốc (UNESCO) trao tặng nhân ngày Quốc tế xoá mù chữ 8-9-1978.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','10', 'Từ 8-10 đến ngày 3-11-1427 đã diễn ra nhiều trận đánh giữa quân ta và giặc Minh từ Đông Quan (Hà Nội ngày nay) đến biên giới Việt - Trung, mà khu vực chủ yếu là Chi Lăng (Lạng Sơn), Xương Giang (Bắc Giang). Quân ta đã vây thành, diệt quân địch tiếp viện, phục kích, tập kích, công kích quân địch phòng ngự dã ngoại. Sau gần một tháng, quân ta đã diệt và bắt khoảng 22 vạn quân tiếp viện của nhà Minh, trong đó hầu hết các tướng chỉ huy chủ yếu. Tướng Liễu Thăng bị chém ở chân núi Mã Yên, Thượng thư Lý Khánh khiếp sợ quá phải tự sát, Tổng binh Vương Thông bị bao vây ở thành Đông Quan phải đầu hàng. Chiến thắng Chi Lăng, Xương Giang đã buộc nhà Minh phải rút số quân còn lại về nước và thừa nhận nền độc lập của dân tộc ta.\nNguyễn Trãi đã mô tả chiến thắng này trong bản \"Bình Ngô đại cáo\" như sau:\n\"Đánh trận đầu, sạch sanh kình ngạc\nĐánh trận nữa, tan tác chim muông\nLỗ kiến soi, đê vỡ phá tung\nGió thổi mạnh, lá khô trút sạch\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','10', 'Ngày 8-10-1946, Chính phủ đã ra sắc lệnh thành lập ngành Sư phạm Việt Nam.\nĐến nay ngành có giáo viên mầm non, giáo viên phổ thông, chuyên nghiệp và đại học, với tổng số 80 vạn người. Trong số này có hơn 300 giáo sư, hàng chục nghìn tiến sĩ, phó tiến sĩ, hàng chục vạn cử nhân, cả nước có 81 trường sư phạm, trong đó có 10 trường đại học, 40 trường cao đẳng và 31 trường trung học sư phạm.\nNgành sư phạm nước ta đã được Nhà nước trao tặng Huân chương Độc lập hạng nhất.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','10', 'Hội Liên hiệp Thanh niên Việt Nam chính thức được thành lập trong đại hội đại biểu thanh niên toàn quốc, khai mạc tại Nhà hát lớn Hà Nội ngày 8-10-1956\nCác đại biểu đã bầu Ban Chấp hành Trung ương Hội Liên hiệp Thanh niên Việt Nam gồm 52 người, do bác sĩ Phạm Ngọc Thạch làm chủ tịch.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','10', 'Ngày 8-10-1960 đã diễn ra Lễ kết nghĩa ba thành phố lớn Hà Nội - Huế - Sài Gòn tại Hà Nội. Trong buổi lễ, đồng bào Huế tặng đồng bào Hà Nội, đồng bào Sài Gòn bức trướng thêu:\n\"Hà Nội - Huế - Sài Gòn\nLà cây một cội, là con một nhà\".\nCùng với phong trào kết nghĩa giữa ba thành phố lớn, mỗi tỉnh, mỗi thành phố miền Nam kết nghĩa với một tỉnh, thành miền Bắc. Phong trào kết nghĩa Bắc - Nam còn đi sâu vào nhiều ngành, nhiều đơn vị nói lên mối tình keo sơn ruột thịt của đồng bào hai miền Bắc - Nam và sức mạnh chân lý: dân tộc ta là một, đất nước ta là một.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '08','10', 'Nhà văn Henri Phinđing (Henri Fillding) sinh ngày 22-4-1707. Các tác phẩm của ông luôn hướng về việc miêu tả những con người thuộc tầng lớp bình dân. Ông nghiêng về ca ngợi, phát hiện những vẻ đẹp nhân tính trong thân phận những con người nhỏ bé. Ông được suy tôn là \"Người cha của tiểu thuyết Anh\".\nÔng mất ngày 8-10-1754.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','11', 'Ngày 8-11-1917, Đại hội lần thứ II Xô Viết đại biểu công nhân, binh lính và nông dân Nga thông qua sắc lệnh về ruộng đất và sắc lệnh về hoà bình, thành lập Chính phủ Xô Viết đầu tiên do Lênin đứng đầu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','11', 'Ngày 8-11-1958, Hồ Chủ tịch đã đến thǎm và nói chuyện với công nhân, cán bộ Nhà máy Điện Bờ Hồ (nay là Công ty Điện lực Hà Nội)\nTrước đó, Bác Hồ đã đến thǎm nhà máy này hai lần vào ngày 21-12-1954 và ngày 25-4-1958.\nTrong các lần đến thǎm, Bác đã cǎn dặn công nhân, cán bộ phải có trách nhiệm của người làm chủ xí nghiệp, đẩy mạnh sản xuất và thực hành tiết kiệm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','11', 'Từ ngày 8 đến ngày 10-11-1983, Đại hội những người công giáo Việt Nam xây dựng và bảo vệ Tổ quốc, bảo vệ hoà bình họp tại Hà Nội. Đại hội đã cử ra Uỷ ban đoàn kết công giáo yêu nước Việt Nam và thông qua Điều lệ của Uỷ ban.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '08','11', 'Mikhain Vaxiliêvich Lômônôxốp sinh ngày 8-11-1711, mất nǎm 1765, là nhà bác học Nga thế kỷ XVIII. Những sáng tác, những cống hiến của ông rất đa dạng, đặc biệt trong hoá học và vật lý học, trong nghiên cứu địa chất và địa lý, trong xây dựng ngành mỏ, luyện kim và đồ sứ, cả trong ngôn ngữ vǎn học, lịch sử, triết học.\nTại thủ đô Mátxcơva của Liên bang Nga có một trường đại học tổng hợp quốc gia mang tên Lômônôxốp, ở đó đã đào tạo nhiều nhà khoa học Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','12', 'Nguyễn Bỉnh Khiêm sinh nǎm 1491 và qua đời ngày 8-12-1585, người làng Trung Am, huyện Vĩnh Bảo, Hải Phòng.\nÔng học giỏi, đỗ trạng nguyên, làm quan dưới triều nhà Mạc. Ông đã dâng sớ vạch tội và xin chém đầu 10 tên lộng thần. Vua không nghe, ông từ quan, về dạy học. Học trò có nhiều người nổi tiếng như Phùng Khắc Khoan, Nguyễn Dữ... Trong khi ông ở ẩn, vua nhà Mạc và các Chúa Trịnh, Nguyễn có nhiều việc hệ trọng vẫn cho người đến hỏi ý ông. Ông thường kín đáo khuyên vua chúa cố gắng tránh chiến tranh để nhân dân khỏi khổ. Lúc mất ông được vua Mạc truy phong Trình quốc công, do đó có tên gọi là Trạng Trình.\nNguyễn Bỉnh Khiêm còn để lại hai tập thơ chữ Hán và chữ Nôm là: Bạch vân am thi tập và Bạch vân quốc ngữ thi tập. Ông được xếp vào hàng những nhà thơ lớn của dân tộc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','12', 'Đêm 7 rạng ngày 8-12-1941 không quân của hạm đội Nhật bất ngờ tập kích vào Trân Châu Cảng, một cǎn cứ chiến lược Mỹ ở Ha Oai (Thái Bình Dương) gây cho quân Mỹ những thiệt hại nghiêm trọng. Ngày 8-12, Nhật tuyên chiến với Mỹ, Anh, Úc và Canađa, đồng thời cho quân đội đổ bộ lên đảo Boocnéo (thuộc địa Anh ở Đông Nam Á), chiếm tô giới Anh ở Thiên Tân, Thượng Hải. Cùng Ngày, Mỹ, Anh, Úc, Canađa và Pháp cũng tuyên chiến với Nhật. Chiến tranh Thái Bình Dương bùng nổ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '08','12', 'Ngày 8-12-1973, đoàn 24 công nhân xây dựng Cuba đầu tiên do Đảng cộng sản và Chính phủ Cuba cử sang xây dựng đã đến Việt Nam. Đội xây dựng quốc tế xã hội chủ nghĩa Cuba đã tham gia xây dựng nhiều công trình quan trọng ở miền Bắc Việt Nam. Trong nhiều nǎm liền các công nhân xây dựng Cuba, với tình cảm anh em, đã góp phần thắt chặt mối quan hệ hữu nghị sâu sắc Việt Nam - Cuba. Nhiều công trình đã phát huy tốt, góp phần vào công cuộc xây dựng xã hội chủ nghĩa ở Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '08','12', 'Nhà máy điện nhờ trữ không khí nén đã được Hǎntophơ (Huntorf) - người Đức - giới thiệu trước công chúng ngày 8-12-1978.\nCác nhà máy điện nhờ trữ không khí nén cho phép thay nhà máy thuỷ điện ở vùng đồng bằng. Một bể chứa được tạo thành từ những hang ở độ sâu 500 mét, được chứa đầy không khí nén. Nó có thể tạo ra công suất khoảng 220 mêga oát.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','01', 'Ngày 9-1-1950, hơn hai nghìn học sinh, sinh viên Sài Gòn biểu tình trước dinh Tổng trấn Nam phần, chống chính quyền bù nhìn, tay sai Pháp.\nCuộc biểu tình bị đàn áp dã man, một số học sinh, sinh viên bị chết và bị thương, trong đó có học sinh Trần Vǎn Ơn. Hôm sau, hàng vạn đồng bào Sài Gòn xuống đường đưa tang những người hy sinh. Ngày 13 tháng Giêng nǎm 1950, hầu hết học sinh, sinh viên Hà Nội bãi khoá để tỏ tình đoàn kết với các bạn Sài Gòn.\nTừ đó, ngày 9 tháng Giêng hằng nǎm trở thành ngày học sinh, sinh viên toàn quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','01', 'Ngày 9-1-1965, đường xe ôtô Bắc Quang - Hoàng Su Phì (Hà Giang) dài 61km, vượt qua nhiều đèo dốc cheo leo được khánh thành. Đông đảo đồng bào thuộc 11 dân tộc đã hǎng hái tham gia cùng với lực lượng thanh niên xung phong làm con đường này. Việc thông thường đã góp phần đưa vǎn hoá lên vùng cao, tạo cho bà con các dân tộc ít người có điều kiện phát triển sản xuất và đời sống.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','01', 'Danh sĩ Phan Huy Ích sinh ngày 9-1-1750, quê ở tỉnh Hà Tĩnh (là bố của Phan Huy Chú) qua đời nǎm 1822.\nPhan Huy Ích đỗ tiến sĩ và đã làm quan dưới thời vua Lê chúa Trịnh nhưng chán ghét chế độ.\nNǎm 1788, Nguyễn Huệ đem quân ra Bắc lần thứ hai, ông cùng với một số danh sĩ được Nguyễn Huệ mời vào Phú Xuân lĩnh nhiệm vụ bang giao với nhà Thanh. Ông được vua Quang Trung phong làm Tả thị lang Bộ Hộ. Nǎm 1790, ông cùng sứ bộ sang nhà Thanh. Đến nǎm 1802, ông bị nhà Nguyễn bắt và đánh đòn tại Vǎn Miếu rồi thả về.\nPhan Huy Ích để lại nhiều tác phẩm chữ Hán và chữ Nôm, trong đó có: Dụ Am ngâm lục và Dụ Am vǎn tập.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '09','01', 'Hoàng thân Xuphanuvông sinh nǎm 1909 và qua đời 9-1-1995, tại Thủ đô Viêng Chǎn (Cộng hoà dân chủ nhân dân Lào).\nÔng nguyên là Uỷ viên Bộ Chính trị Ban Chấp hành Trung ương Đảng nhân dân cách mạng Lào, Chủ tịch nước Cộng hoà dân chủ nhân dân Lào, Chủ tịch Mặt trận Lào xây dựng đất nước.\nÔng Xuphanuvông có nhiều cống hiến to lớn vào việc xây dựng và củng cố tinh thần đoàn kết chiến đấu và tình hữu nghị Việt - Lào.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '09','01', '9-1-1905, Ngày Chủ nhật đẫm máu ở Nga, những người biểu tình đã bị quân của Nga Sa hoàng bắn chết.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '09','01', 'Ngày 9-1-1941, 6.000 người Do Thái bị giết trong một cuộc tàn sát ở Bucharest, Rumania.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','02', 'Đồng chí Trường Chinh tên thật là Đặng Xuân Khu sinh ngày 9-2-1907 tại làng Hành Thiện, huyện Xuân Trường, tỉnh Nam Định, qua đời nǎm 1988 tại Hà Nội.\nĐồng chí là một trong những người lãnh đạo chủ chốt của Cách mạng Việt Nam, từng giữ nhiều chức vụ quan trọng: Uỷ viên Bộ Chính trị, Tổng Bí thư Đảng Cộng sản Việt Nam, Chủ tịch Uỷ ban thường vụ Quốc hội, Chủ tịch Hội đồng Nhà nước.\nĐồng chí Trường Chinh có cống hiến to lớn về lý luận Cách mạng, có nhiều trước tác giá trị. Đồng chí còn là nhà báo sắc sảo và nhà thơ với bút danh Sóng Hồng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','02', 'Đêm 9-2-1930, cuộc khởi nghĩa do Việt Nam Quốc dân đảng lãnh đạo nổ ra ở Yên Bái, tiếp theo là Phú Thọ, Hải Dương, Thái Bình. Ở Hà Nội cũng có đánh bom để phối hợp.\nTại tỉnh Yên Bái, quân khởi nghĩa đã chiếm được trại lính, giết và làm bị thương một số sĩ quan và hạ sĩ quan Pháp, song không làm chủ được tỉnh lỵ nên hôm sau đã bị quân Pháp phản công và dập tắt. Còn ở các nơi khác, nghĩa quân chỉ tạm thời làm chủ vài huyện lỵ nhỏ nhưng đã bị địch nhanh chóng chiếm lại.\nCuộc khởi nghĩa của Việt Nam Quốc dân đảng tuy thất bại song đã cổ vũ lòng yêu nước và chí cǎm thù của nhân dân Việt Nam đối với thực dân Pháp và tay sai. Tấm gương hy sinh của các chiến sĩ Yên Bái là sự nối tiếp truyền thống yêu nước, bất khuất của dân tộc ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','02', 'Ngày 9-2-1977, Trung tâm Nghiên cứu khoa học đặt tại Nghĩa Đô, Hà Nội được khánh thành.\nĐây là một trung tâm nghiên cứu khoa học tự nhiên, kỹ thuật và công nghệ. Trung tâm bao gồm các viện nghiên cứu, các phòng thí nghiệm với trang thiết bị hiện đại đủ sức hoàn thành công tác nghiên cứu khoa học và ứng dụng khoa học kỹ thuật. Hiện nay trung tâm là cơ quan khoa học hàng đầu nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '09','02', 'Phêđo Mikhailôvích Đôxtôiépxki sinh nǎm 1821, qua đời ngày 9-2-1881.\nÔng là một nhà vǎn lớn của nước Nga.\nTác phẩm chủ yếu của ông gồm các tiểu thuyết: Tội ác và trừng phạt, Thằng ngốc, Anh em nhà Caramadốp, đã được dịch ra tiếng Việt và được bạn đọc Việt Nam yêu thích.\nGoócki đánh giá Đôxtôiépxki là \"nhà vǎn thiên tài biết phân tích những bệnh trạng của xã hội thời ông\", \"là một thiên tài không thể phủ nhận được; với sức biểu hiện như vậy, chỉ có Sêcxpia mới có thể sánh ngang hàng\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','03', 'Ngay từ nǎm 1944, trên cơ sở phân tích tình hình thế giới và cách mạng Đông Dương, những người Cộng sản Việt Nam đã nhận định về sự hoà hoãn giữa Nhật và Pháp ở Đông Dương \"Có khác chi một cái nhọt bọc chất bên trong biết bao vi trùng và máu mủ chờ dịp vỡ tung ra\". Và tiên đoán cả Nhật và Pháp sửa soạn tiến tới chỗ tranh giành nhau quyết liệt.\nĐúng như vậy, 19 giờ ngày 9-3-1945 đại sứ Nhật tại Hà Nội trao cho toàn quyền Đông Dương một tối hậu thư buộc Pháp đặt toàn bộ binh lính dưới quyền chỉ huy của Nhật. Phía Pháp xin hoãn đến 21 giờ. Nhật nổ súng đánh Pháp. Quân Pháp tại Hà Nội và cả Đông Dương đầu hàng. Đông Dương trở thành thuộc địa của phát xít Nhật.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','03', 'Ngay trong đêm Nhật đảo chính Pháp 9-3-1945, Ban thường vụ Trung ương Đảng đã họp Hội nghị mở rộng và ra đời bản chỉ thị lịch sử: \"Nhật - Pháp bắn nhau và hành động của chúng ta\". Hội nghị xác định: Sau cuộc đảo chính này, đế quốc phát xít Nhật là kẻ thù chính, kẻ thù trước mắt của nhân dân Đông Dương. Khẩu hiệu hành động lúc này là \"Đánh đuổi phát xít Nhật, thành lập chính quyền cách mạng của nhân dân\". Hội nghị cũng đã đề ra nhiều vấn đề cụ thể và các hình thức đấu tranh. Phát động cao trào kháng Nhật để làm tiền đề cho tổng khởi nghĩa và khẳng định dựa vào sức mình là chính. Hội nghị kết thúc ngày 12-3-1945.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '09','03', 'Sự khủng bố khốc liệt của thực dân Pháp đối với phong trào cách mạng và chế độ nhà tù tàn bạo ở Đông Dương đã gây xúc động trong dư luận Pháp. Ngày 9-3-1933, lực lượng tiến bộ mà hạt nhân là Đảng Cộng sản Pháp đã thành lập uỷ ban vận động đại xá tù chính trị Đông Dương. Mục đích của uỷ ban này là nhằm thức tỉnh dư luận Pháp, phản đối chính sách thống trị tàn bạo của bọn thực dân ở thuộc địa. Uỷ ban đã tổ chức mít tinh tại Pari với trên 1.200 người tham gia. Tiếp đó ủy ban đã cử phái đoàn điều tra tới Đông Dương và góp phần tích cực vào chính sách tiến bộ của Chính phủ mặt trận nhân dân Pháp. Tại Sài Gòn cũng đã ra mắt ủy ban Đông Dương vận động đại xá tù chính trị.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','04', 'Ngày 9-4-1288, Ngày chiến thắng Bạch Đằng lần thứ ba. Sau hai lần thất bại lần này quân Nguyên huy động nhiều thuyền chiến hòng uy hiếp quân ta. Đợi khi đoàn quân thuyền giặc lọt hẳn vào trận địa phục kích do Trần Quốc Tuấn bố trí, quân và dân ta từ các ngả trên mặt đất cũng như dưới lòng sông đổ xô ra đánh. Thuyền chiến giặc va vào cọc lim, cái thì vỡ, cái thì đắm. Quân Nguyên rối loạn. Hơn 400 thuyền chiến giặc bị bắt.\nChiến thắng lịch sử Bạch Đằng ngày 9-4-1288 đã giáng một đòn mạnh mẽ vào mưu đồ xâm lược và uy thế của nhà Nguyên. Sau thất bại này Hốt Tất Liệt không sao tổ chức được một đạo quân viễn chinh đủ mạnh để xâm lược Việt Nam một lần nữa. Vì vậy chiến thắng Bạch Đằng là chiến thắng kết thúc cuộc kháng chiến chống quân Nguyên - Mông hồi thế kỷ XIII của dân tộc ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','04', 'Lê Vǎn Hưu là danh sĩ, sử gia đời Trần Thái Tông, quê xã Phủ Lý, huyện Đông Sơn, Thanh Hoá (nay là thôn Phủ Lý Trung, huyện Đông Sơn, xã Thiệu Trung, huyện Thiệu Hoá, tỉnh Thanh Hoá).\nNǎm 1247 ông đỗ bảng nhãn, đến đời Thánh Tông ông sung chức hoạ sĩ Viện Hàn lâm, kiêm giám đốc tu viện Quốc Sử, phụng chỉ soạn bộ Đại Việt sử ký, và đã soạn xong trong nǎm Nhâm Thìn 1272, gồm 30 quyển, chép từ đời Triệu Võ Đế đến đời Lý Chiêu Hoàng (207 trước công nguyên đến 1224).\nÔng mất ngày 9-4-1322, thọ 92 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','04', 'Dưới ánh sáng của bản \"Đề cương vǎn hoá Việt Nam\" - Hội Vǎn hoá cứu quốc đã được thành lập tại Hà Nội tháng 4 nǎm 1943; theo mục tiêu: \"Phải gây ra những tổ chức vǎn hoá cứu quốc và phải dùng những hình thức công khai đặng đoàn kết các nhà vǎn hoá và trí thức...\" Các nhà vǎn Học Phi, Nguyên Hồng, Nguyễn Huy Tưởng, Nam Cao... trở thành hội viên đầu tiên và là nòng cốt cho ngành vǎn hoá vǎn nghệ. Hội đã xuất bản tạp chí Tiền Phong. Việc đoàn kết và lôi kéo các nhà vǎn hoá vào con đường đi của dân tộc, cùng nhân dân đấu tranh Cách mạng. Sau Cách mạng Tháng Tám và trong chín nǎm kháng chiến đội ngũ vǎn nghệ sĩ, trí thức yêu nước đã có nhiều đóng góp xứng đáng. Nhiều người đã hy sinh anh dũng. Nhưng một giai đoạn mới - giai đoạn vǎn hoá cách mạng đã ra đời và tồn tại cho đến ngày nay. Hội Vǎn hoá cứu quốc nǎm 1948 được thay thế bằng Hội Vǎn nghệ Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','04', 'Ngày 9-4-1958, ngày truyền thống binh chủng hoá học.\nSinh ra trong cao trào chống Mỹ cứu nước với lực lượng ban đầu còn ít, bộ đội hoá học đã phải bước ngay vào cuộc đấu tranh quyết liệt chống một kẻ thù hung ác, có trong tay vũ khí giết người hàng loạt có nhiều âm mưu, thủ đoạn xảo quyệt, dã man. Cán bộ, chiến sĩ hoá học đã nêu cao chủ nghĩa Anh hùng cách mạng, mưu trí, sáng tạo, chiến dấu dũng cảm kiên cường, lập nhiều thành tích vẻ vang.\nTừ khi xây dựng và chiến đấu đến nay, binh chủng hoá học đã không ngừng lớn mạnh về mọi mặt, góp phần cùng toàn dân, toàn quân đánh thắng cuộc chiến tranh hoá học ở miền Nam, cuộc chiến tranh phá hoại bằng không quân ở miền Bắc. Bộ đội hoá học đã được Đảng và Nhà nước tặng thưởng nhiều danh hiệu cao quý.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','04', 'Sau 4 ngày hành quân từ Lâm Đồng về khu vực tập kết, sáng ngày 9-4-1975, một bộ phận quân đoàn 4 đã nổ súng tiến công thị xã Xuân Lộc - Cửa ngõ then chốt vào Sài Gòn.\nThị xã Xuân Lộc (tỉnh Long Khánh cũ) nằm trên ba trục giao thông quan trọng: Đường số 1, đường số 20 (Sài Gòn đi Đà Lạt) và đường số 15 (Sài Gòn đi Vũng Tàu).\nĐây là một cửa ngõ quan trọng của Sài Gòn, được quân ngụy xây dựng thành một khu vực phòng thủ trọng yếu trên tuyến phòng thủ cơ bản Biên Hoà - Bà Rịa - Vũng Tàu. Lực lượng địch phòng giữ Xuân Lộc có sư đoàn 18 bộ binh, sư đoàn mạnh nhất của quân đoàn 3 ngụy, một liên đoàn biệt động quân, một trung đoàn thiết giáp, chín tiểu đoàn bảo an của tiểu khu Long Khánh.\nQuyết tâm tiến công Xuân Lộc của ta nhằm phá vỡ tuyến phòng thủ vòng ngoài ở phía Bắc Sài Gòn, tạo thuận lợi tiến công giải phóng Sài Gòn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '09','04', 'Ngày 9-4-1947 là ngày thành lập Uỷ ban Nǎng lượng Nguyên tử quốc tế (AEC).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','05', 'Nhà vǎn Nguyễn Thi còn có một bút danh khác là Nguyễn Ngọc Tấn, sinh ngày 15-5-1928, quê ở huyện Hải Hậu, tỉnh Nam Định.\nÔng sống ở miền Nam từ thuở nhỏ. Nǎm 1946, ông vào bộ đội, chiến đấu tại Nam Bộ, nǎm 1954 tập kết ra Bắc. Nǎm 1962, ông trở lại phục vụ các chiến trường ở Nam Bộ. \nNhiều tác phẩm của ông được bạn đọc yêu thích như các truyện tập ngắn: Trǎng sáng, Đôi bạn, Người mẹ cầm súng, Truyện và ký. Ông hy sinh ngày 9-5-1968 trong cuộc tấn công Tết Mậu Thân. Nơi ông hy sinh với tư thế một chiến sĩ cảm tử nay được mang tên ông - đường Nguyễn Thi, thành phố Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','05', 'Vào ngày 9-5-1959, cái gọi là Quốc hội của chính quyền Ngô Đình Diệm thông qua đạo luật 10-59, đưa máy chém đi khắp miền Nam để tàn sát các chiến sĩ Cách mạng và đồng bào yêu nước.\nChúng thiết lập ba toà án quân sự đặc biệt ở Sài Gòn, Buôn Ma Thuột và Huế.\nToà án này có quyền \"đưa thẳng bị can ra xét xử, không cần mở cuộc thẩm cứu\", nghĩa là có quyền xử tử tại chỗ, công khai tàn sát nhân dân với những cực hình man rợ thời trung cổ. Theo con số ước tính đến nǎm 1959 ở miền Nam có 466.000 người bị bắt, 400.000 người bị tù đày, 68.000 người bị giết hại. Theo số liệu của địch mà ta thu được, trong vòng 10 tháng (từ tháng 7-1955 đến tháng 5-1956) chúng đã bắt, giết 108.000 người. Không khí khủng bố đè nặng lên những thôn ấp ở miền Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','05', 'Ngày 9-5-1971, hàng triệu đồng bào Phật tử ở Sài Gòn và nhiều tỉnh miền Nam đến các chùa để cầu nguyện hoà bình, đòi Mỹ chấm dứt chiến tranh xâm lược.\nTại thành phố Huế có hơn 2 vạn Phật tử tham gia cuộc biểu tình chống Mỹ.\nTại Đà Nẵng, Quy Nhơn có hàng vạn đồng bào xuống đường tuần hành, giương cao các khẩu hiệu đòi hoà bình.\nTại Quảng Ngãi, Cần Thơ, Vĩnh Long, Sóc Trǎng, Mỹ Tho, hàng chục vạn đồng bào, phật tử kéo đến các chùa, hội họp, yêu cầu Mỹ phải chấm dứt chiến tranh xâm lược miền Nam nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','05', 'Ngày 9-5-1972, Mỹ phong toả miền Bắc nước ta lần thứ hai. Chỉ trong vòng 10 ngày, chúng đã rải 43 bãi thuỷ lôi với hàng nghìn quả trên cửa sông, cửa biển, hải cảng, đường hàng hải quốc tế, các khu chuyển tải và vùng ven biển miền Bắc nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','05', 'Ngày 10-5-1972, các chiến sĩ công binh hải quân có mặt kịp thời ở tây bắc Đèn Nơm, cửa Nam Triệu, Hải Phòng. Sau 4 ngày cǎng thẳng, các chiến sĩ đã dũng cảm mưu trí tháo gỡ được quả MK52 đầu tiên. Đây là loại thuỷ lôi mới, hiện đại tinh vi, có sức công phá lớn của địch.\nChính nhờ sự quả cảm của những người đối mặt với tử thần như thế, ngày 18-1-1973, chúng ta đã tuyên bố thông luồng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','05', 'Công trình cầu Thǎng Long được khởi công với sự viện trợ của Trung Quốc, nhưng rồi bị bỏ dở. Trên cơ sở hiệp ước hữu nghị và hợp tác Việt Nam - Liên Xô ký ngày 1-11-1978, Liên Xô nhận viện trợ và giúp Việt Nam xây dựng cầu Thǎng Long.\nQua hơn mười nǎm kể từ ngày khởi công, chiếc cầu lớn hai tầng đồ sộ, cao 14m đã hoàn thành vắt ngang sông Hồng suốt chiều dài hơn 5 km. Như vậy lần đầu tiên những người thợ cầu Việt Nam đã thực hiện những biện pháp thi công hệ trụ cầu với nhiều phương pháp. Một điều quan trọng là ngành cầu Việt Nam đã thực sự trưởng thành. Ta đã có một đội ngũ cán bộ, công nhân lành nghề. Cùng với các công trình khác, cầu Thǎng Long đã đi vào lòng người như những điểm sáng, xứng đáng là công trình thế kỷ của tình hữu nghị Việt - Xô. \nNgày 9-5-1985 cầu Thǎng Long chính thức thông xe và đưa vào sử dụng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '09','05', 'Sau thắng lợi của hồng quân Liên Xô trong chiến dịch công phá Beclin và sau khi quân đội đồng minh gặp nhau bên bờ sông Enbơ, ngày 9-5-1945 đại diện nước Đức bại trận đã phải ký vǎn bản đầu hàng không điều kiện. Đây là chiến thắng vĩ đại của loài người chống chủ nghĩa phát xít mà sự đóng góp và sự hy sinh của nhân dân Liên Xô có ý nghĩa vô cùng quan trọng. Từ đây cục diện thế giới đã nghiêng về phía hoà bình. Chủ nghĩa xã hội đã khẳng định vai trò của mình trên trường quốc tế.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','06', 'Tháng 12-1284, Vua Nguyên sai Thái tử Thoát Hoan đem 50 vạn quân sang xâm lược nước ta, có thêm 10 vạn quân do Toa Đô chỉ huy từ Chiêm Thành đánh ra Nghệ An.\nĐể tránh thế mạnh ban đầu của quân địch, Vua tôi nhà Trần đã tổ chức đánh cầm cự và bỏ kinh thành Thǎng Long vườn không nhà trống, đợi cho chúng khốn khổ vì thiếu lương thực mới phản công địch ở mọi nơi. Với các chiến thắng lẫy lừng ở Chương Dương, Hàm Tử, Tây Kết, Vạn Kiếp... chỉ trong vòng nửa nǎm, quân Nguyên bị đánh tơi bời, phải rút chạy. Các tướng giặc Toa Đô, Lý Hằng, Lý Quán bị chém đầu tại trận, còn Thoát Hoan phải chui vào ống đồng để cho lính khiêng chạy về nước mới thoát chết.\nNgày 9-6-1285, quân và dân Đại Việt về giải phóng kinh thành Thǎng Long, mở hội ca khúc khải hoàn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','06', 'Đúng 11 giờ đêm ngày 9-6-1965, quân giải phóng miền Nam tiến công Đồng Xoài (bắc Biên Hoà), một chi khu quân sự, một mắt xích trong hệ thống phòng tuyến Sông Bé của địch. Quân giải phóng diệt gọn 5 đại đội địch, nhiều xe M.113. Sáng 10-6, Mỹ cho 30 máy bay đổ một tiểu đoàn của lực lượng tổng dự bị xuống phía nam Đồng Xoài. Bọn này bị quân giải phóng bao vây, chia cắt tiêu diệt; Quân giải phóng đã sử dụng 3 M.113 lấy được của địch để tiến công địch. Tính đến ngày 12-6, quân giải phóng đã diệt 1.500 tên địch, trong đó có 50 tên Mỹ, bắn rơi 16 máy bay, phá huỷ đại bác 105 ly và 6 xe bọc thép.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','07', 'Nguyễn Vǎn Cừ sinh ngày 9-7-1912 quê ở thôn Cẩm Giàng, Đồng Nguyên, Từ Sơn, Bắc Ninh. Nǎm 1927 đang học ở trường Bưởi (Hà Nội) do hoạt động trong tổ chức Việt Nam thanh niên Cách mạng đồng chí Hội, ông bị đuổi học. Nǎm 1929 ông được kết nạp vào chi bộ Cộng sản đầu tiên ở Hà Nội. Nǎm 1930 ông làm Bí thư đầu tiên đặc khu Hòn Gai - Uông Bí. Sau đó ông bị bắt, kết án khổ sai đầy ra Côn Đảo. Trong nhà tù ông tranh thủ học tập chủ nghĩa Mác-Lênin, viết báo, và trở thành một cán bộ lý luận xuất sắc.\nNǎm 1036 được trả lại tự do, nǎm 1937 ông được cử vào Ban chấp hành Trung ương Đảng. Nǎm 1939, với cương vị Tổng bí thư, ông chủ trì Hội nghị Trung ương lần thứ VI. Đến tháng 6 nǎm 1940 ông bị bắt tại Sài Gòn. Thực dân Pháp kết án tử hình ông với tội danh: \"chủ trương bạo động\" và là \"Người có trách nhiệm tinh thần trong cuộc khởi nghĩa Nam Kỳ\".\nNgày 28-8-1941, ông bị xử bắn tại Bà Điểm, Hóc Môn. Tại pháp trường ông đã quyết xé tấm bǎng đen bịt mắt và hô lớn: \"Cách mạng Đông Dương thành công muôn nǎm!\" Khi đó ông mới 29 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','07', 'Ngày 9-7-1946, Chủ tịch Chính phủ ký sắc lệnh tổ chức Bộ Quốc gia Giáo dục. Luật sư Vũ Đình Hoè (trong Chính phủ Liên hiệp lâm thời) được cử làm Bộ trưởng.\nBộ Quốc gia Giáo dục có chức nǎng là cơ quan quản lý và tổ chức hệ thống giáo dục trong cả nước. Việc thành lập Bộ Quốc gia Giáo dục có ý nghĩa rất quan trọng trong buổi đầu của Nhà nước dân chủ nhân dân. Bộ Giáo dục và đào tạo ngày nay là tiếp nối chức nǎng nhiệm vụ của Bộ Quốc gia Giáo dục.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','07', 'Hội đồng Chính phủ quyết định thành lập ngành du lịch nước ta ngày 9-7-1960, với tên gọi: Công ty Du lịch Việt Nam. 19 nǎm sau, ngày 23-1-1979, để đáp ứng yêu cầu của sự nghiệp phát triển du lịch trong cả nước, Hội đồng Chính phủ đã quyết định chuyển Công ty du lịch Việt Nam thành Tổng cục Du lịch Việt Nam.\nHai nǎm sau, ngày 17-9-1981 Tổng cục du lịch Việt Nam gia nhập tổ chức Du lịch')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '09','07', 'Ngày 9-7-1968 đã kết thúc trận Khe Sanh. Trận đánh này bắt đầu từ ngày 21-1-1968\nTrong 170 ngày đêm vây hãm địch ở Khe Sanh, lực lượng vũ trang nhân dân giải phóng Bắc Quảng Trị đã:\n- Tiêu diệt gần 17.000 địch (có 13.000 Mỹ)\n- Bắn rơi và phá huỷ 480 máy bay, hàng trǎm xe quân sự, hơn 60 súng lớn, hơn 50 kho xǎng đạn.\n- Thu hàng nghìn súng, hàng trǎm tấn lương thực và đồ dùng quân sự.\nNói về thất bại của Mỹ ở Khe Sanh, hãng thông tin Anh, Roi-tơ, ngày 27-6-1968, viết:\n\"Khe Sanh được ghi vào lịch sử chiến tranh của Mỹ ở Việt Nam như là một nơi phải trả với giá đắt nhất bằng máu\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '09','07', 'Ngày 9-7-1988 Chủ tịch Hội đồng Nhà nước đã công bố luật Quốc tịch Việt Nam. Luật thể hiện mối quan hệ pháp lý giữa nhà nước và công dân, quyền và nghĩa vụ của công dân nước Cộng hoà xã hội chủ nghĩa Việt Nam. Luật có 6 chương, 18 điều. Bao gồm các chương trình như: Xác định quốc tịch Việt Nam; mất quốc tịch, trở lại quốc tịch Việt Nam; Quốc tịch trẻ em khi có sự thay đổi quốc tịch cha mẹ, quốc tịch con nuôi; Thẩm quyền giải quyết các vấn đề quốc tịch...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('4', '09','07', 'Sinh trưởng trong một gia đình luật gia Italia, 9-8-1776, Avôgađô đã được đào tạo để trở thành trạng sư nhưng lòng ham thích khoa học tự nhiên đã khiến ông đột ngột thay đổi đường đời của mình. Nǎm 1806 ông được mời giảng dạy vật lý ở trường Đại học Turin và bắt đầu tiến hành nghiên cứu khoa học có hệ thống.\nAvôgađô là người đầu tiên xác định thành phần định tính, định lượng của các hợp chất. Ông phát minh ra định luật xác định về lượng của các chất thể khí. Định luật này mang tên Avôgađô.\nSự phát triển tiếp theo của lý thuyết phân tử của Avôgađô và nửa sau của thế kỷ 19 đã dẫn đến sự phát triển rõ ràng về khái niệm quan trọng nhất của hoá học: Nguyên tử, phân tử, đương lượng. Ông mất ngày 9-7-1856.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','08', 'Trước tình hình cả nước có chiến tranh, thực hiện chỉ thị của Đảng, đáp lời kêu gọi thiêng liêng của Hồ Chủ tịch, ngày 9-8-1964, Ban Chấp hành Đoàn thanh niên lao động thành phố Hà Nội đã phát động phong trào \"Ba sẵn sàng\" trong đoàn viên và thanh niên. Nội dung của phong trào là: Sẵn sàng chiến đấu và chiến đấu dũng cảm; Sẵn sàng khắc phục khó khǎn đẩy mạnh sản xuất, công tác học tập trong bất kỳ tình huống nào; Sẵn sàng đi bất cứ nơi nào và làm bất cứ việc gì mà Tổ quốc cần đến.\nPhong trào \"Ba sẵn sàng\" của thanh niên Hà Nội là khởi đầu cho phong trào này trong cả nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '09','08', 'Singapo tách khỏi liên bang Malaysia ngày 9-8-1965, và tuyên bố thành lập nước cộng hoà do Iusib Bin Ithac làm tổng thống và Lý Quang Diệu làm thủ tướng. Quốc đảo này có diện tích 639,1km2, dân số gần 3 triệu. Singapo có nền kinh tế phát triển và là một trong nǎm con rồng châu Á. Đặc biệt ở Singapo vấn đề sinh hoạt môi trường được Nhà nước và nhân dân tôn trọng thực hiện. Tiếng Mã Lai là ngôn ngữ chính. Có gần 80 % là người Hoa, ngoài ra còn có người Mã Lai, Ấn Độ v.v...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '09','08', 'Nhà soạn nhạc Liên Xô Đmitơri Sôxtacôvich - một nhạc sĩ tài danh của thế kỷ XX, từ trần ngày 9-8-1975. Sôxtacôvich sinh ở Sanh - Pêtécbua nǎm 1906, là con trai của một kỹ sư và mẹ là nhạc sĩ dương cầm. Ông nổi tiếng trong kỳ thi quốc tế âm nhạc Sô Panh ở Vácsava vào nǎm 1927. Sau đó ông dạy nhạc ở Lêningrat và Mátxcơva. Nhạc khúc giao hưởng đầu tiên của ông là Symphonie Doctoboe đã làm ông nổi tiếng như sóng cồn. Ông còn soạn nhiều nhạc kịch.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','09', 'Ngay sau khi Cách mạng Tháng Tám thành công, ngày 9-9-1945 mạng lưới thông tin vô tuyến quân sự đã được thành lập suốt từ Bắc chí Nam, bảo đảm liên lạc phục vụ Trung ương lãnh đạo chỉ huy quân và dân, bảo vệ chính quyền Cách mạng trong cả nước. Từ đó, ngày 9-9 hàng năm trở thành ngày Hội truyền thống của binh chủng Thông tin liên lạc, một trong những binh chủng kỹ thuật đầu tiên của quân đội nhân dân ta.\nQua hai cuộc kháng chiến chống thực dân Pháp và đế quốc Mỹ, bộ đội thông tin liên lạc đã lớn lên vượt bậc, xứng đáng với lời khen của Hồ Chủ tịch: \"Bộ đội thông tin liên lạc đoàn kết, hiệp đồng, bí mật, an toàn\".\nBinh chủng thông tin liên lạc vinh dự được Quốc hội và Chính phủ tuyên dương là đơn vị Anh hùng lực lượng vũ trang.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','09', 'Ngày 9-9-1952 nói chuyện với hội nghị cán bộ chuẩn bị chiến dịch Tây Bắc, Hồ Chủ tịch đã căn dặn cán bộ nhân dân: \"Làm cách mạng và kháng chiến là việc khó, nhưng quyết tâm thì cũng thành công, quyết tâm không phải ở hội trường, ở lời nói, mà phải quyết tâm trong công tác, hành động\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','09', 'Sáng ngày 9-9-2002, Đại hội đồng Liên minh nghị viện Hiệp hội các quốc gia Đông Nam Á (AIPO) lần thứ 23 đã khai mạc trọng thể tại Hội trường Ba Đình, Hà Nội. Đây là lần đầu tiên kỳ họp AIPO được tổ chức tại Việt Nam dưới sự chủ trì của chủ tịch AIPO Nguyễn Văn An.\nChủ tịch nước Cộng hòa xã hội chủ nghĩa Việt Nam Trần Đức Lương tham dự cuộc họp.\nTham dự Đại hội đồng AIPO-23 có đại diện quốc hội các nước thành viên chính thức, gồm Campuchia, Indonesia, Lào, Malaysia, Philippines, Singapore, Thái Lan và nước chủ nhà Việt Nam. \nĐại diện của Bruney và Myanmar tham dự với tư cách quan sát viên đặc biệt. \nĐại diện các nước Australia, Canada, Trung Quốc, Nghị viện châu Âu, Nhật Bản, Hàn Quốc, New Zealand, Nga, Ban thư ký ASEAN tham dự với tư cách quan sát viên và khách mời.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '09','09', 'Mao Trạch Đông, Chủ tịch Đảng Cộng sản Trung Quốc, đã từ trần ở Bắc Kinh ngày 9-9-1976. Ông sinh năm 1893 tại Hồ Nam trong một gia đình nông dân khá giả.\nThuở nhỏ, Mao Trạch Đông học tại trường Khổng Giáo. Tháng 7-1921 ông là một trong những sáng lập viên lập ra Đảng cộng sản Trung Quốc. Công lao to lớn của ông là tập hợp lực lượng quần chúng nhân dân - mà chủ yếu là giới nông dân nghèo khổ - dưới sự lãnh đạo của đảng cộng sản Trung Quốc, thống nhất được đất nước Trung Quốc rộng lớn. Sau khi thắng Nhật và Quốc dân Đảng, ông thành lập nước Cộng hoà nhân dân Trung Hoa, ngày 1-10-1949.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','10', 'Nữ sĩ Lê Hằng Phương, bút danh Hằng Phương sinh ngày 9-10-1908, quê ở tỉnh Quảng Nam, qua đời năm 1983 ở Hà Nội.\nBà là con gái học giả Lê Dư và là vợ nhà văn Vũ Ngọc Phan.\nSau cách mạng tháng Tám, bà Hằng Phương hoạt động trong Hội phụ nữ cứu Quốc và tham gia kháng chiến chống Pháp, rồi làm việc ở Ban nghiên cứu Văn- Sử- Địa. Thời kỳ chống Mỹ, tuy tuổi đã cao, bà vẫn đến các nơi tuyến lửa.\nNữ sĩ Hằng Phương có các tập thơ đã xuất bản:\n- Hương xuân (năm 1943, in chung với Vân Đài, Anh Thơ, Mộng Tuyết).\n- Mùa gặt (năm 1961)\n- Hương đất nước (năm 1974)\nThơ Hằng Phương êm dịu, ngọt ngào, tình cảm trong sáng, nhân hậu, quan tâm nhiều đến số phận người phụ nữ, lời thơ giản dị, hồn nhiên.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','10', 'Ngày 9-10-1921 đã diễn ra cuộc họp đầu tiên của \"Hội Liên hiệp thuộc địa\" tại Pari. Hội do đồng chí Nguyễn Ái Quốc cùng với một số bạn chiến đấu người Angieri, Tuynidi, Marốc, Mangasơ...sáng lập. Mục đích của Hội là giải phóng các dân tộc thuộc địa. Cơ quan tuyên truyền của Hội là tờ báo LơParia (Người cùng khổ). Hội tập hợp được gần 100 hội viên là những người yêu nước.\n\"Sự ra đời của Hội là một sự kiện chính trị quan trọng đối với các dân tộc bị áp bức. Hội chỉ hoạt động đến 1926 nhưng đã góp phần vào việc xây dựng tình đoàn kết giữa nhân dân các nước thuộc địa. Thông qua Hội, chủ nghĩa Mác Lênin đã được truyền bá đến các thuộc địa.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','10', 'Tháng 10-1930, Ban Chấp hành Trung ương Đảng cộng sản Việt Nam họp tại Hương Cảng (Trung Quốc). Hội nghị đã thông qua Luận cương Chính trị do đồng chí Trần Phú soạn thảo. Luận cương Chính trị đã vận dụng những nguyên lý của chủ nghĩa Mác - Lênin vào hoàn cảnh cụ thể của Việt Nam và Đông Dương, vạch ra con đường Cách mạng chống đế quốc và phong kiến một cách triệt để và toàn diện, đáp ứng những đòi hỏi của phong trào công nhân và phong trào yêu nước của nhân dân Việt nam.\nHội nghị đã bầu đồng chí Trần Phú làm Tổng Bí thư, và quyết định đổi tên Đảng thành Đảng cộng sản Đông Dương nhằm đoàn kết thống nhất các lực lượng yêu nước và Cách mạng của ba dân tộc Việt Nam - Lào - Campuchia trong cuộc chiến đấu chống kẻ thù chung của đế quốc Pháp xâm lược.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','10', 'Ngày 9 - 10 - 1983, tại Hà Nội đã khai mạc Hội khoẻ Phù Đổng toàn quốc lần thứ nhất.\nĐây là đại hội thể dục thể thao cả nước, lớn nhất, đầu tiên của học sinh nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '09','10', 'Ngày 9-10-1967, Ênextô Ghêvara đã hy sinh tại Bôlivia. Ông sinh năm 1928 ở Áchentina\nGhêvara sớm bộc lộ khí chất táo bạo và quả cảm của một nghệ sĩ khao khát tự do, giải phóng cho dân tộc bị áp bức.\nÊnextô Ghêvara đã sát cánh cùng Phiđen Cátxtrô tiến hành cuộc Cách mạng Cuba. Tài năng cộng với tính cách quả cảm của ông lẫy lừng, người dân châu Mỹ la tinh đã đặt tên cho Ghêvara là Chê (thành ngữ Áchentina có nghĩa là bạn thân)\nHoài bão của Chê thể hiện qua những câu nói nổi tiếng: \"Tiến hành kháng chiến đến thắng lợi ở tất cả các nước đang còn nằm dưới ách thống trị của đế quốc, sao cho mỗi nước châu Mỹ la tinh là một Việt Nam\". Chê được tôn vinh là anh hùng du kích.\nSau 30 năm tìm kiếm, tháng 7-1997, hài cốt của Chê đã được đem về đất nước Cu Ba - Tổ quốc thứ hai - Quê hương Cách mạng của ông.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '09','10', 'Tháng 9-1874, đại diện 22 nước họp tại thành phố Bon (Thuỵ Sĩ) đã nhất trí lấy ngày 9-10 hàng năm làm ngày Bưu chính thế giới (gọi tắt là UPU).\nHiện nay có 189 nước tham gia UPU.\nViệt Nam là thành viên của liên minh Bưu chính thế giới từ năm 1951.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','11', 'Bà Triệu (tức Triệu Trinh Nương) hay Triệu Thị Trinh sinh ngày 9-11-226.\nBà là em gái Triệu Quốc Đạt, một hào trưởng lớn ở miền núi Quan Yên thuộc Cửu Chân (tỉnh Thanh Hoá ngày nay). Là người giỏi võ nghệ, cǎm ghét giặc Ngô đô hộ nước ta. Nǎm 19 tuổi, Triệu Thị Trinh cùng với anh tập hợp nghĩa quân, đánh phá các thành ấp của giặc. Mỗi lần ra trận, bà triệu cưỡi voi trắng, mặc áo giáp vàng, đánh đâu thắng đó, khiến giặc Ngô rất lo sợ. Trong một trận đánh ác liệt nǎm 248, Bà Triệu đã hy sinh anh dũng trên núi Tùng (nay thuộc huyện Hậu Lộc, tỉnh Thanh Hoá). Ở đó hiện nay còn lǎng mộ và đền thờ Bà.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','11', 'Ngày 9-11-1831 (nǎm Tân Mão), vua Minh Mạng đổi tên Thǎng Long thành tỉnh Hà Nội, gồm có 4 phủ:\n- Hoài Đức\n- Ứng Hoà\n- Lý Nhân\n- Thường Tín')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','11', 'Nhà vǎn Mạnh Phú Tư, tên thật là Phạm Vǎn Thứ, sinh ngày 9-11-1913 tại tỉnh Hải Dương.\nTrước Cách mạng Tháng Tám, ông là nhà vǎn theo xu hướng hiện thực phê phán. Tuy nhiên những tác phẩm đó chưa vạch rõ mâu thuẫn giai cấp trong xã hội, chưa thấy được sức sống mãnh liệt của nhân dân lao động.\nSau Cách mạng, ngòi bút Mạnh Phú Tư đã phản ánh cuộc chiến đấu của dân tộc. Cuốn \"Rãnh cày nổi dậy\" ghi lại không khí sôi sục của những ngày tháng Tám nǎm 1945 lịch sử của một vùng nông thôn nước ta.\nÔng đột ngột qua đời đầu nǎm 1959.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','11', 'Ngày 9-11-1946, các đại biểu Quốc hội khoá I đã biểu quyết thông qua bản Hiến pháp đầu tiên của nước Việt Nam dân chủ cộng hoà.\nHiến pháp ghi rõ: \"Tất cả quyền lực trong nước Việt Nam dân chủ cộng hoà đều thuộc về nhân dân. Nhân dân sử dụng quyền lực của mình thông qua Quốc hội và Hội đồng nhân dân các cấp do nhân dân bầu ra và chịu trách nhiệm trước nhân dân\".\nSau này, Quốc hội nước ta còn thông qua các Hiến pháp nǎm 1959, 1980, 1992 kế thừa, hoàn thiện và phù hợp với yêu cầu, nhiệm vụ của đất nước trong từng giai đoạn của cách mạng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','11', 'Ngày 9-11-1964 Hồ Chủ tịch đến thǎm đoàn không quân Sao Đỏ. Người cǎn dặn cán bộ chiến sĩ: \"Tổ tiên ta từ xưa đã có những chiến công oanh liệt trên sông, trên biển... Ngày nay, chúng ta phải mở mặt trận trên không thắng lợi, trách nhiệm ấy trước hết là của các chú. Các chú hãy học tập quân giải phóng miền Nam, phát huy lối đánh gần, bám thắt lưng địch mà diệt chúng\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '09','11', 'Clốt Lui Béctôlê (Claude Louis Bertholiet) sinh ngày 9-11-1748 tại Tulông (Pháp). Nǎm 22 tuổi ông tốt nghiệp ngành y và sau đó chuyển tới Pari chuyên tâm nghiên cứu khoa học.\nÔng là người tìm ra nhiều quy trình hoá học áp dụng trong thực tế công nghiệp như: Phương pháp tẩy trắng vải, sáp, bột giấy bằng clo; phương pháp nhuộm...\nÔng mất nǎm 1822.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','12', 'Bà Lê Thị Xuyến sinh ngày 9-12-1909 tại huyện Đại Lộc, tỉnh Quảng Nam, qua đời nǎm 1996 tại Hà Nội.\nTham gia cách mạng từ nǎm 1936, bà vào Đảng Cộng sản Đông Dương nǎm 1947.\nLà cán bộ nữ xuất sắc, có nhiều cống hiến cho sự nghiệp giải phóng phụ nữ và dân tộc, bà Lê Thị Xuyến đã giữ các chức vụ: Hội trưởng Hội liên hiệp Phụ nữ Việt Nam (từ 1946 đến 1956), Phó chủ tịch Hội liên hiệp Phụ nữ Việt Nam (từ 1956 đến 1978), đại biểu Quốc hội từ khoá I đến khoá V, Phó chủ tịch Uỷ ban Mặt trận Liên Việt toàn quốc, Phó chủ tịch Uỷ ban Bảo vệ hoà bình thế giới của Việt Nam.\nBà Lê Thị Xuyến đã được Đảng và Nhà nước tặng thưởng Huân chương Độc lập hạng nhì, Huy hiệu 40 nǎm tuổi Đảng, Huy chương vì sự nghiệp giải phóng phụ nữ, Huy chương vì sự nghiệp Đại đoàn kết dân tộc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','12', 'Ngày 9-12-1920, tại Mátxcơva đã diễn ra Đại hội Xô Viết toàn Nga lần thứ VIII. Tại đại hội, Lênin đã tình bày kế hoạch điện khí hoá toàn quốc, nhằm thúc đẩy quá trình xây dựng nước Nga xã hội chủ nghĩa và củng cố chính quyền Xô Viết. Kế hoạch điện khí hoá toàn quốc, về lý luận và thực tiễn, đều có giá trị cực kỳ quan trọng như chính câu nói bất hủ của Lênin: \"Chủ nghĩa cộng sản là chủ nghĩa chính quyền Xô Viết cộng với điện khí hoá toàn quốc\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','12', 'Ngày 9-12-1956, Hồ Chủ tịch ký sắc lệnh về chế độ báo chí, nhằm đảm bảo quyền tự do ngôn luận của nhân dân và ngǎn cấm những kẻ lợi dụng báo chí hại đến công cuộc đấu tranh cho hoà bình, thống nhất, độc lập và dân chủ của nước nhà.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '09','12', 'Giáo sư, Viện sĩ Nguyễn Khánh Toàn sinh nǎm 1905 ở Nghệ An, qua đời ngày 9-12-1993 tại Hà Nội.\nÔng là một học giả uyên thâm, có kiến thức sâu rộng về các lĩnh vực: sử học, vǎn học, triết học, ngôn ngữ học, dân tộc học, giáo dục học, cùng vốn hiểu biết phong phú về vǎn hoá phương Đông và phương Tây.\nNhững công trình nghiên cứu của Giáo sư trong các lĩnh vực khoa học và tư duy Mác xít sâu sắc. Trong những công trình do chính ông viết hoặc trực tiếp chỉ đạo biên soạn nổi bật tình cảm dân tộc đậm đà, qua sự phân tích truyền thống tốt đẹp, những nhân vật anh hùng, những tài nǎng vǎn hoá lớn của đất nước, những giá trị nhân vǎn của dân tộc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '09','12', 'Giôn Mintơ (Jonh Milton) là nhà thơ Anh thế kỷ 17, sinh ngày 9-12-1608.\nSự nghiệp sáng tác của ông rất phong phú với các tác phẩm như: Niềmvui; Trầm tư; Nhạc kịch thơ Comus.\nTừ nǎm 1652, ông bị mù nhưng vẫn có tác phẩm tuyệt vời: Thiên đường đã mất; Thiên đường lấy lại. Ông được coi là người chủ xướng cải tạo xã hội và là người sáng tạo ra những áng vǎn ưu tú và hùng vĩ nhất trong mọi nền vǎn học thế giới. Ông mất ngày 8-11-1674 thọ 66 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','01', 'Ngày 10-1-1937, đại biểu các xí nghiệp in ở Hà Nội tranh thủ khả nǎng hoạt động công khai, hợp pháp đã tổ chức cuộc họp tại nhà số 181 phố Lò Đúc, Hà Nội để thành lập \"Bắc Kỳ ấn công ái hữu hội\" và nêu thỉnh nguyện 15 điểm với chính phủ Pháp.\nPhong trào đấu tranh của công nhân in ở Hà Nội được công nhân in ở Huế, Đà Nẵng, Sài Gòn hưởng ứng và dần dần lan rộng ra cả nước.\nNgày 10 tháng giêng trở thành ngày truyền thống của công nhân ngành in Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','01', 'Hồ Dầu Tiếng (ở tỉnh Tây Ninh) là một công trình thuỷ lợi lớn được xây dựng từ tháng 4-1981 và hoàn thành vào ngày 10-1-1985.\nMột con đập chính có chiều cao 28 mét, dài 1.200 mét, chân rộng 200 mét và một đập phụ dài 27 kilômét đã làm dòng nước sông Sài Gòn phải dừng lại, tạo nên hồ có sức chứa 1 tỷ rưỡi mét khối nước.\nHồ Dầu Tiếng cung cấp nước cho các tỉnh Tây Ninh, Bình Dương, Bình Phước và thành phố Hồ Chí Minh. Nó còn là một khu du lịch đẹp của nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','01', 'Ngày 10-1-1918 là ngày mất của Đội Cấn, một nhà lãnh đạo nhân dân ở tỉnh Thái Nguyên khởi nghĩa chống thực dân Pháp. Ông bị giết khi đang trên đường làm nhiệm vụ cách mạng. Tên của ông đã được đặt cho một con đường ở Hà Nội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','01', 'Ngày 10-1-1966, thành lập Hiệp hội Vật lý Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','01', 'Ngày 10-1-2005, Hội nghị thường niên lần thứ 13 Diễn đàn nghị viện Châu Á - Thái Bình Dương (APPF-13) do Quốc hội Việt Nam đăng cai tổ chức khai mạc tại Thành phố Hạ Long, tỉnh Quảng Ninh. Chủ tịch Quốc hội Nguyễn Văn An, Chủ tịch APPF-13, chủ trì hội nghị. APPF-13 đã thảo luận và thông qua 26 nghị quyết và tuyên bố chung. Hội nghị kết thúc vào ngày 13-1.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','01', 'Alếchxây Tônxtôi sinh ngày 10 tháng giêng nǎm 1883 tại thành phố Nicôlaiexcơ (nước Nga). Nǎm 1928 ông được tặng thưởng Huân chương Lênin, nǎm 1942 được tặng thưởng Huân chương Lao động Cờ đỏ. Ông còn là nhà hoạt động xã hội tích cực, là Uỷ viên Ban chấp hành Hội Nhà vǎn Liên Xô (trước đây), Viện sĩ Viện Hàn lâm khoa học Liên Xô, đại biểu Xô viết tối cao Liên Xô.\nTônxtôi đã để lại những tác phẩm nghệ thuật kiệt xuất cho vǎn học Nga và nhân loại như tiểu thuyết 3 tập \"Con đường đau khổ\"... Đặc biệt, tác phẩm viết cho thiếu nhi \"Chiếc chìa khoá vàng\" đã làm say mê hàng triệu độc giả nhỏ tuổi trên thế giới.\nCác tác phẩm của A.Tônxtôi thắm đượm tinh thần yêu nước, tư tưởng lạc quan tươi sáng, thể hiện truyền thống vǎn hoá Nga sâu sắc với vǎn phong cổ điển mẫu mực. Ông là người có công lớn trong việc tạo dựng và phát triển thể loại tiểu thuyết sử thi và kịch lịch sử Nga đến độ hoàn mỹ.\nÔng mất ngày 23 tháng 11 nǎm 1945.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','02', 'Hoàng Hoa Thám sinh nǎm 1851, mất ngày 10-2-1913, quê ở Tiên Lữ, tỉnh Hưng Yên, sau dời lên Yên Thế, tỉnh Bắc Giang.\nÔng tham gia chống thực dân Pháp từ nǎm 1895 dưới quyền chỉ huy của Cai Kinh. Sau khi Cai Kinh mất, ông trở thành lãnh tụ của nông dân Yên Thế chống thực dân Pháp. Nghĩa quân do Hoàng Hoa Thám lãnh đạo, chiến đấu liên tục đến nǎm 1913, gây cho Pháp nhiều tổn thất.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','02', 'Alêcxǎng Sécghêvích Puskin sinh nǎm 1799 tại Mátxơva trong một gia đình quí tộc, qua đời ngày 10-2-1837 trong một cuộc đấu súng ở Pêtécbua (nước Nga).\nÔng là nhà thơ cổ điển Nga nổi tiếng, đặt nền móng cho vǎn học hiện thực Nga thế kỷ XIX.\nTừ chủ nghĩa lãng mạn, ông chuyển sang chủ nghĩa hiện thực. Ông cǎm thù chế độ nông nô và ca ngợi tự do. Tập truyện thơ \"Épghêni Ônhêghin\", các tiểu thuyết \"Người con gái viên đại uý\", \"Bôrít Gôđunốp\", \"Con đầm Pích\" là những tác phẩm tiêu biểu của Puskin. Tác phẩm của ông đã được xuất bản bằng hơn 90 thứ tiếng với số lượng hàng trǎm triệu bản.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','02', 'Vinhem Cônrát Rơnghen (Wilhelm Conrad Roentgen), nhà vật lý lỗi lạc Đức, sinh ngày 27-3-1845. Nǎm 1895 ông tìm ra tia X còn được gọi là tia Rơnghen. Tia X sớm được sử dụng trong y học để chuẩn đoán điều trị bệnh và trong công nghệ để làm phương tiện kiểm tra. Việc tìm ra tia X đã giúp cho vật lý nguyên tử tiến một bước quan trọng và có nhiều tác dụng rộng rãi, vững chắc trong đời sống và khoa học. Ngoài ra ông còn là người chuyên nghiên cứu vi hiện tượng áp điện, hoả điện, mối liên hệ tương hỗ giữa nhiều hiện tượng điện và quang trong tinh thể. Ông cũng có nhiều công trình về từ học. Ông mất ngày 10-2-1923.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','02', 'Borít Lêôniđôvích Paxtenắc (Boris Leonidovits Pasternak) nhà thơ, nhà vǎn lớn Nga, sinh ngày 10-2-1890. Thơ ca của ông chứa đựng những cảm xúc dồi dào. Đề tài chủ yếu của ông là con người trong quan hệ với thiên nhiên và lịch sử. Hiện thực Cách mạng đã ảnh hưởng đến một số sáng tác của ông, đặc biệt các bài thơ viết trong thời kỳ chiến tranh vệ quốc.\nCác tác phẩm chính của ông có \"Trên những chiến hào\", \"Chị tôi, đời sống\", \"Mùa Hạ\", \"Trung uý Smit\", \"Tái sinh\", \"Những vần thơ mới\", \"Bác sĩ Givagô\"... Ông được trao giải Noben về vǎn học song rất tiếc vì lý do tế nhị, giải Noben này đã bị từ chối. \nÔng qua đời nǎm 1960. Nǎm 1990 Uỷ ban di sản của ông đã quyết định đề nghị UNESCO tổ chức kỷ niệm lần thứ 100 ngày sinh của nhà thơ trên qui mô toàn thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','02', 'Béctôn Brếch (Bertolt Brecht) sinh ngày 10-2-1898 và từ trần nǎm 1956.\nÔng là nhà soạn kịch Đức, Viện sĩ Viện Hàn lâm nghệ thuật Đức. Nǎm 1951, ông được giải thưởng quốc gia, nǎm 1954 được giải thưởng quốc tế Lênin.\nNhững tác phẩm chủ yếu của ông gồm có các vở kịch: Nhạc kịch baxu, Trong rừng thành phố, Người là người, Mẹ can đảm và các con, Vòng phấn Côcadơ. Cuộc đời Galilê.\nTrong mười nǎm (từ 1956 đến 1966) kịch của Béctôn Brếch đã được trình diễn 1436 lần ở 53 nước trên thế giới. Ông đã có ảnh hưởng đến nhiều nhà soạn kịch hiện đại.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','03', 'Nhà thơ Đông Hồ tên thật là Lâm Tấn Phúc quê ở Hà Tiên (tỉnh Kiên Giang). Ông sinh ngày 10-3-1906 và mất nǎm 1969. Nǎm 1926 khi mới 20 tuổi ông đã lập ra Trí đức học xá nhằm truyền bá vǎn học tiếng Việt. Tác phẩm nổi tiếng của ông là Linh phượng tập lệ ký.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','03', 'Giáo sư Hoàng Xuân Hãn sinh nǎm 1908 nguyên quán xã Đức Nhân, huyện Đức Thọ, tỉnh Hà Tĩnh, từ trần ngày 10-3-1996 tại Pari, Thủ đô Cộng hoà Pháp. Ông là một nhà khoa học, nhà vǎn hoá lớn có tác phẩm chính là Danh từ y học, phương pháp học i tờ, Lịch và lịch Việt Nam, La sơn phu tử... Sau khi ông qua đời, Nhà nước ta đã truy tặng ông Huân chương Độc lập hạng nhì.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','03', 'Trên báo Phụ nữ Tân vǎn (số 122. Ngày 10-3-1932) Phan Khôi đǎng bài Đem một lối thơ mới trình chánh giữa làng thơ và giới thiệu bài thơ Tình già. Sự việc này đã mở màn cho phong trào \"Thơ mới\" và cuộc tranh luận trên diễn đàn vǎn học về vấn đề thơ mới, thơ cũ kéo dài trong nhiều nǎm. Đây cũng là hiện tượng mới mẻ của lịch sử vǎn học cận đại Việt Nam, từ đó xuất hiện các nhà thơ như: Thế Lữ, Lưu Trọng Lư, Nguyễn Nhược Pháp, Xuân Diệu, Huy Cận, Chế Lan Viên, Thanh Tịnh, Hàn Mặc Tử, Nguyễn Bính... Xét về hình thức thơ mới hay thơ cũ chỉ là vấn đề thể loại. Nhưng thực chất là phản ánh cuộc đấu tranh ý thức hệ phong kiến và tư sản.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','03', 'Trước những diễn biến quan trọng của tình hình thế giới mà hoạ phát xít đang ngày một lớn, ngày 10-3-1939, Ban Chấp hành Trung ương Đảng Cộng sản Đông dương đã ra \"Thông báo khẩn cấp\" gửi tới các Đảng bộ. Bản thông cáo chỉ rõ: Cần phải tổ chức các cuộc mít tinh quần chúng phản đối khủng bố của thực dân Pháp và biểu dương lực lượng ủng hộ cách mạng. Bản thông cáo còn đưa ra các khẩu hiệu hành động.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','03', 'Ngày 10-3-1945, sau khi Nhật đảo chính Pháp, Chi bộ Đảng Cộng sản Đông dương xã Trung Màu (huyện Tiên Sơn, tỉnh Bắc Ninh) đã quyết định phát động quần chúng nổi dậy, thành lập ủy ban dân tộc giải phóng. Đây là địa phương đầu tiên giành được chính quyền và duy trì chính quuyền cho đến tổng khởi nghĩa.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','03', 'Sau Cách mạng Tháng tám, ngày 10-3-1946 lần đầu tiên cử tri Thủ đô đi bầu những người đại diện của mình vào Hội đồng nhân dân Thành phố Hà Nội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','03', 'Ngày 10-3-1994, con đường rộng 23m, dài 14km 560, có 6 làn xe, nối từ cầu Thǎng Long đến sân bay Quốc tế Nội Bài đã khánh thành đưa vào sử dụng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','04', 'Ngày 10-4-1887, Nguyễn Phạm Tuân từ trần. Ông sinh nǎm 1842, quê ở Bắc Cử, huyện Lệ Thuỷ, tỉnh Quảng Bình.\nÔng đậu cử nhân thời vua Tự Đức, làm chi phủ huyện Đức Thọ, tỉnh Hà Tĩnh. Nǎm 1885, hưởng ứng chiếu Cần Vương của Vua Hàm Nghi, ông mộ binh chống thực dân Pháp, được phong chức Tán vương quân vụ quan thứ Quảng Bình. Nghĩa quân của ông chiến đấu giỏi, lập nhiều chiến công oanh liệt.\nỞ Hà Nội có một phố mang tên Nguyễn Phạm Tuân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','04', 'Ngày 10-4-1946, Chủ tịch Chính phủ Việt Nam Dân chủ cộng hoà đã ban hành sắc lệnh \"Đảm phụ quốc phòng\" nhằm huy động của cải của nhân dân đóng góp cho nhu cầu kháng chiến chống thực dân Pháp và xây dựng đất nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','04', 'Dù có bận trǎm công nghìn việc, nhưng Bác vẫn luôn luôn dành thời gian cho các cháu nhi đồng một tình cảm đặc biệt thiết tha. Ngày 10-4-1946, bác làm thơ tặng các cháu:\n\"Bác mong các cháu \"cho ngoan\"\nMai sau gìn giữ giang san Lạc - Hồng\nSao cho nổi tiếng Tiên - Rồng\nSao cho tỏ mặt nhi đồng Việt Nam\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','04', 'Ngày 10-4-1957 tại Hà Nội đã tiến hành Đại hội thành lập Hội Liên hiệp Thanh niên thành phố Hà Nội. Đại Hội đã bầu bác sĩ Nguyễn Ngọc San, giảng viên trường đại học y khoa Hà Nội làm chủ tịch Hội.\nĐến nay Hội Liên hiệp Thanh niên Hà Nội hoạt động theo 4 loại hình: Theo đối tượng, theo sở thích, theo nghề nghiệp và theo địa bàn dân cư.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','04', 'Ngày 10-4-1972, Mỹ, Nga và 70 nước khác thoả thuận cấm vũ khí sinh học.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','04', '10-4-1998: Các phe phái chính trị ở Bắc Ailen ký kết hiệp định hoà bình, kết thúc 30 nǎm xung đột.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','05', 'Ngày 10-5-1941, Hội nghị lần thứ tám của Trung ương Đảng họp tại Pác Bó (Cao Bằng) dưới sự chủ toạ của đồng chí Nguyễn Ái Quốc.\nTrên cơ sở phân tích một cách sâu sắc tình hình thế giới và trong nước, Hội nghị đã xác định cuộc \"Cách mạng trước mắt là Cách mạng giải phóng dân tộc, các lực lượng Cách mạng cần tập trung mũi nhọn vào bọn phát xít Nhật - Pháp\". Hội nghị đã phát triển và hoàn chỉnh các nghị quyết của Hội nghị lần thứ sáu và thứ bảy về vấn đề giải phóng dân tộc, chủ trương giải quyết vấn đề giải phóng dân tộc trong khuôn khổ mỗi nước ở Đông Dương và đề ra nhiều chính sách cụ thể nhằm giương cao hơn nữa ngọn cờ giải phóng dân tộc. Theo đề nghị của đồng chí Nguyễn Ái Quốc, Hội nghị quyết định thành lập Việt Minh. Hội nghị đặt vấn đề chuẩn bị khởi nghĩa vũ trang thành nhiệm vụ trung tâm, quyết định thành lập các cǎn cứ địa Cách mạng, xây dựng lực lượng vũ trang và đẩy mạnh mọi công tác để chuẩn bị khởi nghĩa vũ trang. Hội nghị đã bổ xung một số đồng chí vào Trung ương và cử đồng chí Trường Chinh làm Tổng Bí thư.\nHội nghị lần thứ tám của Trung ương có ý nghĩa lịch sử to lớn và có tính chất quyết định đối với thắng lợi của Cách mạng Tháng Tám sau này.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','05', 'Ngày này nǎm 1965, khi tròn 75 tuổi, để chuẩn bị cho cuộc ra đi của mình, Chủ tịch Hồ Chí Minh bắt đầu viết Di chúc để lại cho toàn Đảng và toàn dân ta.\nTrong mấy nǎm chuẩn bị vǎn kiện này, Bác Hồ chỉ giao cho đồng chí thư ký riêng Vũ Kỳ giữ gìn. Cứ đến tháng 5 của các nǎm 1966, 1967, 1968, 1969, Bác lại sửa chữa và viết thêm. Đến ngày 10-5-1969, Bác đã viết xong toàn bộ bản Di chúc lịch sử.\nRất ung dung rất thư thái, Bác Hồ chọn dịp kỷ niệm ngày sinh của mình để viết về ngày ra đi gặp cụ Các Mác, cụ Lênin và các vị Cách mạng đàn anh khác.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','05', 'Ngày 10-5-1969, đại biểu của nhân dân Sài Gòn - Chợ Lớn đã họp đại hội, thành lập chính quyền Cách mạng, Đại hội đã ra quyết định đẩy mạnh việc xây dựng và phát triển cách mạng ở Sài Gòn, Chợ Lớn, bầu ra Ủy ban Nhân dân Cách mạng thành phố, gồm 7 ủy viên do giáo sư Nguyễn Vǎn Chi làm Chủ tịch.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','05', 'Từ đêm mồng 8-5-1972 đế quốc Mỹ đã cho tàu chiến bao vây vùng biển nước Việt Nam Dân chủ cộng hoà, đánh mìn cảng Hải Phòng và đẩy mạnh các cuộc ném bom bắn phá, ngày 10-5-1972 Chính phủ ta đã ra tuyên bố, bản tuyên bố viết \"Đây là một bước leo thang chiến tranh hết sức phiêu lưu và điên rồ, một sự xâm lược cực kỳ trắng trợn của chính quyền Nichxơn, xâm phạm rất thô bạo chủ quyền và lãnh thổ của nước Việt Nam Dân chủ cộng hoà, thách thức láo xược các nước xã hội chủ nghĩa, các nước yêu chuộng hoà bình và công lý, dư luận thế giới và dư luận Mỹ. Hành động côn đồ quốc tế của chính quyền Níchxơn chà đạp hiệp định Giơnevơ nǎm 1954 về Việt Nam và mọi nguyên tắc sơ đẳng của Pháp luật quốc tế, chà đạp quyền tự do hàng hải và tự do thương mại của các nước trên thế giới.\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','05', 'Ngày 10 và 11-5-1994, Hội nghị quốc tế về hang động đã công nhận động Phong Nha (ở tỉnh Quảng Bình) của Việt Nam là động vào loại đẹp nhất thế giới.\nĐộng này cách thị xã Đồng Hới 50km về phía Tây Bắc, nằm ở vùng đá vôi Kẻ Bàng.\nĐộng Phong Nha có chiều dài 7729m, gồm 14 hang. Các động đều do con sông ngầm hoà tan đá vôi mà tạo thành. Các hang ngoài cùng có trần cao hơn mặt nước sông chừng 10m. Các hang trong, trần cao từ 25 đến 40m - từ hang thứ 14, người ta còn có thể theo hành lang hẹp đi vào sâu hơn nữa. Ngay ở cửa hang đã có nhiều nhũ đá rủ xuống. Càng vào bên trong, các cột đá, nhũ đá càng tạo nên cảnh trí huyền ảo hơn có hình dáng như sư tử, rùa, Phật bà v.v... Điều thú vị là muốn đến được hang này, phải ngược dòng sông chừng 30 phút. Trong khi di chuyển, qua làn nước trong xanh, bạn có thể nhìn thấy một \"rừng\" cột đá, tháp đá uy nghi, tráng lệ như cung điện của Long Vương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','05', 'Ngày 10-3-1950, Đại hội toàn quốc của Hội chiến sĩ hoà bình và tự do của Pháp tiến hành ở thủ đô Pari. \nTất cả các đại biểu đã thông qua nghị quyết đòi chính phủ Pháp phải chấm dứt cuộc chiến tranh xâm lược Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','05', 'Ngày 10-5-1760 là ngày sinh của Rugiê. Ông quê tại Lônglơxôniê, miền đông nước Pháp. Ông là tác giả \"Bài hát chiến đấu của quân sông Ranh\" viết vào đêm 25 rạng ngày 26-4-1792. Sau đó ít lâu bài hát này được nổi tiếng với tên gọi là \"Macxâye\" và đến nǎm 1879 thì được tôn làm Quốc ca của nước Pháp cho tới nay.\nRugiê mất ngày 26-6-1836 ở ngoại ô Pari.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','06', 'Học giả Nguyễn Vǎn Tố sinh ngày 10-6-1889, quê ở Hà Nội, qua đời ngày 7-10-1947. Ông là nhà nghiên cứu lịch sử, vǎn hoá, một học gia uyên thâm, có trí nhớ lạ thường. Trước Cách mạng Tháng Tám nǎm 1945, ông làm việc tại trường Viễn Đông bác cổ. Theo sự chỉ đạo của Đảng Cộng sản Đông Dương, ông đứng ra làm Hội trưởng Hội Truyền bá quốc ngữ. Nǎm 1946, ông là Bộ trưởng trong Chính phủ đầu tiên, rồi làm Trưởng ban thường trực Quốc hội khoá I.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','06', 'Chính phủ ta đã ra quyết định thành lập \"Ủy ban Trung ương tiêu diệt bệnh sốt rét\", vào ngày 10-6-1961.\nTrước đó, từ đầu nǎm 1961, toàn miền Bắc đã mở một cuộc tấn công tiêu diệt bệnh sốt rét nhằm xoá bỏ một tại hoạ từ bao đời đã gây hại sức khoẻ của đồng bào ta ở miền núi. Đến nay, việc tiêu diệt bệnh sốt rét vẫn còn là nhiệm vụ quan trọng của ngành y tế và nhân dân ở miền núi nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','06', 'Ngày 10-6-1969, Chính phủ Cách mạng lâm thời Cộng hoà miền Nam Việt Nam họp phiên đầu tiên, dưới sự chủ toạ của chủ tịch Huỳnh Tấn Phát. Chính phủ bàn những biện pháp thực hiện Nghị quyết của Đại hội quốc dân và thông qua chương trình hành động của Chính phủ. Cũng trong phiên họp này, Chính phủ đã ra quyết định về Đoàn đại biểu của chính phủ tại Hội nghị Pari như sau: Đoàn đại biểu Mặt trận dân tộc giải phóng miền Nam Việt Nam tại hội nghị Pari về Việt Nam nay trở thành Đoàn đại biểu Chính phủ Cách mạng lâm thời Cộng hoà miền Nam Việt Nam tại Hội nghị Pari về Việt Nam, do bà Nguyễn Thị Bình, Bộ trưởng ngoại giao Chính phủ Cách mạng lâm thời Cộng hoà miền Nam Việt Nam làm trưởng đoàn và các ông Nguyễn Vǎn Tiến, Đinh Bá Thi làm phó trưởng đoàn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','06', 'Hội nghị thống nhất Hội Liên hiệp phụ nữ toàn quốc, họp tại Hà Nội từ ngày 10 đến 12-6-1976. Hội nghị nhất trí lấy tên cho tổ chức phụ nữ cả nước là Hội Liên hiệp phụ nữ Việt Nam và tán thành danh sách hợp nhất hai ban chấp hành thành Ban Chấp hành trung ương Hội liên hiệp phụ nữ Việt Nam gồm 114 đồng chí, Ban Thường vụ gồm có 30 đồng chí, do bà Nguyễn Thị Định làm chủ tịch Hội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','06', 'Ngày này năm 1994, Việt Nam và Macedonia thiết lập quan hệ ngoại giao.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','06', 'Ampe (André Marie Ampère) là nhà vật lý người Pháp đã sáng lập ra môn \"điện từ học\", sinh ngày 22-1-1775. Ngay từ bé ông đã say mê toán học, vǎn học, triết học và cả thi ca, âm nhạc. Lúc đầu ông dạy các lớp học thêm. Nǎm 1801, ông được cử làm giáo sư vật lý và lao vào các thí nghiệm hoá học và biên soạn khảo luận: \"Nghiên cứu về lý thuyết toán học của trò chơi\".\nNǎm 1805 Ampe làm phụ đạo về toán giải tích ở Đại học Bách khoa tại Pari và sau đó là Tổng thanh tra Đại học, chủ nhiệm bộ môn cơ học. Nǎm 1814 ông được bầu làm Viện sĩ Ban Hình học Viện Hàn lâm khoa học. Ông đã hoàn thành một loạt các chương trình đưa đến lý thuyết đầy đủ về quan hệ giữa điện và từ. Ông đưa ra một định luật của điện từ học (gọi là định luật Ampe). Định luật đã xác định lực giữa hai dòng điện. Ampe còn chế tạo ra dụng cụ đo dòng điện, về sau được hoàn chỉnh, gọi là \"Điện kế\" hay \"Ampe kế\". \nAmpe mất vào ngày 10-6-1836.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','06', 'Loại ô tô Jeep đã xuất hiện cách đây 57 nǎm, ngày 10-6-1940.\nMẫu loại xe này do các kỹ sư của hãng Vynxơ Ôvéclan thiết kế và được sản xuất mạnh mẽ bắt đầu từ nǎm 1941, có sự hợp tác của hãng Ford. Hơn 585.000 chiếc xe Jeep đã được sản xuất trong thời gian chiến tranh (từ 1941 đến 1945).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','07', 'Danh tướng Lý Thường Kiệt sinh nǎm 1019, quê ở làng Bắc Biên, huyện Gia Lâm, Hà Nội, qua đời ngày 10-7-1105. Ông vốn họ Ngô, tên là Tuấn, tự là Thường Kiệt, do có công lớn nên được mang họ Vua - họ Lý.\nNǎm 1075, Nhà Tống chuẩn bị xâm lược nước ta. Thái uý Lý Thường Kiệt cho rằng \"ngồi yên đợi giặc không bằng đưa quân ra trước\". Ông và Tông Đản đã đem quân sang đánh đánh phá các cǎn cứ tập kết lương thực, vũ khí của nhà Tống ở Châu Khâm, Châu Ung, Châu Liêm (Quảng Đông - Quảng Tây - Trung Quốc), rồi chủ động rút quân về nước, lập phòng tuyến chống giặc Tống ở bờ Nam sông Như Nguyệt (sông Cầu).\nCuối nǎm 1076, tướng Quách Quỳ và Triệu Tiết đưa hơn 10 vạn quân Tống vào xâm chiếm Đại Việt. Chúng bị quân và dân ta chặn đánh bên bờ sông Cầu trong hơn hai tháng và đã bị thất bại, phải rút về nước. Chính tại phòng tuyến sông Cầu này, Lý Thường Kiệt đã làm bài thơ được coi như bản \"Tuyên ngôn độc lập\" thứ nhất của nước ta.\nNam quốc sơn hà Nam đế cư\nTuyệt nhiên định phận tại thiên thư\nNhư hà nghịch lỗ lai xâm phạm\nNhữ đẳng hành khan thủ bại hư')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','07', 'Luật sư Nguyễn Hữu Thọ sinh ngày 10-7-1910 trong một gia đình viên chức tại huyện Bến Lức, tỉnh Long An, từ trần ngày 24-12-1996 tại Thành phố Hồ Chí Minh.\nNǎm 1930, ông học luật ở Paris, thủ đô nước Pháp. Đầu những nǎm 50, ông hoạt động trong phong trào trí thức, học sinh, sinh viên đấu tranh phản đối sự can thiệp của Mỹ vào Đông Dương và bị bắt nǎm 1954. Sau khi được trả tự do, ông tham gia phong trào đòi hoà bình thống nhất đất nước. Tháng 12-1960, luật sư Nguyễn Hữu Thọ được bầu làm Chủ tịch Uỷ ban Trung ương Mặt trân dân tộc giải phóng miền Nam (từ 1960-1976). Ông còn được bầu làm chủ tịch hội đồng cố vấn Chính phủ lâm thời công hoà miền Nam. Sau khi đất nước Việt Nam thống nhất, ông được bầu làm Phó chủ tịch Hội đồng Nhà nước nước Cộng hoà xã hội chủ nghĩa Việt Nam, Chủ tịch Quốc hội, Chủ tịch Uỷ ban Trung ương Mặt trận Tổ quốc Việt Nam.\nNguyễn Hữu Thọ là một trí thức lớn, giàu lòng yêu nước, đã hoạt động cách mạng liên tục hơn 50 nǎm, có nhiều công lao lớn đối với sự nghiệp Cách mạng của Đảng và nhân dân ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','07', 'Nguyễn Tuân sinh ngày 10-7-1910 tại Hàng Bạc - Hà Nội. Quê gốc thôn Thượng Đình, làng Nhân Mục (tục gọi Mọc) nay là phường Nhân Chính, quận Thanh Xuân, Hà Nội. Nguyễn học trung học tại thành phố Nam Định. Nǎm 1929 bị đuổi học vì tham gia bãi khoá chống các giáo sư Tây thoá mạ người Việt. Hai lần bị Pháp bỏ tù.\nNguyễn Bắt đầu viết báo, viết vǎn từ đầu nǎm 1930 với các bút danh Ngột Lôi Quật, Thanh Hà, Nhất Lang, Tuân, Nguyễn Tuân v.v... và nổi danh từ nǎm 1938 với \"Một chuyến đi\", và \"Vang bóng một thời\"\nVǎn Nguyễn và đời Nguyễn là cả một pho huyền thoại lấp lánh được người đời dệt thêm lên do lòng ngưỡng mộ. Ngày 28-7-1987, Nguyễn Tuân đã đi vào cõi \"thế giới uỷ\" (chữ dùng của chính ông)')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','07', 'Từ ngày 10 đến ngày 15-7-1948, Hội nghị giáo dục toàn quốc họp tại Việt Bắc. Đây là cuộc họp đầu tiên của ngành trong thời kỳ kháng chiến, Chủ tịch Hồ Chí Minh đã gửi thư tới Hội nghị. Trong thư viết: \"Chúng ta cần phải có một nền giáo dục kháng chiến và kiến quốc\".\nHội nghị đã đánh dấu một bước phát triển mới về nội dung, phương pháp xây dựng nền giáo dục Việt Nam trong điều kiện vừa kháng chiến vừa kiến quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','07', 'Vêniapxki sinh ngày 10-7-1835 tại Lublin, Ba Lan và mất ngày 12-4-1880 tại Mátxcơva. Ông là người biểu diễn viôlông xuất sắc, đồng thời ông còn viết 2 bản côngxéctô và nhiều tác phẩm khác cho cây đàn này. Từ nǎm 1935, ở Ba Lan đã tổ chức cuộc thi viôlông quốc tế thường kỳ mang tên Vêniapxki.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','07', 'Mácsen Prút (Marxel Prust) nhà vǎn hiện đại Pháp, sinh ngày 10-7-1871 tại ngoại ô Pari. Nǎm ông 25 tuổi ông đã in tập sách gồm một số truyện ký và thơ mang tên \"Những thú vui và ngày tháng\". Sau nǎm 1905 một loạt các tác phẩm khác đã ra đời. Đặc biệt phải kể đến là quyển một, bộ tiểu thuyết \"Đi tìm thời gian đã mất\" với nhan đề \"Bên phía nhà Xa Van\" và quyển hai \"Dưới bóng những cô gái tuổi hoa\". Trong 14 nǎm từ 1913 đến 1927 ông hoàn thành bộ tiểu thuyết này gồm 16 tập, tổng công trên 4.000 trang\nCác tác phẩm của ông chứa đầy ám ảnh sợ hãi về một thế giới bạo lực, trần trụi, phi nghĩa, mất nhân tính. Đó là tiếng nói thức tỉnh lương tri nhân loại. Đặc biệt thủ pháp \"dòng ý thức\" trong sáng tạo tiểu thuyết hiện đại thế giới. Ông mất nǎm 1929.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','08', 'Lê Hoàn sinh ngày 10-8-941 tại xã Nhân Lập, huyện Thọ Xuân, tỉnh Thanh Hoá.\nLớn lên, Lê Hoàn đi theo Nam Việt Vương đã lập được nhiều chiến công. Khi Đinh Tiên Hoàng dẹp loạn 12 sứ quân, thống nhất đất nước, lập nên cơ nghiệp nhà Đinh, Lê Hoàn được phong làm Thập đạo tướng quân - lúc tròn 30 tuổi.\nĐinh Tiên Hoàng bị Đỗ Thích giết hại. Đinh Toàn mới 6 tuổi lên làm vua. Nhân cơ hội đó, nhà Tống đưa quân sang xâm lược nước ta. Vì lợi ích của dân tộc. Thái hậu Dương Vân Nga đã trao ngôi vua cho Lê Hoàn. Ông lên ngôi vua, lấy niên hiệu là Lê Đại Hành.\nChỉ trong vòng một tháng, dưới sự chỉ huy của Lê Đại Hành, quân và dân nước Đại Cồ Việt đã phá tan được quân Tống trên mặt trận thuỷ, bộ.\nNǎm 1005 vua Lê Đại Hành từ trần, làm Vua được 25 nǎm, thọ 65 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','08', 'Ngày 10-8-1952, Chủ tịch Hồ Chí Minh đã ký sắc lệnh số 107-SL tặng danh hiệu Anh hùng thi đua ái quốc, gồm có:\n- 4 Anh hùng quân đội: Nguyễn Quốc Trị, Nguyễn Thị Chiên, La Vǎn Cầu, Cù Chính Lan (truy tặng)\n- 3 Anh hùng lao động: Ngô Gia Khảm, Trần Đại Nghĩa, Hoàng Hanh.\nĐây là đợt đầu tiên Nhà nước tặng danh hiệu Anh hùng lực lượng vũ trang nhân dân và Anh hùng lao động cho những chiến sĩ thi đua xuất sắc nhất nước ta')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','08', 'Ngày 10-8-1957, lực lượng vũ trang cách mạng miền Nam tấn công thị trấn Minh Thạnh (Thủ Dầu Một, nay thuộc tỉnh Bình Dương), diệt bọn cảnh sát và quân ngụy, làm chủ thị trấn và trung tâm kinh tế, thu nhiều vũ khí, 10 xe GMC và khá nhiều chiến lợi phẩm, lương thực, tiền bạc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','08', 'Ngày 10-8-1519, một hạm đội gồm 5 chiếc thuyền nhỏ đã rời cảng Xêri thuộc Tây Ban Nha. Hạm đội do Magienlǎng chỉ huy với 256 thuỷ thủ, thực hiện cuộc hành trình vòng quanh thế giới.\nSau khi đi dọc theo bờ biển Nam Mỹ, men theo bờ biển phía đông, Hạm đội đến một eo biển giữa mũi cực nam của đại lục này với đảo Đất Lửa, về sau eo biển này mang tên Magienlǎng. Tiếp theo, đoàn thám hiểm đi vào một đại dương bể lặng sóng yên, Magienlǎng đặt tên là Thái Bình Dương. Sau hơn một nǎm lênh đênh giữa biển khơi với đói khát, bệnh tật, đoàn thám hiểm đến Philipin. Tại đây, đoàn thám hiểm tìm được nhiều hồ tiêu, hương liệu và mặt hàng qúy đối với châu Âu. Nhưng trong cuộc đụng độ với thổ dân, nhiều thuỷ thủ và bản thân Magienlǎng đã bị chết.\nNgày 6-9-1522, đoàn thám hiểm về đến Tây Ban Nha, chỉ còn lại 13 thuỷ thủ, trên tàu đầy ắp hương liệu. Như vậy, Magienlǎng cùng thuỷ thủ đã thực hiện một cuộc hành trình vòng quanh thế giới lần đầu tiên. Cuộc thám hiểm đã khẳng định là Trái đất hình tròn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','08', 'Môlie (Molière Jean Baptiste Poquelin) là nhà viết kịch lớn nhất của nước Pháp sinh ngày 15-1-1622. Sự nghiệp của ông bất tử qua những kiệt tác như \"Đông Joǎng\", \"Anh ghét đời\", \"Táctuýp\", \"Lão Hà tiện\"...\nMôlie không những là người mở đầu vinh quang cho hài kịch cổ điển Pháp, mà còn đưa hài kịch trở thành một thể loại có vị trí xứng đáng trên kịch trường thế giới. Ông lấy cảm hứng trực tiếp từ các hiện trường nghịch lý, đáng cười đang lan tràn trong xã hội. Nhất là trong tầng lớp thượng lưu trưởng giả phong kiến và bọn đội lốt tôn giáo thần quyền. Những nhân vật \"Táctuýp\", \"Đông Joǎng\", \"Ắcpagông\"... đã trở thành hình tượng tiêu biểu trên toàn thế giới để chỉ thói bịp bợm, dâm đãng, tham lam, hà tiện, keo kiệt, được Môlie khái quát từ tầng lớp thầy tu bất chính, phong kiến lỗi thời và bọn tư sản đang lên chứa đầy ung nhọt.\nNgày 10-8-1673, trong đêm diễn vở \"Người bệnh tưởng\" với vai diễn nhân vật chính, Môlie đã kiệt sức, gục ngã và qua đời. Ông đã cống hiến trọn đời mình cho đến phút cuối cùng vì nghệ thuật và khát vọng công bằng, đẹp đẽ của loài người.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','08', 'Gioócdơ Amado là nhà vǎn, nhà hoạt động xã hội nổi tiếng Braxin. Ông sinh ngày 10-8-1912, từng tham gia Mặt trận giải phóng dân tộc và bị giam giữ. Ông sống lưu vong ở nước ngoài, khi trở về tổ quốc được bầu làm nghị sĩ Đảng cộng sản. Nǎm 1951, Amado được giải thưởng Lênin. Nǎm 1961 ông được bầu làm Uỷ viên Hội đồng Hoà bình thế giới, Viện sĩ Viện Hàn lâm Braxin.\nNhững sáng tác phong phú của Amado có thể coi như một bộ biên niên về xã hội Brazin suốt thế kỷ XIX. Với tư tưởng tiến bộ, tác phẩm của ông bộc lộ ý thức phản kháng, đấu tranh vì lẽ phải công bằng. Về nghệ thuật, các tác phẩm của Amado in đậm tính truyền thống, âm hưởng của dân ca và truyện kể dân gian, tái hiện sinh động hình ảnh đất nước Braxin.\nTác phẩm chính: \"Những con đường đói khát\" - tiểu thuyết.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','09', 'Lê Lợi sinh ngày 10-9-1385, tại Lam Sơn, Thanh Hoá. Ông là người cương trực, khảng khái.\nNăm 1946, nhà Minh xâm lược nước ta. Trước cảnh nước mất nhà tan, ông rất đau lòng và nuôi chí diệt giặc, cứu nước.\nNgày 7-2-1418, sau một quá trình chuẩn bị và vận động nhân dân, Lê Lợi được Nguyễn Trãi giúp sức đã phất cờ khởi nghĩa ở Lam Sơn (tỉnh Thanh Hoá). Được đông đảo các tầng lớp nhân dân ủng hộ, cuộc khởi nghĩa đã nhanh chóng chuyển thành chiến tranh giải phóng dân tộc. Cuộc kháng chiến vĩ đại này đã kéo dài 10 năm (từ 1418 đến 1428) và kết thúc thắng lợi, lật đổ ách thống trị của nhà Minh.\nSau khi chiến thắng quân Minh, ngày 29-4-1428, Lê Lợi lên ngôi vua tức Lê Thái Tổ và lập ra triều Lê, đưa chế độ phong kiến Việt Nam lên giai đoạn phát triển thịnh vượng nhất.\nÔng mất ngày 5-10-1433.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','09', 'Khi cuộc kháng chiến chống thực dân Pháp xâm lược kết thúc thắng lợi, để đáp ứng với tình hình và nhiệm vụ mới, Mặt trận Liên Việt được tổ chức thành Mặt trận Tổ quốc Việt Nam.\nĐại hội thành lập Mặt trận tổ quốc Việt Nam họp từ ngày 5 đến 10-9-1955 tại thủ đô Hà Nội. Đại hội đã thông qua cương lĩnh của Mặt trận, chính sách đại đoàn kết toàn dân, vai trò và nhiệm vụ của Mặt trận đối với Cách mạng nước ta trong giai đoạn mới.\nTại đại hội, Chủ tịch Hồ Chí Minh được bầu làm Chủ tịch danh dự của Mặt trận, Cụ Tôn Đức Thắng được bầu làm Chủ tịch Đoàn chủ tịch và 98 uỷ viên đại diện cho tất cả các đoàn thể thuộc nhiều xu hướng chính trị, tôn giáo, thành phần giai cấp khác nhau.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','09', 'Ngày 10-9-1996, Nhà nước ta đã quyết định trao tặng giải thưởng Hồ Chí Minh đợt 1 cho 33 cụm công trình khoa học kỹ thuật và 44 cụm tác phẩm văn học nghệ thuật.\nĐây là một sự biểu dương những cống hiến to lớn của các nhà khoa học, văn nghệ sĩ, trí thức nước ta vào công cuộc giải phóng dân tộc, bảo vệ và xây dựng nước nhà hơn 50 năm qua.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','09', 'Đại hội đồng Liên hiệp quốc khóa 50 đã thông qua Hiệp ước cấm thử hạt nhân toàn diện ngày 10-9-1966\nHiệp ước này quy định: Các quốc gia cam kết không tiến hành hoặc cho phép bất cứ vụ nổ hạt nhân nào ở nơi thuộc quyền kiểm soát và tài phán của mình; không khuyến khích hoặc tham gia dưới bất cứ hình thức nào vào bất cứ vụ nổ hạt nhân nào. Hiệp ước không cấm thử hạt nhân trong phòng thí nghiệm và trên máy vi tính.\nHiệp ước cấm thử hạt nhân toàn diện sẽ có hiệu lực sau khi được tất cả 44 quốc gia được coi là tiềm năng hạt nhân ký và phê chuẩn - nước Việt Nam ta là một trong 44 nước đó.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('1', '10','10', 'Ngày Giải phóng Thủ đô')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','10', 'Chi Lăng là một cửa ải hiểm yếu nằm trên đường từ Lạng Sơn về Đông Quan.\nNgày 10-10-1427, toàn bộ đội quân tiên phong của địch lọt vào trận mai phục và bị tiêu diệt gọn. Tướng nhà Minh là Liễu Thăng bị chém chết bên sườn núi Mã Yên.\nChiến thắng Chi Lăng đã tiêu diệt hoàn toàn 10 vạn quân tiếp viện của nhà Minh. Đây là một chiến thắng oanh liệt, triệt để, có ý nghĩa quyết định thắng lợi của cuộc chiến tranh giải phóng dân tộc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','10', 'Nguyễn Trường Tộ sinh năm 1828 trong một gia đình theo đạo Thiên chúa ở Nghệ An, mất ngày 10-10-1871.\nThuở nhỏ ông học chữ nho, thông minh, hiểu biết hơn người, được gọi là \"Trạng Tộ\". Lớn lên, ông được học thêm tiếng La tinh, tiếng Pháp. Từ năm 1860, được sang Pháp. Ông chú ý quan sát, tìm hiểu sự giàu có, văn minh của họ để tìm cách cứu nước nhà thoát khỏi cảnh nghèo đói, lạc hậu. Từ năm 1863 đến năm 1871, Nguyễn Trường Tộ đã trình lên vua Tự Đức 58 bản hiến kế, thiết tha bày tỏ mong muốn đổi mới đất nước, mong nước ta phải mạnh lên, có nhiều bạn bè để đủ sức đối phó với giặc Pháp.\nNhưng vua quan nhà Nguyễn không chấp nhận các đề nghị canh tân của Nguyễn Trường Tộ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','10', 'Tạ Duy Hiển sinh ngày 10-10-1888 tại Hà Nội và qua đời năm 1966.\nÔng là người đầu tiên ở ngoài Bắc đứng ra lập một gánh xiếc rong của gia đình. Ngày 5-12-1922, đoàn xiếc Việt Nam của ông ra mắt trước công chúng ở rạp hát chợ Hàng Da (Hà Nội). Đây là gánh xiếc đầu tiên của Việt Nam với đầy đủ tiết mục người và thú.\nThành công của gánh xiếc Tạ Duy Hiển đã kích thích sự phát triển của ngành xiếc Việt Nam. Khắp ba miền Trung - Nam - Bắc lần lượt ra đời nhiều gánh xiếc.\nNăm 1958, Tạ Duy Hiển được cử làm trưởng đoàn xiếc nhân dân Trung ương (nay là Liên đoàn xiếc Việt Nam). Ông đã đào tạo hàng loạt nghệ sĩ trẻ nối nghiệp.\nNhà nước đã truy tặng ông Tạ Duy Hiển danh hiệu Nghệ sĩ nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','10', 'Để tăng cường công tác tuyên truyền vận động và chỉ đạo chuẩn bị khởi nghĩa, ngày 10-10-1942 Trung ương đảng Cộng sản Đông Dương cho xuất bản tờ \"Cờ giải phóng\". Tờ báo do Tổng bí thư Trường Chinh trực tiếp phụ trách.\nNội dung tờ báo này đề cập tới các vấn đề chính trị thế giới và trong nước, về sinh hoạt, kinh nghiệm công tác Đảng và nhiều bài có tính chất chỉ đạo phong trào. Báo xuất bản bí mật và không định kỳ. Sau Cách mạng tháng Tám, từ số 16 đến 33 báo xuất bản công khai tại Hà Nội với danh nghĩa là \"Cơ quan tuyên truyền cổ động của Trung ương Đảng cộng sản Đông Dương\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','10', 'Từ sáng sớm ngày 10-10-1954. Nhân dân Thủ đô quần áo chỉnh tề, mang cờ, ảnh Bác Hồ và những bó hoa tươi thắm, thành đội ngũ trật tự theo từng công sở, xí nghiệp, trường học, khu phố... kéo tới những con đường được báo trước là bộ đội hành quân qua.\nĐoàn xe đầu tiên do thiếu tướng Vương Thừa Vũ, Chủ tịch Uỷ ban quân chính và bác sĩ Trần Duy Hưng, Phó chủ tịch Uỷ ban Quân chính Hà Nội dẫn đầu.\n8 giờ: Cánh quân phía Tây xuất phát từ Quần Ngựa. Đó là những chiến sĩ bộ binh của trung đoàn Thủ đô, do Trung đoàn trưởng Nguyễn Quốc Trị, anh hùng quân đội dẫn đầu. Các chiến sĩ diễu binh qua Kim Mã, Hàng Đẫy, Cửa Nam, Hàng Bông, Hàng Đào, Hàng Ngang... Đến 9 giờ 45 tiến vào Cửa Nam, thành Hà Nội.\n8 giờ 45 phút. Cánh quân phía Nam xuất phát từ Việt Nam học xá, tiến qua Bạch Mai, phố Huế, vòng quanh Hồ Gươm rồi vòng lại chiếm lĩnh toàn bộ khu vực Đồn Thuỷ và Đấu Xảo.\n9 giờ 30 : Đoàn Cơ giới và pháo binh xuất phát từ Bạch Mai, qua Phố Huế; 10 giờ 15 đến Bờ Hồ, qua Hàng Đào, Hàng Ngang, Chợ Đồng Xuân, rẽ sang đường Cửa Bắc, tiến vào thành lúc 10 giờ 45 phút.\n15 giờ: Còi Nhà hát thành phố nổi lên một hồi dài. Mấy chục vạn quân dân Hà Nội, đã trang nghiêm dự lễ chào cờ do Uỷ an quân chính tổ chức tại sân vận động Cột Cờ với sự tham gia của các đơn vị quân đội. Sau lễ chào cờ, Chủ tịch Uỷ ban Quân chính Vương Thừa Vũ đã trân trọng đọc lời kêu gọi của Chủ tịch Hồ Chí Minh gửi đồng bào Thủ đô nhân ngày giải phóng.\nMở đầu lời kêu gọi Bác viết: \"Tám năm qua, Chính phủ phải xa rời Thủ đô để kháng chiến cứu nước. Tuy xa nhau, nhưng lòng Chính phủ luôn luôn gần cạnh đồng bào.\nNgày nay do nhân dân ta đoàn kết nhất trí, quân đội ta chiến đấu anh dũng, hoà bình đã thắng lợi, Chính phủ lại trở về Thủ đô với đồng bào. Muôn dặm một nhà, lòng vui mừng không xiết kể\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','10', 'Ngày 10-10-1958 quân ta tiến công quận lỵ Dầu Tiếng (Thủ Dầu Một), diệt và bắt hàng tất cả bọn địch ở đây, sau đó lại đánh tan một tiểu đoàn đến tiếp viện. Các chủ đồn điền người nước ngoài chấp nhận đóng thuế cho Cách mạng. Sau trận này, địch đã rút bỏ 20 đồn lẻ trong quận.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','10', 'Suốt 38 ngày đêm, từ 10-10 đến 18-11-1965 quân giải phóng miền Nam đã liên tục tấn công địch ở Plâyme, một làng nhỏ thuộc tỉnh Gia Lai, cách thị xã Plâycu 40km về phía tây. Quân giải phòng đã liên tục chiến đấu và chiến thắng, diệt 3.000 địch, trong đó có 1.700 Mỹ, một tiểu đoàn Mỹ và 2 tiểu đoàn nguỵ bị tiêu diệt gọn; 88 xe quân sự bị phá huỷ, 44 máy bay bị bắn rơi.\nVới chiến thắng Plâyme, uy thế \"Kỵ binh bay\" của Mỹ bị chôn vùi sau cuộc xuất quân đầu tiên.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','10', 'Liên minh Viễn thông châu Á - Thái Bình Dương (Viết tắt là APT) được thành lập từ năm 1979.\nViệt Nam là thành viên chính thức của APT từ ngày 10-10-1979\nAPT là tổ chức khu vực hoạt động dưới sự bảo trợ của Uỷ ban kinh tế xã hội châu Á - Thái Bình Dương, có mối liên hệ chặt chẽ với các cơ quan, tổ chức của Liên hiệp quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','11', 'Phạm Kiệt tên thật là Phạm Quang Khanh, sinh ngày 10-11-1912, quê ở Quảng Ngãi, qua đời nǎm 1975.\nÔng tham gia Đảng cộng sản Đông Dương từ nǎm 1931. Giữa nǎm đó ông bị giặc Pháp bắt.\nNǎm 1943, ông bị đày đi Ba Tơ (Quảng Ngãi). Tại đây ông cùng các đồng chí tổ chức chi bộ Đảng trong nhà tù, xây dựng lực lượng, chuẩn bị vũ trang.\nTháng 3-1945, Phạm Kiệt đã chỉ huy cuộc khởi nghĩa chiếm đồn Ba Tơ, rồi cũng các đồng chí lãnh đạo nhân dân huyện Ba Tơ nổi dậy giành chính quyền. Ông đã có công xây dựng và phát triển đội du kích Ba Tơ lớn mạnh, là tiền thân của lực lượng vũ trang miền Trung Trung Bộ, góp phần to lớn vào thắng lợi của nhân dân tỉnh Quảng Ngãi trong Tổng khởi nghĩa tháng Tám (nǎm 1945).\nTừ nǎm 1960 đến nǎm 1975, ông Phạm Kiệt lãnh đạo lực lượng công an vũ trang và đã được phong quân hàm Trung tướng nǎm 1974.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','11', 'Trịnh Vǎn Cấn tên thật là Trịnh Vǎn Đạt quê ở huyện Vĩnh Tường, tỉnh Vĩnh Phúc, thường gọi là Đội Cấn.\nÔng là một binh sĩ yêu nước đã cùng với Lương Ngọc Quyến lãnh đạo cuộc khởi nghĩa Thái Nguyên tháng 8-1917. Đội Cấn chỉ huy nghĩa quân chiến đấu rất dũng cảm, nhưng vì lực lượng quá mỏng, phải rút khỏi tỉnh lỵ Thái Nguyên, đem quân về hoạt động ở vùng núi giáp Vĩnh Yên. Đến cuối nǎm sau, trước sự truy lùng của quân Pháp, ông đã tự sát vào ngày 10-11-1918 để giữ trọn khí tiết.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','11', 'Ngày 10-11-1945 từ khắp 4 phương, 437 đại biểu thanh niên và 148 quan sát viên đại diện cho 30 triệu thanh niên của 63 nước trên thế giới đến Luân Đôn họp Đại hội thành lập Liên đoàn Thanh niên dân chủ thế giới. Lá cờ của Liên đoàn với khẩu hiệu \"Thanh niên thế giới đoàn kết lại! Tiến lên vì nền hoà bình lâu dài\" là mục tiêu phấn đấu và là động cơ đoàn kết đấu tranh của thanh niên.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','11', 'Ngày 10-11-1978. Uỷ ban Thường vụ Quốc hội nước Cộng hoà xã hội chủ nghĩa Việt Nam đã ra Quyết nghị: Lực lượng vũ trang nhân dân thành phố Hà Nội được tặng danh hiệu Anh hùng lực lượng vũ trang nhân dân.\nTrong hai lần chống lại chiến tranh phá hoại bằng không quân của đế quốc Mỹ, quân và dân Hà Nội đã bắn rơi 358 máy bay Mỹ các loại và 16 người Hà Nội được Nhà nước tặng danh hiệu Anh hùng lực lượng vũ trang nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','11', 'Phriđrich Silơ (Fricdrich Sehiller) - nhà vǎn, nhà thơ, nhà viết kịch lớn nước Đức, sinh ngày 10-11-1759. Ông học luật, sau chuyển sang ngành y. Tại đại học Y khoa ông đã sáng tác vở kịch \"Những tên cướp\". Vở diễn được nhân dân hoan nghênh. Hai nǎm sau ông viết vở kịch \"Âm mưu và tình yêu\" (1783), và từ đây ông xác lập vị trí vững vàng trên kịch trường.\nTư tưởng trong các tác phẩm của ông thể hiện sự bênh vực nhân dân, động viên nhân dân đứng lên làm cách mạng lật đổ ách phong kiến chuyên chế.\nÔng mất ngày 9-5-1805, khi 46 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','12', 'Chí sĩ Ngô Đức Kế sinh nǎm 1878, quê ở huyện Can Lộc, tỉnh Hà Tĩnh, qua đời ngày 10-12-1929.\nNǎm 1901, ông đỗ tiến sĩ nhưng không ra làm quan, mà liên kết với các nhà yêu nước hoạt động cách mạng. Ông cùng với Đặng Nguyên Cẩn, Lê Vǎn Huân, lập ra \"Triêu Dương thương điếm\" ở Vinh. Có lúc ông dạy học, vận động duy tân, liên hệ với Phan Bội Châu. Nǎm 1908, ông bị bắt đầy ra Côn Đảo, đến nǎm 1921 mới được trả tự do.\nTừ nǎm 1922, Ngô Đức Kế làm chủ bút báo \"Hữu Thanh\" ở Hà Nội, sáng tác thơ vǎn, xuất bản sách tiến bộ, thức tỉnh lòng yêu nước của nhân dân ta.\nCác tác phẩm chính của ông gồm có: Phan Tây Hồ di thảo; Đông Tây vĩ nhân; Thái Nguyên thất thật quang phục ký.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '10','12', 'Từ ngày 10-12-1951 đến ngày 25-12-1952, quân đội ta đã mở chiến dịch Hoà Bình nhằm tiêu diệt sinh lực địch, giải phóng thị xã Hoà Bình, phá phòng tuyến sông Đà và tạo điều mở rộng chiến tranh du kích ở đồng bằng Bắc Bộ.\nTa đã loại khỏi vùng chiến đấu 22.000 tên Pháp - Ngụy, bức hàng, bức rút hơn 1.000 đồn bốt, tháp canh, bắn rơi 3 máy bay, bắn đắm 23 tàu chiến và canô, phá huỷ 17 xe tǎng và bọc thép của địch, giải phóng hơn 2 triệu dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','12', 'Ngày 10-12-1901 lần đầu tiên lễ trao giải thưởng Noben được tổ chức trọng thể tại Xtốckhôm (Thụy Điển). Quỹ giải thưởng được thành lập theo di chúc của nhà hóa học Anphrết Noben (Alfred Nobel) (1836-1896) dành cho các công trình xuất sắc về vǎn học, vật lý, hóa học, y sinh học và giải thưởng hoà bình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','12', 'Ngày 10-12-1983, Tổng thống Raul Alfonsin lên nắm quyền, Argentina trở lại nền dân chủ sau 7 nǎm bị thống trị bởi chế độ độc tài.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '10','12', 'Sau 9 nǎm nghiên cứu, ngày 10-12-1998, các nhà khoa học đã hoàn thành bản sơ đồ gen của loài vật.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','01', 'Ngày 11 - 1 - 1960, hàng nghìn người đang đào nhiều hố ở công viên Bảy Mẫu để trồng cây thì Hồ Chủ tịch đến. Bác chào mọi người rồi nhanh nhẹn cầm lấy xẻng. Một cây đa nhỏ được đặt xuống hố. Bác xúc đất vun cho gốc đa. Trồng xong, Bác nói chuyện với mọi người: \"Ta lao động ở đây là làm cho ta và cho con cháu đời sau. Mấy nǎm trước, nơi đây còn là bãi rác, thế mà nay cây đã lên xanh. Ngày nghỉ, các cô, các chú dẫn con cái ra đây hóng mát, xem hoa, ngắm cây, vui chơi. Đây chính là vườn hoa của các cô, các chú. Vậy chúng ta phải lao động cho thật tốt.\"\nĐó là cây đa thứ nhất Bác Hồ trồng ở Thủ đô Hà Nội trong những ngày đầu của \"Tết trồng cây\" đầu tiên.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','01', 'Ngày 11-1-1973, bộ đội tên lửa, binh chủng đầu tiên của Quân đội nhân dân Việt Nam được Nhà nước tuyên dương đơn vị Anh hùng lực lượng vũ trang nhân dân.\nTrong hai cuộc chiến đấu chống chiến tranh phá hoại của đế quốc Mỹ đối với miền Bắc, bộ đội tên lửa đã bắn rơi 800 máy bay giặc Mỹ. Đặc biệt trong 12 ngày tháng Chạp nǎm 1972, binh chủng tên lửa đã đánh thắng giòn giã, bắn rơi 30 chiếc pháo đài bay B52 (hầu hết rơi tại chỗ).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','01', 'Ngày 11-1-1989, 149 quốc gia đã họp ở Pari để thảo luận và quyết định loại trừ vũ khí hoá học. Cuộc họp thượng đỉnh này gồm 2 khối Đông và Tây cùng đa số các nước thuộc thế giới thứ ba. Trong lời tuyên bố sau cùng, các nước tham gia đồng thanh chống lại việc chế tạo và tàng trữ vũ khí hoá học.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','01', 'Ngày 11-1-1946, thành lập nước Cộng hoà Nhân dân Albania sau khi Quốc vương Zog bị hạ bệ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','01', '11-1-1913, xe ô tô mui kín lần đầu tiên được trưng bày tại Cuộc triển lãm ô tô lần thứ 13 tổ chức tại New York, Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','02', 'Nhà yêu nước Nguyễn Khắc Nhu sinh nǎm 1881, quê ở tỉnh Bắc Ninh. Người đương thời gọi ông là Xứ Nhu. \nTừ nǎm 1927, ông đã cùng với các đồng chí tổ chức tập kích các đồn giặc Pháp ở Bắc Ninh, Đáp Cầu, Phả Lại ... Khi Việt Nam quốc dân Đảng thành lập, ông được mời tham gia và giữ chức Trưởng ban Lập pháp.\nNǎm 1930, ông được phân công trực tiếp lãnh đạo ở Lâm Thao, Hưng Hoá, Việt Trì. Cuộc khởi nghĩa thất bại, ông bị giặc Pháp bắt, đưa về giam tại Hưng Hóa. Tại đây ông Nguyễn Khắc Nhu đã đập đầu vào tường tự vẫn ngày 11-2-1930.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','02', 'Từ ngày 11 đến 19-2-1951 đã diễn ra Đại hội đại biểu toàn quốc lần thứ II của Đảng Cộng sản Đông Dương tại Việt Bắc. Đến dự có 158 đại biểu chính thức và 53 dự khuyết, thay mặt trên 76 vạn đảng viên. Tại hội nghị, Hồ Chủ tịch đọc bản báo cáo chính trị vạch rõ những nhiệm vụ trước mắt của Đảng nhằm đưa kháng chiến đến thắng lợi. Đồng chí Trường Chinh trình bày bản luận cương \"Hoàn thành giải phóng dân tộc, phát triển dân chủ nhân dân tiến tới chủ nghĩa xã hội\". Một quyết định quan trọng của Đại hội là đổi tên Đảng Cộng sản Đông Dương thành Đảng Lao động Việt Nam và Đảng ra công khai hoạt động nhằm tǎng cường sự lãnh đạo của Đảng và đẩy mạnh cuộc kháng chiến mau đến thắng lợi. Đại hội bầu ra BCHTW Đảng do đồng chí Hồ Chí Minh làm Chủ tịch và đồng chí Trường Chinh làm Tổng Bí thư.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','02', 'Khuất Duy Tiến sinh nǎm 1910, quê ở xã Trạch Mỹ Lộc, huyện Phúc Thọ, tỉnh Hà Tây.\nNǎm 1928, ông gia nhập Việt Nam thanh niên Cách mạng đồng chí Hội, hoạt động trong phong trào công nhân ở Nam Định, sau đó vào Đảng Cộng sản Đông Dương. Tháng 3-1930, ông là Tỉnh uỷ viên Tỉnh uỷ Nam Định, tháng 11 nǎm đó là Uỷ viên thường vụ Xứ uỷ Bắc Kỳ. Từ nǎm 1931 đến 1936 và từ tháng 9-1939 đến tháng 3-1945 ông bị đế quốc Pháp cầm tù.\nÔng Khuất Duy Tiến nguyên là Phó Bí thư Thành uỷ Hà Nội. Đại biểu Quốc hội khoá I.\nÔng từ trần ngày 11-2-1984.\nÔng là một chiến sĩ Cộng sản kiên cường, suốt đời tận tụy vì dân vì nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','02', 'Nhạc sĩ Nguyễn Xuân Khoát sinh ngày 11-2-1910, quê ở Hà Nội.\nÔng bắt đầu sáng tác từ trước nǎm 1945, đã viết nhiều tác phẩm, trong đó có những ca khúc nổi tiếng như: Con cò đi ǎn đêm, Mầu thời gian (thơ Đoàn Phú Tứ), Tiếng chuông nhà thờ, Hát mừng bộ đội chiến thắng, Theo lời Bác gọi (thơ Lê Kỳ Vǎn), Ngành y ta đó... Ông còn sáng tác một số tác phẩm khí nhạc như: Trống Tràng Thành (độc tấu pianô), Tổ khúc ông Gióng, Sơn Tinh - Thuỷ Tinh (hoà tấu đàn nhạc), Tiếng pháo giao thừa (bộ gõ dân tộc). \nNguyễn Xuân Khoát là nhạc sĩ thuộc thế hệ đàn anh trong nền âm nhạc Việt Nam hiện đại, là một trong những người đặt nền móng cho việc nghiên cứu âm nhạc chèo, ca trù và âm nhạc dân tộc cổ truyền. Ông là Chủ tịch Hội Âm nhạc Việt Nam trong nhiều nǎm, đã được Nhà nước trao tặng Giải thưởng Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','02', 'Rơnê Đêcáctơ (Rene Descartes), nhà tư tưởng, nhà triết học và người tiên phong trong toán học hiện đại nửa đầu thế kỷ XVII, sinh ngày 31-3-1596.\nÔng là người đã sáng tác ra phép tính vi phân và tích phân, hình học giải tích và nhiều phương pháp toán học khác. Ngoài ra ông còn có những cống hiến về cơ học, thiên vǎn học... Ông sử dụng khoa học để xây dựng hệ thống triết học mới và phê phán triết học kinh viện của giáo hội. Tác phẩm của ông đã bị giáo hội liệt vào \"danh mục sách cấm\". Vì vậy cuộc đời của ông phần lớn thời gian phải sống ở nước ngoài để tránh sự khủng bố của giáo hội. Ông mất ngày 11-2-1650.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','02', 'Kỹ sư Tômớt Envơ Êđixơn (Thomas Alva Edison) nhà phát minh khoa học, sinh ngày 11-2-1847. Nǎm 1864 ông sáng chế ra máy điện báo tự động phát tín hiệu. Sau đó là máy đánh chữ, đĩa hát, máy chiếu hình và bộ phận tiếp sóng của radio. Khám phá của ông về \"Hiệu ứng Edison\" giúp chế tạo ống đèn điện tử hiện đại, làm nền tảng cho máy phát sóng radio, điện thoại đường dài, phim có tiếng động, máy truyền hình, mắt điện tử, tia X... Trong lịch sử khoa học ứng dụng, ông là người chiếm kỷ lục về phát minh (gồm khoảng 1200 bằng phát minh).\nÔng qua đời ngày 18-10-1931.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','02', 'Xécgây Mikhailôvitch Âydanhxtanh đạo diễn sân khấu và điện ảnh nổi tiếng Nga Xô Viết, sinh ngày 10-1-1898. Ông là tác giả của các bộ phim: \"Nhật ký của Glumov\",\"Đình công\". \"Chiếm hạm Potemkin\", \" Tháng Mười\", \"Cái cũ và cái mới\", \"Alexander Nevski\", \"IVan hung bạo\". Ông còn là tác giả của nhiều công trình nghiên cứu lý luận có giá trị như: \"Công tác đạo diễn\", \"Montage\", \"Nguồn cảm hứng\" và tập bút ký ngắn \"Về bản thân\"...\nÔng được tặng danh hiệu nhà hoạt động nghệ thuật công huân CHLB Nga, được phong giáo sư khoa học đạo diễn Trường đại học Điện ảnh Quốc gia Liên Xô, được phong học vị tiến sĩ nghệ thuật.\nÔng mất ngày 11-2-1948.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','03', 'Để tǎng cường lực lượng lãnh đạo giữa lúc phong trào Cách mạng đang lên cao, Đảng chủ trương tổ chức các cuộc vượt ngục để đưa các chiến sĩ Cách mạng đang bị giam giữ trong các nhà tù thực dân trở về hoạt động. Cuộc vượt ngục Đắc Min Buôn Mê Thuột vào ngày 11 tháng 3 nǎm 1943 đã mở đầu cho các cuộc vượt ngục Sơn La, Chợ Chu, Hoà Bình, Bá Vân... Các chiến sĩ Cách mạng vượt ngục thành công đã tǎng cường cho lực lượng cán bộ của Đảng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','03', 'Vào ngày 11-3-1945, ta diệt đồn Bần Yên Nhân (Hưng Yên). Trận đánh do Thường vụ xứ ủy Bắc Kỳ trực tiếp chỉ đạo lực lượng tự vệ chiến đấu địa phương thực hiện. Đồn Bần Yên Nhân nằm trên quốc lộ 5 cách Hà Nội 26 km và là một vị trí quan trọng. Lực lượng tự vệ địa phương đã đánh một trận phối hợp giữa tuyên truyền và sử dụng sức mạnh quân sự. Ta thu toàn bộ vũ khí. Đây là hoạt động quân sự đầu tiên diễn ra ở vùng đồng bằng Bắc Bộ và ngay trên tuyến giao thông huyết mạch nối Hà Nội với Hải Phòng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','03', 'Sau khi Nhật đảo chính Pháp, ngày 11-3-1945, Uỷ ban vận động cứu quốc Ba Tơ quyết định khởi nghĩa. Khi cử đại biểu thương lượng với đồn trưởng BaTơ và bị chúng trở mặt, Uỷ ban lập kế hoạch chiếm đồn. Một bộ phận làm nghi binh làm bụi tung mù, thổi còi bắt chước ô tô. Một bộ phận hô hoán quân Nhật đến. Bọn binh lính và sĩ quan Pháp hỗn loạn bỏ chạy, lính ngụy nộp súng xin hàng. Lá cờ đỏ sao vàng phấp phới bay cao. Uỷ ban Nhân dân Cách mạng được thành lập và tuyên bố các quyền Tự do dân chủ.\nSau 4 ngày nắm chính quyền, ủy ban tổ chức các Hội cứu quốc trong vùng bổ sung lực lượng thành lập Đội du kích Ba Tơ rồi rút lên chiến khu chuẩn bị cho Tổng khởi nghĩa tháng Tám.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','03', 'Ngày 11-3-1951, báo Nhân dân, cơ quan Trung ương của Đảng Lao động Việt Nam, đã phát hành số đầu tiên tại Chiến khu Việt Bắc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','03', 'Ngày 11-3-1954, Hồ Chủ tịch đã gửi thư cho các chiến sĩ ở Điện Biên Phủ, động viên mọi người lập công - Bác Hồ viết:\n\"Các chú sắp ra mặt trận. Nhiệm vụ lần này rất to lớn, khó khǎn nhưng rất vinh quang. \nCác chú vừa được chỉnh huấn chính trị và quân sự, và đã thu được nhiều thắng lợi về tư tưởng và chiến thuật, kỹ thuật. Nhiều đơn vị đã đánh thắng trên các mặt trận. Bác tin chắc rằng các chú sẽ phát huy thắng lợi vừa qua quyết tâm vượt mọi khó khǎn, gian khổ để làm tròn nhiệm vụ vẻ vang sắp tới.\nBác chờ các chú báo cáo thành tích để thưởng những đơn vị và cá nhân xuất sắc nhất.\nChúc các chú thắng to\nBác hôn các chú\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','03', 'Trong cuộc Tổng tiến công và nổi dậy mùa xuân nǎm 1975, một vấn đề có ý nghĩa then chốt đối với thành công của toàn bộ cuộc kháng chiến chống Mỹ cứu nước là chọn hướng tiến công chủ yếu và trận mở màn để đảm bảo thắng lợi chắc chắn, nhanh gọn, giành được yếu tố bất ngờ, tạo được thế chiến lược làm thay đổi toàn bộ cục diện chiến trường.\nBuôn Mê Thuột là vị trí xung yếu của Tây Nguyên. Địch yên trí Buôn Mê Thuột an toàn và không hề nhận biết một dấu hiệu nào bị tấn công. Nhưng đòn chọc sâu bất ngờ đã giáng xuống đầu chúng. Với lối đánh táo bạo điêu luyện, các lực lượng tinh nhuệ của ta đã chiếm sân bay, tiến công sở chỉ huy, chiếm khu trung tâm thông tin và phần lớn thị xã. Trưa ngày 11-3-1975 ta giải phóng Buôn Mê Thuột. Việc giải phóng Buôn Mê Thuột làm ngòi nổ mở màn cho một loạt trận đánh tiếp theo.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','03', 'Hiến Pháp lâm thời còn gọi là Lâm thời ước pháp của Chính phủ Nam Kinh do Tôn Trung Sơn đứng đầu công bố ngày 11-3-1912. Hiến Pháp lâm thời có 7 chương, 56 điều, trong đó quy định chủ quyền của Trung Hoa dân quốc thuộc về toàn thể nhân dân; Nhân dân có quyền tự do cư trú, ngôn luận, xuất bản, lập hội, tín ngưỡng... \nLâm thời ước pháp mang tính chất một bản hiến pháp dân chủ tư sản. Do đó bị Viên Thế Khải phản đối. Nǎm 1914, Viên Thế Khải đã công bố Tân ước pháp thay thế cho Lâm thời ước pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','03', 'Thành lập khối liên minh Việt - Miên - Lào.\nNgày 11-3-1951, Hội nghị đại biểu nhân dân ba nước Việt Nam - Cao Miên - Lào họp.\nSau khi xem xét tình hình thế giới, mối quan hệ nhân dân ba nước Việt - Miên - Lào trong cuộc đấu tranh chống xâm lược của thực dân Pháp, Hội nghị đã ra tuyên ngôn và nghị quyết chung nói rõ ý nghĩa và mục đích việc thành lập khối liên minh nhân dân Việt - Miên - Lào và gây một phong trào ủng hộ khối liên minh đó trong nhân dân ba nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','03', 'Ngày 11-3-1990, Quốc hội Lithuania tuyên bố độc lập khỏi Liên bang Xô Viết.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','04', 'Tại phiên họp thứ 25 của hội nghị toàn thể lần thứ 11 Ban Chấp hành Quốc tế Cộng sản, ngày 11-4-1931 đã ra nghị quyết ghi nhận: \"Đảng Cộng sản Đông Dương trước đây là một chi bộ của Đảng Cộng sản Pháp, từ nay công nhận là một chi bộ độc lập thuộc Quốc tế Cộng sản. Vǎn bản này sau đó đã được thông qua chính thức tại Đại hội VI của Quốc tế Cộng sản. Được thành lập ngày 3-2-1930, Đảng Cộng sản Đông Dương do lãnh tụ Nguyễn Ái Quốc sáng lập đã thể hiện rõ tính giai cấp công nhân và đấu tranh vì lợi ích của dân tộc. Việc Quốc tế Cộng sản công nhận, Đảng Cộng sản Đông Dương là một chi bộ chính thức đã hỗ trợ cho hoạt động của Đảng trên trường quốc tế và khẳng định đường lối của Đảng Cộng sản Đông Dương là đúng đắn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','04', 'Bác Hồ gửi thư tới Đại hội các dân tộc thiểu số miền Nam tại Plâycu (11-4-1946). Bác viết: \"Tiếc vì đường xá xa xôi, tôi không đến được dự hội được. Tôi tuy xa, nhưng lòng tôi và Chính phủ vẫn gần gũi đồng bào.\nNgày nay nước Việt Nam là nước chung của chúng ta. Trong Quốc hội có đủ đại biểu các dân tộc. Chính phủ thì có \"Nha dân tộc thiểu số\" để sǎn sóc cho tất cả các đồng bào. Sông có thể cạn núi có thể mòn, nhưng lòng đoàn kết của chúng ta không bao giờ giảm bớt. Chúng ta quyết góp chung lực lượng lại để giữ vững quyền tự do, độc lập của chúng ta. Xin chúc Đại hội thành công\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','04', 'Để trả đũa các cuộc tiến công nổi dậy của quân và dân ta ở miền Nam, đế quốc Mỹ đã liều lĩnh cho máy bay tàu chiến leo thang đánh phá miền Bắc.\nVới ý chí quyết tâm đánh thắng giặc Mỹ xâm lược, ngày 11-4-1972 chính phủ ta đã ra tuyên bố lên án và bóc trần bộ mặt xâm lược ngoan cố và hiếu chiến của Mỹ. Tuyên bố có đoạn viết: \"Nhân dân Việt Nam quyết đánh, quyết thắng và nhất định sẽ đánh bại hoàn toàn chính sách \"Việt Nam hoá chiến tranh\", không một sức mạnh tàn bạo nào, không một sự đe doạ láo xược nào lay chuyển được quyết tâm của nhân dân Việt Nam chiến đấu vì độc lập, tự do của Tổ quốc\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','04', 'Alếchxanđơ Biuken (Alexander Buchan) sinh ngày 11-4-1829, mất ngày 13-5-1907.\nÔng là nhà khí tượng học người Xcốtlen, là giám đốc các công trình ở đài quan sát Ben Nevis. Sau một chuyến thám hiểm đại dương, ông đã soạn hai báo cáo đặc sắc về khí tượng học và hải dương học.\nBiuken đã cho xuất bản các bản đồ đẳng áp khí quyển và sách giáo khoa về vấn đề này.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','04', 'Ngày 11-4-1957, Anh chấp nhận cho Singapore hưởng quy chế chính phủ tự trị.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','04', '11-4-1991, Hội đồng Bảo an Liên hợp quốc chính thức thông báo kết thúc cuộc chiến tranh Vùng Vịnh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','05', 'Hội nghị Xứ uỷ Bắc Kỳ Đảng Cộng sản Đông Dương được triệu tập vào ngày 11 đến 15-5-1945.\nHội nghị tiến hành nhằm xúc tiến phát động tổng khởi nghĩa. Hội nghị đã khẳng định lại những tinh thần cơ bản của \"Chỉ thị Nhật - Pháp bắn nhau và hành động của chúng ta\", đồng thời vạch ra sách lược đối với quân đồng minh và lực lượng của Pháp Đờ Gôn. Hội nghị cũng đã bầu ra Ban Xứ ủy mới nhằm tǎng cường sức lãnh đạo cho Đảng trong thời đoạn có tính chất quyết định lịch sử.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','05', 'Ngày 11-5-1947, trong cuộc gặp Pômúyt đại diện của cao ủy Pháp Bôla, trước những điều kiện ngang ngược do phái Pháp đưa ra, Chủ tịch Hồ Chí Minh đã nói: \"Lịch sử Việt Nam chúng tôi có những vị anh hùng như Tướng Trần Hưng Đạo 3 lần đánh thắng quân Nguyên, Vua Lê Lợi 10 nǎm chiến đấu chống quân minh xâm lược. Dân tộc Việt Nam chúng tôi có thể chiến đấu 5 nǎm, 10 nǎm hoặc lâu hơn nữa để đi tới thắng lợi. Dân tộc Việt Nam kiên quyết không bôi nhọ lịch sử của mình\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','05', 'Ngày 11 đến 20-5-1951 Đại hội đại biểu Đảng bộ liên khu Việt Bắc lần thứ nhất, có gần 200 đại biểu của 16 tỉnh về dự. Có 3 đảng viên mới được mời là anh Đông Phương Sóc - một chiến sĩ chuyên đánh mìn có nhiều thành tích ở Bắc Ninh và chị Ngô Thị Nọi - một nữ chiến sĩ người Thái ở Yên Bái. Đồng chí Hoàng Quốc Việt đại diện Trung ương Đảng về dự Đại hội.\nĐại hội đã nghiên cứu kỹ chính cương và điều lệ Đảng, nghe báo cáo về tình hình mọi mặt trong liên khu, cǎn cứ vào dự án của liên khu ủy định một chương trình hành động cụ thể cho thời gian tới. Ngày 11-5-1957, Hồ Chủ tịch đã về thǎm nhà máy ô tô \"1-5\". Sau đó 6 nǎm, nhà máy còn được Bác Hồ về thǎm lớp học bổ túc vǎn hoá vào tối 19-12-1963.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','05', 'Trong Đông - Xuân 1964-1965, ta giành thắng lợi lớn và cần tranh thủ đánh nhanh, đánh mạnh để không cho địch kịp hồi phục. Quân ủy miền chủ trương mở đợt hoạt động Xuân - Hè 1965, tiến hành chiến dịch tiến công của chủ lực nhằm mục đích tiêu diệt sinh lực địch , đây là lần đầu tiên ta tập trung lực lượng chủ lực tương đối lớn (4 trung đoàn và 2 tiểu đoàn đặc công) phối hợp với các lực lượng địa phương tiến hành tác chiến trên một khu vực rộng: Hướng chính gồm 2 tỉnh Bình Long và Phước Long; Hướng phối hợp gồm 5 tỉnh Lâm Đồng, Bình Tuy, Long Khánh, Biên Hoà, Bà Rịa. Trong thời gian từ 11-5 đến 22-7-1965 ta đã đánh vào khu vực phòng thủ của địch khá kiên cố của địch với một lượng tương đối lớn. Kết quả ta đã diệt 4 chi khu quân sự của địch, loại khỏi vòng chiến đấu 4459 tên (trong đó có 73 Mỹ) diệt gọn 4 tiểu đoàn, thu 1652 súng các loại, phá huỷ 390 súng, và 60 xe các loại, 60 đầu máy và 1 toa xe lửa, bắn rơi 34 máy bay. Chiến thắng Đồng Xoài đã đẩy quân Ngụy thêm một bước vào nguy cơ sụp đổ và tan rã. Nó cũng chứng minh sự đúng đắn của nhận định chiến trường rừng núi là chiến trường có khả nǎng đánh tiêu diệt và tiêu diệt lớn; có điều kiện hạn chế được chỗ mạnh, khoét sâu được nhược điểm của địch.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','05', 'Ngày 11-5-1969, tại Hà Nội, Hồ Chủ tịch đã gặp gỡ và nói chuyện thân mật với các cán bộ cấp cao toàn quân. Người cǎn dặn phải làm tốt hơn nữa nhiệm vụ của hậu phương đối với tiền tuyến, chú ý xây dựng lực lượng tốt, chất lượng cao, chấp hành các chính sách đối với thương binh, bệnh binh và gia đình có công với kháng chiến.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','05', 'Ốtthô Vôn Guêrích (Otto Von Guericke) qua đời vào ngày 11-5-1686, ông sinh ngày 20-6-1602 tại nước Đức.\nÔng là nhà vật lý kỹ sư, sáng chế máy hút khí, nghiên cứu chân không và vai trò không khí trong sự cháy và hô hấp, chứng minh được là có áp suất trong không khí (nǎm 1654). Guêrích còn là người đầu tiên tạo ra tĩnh điện và thấy sự phát sáng bằng điện (nǎm 1672), và tiên đoán mỗi sao chổi vận hành theo chu kỳ nhấtđịnh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','05', 'Từ ngày 9 đến ngày 11-5-1996 tại Xơun Thủ đô Hàn Quốc đã diễn ra Hội nghị toàn thể lần thứ 7 của Hội Châu Á với sự tham gia của hơn 500 đại biểu chính phủ và giới kinh doanh của các nước thuộc khu vực châu Á - Thái Bình Dương.\nTrong diễn vǎn khai mạc, Tổng thống Hàn Quốc Kim Dâng Xam nói: \"Chủ đề của Hội nghị châu Á phát triển ra toàn cầu được nêu rất đúng lúc và kịp thời vì nó phản ánh không những xu hướng phát triển mà cả mối quan tâm chính gần đây ở châu Á\".\nBộ trưởng Công nghiệp nước ta Đặng Vũ Chư đã phát biểu tại hội nghị. Bộ trưởng giới thiệu công cuộc đổi mới thành công ở Việt Nam trong 10 nǎm qua và tình hình kinh tế Việt Nam hiện nay đang phát triển theo hướng công nghiệp hoá và hiện đại hoá, mở cửa tǎng cường hợp tác với các nước trong khu vực và trên toàn thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','06', 'Đồng chí Phạm Hùng tên thật là Phạm Vǎn Thiện, sinh ngày 11-6-1912 ở làng Long Hồ, huyện Châu Thành, tỉnh Vĩnh Long, mất ngày 10-3-1988.\n16 tuổi, đồng chí đã tham gia cách mạng, bị thực dân Pháp giam ở nhà tù Côn Đảo trong 14 nǎm (từ 1931 đến 1945).\nĐồng chí Phạm Hùng đã giữ nhiều trọng trách của Đảng và Nhà nước. Từ nǎm 1956 đến khi từ trần, đồng chí liên tục là Ủy viên Bộ Chính trị Trung ương Đảng, Bí thư Trung ương Cục miền Nam (từ nǎm 1967 đến 1975); nhiều nǎm là Phó Chủ tịch Hội đồng Bộ trưởng nước Cộng hoà xã hội chủ nghĩa Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','06', 'Nhân dịp kỷ niệm 1000 ngày kháng chiến toàn quốc, ngày 11-6-1948, Chủ tịch Hồ Chí Minh đã ra lời kêu gọi chính thức phát động cuộc vận động thi đua ái Quốc. Người nói:\n\"Bổn phận của người dân Việt Nam, bất kỳ sĩ, nông, công, thương, binh, bất kỳ làm việc gì đều cần phải thi đua: làm cho mau, cho tốt, làm cho nhiều. Mỗi người Việt Nam, bất kỳ già, trẻ, trai, gái, bất kỳ giàu, nghèo, lớn, nhỏ đều phải trở nên một chiến sĩ đấu tranh trên một mặt trận: Quân sự, kinh tế, chính trị, vǎn hoá. Thực hiện khẩu hiệu:\n\"Toàn dân kháng chiến\nToàn diện kháng chiến\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','06', 'Ngày 11-6-1963, trong cuộc tuần hành của trên 1.000 vị tǎng ni và giới lạnh đạo Giáo hội Phật giáo Việt Nam cùng một số đông đảo đồng bào yêu nước chống chế độ độc tài Ngô Đình Diệm, Nguyễn Vǎn Khiết (tức Bồ Tát Thích Quảng Đức) đã phát nguyện tự thiêu, đòi bình đẳng tôn giáo, chống đàn áp Phật giáo và đòi dân sinh, dân chủ.\nCuộc tự thiêu diễn ra giữa ngã tư đường Phan Đình Phùng và Lê Vǎn Duyệt (nay là đường Nguyễn Đình Chiểu và Cách Mạng Tháng Tám). Từ trên xe, Bồ Tát Thích Quảng Đức ung dung bước xuống, tĩnh toạ ngồi và tự tay châm lửa vào thân. Ngọn lửa bốc cao phủ kín thân hình, ông vẫn ngồi thẳng lưng. Sau 15 phút lửa tàn, ông gật đầu rồi nằm ngã ngửa giữa sự chứng kiến của đông đảo đồng bào và ký giả báo chí trong và ngoài nước.\nCái chết bi hùng của Thích Quảng Đức là một trong những nỗi kinh hoàng đối với chế độ Ngô Đình Diệm hồi đó. Hiện nay, tại ngã tư Nguyễn Đình Chiểu và Cách Mạng Tháng Tám vẫn còn tượng đài kỷ niệm ông.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','06', 'Ngày 11-6-1992, Việt Nam và Cộng hoà Mônđôva đã thiết lập quan hệ ngoại giao giữa hai nước.\nMônđôva là thành viên cũ của Liên Xô, nằm ở phía đông châu Âu, có diện tích 33.760km2, số dân 4.500.000 người, thủ đô là thành phố Kisinốp.\nCuối nǎm 1991, Mônđôva tuyên bố độc lập và được kết nạp vào Liên hiệp quốc từ nǎm 1992.\nMônđôva là nước công nghiệp phát triển. Bình quân thu nhập theo đầu người được 4.000 đô la Mỹ một nǎm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','07', 'Ngày 11-7-1950, Chủ tịch Hồ Chí Minh ký sắc lệnh thành lập Tổng cục Cung cấp (sau là Tổng cục Hậu cần) với nhiệm vụ quản trị, trang bị, cấp dưỡng quân đội và sản xuất quốc phòng. Đồng chí Trần Đǎng Ninh được cử làm Chủ nhiệm. Từ đó, ngày 11-7 trở thành truyền thống bộ đội hậu cần.\nTại Hội nghị cung cấp toàn dân ngày 22-6-1952, Chủ tịch Hồ Chí Minh đã dạy: \"Đối với chiến sĩ phải sǎn sóc họ làm sao cho họ đủ ǎn, đủ mặc, đủ súng, đủ thuốc... Các chú phải làm thế nào một bát gạo, một đồng tiền, một viên đạn, một tấc vải phải đi thẳng đến chiến sĩ\". Tuân theo lời dạy của Người, trải qua bao thời kỳ, ngành hậu cần quân đội luôn là bộ phận trong của lực lượng vũ trang và hoàn thành tốt nhiệm vụ bảo đảm vật chất phục vụ quân đội. Nhiều đơn vị và cán bộ chiến sĩ hậu cần được tuyên dương là Anh hùng lực lượng vũ trang nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','07', '11-7-1959, ngành giao thông vận tải bắt đầu xây dựng đường sắt Hà Nội - Quán Triều, và ngày 30-8-1960 tổ chức thông tuyến và đưa vào sử dụng.\nTuyến đường sắt này dài 76 km, qua các ga Long Biên, Gia Lâm, Cổ Loa, Đông Anh, Đa Phúc, Trung Giã, Thái Nguyên. Nối khu công nghiệp gang thép Thái Nguyên với Hà Nội và cả nước. Tuyến đường sắt đã đảm bảo vận chuyển phục vụ sản xuất và đời sống nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','07', 'Anphrết Đơ Muyxê (Alfred de Musset) là nhà thơ nổi tiếng nhất thuộc thế hệ những lãng mạn trẻ nước Pháp. Ông sinh ngày 11-7-1810. Nǎm 20 tuổi ông ra đời tập thơ đầu tay \"Chuyện Tây Ban Nha và Italia\" gây tiếng vang lớn trong thi đàn lãng mạn Pháp.\nĐương thời công chúng châu Âu biết đến Muyxê như một nhà thơ tình yêu lớn bên cạnh Víchto Huygô. Đối với 24 vở kịch lớn nhỏ của ông, nhân dân Pháp và thế giới tôn vinh và \"chào đón như một kiệt tác chân chính nhất của nền sân khấu Pháp\". Trong số này kiệt xuất nhất phải kể đến vở \"Lôzǎnzacciô\", \"Fantaziô\", \"Không đùa với tình yêu\" và tập truyện \"Tâm sự của một đứa con thời đại\". Ông mất ngày 2-5-1857, với lời di nguyện: \"Các bạn ơi, đến khi nào nhắm mắt, trên mộ tôi các bạn hãy trồng một cây hồng có lá cành nho nhỏ\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','07', 'Ngày 11-7-1987, lúc 6 giờ 35 phút (giờ GMT), chú bé người Nam Tư tên là Emmatêgiơ Gaxpa ra đời và được coi là công dân thứ 5 tỷ của hành tinh.\nĐến tháng 11-1989, diễn đàn dân số thế giới đã quyết định lấy ngày sinh của em - ngày 11-7 hằng nǎm làm \"Ngày dân số thế giới\" nhằm nhắc nhở các quốc gia và mỗi người sống trên trái đất về nguy cơ dân số tǎng quá nhanh, con người không đủ điều kiện sống với đầy đủ quyền chính đáng như: quyền học hành, có việc làm, đủ dinh dưỡng, nhà ở và bảo vệ sức khoẻ.\nTheo dự báo của Liên hiệp quốc, vào cuối thế kỷ này số dân vượt quá 6 tỷ, nǎm 2025 là 8 tỷ 520 triệu người, nǎm 2050 là 10 tỷ người.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','08', 'Ngành vận tải đường sông nước ta được thành lập ngày 11-8-1958.\nTrong những nǎm chống đế quốc Mỹ, ngành đã kiên cường bám luồng, bám phương tiện với phương châm \"Địch đánh ta cứ đi, mở luồng mà tiến, đánh giặc mà đi\". Ngành đã tích cực tham gia vận chuyển lương thực, vũ khí, thuốc men và đưa bộ đội vượt sông, công đồn, diệt giặc. Trong xây dựng hoà bình, ngành vận tải đường sông đã góp công chở nhiều thiết bị máy móc, vật liệu để xây dựng các công trình lớn nước ta.\nNǎm 1996, Nhà nước đã tặng Huân chương Độc lập hạng nhì cho ngành vận tải đường sông.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','08', 'Từ ngày 11 đến 17-8-1969, nhằm tiến công vùng giải phóng của ta và thực hiện kế hoạch \"phòng ngự từ xa\", giặc Mỹ và tay sai điều động liên tỉnh Bình Long và Tây Ninh hơn 20 tiểu đoàn và hơn 500 xe quân sự.\nQuân và dân Bình Long, Tây Ninh đã phối hợp tiến công dồn dập 80 trận vào 40 mục tiêu, cǎn cứ sào huyệt kiên cố của Mỹ Ngụy, gây cho địch những thiệt hại lớn: hơn 6000 tên địch (có hơn 3.500 tên Mỹ) bị diệt và bắt, 530 xe quân sự (có 45 xe tǎng và xe bọc thép) bị phá huỷ, 70 máy bay bị bắn rơi và phá hỏng, hàng chục kho tàng quân sự bị đốt cháy.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','09', 'Nữ sĩ Đoàn Thị Điểm sinh năm 1705, người làng Giai Phạm, huyện Mỹ Văn (tỉnh Hưng Yên) và qua đời ngày 11-9-1746.\nBà là con Đoàn Doãn Nghi, em Đoàn Doãn Luân, hai người đều đỗ Hương Cống, nhưng không ra làm quan chỉ ở nhà dạy học.\nBản thân Đoàn Thị Điểm là phụ nữ có dung sắc đẹp đẽ, có học vấn. Chồng bà là Tiến sĩ Nguyễn Kiều đã từng ca ngợi bà \"tài năng nương tử, xưa hiếm nay không, xuất khẩu thành chương, thông minh bẩm chất\". Hai vợ chồng bà sống hạnh phúc nhưng bà mất sớm.\nNgoài bản dịch \"Chinh phụ ngâm\" (nguyên văn chữ Hán của Đặng Trần Côn), bà còn viết tập truyện chữ Hán \"Truyện kỳ tân phả\" và nhiều thơ phú khác.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','09', 'Đại hội lần thứ 6 của Quốc tế cộng sản họp từ ngày 17-7 đến 11-9-1928 đã thông qua bản \"Đề cương về Cách mạng ở các nước thuộc địa và nửa thuộc địa\".\nBản đề cương nêu rõ tính chất của cuộc cách mạng là \"phải tiến hành cuộc cách mạng dân chủ tư sản\". Nhiệm vụ cơ bản của cuộc cách mạng đó là \"Giải phóng đất nước khỏi chủ nghĩa đế quốc; tổ chức Xô Viết công nông, lập chuyên chính công nông củng cố quyền lãnh đạo của giai cấp vô sản...\"\nBản đề cương đã góp phần lớn vào việc phát triển phong trào Cách mạng ở Việt Nam. Là một yếu tố góp phần thúc đẩy nhanh thêm quá trình chuẩn bị thành lập Đảng cộng Sản Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','09', 'Từ ngày 11 đến 16-9-1961, đã diễn ra Hội nghị Thuỷ lợi toàn miền Bắc. Hội nghị tổ chức tại Hưng Yên, một trong những tỉnh có nhiều thành tích về công tác thuỷ lợi và biện pháp cụ thể nhằm đưa công tác thuỷ lợi tiến lên phục vụ tốt kế hoạch 5 năm phát triển nông nghiệp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','09', 'Sáng 11-9-2001, một sự kiện đã gây chấn động nước Mỹ.\nMáy bay của hàng không dân dụng Mỹ đã bị cướp và đâm vào toà tháp đôi nổi tiếng của Trung tâm Thương mại')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','10', 'Thực hiện chỉ thị của Xứ uỷ Bắc Kỳ, Thành uỷ Hà Nội phát động một chiến dịch ủng hộ phong trào Xô Viết Nghệ Tĩnh, hàng vạn truyền đơn đã được phân phát ra khắp thành phố. Ngày 11-10-1930, lúc công nhân đi làm về và học sinh các trường cũng bắt đầu hết giờ học, đội thanh niên xung phong tập hợp hàng trăm người tại phố Sinh Từ (nay là phố Nguyễn Khuyến), phân phát truyền đơn, diễn thuyết hô hào nhân dân ủng hộ công nhân và nông dân Nghệ An và Hà Tĩnh dưới nhiều hình thức đấu tranh như bãi công, đòi tăng lương, bớt giờ làm việc... Cảnh sát, mật thám ập tới khủng bố và bắt đi hai đội viên song phong trào ủng hộ Nghệ Tĩnh vẫn tiếp tục. Khẩu hiệu \"Phản đối đế quốc chủ nghĩa thảm sát nông dân Nghệ Tĩnh\" luôn được nêu cao.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','10', 'Sáng ngày 11-10-1962, Mỹ - Diệm mở chiến dịch \"Sao Mai\" nhằm càn quét vào vùng từ Long An đến Tây Ninh lưu vực sông Vàm Cỏ Đông, vòng cung phía tây nam và tây bắc Sài Gòn.\nLực lượng vũ trang giải phóng đánh trả quyết liệt, các máy bay lên thẳng đều trúng đạn của du kích. Đồng ruộng tuy rộng nhưng nơi nào địch có thể xuống, ta đều cắm đầy cọc tre vót nhọn 4 đến 5 mét, làm cho bọn lính dù và phi công khiếp sợ. Đường sá bị nhân dân đào phá, các đoàn xe chở lính đều bị tắc nghẽn, 6000 quân địch bị dồn ứ lại trong một vòng đất hẹp, tiếp tế và vận tải đều khó khăn. Sau nhiều ngày bị vây hãm trong đồng lầy, nước ngập lại bị tập kích liên tiếp, ngày 18-10-1962, quân địch phải tháo chạy.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','10', 'Ngày 11-10-1803 ông Gácnơranh (sinh năm 1769, mất năm 1803) người Pháp đã đăng ký phát minh dù.\nTrước đó, chính Gácnơranh đã thực hiện cú nhảy dù thực sự đầu tiên vào ngày 22-10-1797. Ông bay trên một khí cầu của công viên Mútxô ở Pari, khi đạt độ cao khoảng 800m, ông đã cắt dây treo giỏ của kinh khí cầu, Chiếc giỏ treo trên một cái dù từ từ hạ xuống.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','10', 'Giêmxơ Prexcốt Giun (James Precott Jun) sinh năm 1818 qua đời ngày 11-10-1889.\nÔng là nhà vật lý nổi tiếng người Anh đã dùng thực nghiệm để tìm ra luật bảo toàn và chuyển hoá năng lượng.\nGiun đã cùng với các nhà bác học Nga Lenxơ tìm ra định luật xác định tác dụng nhiệt của dòng điện\nNgười ta đã lấy tên ông Giun đặt cho đơn vị công (Ký hiệu là J).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','11', 'Nhà vǎn Trần Đǎng tên thật thật là Đặng Trần Thi, sinh ngày 11-11-1921, quê ở xã Tây Tựu, huyện Từ Liêm, Hà Nội, học trung học và đại học luật khoa ở Hà Nội.\nSau Cách mạng Tháng Tám, Trần Đǎng làm việc trong Ban Liên kiểm Pháp - Việt và bắt đầu viết vǎn. Kháng chiến toàn quốc, ông tham gia quân đội, làm phóng viên báo Vệ Quốc quân.\nLà một trong những người cầm bút đến với bộ đội sớm nhất, Trần Đǎng đã đưa vào vǎn học những hình ảnh giản dị, chân thực, tươi trẻ của người chiến sĩ và cuộc sống gian khổ của họ.\nSáng tác của ông tuy ít nhưng đã góp phần những thành tựu đầu mùa và khẳng định con đường đi đúng đắn của vǎn học những nǎm kháng chiến chống Pháp.\nCác tác phẩm chính: Truyện ngắn \"Một lần tới thủ đô\" (1946), ký sự \"Trận Phố Ràng\" (1949) và \"Một cuộc chuẩn bị\" (1949).\nNhà vǎn Trần Đǎng hy sinh ở chiến trường biên giới phía Bắc cuối nǎm 1949.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','11', 'Ngày 11-11-1924, đồng chí Nguyễn Ái Quốc đến Quảng Châu (Trung Quốc) để xây dựng phong trào Cách mạng Việt Nam.\nTrước đó, tại Quảng Châu, Phạm Hồng Thái đã mưu sát viên toàn quyền Đông Dương MécLanh nhưng không thành công. Về sự kiện này đồng chí Nguyễn Ái Quốc đã nhận xét: \"Việc đó tuy nhỏ nhưng nó báo hiệu bắt đầu thời đại đấu tranh dân tộc, như chim én nhỏ báo hiệu mùa xuân\".\nTại Quảng Châu đồng chí Nguyễn Ái Quốc đã gặp và trao đổi với cụ Phan Bội Châu và báo cáo kết quả cuộc gặp đó lên Đoàn Chủ tịch Quốc tế cộng sản.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '11','11', 'Ngày 11-11-1945, Ban chấp hành Trung ương Đảng họp và ra Nghị quyết về việc tuyên bố \"Tự giải tán\" Đảng cộng sản Đông Dương.\nThông báo của Hội nghị này chỉ rõ: Việc tuyên bố Đảng \"tự giải tán\" là \"để phá tan tất cả những điều hiểu lầm ở ngoài nước và ở trong nước có thể trở ngại cho tiền đồ giải phóng nước nhà \". Những người muốn nghiên cứu về chủ nghĩa Mác-Lênin \"sẽ gia nhập Hội nghiên cứu chủ nghĩa Mác ở Đông Dương\".\nĐảng cộng sản Đông Dương tuyên bố \"Tự giải tán\" nhưng thực chất là rút vào hoạt động bí mật.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','11', 'Pie đơ Rôngxa (Pierre de Ronsand) là nhà thơ Pháp thời phục hưng, sinh ngày 11-11-1524.\nÔng là nhà cách tân thơ ca lớn, là nhà thơ ca Pháp thoát khỏi nghệ thuật thi ca Trung cổ, bước vào thời cận đại, đưa vào thơ những tư tưởng nhân vǎn hướng tới con người. Ông cũng là người mở đường cho thơ ca lãng mạn Pháp sau này.\nÔng mất ngày 27-12-1585.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '11','11', 'Phêđô Mikhailôvích Đôstôiepxki - nhà vǎn vĩ đại, nhà tâm lý thiên tài Nga - sinh ngày 11-11-1821.\nCác tác phẩm chính của ông: Thằng ngốc (1867); Anh em nhà Karamarốp, Tội ác và hình phạt...\nÔng đã nâng cao thể loại tiểu thuyết hiện thực phê phán. Nhà vǎn đã miêu tả và khai thác đến tột đỉnh diễn biến tâm lý nhân vật trong các tác phẩm chính của mình.\nÔng mất ngày 9-2-1882.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','01', 'Ngày 12-1-1958, Chủ tịch Hồ Chí Minh đã về thǎm và tát nước chống hạn với bà con nông dân ở làng Tó, xã Tả Thanh Oai, huyện Thanh Trì, và thǎm cánh đồng bị hạn hán ở xã Mễ Trì, huyện Từ Liêm, Hà Nội.\nTại xã Mễ Trì, Bác khen thành tích chống hạn của nhân dân và cán bộ trong xã. Bác nói: \"Đồng bào Mễ Trì phải vắt đất ra nước thay trời làm mưa, đừng tin vào cầu đảo, lễ bái. Hôm nay Bác đến thǎm và có phần thưởng cho Mễ Trì. Bác trao ba huy hiệu này cho xã, ai có thành tích xuất sắc trong việc chống hạn thì được tặng\".\nSau đó xã đã trao ba huy hiệu Bác Hồ cho các ông: Nguyễn Vǎn Chi , Nguyễn Vǎn Tú và bà Lê Thị Tương. Đó là những xã viên tích cực trong phong trào làm thuỷ lợi ở xã.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','01', 'Ngày 12-1-1976, Uỷ ban thường vụ Quốc hội nước Việt Nam Dân chủ cộng hoà đã phê chuẩn việc thành lập Tổng cục Hàng không dân dụng Việt Nam trực thuộc Hội đồng Chính phủ.\nTừ đó đến nay, ngành hàng không dân dụng nước ta đã phát triển mạnh mẽ, trở thành một ngành kinh tế quan trọng của đất nước, với nhiều chuyến bay trong nước và quốc tế.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','01', 'Ngày 12-1-1981, Đại hội đồng Liên hiệp quốc khoá 35 chấp nhận Việt Nam và 4 nước khác là Tây Ban Nha, Cộng hoà Ả Rập, Xyri, Thượng Vôn ta và Urugoay làm thành viên chính thức của Uỷ ban sử dụng hoà bình khoảng không vũ trụ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','01', '12-1-1933, Quốc hội Mỹ công nhận độc lập của Philippines.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','01', 'Giắc Lơnđơn (Jack London) là nhà vǎn hiện thực Mỹ. Ông sinh ngày 12-1-1876. Các tác phẩm của ông bộc lộ rõ xu hướng xã hội chủ nghĩa, phê phán sâu sắc trật tự tư bản. Ông là nhà vǎn được quần chúng công nhận, trong và ngoài nước yêu chuộng, có ảnh hưởng lớn đến các nhà vǎn tiến bộ sau này. Ông có các tác phẩm nổi tiếng như: \"Tiếng gọi nơi hoang dã\" (1903), \"Tình yêu cuộc sống\" (1907), \"Nanh trắng\" (1907), \"Gót sắt\" (1907), \"Dân chúng của vực thẳm\", \"Chiến tranh giai cấp\".\nNhững nǎm cuối đời, tai hoạ, bệnh tật khiến tinh thần ông khủng hoảng. Ngày 22-11-1916, ông uống thuốc độc tự tử lúc mới 40 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','01', 'Igo Cutratốp (Igor Kurratov) là nhà khoa học nổi tiếng của Nga và thế giới. Ông sinh ngày 12-1-1903. Do những công trình nghiên cứu các chất điện môi, ông được công nhận là tiến sĩ khoa học toán lý. Từ nǎm 1932, ông được giao trọng trách tổ chức nghiên cứu nguyên tử. Trong chiến tranh thế giới II ông đi sâu nghiên cứu gỡ thủy lôi, dùng tàu phá mìn và nghiên cứu chế ngự nǎng lượng nguyên tử. Nǎm 1949, Liên Xô đưa thử thành công bom nguyên tử, phá vỡ thế độc quyền nguyên tử của Mỹ.\nCutratốp qua đời ngày 6-2-1960 sau khi đã điều khiển thành công máy nhiệt hạch Ogra.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','02', 'Ngày 12-2-1947, Chủ tịch Hồ Chí Minh ký sắc lệnh thành lập Phòng Dân quân tự vệ, nay là Cục Dân quân tự vệ thuộc Bộ Quốc phòng.\nNgày này đã trở thành ngày truyền thống của lực lượng dân quân tự vệ cả nước.\nTrong hai cuộc kháng chiến chống thực dân Pháp và đế quốc Mỹ, nhiều đơn vị dân quân tự vệ đã lập công xuất sắc. Nhà nước ta đã tuyên dương 961 Đơn vị anh hùng. 161 Anh hùng lực lượng vũ trang nhân dân thuộc lực lượng dân quân tự vệ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','02', 'Tối 12-2-1964 (tức 30 tết Giáp Thìn) Chủ tịch Hồ Chí Minh đã đi thǎm và chúc tết công nhân, cán bộ tại khu tập thể các nhà máy: Cao su Sao Vàng, Xà phòng Hà Nội, Thuốc lá Thǎng Long, khu tập thể cán bộ miền Nam tập kết ở Phan Đình Phùng và một số gia đình ở Hà Nội: Ông Phan Huy Nhật (công nhân nhà máy nước), Nguyễn Vǎn Tá (trưởng ban bảo vệ khối 30, khu phố Đống Đa), giáo sư Nguyễn Xiển (Tổng thư ký Đảng Xã hội), bác sĩ Trần Hữu Tước và ông Phan Vǎn Chúc (Việt kiều về nước).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','02', 'Ngày 12-2-1973, toán phi công Hoa Kỳ đầu tiên bị bắt sống ở miền Bắc được Chính phủ ta trao trả tại sân bay Gia Lâm (Hà Nội). Toán đầu tiên này gồm 116 tên (trong tổng số 472 tên bị bắt trong cuộc chiến tranh phá hoại bằng không quân của Mỹ từ 1964-1973) lái nhiều loại máy bay thuộc không quân, hải quân, lính thuỷ đánh bộ, bị bắt ở hầu hết các tỉnh trên miền Bắc. Chiều 29-3, toán giặc lái cuối cùng gồm 107 tên được trao trả cho phía Hoa Kỳ tại sân bay Gia Lâm Hà Nội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','02', 'Ngày 12-2-1974, đã diễn ra Đại hội Công đoàn toàn quốc lần thứ ba. Về dự có 507 đại biểu và còn có Đoàn đại biểu Liên hiệp Công đoàn giải phóng miền Nam Việt Nam, Đoàn đại biểu Liên hiệp Công đoàn thế giới. Đại hội quyết định sửa đổi điều lệ Công đoàn và bầu BCH Tổng công đoàn.\nTại đại hội này, Quốc hội nước Cộng hoà xã hội chủ nghĩa Việt Nam đã trao tặng Huân chương Hồ Chí Minh cho phong trào sản xuất và tổ chức Công đoàn Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','02', 'Sáclơ Rôbớt Đácuyn (Charle Robert Darwin), nhà sinh vật học lỗi lạc nhất nước Anh thế kỷ XIX, sinh ngày 12-2-1809. Nǎm 1859, ông xuất bản tác phẩm có giá trị nhất của mình là \"Nguồn gốc các loài, Con đường chọn lọc tự nhiên\". Cuốn sách thực sự là một cuộc Cách mạng trong ngành khoa học tự nhiên cho rằng vạn vật đều biến đổi. Nǎm 1868 ông xuất bản cuốn \"Sự biến đổi của động vật và thực vật trong chǎn nuôi và trồng trọt\". Sau đó ông mở rộng học thuyết tiến hoá với tác phẩm \"Nguồn gốc loài người và sự chọn lọc giới tính\".\nÔng qua đời ngày 19-4-1882.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','02', 'Giơhác ácmauơ Henxơn (Gerhard Armauer Hansen) sinh nǎm 1841, mất ngày 12-2-1912.\nÔng là bác sĩ y khoa và thực vật học NaUy. Nǎm 1874, ông tìm thấy vi khuẩn gây bệnh phong và nó được gọi là vi khuẩn Henxơn. Ông dạy học tại viện bài trừ bệnh phong Bergen. Qua nhiều bài nghiên cứu đǎng trên sách báo, Henxơn đã chứng minh bệnh phong khó lây lan và hoàn toàn có thể chữa được tuỳ theo mức độ phát hiện bệnh sớm hay muộn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','03', 'Phan Bá Vành quê ở làng Minh Giám, huyện Vũ Thư, tỉnh Thái Bình, sinh nǎm 1784 và hy sinh ngày 12-3-1827.\nÔng là thủ lĩnh phong trào nông dân lớn nhất Bắc Hà.\nNǎm 1826, ông khởi binh ở Nam Định, đánh phủ Trà Lý và Lân Hải, đánh huyện Tân Minh và Nghi Dương (Hải Dương).\nNghĩa quân đi đến đâu cũng lấy của nhà giàu chia cho dân nghèo. Nǎm 1827, ông chỉ huy nông dân đánh phủ Thiên Trường và Kiến Xương, nhưng sau bị quân Triều Nguyễn bắt sống ở Trà Lũ. Trong câu ca dao xưa có câu:\nTrên trời có sao tua rua\nỞ dưới hạ giới có vua Bá Vành.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','03', 'Ngày 12-3-1960, Mỹ-Diệm đã giết hại ông Hoàng Lệ Kha, nguyên Chủ tịch Uỷ ban hành chính kháng chiến huyện Dương Minh Châu, Tỉnh uỷ viên tỉnh Tây Ninh. Sau khi ban hành luật \"10-59\" chúng đã lê máy chém đi tàn sát những người yêu nước. Hoàng Lệ Kha là người đầu tiên bị chúng sát hại. Hành động phát xít này của Mỹ - Diệm đã gây nên một làn sóng cǎm phẫn, phản đối trong nước ta lúc đó.\nÔng Hoàng Lệ Kha sinh nǎm 1907, quê ở xã Hà Phong, huyện Hà Trung tỉnh Thanh Hoá, tham gia cách mạng từ nǎm 1931. Ngày 23-7-1997, liệt sĩ Hoàng Lệ Kha được Nhà nước truy tặng danh hiệu \"Anh hùng lực lượng vũ trang nhân dân\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','03', 'Ngày 12-3-1963, Hồ Chủ tịch về thǎm Nhà máy cơ khí Hà Nội (nay là Nhà máy chế máy công cụ số 1) cùng với vua nước Lào. Đây là lần cuối cùng Bác về thǎm Nhà máy.\nLần đầu Bác về chúc Tết công nhân cán bộ ở đây vào ngày 18-2-1958. Nhà máy này được Bác về thǎm nhiều nhất, tới 9 lần. Có lần Bác thǎm nơi sản xuất, có lần xem nơi ǎn ở, có lần thǎm lớp học đại học buổi tối, có lần Bác đi cùng với các vị khách nước ngoài. Bác còn tặng huy hiệu cho những công nhân, cán bộ của Nhà máy cơ khí Hà Nội có thành tích về sản xuất và học tập.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','03', 'Ngày 12-3-1975, Việt Nam và Niger thiết lập quan hệ ngoại giao ở cấp đại sứ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','03', 'Ngày 12-3-1918, thành phố Matxcơva trở thành Thủ đô của nước Nga Xô viết.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','03', 'Ngày 12-3-1925 tại Bắc Kinh, Tôn Trung Sơn, nhà cải cách dân chủ vĩ đại của Trung Hoa trút hơi thở cuối cùng.\nTôn Trung Sơn sinh nǎm 1866. Ông theo học trường Đại học Y khoa ở Hương Cảng và trở thành bác sĩ. Trong hoàn cảnh đất nước bị đế quốc xâu xé, ông thấy việc cứu nguy cho toàn xã hội quan trọng hơn nên bỏ nghề y tham gia hoạt động chính trị. Ông tập hợp người cùng chí hướng thành lập Hưng Trung Hội với tôn chỉ đánh đổ Mãn Thanh, khôi phục Trung Hoa. Sau đó thành lập \"Trung Quốc đồng minh hội\", đề cao chủ nghĩa Tam dân, nổi dậy khởi nghĩa và giành thắng lợi. Tôn Trung Sơn được đề cử làm Tổng thống Chính phủ Trung Hoa lâm thời và tuyên bố thành lập Trung Hoa dân quốc. Để lôi kéo quần chúng theo cách mạng và để tránh cảnh \"Nam Bắc phân tranh\", ông đã từ chức Tổng thống. Khi Viên Thế Khải lộ mặt phản động, ông thành lập Chính phủ cách mạng ở Quảng Đông và xây dựng lực lượng cách mạng. Phong trào cách mạng đang lên cao thì Tôn Trung Sơn mắc bệnh từ trần. Cái chết của ông là một tổn thất lớn cho Trung Hoa lúc đó')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','03', 'Ngày 12-3-1958, các chuyên gia thể thao quân đội của 12 nước xã hội chủ nghĩa đã họp tại Matxcơva và thông qua Điều lệ tổ chức Uỷ ban thể thao quân đội các nước hữu nghị (gọi tắt là SKDA).\nSKDA thường xuyên tổ chức các đại hội thể thao mùa đông, mùa hè và các cuộc thi vô địch của các môn thể thao Olimpic và thể thao quân sự thực dụng. Vận động viên quân đội các nước thành viên đã giành nhiều giải thưởng, huân huy chương trong các kỳ Đại hội. Đây cũng là một hình thức nhằm tạo ra mối đoàn kết, hiểu biết, học hỏi lẫn nhau giữa quân đội các nước xã hội chủ nghĩa.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','03', 'Ngày 12-3-1968, Mauritus trở thành một thành viên độc lập thuộc Khối Thịnh vượng chung của Anh sau khi đã từng là thuộc địa của Anh từ nǎm 1810.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','03', '12-3-1999, Ba Lan, Hungary và Cộng hoà Séc trở thành những cựu thành viên đầu tiên của khối Liên bang Xô Viết gia nhập NATO.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','04', 'Ngày 12-4-1958 Nhà máy Cơ khí Hà Nội (nay là nhà máy chế tạo công cụ số 1) làm lễ khánh thành.\nĐây là nhà máy hiện đại đầu tiên của nền công nghiệp Việt Nam được Chính phủ và nhân dân Liên Xô giúp đỡ ta xây dựng. Nhà máy được trang bị 203 loại máy hiện đại sản xuất công cụ với độ chính xác 1/1000mm.\nNhà máy Cơ khí Hà Nội là một trong những đơn vị vinh dự được Hồ Chủ tịch lúc sinh thời đến thǎm nhiều nhất (9 lần).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','04', 'Ngày 12-4-1960 Quân giải phóng Miền Nam khu Sài Gòn Gia Định đã tiến công dữ dội sân bay Tân Sơn Nhất ở sát nách phía Bắc Sài Gòn. Phá huỷ và phá hỏng 67 máy bay, tiêu diệt 300 tên, đốt cháy một kho xǎng lớn.\nĐây là một trong những trận đánh lớn ở khu vực Sài Gòn, Gia Định và nó chứng tỏ rằng Quân giải phóng Miền Nam có đủ khả nǎng đánh vào các vị trí then chốt và sào huyệt của Mỹ và tay sai.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','04', 'Ngày 12-4-1966 lần đầu tiên đế quốc Mỹ dùng chiến lược B52 ném bom miền Bắc nước ta. Máy bay B52 xuất phát từ đảo Guyam đến ném bom một địa điểm ở phía tây Quảng Bình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','04', '12-4-1973, Việt Nam và Cộng hoà Pháp thiết lập quan hệ ngoại giao giữa hai nước.\nPháp ở phía tây châu Âu, có diện tích 51.600 km2, số dân 57 triệu người, thủ đô là thành phố Pari.\nPháp là một nước công - nông nghiệp phát triển cao, là thành viên của Liên hiệp quốc, của Liên minh châu Âu và của NATO.\nNhững nǎm gần đây, Pháp và Việt Nam đã tǎng cường hợp tác kinh tế và giao lưu vǎn hoá, khoa học kỹ thuật.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','04', 'Ngày 12-4-1961, loài người được chứng kiến một sự kiện vĩ đại. Gagarin, công dân Liên Xô đã hoàn thành chuyến bay vũ trụ vòng quanh quả đất trên tàu Phương Đông 1, chuyến bay vũ trụ đầu tiên có người trong lịch sử loài người. Sau khi bay một vòng quanh quả đất trong khoảng thời gian 108 phút, tàu vũ trụ đã hạ cánh an toàn trên một cánh đồng trên bờ sông Vonga. Thành công của chuyến bay vũ trụ đầu tiên đó đã mở ra một kỷ nguyên mới trong lịch sử khoa học thế giới. Kỷ nguyên con người làm nghiên cứu khoa học ngay trong vũ trụ.\nChuyến bay của Gagarin là một chuyến bay có ý nghĩa lịch sử to lớn. Chính vì vậy, Liên đoàn Hàng không quốc tế đã quyết định lấy ngày 12-4 hàng nǎm làm ngày hàng không và du hành vũ trụ quốc tế. Đó là sự đánh giá đúng đắn công lao của Gagarin và những thành tựu của khoa học vũ trụ Liên Xô, đất nước đã mở ra kỷ nguyên mới cho loài người: Kỷ nguyên chinh phục vũ trụ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','04', '12-4-1996: Khánh thành tháp Petronas - được xem là toà nhà cao nhất thế giới ở Kuala Lumpur.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','05', 'Võ tướng Tôn Thất Thuyết sinh ngày này 11-5-1839 ở thành phố Huế và từ trần nǎm 1913.\nNǎm 1881, ông là Thượng thư Bộ Binh, rồi làm Phụ chính đại thần sau khi vua Tự Đức qua đời. Trong triều đình có nhiều quan chủ hoà, nhưng Tôn Thất Thuyết kiên quyết chủ chiến với thực dân Pháp. Đêm 4-7-1885, quân ta tấn công doanh trại của Pháp ở Huế nhưng vũ khí quá thô sơ nên không thành công.\nTôn Thất Thuyết phải đưa Vua Hàm Nghi ra Quảng Trị, phát động phong trào Cần Vương chống Pháp. Nhiều vǎn thân ở Bắc Kỳ và Trung Kỳ đã hưởng ứng lời chiếu của nhà vua. Tôn Thất Thuyết là linh hồn, là vị chỉ huy của phong trào ấy. Hai con trai của ông là Tôn Thất Đạm và Tôn Thất Thiệp đều là tướng bảo vệ vua Hàm Nghi và đã hy sinh khi chống lại bọn phản bội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','05', 'Ngày này nǎm 1875, Nguyễn Hữu Huân bị thực dân Pháp xử chém. Ông cắn lưỡi tự tử tại pháp trường, không để chúng hành quyết.\nÔng sinh nǎm 1834 ở tỉnh Tiền Giang, người đương thời thường gọi là Thủ Khoa Huân. Ông làm giáo thụ, khi quân Pháp xâm lược Nam Kỳ, ông đã hai lần tổ chức khởi nghĩa ở Tân An và Mỹ Tho, ba lần bị giặc bắt, cả bị đi đầy ở đảo Réunion và bị dụ hàng, nhưng ông vẫn bất khuất.\nThơ ông còn truyền tụng nhiều, và tên ông dược đặt cho một đường phố của Hà Nội')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','05', 'Ngày 12-5-1917 là ngày sinh của nhà thơ Nguyễn Tuấn Linh bút danh Thâm Tâm, quê ở thị xã Hải Dương.\nNhà nghèo, học hết bậc tiểu học, ông bỏ đi làm. Từ nǎm 1938 ông cùng gia đình sống ở Hà Nội, vẽ tranh và sáng tác vǎn học. Ông viết nhiều nhất ở các báo \"Tiểu thuyết thứ bảy\", \"Truyền bá\", \"Phổ thông bán nguyệt san\".\nSau Cách mạng Tháng Tám, ông tham gia Hội Vǎn nghệ cứu quốc, rồi toàn quốc kháng chiến, ông vào bộ đội, làm thư ký toà soạn báo Vệ quốc quân\nThơ ông được tán thưởng nhiều qua các bài \"Thánh ca\", \"Vọng nhân hành\", \"Chiều mưa đường số 5\", \"Tống biệt hành\"... Nǎm 1950 ông tham dự chiến dịch Cao Lạng, trên đường hành quân ông hy sinh vào ngày 18-8-1950 khi mới 33 tuổi. Sự hy sinh của ông đã để lại nhiều thương tiếc cho nhân dân và đồng đội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','05', 'Ngày 12-5-1967, Hồ Chủ tịch đã gửi thư tới đồng bào, bộ đội và cán bộ Vĩnh Linh.\nNgười viết:\n\"Bác rất vui lòng được tin Vĩnh Linh đã bắn rơi 100 máy bay Mỹ, Vĩnh Linh còn bắn cháy nhiều tàu chiến Mỹ và trừng trị đích đáng pháo binh Mỹ. Sản xuất và phòng không của Vĩnh Linh cũng tốt.\nThay mặt Trung ương Đảng và Chính phủ, Bác gửi lời khen ngợi đồng bào, bộ đội và cán bộ Vĩnh Linh đã đoàn kết, anh dũng, kiên cường trong chiến đấu và sản xuất, giành nhiều thắng lợi vẻ vang\".\nTừ tháng 8-1964 đến hết nǎm 1972, quân và dân Vĩnh Linh đã bắn rơi 284 máy bay Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','05', 'Từ 12 đến 14-5-1981 Việt Nam được Hội nghị BCH Hội đồng tương trợ kinh tế họp kỳ thứ 99 thông qua những biện pháp đặc biệt kể cả trong lĩnh vực KHKT nhằm nhanh chóng phát triển và nâng cao hiệu qủa nền kinh tế quốc dân. Các nước thành viên sẽ hợp tác về kinh tế khoa học và kỹ thuật với Việt Nam trong các lĩnh vực nông nghiệp, địa chất, vận tải, khí tượng, y tế, trang bị cho các viện và các trung tâm nghiên cứu khoa học, đào tạo và nâng cao trình độ khoa học và kỹ thuật.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','05', 'Tại Matxcơva ngày 12-5-1975, đại diện Chính phủ Liên bang Cộng hoà xã hội chủ nghĩa Xô Viết ký \"hiệp định về việc Liên Xô viện trợ khẩn cấp không hoàn lại cho Việt Nam\" để giúp nhân dân ta ổn định đời sống, khôi phục và phát triển sản xuất. Theo hiệp định này, Liên Xô giao sang Việt Nam trong nǎm 1975 gồm có xǎng dầu, phân bón, lương thực, xe vận tải và nhiều loại hàng tiêu dùng khác.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','06', 'Ngày 12-6-1945, Khâm sai Bắc bộ Phan Kế Toại ra nghị định đổi tên trường trung học Bảo hộ ở Hà Nội thành trường Quốc lập Trung học hiệu Chu Vǎn An.\nĐây là một trong những trường trung học lớn nhất Đông Dương được thành lập từ cuối thế kỷ XIX ở khu vực làng Bưởi. Trường này chủ yếu nhằm đào tạo những viên chức bản xứ theo Tây học cho bộ máy hành chính của thực dân Pháp. Về lâu dài, trường nhằm đào tạo những thế hệ trí thức Tây học, phục vụ chế độ thuộc địa. Nhưng chính ở đây đã hình thành một trung tâm phong trào yêu nước của học sinh Hà Nội, với thế hệ những học sinh sau này trở thành những chiến sĩ cách mạng xuất sắc như: Nguyễn Vǎn Cừ, Ngô Gia Tự, Đỗ Ngọc Du, Phạm Vǎn Đồng, Lê Vǎn Lương, Hà Huy Giáp...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','06', 'Ngày 12-6-1965 đế quốc Mỹ dã man, vô nhân đạo đã lợi dụng đêm tối bắn phá ném bom Trại điều dưỡng bệnh phong Quỳnh Lập (Nghệ An) làm 139 đồng bào ta đang điều dưỡng bệnh ở đó bị chết, 80 đồng bào khác bị thương, 200 người bệnh đang điều trị không có nhà ở. Dã man hơn nữa, 10 ngày sau đế quốc Mỹ còn cho máy bay đến bắn phá liên tiếp làm toàn bộ 120 ngôi nhà gạch lớn nhỏ cùng với hơn 40 ngôi nhà tranh của khu này bị phá huỷ hoàn toàn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','06', 'Ngày 12-6-1969 Ủy ban Thường vụ Quốc hội và Chính phủ Việt Nam Dân chủ cộng hoà ra Tuyên bố về việc thành lập Chính phủ Cách mạng lâm thời cộng hoà miền Nam Việt Nam.\nNhân dịp này, Hồ Chủ tịch và Thủ tướng Phạm Vǎn Đồng đã gửi điện mừng. Bức điện có đoạn viết: \"Việc họp Đại hội đại biểu quốc dân miền Nam Việt Nam và việc thành lập chính quyền cách mạng trên toàn miền Nam chứng tỏ rằng đồng bào miền Nam quyết tâm tǎng cường đoàn kết, kiên trì chiến đấu để làm chủ vận mệnh của mình và đào tạo thêm thuận lợi mới, tiến tới thắng lợi hoàn toàn\".\nNhân dân cả nước cũng như bạn bè khắp nǎm châu nhiệt liệt chào mừng sự kiện lịch sử này của cách mạng Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','06', 'Ngày 12-6-1968, Đại hội đồng Liên hiệp quốc đã thông qua hiệp ước cấm phổ biến vũ khí hạt nhân và kêu gọi các nước phê chuẩn hiệp ước này.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','06', 'Liên bang Nga tuyên bố chủ quyền quốc gia của mình ngày 12-6-1991, sau đó, ngày 19-8-1991, Liên Xô tan vỡ.\nLiên bang Nga là nước rộng lớn nhất châu Âu, có diện tích 17.075.000km2, số dân 150 triệu người, thủ đô là thành phố Mátxcơva.\nLiên bang Nga là một nước công nghiệp phát triển cao, là Ủy viên thường trực hội đồng Bảo an Liên hiệp quốc, đã thiết lập quan hệ ngoại giao với Việt Nam từ ngày 30-1-1950.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','07', 'Nhà sư nổi tiếng Dương Không Lộ còn gọi là Không Lộ thiền sư, viên tịch vào ngày 12-7-1119. Ông quê ở Giao Thuỷ, nay thuộc tỉnh Nam Định.\nSau khi học đạo Phật, ông về tu ở chùa Keo (tỉnh Thái Bình) và đi truyền đạo ở nhiều nơi trong nước. Truyền rằng thời ấy có thái tử bên Trung Quốc bị bệnh nặng, không ai chữa khỏi, phải cho mời ông. Chữa khỏi bệnh cho Thái Tử, ông chỉ nhận đồng đem về nước, đúc hàng loạt chuông lớn, ban cho các nhà chùa. Do đó, nhiều làng có nghề đúc đồng đều thờ ông làm tổ sư nghề.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','07', 'Vào ngày 17-7-1946, các chiến sĩ công an đã khám xét một trụ sở của bọn Quốc dân Đảng ở số nhà 7 phố Ôn Như Hầu, Hà Nội (nay là phố Nguyễn Gia Thiều). Nơi đây là một ổ làm giấy bạc giả, bắt cóc người để tống tiền, có những dụng cụ để tra tấn, có nhiều xác chết và người bị giam. Cũng tại đây, chúng đã bắt và giết hại nhều cán bộ, chiến sĩ Việt Minh.\nCùng ngày hôm đó (12-7-1946), công an ta còn khám xét một trụ sở khác của bọn Quốc dân Đảng ở 132 phố Đuyvinhô (nay là phố Bùi Thị Xuân, Hà Nội). Hơn 20 tên phản động bị bắt cùng với vũ khí, máy in, hàng vạn tờ truyền đơn kêu gọi lật đổ chính quyền của ta và ủng hộ Chính phủ của chúng lập ra.\nNhư vậy là âm mưu đảo chính của bọn phản động đã bị phát hiện. Bộ mặt côn đồ của chúng đã bị vạch trần trước nhân dân. Chính quyền non trẻ của ta vẫn đứng vững.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','07', 'Ngày 12-7-1984, tại Hà Nội. Ủy ban Khoa học và kỹ thuật Nhà nước đã tổ chức lễ cấp bằng sáng chế và giấy chứng nhận đǎng ký nhãn hiệu hàng hoá lần đầu tiên.\nĐợt này cấp bằng cho các tác giả của 9 sáng chế đầu tiên, trong số đó có việc tạo ra các giống lúa NN 75-10; NN 75-2; NN 75-6; giống lợn DB1-81; dầu VH 3-2; phương pháp loại sắt ra khỏi thiếc.\nTrong số 19 tác giả của 9 sáng chế này có 4 tác giả là phụ nữ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','07', 'Ngày 12-7-1995, Việt Nam và Hoa Kỳ chính thức thiết lập quan hệ ngoại giao ở cấp đại sứ.\nSau nǎm 1975, Mỹ thi hành chính sách cấm vận toàn diện đối với Việt Nam. Từ nǎm 1987 Mỹ từng bước nới lỏng cấm vận thương mại. Tháng 7 nǎm 1993 Tổng thống Mỹ quyết định giải toả quan hệ Việt Nam với quỹ tiền tệ quốc tế. Đến ngày 3-2-1994 Mỹ huỷ bỏ lệnh cấm vận đối với Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','07', 'Ngày 12-7-1995, nhạc sĩ Vǎn Cao qua đời tại Hà Nội.\nTrước Cách mạng tháng Tám, ông đã nổi tiếng về viết vǎn, làm thơ, vẽ tranh, sáng tác bài hát. Những ca khúc trữ tình lãng mạn trong thời kỳ tiền khởi nghĩa mà âm hưởng nó còn vang vọng tới ngày nay như: \"Thiên thai\", \"Bến xuân\", \"Trương Chi\", \"Suối mơ\"... Đặc biệt là bài \"Tiến quân ca\" của nhạc sĩ Vǎn Cao đã được Quốc hội khoá I chọn làm Quốc ca của Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','07', 'Nhà thơ cộng sản Pablô Nêruđa sinh ngày 12-7-1904. Ông là nhà thơ trữ tình chính trị lớn nhất của nhân dân Chilê và châu Mỹ La tinh.\nSinh ra trong một gia đình viên chức, ông tốt nghiệp đại học Sanchicagô rồi làm công chức ngoại giao, hàm lãnh sự, tại nhiều nước Viễn Đông, châu Âu, châu Mỹ La tinh. Các tập thơ nổi tiếng của ông là: \"Tây Ban Nha trong lòng tôi\", \"Bài ca gửi Xtalinggrat\", \"Một tiếng ca chung\", \"Những con chim Chilê\", \"Một trǎm bài thơ tình\".\nCác bài thơ của Nêruđa là vũ khí sắc bén lên tiếng tố cáo kẻ thù, là tiếng kèn tập hợp những người yêu nước, là tiếng nói nhiệt thành chia sẻ và cổ cũ các phong trào giải phóng dân tộc trên thế giới. Ông đã được nhận giải thưởng Noben về vǎn học nǎm 1971. Cũng chính vì uy tín của nhà thơ cộng sản này ngày càng to lớn làm cho kẻ thù phải run sợ, bọn phản động Pinôchê đã bắt giam và hèn hạ thủ tiêu ông vào ngày 24-11-1973.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','07', 'Bécna Clốt (Bernard Claude) sinh ngày 12-7-1813 trong một gia đình trồng nho ở miền nam nước Pháp. Nǎm 21 tuổi ông đến Pari với tham vọng viết kịch nhưng lại ghi tên học trường y. Nǎm 32 tuổi ông cho in các tác phẩm: \"Nghiên cứu dây thần kinh tuỷ sống\", \"Đường glucô trong gan\", \"Nguồn gốc và vai trò của gan trong hoạt động của sinh vật\", \"Về một chức nǎng mới của gan người và động vật\". Nǎm 40 tuổi được bầu vào Viện Hàn lâm y học Pháp. Đến nǎm 52 tuổi ông in tác phẩm \"Nhập môn y học thực nghiệm\". Ông được đánh giá \"không chỉ là sinh lý học vĩ đại mà ông là bản thân ngành sinh lý học\". Ông mất nǎm 1878, thọ 65 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','07', 'Bác sĩ Saclơ Lêông Canméttơ sinh ngày 12-7-1863 và đời nǎm 1933. Ông là nhà vi trùng học người Pháp, sáng lập ra viện Paxtow Sài Gòn và làm giám đốc bệnh viện này từ nǎm 1891 đến nǎm 1893. Ông đã nghiên cứu nọc rắn và huyết thanh trị nọc rắn, huyết thanh trị bệnh hạch chuột, kiện toàn thuốc chủng đậu. đặc biệt Canméttơ cùng với Camilơ Guyêranh phát minh thuốc BCG để phòng ngừa bệnh lao.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','08', 'Ngày 12-8-1945, Uỷ ban chỉ huy lâm thời khu giải phóng ra mệnh lệnh khởi nghĩa.\nBản mệnh lệnh gồm 10 điểm, quy định phương án tác chiến cho lực lượng vũ trang như tấn công địch ở các đô thị. Chính sách đối với quân Ngụy, tù binh và hàng binh Nhật, những người thuộc phái Đờ Gôn, vấn đề vũ khí, thông tin liên lạc, phối hợp với các lực lượng nhân dân, và cuối cùng nhấn mạnh \"Lúc này là lúc quân sự hành động, kỷ luật phải nghiêm\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','08', 'Ngày 12-8-1954, trong lần tiếp Uỷ ban Quốc tế giám sát và kiểm soát việc thi hành hiệp định Giơnevơ về Việt Nam, Hồ Chủ tịch đã nói: \"Hiệp định đình chiến ở Đông Dương ký kết tại Hội nghị Giơnevơ không những có một ý nghĩa to lớn đối với Đông Dương và Đông Nam Á, mà nó còn mở đường cho hoà bình lâu dài ở Châu Á và làm cho tình hình kinh tế bớt gǎng. Nhưng chúng ta phải nhận rõ rằng: đình chiến mới chỉ là bước đầu trong sự giải quyết toàn bộ vấn đề Việt Nam\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','08', 'Để tǎng cường hiệu lực quản lý Nhà nước về rừng, ngǎn chặn tình trạng phá rừng, nâng cao trách nhiệm và khuyến khích các tổ chức, cá nhân bảo vệ, phát triển rừng, phát huy các lợi ích của rừng phục vụ sự nghiệp xây dựng và bảo vệ tổ quốc, ngày 12-8-1991, Quốc hội đã thông qua \"Luật Bảo vệ và phát triển rừng\" (Được Chủ tịch Hội đồng Nhà nước ký sắc lệnh công bố ngày 19-8-1991).\nLuật gồm 9 chương, với 54 điều.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','08', 'Để nâng cao trách nhiệm của gia đình, cơ quan Nhà nước, nhà trường, tổ chức xã hội và công dân trong việc bảo vệ, chǎm sóc và giáo dục trẻ em, nhằm bồi dưỡng các em trở thành công dân tốt của đất nước, ngày 12-8-1991. Quốc hội nước Cộng hoà xã hội chủ nghĩa Việt Nam đã thông qua \"Luật Bảo vệ và chǎm sóc trẻ em\". (Được Chủ tịch Hội đồng Nhà nước ký lệnh công bố ngày 16-8-1991).\nLuật này quy định các quyền cơ bản, bổn phận của trẻ em và việc bảo vệ, chǎm sóc, giáo dục trẻ em. Luật có 5 chương và 26 điều.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','09', 'Nhạc sĩ Lưu Hữu Phước (còn gọi là Huỳnh Minh Siêng), sinh ngày 12-9-1921 tại huyện Long Phú, tỉnh Sóc Trăng và qua đời năm 1989.\nTrước năm 1945, ông học đại học ở Hà Nội, hoạt động phong trào sinh viên yêu nước.\nSau Cách mạng Tháng Tám, trong cuộc kháng chiến chống Pháp, ông đảm nhiệm các công tác văn hoá nghệ thuật, thông tin, báo chí, thiếu nhi, thanh niên.\nHoà bình lập lại (năm 1954), ông giữ các chức vụ: Vụ trưởng Vụ Âm nhạc và múa, Phó tổng thư ký Hội Nhạc sĩ Việt Nam.\nNăm 1965, nhạc sĩ Lưu Hữu Phước trở về miền Nam tham gia kháng chiến chống Mỹ, làm Bộ trưởng Bộ Thông tin văn hoá của Chính phủ Cách mạng lâm thời miền Nam. Năm 1981 ông được phong Giáo sư, giữ chức vụ Chủ tịch Hội đồng Âm nhạc quốc gia, Ủy viên thông tấn của Hội đồng Âm nhạc quốc tế.\nSự nghiệp âm nhạc của Lưu Hữu Phước khá to lớn, biểu lộ rõ khuynh hướng yêu nước và dân tộc, gắn chặt với \"nhịp đi\" của Cách mạng nước nhà. Lưu Hữu Phước xứng đáng là ngôi sao sáng của nền âm nhạc cách mạng với các bài hát đã đi vào lòng người như: \"Tiếng gọi thanh niên\", \"Lên đàng\", \"Giải phóng miền Nam\", \"Lãnh tụ ca\", \"Bạch Đằng Giang\", \"Đông Nam Á châu\"...\nNăm 1996, Nhà nước ta đã truy tặng nhạc sĩ Lưu Hữu Phước giải thưởng Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','09', 'Ngày 12-9-1930, 8.000 nông dân phủ Hưng Nguyên, Nghệ An biểu tình tiến về phủ lỵ. Hoảng sợ trước khí thế đấu tranh của quần chúng, Pháp cho máy bay ném bom vào đoàn biểu tình giết chết hàng trăm người. Đến chiều, khi nhân dân các làng Thông Lạng, Thái Lão ra chôn cất người hy sinh, máy bay lại ném bom, tổng số hy sinh là 217.\nTrong phong trào Xô Viết, sự kiện 12-9 ở Hưng Nguyên là mốc đánh dấu thời kỳ quyết liệt và đẫm máu nhất của phong trào.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','09', 'Ngày 6-9-1961, Hồ Chủ tịch ký sắc lệnh công bố \"Pháp lệnh đặt huân chương và huy chương chiến sĩ vẻ vang\" được Ủy ban thường vụ Quốc hội thông qua ngày 12-9-1961. Mục đích là để khen thưởng cán bộ và chiến sĩ có công lao trong việc xây dựng quân đội nhân dân và công an nhân dân vũ trang sau ngày hoà bình lập lại.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','09', 'Ngày 12-9-1991, tại Tơriextơ (Italia), nhà toán học nữ Lê Hồng Vân, 30 tuổi, chuyên gia về hình học vi phân đã được tặng giải thưởng của Trung tâm quốc tế vật lý lý thuyết.\nChị là người phụ nữ đầu tiên trên thế giới và là người Việt Nam đầu tiên được tặng giải thưởng này.\nNăm ấy Lê Hồng Vân đã là tiến sĩ toán học. Chị sinh tại Hà Nội tại một dòng họ nhiều đời khoa bảng về Hán học cũng như Tây học. Thân sinh của chị là ông Lê Văn Giang, nguyên Thứ trưởng Bộ Đại học và Trung học chuyên nghiệp, một tri thức có tiếng từ Cách mạng Tháng Tám. Chị ruột của chị là Lê Hoàng Lan, phó tiến sĩ ngữ văn. Cả ba chị em đều bảo vệ luận án tại Trường đại học Tổng hợp Lômônôxôp (Liên Xô).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','10', 'Lấy tư cách là đại biểu nông dân các nước thuộc địa, đồng chí Nguyễn Ái Quốc tham dự đại hội lần thứ nhất của nông dân Quốc tế Mácxcơva từ 12 đến 15-10-1923. Phát biểu tại Đại hội, đồng chí nêu rõ tình cảnh của nông dân Việt Nam bị thực dân Pháp chiếm đoạt ruộng đất, vơ vét lúa gạo. Người kết luận: \"Quốc tế của chúng ta chỉ trở nên một quốc tế thực sự nếu nhân dân phương Đông, nhất là nông dân các thuộc địa là những người bị bóc lột và áp bức nhất, tham gia Quốc tế\".\nTại Đại hội này, đồng chí Nguyễn Ái Quốc được bầu vào Ban Chấp hành Quốc tế nông dân.\nNgày 12-10-1958, Chủ tịch Hồ Chí Minh về thăm hợp tác xã nông nghiệp Đại Từ , nay thuộc xã Đại Kim, huyện Thanh Trì, Hà Nội.\nBác động viên đồng bào cố gắng sản xuất cho tốt và hẹn: Hợp tác xã phấn đấu năng suất lúa chiêm lên 2 tấn rưỡi thóc một héc ta thì Bác sẽ về ăn cơm chiêm với bà con\".\nĐể ghi nhớ công ơn của Bác Hồ và ngày Người về thăm, nhân dân ở đây đã dựng một tượng đài Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','10', 'Nhà văn Pháp Anatôn Frăngxơ (Anatole France) sinh năm 1844 tại Pari và qua đời ngày 12-10-1924. Năm 192, ông được giải thưởng Nôben.\nÔng nổi tiếng với các tiểu thuyết \"Tội ác của Xinvet Bona\", \"Trên phiến đá trắng\" và nhiều truyện ngắn khác.\nVăn phong của Anatôn Frăngxơ trong sáng tuyệt vời, nhẹ nhàng và nên thơ, nhiều nhạc điệu, sâu sắc và giễu cợt, biểu lộ một tâm hồn yêu con người thắm thiết.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','10', 'Driken là người Mỹ, giáo sư ở trường Đại học Ha vớt (Mỹ), năm 1927 đã chế tạo ra phổi nhân tạo.\nNgày 12-10-1928 phổi đó được thử ở bệnh viện Boston (bang Masachusét) cho một cô bé.\nMẫu đầu tiên được chế tạo với những bộ phận rất khác nhau: 2 máy hút dùng trong nội trợ tạo ra lần lượt áp suất dương và áp suất âm trên lồng ngực người.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','10', 'Ngày 12-10-1964, tàu vũ trụ \"Rạng Đông 1\" của Liên Xô đưa kỹ sư V.M Camarốp, nhà nghiên cứu Phêôtixtốp và bác sĩ Egôrôp bay vào vũ trụ. Lần đầu tiên các nhà bác học và bác sĩ trực tiếp nghiên cứu khoảng không vũ trụ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','11', 'Nhà thơ Vũ Đình Liên sinh ngày 12-11-1913 tại Hà Nội và qua đời nǎm 1995.\nÔng sáng tác không nhiều nhưng được bạn đọc nhớ mãi với bài thơ \"Ông đồ\". Bài thơ thể hiện niềm hoài cổ và cảm thông với số phận của \"những người muôn nǎm cũ, hồn ở đâu bây giờ\".\nNgoài làm thơ, Vũ Đình Liên còn dạy học, từng là chủ nhiệm khoa tiếng Anh trường Đại học Sư phạm Ngoại ngữ, dịch vǎn học Pháp. Ông được Nhà nước tặng danh hiệu Nhà giáo nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','11', 'Ngày 12-11-1936, hơn 10 nghìn công nhân mỏ than Cẩm Phả đã giành được thắng lợi sau một tuần bãi công, đấu tranh buộc chủ mỏ phải tǎng lương giảm giờ làm.\nTrong tháng 11-1936 còn có cuộc tổng bãi công của hơn 30 nghìn công nhân mở than ở Hồng Gai, Cẩm Phả, Hà Tu, Hà Lầm, Cọc 5.\nNgày 12 tháng 11 hàng nǎm đã trở thành Ngày truyền thống của công nhân mỏ Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','11', 'Ngày 12-11-1945, tại Cái Răng (tỉnh Cần Thơ), Lê Bình và một số tự vệ đóng giả là những người Hoa Kiều vào đồn pháp xin giấy phép đi lại các làng để mua lợn về mổ bán. Lừa khi bọn Pháp sơ ý. Lê Bình cùng bốn đồng đội đã rút súng bắn vào tên đồn trưởng cùng bọn lính. Sau đó Lê Bình leo lên cột cờ để hạ lá cờ giặc xuống, treo cờ đỏ sao vàng lên thì anh bị trúng đạn, hy sinh. Bốn chiến sĩ cảm tử cũng đều tử trận.\nTrận Cái Rǎng do Lê Bình chỉ huy là trận đánh của quân và dân Cần Thơ từ những ngày đầu của Nam Bộ kháng chiến. Nó đã có tiếng vang rộng lớn trong nước và báo chí Pháp cũng có nhiều bài tường thuật.\nLê Bình sinh nǎm 1924, quê ở Hà Tĩnh. Nhà nước đã truy tặng danh hiệu Anh hùng lực lượng vũ tranh nhân dân cho liệt sĩ Lê Bình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','11', 'Sáng sớm ngày 12-11-1965, quân giải phóng Miền Nam bất ngờ tấn công vào sở chỉ huy, và khu pháo binh địch ở Bầu Bàng tỉnh Thủ Dầu Một (cũ). Sau 3 giờ chiến đấu, quân giải phóng đã giết và làm bị thương hơn 2000 tên Mỹ, phá huỷ 40 xe tǎng, bắn rơi 2 chiếc máy bay địch.\nNgay sau trận đánh, Chủ tịch Uỷ ban Trung ương Mặt trận dân tộc giải phóng miền Nam Nguyễn Hữu Thọ đã đến thǎm và khen ngợi đơn vị quân giải phóng vừa chiến thắng ở Bầu Bàng..')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','11', 'Đại hội Đảng bộ Hà Nội lần thứ VII vòng 1 được tiến hành từ ngày 12 đến ngày 22-11-1976 tại Câu lạc bộ Lao Động cũ. Có 539 đại biểu thay mặt hơn 67 nghìn Đảng viên về dự.\nĐại hội vòng 2 họp từ ngày 25 tháng 5 đến 2-6-1977, gồm có 619 đại biểu thay mặt gần 70 nghìn đảng viên.\nCác đại biểu đã bầu Ban Chấp hành Đảng bộ Hà Nội khóa VII gồm có 44 đồng chí. Đồng chí Lê Vǎn Lương, uỷ viên Bộ Chính trị được bầu làm Bí thư Thành uỷ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','11', 'Lưu Thiếu Kỳ sinh nǎm 1898 trong một gia đình nông dân tỉnh Hồ Nam (Trung Quốc). Ông học trường Đại học cộng sản Phương Đông ở Mátxcơva. Sau khi về nước, ông trở thành một trong những lãnh tụ phong trào Công đoàn. Sau khi nước Cộng hoà nhân dân Trung Hoa thành lập 1949, Lưu Thiếu Kỳ được cử làm Chủ tịch Ban Thường trực Quốc hội, Uỷ viên Thường vụ Bộ Chính trị Trung ương Đảng. Tháng 12-1958, ông được cử làm Chủ tịch nước. Đồng thời, ông được bầu làm Phó chủ tịch Ban Chấp hành Trung ương Đảng cộng sản Trung Quốc. Ngày 12-11-1967, ông bị ốm và chết tại Khai Phong (Hà Nam)')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','12', 'Nhà thơ Thanh Tịnh tên thật là Trần Vǎn Ninh, sinh ngày 12-12-1911 quê ở thành phố Huế.\nTrước nǎm 1945, ông sáng tác thành công tập truyện: Quê mẹ (nǎm 1941), Chị và em (nǎm 1942), Ngậm ngải tìm trầm(1943).\nTrong thời kỳ kháng chiến chống Pháp, ông khai sinh ra hình thức tấu nói, rất được phổ biến trong nhân dân.\nTập truyện thơ Đi từ giữa một mùa sen dài gần 2000 câu kể về thời niên thiếu của Bác Hồ. Tác phẩm viết dưới hình thức kể truyện, mang đậm phong cách dân gian, phù hợp với giọng điệu tâm tình của thơ Thanh Tịnh.\nNhà văn Thạch Lam nhận xét về tác phẩm của Thanh Tịnh: \"Truyện ngắn nào hay đều có chất thơ, và bài thơ nào hay đều có cốt truyện\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','12', 'Nghệ sĩ Can Trường tên thật là Phạm Hữu Lộc, sinh ngày 12-12-1930 ở tỉnh An Giang, qua đời nǎm 1977, tại thành phố Hồ Chí Minh.\nTrong những nǎm đầu kháng chiến chống Pháp ở Nam Bộ, ông là diễn viên đoàn kịch Cửu Long Giang. Tập kết ra Bắc, ông là diễn viên đoàn kịch nói Nam Bộ. Nghệ sĩ Can Trường đã thể hiện thành công vai kịch xuất sắc như: Lênin trong \"Chuông đồng hồ chuông điện Kremlin\", Trương Định trong \"Trương Định\", Tám khoẻ trong \"Người ven đô\"\nCan Trường đã được Nhà nước tặng thưởng Huân chương Lao động hạng ba và danh hiệu Nghệ sĩ Nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '12','12', 'Cùng với các cuộc biểu tình, tuần hành của đồng bào Huế, Sài Gòn, Đà Nẵng, Đà Lạt... đòi lật đổ chính phủ Trần Vǎn Hương, ngày 12-12-1964 đông đảo các nhà sư, trong đó có Viện trưởng Viện Tǎng thống giáo hội Phật giáo thống nhất Việt Nam Thích Tinh Khiết tuyệt thực phản đối Mỹ và Chính phủ tay sai Trần Vǎn Hương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','12', 'Ngày 12-12-1963, Kenya giành được độc lập trong Khối Thịnh vượng chung và ông Jomo Kenyatta làm Thủ tướng. Một nǎm sau đó Kenya trở thành một nước Cộng hoà và ông Jomo Kenyatta lên làm Tổng thống.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','12', 'Ngày này năm 1969, Hy Lạp tự rút ra khỏi Hội đồng châu Âu trước khi sắp bị buộc phải ra khỏi Hội đồng này do đi theo chế độ quân sự.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '12','12', '12-12-1992, động đất xảy ra tại một dải đảo nhỏ thuộc tỉnh East Nusa Tenggara, nặng khoảng 6,8 độ rit-te làm 4390 người thiệt mạng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','01', 'Ngày 13-1-1941, Đội Cung với 50 anh em tù binh lính đóng tại Đô Lương (Nghệ An) nổi dậy cướp đồn. Sau đó kéo về định chiếm thành Vinh nhưng việc chưa thành thì đã bị bắt. Đội Cung và 9 đồng chí đã bị thực dân Pháp đưa về hành hình tại Chợ Rạng vào sáng ngày 25-4-1941.\nCuộc binh biến Rạng - Đô Lương thất bại nhưng có tác dụng kích thích tinh thần ái quốc làm đế quốc bối rối. Cùng với khởi nghĩa Bắc Sơn, Nam Kỳ, cuộc biến Đô Lương là những tiếng súng báo hiệu cho cuộc khởi nghĩa toàn quốc, là bước đấu tranh bằng vũ lực của các dân tộc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','01', 'Từ ngày 13 đến ngày 28 tháng giêng nǎm 1953, Bộ Tư lệnh Liên khu V đã tổ chức, chỉ đạo chiến dịch An Khê.\nNgày 13 ta tiến công tiêu diệt các cứ điểm Cửu An, Eo Gió Tứ Thuỷ. Ngày 21 đồn Thượng An bị diệt. Ngày 25 ta chặn đánh một đoàn xe vận tải 25 chiếc ở phía Tây An Khê, diệt gọn một đại đội địch. Ngày 28 chiến dịch An Khê kết thúc.\nKết quả là ta tiêu diệt một tiểu đoàn sơn chiến, bốn đại đội ở cứ điểm, đánh thiệt hại nặng tiểu đoàn dù số 1 của Pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','01', 'Ngày 13-1-1960 Hồ Chủ tịch đã ký sắc lệnh công bố Luật hôn nhân và gia đình nhằm tiếp tục xoá bỏ những tàn tích của chế độ hôn nhân và gia đình phong kiến, đặt cơ sở pháp lý để xây dựng gia đình hạnh phúc, dân chủ, bình đẳng, hoà thuận trong chế độ xã hội mới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','01', 'Ngày 13-1-1969, Ban Bí thư Trung ương Đảng ra chỉ thị về việc bồi dưỡng và noi gương người tốt việc tốt. Ban chỉ thị nêu rõ: \"Việc noi gương và cổ vũ người tốt việc tốt không những chỉ có ý nghĩa động viên mọi người hoàn thành các nhiệm vụ Cách mạng trước mắt mà còn là một trong những biện pháp cơ bản để xây dựng Đảng và các lực lượng nòng cốt của Cách mạng, xây dựng con người mới và cuộc sống mới\". Ban Bí thư còn quyết định cho các nhà xuất bản biên soạn loại sách \"Người tốt việc tốt\" nhằm phổ biến rộng rãi để mọi người học tập và làm theo.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','01', 'Ngày 13-1-1973 Uỷ ban thường vụ Quốc hội quyết định thưởng huân chương cho 140 địa phương và các đơn vị có nhiều thành tích xuất sắc trong chiến đấu, phục vụ chiến đấu, dũng cảm cứu người, cứu tài sản của nhà nước, của nhân dân, nhanh chóng khắc phục hậu quả của chiến tranh kể từ khi giặc Mỹ đánh phá trở lại miền Bắc, nhất là trong những ngày từ 18 đến 29-12-1972.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '13','01', '13-1-1915, xảy ra một trận động đất tại Avezzano, Italia, làm thiệt mạng 30.000 người.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','02', 'Ngày 13-2-1964 Chủ tịch Hồ Chí Minh đến thǎm và nói chuyện với đại đội 130 pháo cao xạ bảo vệ Hà Nội.\nBác chỉ thị: Các chú phải luôn luôn cảnh giác và lúc nào cũng sẵn sàng chiến đấu, phải kiên quyết bắn rơi máy bay địch nếu chúng liều lĩnh xâm phạm vùng trời miền Bắc nước ta. Muốn làm được như vậy, các chú phải ra sức rèn luyện kỹ thuật cho giỏi. Cán bộ, chiến sĩ cần đoàn kết chặt chẽ, thương yêu nhau như con một nhà.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','02', 'Sáng ngày 13-2-1964 (tức là mồng Một Tết nguyên đán Giáp Thìn), Chủ tịch Hồ Chí Minh đã về thǎm và chúc tết đồng bào xã Liên Hà, huyện Đông Anh, Hà Nội.\nBác Hồ đã vào thǎm gia đình cụ Nguyễn Thế Oanh có 4 con trai đi bộ đội (3 người đã hy sinh), ông Đinh Thiệm, chủ nhiệm hợp tác xã nông nghiệp. \nNói chuỵện với bà con tại đình làng Lỗ Khê (xã Liên Hà), Bác Hồ khen ngợi xã nhà đã cần kiệm xây dựng hợp tác xã và cǎn dặn: \"Đây mới là thắng lợi bước đầu, không được tự mãn, chủ quan. Không phải cái gì cũng phải tốt cả, mà còn có những cái kém. Đó là vệ sinh, chǎn nuôi, trồng màu và trồng cây\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '13','02', 'Ngày 13-2-1862, Maicơn Pharađây đã làm thí nghiệm thứ 16.041 của mình. Ông sinh nǎm 1791 và mất nǎm 1867.\nÔng là một nhà vật lý nổi tiếng người Anh, đã phát hiện ra hiện tượng cảm ứng điện từ, tìm ra định luật điện phân, đã đưa ra cách biểu diễn điện trường và từ trường bằng đường sức và có nhiều công trình khoa học khác.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '13','02', 'Vinhem Rixát Vácnơ (Wilhemlm Richard Wagner), nhà soạn nhạc nổi tiếng Đức, sinh ngày 22-5-1813. Nǎm 20 tuổi ông đã trở thành nhạc sĩ chuyên nghiệp. Các nhạc phẩm tiêu biểu: \"Rienzi và chiếc tàu ma\", \"Tannhauser\", \"Chiếc vòng của giống người Nibelungen\", \"Trista và Isode\", \"Những ca sĩ thành Nuremberg\". \nÔng mất ngày 13-2-1883.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '13','02', 'Anphôngxơ Béctilông (Alphonse Bertillon) sinh nǎm 1853, mất ngày 13-2-1914.\nÔng là nhà bác học Pháp đã lập ra \"Phép đo người nhận dạng\". Hệ thống nhận dạng tội phạm hình sự được toà án Pari áp dụng từ nǎm 1882. Bằng nhiều phát minh và sáng kiến xuất sắc, ông đã đóng góp lớn lao vào sự tiến bộ của kỹ thuật cảnh sát hình sự như lấy dấu tay, đo sọ.v.v...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','03', 'Từ tháng 2-1908, nhân dân huyện Đại Lộc (tỉnh Quang Nam cũ) kéo lên huyện lỵ, rồi lên tỉnh, biểu tình trước toà công sứ Pháp, đòi giảm ngày đi phu, đòi giảm sưu thuế. Có ngày 7-8 ngàn người đi biểu tình. Ngày này nǎm đó, 13-3-1908, số người đi biểu tình lên tới 10 ngàn. Họ đã bao vây dinh tổng đốc tỉnh Quảng Nam, phủ lỵ Tam Kỳ, Thǎng Bình, Điện Bàn, Duy Xuyên, lùng bắt và trừ khử bọn cường hào gian ác.\nPhong trào chống đi phu và chống sưu thuế kéo dài tới tháng 8-1908 và lan ra nhiều tỉnh ở miền Trung như: Quảng Ngãi, Bình Định, Phú Yên, Thừa Thiên, Hà Tĩnh, Nghệ An, Thanh Hoá. Mục tiêu đấu tranh vẫn là không đi phu, không nộp thuế cho Pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','03', 'Ngày 13-3-1930, công nhân nhà máy cưa Thái Hợp - Thành phố Vinh đã tiến hành đấu tranh với chủ. Cuộc đấu tranh do chi bộ Cộng sản và Công hội đỏ chỉ đạo. Tuy quy mô nhỏ (có 50 người tham gia) nhưng với yêu sách đòi chủ phải thay một tên cai độc ác đã nhanh chóng lan chuyền thành những cuộc đấu tranh liên tục của công nhân Vinh - Bến Thuỷ - Mở đầu cho cuộc đấu tranh của giai cấp công nhân Nghệ Tĩnh trong cao trào cách mạng 30-31.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','03', 'Ngày 13 và 14-3-1937 tại xã Tân Thới Nhứt (Bà Điểm, Gia Định) nay thuộc thành phố Hồ Chí Minh đã diễn ra Hội nghị mở rộng Ban chấp hành Trung ương Đảng Cộng sản Đông Dương. Nội dung của hội nghị được thể hiện qua các vǎn kiện sau:\nThông báo gửi các cấp bộ Đảng - đề cập vấn đề như tham gia tuyển cử, lập mặt trận thống nhất nhân dân Đông Dương.\nChủ trương tổ chức đổi mới của Đảng như: Thay đổi và kiện toàn tổ chức Đảng cũng như các tổ chức quần chúng cho phù hợp với tình hình mới của Cách mạng Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','03', 'Ngày 13-3-1954 được coi là ngày mở màn của chiến dịch Điện Biên Phủ.\nTừ đông xuân 1953 - 1954 ta đã có những đòn tấn công buộc địch phải phân tán lực lượng cơ động trên các chiến trường, trong khi đó ta tập trung chủ lực về hướng Tây Bắc. Nhạy cảm trước ý đồ chiến lược của ta, địch tǎng cường lực lượng biến Điện Biên Phủ thành một tập đoàn cứ điểm mạnh. Nhận định đây là một cơ hội lớn để tiêu diệt sinh lực địch, ta quyết định mở chiến dịch tiến công Điện Biên Phủ.\nSau khi hoàn thành các công tác chuẩn bị, ngày 13-3, ta nổ súng tiến công tuyến phòng ngự vòng ngoài. Chiến dịch quyết chiến vĩ đại bắt đầu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '13','03', 'Trong nhiều thế kỷ, trên thế giới vẫn còn tệ phân biệt chủng tộc - Ngày 13-3-1969, Công ước của Liên hiệp quốc về việc huỷ bỏ tất cả các hình thức phân biệt chủng tộc bắt đầu có hiệu lực.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','04', 'Tối 13-4-1946, Hồ Chủ tịch đã đến thǎm một lớp bình dân học vụ ở phố Hàng Trống, Hà Nội.\nTrong cuốn hồi ký Những chặng đường lịch sử, Đại tướng Võ Nguyên Giáp kể lại lớp học đó như sau: \"Bàn học là những cánh cửa kê tạm. Giáo viên là một người trẻ tuổi đeo kính trắng đang cầm phấn viết mấy chữ mẫu trên bảng đen. Người học thuộc nhiều lứa tuổi, ǎn mặc khác nhau. Những mái đầu cặm cụi. Mái tóc bạc phơ của một cụ già vận áo dài ta bên bộ tóc đen nhánh của một chú bé mặc quần cộc. Bác đứng chống gậy tre nhìn quang cảnh này tỏ vẻ rất xúc động. Bác khuyến khích mọi người và nói: \"Cả người dạy và người học đều là những chiến sĩ trên mặt trận chống giặc dốt\".\nLà người quan tâm tới việc chống nạn mù chữ và nâng cao trình độ vǎn hoá của nhân dân, Bác Hồ đã 13 lần đến thǎm các lớp bình dân học vụ và bổ túc vǎn hoá ở các đường phố, khu lao động và nhà máy tại Hà Nội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','04', 'Ông Bùi Bằng Đoàn qua đời ngày này nǎm 1955.\nÔng là nhân sĩ, sinh nǎm 1889, quê ở huyện Ứng Hoà, tỉnh Hà Tây, đỗ cử nhân nǎm 1906. Nǎm 1911, ông làm chi huyện, rồi tuần phủ tỉnh Ninh Bình, đến nǎm 1933, làm Thượng thư Bộ Hình. Sau cách mạng tháng Tám nǎm 1945, ông Bùi Bằng Đoàn được bầu làm đại biểu Quốc hội khoá I, được cử vào Ban Thường trực Quốc hội, rồi làm Trưởng ban Thường trực Quốc hội cho đến khi từ trần. Sinh thời Bác Hồ đã có thơ tặng cụ Bùi Bằng Đoàn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','04', '13-4-1962, nhà máy xe lửa Gia Lâm (Hà Nội) đã phát động phong trào \"Ngày thứ bảy đấu tranh thống nhất\". Phong trào này đã nhanh chóng lan rộng ra khắp các nhà máy, cơ quan, các ngành trên miền Bắc, và được Tổng Công đoàn chỉ đạo thành phong trào lấy tên là: \"Ngày thứ bảy đẩy mạnh sản xuất, thống nhất đất nước\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','04', 'Ngày 13-4-1967 Hồ Chủ tịch gửi thư khen bộ đội pháo binh. Người biểu dương: \"Pháo binh ta có truyền thống oanh liệt, chân đồng vai sắt, đánh giỏi, bắn trúng\". Trong thư khen, Người cǎn dặn: \"Phải ra sức học tập và thi đua với pháo binh quân giải phóng Miền Nam tài giỏi, anh hùng\".\nXứng đáng với lời khen của Hồ Chủ tịch, pháo binh ta bắn trúng nhiều tàu chiến Mỹ, bắn chìm nhiều tàu biệt kích Mỹ và tay sai, đánh trả pháo binh địch ở bờ Nam khu phi quân sự, diệt nhiều giặc Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','04', 'Ngày 13-4-1967, quân và dân miền Đông Nam Bộ đã đánh bại cuộc hành quân quy mô lớn của Mỹ mang tên Gianxơn Xity, gồm 45 nghìn quân thuộc 8 lữ đoàn Mỹ. \nHơn 14 tên địch bị loại khỏi vòng chiến đấu, hơn 1000 xe quân sự và 90 khẩu pháo hạng nặng bị phá huỷ; 167 máy bay Mỹ bị phá huỷ và bắn rơi. Thượng tướng Mỹ Lêman chỉ huy cuộc hành quân này, bị cách chức.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','05', 'Cầm Bá Thước, người dân tộc Thái, sinh nǎm 1859 tại huyện Thường Xuân, tỉnh Thanh Hoá, trong một gia đình làm lang đạo.\nNǎm 1885, hưởng ứng phong trào Cần Vương chống Pháp, ông đã chọn quê hương Trình Vạn làm cǎn cứ khởi nghĩa. Lực lượng nghĩa quân phát triển nhanh chóng, không chỉ hoạt động ở vùng Thường Xuân, Lang Chánh mà còn ở Ngọc Lặc, Như Xuân, Quan Hoá (thuộc Thanh Hoá) và còn phối hợp với cả nghĩa quân Lang Vǎn Thiết, Lang Vǎn Hạnh ở miền Tây Nghệ An. Nghĩa quân do Cầm Bá Thước chỉ huy đã tập kích các đồn lẻ của địch, đánh các cánh của địch đi càn quét trong vùng.\nTháng 5-1895, bọn thực dân Pháp đã tập trung lực lượng đánh lớn vào cǎn cứ của nghĩa quân ở Hòn Bòng. Sau 4 ngày bọn giặc mới vào được trung tâm cǎn cứ. Cầm Bá Thước sa vào tay giặc ngày 13-5-1895.\nGiặc Pháp đưa ông về quê, dụ dỗ và mua chuộc ông nhưng không lay chuyển được ông. Chúng đã xử tử Cầm Bá Thước khi ông mới 36 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','05', 'Ngày 13-5-1954 Lễ mừng chiến thắng Điện Biên Phủ được tổ chức trọng thể tại cánh đồng Mường Thanh - Trung tâm tập đoàn cứ điểm của địch vừa bị tiêu diệt. Đại tướng Võ Nguyên Giáp - Bộ trưởng Bộ quốc phòng, Tư lệnh chiến dịch thay mặt Trung ương Đảng và Chính phủ tuyên dương công trạng cac đơn vị tham gia chiến dịch, các đơn vị trong toàn quân, đồng bào ở địa phương trong cả nước đã tạo nên chiến thắng vĩ đại Điện Biên Phủ. Nhân dịp này đại đoàn 312 đã được nhận cờ \"Quyết chiến quyết thắng\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','05', 'Theo hiệp định Giơnevơ 1954 Quân Pháp ở miền Bắc phải rút về khu vực tập kết tại Hải Phòng và chuẩn bị rút toàn bộ quân viễn chinh khỏi Đông Dương. Chúng buộc phải cuốn gói nhưng lại cấu kết với đế quốc Mỹ và tay sai phản động thực hiện chiến dịch cưỡng ép di cư, nhằm vơ vét sức người, sức của vào miền Nam chuẩn bị cho cuộc chiến tranh của đế quốc Mỹ đối với ta. Hải Phòng trở thành điểm nóng của toàn quốc.\nPhong trào đấu tranh của công nhân và các tầng lớp nhân dân Hải Phòng phát triển mạnh mẽ. Cán bộ, bộ đội đi sát những người bị dụ dỗ, vạch trần âm mưu thủ đoạn của địch, vận động hàng chục vạn người bỏ các trại di cư. Công nhân đoàn kết, đấu tranh chống tháo dỡ di chuyển máy móc. Nông dân vận động được hai vạn binh lính ngụy bỏ hàng ngũ địch. Học sinh trí thức sôi nổi tham gia đấu tranh bảo vệ học đường, công trình vǎn hoá. \nNgày 13-5-1955 lịch sử, khắp nội ngoại thành Hải Phòng sống lại không khí sôi nổi hào hứng của những ngày Cách mạng tháng Tám. Quân ta đã tiến vào tiếp quản Hải Phòng thắng lợi. Hải Phòng sạch bóng thù. Tên lính viễn trinh cuối cùng của thực dân Pháp xâm lược đã rút khỏi, miền Bắc hoàn toàn giải phóng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','05', 'Ngày 13-5-1959, Hồ Chủ tịch đã về thǎm Nhà máy in Tiến Bộ.\nNhà máy in này đã được khánh thành nǎm 1958 do nước Cộng hoà dân chủ Đức giúp ta xây dựng tại nhà số 175 phố Nguyễn Thái Học, Hà Nội.\nNhà máy xây dựng trên nền nhà tiền, một nhà tù lớn của thực dân Pháp tại Hà Nội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','05', 'Ngày 13-5-1968, khai mạc phiên họp đầu tiên của cuộc hội đàm giữa đại diện Chính phủ Việt Nam Dân chủ cộng hoà và Mỹ tại Pari. Trong phiên họp, Bộ trưởng Xuân Thuỷ nhấn mạnh mục đích của cuộc nói chuyện này \"là để xác định với phía Mỹ việc ném bom và mọi hành động chiến tranh khác chống nước Việt Nam dân chủ cộng hoà\". Bộ trưởng nêu rõ những thất bại ngày càng nặng của đế quốc Mỹ và tố cáo những luận điệu hoà bình của Tổng thống Mỹ. Bộ trưởng nêu bật 4 điểm chính nghĩa của Chính phủ Việt Nam.\nHội nghị Pari đã mở đầu thời kỳ ta tiến công trực tiếp về ngoại giao trên bàn hội nghị, là một diễn đàn rất quan trọng để ta vạch mặt xâm lược tàn bạo và ngoan cố của đế quốc Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','05', 'Ngày 13-5-1975, ngành bưu điện đã chuyển chuyến thư đầu tiên của đồng bào Sài Gòn, Gia Định gồm 15.600 bức thư ra miền Bắc sau hơn 20 nǎm gián đoạn. Cũng từ ngày 13-5-1975, ngành bưu điện đã nhận chuyển thư từ miền Bắc vào Sài Gòn.\nĐồng thời, trong ngày 13-5-1975, chuyến tàu biển đầu tiên có sức chở 10.000 tấn của miền Bắc vào thǎm đồng bào Sài Gòn - Gia Định đã cập bến cảng Sài Gòn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','06', 'Phùng Vǎn Minh, còn có tên là Quận Cồ, sinh nǎm 1849, người Nam Định, theo ông nội di cư lên sống ở Sơn Tây. \nSau khi thực dân Pháp đánh chiếm tỉnh Sơn Tây cũ, Quận Cồ cùng với một số người yêu nước tổ chức nhân dân các huyện nổi lên chống Pháp.\nĐồng bào hầu hết các tỉnh Sơn Tây cũ và một số huyện của tỉnh Vĩnh Yên đã ủng hộ nghĩa quân của Quận Cồ. Giặc Pháp mở nhiều cuộc hành quân nhằm tiêu diệt nghĩa quân nhưng đều thất bại. Nghĩa quân đã duy trì cuộc kháng chiến được gần 7 nǎm.\nNgày 13-6-1889, trong trận đánh đồn Vật Lại, Quận Cồ Phùng Vǎn Minh bị một tên tay sai của Pháp gài vào hàng ngũ nghĩa quân, đánh lén, rồi chặt đầu ông đem về nộp cho giặc để lĩnh tiền thưởng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','06', 'Lương Vǎn Can sinh nǎm 1854, quê ở Nhị Khê, Thường Tín, Hà Đông.\nNǎm 20 tuổi ông đỗ cử nhân và dạy học ở Hà Nội. Nǎm 1908 ông liên kết với các đồng chí lập ra trường Đông Kinh Nghĩa Thục, khởi xướng phong trào Duy Tân. Các sĩ phu yêu nước và nhân dân tiến bộ nhiệt liệt tham gia, khiến thực dân hoảng hốt khủng bố, ra lệnh đóng cửa trường bắt giam một số người. Nǎm 1914, nhân vụ ném tạc đạn ở khách sạn Hà Nội, thực dân Pháp bắn hơn trăm người, trong đó có ông và kết án ông 10 nǎm biệt sứ, lưu đầy ở Nông Pênh Cǎmpuchia. Đến 25-11-1921 ông mới về lại Hà Nội và tiếp tục dạy học. Ông mở trường Ôn Như và chuyên tâm soạn sách. Các tác phẩm chính của ông gồm có: \"Quốc sư phạm lịch sử\", \"Hán tự tuyệt kính\", \"Ấm học tùng đàm\", \"Gia huấn\", \"Hán tự quốc âm\", \"Hạch đàm loại ngữ\", \"Châu thư loại ngữ\".\nÔng mất tại Hà Nội ngày 13-6-1927, thọ 73 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','06', 'Trước khi rời nước Pháp, đồng chí Nguyễn Ái Quốc để lại thư cho các bạn cùng hoạt động: \"Trong cuộc chiến đấu của chúng ta, chúng ta không cô độc, vì chúng ta có tất cả dân tộc của chúng ta ủng hộ và vì những người Pháp dân chủ, những người Pháp chân chính đứng bên cạnh chúng ta. Chúng ta phải làm gì?... Đối với tôi câu trả lời đã rõ ràng: trở về nước, đi vào quần chúng thức tỉnh họ, tổ chức họ, đoàn kết họ, huấn luyện họ đưa họ ra đấu tranh giành tự do độc lập\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','06', 'Ngày 13-6-1955, Hồ Chủ tịch đã tới nhà thương Đồn Thuỷ (nay là bệnh viện Hữu Nghị và Viện Quân y 108) và nhà thương Phủ Doãn (nay là bệnh viện Việt Đức).\nTại hai bệnh viện, Bác Hồ đã ân cần hỏi thǎm người bệnh và bệnh binh động viên họ yên tâm chữa bệnh, cǎn dặn các thầy thuốc coi người bệnh như những người thân của mình, tận tình chữa trị cho họ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','06', 'Ngày 13-6-1975, toàn lãnh thổ Việt Nam thống nhất dùng giờ Đông Dương theo múi giờ thứ 7.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '13','06', 'Giêmxơ Clêx Mắcoen (James Clerk Maxwell) sinh ngày 13-6-1831 ở Xcốtlen và qua đời ngày 5-11-1879.\nMắcoen là nhà vật lý đã chứng minh rằng: ở cùng nhiệt độ, động nǎng trung bình của các phân tử chất, không tuỳ thuộc bản chất của chúng. Ông đã tìm ra công thức điện từ khi mạch điện xê dịch trong từ trường, đề ra thuyết điện từ về ánh sáng và phương trình tổng quát về sự truyền điện từ trường.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','07', 'Ngày 13-7-1885, vua Hàm Nghi (vua yêu nước chống Pháp) đã ra chiếu Cần Vương, kêu gọi nhân dân ra sức phò vua, cứu nước thoát khỏi cảnh ngoại xâm.\nCả nước đã hưởng ứng chiếu Cần Vương. Phong trào vũ trang chống Pháp do các sĩ phu cầm đầu, sôi nổi khắp Trung Kỳ với các cuộc khởi nghĩa của Phan Đình Phùng, Nguyễn Thiện Thuật, Mai Xuân Thưởng v.v... Phòng trào Cần Vương còn kéo dài đến hết thế kỷ XIX.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','07', 'Tháng 7-1936, Hội nghị Ban chấp hành Trung ương Đảng cộng sản Đông Dương đã tiến hành ở Thượng Hải (Trung Quốc). Hội nghị dự trên nghị quyết của đại hội lần thứ 7 quốc tế cộng sản, cǎn cứ vào tình hình thực tế Việt Nam để định ra đường lối và phương pháp đấu tranh thích hợp. Hội nghị xác định: Mục tiêu trực tiếp, trước mắt của Cách mạng Đông Dương là đấu tranh chống chế độ phản động thuộc địa, chống phát xít và chiến tranh, đòi tự do, dân chủ, cơm áo và hoà bình. Kẻ thù chủ yếu trước mắt là bọn phản động thuộc địa và bè lũ tay sai của chúng; phương pháp đấu tranh là kết hợp các hình thức công khai và bí mật, hợp pháp và bất hợp pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','07', 'Để tổng kết những kinh nghiệm quý báu, nhất là kinh nghiệm chống địch càn quét và đề ra những nhiệm vụ cụ thể và chỉ đạo chiến lược, chiến thuật, tổ chức, xây dựng đưa chiến tranh du kích phát triển đến một trình độ cao, ngày 13-7-1952 ta đã mở Hội nghị chiến tranh du kích. Hồ Chủ tịch đã tới dự và trong bài nói tại Hội nghị. Người đã nêu rõ mục đích của chiến tranh du kích không phải ǎn to đánh lớn và phải tỉa dần, đánh làm sao cho giặc ǎn không ngon ngủ không yên, bị hoang mang về tinh thần, vật chất rồi đi đến chỗ tiêu diệt, nhiệm vụ của du kích là phá âm mưu \"lấy chiến tranh nuôi chiến tranh\" của địch. \"Phá được âm mưu đó là góp phần lớn vào công việc tổng phản công\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','08', 'Viện sĩ - Hoạ sĩ Trần Vǎn Cẩn, sinh ngày 13-8-1919 tại thị xã Kiến An, nguyên quán ở Từ Sơn, tỉnh Bắc Ninh, qua đời nǎm 1994 ở Hà Nội.\nÔng được những người yêu nghệ thuật tạo hình biết đến từ trước nǎm 1945. Sau Cách mạng tháng Tám, ông tích cực tham gia các hoạt động hội hoạ phục vụ kháng chiến. Tuy ông tốt nghiệp trường cao đẳng Mỹ thuật Đông Dương, nhưng ông luôn luôn tìm tòi lối vẽ truyền thống Việt Nam kết hợp với lối vẽ Hàn lâm Pháp.\nTrần Vǎn Cẩn có nhiều tác phẩm tiêu biểu như: \"Em Thuý\" (sơn dầu), \"Gội đầu\" (khắc gỗ), \"Xuống đồng\" (tranh lụa), \"Tát nước đồng chiêm\" (sơn mài), \"Nữ dân quân vùng biển\" (sơn dầu)...\nCác tác phẩm của Trần Vǎn Cẩn đã được trưng bày ở nhiều cuộc triển lãm trong nước và nước ngoài.\nÔng không chỉ là một hoạ sĩ nổi tiếng mà còn là một người thầy tận tụy, hết lòng đào tạo các nghệ sĩ tạo hình nước ta.\nNǎm 1996, cố hoạ sĩ Trần Vǎn Cẩn được Nhà nước trao giải thưởng Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','08', 'Ngay sau khi được tin Nhật đầu hàng, Trung ương Đảng và Tổng bộ Việt Minh đã thành lập Uỷ ban khởi nghĩa toàn quốc gồm 5 đồng chí: Trường Chinh, Võ Nguyên Giáp, Trần Đǎng Ninh, Lê Thanh Nghị, Chu Vǎn Tấn, do Tổng bí thư Trường Chinh trực tiếp phụ trách.\n23 giờ ngày 13-8-1945, Uỷ ban đã ban bố Quân lệnh số 1, hạ lệnh tổng khởi nghĩa. Vǎn kiện lịch sử này kêu gọi: \"Hỡi quân dân toàn quốc! Giờ Tổng khởi nghĩa đã đến, cơ hội có một của nhân dân Việt Nam vùng dậy giành lấy quyền độc lập cho nước nhà!\". Vǎn kiện thông báo việc thành lập Uỷ ban khởi nghĩa và kêu gọi toàn thể nhân dân và các lực lượng vũ trang cách mạng hãy hành động theo mệnh lệnh của Uỷ ban khởi nghĩa, và thúc giục: \"Chúng ta phải hành động nhanh với một tinh thần vô cùng quả cảm, vô cùng thận trọng! Tổ quốc đang đòi hỏi những hy sinh lớn lao của các bạn! Cuộc thắng lợi hoàn toàn nhất định sẽ về ta!\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','08', 'Từ ngày 13 đến ngày 15-8-1945, Hội nghị toàn quốc Đảng cộng sản Đông Dương họp tại Tân Trào. Tham dự Hội nghị có đại biểu các Đảng bộ điạ phương và một số đại biểu hoạt động của nước ngoài. Hội nghị họp đúng vào tình thế Cách mạng trở nên khẩn trương, như Nghị quyết hội nghị vạch rõ: \"Những điều kiện khởi nghĩa ở Đông Dương đã chín muồi... Toàn dân tộc đang sôi nổi đợi chờ khởi nghĩa giành quyền độc lập\". Sau khi phân tích tình hình thế giới và trong nước, bản Nghị quyết đề cập tới hàng loạt các vấn đề cụ thể và cấp bách như: Công tác ngoại giao, tuyên truyền cổ động, nhiệm vụ quân sự, kinh tế, giao thông, vận động các giới, đảng phái. Hội nghị cũng thông qua 10 chính sách lớn của Việt Minh, coi đó là một mục tiêu cơ bản và trước mắt của toàn Đảng, toàn dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','08', 'Nhạc sĩ Cao Vǎn Lầu sinh nǎm 1892 ở tỉnh Long An và mất ngày 13-8-1976 tại thành phố Hồ Chí Minh.\nNǎm 20 tuổi, ông bắt đầu đi hát với các bạn.\nNǎm 26 tuổi ông sáng tác bài \"Dạ cổ hoài lang\" (nghĩa là: \"Nghe tiếng trống đêm nhớ người tình\"). Từ bài Vọng cổ này đã hình thành dòng dân ca Cải lương Nam Bộ thắm đượm lòng người và tình yêu quê hương đất nước. Nhạc sĩ Cao Vǎn Lầu được coi là người khởi xướng dân ca Cải lương với sự kết hợp nhuần nhuyễn dân ca Nam Bộ với ca cổ.\nCách mạng Tháng Tám thành công, ông tham gia công tác Mặt trận liên Việt ở Bạc Liêu. Nǎm 1947, ông đã cứu được một số cán bộ bị bắt giữ. Suốt cuộc đời ông thà chịu nghèo, chứ không phục vụ bọn cướp nước và bán nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '13','08', 'Caclipnêch là nhà hoạt động xuất sắc trong phong trào công nhân Đức, lãnh tụ phái tả Đảng xã hội Dân chủ Đức, người sáng lập ra Liên minh Xpactacut và Đảng cộng sản Đức.\nÔng sinh ngày 13-8-1871. Nǎm 1903 ông thành lập tổ chức xã hội chủ nghĩa đầu tiên của thanh niên và sau đó đề xướng việc tổ chức Đại hội thanh niên quốc tế. Tại Đại hội, ông đọc báo cáo \"Chủ nghĩa quân phiệt và chống chủ nghĩa quân phiệt\". Ông bị kết án tù. Khi ra tù, ông tiếp tục đấu tranh chống chủ nghĩa cơ hội trong Đảng xã hội. Nǎm 1912 ông là Đại biểu quốc hội và hoạt động với tư cách của người theo chủ nghĩa quốc tế tuyên truyền chống chiến tranh và chủ nghĩa đế quốc.\nKhi nước Đức sắp đầu hàng các nước Đồng Minh trong chiến tranh thế giới I, Cáclipnêch cùng với các đồng chí trong Liên minh Xpactacut kịp thời lãnh đạo công nhân và binh lính cách mạng ở Beclin đứng lên lật đổ chính thể quân chủ của Vinhem II và thiết lập chế độ Cộng hoà Xô Viết. Chính phủ mới thành lập sau Cách mạng do phái hữu của Đảng xã hội dân chủ nắm quyền đã phản bội lại phong trào cách mạng, đàn áp cuộc khởi nghĩa của quần chúng và giết hại một cách dã man Caclipnêch vào ngày 15-1-1919.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '13','08', 'Ngày 13-8-1927 là ngày sinh của Phi đen Caxtrô, nhà hoạt động cách mạng của Cuba, Bí thư thứ nhất Đảng cộng sản, Chủ tịch Hội đồng Nhà nước, Chủ tịch Hội đồng Bộ trưởng và Tổng tư lệnh lực lượng vũ trang Cách mạng Cuba.\nNǎm 1945 ông học đại học Luật, 1950 đỗ tiến sĩ luật học.\nNǎm 1952 ông tổ chức \"Phong trào Cách mạng. Ngày 26-7-1953, ông cùng các đồng chí trong \"Phong trào cách mạng\" tấn công vào trại lính Môncađa. Cuộc khởi nghĩa thất bại, ông bị bắt và bị kết án 15 nǎm tù. Hai nǎm sau, khi được trả tự do, ông tập hợp lực lượng Cách mạng, đổi tên \"Phong trào cách mạng\" thành \"Phong trào 26 tháng bảy\".\nNǎm 1956, Phiđen cùng 82 chiến sĩ Cách mạng Cuba từ Mêhicô vượt biển trở về tổ quốc trên con tàu mang tên Granma, xây dựng cǎn cứ du kích.\nNgày 1-1-1959, phối hợp chặt chẽ với tổng bãi công chính trị của công nhân và đấu tranh của nhân dân, nghĩa quân tiến vào thủ đô LaHabana lật đổ chế độ độc tài Batixta. Cách mạng thắng lợi, nhân dân Cuba dưới sự lãnh đạo của Phiđen đã tiến hành xây dựng chủ nghĩa xã hội.\nNgày 17-4-1961 quân dân Cuba dưới sự chỉ huy của Tổng tư lệnh Phiđen đã tiêu diệt hoàn toàn quân lính đánh thuê của Mỹ đổ bộ trên bãi Hirôn. Ông đề xướng thành lập \"Tổ chức cách mạng thống nhất\", sau đổi tên thành Đảng cộng sản Cuba.\nPhiđen Caxtơrô là biểu tượng của chủ nghĩa anh hùng cách mạng, người kiên quyết đấu tranh chống mọi thế lực phản động, lãnh đạo nhân dân Cuba đi theo con đường xã hội chủ nghĩa. Với cách mạng Việt Nam, Phiđen hết lòng ủng hộ sự nghiệp đấu tranh chống Mỹ cứu nước của nhân dân ta, với câu nói nổi tiếng: \"Vì Việt Nam, chúng ta hiến dâng tất cả máu của mình!\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','09', 'Giáo sư Trần Đại Nghĩa tên thật là Phạm Quang Lễ, sinh ngày 13-9-1913 ở tỉnh Vĩnh Long, mất ngày 9-8-1997 tại Thành Phố Hồ Chí Minh.\nNăm 1935, ông sang Pháp học qua các trường đại học kỹ nghệ cầu cống, điện, Viện nghiên cứu máy bay và đại học Xoócbon. Sau đó ông làm việc ở công trường cầu, xưởng chế tạo máy điện và xưởng chế tạo máy bay. Năm 1942 ông sang Đức làm việc trong xưởng chế tạo máy bay và Viện nghiên cứu Vũ khí.\nNăm 1946, khi Hồ Chủ tịch sang Pháp, ông về nước với Bác, được giao chức vụ Cục trưởng Cục quân giới. Trong cuộc kháng chiến chống thực dân Pháp, giáo sư Trần Đại Nghĩa đã nghiên cứu và chỉ đạo kỹ thuật chế tạo: súng Badôca, súng không giật (DKZ) và đạn bay. Các công trình này của ông đã được Nhà nước tặng Giải thưởng Hồ Chí Minh.\nÔng được phong hàm Thiếu tướng từ năm 1948. Năm 1952, được tuyên dương Anh hùng lao động. Năm 1966 được bầu là Viện sĩ nước ngoài của Viện Hàn lâm khoa học Liên Xô. Giáo sư Trần Đại Nghĩa là một trí thức lớn, giàu lòng yêu nước, có nhiều cống hiến to lớn trong việc chế tạo vũ khí phục vụ hai cuộc kháng chiến của nhân dân ta chống các đế quốc xâm lược nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','09', 'Sau khi thoát khỏi nhà tù Tưởng Giới Thạch, ngày 13-9-1943 lãnh tụ Hồ Chí Minh đã bắt liên lạc với Hội Giải phóng. Qua đó tiếp xúc với tổ chức chống Nhật - Pháp ở Liễn Châu và liên lạc với Đảng ta, tiếp tục lãnh đạo Cách mạng.\nHội Giải phóng là tổ chức Việt Nam dân chúng giải phóng vận động được thành lập trong giới Việt kiều yêu nước ở Vân Nam (Trung Quốc). Mục đích của hội là đoàn kết Việt kiều đánh Nhật - Pháp đòi Việt Nam độc lập. Hội hoạt động công khai, cho xuất bản báo \"Giải phòng\", tổ chức hoạt động tương tế, mở lớp học. Cuối năm 1942, sau khi liên lạc với Cách mạng trong nước, Hội nghị được coi như một bộ phận của Mặt trận Việt Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','09', 'Từ 13-9-1950, Bác Hồ rời Sở Chỉ huy chiến dịch để đến mặt trận Đông Khê trực tiếp theo dõi và động viên bộ đội đánh trận mở màn chiến dịch. Người đã chỉ thị: \"Dù khó khăn đến đâu cũng kiên quyết khắc phục đánh cho kỳ thắng trận đầu!\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '13','09', 'Ngày 13-9-1993 Israen (Israel) và PLO ký thoả thuận trao quyền tự trị cho người Palestin ở dải Gaza.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','10', 'Giáo sư, bác sĩ Trần Hữu Tước sinh ngày 13-10-1913 tại làng Bạch Mai, xã Hoàn Long, tỉnh Hà Đông, nay thuộc quận Hai Bà Trưng, thành phố Hà Nội. Là một trong những học sinh Việt Nam xuất sắc nhất của trường trung học Anbesarô, vào đầu thập kỷ 30 ông được gửi sang Pháp học và thi đậu vào trường Đại học Y khoa Pari. Năm 1937 ông bảo vệ luận án bác sĩ xuất sắc.\nTrong cuộc kháng chiến chống pháp, bác sĩ Trần Hữu Tước đã tham gia giảng dạy tại trường đại học Y dược Cách mạng. Hoà bình lập lại trên miền Bắc năm 1954, ông được cử làm giáo sư trường đại học Y dược Hà Nội, phụ trách bộ môn và chuyên khoa tai - mũi - họng, đồng thời đảm nhận chức vụ Giám đốc bệnh viện Bạch Mai.\nÔng đã góp phần quan trọng và công tác điều trị từ Trung ương đến tỉnh, song song với việc xây dựng mạng lưới tai - mũi - họng rộng khắp, và đào tạo cán bộ T-M-H từ sơ cấp đến cao cấp. Với sự nỗ lực của ông. Viện T-M-H Trung ương ra đời năm 1969, và ông trở thành người Viện trưởng đầu tiên. Do những thành tích đó, ông được tặng danh hiệu Anh hùng Lao động và nhiều Huân chương cao quý của Nhà nước.\nÔng từ trần ngày 23-10-1983, hưởng thọ 70 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','10', 'Nhà văn Vũ Trọng Phụng sinh năm 1912, quê Hưng Yên, nhưng cả cuộc đời gắn bó với Hà Nội. Ông mất ngày 13-10-1939.\nÔng nổi tiếng với các phóng sự: \"Cạm bẫy người\", \"Kỹ nghệ lấy Tây\", \"Cơm thầy cơm cô\", \"Lục xì\"...\nTừ năm 1935, ông cho xuất bản một loạt tiểu thuyết và truyện dài như: \"Giông tố\", \"Số đỏ\", \"Vỡ đê\", \"Làm đĩ\"...\nVũ Trọng Phụng đả kích cay độc thói đua đòi văn minh rởm, lố lăng, phơi trần bộ mặt xã hội thực dân nửa phong kiến đương thời với những tên tư sản đểu cáng, dâm dật cùng với chính sách bần cùng hoá người lao động và nông dân. Tuy nhiên, bên cạnh giá trị hiện thực phê phán tác phẩm của ông đôi lúc sa vào tự nhiên chủ nghĩa.\n28 tuổi đời, 10 tuổi văn, nhà văn Vũ Trọng Phụng đã để lại số lượng tác phẩm đáng kể.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','10', 'Mẹ Nguyễn Thị Suốt sinh năm 1906 tại Quảng Bình, hy sinh ngày 13-10-1968 trong khi đang chèo đò chở bộ đội qua sông.\nTrong những năm Mỹ leo thang chiến tranh, tăng cường đánh phá miền Bắc, Quảng Bình là một vùng đất lửa ác liệt. Mẹ Suốt đã dũng cảm chèo đò qua sông Nhật Lệ, chuyên chở bộ đội, hàng hóa, vũ khí từ bờ bắc sang bờ nam, trung bình mỗi năm 1.400 chuyến đò.\nNăm 1967, mẹ Suốt được Nhà nước phong tặng danh hiệu Anh hùng lực lượng vũ trang.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','10', 'Ngày 13-10-1982, Chính phủ đã có nghị định số 174 thành lập Liên hiệp các xí nghiệp cà phê Việt Nam. Năm 1995 Liên hiệp đổi tên là Tổng công ty Cà phê Việt Nam.\nĐây là một doanh nghiệp của Nhà nước về sản xuất, chế biến và xuất khẩu cà phê lớn nhất Việt Nam. Tại thời điểm thành lập, Tổng công ty Cà phê Việt Nam có 68 đơn vị thành viên, trong đó có 60 doanh nghiệp và 8 đơn vị hành chính sự nghiệp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','10', 'Nhà thơ Quang Dũng tên thật là Bùi Đình Diệm, sinh năm 1921, người làng Phượng Trì, huyện Đan Phượng, tỉnh Hà Tây. Ông qua đời ngày 13-10-1988 tại Hà Nội.\nÔng là một nghệ sĩ tài năng: làm thơ, viết văn, vẽ tranh, sáng tác nhạc. Nhưng trước hết Quang Dũng là một nhà thơ, một hồn thơ trung hậu, yêu tha thiết quê hương, đất nước mình. Trong thơ của ông có một cái tôi hào hoa, thanh lịch, giàu chất lãng mạn, có khả năng cảm nhận và diễn tả một cách tinh tế vẻ đẹp thiên nhiên và tình người, đồng thời lại rất hồn nhiên, bình dị, chân thật.\nCác tác phẩm chính của ông gồm có: \"Một chặng đường:\", \"Cao Bắc\", \"Gương mặt hồ Tây\", \"Hoa lại vàng tháng Chạp\", \"Nhà đồi\", \"Phiên chợ Bắc Hà\", \"Mùa chim ngói dưới chân núi Ba Vì\", \"Mây đầu ô\". Bài thơ \"Tây Tiến\" của Quang Dũng rất nổi tiếng và được nhiều người yêu thích đến thuộc lòng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','10', 'Lê Đức Thọ tên thật là Phan Đình Khải, quê ở tỉnh Nam Định, sinh năm 1911, mất ngày 13-10-1990.\nÔng hoạt động Cách mạng rất sớm, bị giặc Pháp bắt đày ở nhiều nơi. Từ năm 1948 đến năm 1954 ông công tác tại miền Nam, giữ cương vị chủ chốt trong Xứ uỷ Nam Bộ và Trung ương Cục miền Nam.\nNăm 1955, ông ra Bắc và được bổ sung vào Bộ Chính trị Trung ương Đảng, phụ trách công tác tổ chức, vào Quân uỷ Trung ương. Tháng 5-1968, ông được giao nhiệm vụ phụ trách công tác đấu tranh ngoại giao, làm cố vấn đặc biệt của Đoàn đại biểu Chính phủ ta tại Hội nghị Pari, và trực tiếp đàn phán với đại diện Chính phủ Mỹ để giải quyết hoà bình về vấn đề Việt Nam. Năm 1975, ông lại vào miền Nam tham gia chỉ đạo cuộc tổng tiến công mùa xuân và chiến dịch Hồ Chí Minh.\nSau ngày giải phóng miền Nam, ông tiếp tục giữ những chức vụ quan trọng của Đảng. Tháng 12-1986, ông được cử làm cố vấn của Ban chấp hành Trung ương Đảng.\nTrong hơn 60 năm hoạt động Cách mạng, ông Lê Đức Thọ đã có những cống hiến to lớn trong sự nghiệp Cách mạng của Đảng và nhân dân ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','11', 'Tháng 11 nǎm 938, vua Nam Hán sai con là Hoàng Thao đưa quân sang xâm lược nước ta.\nNgô Quyền đã bố trí một trận địa cọc nhọn bịt sắt cắm xuống lòng sông Bạch Đằng. Đợi cho thuỷ triều xuống quân ta mới tấn công dữ dội, thuyền giặc tháo chạy, va vào cọc nhọn bịt sắt. Hoàng Thao bị chết tại trận. Quân ta giết và bắt hầu hết các quân Nam Hán.\nĐây là chiến thắng Bạch Đằng lần thứ nhất.\nSau chiến thắng này, Ngô Quyền lên ngôi vua, đóng đô ở Cổ Loa (nay thuộc huyện Đông Anh - Hà Nội), mở ra thời kỳ độc lập tự chủ của nước ta, chấm dứt hoàn toàn ách đô hộ hơn một nghìn nǎm của các triều đại phong kiến Trung Quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','11', 'Từ ngày 13 đến ngày 20-11-1967, Chủ tịch Hồ Chí Minh đã gửi thư khen và thưởng danh hiệu của Người cho 5 đơn vị dân quân gái có thành tích dũng cảm bắn rơi máy bay giặc Mỹ. Đó là đơn vị dân quân gái của các xã:\n- Xã Hoằng Trường và xã Hoằng Hải (huyện Hoằng Hoá, tỉnh Thanh Hoá).\n- Xã Hà Phú và xã Hà Toại (huyện Hà Trung, Thanh Hoá).\n- Xã Võ Ninh (huyện Quảng Ninh, tỉnh Quảng Bình).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','11', 'Ngày 13, 14 tháng 11 nǎm 1969 tại Hà Nội, những người làm công tác nghệ thuật điện ảnh Việt Nam đã tiến hành Đại hội thành lập Hội Điện ảnh Việt Nam. Hội có trụ sở ở 51 phố Trần Hưng đạo - Hà Nội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '13','11', 'Chuyến cất cánh đầu tiên được thừa nhận của một máy bay lên thẳng cùng với phi công đã được Cócnuy (người Pháp) thực hiện ngày 13-11-1907 tại Lidiơ.\nMáy bay này nặng 260 Kilôgam. Động cơ của nó là động cơ Ǎngtoanét 24 sức ngựa.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','12', 'Nguyễn Nhạc mất ngày 13-12-1793\nÔng là người khởi xướng và là thủ lĩnh phong trào khởi nghĩa của nông dân Tây Sơn.\nNǎm 1771, lực lượng nghĩa quân do ông lãnh đạo dấy lên từ đất Tây Sơn (tỉnh Bình Định), dần dần chiếm ra đến Quảng Nam và quét sạch quân chúa Nguyễn ở khu vực phía nam Bình Định. Tháng 6-1787 ông lên ngôi hoàng đế, lấy hiệu là Thái Đức, phong cho Nguyễn Lữ làm Đông Định Vương cai quản các tỉnh miền Nam, Nguyễn Huệ làm Bắc Bình Vương cai quản các tỉnh từ bắc Quảng Nam trở ra. Sau khi lên ngôi Hoàng đế, Nguyễn Nhạc cầu an, hưởng lạc, giảm sút ý chí chiến đấu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','12', 'Ngày 13-12-1946 diễn ra hội nghị các khu trưởng quân sự họp tại Hà Đông, do Trung ương Đảng triệu tập. Hội nghị quán triệt nhận định: Chủ trương của Đảng vẫn là tranh thủ khả nǎng hoà bình, nhưng phải chuẩn bị cấp tốc để tiến hành chiến tranh toàn dân, toàn diện và trường kỳ. Hội nghị rút kinh nghiệm tác chiến ở Hải Phòng, Lạng Sơn, Nam Bộ và đề ra kế hoạch \"Vườn không nhà trống\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '13','12', 'Từ ngày 13-12-1974 đến ngày 6-1-1975, quân giải phóng miền Nam đã tiến hành chiến dịch Phước Long (nay thuộc tỉnh Bình Phước) nhằm đánh giá phản ứng của quân Mỹ sau khi rút khỏi miền Nam (tháng 3-1973).\nTa đã đánh Bù Đǎng, thị xã Phước Long, Vĩnh Thiện, Phước Bình. Đến 19 giờ ngày 6-1-1975, ta giải phóng hoàn toàn thị xã Phước Long.\nChiến dịch Phước Long cho thấy khả nǎng phản ứng hạn chế của quân đội Sài Gòn và cả nước Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '13','12', 'Viét Phrǎngxoa (Viète Francois) là nhà bác học lỗi lạc người Pháp, sinh nǎm 1540, mất ngày 13-12-1603. Ông được mệnh danh là \"cha đẻ của đại số học\".\nĐại số học trong các công trình của Viét trở thành khoa học tổng quát về phương trình đại số, dựa trên phép tính các biểu thức chữ. Ông là người đầu tiên đã ký hiệu không những ẩn số mà cả các hệ số của phương trình bằng các chữ. Nhờ đó, ông có thể diễn đạt các tính chất của phương trình bằng những công thức tổng quát. Viét là người đầu tiên đã đưa ra các công thức (ngày nay mang tên ông) để thiết lập sự phụ thuộc lẫn nhau giữa các nghiệm và các hệ số của phương trình đại số.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '13','12', 'Henrich Hainơ (Herinich Heine), nhà thơ Đức lớn nhất thế kỷ XIX sinh ngày 13-12-1797.\nNǎm 1825 ông đỗ tiến sĩ luật và trở thành luật sư. Tuy vậy ông chú ý đến vǎn học, triết học. Ông được coi là nhà thơ lãng mạn cách mạng. Bên cạnh những bài thơ về tình yêu và thiên nhiên, ông còn có các bài đả kích sâu cay bè lũ quý tộc thiển cận và tố cáo giáo hội. Nǎm 1831 ông sang Pari. Thời gian này ông gặp Mác. Chịu ảnh hưởng của Mác, ông sáng tác bài Những người thợ dệt ở Xilêdi nói về cuộc nổi dậy của thợ dệt. Ngoài thơ ca, ông còn có nhiều bài báo chống lại chế độ thống trị đương thời.\nÔng mất ngày 17-12-1856.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '13','12', '13-12-1991, Nam và Bắc Triều tiên kí hiệp định không tấn công lẫn nhau, mở ra một trang mới trong quan hệ giữa hai nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '13','12', 'Tào Ngu sinh nǎm 1911 và qua đời ngày 13-12-1996\nÔng là người viết kịch nổi tiếng Trung Quốc. Trong vở kịch Lôi Vũ, Tào Ngu đã dựng lại được cuộc sống hủ bại và đầy tội ác của một gia đình phong kiến tư sản hoá, hình ảnh tượng trưng của xã hội nửa phong kiến, nửa thuộc địa Trung Hoa. Tác giả lên án chế độ xã hội kỳ quái và chỉ rõ con đường diệt vong không tánh khỏi của nó.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','01', 'Ngày 14-1-1950 được coi là ngày đánh dấu sự thắng lợi của đường lối ngoại giao của Chính phủ Việt Nam dân chủ cộng hoà. Trong ngày này Chính phủ đã ra tuyên bố về đường lối ngoại giao.\nNgày 15-1 Chính phủ Việt Nam thừa nhận Chính phủ nước Cộng hoà nhân dân Trung Hoa. Ngày 18-1 Chính phủ nước Cộng hoà nhân dân Trung Hoa - nước đầu tiên - công nhận Chính phủ ta. Ngày 30-1 Chính phủ liên bang cộng hoà chủ nghĩa Xô Viết, ngày 31-1 Chính phủ Cộng hoà nhân dân Tiệp Khắc và Chính phủ Cộng hoà dân chủ Đức, ngày 3-2 Chính phủ Cộng hoà nhân dân Rumani, ngày 5-2 các Chính phủ Cộng hoà Ba Lan và Cộng hoà nhân dân Hungari, ngày 8-2 Chính phủ Cộng hoà nhân dân Bungari, ngày 18-2 Chính phủ Anbani, ngày 17-11 Chính phủ CHND Mông Cổ công nhận và đặt quan hệ ngoại giao với Chính phủ Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','01', 'Nhà thơ Chế Lan Viên tên thật là Phan Ngọc Hoan, sinh ngày 14-1-1920, quê ở tỉnh Quảng Trị, mất nǎm 1989 ở thành phố Hồ Chí Minh.\nThơ Chế Lan Viên giàu chất suy tưởng và vẻ đẹp trí tuệ (trong những nǎm chống đế quốc Mỹ lại đậm tính chính luận). Ông chú trọng khai thác tương quan đối lập giữa các sự vật, hiện tượng, sáng tạo hình ảnh đẹp, mới lạ và ngôn ngữ sắc sảo.\nCác tập thơ chủ yếu của Chế Lan Viên gồm có:\nĐiêu tàn (1937), Ánh sáng và phù sa (1960), Hoa ngày thường - chim báo bão (1967), Những bài thơ đánh giặc (1972), Đối thoại mới (1973), Hoa trước lǎng Người (1976), Hái theo mùa (1977), Hoa trên đá (1984), Ta gửi cho mình (1986) và 2 tập \"Di cảo\" sau khi ông qua đời.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','01', 'Êmôn Hali (Edmond Halley) sinh nǎm 1656, mất ngày 14-1-1742.\nÔng là nhà thiên vǎn học và toán học của nước Anh. Nǎm 1705, ông là người đầu tiên tính được quỹ đạo của một sao chổi và tiên đoán rằng nó sẽ trở lại trái đất vào nǎm 1858, do đó sao chổi này được mang tên là sao chổi Halley.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','01', 'ILya Grigorjecitsh Ehrenbung nhà vǎn, nhà báo, nhà hoạt động xã hội nổi tiếng Nga. Ông sinh ngày 14-1-1891. Ông tham gia tổ chức bí mật Bônsêvích từ những nǎm 1905-1907. Ông nổi tiếng với hàng ngàn bài báo, chính luận ca ngợi chủ nghĩa yêu nước xô viết, lòng cǎm thù chủ nghía phát xít, khẳng định niềm tin và thắng lợi của chủ nghĩa xã hội. Các tác phẩm nổi tiếng của ông có: Ngày hôm sau; Chó rừng; Pari thất thủ; Bão táp; Làn sóng thứ chín; Tuyết tan; Người con gái; Nǎm tháng cuộc đời...\nÔng được nhiều giải thưởng quốc gia Liên Xô và được giải thưởng Lênin, ông là chiến sĩ hoà bình nổi tiếng thế giới. Ông mất ngày 31-8-1967 tại Matxcơva.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','02', 'Ngày 14-2-1941, Đội du kích Bắc Sơn chính thức được thành lập trong một buổi lễ tổ chức tại khu rừng Khuổi Nọi (thuộc xã Vũ Lễ, châu Bắc Sơn, tỉnh Lạng Sơn). Đồng chí Hoàng Vǎn Thụ thay mặt Trung ương Đảng công nhận, giao nhiệm vụ và trao lá cờ đỏ sao vàng cho đơn vị vũ trang đầu tiên của Đảng gồm 32 chiến sĩ do đồng chí Lương Vǎn Chi chỉ huy. Ngay sau khi thành lập đơn vị vừa tổ chức huấn luyện, vừa tham gia những hoạt động quân sự, nhằm bảo vệ cǎn cứ địa Cách mạng và không ngừng phát triển lực lượng. \nĐến tháng 5-1941 đơn vị đổi tên là Trung đội Cứu quốc quân thứ nhất, nǎm 1945 sáp nhập với Việt Nam Tuyên truyền giải phóng quân thành Việt Nam Giải phóng quân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','02', 'Trận đánh chợ Đồng Xuân diễn ra ngày 14-2-1947, lớn nhất ở Liên khu I. Trong điều kiện so sánh lực lượng địch - ta rất chênh lệch, nó tiêu biểu cho khí phách anh hùng và sự kết hợp giữa lòng quả cảm và trí thông minh sáng tạo của các chiến sĩ cảm tử của Thủ đô trong những ngày đầu cuộc kháng chiến chống Pháp. Ta đã loại khỏi vòng chiến đấu 200 tên địch. Tại đây các chiến sĩ tự vệ đã sử dụng cả những vũ khí thô sơ nhất để tiêu diệt địch. Nhiều khi để diệt một tên địch, chiến sĩ ta phải đổi cả tính mạng của mình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','02', 'Ngày 14-2-1996, ngành giao thông vận tải Việt Nam và Trung Quốc đã mở lại tuyến đường sắt Hà Nội - Bắc Kinh để đáp ứng nhu cầu về vận chuyển hành khách và hàng hoá giữa hai nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','02', 'Nhà thơ Nguyễn Đình sinh nǎm 1918, quê ở tỉnh Quảng Nam, mất ngày 14-2-1975.\nTrong những nǎm kháng chiến chống Pháp, ông vừa làm công tác giáo dục, vừa sáng tác ca dao phục vụ sản vuất và chiến đấu. Từ nǎm 1954, tập kết ra miền Bắc, ông làm biên tập viên ở các báo Độc lập, Vǎn học, Vǎn nghệ và chuyên làm thơ trào phúng, đả kích Mỹ - Ngụy, phục vụ sự nghiệp đấu tranh thống nhất đất nước.\nTác phẩm chính của Nguyễn Đình có: Đánh mấy vần (in chung với Lê Kim, Phú Sơn), Những mũi tên nhọn, Ngọn lửa mới nhen \nTrong 20 nǎm làm thơ trào phúng, ông luôn bám sát thời sự chính trị, khai thác mọi sơ hở, mâu thuẫn, thủ đoạn nham hiểm, tội ác man rợ của địch và đánh chúng kịp thời, ráo riết.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','03', 'Ngày 14-3-1900 là ngày sinh của nhà thơ hiện đại Hồ Trọng Hiếu, bút danh Tú Mỡ, sinh quán ở Hà Nội.\nÔng chuyên về lối thơ trào phúng, dí dỏm mà sâu sắc, duyên dáng ý nhị trong từ ngữ điêu luyện. Từ nǎm 1936 ông cộng tác với báo \"Phong hoá\" ; \"Ngày nay\" và nhóm \"Tự lực vǎn đoàn\", đến cách mạng Tháng Tám trong kháng chiến chống Pháp, ông tích cực tham gia, sinh hoạt trong Hội Vǎn hoá nghệ thuật góp phần công sức của mình vào công cuộc cứu nước và xây dựng nền vǎn hoá mới.\nTú Mỡ là một nhà thơ trào phúng nổi tiếng của nền vǎn học hiện đại của nước ta, thơ ông mang sắc thái dân tộc hiện đại.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','03', 'Nhà vǎn Hoài Thanh tên thật là Nguyễn Đức Nguyên, quê ở huyện Nghi Lộc, tỉnh Hà Tĩnh, sinh nǎm 1909, qua đời ngày 14-3-1982. \nLúc nhỏ ông học ở Nghệ An và Huế. Sau đó ông dạy học ở Huế và nghiên cứu vǎn hoá dân tộc. Sau Cách mạng Tháng Tám, ông có nhiều đóng góp trong lĩnh vực vǎn nghệ. \nÔng nổi tiếng là một nhà phê bình vǎn học theo quan điểm Mác - Lênin trong giai đoạn đầu xây dựng chủ nghĩa xã hội ở nước ta.\nCác tác phẩm chính của Hoài Thanh gồm có: Thi nhân Việt Nam (cùng viết với Hoài Chân) Có một nền vǎn hoá Việt Nam, Phê bình và tiểu luận, Truyện thơ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','03', 'Anbe Anhxtanh sinh ngày 14-3-1879 và qua đời ngày 18-4-1955.\nÔng là nhà bác học vĩ đại bậc nhất của thế kỷ XIX, nhà vật lý lý thuyết người Đức, một trong người sáng lập vật lý học hiện đại.\nAnhxtanh là tác giả những công trình cơ sở về thuyết lượng tử của ánh sáng: Đưa ra khái niệm về phôton (nǎm 1905), thiết lập các định luật quang điện, định luật cơ bản của quang hoá. Từ nǎm 1917, ông đã tiên đoán bức xạ cảm ứng là cơ sở của kỹ thuật made và lade ngày nay.\nNguyên tố hoá học 99 mang tên Anbe Anhxtanh. Nǎm 1921 ông được nhận giải Nôben về vật lý.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','03', 'Ngày 14-3-1883, Các Mác (Karl Marx) nhà triết học, kinh tế học, người sáng lập chủ nghĩa xã hội khoa học, lãnh tụ cách mạng vĩ đại của giai cấp công nhân thế giới đã từ trần. Tên tuổi của C. Mác cùng với Ph. Ǎngghen (Friedries Engels) mãi mãi đi vào lịch sử nhân loại như những người sáng lập ra chủ nghĩa cộng sản khoa học có tác động to lớn và sâu rộng đối với nhân loại tiến bộ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','04', 'Ngày này nǎm xưa, 14-4-1887, Nguyễn Cao đã qua đời. Ông sinh nǎm 1828, quê ở tỉnh Bắc Ninh cũ. Sau khi đỗ cử nhân ông đi làm chi huyện Yên Dũng, rồi lên làm bố chính Thái Nguyên. Nǎm 1883, Nguyễn Cao cùng nhiều nhân sĩ kháng chiến chống Pháp hưởng ứng phong trào Cần Vương, sung chức tán tương quân vụ. Nǎm 1887, ông bị sa vào tay giặc Pháp, tự đâm bụng, moi ruột chết giữ trọn khí tiết. Ở Hà Nội có đường phố mạng tên Nguyễn Cao thuộc quận Hai Bà Trưng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','04', 'Ngày 14 và 22 tháng 4 nǎm 1952, quân ta đã đánh bại trận càn quét lớn của địch ở Bắc sông Luộc (Hưng Yên - Hải Dương). Ngày 23-4-1952 địch tập trung 18 tiểu đoàn bộ binh, nhiều xe cơ giới, trọng pháo, máy bay càn quét vào Bắc sông Luộc nhằm phá hoại cơ sở của ta. Quân ta tích cực chống càn, tiêu diệt 1980 tên địch (đa số là lính Âu Phi), phá huỷ một xe tǎng, 30 xe vận tải, 2 ca nô, bắn rơi và bắn hỏng 2 máy bay và thu 30 súng các loại.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','04', 'Ngày 14 đến 29-4-1975, giải phóng các đảo do quân ngụy Sài Gòn chiếm đóng thuộc quần đảo Trường Sa. Ngày 4-4 Bộ Chính trị và Quân uỷ Trung ương chỉ thị cho Khu uỷ, Quân khu uỷ, Bộ Tư lệnh Quân khu 5 và Bộ Tư lệnh Hải quân \"nghiên cứu và chỉ đạo gấp rút, nhằm thời cơ thuận lợi nhất đánh chiếm các đảo do quân nguỵ chiếm đóng thuộc quần đảo Trường Sa. Đây là nhiệm vụ rất quan trọng\". Ngày 10-4 Bộ Tư lệnh Hải quân điều 3 tàu vận tải 673, 674, 675 (trung đoàn 125) từ Hải Phòng vào Đà Nẵng. Ngày 11-4, lực lượng chiến đấu gồm đội 4 (trung đoàn 126 đặc công nước), một số đội đặc công của Quân khu 5 và tỉnh Khánh Hoà do trung tá Mai Nǎng chỉ huy tiến ra Trường Sa. Ngày 14-4 bộ đội ta giải phóng đảo Song Tử Tây. Từ ngày 25 đến 29-4 giải phóng các đảo Nam Yến, Sinh Tồn, Trường Sa.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','04', 'La Phôngten, nhà ngụ ngôn và nhà vǎn cổ điển Pháp, mất ngày 14-4-1695.\nÔng sinh nǎm 1621, được sự giáo dục đầy tinh thần tự do và sâu rộng của người cha.\nTrong 20 nǎm làm nghề quản lý rừng, La Phôngten sống giữa thiên nhiên và người lao động. Ông viết nhiều thể loại: thơ, tiểu thuyết, kịch nhưng nổi tiếng hơn cả là ngụ ngôn gồm 12 quyển, viết trong 28 nǎm.\nLa Phôngten đã sáng tạo ngụ ngôn Pháp với đầy đủ tính dân tộc và ông coi mục đích của vǎn học là rǎn dạy người đời bằng nghệ thuật ngôn ngữ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','04', 'Ngày 14-4 nǎm 1891, là ngày sinh của Xécgây Prôcôphiép nhạc sĩ sáng tác Xô Viết lỗi lạc, người đã làm phong phú thêm cho nền âm nhạc Nga và thế giới bằng những tác phẩm mang tính con người sâu sắc. Với bút pháp tươi mát, táo bạo và độc đáo, ông đã viết Ôpêra giao hưởng, ba lê. Đặc biệt là các vở Chiến tranh và hoà bình, lọ lem, Rômêô và Juyliét v.v...Ông mất nǎm 1953.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','04', 'Nhà thơ Xô Viết Maiakốpxki qua đời ngày 14-4-1930. Ông sinh nǎm 1893. Trước nǎm 1917, ông chịu ảnh hưởng của chủ nghĩa vị lai, mang nặng tư tưởng hư vô, vô Chính phủ, nhưng thơ của ông thể hiện khát vọng cải tạo xã hội, tin tưởng vào tương lai tốt đẹp.\nSau cách mạng tháng 10 Nga Maiakốpxki dứt khoát đi theo chính quyền Xô Viết, phục vụ sự nghiệp cách nạng của giai cấp vô sản và trở thành nhà thơ vĩ đại của Liên Xô. Thơ của ông đến với Việt Nam từ thời kỳ mặt trận Dân chủ (1936-1939).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','04', 'Ngày 14-4-1931, nhà vật lý người Pháp Bactêlơmy đã thực hiện buổi phát hình công cộng đầu tiên ở nước Pháp. Đây cũng là buổi phát hình đen trắng đầu tiên trên thế giới.\nHôm ấy có khoảng 800 người ngồi ở giảng đường trường Đại học điện Malakôfơ có thể nhìn thấy trực tiếp cô Suydannơ Bridu giới thiệu chương trình đầu tiên trên lịch sử truyền hình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','05', 'Chính phủ Việt Nam Dân chủ cộng hoà ban sắc lệnh thành lập Sở Mậu dịch trong Bộ Công thương ngày 14-5-1951. Sở Mậu dịch có nhiệm vụ:\n1. Tổ chức việc buôn bán trong nước: Điều hoà thị trường ổn định giá cả, giúp đỡ sản xuất, giúp đỡ hợp tác xã phát triển, cung cấp những nhu cầu thiết yếu cho bộ đội, cơ quan và nhân dân, hướng dẫn, tập hợp thương nhân phục vụ kháng chiến, phục vụ dân sinh.\n2. Tổ chức buôn bán trao đổi với nước ngoài. \n3. Tổ chức việc đấu tranh mậu dịch với địch.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','05', 'Ngày 14-5-1992 Trung ương Mặt trận Tổ quốc Việt Nam phối hợp với Bộ Lao động - Thương binh - Xã hội và Bộ Quốc phòng phát động phong trào \"Tặng sổ tiết kiệm tình nghĩa\" trong cả nước, thể hiện sự quan tâm của Đảng, Nhà nước, quân đội và nhân dân với những người có công với nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','05', 'Nhạc sỹ Xuân Hồng tên thật là Nguyễn Hồng Xuân, sinh nǎm 1928 tại tỉnh Tây Ninh và qua đời ngày 14-5-1996 tại thành phố Hồ Chí Minh.\nTừ nhỏ, Xuân Hồng được mẹ ru những khúc ca dao, dân ca Nam Bộ, còn cha dạy đàn ca tài tử. Nǎm 1949, ông bắt đầu sáng tác những bài hát đầu tiên như: Bài ca may áo, Xuân chiến khu, Chiếc khǎn tay, Mùa xuân trên thành phố Hồ Chí Minh, Người mẹ của tôi được nhiều người yêu thích vì thấm đượm tính dân tộc, trữ tình và phản ánh cuộc chiến đấu chống ngoại xâm của nhân dân ta trong mấy chục nǎm qua.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','05', 'Rốbơt Ôoen, nhà chủ nghĩa xã hội không tưởng người Anh, sinh ngày 14-5-1771.\nÔng là chủ xưởng một xí nghiệp lớn. Đã trải qua cuộc đời lao động và nhìn tận mắt thấy những cảnh đau khổ của giai cấp công nhân nên ông muốn cải thiện đời sống cho công nhân xí nghiệp. Ông rút giờ làm việc trong xí nghiệp, trả lương cao và cho công nhân hưởng phúc lợi tập thể. Ông quan tâm đến việc giáo dục con cái của công nhân như xây dựng trường học, tổ chức nhà trẻ, vườn trẻ. Ông đưa ra dự án cải tổ xã hội theo chế độ cộng sản chủ nghĩa. Ông mua một khoảng đất rồi cùng môn đệ đến đó tổ chức thí nghiệm hợp tác xã sản xuất và tiêu thụ.\nVề quan điểm xã hội, Ôoen cho xã hội tư bản có tính chất tạm thời, do đó cần thiết lập xã hội trong tương lai như là một liên minh của hợp tác xã, trong đó công việc được phân chia giữa những người lao động theo khả nǎng và sản phẩm được phân chia theo nhu cầu. Bên cạnh đó, ông lại cương quyết bác bỏ việc sử dụng Cách mạng, đấu tranh giai cấp và không nhận thức được vai trò của giai cấp công nhân.\nÔng đặt hy vọng vào giai cấp thống trị và những người trí thức sẽ đóng góp vào việc sáng tạo ra xã hội lý tưởng trong tương lai.\nÔng mất ngày 17-11-1858.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','05', 'Ngày 14 tháng 5 nǎm 1936, tổng tuyển cử Pháp, Mặt trận nhân dân do Đảng Cộng sản Pháp làm nòng cốt đã giành thắng lợi lớn. Sau bầu cử, một chính phủ cấp tiến được thành lập ở Pháp. Nhờ thắng lợi và uy tín chính trị của Mặt trận nhân dân mà tình hình chính trị - xã hội Pháp có nhiều biến chuyển có lợi cho phong trào công nhân. Đối với các nước thuộc địa trong đó có Đông Dương, Mặt trận nhân dân đã phát huy ảnh hưởng và tác động tích cực, phần nào tạo thuận lợi cho các hoạt động của Cách mạng - giai đoạn từ 1936 đến 1939.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','05', 'Ngày 14-5-1955 tại cung điện Rátdivin diễm lệ ở trung tâm thủ đô Vacsava Ba Lan, bảy nước: Liên Xô, Anbani, Ba Lan, Bungari, Rumani, Tiệp Khắc đã cùng nhau ký kết một vǎn kiện quan trọng: Hiệp ước hữu nghị hợp tác và tương trợ giữa các nước đồng minh xã hội chủ nghĩa ở châu Âu, gọi tắt là hiệp ước Vácsava.\nTổ chức hiệp ước Vácsava về bản chất là một liên minh phòng thủ tích cực, không đe doạ tấn công. Trái lại nhằm mục đích thông qua các biện pháp cần thiết để bảo đảm an ninh của các nước thành viên, góp phần duy trì hoà bình ở châu Âu và thế giới. Các nước thành viên hành động theo tinh thần hữu nghị hợp tác nhằm mục đích phát triển và củng cố các mối quan hệ kinh tế, vǎn hoá và theo nguyên tắc tôn trọng độc lập, chủ quyền, toàn vẹn lãnh thổ của nhau cũng như của các nước khác.\nTổ chức hiệp ước Vacsava ra đời là một tất yếu lịch sử, đòi hỏi khách quan của các nước XHCN châu Âu nhằm đối phó với âm mưu gây chiến và xâm lược của khối liên minh NATO do các nước đế quốc đề ra. Từ sau khi khối XHCN ở Đông Âu tan rã, khối hiệp ước Vacsava ngừng hoạt động. Hiện nay một số quốc gia trong khối Vacsava cũ toan tính gia nhập NATO - khối quân sự đối đầu trước kia của họ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','05', 'Ngày 14-5-1996, Tổng thống Mỹ Bin Clintơn đã ký lệnh chấm dứt việc coi Việt Nam là khu vực chiến sự sau 21 nǎm quân đội Mỹ rút khỏi miền Nam Việt Nam. Lệnh trên có hiệu lực từ ngày 30-6-1996, cho phép chính quyền Mỹ ngừng cung cấp những khoản viện trợ cho các gia đình có quân nhân bị mất tích cũng như cho những người đang tìm kiếm hài cốt người thân của họ.\nCòn nhớ sắc lệnh của Mỹ quy định Việt nam là khu vực chiến sự được ban hành từ ngày 24-4-1965.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','06', 'Hội nghị ban lãnh đạo Đảng Cộng sản Đông Dương ở ngoài nước và đại biểu trong nước đã họp tại Ma Cao (Trung Quốc), ngày 14-6-1934. Hội nghị kiểm điểm việc thực hiện chương trình hành động nǎm 1932 và vạch ra nhiệm vụ trước mắt của tổ chức Đảng và các đoàn thể quần chúng nhằm chuẩn bị cho đại hội Đảng lần thứ nhất.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','06', 'Ngày 14-6-1961, Hồ Chủ tịch đã về thǎm hợp tác xã Kiều Mai (xã Phú Diễn, huyện Từ Liêm) Bác Hồ còn về thǎm nông dân ở đây vào ngày 25-11-1961 khi bà con đang gặt lúa mùa. Bác trồng cây đa và chúc tết nhân dân xã Phú Diễn ngày 31-1-1965.\nNông dân ngoại thành Hà Nội được đón Bác nhiều lần trong các dịp chống hạn, đắp đê, phòng chống lũ lụt, thử máy cấy lúa, trồng cây...\nNhiều nông dân có thành tích về chống hạn, xây dựng công trình thuỷ lợi đã được Bác Hồ tặng huy hiệu của Người.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','06', 'Vũ Ngọc Phan, nguyên quán ở tỉnh Hà Bắc (cũ) sinh ngày 8-9-1902 và qua đời vào ngày 14-6-1987 tại Hà Nội.\nÔng viết vǎn, soạn sách, dịch thuật từ những nǎm còn trẻ, nổi tiếng là một cây bút tiểu luận, phê bình vǎn học. Từ sau Cách mạng Tháng Tám, ông tích cực tham gia công tác vǎn hoá, vǎn nghệ.\nCác tác phẩm của nhà nghiên cứu vǎn học Vũ Ngọc Phan gồm có: \"Nhà vǎn hiện đại\", \"Dân ca Việt Nam\", \"Trên đường nghệ thuật\", \"Tục ngữ ca dao\", \"Hồi ký\" và một số truyện ký.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','06', 'Culông (Coulomb) là nhà vật lý Pháp, người đã đưa ra định luật cơ bản về lực tác dụng giữa các điện tích. Ông sinh ngày 14-6-1736 và mất vào nǎm 1806.\nCulông sớm tham gia quân đội và là kỹ sư công binh. Khi ra khỏi quân đội, ông giành thời gian để nghiên cứu hoa học, ông phát minh ra cân xoắn, một dụng cụ rất nhạy dùng đo lực tác dụng giữa các điện tích trên cơ sở nghiên cứu tính đàn hồi xoắn của các dây và tóc. Ông đưa ra công thức về cường độ điện trường ở gần một mạng điện. Và đặc biệt ông đã đưa ra một định luật cơ bản về lực tác dụng giữa các điện tích. Định luật này được mang tên ông, gọi là định luật Culông.\nNgoài các công trình về điện và từ, Culông còn có những nghiên cứu về sự ma sát trong các máy, về cối xay gió, về tính đàn hồi của kim loại và sợi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','06', 'Lanstainơ sinh ngày 14-6-1868 tại Viên - thủ đô nước Áo, trong một gia đình gốc do Thái. Nǎm 23 tuổi ông tốt nghiệp khoa y trường Đại học Tổng hợp, trở thành thầy thuốc kiêm nhà nghiên cứu. Về sau, ông làm giáo sư tại Học viện Rốcphelơ.\nNǎm 1900, Lanstainơ lần đầu tiên đã thử trộn riêng rẽ nhiều hồng cầu của người, lần lượt với huyết thanh của nhiều người khác và đánh dấu các trường hợp máu bị ngưng kết. Phân tích kết quả thí nghiệm, ông đã chia máu người thành 4 nhóm A, B, AB, và O. Nếu tiếp cho \"người nhận\" \"máu của những người cho\" thích hợp theo sơ đồ các nhóm máu thì việc truyền máu sẽ thành công. Nǎm 1930 Lanstainơ đã được nhận giải thưởng Nôben. Ông từ trần ngày 26-6-1943, thọ 75 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','07', 'Tháng 7-1920, tại nước Pháp. Nguyễn Ái Quốc đã đọc bản \"Luận cương về các vấn đề dân tộc và thuộc địa\" của Lênin, khẳng định lập trường kiên quyết của Quốc tế cộng sản ủng hộ phong trào giải phóng dân tộc ở các nước Phương Đông. Luận cương của Lênin đã chỉ cho Nguyễn Ái Quốc con đường giành độc lập cho dân tộc và tự do cho đồng bào.\nSau này, Người nhớ lại và viết \"Luận cương của Lênin làm cho tôi rất cảm động, phấn khởi, sáng tỏ, tin tưởng biết bao. Tôi mừng đến phát khóc lên. Ngồi một mình trong phòng mà tôi nói to lên như đang nói trước quần chúng đông đảo: \"Hỡi đồng bào bị đày đoạ đầy đau khổ, đây là cái cần thiết cho chúng ta, đây là con đường giải phóng của chúng ta\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','07', 'Ngày 14-7-1928, tại thành phố Huế đã ra đời Tân Việt cách mạng Đảng, gọi tắt là Đảng Tân Việt. Đảng này tập hợp những trí thức nhỏ và thanh niên tiểu tư sản yêu nước. Địa bàn hoạt động của Đảng Tân Việt chủ yếu ở Trung Kỳ.\nĐảng Tân Việt chủ trương vận động hợp nhất với \"Việt Nam thanh niên cách mạng đồng chí Hội\" vì lợi ích của cách mạng. Đảng Tân Việt ra đời và hoạt động trong điều kiện \"Việt Nam thanh niên cách mạng đồng chí Hội\" đã phát triển mạnh lên đường lối của Hội đã cuốn hút nhiều Đảng viên trẻ của Đảng Tân Việt.\nNội bộ Đảng Tân Việt đã diễn ra cuộc đấu tranh và khuynh hướng Cách mạng theo quan điểm vô sản đã chiếm ưu thế.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','07', 'Chính phủ Việt Nam dân chủ cộng hoà ban Sắc lệnh giảm tô ngày 14-7-1949 nhằm bổ sung cho Thông tư giảm tô nǎm 1945. Sắc lệnh này quy định: Chủ ruộng phải giảm 25% so với mức tô trước Cách mạng tháng Tám, xoá bỏ tô phụ và chế độ tá điền, lập Hội đồng giảm tô ở cấp tỉnh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','07', 'Ngày 14-7-1982 một bộ phận quân đội nhân dân Việt Nam sang làm nhiệm vụ quốc tế ở Camphuchia về nước. Đây là đợt rút quân đầu tiên theo thông cáo chung của Hội nghị lần thứ 6 của Bộ trưởng Ngoại giao 3 nước Đông Dương. Nhân dịp này, Hội đồng Nhà nước trao tặng Huân chương Quân công hạng nhất cho đơn vị quân đội nhân dân Việt Nam vừa hoàn thành nhiệm vụ quốc tế ở Camphuchia trở về.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','08', 'Nhà vǎn, nhà báo Nguyễn An Ninh sinh nǎm 1900, quê ở Quán Tre, huyện Hóc Môn, tỉnh Gia Định (thành phố Hồ Chí Minh). Thuở nhỏ ông học ở Sài Gòn, sau du học ở Pháp và đỗ cử nhân Luật lúc 21 tuổi.\nTrong thời gian học ở Pari, ông liên hệ với Phan Châu Trinh, Phan Vǎn Trường và tiếp xúc với Nguyễn Ấi Quốc. Nǎm 1922, ông về nước. Bài diễn thuyết \"Cao vọng thanh niên\" ông đọc tại Hội khuyến học Nam Kỳ vang vọng như một bản tuyên ngôn, kêu gọi thanh niên và giới trí thức. Ông ra tờ báo \"Cái chuông rè\" là cơ quan ngôn luận chống thực dân Pháp. Ngoài những bài diễn thuyết, những bài báo cả tiếng Việt lẫn tiếng Pháp đều nổi tiếng và gây nên một tiếng vang lớn trong giới tiến bộ, ông còn soạn các sách: \"Tôn giáo\", \"Trưng Nữ Vương\", \"Dân ước\"\nSau hai lần bị bắt, ra tù ông vẫn tiếp tục đấu tranh. Ngày 5-10-1939, ông bị bắt và bị kết án 5 nǎm lưu đày ở Côn Đảo. Trên đảo, ông bị kiệt sức vì bị hành hạ và đã mất ở trong tù vào ngày 14-8-1943 lúc đó ông mới 43 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','08', 'Ngày 14-8-1945, sau hội nghị toàn quốc, Đảng quyết định phát động toàn dân khởi nghĩa, ra lời hiệu triệu kêu gọi các Đảng viên: \"Giờ tổng khởi nghĩa đã đến! Dân tộc ta đã đến lúc vùng dậy cướp lại chính quyền độc lập của mình! Trước cơ hộ có một không hai này, toàn thể dân tộc ta phải đem hết lực lượng, dùng hết can đảm, bao quanh đạo quân giải phóng Việt Nam, tung xương máu ra đánh đuổi giặc Nhật, đòi lấy tự do hạnh phúc cho nhân dân\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','08', 'Ngày 14-8-1996, Bộ giao thông vận tải nước ta đã tổ chức lễ khởi công xây dựng đường ô tô cao tốc Láng - Hoà Lạc giai đoạn I (thuộc tuyến đường mới Hà Nội - Ba Vì).\nTuyến đường quan trọng này dài 30 km nhằm phục vụ chương chình khai thác tiềm nǎng kinh tế - vǎn hoá của vùng Ba Vì (Hà Tây), thông qua việc hình thành ở đây một khu công nghệ kỹ thuật cao, Trung tâm đại học quốc gia, Làng vǎn hoá các dân tộc Việt Nam và cụm du lịch sinh thái rất gần với thủ đô Hà Nội.\nTuyến đường Láng - Hoà Lạc có 2 km ở nội thành và 12 km ở ngoại thành Hà Nội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','08', 'Béctôn Brếch là nhà soạn kịch nổi tiếng, nhà thơ, nhà vǎn, nhà lý luận, nhà đạo đức xuất sắc người Đức. Ông sinh ngày 10-2-1898, học đại học vǎn, triết, y và sáng tác rất sớm. Nǎm 1922, ông được giải thưởng về vở kịch \"Tiếng chày trong đêm\". Ông nghiên cứu tìm cách thể hiện trên sân khấu mối quan hệ biện chứng giữa thực tại xã hội và thái độ của người xem, nhằm thay đổi thực trạng đó. Những quan điểm nghệ thuật kịch được trình bày trong các bài viết: \"Bàn về kịch\". \"Kịch giải trí hay kịch giáo huấn\", \"Kỹ thuật mới và nghệ thuật sân khấu\" v.v...\nNgoài kịch, ông còn sáng tác nhiều thơ, truyện và tiểu thuyết. Tập 100 bài thơ là một đóng góp vào bản sắc của thi ca Đức. Về tiểu thuyết, ông có tác phẩm \"Cửa hàng của Giulius Xexa\" được coi là mẫu mực của thể loại dài hơi mà diễn đạt bằng ngôn ngữ súc tích, mạch lạc, khẳng định sự phát triển của cuộc sống. Brếch mất ngày 14-8-1956 tại Béc lin.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','08', 'Ngày 14-8-1941 đã diễn ra cuộc gặp gỡ giữa Tổng thống Mỹ Rudơven và thủ tướng Anh Sớcsin ở Terre Neuve (Đại Tây Dương) và soạn thảo hiến chương Đại Tây Dương: \"Không có sự thay đổi nào về lãnh thổ mà không có sự đồng tình của nhân dân nơi đó, quyền của các dân tộc tự lực chọn chính thể, hợp tác quốc tế vì tiến bộ kinh tế và an ninh, các dân tộc đều được hưởng các nguồn nguyên liệu và được tự do trên biển...\"\nBản hiến chương này là cơ sở Hiến chương Liên hiệp quốc vào nǎm 1945.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','08', 'Ngày 14-8-1947, Chính phủ Anh công bố sắc lệnh \"Công nhận nền độc lập của Ấn Độ\" Chia Ấn Độ thành hai nước trên cơ sở tôn giáo: Ấn Độ của người Ấn Độ giáo, Pakistan của người hồi giáo.\nCùng ngày, cộng hoà Hồi giáo Pakistan ra đời.\nNgày 15-4, Nêru, lãnh đạo Đảng Quốc đại, long trọng kéo lá cờ quốc kỳ Ấn Độ tại thủ đô Niu Đê li, mở đầu trang sử mới của Ấn Độ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','09', 'Ngày 14-9-1946, Đoàn đại biểu Chính phủ ta lên đường đi dự đàm phán ở Pháp.\nCuộc đàm phán thất bại do thái độ ngoan cố và hiếu chiến của thực dân Pháp. Ngày 14-9, Hồ Chủ tịch đã ký tạm ước với Pháp để tranh thủ thời gian tiếp tục xây dựng lực lượng chuẩn bị cho cuộc kháng chiến toàn quốc mà Người biết chắc chắn sẽ xảy ra. Ngày 16-9, Hồ Chủ tịch rời Pari và đến Hải Phòng ngày 20-10.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','09', 'Ngày 14-9-1952, Thủ tướng Chính phủ có Nghị định truy tặng liệt sĩ Phan Khắc Trình (tức Dương Xuân Ngô), Phó trưởng ban Điệp báo Ty Công an Hà Nội, Huân chương Kháng chiến hạng ba.\nÔng Phan Khắc Trình có công chấn chỉnh lại tổ chức điệp báo nội thành trong năm 1950 đã thu được nhiều tài liệu tối mật. Tháng 6-1951, ông bị giặc Pháp bắt và tra tấn rất dã man nhưng không khai các bí mật, bảo toàn được cơ sở. Ông đã bị địch sát hại.\nTháng 8-1955, Chủ tịch nước Cộng hoà xã hội chủ nghĩa Việt Nam đã quyết định truy tặng liệt sĩ Phan Khắc Trình danh hiệu \"Anh hùng lực lượng vũ trang nhân dân\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','09', 'Thành phố Vinh, thành phố đầu tiên ở miền Bắc bắn rơi chiếc máy bay Mỹ thứ 100 trên địa phương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','10', 'Ngày 14-10-1890 tỉnh Thái Bình được thành lập. Hiện nay tỉnh có diện tích 1.508 km2 và khoảng 1.789.200 người. Thái Bình có các đơn vị hành chính: thị xã Thái Bình và 7 huyện là Quỳnh Phụ, Hưng Hà, Đông Hưng, Vũ Thư, Kiến Xương, Tiền Hải và Thái Thuỵ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','10', 'Ngày 14-10-1930 hơn 400 nông dân từ các xã thôn Lâm, Đồng Cao, Thanh Giám, hàng ngũ chỉnh tề, cờ đỏ búa liềm rầm rậm kéo nhau lên huyện lỵ Tiền Hải, vừa đi vừa hò vang các khẩu hiệu Cách mạng. Trước khí thế dũng mãnh của quần chúng, tri huyện bỏ chạy, nhưng tên lục sự gian ác đã cưỡng bức lính bắn xả vào đoàn biểu tình làm 8 người chết, 12 người bị thương. Được tin báo, bọn địch trên tỉnh đưa lính về làng xã có người đi biểu tình để khủng bố, nhưng nông dân Tiền Hải vẫn tìm mọi cách duy trì cơ sở tổ chức, kiên trì đấu tranh. Kết quả bọn địch phải nhượng bộ một số quyền lợi như: trả tiền đào sông Cốc, rút một số ruộng đem chia cho nông dân. \nCuộc đấu tranh của nhân dân Tiền Hải có tiếng vang lớn. Đảng cộng sản Pháp đã phát động phong trào phản đối khủng bố Tiền Hải.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','10', 'Từ ngày 14-10 đến 10-12-1952, Bộ Tổng tư lệnh quân đội ta đã tổ chức, chỉ đạo, chỉ huy chiến dịch Tây Bắc \nChiến dịch này diễn ra qua ba đợt\nBốn đại đoàn chủ lực ta vượt sông Thao, sông Đà, nhanh chóng đánh tan hệ thống phòng ngự của địch tại sông Đà và Mộc Châu.\nMột trung đoàn chủ lực từ Lao Cai đánh sau lưng địch, giải phóng nhiều vùng ở Tây Bắc như Thuận Châu, Tuần Giáo, Sơn La, Điện Biên phủ. Cùng lúc đó, hai đại đoàn của ta ở đồng bằng Bắc Bộ cũng hoạt động mạnh để mở rộng căn cứ kháng chiến. Quân ta đã dành được nhiều thắng lợi to lớn. Ta loại khỏi vòng chiến đấu 13.800 tên địch, phá tan âm mưu củng cố các xứ tự trị của địch, giải phóng 28.500 km2 với 25 vạn dân, nối liền Tây Bắc với Việt Bắc.\nVới chiến thắng Tây Bắc ta giữ vững được chủ quyền chủ động chiến lược trên chiến trường Bắc Bộ. \nThực dân pháp càng lún sâu vào thế phòng ngự bị động. Từ đây, một cục diện mới mở ra trên chiến trường Đông Dương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','10', 'Lần đầu tiên trên miền Bắc, ngày 14-10-1967 trung đội lão dân quân xã Hoằng Trường, huyện Hoằng Hoá, tỉnh Thanh Hoá, với 92 viên đạn súng bộ binh đã bắn rơi một máy bay phản lực của Mỹ. Đây cũng là chiếc máy bay thứ 2.400 bị bắn rơi trên miền Bắc. Ngày 17-10 Chủ tịch Hồ Chí Minh đã ký lệnh thưởng Huân chương quân công hạng ba cho trung đội lão dân quân xã Hoàng Hoá và gửi thư khen các cụ: \"Tuổi cao chí càng cao\" nêu gương cho đồng bào cả nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','10', 'Ngày 14-10-1946, đại diện 25 nước kinh tế phát triển đã thành lập Tổ chức tiêu chuẩn hoá quốc tế (Gọi tắt là ISO) nhằm đáp ứng các yêu cầu phát triển thương mại và giao lưu quốc tế nói riêng và kinh tế nói chung. Việt Nam là thành viên của tổ chức này từ năm 1977 \nMỗi năm, Ngày tiêu chuẩn hoá quốc tế có chủ đề riêng. Ở nước ta, cơ quan tiêu chuẩn hoá của Nhà nước được thành lập từ năm 1962. Công tác tiêu chuẩn hoá đã có những đóng góp nhất định vào việc thúc đẩy sản xuất phát triển, xây dựng và bảo vệ tổ quốc.\nCông tác tiêu chuẩn hoá của Việt Nam đang được cải tiến, đổi mới theo phương hướng hoà nhập, đồng bộ với khu vực Đông Nam Á và thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','11', 'Ngày 14-11-1953, Hội nghị toàn quốc lần thứ nhất của Đảng Lao động Việt Nam đã khai mạc. Gần 200 đại biểu trong cả nước đã về dự. Hội nghị đã nghe và thảo luận báo cáo của đồng chí Hồ Chí Minh về tình hình trước mắt và nhiệm vụ cải cách ruộng đất.\nThực hiện khẩu hiệu \"Người cày có ruộng\" cải cách ruộng đất đã làm cho tinh thần kháng chiến của hàng triệu nông dân được phát động mạnh mẽ, liên minh công - nông thực sự được củng cố. Mặt trận dân tộc thống nhất được vững chắc thêm, chính quyền, quân đội được tǎng cường, mọi hoạt động kháng chiến được đẩy mạnh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','11', 'Ngày 14-11-1998, Việt Nam, Peru và Nga trở thành thành viên chính thức của APEC tại cuộc gặp bộ trưởng của APEC ở Kualar Lumpur.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','11', 'Laibơnit sinh nǎm 1646 và mất ngày 14-11-1716.\nÔng là nhà toán học, vật lý học, triết học người Đức là một trong hai nhà toán học vĩ đại sáng tạo ra phép tính vi phân, tích phân.\nTên của Laibơnit đã được đặt cho một dãy núi ở phần trông thấy và cho một miệng núi lửa ở phần không trông thấy của mặt trǎng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','11', 'Gióogiơ Vinhem Phriêđrich Hêghen sinh nǎm 1770 và từ trần ngày 14-11-1831.\nÔng là nhà triết học cổ điển Đức.\nTriết học Hêghen một mặt có xu hướng tiến bộ, mặt khác lại có những tư tưởng bảo thủ. Thành quả quan trọng nhất của triết học Hêghen là phép biện chứng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','11', 'Giaoaháclen Nêru sinh ngày 14-11-1889 và từ trần nǎm 1964.\nKhi Ấn Độ tuyên bố độc lập (nǎm 1947), ông giữ chức Thủ tướng Chính phủ. Là nhà hoạt động chính trị và Nhà nước nổi tiếng của Ấn Độ, Nêru còn là người có nhiều đóng góp vào sự nghiệp hoà bình thế giới. Ông là một trong những người đề xướng 5 nguyên tắc chung sống hoà bình và sáng lập tổ chức Các nước không liên kết.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','11', 'Ngày 14-11-1911, một người Mỹ tên là Ely đã lái máy bay hai lớp cánh Curtis trang bị 2 động cơ 50 sức ngựa hạ cánh xuống một mặt bằng bố trí trên thượng tầng đuôi của một chiếc thiết giáp hạm. Vài phút sau, ông lại cất cánh.\nTàu sân bay được hình thành và sử dụng từ đấy.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','12', 'Từ ngày 14 đến 20-12-1976 đã diễn ra đại hội đại biểu toàn quốc lần thứ tư Đảng Lao động Việt Nam. 1008 đại biểu thay mặt cho hơn 1,5 triệu Đảng viên trong cả nước đã về Thủ đô Hà Nội dự Đại hội.\nĐại hội đã thảo luận và nhất trí quyết định đường lối xây dựng kinh tế xã hội chủ nghĩa trong giai đoạn mới; quyết định phương hướng, nhiệm vụ và mục tiêu chủ yếu của kế hoạch 5 nǎm; quyết định đường lối xây dựng đảng để nâng cao nǎng lực lãnh đạo và sức chiến đấu của Đảng trong giai đoạn mới, đổi tên Đảng Lao động Việt Nam thành Đảng cộng sản Việt Nam. Đồng chí Lê Duẩn được bầu làm Tổng Bí thư Ban Chấp hành Trung ương.\nĐại hội lần thứ tư của Đảng\" là một cái mốc mới quan trọng trên con đường tiến lên của Cách mạng Việt Nam, là Đại hội toàn thắng của sự nghiệp giải phóng dân tộc và thống nhất Tổ quốc, là đại hội đưa cả nước tiến lên chủ nghĩa xã hội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','12', 'Từ ngày 14 đến 16-12-1977 đã diễn ra Hội nghị Thương binh, gia đình liệt sĩ tiêu biểu toàn quốc tại Hà Nội. Nhiều đại biểu thương binh, gia đình liệt sĩ tiêu biểu toàn quốc về dự hội nghị như: Cụ Lê Vǎn Đại, là anh đồng Lý Tử Trọng; cụ Nguyễn Thị Lành, quận Củ Chi thành phố Hồ Chí Minh có 8 con trai và 2 cháu là liệt sĩ; cụ Trần Thị Mít, tỉnh Bình Trị Thiên có 7 con trai là liệt sĩ; cụ Nguyễn Vǎn Thoàn, tỉnh Quảng Nam - Đà Nẵng, thân sinh của anh hùng liệt sĩ Nguyễn Vǎn Trỗi; ông Lê Vǎn Phước - thân sinh nữ Anh hùng liệt sĩ Lê Thị Hồng Gấm... Hội nghị cũng đồng thời tuyên dương, ghi nhận những hy sinh, những đóng góp của các thương binh, gia đình liệt sĩ vào sự nghiệp cách mạng trong suốt quá trình đấu tranh giải phóng và xây dựng Tổ quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '14','12', 'Ngày 14-12-1977 giáo sư Tôn Thất Tùng được tặng Huân chương vàng quốc tế của Viện Hàn lâm Phẫu thuật Pari.\nHuân chương vàng quốc tế Lannơlônggiơ là giải thưởng quốc tế cao nhất của ngành mổ xẻ Pháp, cứ 5 nǎm tặng một lần, dành cho nhà mổ xẻ có phát minh và góp phần đẩy mạnh sự phát triển của ngành mổ xẻ trên thế giới.\nTrong 50 nǎm, chỉ có 8 giáo sư được tặng Huân chương này: Gôddiê (Pháp), Cơráppho (thuỵ Điển), Oanghen Stin (Mỹ)... Giáo sư Tôn Thất Tùng là người thứ 9 được giải thưởng này. Ông là Viện sĩ của Viện Hàn lâm Y học Liên Xô, Viện Hàn lâm Phẫu thuật Pari và một số Hội phẫu thuật quốc tế khác.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','12', 'Pôn Êluya (Paul Eluard), nhà thơ lớn xã hội chủ nghĩa Pháp, sinh ngày 14-12-1895.\nGiai đoạn đầu thơ ông theo trào lưu siêu thực. Từ những nǎm 30 ông là nhà thơ chống lại tư bản, chống phát xít gây chiến, chống chiến tranh. Các tác phẩm chính là: Đường lối tự nhiên, Bài ca toàn vẹn, Thơ và sự thật, Bảy bài thơ tình trong chiến tranh v.v... Thơ của ông đầy khát vọng và ước mơ tự do, chan chứa tính nhân đạo chủ nghĩa cao cả, là tiếng nói yêu đương nồng nàn làm xao xuyến lòng người. Ông mất nǎm 1952.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','12', 'Tổ chức vǎn hoá khoa học và giáo dục của Liên hiệp quốc, gọi tắt là UNESCO, được thành lập ngày 14-12-1946 tại Luân Đôn (Anh). Thành viên tổ chức bao gồm các quốc gia thành viên Liên hiệp quốc (trong đó có Việt Nam). Đây là một tổ chức xúc tiến việc phát triển giáo dục, vǎn hoá và khoa học của Liên hiệp quốc. Mục đích của tổ chức là góp phần bảo vệ hoà bình, an ninh quốc tế. Tổ chức có trụ sở tại Pari (Pháp). Tổ chức này đã tài trợ cho nhiều công trình bảo vệ di tích vǎn hoá và phát triển khoa học của Việt Nam và thế giới. Tờ báo Bản tin người đưa tin UNESCO được dịch ra tiếng Việt.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','12', 'Ngày 14-12-1992, Mỹ nới lỏng lệnh cấm vận kéo dài 17 nǎm đối với Việt Nam và cho phép các công ty ký các hợp đồng với Việt Nam')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','12', 'Ngày này năm 1993, Liên minh châu Âu thiết lập mối quan hệ ngoại giao với Nam Phi, chấm dứt thời kì cô lập nhiều nǎm đối với nước này do chính sách phân biệt chủng tộc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','12', 'Ngày 14-12-1997, Tổng thống Iran, Mohammed Khatami, tuyên bố sẵn sàng thiết lập quan hệ với Hoa Kỳ, đây là lời tuyên bố đầu tiên về vấn đề này kể từ nǎm 1979.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '14','12', 'Hội nghị thượng đỉnh Đông Á tổ chức lần đầu tiên tại Malaysia ngày 14-12-2005, một mốc quan trọng về phát triển chính trị và kinh tế của khu vực. Cũng tại đây, các nhà lãnh đạo ASEAN lập ra nhóm các nhân vật nổi bật để xây dựng Hiến chương ASEAN.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','01', 'Ngày 15-1-1937 Báo Nhành lúa ra số đầu tiên tại Huế. Đây là một tờ báo của những người Cộng sản ở Trung Kỳ do Hải Triều (tức Nguyễn Khoa Văn) làm chủ bút. Tờ báo đánh dấu thời kỳ Mặt trận dân chủ đấu tranh trên lĩnh vực báo chí công khai. Do bị đàn áp, tờ báo ra được tới số 9 vào ngày 13-9-1937 thì bị đình bản.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','01', 'Ngày 15-1-1952, chị Bùi Thị Cúc, chiến sĩ công an tỉnh Hưng Yên được Chủ tịch Hồ Chí Minh ký sắc lệnh truy tặng Huân chương Độc lập hạng ba.\nChị Bùi Thị Cúc đã có công trừ gian xây dựng cơ sở kháng chiến địa phương. Chị bị địch bắt, chúng tra tấn cực kỳ dã man, chị vẫn giữ vững tinh thần, không khai báo và đã hy sinh khi mới 23 tuổi.\nTháng 8-1995, Chủ tịch nước Cộng hoà xã hội chủ nghĩa Việt Nam đã ra quyết định truy tặng chị Bùi Thị Cúc danh hiệu \"Anh hùng lực lượng vũ trang nhân dân\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','01', 'Ngày 15-1-1970 Uỷ ban Thường vụ Quốc Hội thông qua Pháp lệnh đặt các danh hiệu vinh dự cấp Nhà nước: Anh hùng lao động và Anh hùng lực lượng vũ trang nhân dân. Pháp lênh quy định danh hiệu, tiêu chuẩn đối với mỗi người hoặc đơn vị để được tặng. Ngày 27-1 Chủ tịch Tôn Đức Thắng đã ký Lệnh công bố.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','01', 'Trong cuộc chiến tranh phá hoại lần thứ 2 trên miền Bắc nước ta từ 16-4-1972 đến 29-12-1972, đế quốc Mỹ đã huy động đến mức cao nhất từ trước đến nay lực lượng hải quân, không quân kể cả không quân chiến lược gồm các loại máy bay hiện đại nhất lúc đó đánh phá ác liệt Hà Nội, Hải Phòng và một số địa phương khác. Quân và dân miền Bắc đã kiên quyết giáng trả thích đáng, đập tan âm mưu của Mỹ.\nNgày 15-1-1973 Tổng thống Mỹ Ních xơn phải tuyên bố chấm dứt toàn bộ việc ném bom, bắn phá, thả mìn miền Bắc nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','01', 'Sư đoàn phòng không Hà Nội (tức sư đoàn 361) được Nhà nước tuyên dương đơn vị Anh hùng lực lượng vũ trang nhân dân ngày 15-1-1976.\nSư đoàn đã bắn rơi 591 máy bay Mỹ (trong đó có 35 pháo đài bay B52), 225 chiếc rơi tại chỗ, bắt sống 92 giặc lái, bảo vệ vùng trời thủ đô Hà Nội và miền Bắc trong những nǎm có chiến tranh phá hoại của đế quốc Mỹ.\nĐặc biệt tối 18-12-1972, sư đoàn đã phóng quả tên lửa đầu tiên hạ ngay một chiếc B52 mở đầu trận \"Điện biên Phủ trên không\".\nSinh thời, Hồ Chủ tịch đã 7 lần tới thǎm các trận địa của sư đoàn 361 và khen ngợi chiến sĩ, cán bộ của sư đoàn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','01', 'Ông Dương Quảng Hàm sinh ngày 15-1-1898 quê ở tỉnh Hưng Yên, qua đời cuối nǎm 1946 tại Hà Nội.\nNǎm 1920, ông tốt nghiệp trường cao đẳng sư phạm, rồi làm giáo sư trường trung học bảo hộ (tức trường Bưởi, nay là trường Chu Vǎn An, Hà Nội).\nÔng Dương Quảng Hàm là nhà giáo dục và nhà nghiên cứu vǎn học - Ông còn để lại nhiều tác phẩm có giá trị, nổi bật nhất là cuốn \"Việt Nam Vǎn học sử yếu\" xuất bản nǎm 1941, sách giáo khoa của nhiều thế hệ học sinh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','01', 'Thiếu tướng Hoàng Sâm, tên thật là Trần Vǎn Kỳ sinh nǎm 1915 ở tỉnh Quảng Bình, mất ngày 15-1-1969.\nCuối nǎm 1944, ông là Đội trưởng Việt Nam tuyên truyền giải phóng quân, chỉ huy các trận chiến thắng Phay Khắt, Nà Ngần. Nǎm 1947 ông là chỉ huy trưởng Mặt trận Tây Tiến, có nhiều công lao trong việc tiêu diệt địch, xây dựng cơ sở, phát triển chiến tranh du kích. Từ nǎm 1952 đến nǎm 1954, ông là Đại đoàn trưởng đại đoàn 304 và phái viên của Bộ Tổng tư lệnh đi các chiến dịch.\nTừ cuối nǎm 1955 đến đầu nǎm 1969 là tư lệnh nhiều quân khu. Lúc qua đời là Bí thư quân khu quân khu uỷ và Tư lệnh Quân khu III.\nThiếu tướng Hoàng Sâm đã được Nhà nước truy tặng Huân chương Quân công hạng nhất.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','01', '15-1-1944, Uỷ ban Cố vấn châu Âu quyết định chia cắt nước Đức.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','01', 'Môlie (Molière) là nhà viết hài kịch Pháp. Ông sinh ngày 15-1-1622. Thời trẻ ông học luật nhưng lại ham mê sân khấu. Từ nǎm 1644 ông thành lập đoàn kịch. Ông là diễn viên và kiêm viết kịch bản. Các vở kịch của ông chủ yếu phê phán giai cấp quí tộc rởm đời, bọn tu hành lợi dụng tôn giáo; và luôn luôn đứng về phía nhân dân lao động. Ông nổi tiếng với các vở \"Trường học làm chồng\", \"Trường học làm vợ\", \"Jac Tuyphơ\", \"Đông Goǎng\", \"Người ghét đời\", \"Lão hà tiện\", \"Trưởng giả học làm quý tộc\", \"Người bệnh tưởng\".\nÔng mất vào ngày 10-8-1673.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','01', 'Xôphia Vaxiliépna Cavalépxcaia là nữ bác học xuất sắc người Nga, sinh ngày 15-1-1850 tại Matxcơva. Nǎm 1873 bà đã nghiên cứu thành công về Lý thuyết phương trình vi phân đạo hàm riêng và đưa vào giáo trình cơ bản của toán giải tích. Sau đó bà có công trình Bổ sung và nhận xét về nghiên cứu hình dáng vành sao Thổ. Nǎm 1883 bà là người phụ nữ đầu tiên trên thế giới được phong chức phó giáo sư rồi giáo sư của Vương quốc Thụy Điển. Ngoài nghiên cứu khoa học bà còn viết vǎn, viết kịch như tiểu thuyết Người theo chủ nghĩa hư vô (1884), vở kịch Cuộc đấu tranh vì hạnh phúc (1887). Nǎm 1888 bà hoàn thành công trình Về sự quay của một vật rắn xung quanh một điểm đứng yên và được giải thưởng của Viện Hàn lâm khoa học Pari và Thụy Điển. Bà qua đời vào nǎm 1891 ở tuổi 41.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','02', 'Ngày 15-2-1947, khai mạc Hội nghị chính trị viên toàn quốc lần thứ nhất do Tổng quân ủy triệu tập. Hội nghị đã xác định vai trò lãnh đạo tuyệt đối của Đảng trong quân đội, đặt hệ thống tổ chức Đảng trong quân đội từ Tổng quân ủy đến mỗi chi bộ. Xác định nhiệm vụ lãnh đạo quân sự và nắm vững quân đội của mỗi đảng viên, đặt mối quan hệ giữa hệ thống Đảng trong quân đội và các địa phương; củng cố quân đội chủ lực và chấn chỉnh tổ chức dân quân; tổ chức chính trị viên từ trên xuống dưới, đào tạo cán bộ quân sự và ra tờ báo \"Vệ Quốc quân\" (là báo Quân đội nhân dân hiện nay) làm cơ quan giáo dục toàn quân. Hội nghị cũng quyết định 12 điều kỷ luật dân vận và 10 nhiệm vụ công tác chính trị trong quân đội Cách mạng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','02', 'Ngày 15-2-1961, tại chiến khu Đ ở Nam Bộ, một hội nghị quân sự quan trọng được tổ chức để thống nhất các lực lượng vũ trang nhân dân giải phóng ở miền Nam (lúc này có đủ ba thứ quân là bộ đội chủ lực, bộ đội địa phương và dân quân du kích). Hội nghị quyết định: Thống nhất tất cả các lực lượng vũ trang giải phóng miền Nam thành \"Quân giải phóng miền Nam\" và đặt dưới sự chỉ huy chung là Bộ tư lệnh các lực lượng vũ trang giải phóng miền Nam Việt Nam.\nĐại biểu của Mặt trận dân tộc giải phóng miền Nam đã trao cho quân đội và các binh chủng thuộc quân giải phóng các lá quân kỳ có dòng chữ: \"Giải phóng quân anh dũng chiến thắng\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','02', 'Ngày 8-2-1967, Tổng thống Mỹ Giônxơn đã gửi thư tới Chủ tịch Hồ Chí Minh.\nNgày 15-2-1967, Chủ tịch Hồ Chí Minh đã gửi thư trả lời tổng thống Mỹ.\nBằng những lời đanh thép, Hồ Chủ tịch đã vạch trần âm mưu xảo quyệt, ngoan cố của đế quốc Mỹ và khẳng định quyết tâm của nhân dân Việt Nam đánh thắng giặc Mỹ xâm lược. Người chỉ rõ: \"Chính phủ Mỹ đã gây ra chiến tranh xâm lược Việt Nam thì con đường đưa đến hoà bình ở Việt Nam là Mỹ phải chấm dứt xâm lược. Nếu chính phủ Mỹ muốn nói chuyện thì trước hết Mỹ phải chấm dứt không điều kiện việc ném bom và mọi hành động chiến tranh khác chống nước Việt Nam Dân chủ Cộng hoà\".\nCuối thư, Người viết: \"Nhân dân Việt Nam quyết không khuất phục trước vũ lực và quyết không nói chuyện trước sự đe doạ của bom đạn\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','02', 'Nhà báo, nhà vǎn, nhà hoạt động chính trị Nguyễn Vǎn Nguyễn sinh ngày 15-2-1910 tại tỉnh Mỹ Tho cũ (nay thuộc tỉnh Tiền Giang), mất nǎm 1953 ở tỉnh Bình Định.\nNǎm 1930 ông là một trong số những Đảng viên đầu tiên của Đảng cộng sản Đông Dương. Trước nǎm 1945, ông viết bài cho nhiều báo ở Nam Kỳ, lên án chế độ thuộc địa của thực dân Pháp nên nhiều lần bị địch bắt, xử án tù. Khi Nhật đảo chính Pháp ở Đông Dương (tháng 3-1945), ông vượt ngục về sinh sống và hoạt động tại Sài Gòn. Tại đây ông được bầu vào Xứ ủy Nam Kỳ, rồi tham gia lãnh đạo cướp chính quyền và tổ chức kháng chiến ở Nam Bộ. Ông đã giữ các chức vụ: Giám đốc Sở Thông tin Nam Bộ và Đài Phát thanh tiếng nói Nam Bộ, chủ bút báo Cứu Quốc Nam Bộ.\nÔng Nguyễn Vǎn Nguyễn còn để lại các tác phẩm: Án mạng đường Bácbiê, Lá rụng về cội, Cán bộ Cách mạng, Tháng Tám trời mạnh thu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','02', 'Liệt sĩ Kim Đồng, tên thật là Nông Vǎn Dền, sinh nǎm 1929, người dân tộc Nùng, quê ở xã Trường Hà, huyện Hà Quảng, tỉnh Cao Bằng.\nTham gia Cách mạng từ nǎm 11 tuổi, làm giao thông liên lạc cho các cán bộ hoạt động bí mật.\nTuy còn nhỏ tuổi, Kim Đồng đã cǎm thù giặc và tỏ ra dũng cảm, thông minh - không sợ nguy hiểm, gian khổ, là đội trưởng Đội nhi đồng cứu vong.\nKim Đồng bị địch sát hại ngày 15-2-1943 khi mới 14 tuổi. Tháng 7-1997, Kim Đồng được Nhà nước truy tặng danh hiệu \"Anh hùng lực lượng vũ trang nhân dân\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','03', 'Ngày 15-3-1874, tại Sài Gòn, Triều đình Huế đã ký với Pháp một vǎn bản mang tên \"Hiệp ước hoà bình và liên minh\" (còn gọi là Hiệp ước Giáp Tuất). Đây là hiệp ước đầu hàng, mà nội dung chính là: Triều đình Huế chính thức công nhận chủ quyền của Pháp ở cả Lục tỉnh Nam Kỳ, Triều đình Huế không được ký hiệp ước thương mại với nước nào khác ngoài Pháp, phải thay đổi chính sách đối với đạo thiên chúa, phải để cho giáo sĩ tự do đi lại và hoạt động trên khắp nước Việt Nam; phải mở cửa sông Hồng, các cửa biển Thị Nại (thuộc Quy Nhơn), Ninh Hải (Hải Dương) và thành phố Hà Nội cho Pháp buôn bán. Tại các nơi đó, Pháp đặt lãnh sự quán và lãnh sự Pháp có quân lính riêng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','03', 'Ngày 15-3-1945, Tổng Bộ Việt Minh phát \"Hịch kháng Nhật cứu nước\". Nội dung vạch rõ: Giặc Nhật là kẻ thù số 1 và báo trước rằng cách mạng nhất định thắng lợi. Lời hịch kêu gọi: Giờ kháng Nhật cứu nước đã đến. Kịp thời nhằm theo lá cờ đỏ sao vàng nǎm cánh của Việt Minh. Cách mạng Việt Nam thành công muôn nǎm. Nước Việt Nam dân chủ cộng hoà muôn nǎm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','03', 'Từ ngày 15-3 đến ngày 30-4-1949, trên địa bàn các tỉnh Cao Bằng, Bắc Cạn, Lạng Sơn, Bộ tư lệnh quân đội ta đã chỉ đạo chiến dịch nhằm tiêu diệt sinh lực địch, làm tan rã khối ngụy binh, làm tê liệt đường số 4, triệt tiếp tế của địch ở khu vực bắc - đông bắc.\nChiến dịch này chia làm 2 đợt:\n- Đợt 1 từ ngày 15-3 đến 14-4, ta tiến công địch trên đường số 4, từ Thất Khê đến Na Sầm.\n- Đợt 2 từ ngày 25-4 đến ngày 30-4. Ngày 25, ta phục kích ở đoạn Bông Lau - Lũng Phầy, ta tiêu diệt một đoàn xe có hơn 100 chiếc, diệt 500 lính Âu Phi, phá huỷ 53 xe vận tải, 500 phuy xǎng, thu nhiều vũ khí đạn dược.\nTrong các ngày 26 và 27-4 ta bao vây một số đồn bốt địch trên đường Cao Bằng, Trà Lĩnh, diệt đồn Bàn Pái; địch ở đồn Pò Mã, Pò Pạo phải rút chạy.\nTrong cả chiến dịch Cao - Bắc - Lạng, ta tiêu diệt bắt sống hơn 1.400 tên địch, san bằng 4 cứ điểm, đánh thiệt hại 4 đồn, phá huỷ hơn 80 xe quân sự, thu nhiều quân trang, quân dụng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','03', 'Thực tiễn cho thấy lịch sử nền điện ảnh Việt Nam chỉ thực sự ra đời dưới chế độ Cách mạng và được ghi nhận bằng sự kiện Chủ tịch Hồ Chí Minh ký sắc lệnh thành lập Doanh nghiệp Điện ảnh và nhiếp ảnh Việt Nam vào ngày 15-3-1953.\nĐịa danh \"đồi cọ\" - một địa danh thuộc tỉnh Phú Thọ đã đi vào tâm trí các nhà điện ảnh Việt Nam như một kỷ niệm có ý nghĩa lịch sử gắn với sự ra đời của nền Điện ảnh Cách mạng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','03', 'Ngày 15-3-1960, trong cao trào \"Đồng khởi\" của Bến Tre, lần đầu tiên, hơn 5.000 phụ nữ gồm đủ các thành phần, đủ mọi lứa tuổi của các xã Phước Hiệp, Bình Khánh, Định Thuỷ, Đa Phước Hội, An Định, Thành Thới họp thành một đoàn người đội khǎn tang, mặc áo rách, bồng con, kéo vào quận Mỏ Cày, đòi chấm dứt chiến tranh, đòi bồi thường tính mạng, đòi trừng trị bọn ác ôn ở Phước Hiệp.\nBè lũ Mỹ - Diệm rất sợ lực lượng hùng hậu này và chúng đã phải gọi là \"Đội quân tóc dài\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','03', 'Ngày 15-3-1975, Quân ủy Trung ương điện cho Bộ Chỉ huy chiến dịch Tây Nguyên, nêu lên 3 khả nǎng;\n- Một là địch có thể tǎng cường phản kích,\n- Hai là nếu địch bị đánh thì chúng co cụm về Plâycu, ta cần hình thành bao vây ngay Plâycu,\n- Ba là dự tính việc rút lui chiến dịch của địch.\nBắt đầu từ ngày 15-3 có nhiều dấu hiệu địch rút quân khỏi Plâycu. Đến 21 giờ đêm ngày 16-3, ta nhận được tin địch đang rút chạy khỏi Plâycu, một đoàn xe đã qua ngã ba Mỹ Thanh, theo hướng đường số 7, kho đạn ở Plâycu đang nổ và có nhiều đám cháy trong thị xã này.\nMột đại tá ngụy bị ta bắt đã khai: Do bị đòn thảm hại ở Buôn Ma Thuột nên ngày 14-3-1975, Nguyễn Vǎn Thiệu đã ra lệnh cho Phạm Vǎn Phúc, tư lệnh quân đoàn hai rút khỏi Tây Nguyên, về giữ đồng bằng ven biển để bảo toàn lực lượng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','04', 'Tháng tư nǎm 1936, Đảng Cộng sản Đông Dương đã gửi thư ngỏ tới đến các đảng phái, lực lượng chính trị về việc thành lập mặt trận thống nhất nhân dân chống đế quốc. Bức thư xác định rõ: mục tiêu của những người Cộng sản là thống nhất tǎng cường vào củng cố tất cả các lực lượng phản đế ở Đông Dương; chuẩn bị cho cuộc cách mạng dân tộc giải phóng đất nước. Sau khi phân tích tình hình thế giới và Đông Dương, bức thư đưa ra những gợi ý cụ thể về phương thức, biện pháp để tiến tới thống nhất, hành động. Đây là chủ trương đúng đắn của Đảng Cộng sản Đông Dương nhằm đoàn kết các lực lượng, phe phái chính trị trong cả nước vì mục tiêu giải phóng đất nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','04', 'Ngày 15-4-1945, Ban chấp hành Trung ương Đảng Cộng sản Đông Dương triệu tập hội nghị quân sự cách mạng Bắc Kỳ tại Hiệp Hoà tỉnh Bắc Giang và do đồng chí Trường Chinh chủ trì, Hội nghị nhằm giải quyết các vấn đề quân sự đã được hội nghị tháng 3-1945 của thường vụ Trung ương Đảng nêu ra. Sau khi phân tích tình hình thế giới và cao trào chống Nhật của nhân dân ta, nghị quyết của hội nghị vạch rõ: tình thế đã đặt nhiệm vụ quân sự lên trên tất cả các nhiệm vụ quan trọng và cần kíp lúc này. Chúng ta phải tích cực phát triển chiến tranh du kích, gây dựng cǎn cứ địa kháng Nhật để chuẩn bị cho tổng khởi nghĩa. Hội nghị đã quyết định thống nhất Cứu quốc quân và Việt Nam tuyên truyền giải phóng quân thành Việt Nam giải phóng quân. Một ủy ban quân sự được thành lập gồm các đồng chí: Võ Nguyên Giáp, Vǎn Tiến Dũng, Lê Vǎn Nghị, Trần Đǎng Ninh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','04', 'Ngày 15-4-1946, Bộ Quốc phòng khai giảng khoá đầu tiên trường võ bị Trần Quốc Tuấn (Tức là trường sĩ quan lục quân sau này) để đào tạo những sĩ quan ưu tú của quân đội cách mạng Việt Nam. Ngày 14-4 trở thành ngày truyền thống của trường sĩ quan lục quân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','04', 'Ngày 15-4-1949, Bộ Quốc phòng đã quyết định thành lập Đại đoàn 308 (Còn gọi là sư đoàn Quân Tiên phong) là đại đoàn chủ lực đầu tiên của quân đội ta.\nTrong hai cuộc kháng chiến chống giặc Pháp và giặc Mỹ, Đại đoàn 308 đã lập được nhiều chiến công vẻ vang.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','04', 'Ngày 15-4-1992, Quốc Hội khoá 8 tại kỳ họp thứ 11 đã thông qua hiến pháp mới của nước ta. Hiến Pháp mới có 147 điều, 12 chương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','04', '15-4-1991, các ngoại trưởng của các nước trong Cộng đồng châu Âu đã đồng ý xoá bỏ tất cả các lệnh cấm vận còn lại đối với Nam Phi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','05', 'Chế độ khoa cử và khoa trường tồn tại khá lâu qua các triều đại phong kiến Việt Nam, nói chung đã đào tạo và phát hiện nhiều nhân tài đóng góp trí tuệ cho bộ máy cai trị và cho kho tàng vǎn hoá dân tộc. Từ sau khi thực dân Pháp xâm lược Việt Nam, chữ Quốc ngữ và tiếng Pháp dần dần thay thế chữ Nho, chữ Nôm. Ngày 15-5-1919, khoa thi cuối cùng của nền thi cử truyền thống đã diễn ra và kết thúc với 7 đệ tam giáp đồng tiến sĩ và 16 phó bảng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','05', 'Trong thời gian diễn ra hội nghị Trung ương 8 tại Pác Bó, Trung ương Đảng đã chỉ thị cho Đoàn thanh niên cứu quốc tổ chức và phụ trách đội TNTP và Hội nhi đồng cứu vong. Ngày 15-5-1941 được coi là ngày thành lập Đội TNTP Hồ Chí Minh hiện nay. Cùng cha anh mình, các thế hệ thiếu nhi Việt Nam đã góp phần vào cuộc đấu tranh Cách mạng. Tên tuổi các đội viên như: Kim Đồng, Vừ A Dính, Lê Vǎn Tám, Kpa Klơn, Nguyễn Bá Ngọc...đã khắc vào trang sử vẻ vang của đội. Trải qua các tên gọi như: Hội nhi đồng cứu quốc (1945) Đội thiếu nhi tháng 8 (1951). Đội TNTP (1950) và Đội TNTP Hồ Chí Minh và Đội nhi đồng Hồ Chí Minh (1970). Ngày nay thiếu nhi Việt Nam đang thi đua phấn đấu theo mục tiêu: Con ngoan - trò giỏi - Đội viên tích cực.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','05', 'Thực hiện nghị quyết của Hội nghị Cách mạng quân sự Bắc Kỳ. Ngày 15-5-1945 tại chợ Chu, Thái Nguyên đã diễn ra lễ thành lập Việt Nam giải phóng quân gồm các đồng chí Võ Nguyên Giáp, Trần Đǎng Ninh. Đây là lực lượng vũ trang được tổ chức hoàn chỉnh và trang bị tương đối đầy đủ. \nViệt Nam giải phóng quân gồm các chi đội chiến đấu. Các chi đội chiến đấu đã tham gia giành chính quyền và giữ chính quyền ở một số địa phương trước và trong Cách mạng, trưởng thành mau chóng và trở thành QĐNDVN.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','05', 'Ngày 15-5-1946, Hội Công nhân cứu quốc Bắc Bộ tổ chức lớp đào tạo cán bộ công vận tại ngôi đình Khuyến Lương, nay thuộc xã Trần Phú, huyện Thanh Trì, Hà Nội.\nTừ lớp đào tạo đầu tiên đó, những nǎm sau đã ra đời trường Hoàng Quốc Việt (1950-1954), Trường cán bộ Trung ương (1961-1981), Trường cao cấp Công đoàn (1981-1992) và Trường đại học Công đoàn từ nǎm 1992 đến nay.\nHiện nay nhà trường có hơn 3000 sinh viên, trong đó có hơn 2000 người là cán bộ công đoàn. Trường đại học Công đoàn có nhiệm vụ đào tạo, bồi dưỡng cán bộ công đoàn, đáp ứng yêu cầu công đoàn hoạt động trong nền kinh tế thị trường, góp phần nâng cao dân trí, công nghiệp hoá, hiện đại hoá đất nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','05', 'Ngày này 15-5-1947, Chủ tịch Chính phủ đã có sức lệnh đặt ra huân chương Quân công và Huân chương Chiến sĩ để động viên, khen thưởng quân nhân và dân quân du kích có thành tích.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','05', 'Ngày 15-5-1975, 70 vạn người ở Thủ đô Hà Nội, và tại Sài Gòn, Gia Định có hàng triệu đồng bào xuống đường tham gia cuộc mít tinh, diễu hành mừng chiến thắng to lớn của dân tộc ta: miền Nam được hoàn toàn giải phóng, đất nước thống nhất.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','05', 'Ngày 15-5-1991, Chính phủ ta đã cho phép Bộ Nội vụ gia nhập Tổ chức cảnh sát hình sự quốc tế (gọi tắt là Interpol). Đến ngày 25-5-1991, Bộ trưởng Bộ nội vụ quyết định thành lập vǎn phòng Interpol Việt Nam trực thuộc Tổng cục Cảnh sát nhân dân, khẳng định sự cần thiết phải mở rộng hợp tác quốc tế trong việc chống tội phạm. Cái được lớn nhất sau khi ra nhập Interpol là ngành cảnh sát Việt Nam đã có được tất cả các thông tin mới nhất về tội phạm trên thế giới, về tình hình an ninh, trật tự quốc tế và khu vực Đông Nam Á, Interpol Việt Nam đã tham gia điều tra hàng chục vụ án lớn quốc tế.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','05', 'Ngày 15-5-1859 là ngày sinh của nhà vật lý người Pháp tên là Pie Quiri. Ông cùng với vợ là Mari lãnh giải Nôben về vật lý nǎm 1903 do tìm thấy hai nguyên tố polonium và radium. Tên ông - Quiri được đặt cho đơn vị ước số phóng xạ và nguyên tố hoá học 96. Ông qua đời ngày 19-4-1906 do xe ngựa chở hàng đè lên người. Bà Mari thay ông làm việc ở đại học Soócbon (Pari) và lại nhận giải Nôben về hoá học vào nǎm 1911. Con gái của bà Mari và ông Quiri là Iren cũng say mê nghiên cứu khoa học, Iren cùng với chồng là giáo sư Frêđêric Giôliô phát minh ra tính phóng xạ nhân tạo. Nǎm 1935, họ cũng được nhận giải Nôben về sự tổng hợp những chất phóng xạ đầu tiên.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','05', 'Đứng trước những biến chuyển phức tạp của Đại chiến thế giới lần thứ 2, để mở rộng khả nǎng liên minh các lực lượng dân chủ chống phát xít, đồng thời đứng trước sự lớn mạnh của các Đảng cộng sản và công nhân các nước, ngày 15-5-1943, Quốc tế cộng sản tuyên bố tự giải tán sau khi hoàn thành sứ mệnh lịch sử của mình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','06', 'Đồng chí Hồ Từng Mậu sinh ngày 15-6-1896 quê ở huyện Quỳnh Lưu tỉnh Nghệ An, đồng chí hoạt động Cách mạng từ lúc còn trẻ, nhiều nǎm ở Thái Lan và Trung Quốc.\nĐồng chí là một trong những người đầu tiên tham gia tổ chức tiền thân của Đảng Cộng sản Việt Nam, và đã tham dự các lớp huấn luyện cán bộ ở Quảng Châu do lãnh tụ Nguyễn Ái Quốc tổ chức.\nDo hoạt động cách mạng, đồng chí đã bị trục xuất khỏi Tô Giới Anh ở Hồng Kông, bị thực dân Pháp bắt tháng 6-1931, đưa về nước, kết án tử hình, sau giảm xuống chung thân.\nSau Cách mạng Tháng Tám, đồng chí Hồ Tùng Mậu được giao nhiều chức quan trọng: Chủ tịch Ủy ban hành chính liên khu IV. Tổng thanh tra của Chính phủ. Tại đại hội lần thứ II của Đảng, đồng chí được bầu vào Ban Chấp hành Trung ương Đảng.\nĐồng chí Hồ Tùng Mậu đã hy sinh trên đường đi công tác ở Thanh Hoá ngày 23-7-1951.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','06', 'Nhạc sĩ Vũ Trọng Hối quê ở Hải Phòng, sinh ngày 15-6-1926 và từ trần nǎm 1985.\nÔng đã tu nghiệp tại Nhạc viện Anma Ata (nước cộng hoà Cadǎctǎng), nguyên công tác tại vǎn phòng Vǎn nghệ quân đội, mang quân hàm đại tá.\nÔng đã sáng tác nhiều ca khúc, trong đó có những bài được công chúng ưa thích như: \"Nghe lời Bác gọi, thanh niên lên đường\", \"Những chuyến xe đêm\", \"Niềm vui anh quân bưu\", \"Đường tôi đi dài theo đất nước\", \"Bước chân trên dải trường sơn\"...\nNgoài ra, nhạc sĩ Vũ Trọng Hối còn viết một số tác phẩm khí nhạc, nhạc múa, nhạc sân khấu, nhạc phim.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','06', 'Trường đại học Sư phạm khai giảng khoá học đầu tiên tại Thanh Hoá ngày 15-6-1949. Lúc đó, nhà trường có các khoa: Triết học, Sử học, Kinh tế học, Chính trị học, Ngoại ngữ, Vǎn chương, Toán học, Lý học và vạn vật học.\nCác sinh viên của khoá học đầu tiên nǎm ấy đã trở thành các thầy giáo, nhiều người là cán bộ quản lý. Đó là những nhà sư phạm trưởng thành trong cuộc đấu tranh giải phóng và đóng góp nhiều công sức vào nền giáo dục nước nhà.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','06', 'Từ ngày 15-6 đến 24-10-1950, \"Chiến dịch Phan Đình Phùng\" diễn ra trên địa bàn Quảng Bình, Quảng Trị do bộ tư lệnh phân khu Bình Trị Thiên tổ chức, chỉ đạo, chỉ huy với quy mô hai trung đoàn. Mục đích: Phối hợp với chiến trường Biên giới, giáng đòn phủ đầu vào quân chủ lực cơ động của Pháp vừa được tổ chức trên chiến trường Bình Trị Thiên.\nChiến dịch chia làm hai đợt:\nĐợt 1: Từ 15 đến 30-6 bộ đội bao vây đồn Ba Đình, phục kích lực lượng cơ động địch đi ứng cứu từ Huế ra Đồng Hới tại đoạn Chấp Lễ - Hạ Cờ. Ta tiêu diệt 300 tên phá huỷ 4 xe, đánh tiêu hao nặng tiểu đoàn quân ứng chiến của địch\nĐợt 2: Từ 1-7 đến 24-10: Ta tổ chúc tập kích vào thành phố Huế, Đồng Hới, Đông Hà, Quảng Trị, Nổi lên là trận phục kích tiêu diệt đoàn tàu bọc thép của địch có 15 toa, trên đoạn Nhu Sơn - Bến Đá. Kết quả toàn chiến dịch: Địch bị tiêu diệt 540 tên bị thương 300, bắt 20. Ta phá huỷ một đoàn tàu bọc thép 15 toa, 40 xe vận tải, bắn rơi 1 máy bay, thu 1 khẩu pháo do-pho, 150 súng các loại.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','06', 'Ngày 15-6-1960, 6 vạn công nhân của 124 nghiệp đoàn Sài Gòn, Chợ Lớn tổng bãi công trong 24 giờ để ủng hộ cuộc bãi công đòi quyền sống, chống sa thải của công nhân kho tồn trữ Thủ Đức (nổ ra ngày 28-5).\nTiếp đó ngày 25-6, gần 10 vạn công nhân Sài Gòn, Chợ Lớn, Gia Định lại tổng bãi công không thời hạn để tiếp tục ủng hộ cuộc bãi công của công nhân kho tồn trữ Thủ Đức. Hai cuộc bãi công kể trên đã làm tê liệt mọi hoạt động ở thành phố, nhất là ở các cảng, bến tàu. Ở các cảng quân sự, tàu Mỹ cập bến nhưng không hoạt động được. Trong ngày 15-6-1970, lính Mỹ phải tự bốc dỡ đồ tiếp tế ở cảng Sài Gòn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','06', 'Nhạc sĩ Gric, người Na Uy, sinh ngày 15-6-1843 và qua đời nǎm 1907.\nNǎm 12 tuổi, Gric đã có sáng tác đầu tay. Nǎm 15 tuổi, ông đến Nhạc viện Lépdích để học tập một cách toàn diện. Khi ấy Gric dã là người biểu diễn Pianô có tài.\nNǎm 1862, ông trở về nước, là người đứng đầu các hoạt động âm nhạc ở thủ đô Ôxlô. Vừa là người lãnh đạo của các tổ chức biểu diễn, ông vừa là người sáng tác.\nGric là người khai sáng nền âm nhạc cổ điển của nước Na Uy.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','07', 'Đồng chí Nguyễn Duy Trinh sinh ngày 15-7-1910 tại tỉnh Nghệ An, qua đời năm 1985 tại Hà Nội.\nTham gia cách mạng từ nǎm 1927 đến đầu nǎm 1932, đồng chí bị địch kết án tù khổ sai và đày đi Côn Đảo (từ 1935 đến 1945).\nSau Cách mạng tháng Tám, đồng chí Nguyễn Duy Trinh lần lượt giữ nhiều chức vụ quan trọng: Ủy viên Trung ương Đảng (nǎm 1951), Bí thư Trung ương Đảng (nǎm 1955), Ủy viên Bộ Chính trị Ban Chấp hành Trung ương Đảng (nǎm 1956), Phó thủ tướng kiêm Bộ trưởng Bộ Ngoại giao (từ nǎm 1965 đến 1980). Đồng chí là đại biểu Quốc hội từ khoá 1 đến khoá 7.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','07', 'Tháng 6-1946, Bộ Nội vụ cho thành lập trường trung cấp huấn luyện công an (tiền thân của trường đại học An ninh ngày nay).\nNgày 15-7-1946, khoá học đầu tiên khai giảng tại 15 phố Trần Bình Trọng, Hà Nội. Khoa huấn luyện này có 40 học viên, hầu hết là trưởng, phó ty; trưởng, phó ban của các tỉnh từ Nam Trung Bộ trở ra. Thời gian học là ba tháng. Chương trình học gồm các môn pháp luật, công tác công an, lịch sử, hành chính, cǎn cước.\nKhoá học đầu tiên của trường trung cấp huấn luyện công an đã góp phần đào tạo cán bộ cho ngành, phục vụ có hiệu qủa cuộc kháng chiến chống Pháp và tạo nền móng cho công tác giáo dục và đào tạo của ngành công an sau này.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','07', 'Ngày 15-7-1974 là ngày công bố kết quả thi toán quốc tế lần thứ 16 tổ chức tại nước Cộng hoà dân chủ Đức. Lần đầu tiên nước ta cử một đoàn 5 học sinh hết lớp 10 phổ thông đến dự thi với 140 học sinh của các 18 nước. Đoàn học sinh Việt Nam đã được một giải nhất - Hoàng Lê Minh, một giải nhì và hai giải ba.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','07', 'Tháng 6-1995, Thủ tướng Chính phủ đã ra quyết định lấy ngày 15-7 hàng nǎm làm \"Ngày truyền thống của lực lượng thanh niên xung phong\"\nNgày 15-7-1950, tại chiến khu Việt Bắc đã thành lập đội thanh niên xung phong đầu tiên, gồm 225 người từ 18 đến 25 tuổi để phục vụ chiến dịch Biên giới.\nTrong hai cuộc kháng chiến chống thực dân Pháp và đế quốc Mỹ, lực lượng thanh niên xung phong nước ta có nhiều đóng góp to lớn, xứng đáng là niềm tự hào của tuổi trẻ Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','07', 'Rembran - danh hoạ người Hà Lan - sinh ngày 15-7-1606, mất nǎm 1669. Cuộc đời của ông gắn liền với tâm trạng các nhân vật trong tranh ông: Vinh quang và cay đắng, hạnh phúc và đau khổ.\nÔng là nghệ sĩ tài hoa. Ông đã trao cho con người trần thế cái ánh sáng biểu hiện sâu sắc qua các tác phẩm \"Tuần tra đêm\", \"Tự hoạ\". Đặc biệt là tác phẩm \"Đanaê\" được sáng tác nǎm 1636 đã được người đời ghi nhớ, giữ gìn. Nàng Đanaê của rembran không chìm đắm mơ màng trong giấc mơ tình yêu và khao khát giơ tay đón nhận, trao gửi một tình yêu hiện diện. Cô gái lao động Hà Lan mạnh khoẻ là hiện thân một quan niệm về cái đẹp nhân bản trong tranh Rembran vào những nǎm cuối đời.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','07', 'Anto Paplôvich Sêkhốp là nhà vǎn nổi tiếng Nga. Ông sinh ngày 29-1-1860. Nǎm ông 24 tuổi ông tốt nghiệp đại học và trở thành bác sĩ nông thôn. Không chỉ chữa bệnh về thể xác, ông dùng vǎn học để chữa bệnh tinh thần cho nhân dân. Với ngòi bút hiện thực phê phán thấm đượm hài hước trào lộng, ông trở thành nhà vǎn lớn ở thế kỷ XIX. Ngòi bút của ông đã phê phán và đấu tranh cho mọi tầng lớp người nghèo khổ. Với những cống hiến lớn lao, nǎm 1900 ông được bầu làm Viện sĩ danh dự Viện Hàn lâm khoa học Nga.\nSêkhốp được coi là nhà viết truyện ngắn thiên tài bậc nhất qua các tác phẩm \"Anh béo, anh gầy\", \"Cái chết của một viên chức\", \"Con kỳ nhông\", \"Nỗi buồn\" \"Vanka\", \"Người đàn bà có con chó nhỏ\"... Ông còn là nhà cách tân nghệ thuật sân khấu Nga lỗi lạc. Với những vở \"Cậu Vania\", \"Chim hải âu\", \"Ba chị em\"... Ông đã làm thay đổi diện mạo của sân khấu bằng cách đưa vào kịch những hình thức diễn đạt, những hành động mới mẻ làm cho kịch thấm đậm yếu tố trữ tình và yếu tố tâm lý.\nSêkhốp mất ngày 15-7-1904 vì bệnh lao phổi tại Đức.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','08', 'Từ 15 đến 28-8-1945, Việt Nam giải phóng quân cùng toàn dân tổng khởi nghĩa giành chính quyền trên cả nước.\nĐêm ngày 15 và ngày 16-8-1945, từ Tân Trào, Việt Nam giải phóng quân chia làm hai bộ phận, tiến về tỉnh lỵ Tuyên Quang và Thái Nguyên. Ngày 17 tiến công trại lính Nhật ở thị xã Tuyên Quang, ngày 20 bao vây quân Nhật ở Thái Nguyên, kêu gọi chúng đầu hàng.\nCùng nhân dân các địa phương, Việt Nam giải phóng quân phát triển nhanh chóng trong quá trình tổng khởi nghĩa và là lực lượng quân sự cách mạng đóng góp vào sự thành công của việc giành chính quyền về tay nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','08', 'Ngày 15-8-1946, Chính phủ ra quyết định thành lập thị xã Hưng Yên, gồm có 2 khu phố Đầu Lĩnh và Đằng Giang. Thị xã Hưng Yên có phố Hiến nổi tiếng, từ cuối thế kỷ XVI đã có nhiều người nước ngoài đến đây buôn bán, tàu buôn đến dỡ hàng và nộp thuế ở đây. Các lái buôn Bồ Đào Nha, Hà Lan, Pháp, Anh, Hoa kiều lần lượt đến phố Hiến.\nHiện nay, phố Hiến và thị xã Hưng Yên còn nhiều di tích lịch sử hấp dẫn khách du lịch trong nước và ngoài nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','08', 'Oantơ Xcốt là nhà vǎn Anh, sinh ngày 15-8-1771 tại Xcốtlen, qua đời nǎm 1832.\nCha ông là chưởng lý tại toàn án và muốn ông nối nghiệp mình. Nhưng Xcốt lại ham thích vǎn chương. Lúc đầu ông làm thơ, sau chuyển sang viết tiểu thuyết lịch sử và trở thành một nhà vǎn độc đáo.\nTuy các tiểu thuyết lịch sử Xcốt lấy đề tài từ nhiều nước và nhiều thời đại khác nhau nhưng có một nét thống nhất là bao giờ nhà vǎn cũng chọn giai đoạn lịch sử trong đó diễn ra những mâu thuẫn và đấu tranh giai cấp gay gắt. Mặt khác, trong khi dựng lại bức tranh quá khứ, ông nhìn thấy rõ vai trò của nhân dân và quá trình phát triển thấy rõ vai trò của nhân dân và qúa trình phát triển khách quan của xã hội.\nBạn đọc Việt Nam quen biết nhà vǎn Oantơ Xcốt qua tiểu thuyết \"Ai van hô\", tác phẩm lớn nhất của ông đã được dựng thành phim.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','08', 'Pharađây là nhà vật lý và hoá học người Anh. Ông sinh ngày 22-9-1791.\nNǎm 1812 với những thí nghiệm của mình, ông được làm trợ lý khoa học của Viện nghiên cứu Hoàng Gia. Nǎm 1823 ông đã thực hiện việc hoá lỏng phần lớn chất khí đã biết lúc bấy giờ.\nVề vật lý, ông đã nghiên cứu về hiện tượng điện từ và tìm thấy tác dụng của nam châm ảnh hưởng đến dòng điện. Nǎm 1831 ông phát hiện ra hiện tượng cảm ứng điện từ, tức là sự biến đổi công cơ học thành điện nǎng, và như vậy đặt nền tảng cho việc chế tạo ra máy phát điện. Sau đó ông đưa ra lý thuyết về hiện tượng điện phân. Chính các từ \"điện phân\", \"điện cực\", \"i-on\" là do ông đặt ra. Ông còn đưa ra lý thuyết về sự nhiễm điện bằng cảm ứng.\nPharđây còn là người chỉ ra hiện tượng phát quang và khám phá ra tính nghịch từ. Vào những nǎm cuối đời, ông còn quan tâm nghiên cứu về tác dụng của từ trường đối với ánh sáng phân cực. Ông được Viện Hàn lâm khoa học Pari công nhận là người kế tục Đantông trong số 8 thành viên nước ngoài của Viện. Pharđây mất ngày 15-8-1867.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','08', 'Sau 10 nǎm xây dựng, ngày 15-8-1914 chính thức khai thông kênh đào Panama, rút ngắn con đường hàng hải Đại Tây Dương và Thái Bình Dương.\nKênh đào Panama nằm trên lãnh thổ nước cộng hoà Panama. Kênh dài 79,6 km, rộng từ 150 đến 304 m, sâu 13,5 m đến 26,5 m. Trên kênh có ba hệ thống bể van với 6 bể cao rồi từ từ hạ xuống, thuận tiện cho việc tàu bè ngược xuôi. Kênh đào Panama rút ngắn thời gian đi từ Đại Tây Dương sang Thái Bình Dương, tạo thuận lợi cho sự giao lưu giữa bờ biển phía đông với bờ biển phía tây châu Mỹ, với châu Á và châu Đại Dương. Mỗi nǎm có khoảng 15.000 tàu thuyền qua kênh, chiếm 5% số hàng hoá chở bằng tàu thuyền trên địa cầu. Có khoảng 60 quốc gia và khu vực sử dụng con kênh này.\nĐây là một trong những công trình thế kỷ, thể hiện sức mạnh vĩ đại của con người trước thiên nhiên.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','08', 'Thực hiện lời cam kết với lực lượng đồng minh Anh - Mỹ ngày 8-8-1945, Liên Xô tuyên chiến với Nhật. Hồng quân chia làm 4 mũi tiến công như vũ bão kéo dài 5000km ở đông - bắc Trung Quốc.\nMũi thứ nhất tiến vào Trường Xuân, Thẩm Dương. Mũi thứ hai phối hợp với quân Mông Cổ tiến vào Thừa Đức, Cẩm Châu, Trương Gia Khẩu. Mũi thứ ba từ Hải Sâm Uy tiến vào Cát Lâm, Cáp Nhĩ Tân. Mũi thứ tư tiến công vào Tề Tề Cáp Nhĩ. Trong khi đó hạm đội Thái Bình Dương của hải quân Xô Viết vẫn đổ bộ vào Bắc Triều Tiên, miền nam đảo Xakhalin và quần đảo Curin để phối hợp tác chiến.\nHồng quân Liên Xô đã tiêu diệt cǎn cứ chiến lược bậc nhất của quân phiệt Nhật ở Đông Tam Tỉnh và đạo quân Quan Đông tinh nhuệ nhất gồm hơn 1 triệu quân Nhật. Trước tình thế thất bại rõ ràng, ngày 15-8-1945 Nhật hoàng công bố lệnh đầu hàng không điều kiện Liên Xô và lực lượng đồng minh chống phát xít. Ngày 2-9-1945 Hiệp ước đầu hàng đã được chính thức ký trên một chiến hạm của Hoa Kỳ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','09', 'Ngày 15-9-1792 Quang Trung - Nguyễn Huệ từ trần. Ông làm Hoàng đế được bốn năm, hưởng thọ 39 tuổi, miếu hiệu là Thái Võ Hoàng Đế.\nNguyễn Huệ là một trong những nhân vật đặc biệt trong lịch sử Việt Nam. Ông đã chiến đấu liên tục, chiến đấu với nhiều kẻ thù trong và ngoài, xử lý nhiều tình huống hiểm nghèo của đất nước, đưa đất nước thoát khỏi mọi hiểm họa. Trong suốt hơn 20 năm chiến đấu, ông tin tưởng vào dân chúng, trọng dụng nhân tài, có lòng gan dạ. Ông là vị tướng chỉ đánh thắng, không có bại, là một nhà chiến lược lỗi lạc, là một vị Hoàng đế giỏi trị vì đất nước. Ông đề ra những quyết sách quan trọng nhằm đưa đất nước tiến lên. Nguyễn Huệ còn là một vị anh hùng dân tộc trên cả lĩnh vực khác như xây dựng đất nước, đấu tranh cho thống nhất Tổ quốc với cương vị Hoàng đế anh minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','09', 'Nguyễn Chí Diểu sinh năm 1908, quê ở xã Phú Mậu, huyện Phú Vang, Tỉnh Thùa Thiên - Huế, qua đời ngày 15-9-1939 do bị bệnh lao.\nNăm 1927, ông tham gia bãi khoá tại trường Quốc Học Huế, năm sau ông đắc cử xứ ủy viên Trung Kỳ của Đảng Tân Việt. Năm 1929, một bộ phận của Đảng Tân Việt trở thành Đông Dương Cộng sản liên đoàn, Ông là đảng viên Cộng sản.\nSau khi ba nhóm cộng sản ở ba kỳ thống nhất thành Đảng Cộng sản Việt Nam. Ông được điều vào hoạt động ở Nam Kỳ, được cử làm Bí thư tỉnh uỷ Gia Định. Ở đây, ông bị Pháp bắt vào tháng 10-1930. Đến tháng 5-1933, toà án thực dân kết án ông tù khổ sai trung thân rồi đày ra Côn Đảo.\nTháng 6-1936, ông được trả tự do nhưng bị quản thúc ở Huế. Tại quê nhà, ông tiếp tục hoạt động, bí mật lãnh đạo các cuộc đấu tranh ở Huế và các tỉnh Trung Kỳ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','09', 'Lễ thành lập Trung đội Việt Nam Cứu quốc quân thứ hai - được tổ chức tại khu rừng Khuôn Mánh thôn Ngọc Mỹ, Tràng Xá, Vũ Nhai, tỉnh Lạng Sơn, ngày 15-9-1941. Lễ thành lập có mặt đồng chí Hoàng Quốc Việt, đại diện Trung ương Đảng. Đơn vị gồm 47 chiến sĩ, trong đó có 3 nữ, chia làm 5 tiểu đội, đơn vị có chi bộ Đảng do đồng chí Chu Văn Tấn làm bí thư. Mặc dầu được trang bị rất thô sơ, nhưng ngay sau khi thành lập nó đã trở thành chỗ dựa vững chắc cho phong trào quần chúng, bảo vệ và mở rộng khu căn cứ, hỗ trợ tích cực cho các cuộc đấu tranh xây dựng lực lượng vũ trang Cách mạng ở địa phương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','09', 'Ngày 15-9-1945, tại Hà Nội đã thành lập Thông tấn xã Việt Nam.\nHiện nay, Thông tấn xã Việt Nam có 61 phân xã ở các tỉnh, thành phố trong cả nước, hai cơ quan đại diện tại thành phố Hồ Chí Minh và Đà Nẵng, hơn 20 phân xã ở nước ngoài, có quan hệ với 35 tổ chức báo chí và hãng thông tấn quốc tế.\nThông tấn xã Việt Nam là thành viên của tổ chức thông tấn xã các nước không liên kết và tổ chức thông tấn xã châu Á - Thái Bình Dương.\nThông tấn xã Việt Nam có 30 ấn phẩm, có kho tư liệu phim gần 600 kiểu phim và nhiều tư liệu quý khác. Mỗi năm in hơn 300 triệu trang tin, 7 vạn bức ảnh cho các báo và gần 20 hãng thông tấn và tổ chức quốc tế.\nTrong hai cuộc kháng chiến, Thông tấn xã Việt Nam có nhiều cán bộ phóng viên hy sinh trong khi làm nhiệm vụ. Thông tấn xã Việt Nam đã được Đảng và Nhà nước tặng Huân chương Hồ Chí Minh và Huân chương Sao Vàng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','09', 'Ngày 15-9-1945 Chủ tịch Hồ Chí Minh ký sắc lệnh thành lập Phòng quân giới trong Cục quân nhu với hai nhiệm vụ: Thu nhập mua sắm vũ khí và tổ chức cơ sở sản xuất vũ khí. Và từ đó ngày 15-9 trở thành ngày truyền thống của bộ đội quân giới Việt Nam.\nTừ những xưởng bé nhỏ, phân tán đã quy tụ hình thành các cụm sản xuất vũ khí, hoá chất. Từ sản xuất vũ khí thô sơ đã từng bước chế tạo các loại vũ có uy lực cao như súng đạn chống tăng, súng không giật, súng B40, B41...\nNgày nay, hệ thống các nhà máy thuộc ngành quân giới đã được củng cố và mở rộng với đội ngũ dày dạn kinh nghiệm có trình độ nghề nghiệp cao đang góp phần vào sự nghiệp công nghiệp hoá hiện đại hoá đất nước.\nNgành quân giới đã được Quốc hội và Chính phủ tặng thưởng nhiều danh hiệu cao quý.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','09', 'Ngày 15-9-1973, đồng chí Phiđen Caxtơrô, Thủ tướng Chính phủ Cách mạng Cuba, Bí thư thứ nhất BCH Trung ương Đảng cộng sản Cuba đi thăm vùng giải phóng miền Nam.\nThủ tướng đã đến thăm thị xã Đông Hà, một số nơi đã diễn ra những chiến công oanh liệt của nhân dân và các lực lượng vũ trang nhân dân giải phóng miền Nam Việt Nam trong năm 1972, nhiều đơn vị anh hùng của các lực lượng vũ trang giải phóng và dự cuộc mít tinh nồng nhiệt của nhân dân Quảng Trị chào mừng Thủ tướng.\nThủ tướng Phiđen Caxtơrô là vị nguyên thủ đầu tiên của một nước bạn tới thăm vùng giải phóng miền Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','09', 'Việt Nam trở thành hội viên chính của tổ chức Quỹ tiền tệ quốc tế (viết tắt là IMF) ngày 15-9-1976.\nQuỹ này là cơ quan chuyên môn của Liên hiệp quốc, bắt đầu hoạt động từ tháng 3-1947.\nMục đích của Quỹ là giúp đỡ sự hợp tác quốc tế trong lĩnh vực tài chính - tiền tệ, phát triển thương mại quốc tế, điều chỉnh tỷ giá ngoại hối của các nước cho vay theo thời gian ngắn và trung bình.\nVốn của Quỹ tiền tệ quốc tế được xây dựng chủ yếu dựa vào sự đóng góp của các nước thành viên.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','11', 'Nhạc sĩ Vǎn Cao, sinh ngày 15-11-1923 ở Nam Định, qua đời nǎm 1995 tại Hà Nội.\nNǎm 1944, ông sáng tác bài hát \"Tiến quân ca\" để cổ vũ phong trào Cách mạng của nhân dân ta. Kỳ họp đầu tiên của Quốc hội khoá I của nước Việt Nam dân chủ cộng hoà đã chọn bài hát này làm Quốc ca Việt Nam cho tới ngày nay.\nVǎn Cao là một trong những nhạc sĩ lớp đầu tiên của nền âm nhạc hiện đại Việt Nam, có nhiều ca khúc có giá trị trong đời sống âm nhạc của nhân dân ta. \nVǎn Cao còn nổi tiếng về làm thơ, vẽ tranh. Nhạc sĩ Vǎn Cao đã được Nhà nước tặng thưởng Huân chương Độc lập hạng nhất và truy tặng giải thưởng Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','11', 'Ngày 15-11-1961, Hội đồng an ninh Mỹ đã thống kê kế hoạch Xtalây Taylo, kế hoạch đầu tiên của \"chiến tranh đặc biệt\" nhằm bình định miền nam trong 18 tháng, rồi tiến ra miền Bắc.\nVới hai cuộc chiến đấu của quân và dân ta ở cả 2 miền Nam Bắc, kế hoạch Xtalây Taylo đã thất bại hoàn toàn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','11', 'Ngày 15-11-1965, Đoàn đại biểu anh hùng và chiến sĩ thi đua các lực lượng vũ trang giải phóng miền Nam ra thǎm miền Bắc. Đoàn gồm các anh hùng quân giải phóng: Tạ Thị Kiều (tức Mười Lý), Trần Dưỡng, Huỳnh Vǎn Đảnh, đồng chí Vai và chiến sĩ thi đua giải phóng Lê Chí Nguyện. Đoàn đã được gặp Bác Hồ trong tình cảm ruột thịt của Người.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','11', 'Từ ngày 15 đến ngày 21-11-1975 tại thành phố Sài Gòn - Gia Định, Đoàn đại biểu miền Bắc do đồng chí Trường Chinh, Uỷ viên Bộ chính trị Ban Chấp hành Trung ương Đảng Lao động Việt Nam, Chủ tịch Uỷ ban Thường vụ Quốc hội nước Việt Nam dân chủ cộng hoà làm trưởng đoàn, và Đoàn đại biểu miền Nam do đồng chí Phạm Hùng, Uỷ viên Bộ Chính trị Ban Chấp hành Trung ương Đảng Lao động Việt Nam, Bí thư Đảng bộ miền Nam, đại diện Đảng trong Mặt trận dân tộc giải phóng miền Nam Việt Nam làm Trưởng đoàn, tiến hành hội nghị Hiệp thương chính trị để bàn vấn đề thống nhất nước Việt Nam về mặt Nhà nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','11', 'Ngày 15-11-1901 Hátchinxơn (Hutchinson), người ở thành phố Niu Oóc (Mỹ) 26 tuổi, đã phát minh tai nghe điện. Đây là dụng cụ điện thanh đầu tiên cho người điếc sử dụng để làm to âm thanh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','11', 'Ngày 15-11-1988, thành lập nhà nước Palestine.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','11', 'Theo sáng kiến của Huân tước Béctơrǎng Rútxen, nhà triết học và chiến sĩ hoà bình nổi tiếng của nước Anh, ngày 15-11-1996, Toà án quốc tế xử tội ác chiến tranh của đế quốc Mỹ ở Việt Nam đã thành lập tại Luân Đôn.\nHuân tước Béctơrǎng Rútxen nói :\"Tôi không giấu giếm các bạn rằng, nhân dân Việt Nam đã làm tôi khâm phục và say mê thế nào. Chúng ta có nhiệm vụ phát hiện và nói hết. Tôi tin tưởng rằng không có sự tôn vinh nào lớn hơn đối với dân tộc này là nói lên sự thật, kết quả cuộc điều tra sâu sắc và triệt để. Mong rằng toà án này sẽ có thể ngǎn được tội ác im lặng\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','11', 'Ngày này năm 1999, Trung Quốc và Mỹ ký hiệp định thương mại, xoá bỏ trở ngại lớn nhất của Trung Quốc trên con đường đến WTO.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','12', 'Nguyễn Quang Bích là nhà thơ, nhà yêu nước, sinh nǎm 1869, đỗ đình nguyên hoàng giáp, làm chi phủ tỉnh Hưng Hoá. Khi giặc Pháp đánh đến vùng này, vì giữ không nổi, ông đã rút về Cẩm Khê (Phú Thọ), lập cǎn cứ kháng chiến, đánh lui nhiều đợt tấn công của giặc trong hai nǎm 1884-1885. Sau đó, ông lại xây dựng cǎn cứ ở Nghĩa Lộ để chiến đấu với giặc lâu dài. Nghĩa quân của ông bao gồm người Kinh và các dân tộc ít người ở vùng Tây Bắc.\nNguyễn Quang Bích còn để lại Ngư Phong thi tập, sáng tác trong những nǎm ông lãnh đạo nghĩa quân chống Pháp (1884-1885).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','12', 'Hoạ sĩ Tô Ngọc Vân sinh ngày 15-12-1906 ở Hưng Yên và mất trên đường tham gia chiến dịch Điện Biên Phủ tháng 6-1954.\nÔng tốt nghiệp trường Cao đẳng Mỹ thuật Đông Dương khoá II (1926-1931) và dạy ở trường này nǎm 1939-1940. Sau Cách mạng tháng Tám, ông tích cực phục vụ kháng chiến chống thực dân Pháp. Trong nghệ thuật, ông có nhiều trǎn trở, Những tranh: \"Thiếu nữ bên hoa Huệ\", \"Thiếu nữ bên hoa sen\" vẽ trong thời kỳ đầu thể hiện ấn tượng hiện thực của ông. Sau này ông có nhiều tác phẩm nổi tiếng như: Bác Hồ ở Phủ chủ tịch, Xưởng quân giới, Dừng chân bên đồi, Hai chiến sĩ, Nữ cứu thương v.v... đậm đà sức sống, làm thay đổi sáng tác của ông.\nHoạ sĩ Tô Ngọc Vân là người có công đầu trong sử dụng sơn dầu vẽ tranh Việt Nam.\nNǎm 1996 ông được Nhà nước truy tặng giải thưởng Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','12', 'Đại hội đại biểu toàn quốc lần thứ VI Đảng cộng sản Việt Nam tiến hành từ ngày 15 đến ngày 18-12-1986 tại thủ đô Hà nội.\nDự đại hội có 1.129 đại biểu thay mặt cho hơn 1 triệu 800 nghìn đảng viên trong cả nước, và 37 đại biểu các đảng anh em trên thế giới.\nĐại hội đã vạch ra phương hướng, mục tiêu chủ yếu phát triển kinh tế, xã hội trong 5 nǎm (từ 1986 đến 1990), đề ra ba chương trình kinh tế lớn về: Lương thực, thực phẩm, hàng tiêu dùng và hàng xuất khẩu. Đại hội VI của Đảng đã đánh dấu bước chuyển biến quan trọng đổi mới sự lãnh đạo của Đảng về chính trị, tư tưởng và tổ chức.\nBan Chấp hành Trung ương mới do Đại hội bầu ra gồm có 124 Uỷ viên chính thức và 49 ủy viên dự khuyết. Đồng chí Nguyễn Vǎn Linh được bầu làm Tổng Bí thư.\nCác đồng chí Trường Chinh, Phạm Vǎn Đồng và Lê Đức Thọ được cử làm cố vấn Ban Chấp hành Trung ương Đảng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','12', 'Ngày 15-12-1972, Việt Nam và và Tunisia thiết lập mối quan hệ ngoại giao ở mức Đại sứ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '15','12', 'Ngày 15-12-1998 tại Hà Nội diễn ra cuộc gặp cấp cao ASEAN VI.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','12', 'L.L Damenhôp, nhà vǎn hoá Ba Lan, tác giả ngôn ngữ quốc tế Expêrantô, sinh ngày 15-12-1859. Ở thành phố Bialittôc (Ba Lan), nạn phân biệt giữa người Nga, Ba Lan, Do Thái... làm cậu học sinh Damenhốp ngày đêm suy nghĩ rất nhiều về một thứ tiếng môi giới hay thống nhất giữa các dân tộc mà nhiều nhà vǎn hoá thế giới từng mơ ước.\nSau 10 nǎm tìm tòi, nghiên cứu, nǎm 1887 ông xuất bản cuốn sách đầu tiên về quốc tế ngữ.\nQuôc tế ngữ Expêrantô được cấu tạo một cách khoa học và hợp lý, kết tinh nhiều ngôn ngữ cǎn bản châu Âu, vǎn phạm đơn giản, dễ dàng, dễ học gấp 5-10 lần so với tiếng dân tộc khác.\nQuốc tế ngữ đến nay được sử dụng trên 100 nước trong giao dịch hàng ngày: nói, viết, dịch, sáng tác, vǎn học, nghệ thuật, du lịch, ngoại thương, điện ảnh, sân khấu, thể thao...\nNǎm 1954, UNESCO đã xác nhận những thành tích mà Expêrantô đạt được trong việc trao đổi vǎn hoá quốc tế, làm cho các dân tộc hiểu biết lẫn nhau. Hội quốc tế ngữ bảo vệ hoà bình của Việt Nam thành lập 1956, đoàn kết chặt chẽ với các Hội quốc tế ngữ trên thế giới vì độc lập, tự do, hoà bình và hữu nghị các dân tộc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','12', 'Anphôngxơ Đôđê (Alphonse Daudet) sinh nǎm 1840, mất ngày 15-12-1897.\nÔng là nhà vǎn hiện thực và nhân đạo chủ nghĩa nổi tiếng của nước Pháp.\nNhững tác phẩm chính của ông gồm có: Những bức thư viết từ cối xay gió, Những cuộc phưu lưu kỳ diệu của Táctaranh ở Taraxcông, Những ông vua lưu đày, Người bất tử.\nTác phẩm của Anphôngxơ Đôđê thấm đượm một tinh thần nhân đạo sâu xa, gây xúc động nhẹ nhàng, một chất thơ trong sáng, lòng yêu đời, đầy niềm tin vào con người bình thường và can đảm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','12', 'Ngày 15-12-1992, Chính phủ El Salvado và nhà lãnh đạo quân phiến loạn chính thức tuyên bố kết thúc cuộc nội chiến kéo dài 12 nǎm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','12', 'Ngày này năm 1995, các quốc gia Đông Nam Á ký hiệp ước cấm tàng trữ, sản xuất vũ khí hạt nhân và thành lập khu vực phi hạt nhân từ Myanmar và Việt Nam ở phía Bắc đến Inđônêxia ở phía Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '15','12', 'Liên minh châu Âu cho ra đời đồng Euro vào ngày này năm 1995.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','01', 'Sau mười nǎm kháng chiến chống quân Minh xâm lược, nước Đại Việt giành lại được độc lập vào cuối nǎm 1427. Vào giữa tháng Giêng nǎm 1428, Bình Định Vương Lê Lợi công bố \"Bình Ngô đại cáo\" do Nguyễn Trãi soạn thảo.\nĐây là một \"Áng thiên cổ hùng vǎn\" chói sáng các đời và là bản Tuyên ngôn độc lập của dân tộc ta ở thế kỷ XV.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','01', 'Ngày 16-1-1961, Chính phủ nước Việt Nam dân chủ cộng hoà đã ra nghị định thành lập Uỷ ban trị thuỷ và khai thác hệ thống sông Hồng (gọi tắt là Uỷ ban sông Hồng).\nHệ thống sông Hồng gồm ba sông chính là: sông Hồng, sông Lô và sông Đà. Riêng chiều dài của sông Hồng là 1.149 kilômét, thượng lưu ở Trung Quốc, đoạn trung lưu và hạ lưu chảy qua nước ta dài 510 kilômét.\nHàng nǎm các sông này đem lại phù sa màu mỡ và tạo điều kiện cho tàu thuyền đi lại, mặt khác hệ thống sông Hồng còn xảy ra lũ lớn, gây ngập lụt, thiệt hại lớn về mùa màng, tài sản của nhân dân và của nhà nước.\nViệc thành lập Uỷ ban sông Hồng nhằm xúc tiến công tác phòng chống lụt bão và trị thuỷ, góp phần ổn định sản xuất và đời sống nhân dân vùng sông Hồng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','01', 'Tháng 12-1972, máy bay Mỹ ném bom huỷ diệt bệnh viện Bạch Mai, Hà Nội. Ngày 16-1-1975, ta đã xây lại xong bệnh viện này. Đây là một trung tâm chữa bệnh lớn, nghiên cứu khoa học và đào tạo cán bộ của ngành y tế. Toàn bộ hệ thống gồm có các khoa ngoại, sản, nội, xét nghiệm, huyết học, điện quang, các cơ sở nghiên cứu và hệ thống điện nước của bệnh viện Bạch Mai được khôi phục như cũ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','01', 'Nǎm 1977, Công ty xi mǎng Hoàng Thạch được xây dựng tại xã Minh Tân, huyện Kim Môn, tỉnh Hải Dương, đến ngày 16-1-1984, xuất xưởng bao xi mǎng đầu tiên. Theo thiết kế, mỗi nǎm nhà máy sản xuất được 1,1 triệu tấn. Đến tháng 3-1996, Công ty lại xây dựng xong dây chuyền thứ hai với sản lượng 1,2 triệu tấn một nǎm. Như vậy, Công ty xi mǎng Hoàng Thạch có tổng sản lượng 2,3 triệu tấn 1 nǎm.\nXi mǎng Hoàng Thạch sản xuất bằng các thiết bị hiện đại nhất của Đan Mạch nên đạt chất lượng cao. Xi mǎng Hoàng Thạch đã được sử dụng để xây dựng nhiều công trình quan trọng của đất nước như Nhà máy thuỷ điện Hoà Bình, cầu Thǎng Long, Bảo tàng Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','01', 'Ngày 16-1-1986 tại Hà Nội khai mạc Đại hội anh hùng và chiến sĩ thi đua toàn quốc lẫn thứ nǎm, và là Đại hội đầu tiên từ ngày đất nước thống nhất. Có 218 tập thể anh hùng, 223 chiến sĩ thi đua toàn quốc. Đại hội đã ghi nhận thành tích của các tập thể và cá nhân trong lao động sản xuất và chiến đấu, góp phần xây dựng tổ quốc Việt Nam thống nhất và đi lên chủ nghĩa xã hội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '16','01', '16-1-1991, chiến dịch Bão táp Sa mạc được bắt đầu, Mỹ và 27 đồng minh tấn công Iraq (cuộc không kích bắt đầu vào 2h38 sáng ngày 17/1).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','02', 'Ngày 16-2-1951, ta đánh quân Pháp ở Tầm Vu (Cần Thơ) tiêu diệt hoàn toàn đoàn xe địch, có tên trung tá chỉ huy và thu toàn bộ vũ khí. Trong đó có 1 khẩu đại bác 105 ly còn nguyên vẹn. Trận Tầm Vu đã chứng tỏ quân dân Cần Thơ nói riêng, quân dân Nam Bộ nói chung có khả nǎng đánh các trận đánh lớn với quân Pháp có vũ khí hiện đại. Để ca ngợi chiến thắng này nghệ sĩ Quốc Hương đã thể hiện rất thành công bài hát Tầm Vu nổi tiếng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','02', 'Ngày 16-2-1953, Chủ tịch Hồ Chí Minh đã ký sắc lệnh thành lập lực lượng Cảnh vệ.\nCác thế hệ cán bộ, chiến sĩ Cảnh vệ đã hoàn thành xuất sắc nhiệm vụ trong sự nghiệp giải phóng dân tộc, thống nhất đất nước, xây dựng và bảo vệ Tổ quốc xã hội chủ nghĩa, làm tròn nghĩa vụ quốc tế.\nLực lượng Cảnh vệ đã được Đảng và Nhà nước tặng thưởng huân chương Hồ Chí Minh, hai Huân chương Quân công hạng nhất, 3 đơn vị và một cá nhân được tặng danh hiệu \"Anh hùng lực lượng vũ trang nhân dân\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','02', 'Ngày 16-2-1962 khai mạc Đại hội Mặt trận dân tộc giải phóng miền Nam lần thứ nhất. Tham dự đại hội có hơn 100 đại biểu đủ mọi tầng lớp xã hội, tôn giáo, dân tộc thuộc nhiều xu hướng chính trị khác nhau. Đại hội đã thông qua bản cương lĩnh chính trị, các chính sách lớn của mặt trận về các vấn đề hoà bình, trung lập, dân tộc, ruộng đất, các chính sách đối với giai cấp tư sản, trí thức ngoại kiều, tôn giáo, binh sĩ và nhân viên ngụy quyền.... Đại hội bầu ra ủy ban Trung ương của Mặt trận gồm 52 người do luật sư Nguyễn Hữu Thọ làm Chủ tịch.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','02', 'Sáng ngày 16-2-1969 (tức mồng một Tết nǎm Kỷ Dậu) Chủ tịch Hồ Chí Minh đi chúc tết cán bộ, chiến sĩ, quân chủng phòng không - không quân tại sân bay Bạch Mai, Hà Nội. Bác Hồ đã gặp gỡ, nói chuyện với đại biểu anh hùng, chiến sĩ thi đua, đại biểu các đơn vị có thành tích trong chiến đấu và phục vụ chiến đấu. \nTrưa cùng ngày, Bác Hồ đến thǎm và chúc tết nhân dân xã Vật Lại, huyện Ba Vì, Hà Tây. Người cùng bà con trong xã trồng cây ở đồi Yên Bồ. Sau đó, Bác nói chuyện thân mật với mọi người.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','02', 'Nhà thơ Vĩnh Mai, tên thật là Nguyễn Hoằng, quê ở tỉnh Quảng Trị, sinh nǎm 1918, mất ngày 16-2-1981.\nNǎm 1945 ông tham gia khởi nghĩa, giành chính quyền ở Tuy Hoà (tỉnh Phú Yên).\nTrong thời gian kháng chiến chống Pháp, ông làm công tác chính quyền. Từ nǎm 1955, công tác ở Hội Liên hiệp Vǎn học nghệ thuật Việt Nam, rồi làm biên tập viên tuần báo Vǎn nghệ. Những tác phẩm chính của Vĩnh Mai có: Người dân quân xã, Lên đường, Tiếng hát, Đất đen và hoa thắm.... \nNhững bài thơ thành công của ông đều chân thành, đôn hậu, cởi mở. Đó là khi ông viết về quê hương, về những người thân, về những đau xót của đất nước và con người bị chiến tranh tàn phá.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','03', 'Trung tuần tháng 3- 1944, lãnh tụ Hồ Chí Minh đã tham dự Đại Hội hải ngoại của Việt Nam Cách mạng đồng minh Hội tại Liễu Châu (Trung Quốc). Tại Đại hội này (còn gọi là Đại hội Quốc tế chống xâm lược) lãnh tụ Hồ Chí Minh đã đọc hai bản báo cáo về hoạt động của Việt Minh và Đảng Cộng sản Đông Dương đối với phong trào Cách mạng Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','03', 'Ngày 16-3-1968, tại thôn Tư Cung, xã Tịnh Khê, huyện Sơn Tịnh, tỉnh Quảng Ngãi đã xảy ra vụ thảm sát Sơn Mỹ (còn gọi là Mỹ Lai). Một tên sĩ quan Mỹ tên là Côly ra lệnh giết một lúc 502 đồng bào ta (trong đó có 380 người thôn Tư Cung và 122 người thôn Cổ Lũy), 24 gia đình bị giết sạch.\nVụ thảm sát Sơn Mỹ đã gây xúc động và phẫn nộ khắp thế giới. Chính phủ Mỹ phải xử tù Côly mặc dù đó chỉ là hình thức.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','03', 'Ngày 16-3-1975 toàn bộ lực lượng địch ở Kontum, Plâycu gồm các liên đoàn biệt động quân số 6, 7, 23, 25; các trung đoàn thiết giáp số 3, 9, 21 và cơ quan bộ tư lệnh quân đoàn 2, sư đoàn 6 không quân rút chạy theo đường 7 về Phú Yên. Lần đầu tiên trong cuộc chiến tranh ở Đông Dương và trong phạm vi một chiến dịch, một quân đoàn địch được trang bị hiện đại đã phải rút bỏ một địa bàn chiến lược quan trọng. Sư đoàn 320 của ta đang đánh địch ở đường 14 được lệnh cấp tốc hành quân truy kích địch.\nTiểu đoàn 9 suốt ngày đêm chạy tắt rừng hướng đến đường 7 - Đến chiều ngày 17-3 -1975, họ đã kịp chốt chặt ở Đông Nam thị xã Cheo Reo 4 kilômét và bắt đầu nổ súng chặn địch.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','03', 'Ngày 16-3-1981, Khánh thành Bệnh viện trẻ em Việt Nam - Thụy Điển tại Hà Nội - Công trình do chính phủ Thụy Điển viện trợ. Đây là trung tâm chữa bệnh và nghiên cứu các bệnh trẻ em ở tuyến cao nhất trong cả nước. Công trình được khởi công xây dựng từ tháng 9-1975, khi khánh thành và đưa vào sử dụng, bệnh viện có trên 500 giường đến nay đã phát triển hơn nhiều.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','04', 'Ngày 16-4-1946, thành lập Bộ đội quân y. Ra đời vào nǎm đầu tiên của nền Cộng hoà, Bộ đội quân y Việt Nam đã nhanh chóng hội nhập và trở thành bộ phận cấu thành của Quân đội nhân dân Việt Nam.\nVới nhiệm vụ bảo đảm quân y cho các đơn vị chiến đấu và phục vụ chiến đấu; Bộ đội quân y kết hợp cùng dân y lập mạng lưới y tế thống nhất, góp phần chǎm sóc và bảo vệ sức khoẻ nhân dân. Các chiến sĩ quân y đã thực sự là những người lính trên mặt trận không tiếng súng mà không kém phần cam go thử thách. Ngày nay Bộ đội quân y đã lớn mạnh, có trình độ y học hiện đại với mạng lưới rộng từ TW đến các đơn vị cơ sở.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','04', 'Từ ngày 16-4-1946 đến ngày 23-5-1946, Phái đoàn Quốc hội nước Việt Nam dân chủ cộng hoà do đồng chí Phạm Vǎn Đồng dẫn đầu sang thǎm thân thiện Quốc hội và nhân dân Pháp. Nhiệm vụ của phái đoàn là đoàn kết, làm cho người Pháp hiểu ta để gây tình hữu nghị giữa hai dân tộc. Cuộc đi thǎm nước Pháp của phái đoàn Quốc hội ta đã thành công tốt đẹp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','04', 'Ngày 16-4-1959, tại Đại hội lần thứ hai Hội Nhà báo Việt Nam. Hồ Chủ tịch đã cǎn dặn: \"Chính trị phải làm chủ đường lối, chính trị đúng thì những việc khác mới đúng được cho nên báo chí của ta đều phải có đường lối chính trị đúng\".\nThấm nhuần lời dạy của Bác, giới báo chí Việt Nam không ngừng trau dồi bản lĩnh nghề nghiệp, giữ gìn phẩm chất Cách mạng xứng đáng là lực lượng xung kích trên mặt trận tư tưởng vǎn hoá, góp phần tích cực vào công cuộc đổi mới toàn diện hiện nay.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','04', 'Ngày 16-4-1972, Tổng thống Nichxơn ra lệnh cho tàu chiến và máy bay Mỹ ồ ạt đánh phá miền Bắc nước ta một cách dã man chưa từng có; tàu chiến Mỹ bắn phá dọc bờ biển từ Nghệ An đến Đồ Sơn; lúc 2 giờ 15 phút hàng trǎm máy bay trong đó có hàng chục máy bay B52 đánh phá bừa bãi cảng Hải Phòng; đến 9 giờ 30 phút nhiều máy bay Mỹ bắn phá nội thành Hà Nội và đánh trở lại cảng Hải Phòng. Quân dân Hà Nội - Hải Phòng cảnh giác cao, đánh giỏi thắng lớn, bắn tan xác 15 máy bay Mỹ, trong đó thủ đô Hà Nội bắn rơi 5 chiếc, thành phố cảng Hải Phòng bắn rơi 10 chiếc trong đó có một chiếc B52.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','04', '16-4-1975: giải phóng Phan Rang. \nMở đầu trận đánh, sư đoàn 3 quân khu 5 được tǎng cường trung đoàn bộ binh 25 hình thành ba mũi tấn công Phan Rang, sân bay Thành Sơn và cảng Ninh Chữ. Trung đoàn 2 tiến công phá vỡ tuyến phòng ngự của địch ở Kiền Kiền. Trung đoàn 414 đánh về hướng Ninh Chữ. Trung đoàn 25 được tǎng cường tiểu đoàn 6 (trung đoàn 12) tiếp tục tiến công tuyến phòng ngự ngoại vi sân bay. Trung đoàn 101 (sư đoàn 325) và một tiểu đoàn xe tǎng bước vào chiến đấu thực hiện đòn tiến công quyết định. Địch chống trả quyết liệt. Ta thực hành tiến công trong hành tiến, tập trung ưu thế binh hoả lực trên hướng chủ yếu, hình thành thế bao vây vu hồi, đột phá mạnh, nhanh chóng chọc sâu chia cắt địch. Phối hợp chặt chẽ giữa bộ đội chủ lực và bộ đội địa phương, ta lần lượt đánh chiếm dinh tỉnh trưởng, sân bay Thành Sơn, Tháp Chàm và một số vị trí khác, làm tan rã toàn bộ lực lượng địch ở Phan Rang, bắt sống trung tướng Nguyễn Vĩnh Nghi, chuẩn tướng Phạm Ngọc Sang, thu nhiều vị trí trang bị, trong đó có gần 40 máy bay nguyên vẹn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '16','04', 'Tenlơman, nhà hoạt động chính trị lỗi lạc của phong trào công nhân Đức và Quốc tế, tổng bí thư Đảng cộng sản Đức sinh ngày 16-4-1886.\nLà Đảng viên Đảng Xã hội - Dân chủ Đức nhưng ông phản đối chủ trương cơ hội của Đảng. Là bí thư Đảng xã hội - Dân chủ nhưng khi Đảng Cộng sản thành lập ông đã ra nhập và được bầu vào BCHTW Đảng Cộng sản Đức. Ông cầm đầu cuộc khởi nghĩa vũ trang của giai cấp vô sản và đưa lực lượng vũ trang của giai cấp công nhân thoát khỏi vòng vây của kẻ địch.\nNǎm 1924, Tenlơman là Uỷ viên Bộ Chính trị, 1925, là Tổng Bí thư Đảng Cộng sản Đức. Ông được bầu vào ban chấp hành quốc tế cộng sản từ nǎm 1924. Ông đã tham gia làm đại biểu Cộng sản trong Quốc hội Đức và tổ chức ra \"Liên đoàn xung kích đỏ\" để chống cảnh sát, côn đồ và bọn phát xít phá hoại phong trào công nhân. \nKhi Hítle thành lập chế độ độc tài phát xít Tenlơman chuyển vào hoạt động bí mật. Nǎm 1933 ông bị cảnh sát phát xít Đức bắt giam sau 11 nǎm giam cầm, không làm nhụt được tinh thần cách mạng bất khuất của ông, bọn phát xít đã ám hại ông ở trại tập trung Bukhenvan tháng 8 nǎm 1944.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '16','04', 'Vua hề Saclô - nhà điện ảnh tài ba - tên thật là Sacli Saplin sinh ngày 16-4-1889. Sự nghiệp nghệ thuật của Saclô đã để lại một khối lượng to lớn và đóng góp nhiều giá trị cho điện ảnh bởi những tư tưởng yêu hoà bình và tự do.\nÔng đã để lại những tác phẩm điện ảnh nổi tiếng như: Nhà độc tài, Thời đại mới, Đi tìm vàng, Gà trống nuôi con...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '16','04', '16-4-1948: Thành lập Tổ chức Hợp tác Kinh tế châu Âu (OEEC).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','05', 'Đặng Huy Trứ sinh ngày 16-5-1825 ở huyện Hương Điền, nay thuộc tỉnh Thừa Thiên Huế, đậu giải nguyên và làm quan ngự sử ở triều đình.\nTrong các nǎm 1865 và 1867, ông được cử sang Hương Cảng và Áo Môn để thǎm dò tình hình thực dân Pháp và mua sắm vũ khí. Ông là một trong những người sớm nhận ra rằng: \"Muốn thắng được kẻ thù thì phải hiểu rõ chúng\". Đặng Huy Trứ có công đóng góp trên nhiều lĩnh vực: Giáo dục, vǎn hoá, chính trị, kinh tế, ngoại giao, quân sự. Ông thuộc phái \"chủ chiến\" trong triều đình huế. Sau khi Hà Nội thất thủ lần thứ nhất (1873), ông cùng Hoàng Tá Viêm rút lên Đồn Vòng, Hưng Hoá chống Pháp và mất tại đây lúc 19 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','05', 'Ngày 16-5-1939 hơn 3000 công nhân nhà máy sợi Hải Phòng tiến hành cuộc bãi công. Từ trước đó, đại biểu của công nhân đã nhiều lần đưa yêu sách đòi tǎng lương nhưng không được giới chủ chấp nhận. Cuộc bãi công lần này là cuộc bãi công tập thể và được sự ủng hộ nhiệt thành của giai cấp công nhân ở Hải Phòng và Hà Nội. Trong bức thư gửi tới lao động cả nước cám ơn sự ủng hộ, những người bãi công đã viết: \"Vô sản thế giới muôn nǎm. Mặt trận dân chủ muôn nǎm. Tự do nghiệp đoàn, dân chủ và đoàn kết muôn nǎm\". Đây là cuộc đấu tranh tiêu biểu nhất của phong trào công nhân nǎm 1939.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','05', 'Theo một đạo dụ cho phép của Bảo Đại, ngày 16-5-1945 Hội Tân Việt nam được thành lập đã thu hút nhiều trí thức đương thời. Tham gia có: Đào Duy Anh, Phan Anh, Đỗ Đức Dục, Vũ Đình Hoè, Nguyễn Đình Trọng, Nguyễn Xiển, Nghiêm Xuân Yêm...do Vũ Đình Hoè làm Tổng thư ký.\nHội Tân Việt Nam có đường lối chính trị không rõ rệt, mục tiêu chung chung. Tổ chức này nhanh chóng phân hoá. Một bộ phận trở thành thành viên của Việt Minh. Ngày 22-7-1945, Hội Tân Việt Minh tự giải tán.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','05', 'Ngày 16-5-1946, Chính phủ nước Việt Nam Dân chủ cộng hoà thành lập \"Quĩ tham gia kháng chiến\", kêu gọi mỗi người dân đóng góp một số tiền bằng 10 ngày sinh hoạt phí của bộ đội. Kết quả là thu được 174 triệu đồng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '16','05', 'Ngày 16-5-1965, Tại Đại hội Đảng Cộng sản Trung Quốc khoá 9, Mao Trạch Đông đã phát động cuộc đại Cách mạng vǎn hoá vô sản (1966-1976). Thực chất đây là cuộc tranh giành quyền lực, gây tổn thất nặng nề đối với Cách mạng Trung Quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','06', 'Ngày 16-6-1919, Nguyễn Tất Thành chính thức lấy tên Nguyễn Ái Quốc, gửi đến hội nghị Vécxây bản yêu sách của nhân dân Việt Nam đòi Chính phủ Pháp phải thừa nhận các quyền tự do, dân chủ và quyền bình đẳng của dân tộc Việt Nam, Bản yêu sách gồm 8 điểm:\n1. Ân xá toàn thể chính trị phạm Việt Nam \n2. Cải cách nền pháp lý Đông Dương.\n3. Tự do báo chí và tự do tư tưởng.\n4. Tự do lập hội và tự do hội họp.\n5. Tự do cư trú ở nước ngoài.\n6. Tự do học tập và mở các trường kỹ thuật.\n7. Thay chế độ sắc lệnh bằng chế độ luật pháp.\n8. Phải có đại diện người Việt Nam để trình bày nguyện vọng của người bản xứ.\nViệc đưa bản yêu sách là đòn tiến công đầu tiên của đồng chí Nguyễn Ái Quốc vào đế quốc Pháp và được đánh giá là \"tiếng bom Vécxây\". Đó là sự kiện chính trị quan trọng gây xáo động trong thế giới thuộc địa, đồng thời là tiếng sấm mùa xuân, thức tỉnh nhân dân Việt Nam đứng lên đấu tranh cách mạng, báo hiệu một giai đoạn mới trong phong trào giải phóng dân tộc ở nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','06', 'Để tǎng cường khai thác than phục vụ cho chiến tranh xâm lược, thực dân Pháp bắt công nhân làm thêm giờ, tǎng thêm chuyến xe mà không tǎng lương. Tiếp tục cuộc đấu tranh đã phát động từ đầu nǎm, ngày 16-6-1951 toàn thể công nhân mỏ Hà Tu, Hòn Gai tổ chức bãi công.\nCuộc đấu tranh kiên quyết của công nhân mỏ Hà Tu đã thu được thắng lợi. Chủ mỏ phải tǎng lương cho công nhân 50 xu một ngày, và rút 2 chuyến xe ở những nơi khó làm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','06', 'Trung đội dân quân gái xã Hoa Lộc, huyện Hậu Lộc, tỉnh Thanh Hoá đã bắn rơi 1 máy bay phản lực A4 của Mỹ ngày 16-6-1967. Đây là đơn vị dân quân gái đầu tiên ở miền Bắc bắn rơi máy bay Mỹ. Hồ Chủ tịch đã gửi thư khen. Sau đó hơn 4 tháng, ngày 2-11-1967, đơn vị này lại bắn rơi 1 chiếc F4 của Mỹ. Trong cuộc chiến đấu chống chiến tranh phá hoại, các đơn vị dân quân gái ở Thanh Hoá đã bắn rơi 7 máy bay Mỹ các loại.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','06', 'Ngày 16-6-1994, tại Mátxcơva, hiệp ước về những nguyên tắc cơ bản giữa liên bang Nga và Cộng hoà xã hội chủ nghĩa Việt Nam được 2 bên ký kết. Hiệp ước này tạo cơ sở pháp lý để phát triển tình hữu nghị truyền thống và sự hợp tác anh em giữa hai nước, thay cho Hiệp ước hữu nghị và hợp tác giữa Liên Xô cũ và Việt Nam ký nǎm 1978.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','06', 'Lãnh tụ nông dân Nguyễn Trung Trực đã cầm đầu cuộc khởi nghĩa chống Pháp xâm lược vào ngày 16-6-1868 tại tỉnh Kiên Giang.\nÔng chỉ huy nghĩa quân đánh úp đồn Rạch Giá, diệt chủ tỉnh, 4 sĩ quan và 67 lính địch, thu gần 100 súng các loại. Trận đánh tiêu diệt tàu Hy Vọng của Pháp do ông cùng nghĩa quân cải trang trên sông Vàm Cỏ đã chứng tỏ một cách đánh thông minh, sáng tạo và hiệu quả. Cuộc khởi nghĩa được đông đảo nông dân Nam Bộ hưởng ứng. Song do thế của nghĩa quân và bọn Pháp quá chênh lệnh Nguyễn Trung Trực bị sa vào tay giặc. Chúng đưa ông lên Sài Gòn tra tấn và dụ dỗ không được, đã đem ông về Rạch Giá. Ngày 27-10-1868, trước khi bị hành quyết, ông nói một câu nổi tiếng: \"Bao giờ nước Nam này hết cỏ, thì mới hết người Nam đánh Tây\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '16','06', 'Hồi 12 giờ 30 phút (giờ Mátxcơva) ngày 16-6-1963, Liên Xô đã phóng lên quỹ đạo vệ tinh trái đất tàu vũ trụ \"Phương Đông 6\" do Têrêscôva Valentina Vladimirôpna, công dân Liên Xô điều khiển. Tàu vũ trụ đã bay 48 vòng quanh trái đất, ở trong vũ trụ 71 giờ và trở về mặt đất an toàn ngày 19-6-1963.\nValentina Têrêscôva sinh trưởng trong một gia đình nông trang viên ở làng Maxlenicôvô. Khi nghe tin \"Con người bay vào vũ trụ\" với người anh hùng Gagarin, tiếp theo là Titôp, đang sinh hoạt ở câu lạc bộ hàng không, Valentina quyết định xin ra nhập đội ngũ các nhà du hành vũ trụ, và chị đã được toại nguyện. Chuyến bay \"Phương Đông 6\" thành công là kết quả của sự rèn luyện kiên trì, gian khổ với tinh thần dũng cảm và sáng tạo.\nValentina Têrêscôva được bầu làm phó chủ tịch Liên đoàn phụ nữ dân chủ quốc tế, Ủy viên Hội đồng hoà bình thế giới, Ủy viên Ban Chấp hành Trung ương Đảng cộng sản Liên Xô, Ủy viên Đoàn chủ tịch Xô Viết tối cao. Têrêscô Valentina còn được mệnh danh là \"Người phụ nữ của thế kỷ\", \"Nữ chủ nhân của vũ trụ\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','07', 'Tại Việt Bắc, Hội nghị vǎn hoá toàn quốc lần thứ hai đã khai mạc vào ngày 16-7-1948.\nTrong Hội nghị, đồng chí Trường Chinh, Tổng Bí thư của Đảng đã trình bày bản báo cáo nổi tiếng \"Chủ nghĩa Mác và vấn đề vǎn hoá Việt Nam\". Bản báo cáo đã giải thích rõ ràng lập trường và quan điểm của Đảng về vấn đề vǎn hoá Việt Nam và đề ra phương hướng hoạt động của mặt trận vǎn hoá trong giai đoạn mới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','07', 'Ngày 16-7-1960, Hồ Chủ tịch đã đi thǎm Trại thí nghiệm giống lúa của Sở Nông lâm Hà Nội ở thôn Phú Mỹ, xã Mỹ Đình, nay thuộc huyện Từ Liêm. Tại đây Người đã cấy thử máy cấy lúa và Người nói \"Người nông dân nước ta làm việc vất vả, nhất là việc cấy, cứ phải cúi gập lưng xuống... Cần phải đẩy mạnh việc cải tiến nông cụ, không những giảm bớt được sức lao động mà còn nâng cao nǎng suất. Cả hai đều nhằm nâng cao đời sống cho mọi người...\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','07', 'Ngày 16-7-1980, Tổng cục Bưu điện tổ chức khánh thành công trình Đài thông tin vệ tinh mặt đất Hoa Sen.\nSau gần nửa nǎm khẩn trương xây dựng, với tinh thần lao động cần cù, sáng tạo của tập thể cán bộ, công nhân Việt Nam, với sự giúp đỡ hết lòng của Đoàn chuyên gia Liên Xô, công trình đài vệ tinh mặt đất Hoa Sen đã chính thức đưa vào sử dụng. Ngày 7-6-1980 đã hoàn thành việc đưa ǎng ten gương Parabôn lên trụ, một công việc chủ yếu của công trình. Ngày 24-6 lắp xong đoạn cuối cùng của một cột ǎng ten cao 124,80 mét. 16 giờ 30 ngày 7-7-1980 lần đầu tiên tại Trung tâm truyền hình Giảng Võ, Hà Nội đã trực tiếp ghi nhận được hình ảnh màu của chương trình hàng ngày ở Matxcơva. Công trình đã được đưa vào sử dụng đúng dịp kỷ niệm lần thứ 25 ngày ký Hiệp định về hợp tác kinh tế khoa học kỹ thuật, thương mại giữa Việt Nam và Liên Xô, và khai mạc đại hội Ôlympic Matxcơva 1980.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '16','07', 'Ngày 16-7-1945. Hoa Kỳ đã thử bom nguyên tử (bom A) lần đầu tiên.\nSau đó, bốn cường quốc khác cũng đã thử bom A lần đầu tiên của họ.\n- Liên Xô: Ngày 29-8-1949\n- Anh: Ngày 3-10-1952\n- Pháp: Ngày 14-2-1960\n- Trung Quốc: Ngày 16-10-1964')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','08', 'Tại phiên họp thứ 40 Đại hội lần thứ 7 của Quốc tế cộng sản ngày 16-8-1935, đồng chí Nguyễn Thị Minh Khai (lấy tên là Phan Lan) đã đọc tham luận. Bản tham luận nêu rõ phong trào đấu tranh và những thành tích của phụ nữ Đông Dương, sau đó đề cập đến vai trò phụ nữ trong cuộc đấu tranh hoà bình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','08', 'Quốc dân đại hội được triệu tập ở Tân Trào (Tuyên Quang) từ ngày 16 đến 17-8-1945. Tham dự Đại hội có hơn 60 đại biểu Trung, Nam, Bắc, đại biểu kiều bào ở nước ngoài, đại biểu các đảng phái chính trị, các đoàn thể nhân dân, các dân tộc và tôn giáo ... Đại hội đã quán triệt đường lối của Hội nghị toàn Quốc Đảng cộng sản Đông Dương đã tiến hành khởi nghĩa giành chính quyền trước khi quân đồng minh vào Đông Dương. Đại hội thông qua 10 chính sách lớn của Việt Minh là:\n- Giành lấy chính quyền, xây dựng một nước Việt Nam Dân chủ cộng hoà trên nền tảng hoàn toàn độc lập ...\n- Vũ trang nhân dân, phát triển Quân giải phóng Việt Nam.\n- Tịch thu tài sản của giặc và Việt gian, tuỳ từng trường hợp, sung công hay chia cho dân nghèo.\n- Bỏ các thứ thuế do Nhật, Pháp đặt ra, đặt một thứ thuế công bằng và nhẹ.\n- Ban bố những quyền dân chủ cho dân.\n- Chia lại ruộng công cho công bằng, giảm địa tô, lợi tức, hoãn nợ, cứu tế nạn dân.\n- Ban bố luật lao động.\n- Xây dựng kính tế quốc dân, phát triển nông nghiệp, mở mang ngân hàng.\n- Xây dựng nền quốc dân giáo dục.\n- Thân thiện và giao hảo với các nước đồng minh và các nước dân tộc nhược tiểu.\nĐại hội còn quy định Quốc kỳ là lá cờ đỏ có ngôi sao vàng 5 cánh ở giữa. Quốc ca là bài \"Tiến quân ca\" và cử ra Uỷ ban Giải phóng dân tộc Việt Nam, tức Chính phủ lâm thời do Hồ Chí Minh làm Chủ tịch. Uỷ ban giải phóng dân tộc giao quyền chỉ huy cho Uỷ ban khởi nghĩa toàn quyền hành động.\nĐại hội Quốc dân Tân Trào đã thể hiện được sự đoàn kết nhất trí và ý chí quyết thắng của cả dân tộc trong mặt trận Việt Minh, dưới sự lãnh đạo của Đảng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','08', 'Giáo dục tiểu học là điều kiện cơ bản để nâng cao dân trí là cơ sở ban đầu hết sức quan trọng cho việc đào tạo trẻ em trở thành công dân tốt cho đất nước. Với mục đích đó ngày 16-8-1991, Chủ tịch Hội đồng Nhà nước đã ký lệnh công bố Luật phổ cập giáo dục tiểu học.\nLuật này quy định chế độ phổ cập giáo dục tiểu học với nội dung chính như: Học sinh, nhà trường, gia đình, xã hội trong phổ cập giáo dục tiểu học; Quản lý Nhà nước về phổ cập giáo dục tiểu học; khen thưởng và xử lý vi phạm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','09', 'Giáo sư, luật sư Nguyễn Mạnh Tường sinh ngày 16-9-1909 tại phố Hàng Đào - Hà Nội.\nNăm 1932, vào lúc 22 tuổi. Nguyễn Mạnh Tường đỗ tiến sĩ Luật khoa và tiến sĩ văn chương. Báo chí nước Pháp và trong nước ca ngợi ông, người thanh niên Việt Nam thông minh, hiếu học và tài giỏi, trong một năm đỗ 2 bằng tiến sĩ, hiện tượng chưa từng có trong nền giáo dục văn chương Pháp.\nTrở về năm 1936, Nguyễn Mạnh Tường dạy học ở trường Bưởi (nay là trường Chu Văn An). Sau năm 1945, ông tham gia kháng chiến chống Pháp, là thành viên của phái đoàn Chính phủ dự hội nghị đàm phán ở Đà Lạt, dự các Hội nghị hoà bình thế giới ở Bắc Kinh và Viên.\nHoà bình lập lại, từ 1954 đến 1970, Nguyễn Mạnh Tường là Uỷ viên Trung ương Mặt trận Tổ quốc Việt Nam khoá I, Phó chủ tịch Hội Luật gia Việt Nam, Phó hiệu trưởng trường Đại học Sư phạm Hà Nội, chuyên viên nghiên cứu giáo dục. Ông đã viết 14 tác phẩm bằng tiếng Pháp và 4 tác phẩm lớn bằng tiếng Việt. Ông mất năm 1997. Trong sổ tang tưởng niệm nhà trí thức Nguyễn Mạnh Tường, tổng bí thư Đảng cộng sản Việt Nam Đỗ Mười đã ghi \"....Vô cùng thương tiếc giáo sư - luật sư Nguyễn Mạnh Tường - Một trí thức yêu nước đã có cống hiến cho sự nghiệp giải phóng dân tộc, thống nhất tổ quốc và góp phần xây dựng nền giáo dục của Việt Nam\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','09', 'Ngày 16-9-1950, bộ đội ta tiêu diệt cứ điểm Đông Khê mở màn chiến dịch Biên giới và kết thúc vào ngày 14-10 năm đó.\nChủ tịch Hồ Chí Minh đã ra mặt trận trực tiếp chỉ đạo ban chỉ huy chiến dịch và động viên bộ đội chiến đấu.\nTrong chiến dịch Biên giới, ta tiêu diệt và bắt sống hơn 8.000 tên địch, giải phóng một dải biên giới dài 750 km với 35 vạn dân, 5 thị xã, 13 thị trấn và nhiều vùng quan trọng. Căn cứ Việt Bắc được mở rộng và củng cố.\nChiến dịch Biên giới là chiến dịch tiến công quy mô lớn đầu tiên của ta, tạo ra bước ngoặt của cuộc kháng chiến chống Pháp. Đánh dấu sự trưởng thành của quân ta, đánh dấu bước chuyển biến giai đoạn mới: giai đoạn ta giành thế chủ động trên chiến trường Bắc Bộ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','09', 'Đầu tháng 5-1972, ta đã giải phóng tỉnh Quảng Trị. Từ ngày 28-6, Mỹ - Nguỵ đã huy động lực lượng lớn mở các cuộc hành quân hòng chiếm lại Quảng Trị mà mục tiêu số 1 là tỉnh lỵ, trong đó có Thành cổ để mặc cả với ta trên bàn hội nghị Pari. Đến ngày 16-9-1972, ta rút khỏi Thành cổ sau khi hoàn thành nhiệm vụ đặt ra.\nTrong 81 ngày đêm \"lửa thép\" Quảng Trị, ta liên tục chiến đấu ngăn chặn các cuộc tiến công của địch, bảo vệ thị xã và Thành cổ.\nQuân và dân ta đã loại khỏi vòng chiến đấu 26 nghìn tên địch, phá huỷ 349 xe quân sự, hàng chục trận địa pháo của địch, bắn rơi gần 200 máy bay các loại. Hai sư đoàn thiện chiến nhất của địch bị mệt mỏi, kiệt sức. Ngược lại qua chiến đấu, bộ đội ta có bước trưởng thành mới về sử dụng sức mạnh tổng hợp trong tác chiến hợp đồng binh chủng, tích luỹ được nhiều kinh nghiệm về cách đánh trong chiến dịch phòng ngự, góp phần cùng các chiến trường khác đánh bại chiến lược \"Việt Nam hoá chiến tranh\" của đế quốc Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','09', 'Theo Nghị quyết số 49/114 ngày 19-12-1994, Đại hội đồng Liên hiệp quốc đã chọn ngày 16-9 hàng năm là \"Ngày quốc tế bảo vệ tầng Ô-dôn\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '16','09', 'Ngày 16-9-1745 tại Pêtécbua đã ra đời người con ưu tú, vị thống soái Nga Kutuzốp. Người đã đánh bại đội quân Napôlêong trong cuộc chiến tranh ái quốc năm 1812 của nước Nga và chôn vùi mộng bá chủ thế giới của Napôlêong. Thắng lợi của quân Nga cho thấy tài năng và nghệ thuật chỉ đạo chiến lược chiến thuật của Kutuzốp đạt tới trình độ rất cao. Ông biết sử dụng hình thức cổ điển phòng ngự chiến lược, tạo thế trận để xuất phát tiến công, đồng thời đã sử dụng các hình thức mới để tiến hành phản công, tổng hợp tài tình việc cơ động lực lượng cho trận quyết chiến chiến lược.\nTrong những năm chiến tranh giữ nước vĩ đại 1941-1945, nhà nước Liên Xô đã lập ra Huân chương Kutuzốp. Ông mất ngày 28-9-1813.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '16','09', 'Tề Bạch Thạch - thiên tài hội hoạ, một danh nhân văn hoá thế giới. Ông sinh ngày 22-11-1863 tại tỉnh Hồ Nam, Trung Quốc.\nÔng vẽ xuất thần tất cả các loại tranh sơn thuỷ, nhân vật, hoa điểu, thảo tùng. Ngoài vẽ tranh giỏi ông lại làm thơ hay, khắc dấu triện, ba tài năng đều đạt tới đỉnh cao tuyệt vời. Trọn đời ông đứng về người lao động và hướng thiện, vị tha. Nhiều tranh của ông ca ngợi đất nước, nhân dân, hoà bình, hạnh phúc.\nSau ngày giải phóng (1949), ông được phong học hàm Giáo sư danh dự Học viện Mỹ thuật Trung ương, được phong nghệ sĩ nhân dân, đại biểu Quốc hội. Năm 1955, ông được hội đồng hoà bình thế giới trao giải thưởng và công nhận Danh nhân văn hoá thế giới. Ông mất ngày 16-9-1957 tại Bắc Kinh, thọ 94 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','10', 'Ngày 16-10-1954, tại Bắc Bộ phủ, Chủ tịch Hồ Chí Minh đã tiếp đoàn đại biểu nhân dân Thủ đô Hà Nội. Mở đầu buổi tiếp, Người nói: \"Đã 8 năm xa cách, thời gian đâu phải ngắn, ta cũng nên gặp mặt nhau. Việc quan trọng nhất trước mắt ta là: Sản xuất, khôi phục sản xuất. Nếu mọi người thực sự hoan nghênh Đảng và Chính phủ trở về thì hãy đem cái tinh thần quý báu đó vào công việc trên...\"\nTiếp đó Hồ Chủ tịch biểu dương những thành tích của bộ đội, cán bộ, nhân dân trong việc tiếp quản Thủ đô, trong khôi phục các hoạt động và ổn định sinh hoạt của nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','10', 'Tổ chức lương thực và nông nghiệp Liên hiệp quốc (viết tắt là FAO) ra đời ngày 16-10-1945.\nHiện nay FAO có hơn 160 nước thành viên (trong đó có Việt Nam), trụ sở đặt tại Rôma (nước Italia). FAO có phòng làm việc tại nhiều khu vực trên thế giới, trong đó có Việt Nam.\nHoạt động của FAO nhằm tìm cách làm tăng khối lượng và cải tiến hệ thống phân phối nông sản, góp phần nâng cao phúc lợi và điều kiện sống của nông dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','10', 'Trần Quốc Thảo, tên thật là Hồ Xuân Lưu, sinh năm 1914 ở tỉnh Quảng Trị hy sinh ngày 16-10-1957 tại Sài Gòn.\nNăm 1936, ông tham gia mặt trận dân chủ tại Quảng Trị \nNăm 1940 ông tham gia vào Ban Thường vụ Xứ uỷ Trung Kỳ.\nSau khi Nhật đảo chính Pháp (Tháng 3-1945) ông về hoạt động ở quê nhà Quảng Trị. Năm 1946, ông ra Hà Nội, phụ trách báo Lao động. Năm 1949, ông vào Nam Bộ làm Bí thư Ban Công vận Xứ uỷ, năm sau làm thường vụ đặc khu Sài Gòn - Chợ Lớn. Đến năm 1957, ông được đề cử làm Bí thư Khu uỷ Sài Gòn - Chợ Lớn. Ông đã đưa phong trào đấu tranh chính trị phát triển mạnh ở nội thành. Đầu năm 1957, ông bị bắt, bị tra tấn tàn nhẫn và hy sinh anh dũng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','11', 'Trần Khâm, tức Trần Nhân Tông, sinh ngày 7-12-1258. Triều đại ông nổi bật tinh thần nhân quân dân đại đoàn kết, nổi tiếng qua hai cuộc Hội nghị Diên Hồng và Bình Than, và hai lần chiến thắng quân Nguyên xâm lược.\nSau khi nhường ngôi cho con, nǎm 1293 Trần Nhân Tông ẩn tu tại núi Yên Tử và mất ở đó ngày 16-11-1308.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','11', 'Ngày 12-11-1886 đã nổ ra cuộc khởi nghĩa Ba Đình chống quân Pháp, do Phạm Bành và Đinh Công Tráng lãnh đạo.\nCǎn cứ Ba Đình được xây dựng trên 3 làng Mỹ Khê, Thượng Thọ và Mậu Thịnh (thuộc huyện Nga Sơn, tỉnh Thanh Hoá).\nTuy cuộc khởi nghĩa Ba Đình thất bại, song đã làm cho quân địch thiệt hại nặng nề, nêu cao tinh thần chiến đấu anh dũng của nghĩa quân và nhân dân tỉnh Thanh Hoá.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','11', 'Nhà vǎn Nguyễn Thành Long sinh ngày 16-11-1925, quê ở thành phố Quy Nhơn, tỉnh Bình Định, qua đời nǎm 1991 tại Hà Nội.\nTrong kháng chiến chống thực dân Pháp, ông hoạt động vǎn nghệ trong chi hội vǎn học kháng chiến Liên khu V.\nTừ nǎm 1955 đến cuối đời, ông sống chủ yếu ở Hà Nội.\nTrong khoảng hơn 10 tác phẩm của nhà vǎn Nguyễn Thành Long, bạn đọc yêu thích các truyện ký: Bát cơm cụ Hồ, Giữa trong xanh, Lý Sơn mùa tỏi, Lặng lẽ Sa Pa, Trời xanh mông mênh, Hạnh Nhơn, Núi Đỗ Quyên...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','11', 'Từ ngày 16 đến 18-11-1983 tại Hà Nội đã diễn ra Đại hội Công đoàn Việt Nam lần thứ IV. Có 949 đại biểu tham dự. Nghị quyết Đại hội nhấn mạnh: Phát huy mạnh mẽ ý thức và quyền làm chủ tập thể xã hội chủ nghĩa, hǎng hái đi đầu trong việc thực hiện 3 cuộc cách mạng khắc phục khó khǎn, tạo ra những chuyển biến mạnh mẽ trong đời sống kinh tế - xã hội.\nĐại hội đã bầu ra Ban Chấp hành mới gồm 155 đồng chí, do đồng chí Nguyễn Đức Thuận làm Chủ tịch.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','11', 'Ngày 16-11-1989, Chủ tịch Hội đồng nhà nước Võ Chí Công đã ký lệnh công bố Pháp lệnh về đê điều.\nMục đích của Pháp lệnh nhằm nâng cao trách nhiệm của chính quyền các cấp, cơ quan Nhà nước, tổ chức xã hội và cá nhân trong việc xây dựng, tu bổ, bảo vệ, sử dụng và hộ đê. Pháp lệnh có 6 chương với 34 điều.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','11', 'Đại hội Đảng bộ Hà Nội lần thứ XI đã tiến hành từ ngày 16 đến ngày 19-11-1991, tại Cung vǎn hoá Hữu nghị Việt Xô. Có 437 đại biểu thay mặt gần 140 Đảng viên về dự.\nĐại hội nhận định: Đảng bộ Hà Nội đã lãnh đạo giữ vững ổn định chính trị, củng cố an ninh quốc phòng, đẩy lùi tiêu cực, tích cực đổi mới, cùng cả nước cơ bản ra khỏi khủng hoảng kinh tế - xã hội.\nCác đại biểu đã bầu Ban Chấp hành Đảng bộ Hà Nội khoá XI gồm có 51 uỷ viên, do đồng chí Phạm Thế Duyệt làm Bí thư Thành uỷ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '16','11', 'Đalǎngbe Giǎng LơRông (D\\'alembert, Jean le Roud) sinh ngày 16-11-1717 tại Pari, thủ đô nước Pháp.\nCác sĩ quan pháo binh rất quen biết tên tuổi Đalǎngbe, vì ông là người đầu tiên đã giải thích rất khoa học, tại sao đường đạn lại bay cong? Còn các nhà khí tượng thì vẫn coi Đalǎngbe là đồng nghiệp của mình vì chính ông đã chứng minh được hiện tượng triều dâng của không khí. Các nhà Vật Lý tôn thờ Đalǎngbe vì ông đã viết một luận vǎn nổi tiếng về hiện tượng dao động ngang của màng rung. Bệnh sỏi thận đã cướp đi sinh mệnh của ông vào cuối nǎm 1783, khi ông 66 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '16','11', 'Ngày 16-11-1965, tàu vũ trụ không người lái Venus III của Nga được phóng và hạ cánh thành công lên sao Kim.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','12', 'Ngày 16-12-1427, tại phía nam thành Đông Quan (Hà Nội ngày nay), trước phái đoàn đại biểu nghĩa quân do Lê Lợi cầm đầu, Tổng binh Vương Thông phải tuyên thệ xin rút quân Minh về nước.\nTrong bài vǎn tế thề, Vương Thông phải cam kết \" Đem quân về nước, không thể kéo dài nǎm tháng để đợi viện binh\". Nếu \"làm trái lời thề thì bản thân Vương Thông, cả nhà, thân thích bị Trời Đất làm cho chết hết, còn quan quân không một người nào về đến nhà\".\nĐây là thắng lợi oanh liệt của cuộc kháng chiến chống quân Minh xâm lược (từ 1418 đến 1427).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','12', 'Nhà vǎn Bùi Huy Phồn sinh ngày 16-12-1912 quê ở Ứng Hoà, tỉnh Hà Tây, mất nǎm 1990.\nCác tác phẩm chính của ông gồm có: Lá huyết thư (Tiểu thuyết 1937), Một chuỗi cười (tiểu thuyết nǎm 1945), Tàn xuân đế quốc (tập thơ trào phúng nǎm 1959), Phất (tiểu thuyết nǎm 1961), Trái cam (tập trruyện nǎm 1972). Tiểu thuyết \\'\\'Phất\" là một thành công của Bùi Huy Phồn. Bằng ngòi bút trào phúng, ông đã dựng lại bức tranh khá sinh động về giai cấp tư sản Việt Nam thời kỳ Hà Nội bị thực dân Pháp chiếm đóng. Ông trình bày khá sắc nét nguồn gốc xuất thân, quá trình làm giàu, lối sống vô luân lý, \"trọng tiền khinh nghĩa\" của chúng, sự gắn bó giữa chúng với thực dân, đế quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','12', 'Nhà thơ, nhà báo Thôi Hữu, tên thật là Nguyễn Đức Giới quê ở huyện Hoằng Hoá, tỉnh Thanh Hoá, sinh nǎm 1919 hy sinh ngày 16-12-1950 tại Việt Bắc.\nNǎm 1940 ông làm thợ điện, tích cực hoạt động Đoàn thanh niên Dân chủ. Nǎm 1943 ông vào Đảng cộng sản Đông Dương, nǎm sau bị bắt, rồi vượt ngục tham gia Hội vǎn hoá Cứu quốc.\nSau Cách mạng tháng Tám, ông công tác ở các báo Sự thật, Thủ đô, Vệ quốc quân.\nThôi Hữu có nhiều bài thơ hay như: Lên Cấm Sơn, Xe trâu, Lời cô lái đò, Sau luỹ tre xanh và các phóng sự: Đi vào hậu địch, Tù binh đường số 4.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','12', 'Ngày 16-12-1952, Chủ tịch Hồ Chí Minh ký sắc lệnh số 129 đặt các \"Bảng gia đình vẻ vang\" và \"Bảng Vàng danh dự\" để tặng các gia đình có nhiều người tham gia lực lượng vũ trang (bộ đội chủ lực, bộ đội địa phương và dân quân du kích) và có công lao trong các cuộc kháng chiến.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '16','12', 'Ngày 16-12-1994, tại kỳ họp thứ 18 tổ chức ở Phukét (Thái Lan), Hội đồng di sản thế giới thuộc tổ chức UNESCO đã chính thức quyết định công nhân Vịnh Hạ Long (tỉnh Quảng Ninh) là di sản thiên nhiên thế giới.\nVịnh Hạ Long là một vùng lõm của biển Bắc Bộ, nằm giữa Hòn Gia và Cẩm Phả, dài chừng 40km, rộng 1.500 km2, với hơn 1.000 hòn đảo lớn nhỏ. Do sự tạo lập đặc biệt của thiên nhiên mà Hạ Long vừa duyên dáng vừa thơ mộng, vừa hùng vĩ. Một cảnh vật thần tiên khó nơi nào sánh được.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '16','12', 'Lútvích Van Béttôven (Beethoven Ludwig van) nhạc sĩ Đức vĩ đại, đại biểu lỗi lạc của trường phái cổ điển Viên, người đạt thành tựu xuất sắc trong hai thể loại lớn của âm nhạc là giao hưởng và xô nát. Ông là một trong số những nhạc sĩ cho tới nay có nhiều tác phẩm được sử dụng trong các chương trình biểu diễn âm nhạc trên thế giới.\nSinh ngày 16-12-1770, ông tập đàn từ nǎm 3 tuổi. Nǎm 11 tuổi ông đã biểu diễn như một nghệ sĩ đàn dương cầm điêu luyện. Nǎm 13 tuổi đã có tác phẩm đầu tay. Các tác phẩm nổi tiếng của ông là bản giao hưởng \"Anh hùng ca\", bản xônát \"Bình minh\". \"Apasiônata\"... Ông bị điếc từ nǎm 1798 và mất ngày 26-3-1827.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','01', 'Tháng 8-1990, với quan điểm Côoét thuộc lãnh thổ của mình, Irắc đã đưa quân vào chiếm đóng Côoét.\nHội đồng bảo an Liên hiệp quốc đã ra nhiều nghị quyết yêu cầu rút quân ra khỏi Côoét nhưng Irắc không chấp hành.\nNgày 17-1-1991, Liên quân gồm 40 nước do Mỹ cầm đầu đã mở cuộc tiến công đầu tiên bằng tên lửa phóng từ tàu chiến Mỹ ở ngoài khơi vịnh Pécxích và sau đó dùng máy bay ném bom xuống các mục tiêu quan trọng ở Côoét và Irắc.\nChiến tranh vùng Vịnh đã kéo dài suốt 40 ngày đêm, đến ngày 28-1-1991 mới kết thúc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','01', 'Bích Khê tên thật là Lê Quang Lương. Sinh ngày 24-3-1916 tại xã Phước Lộc, huyện Sơn Tinh tỉnh Quảng Ngãi.\nLàm thơ từ nǎm 12 tuổi đến khi nằm xuống, Bích Khê để lại ba tập thơ: \"Mấy dòng thơ cũ\" (1931-1936), \"Tinh huyết\" (1939). \"Tinh Hoa\" (1938-1944); một tập vǎn xuôi tự truyện \"Lột truồng\". Sinh thời, tất cả các tác phẩm đều chưa được xuất bản, trừ \"Tinh huyết\".\nLà một nhà cách tân thơ, Bích Khê đã góp cho làng thơ mới một giọng du dương đầy chất tượng trưng và huyền diệu. Bích Khê mất ngày 17-1-1946.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','02', 'Ngày 17-2-1947, kết thúc 60 ngày đêm chiến đấu dũng cảm của quân và dân Thủ đô. Trong 60 ngày đêm quân dân Thủ đô đã tiêu diệt gần 2000 tên địch, nổi tiếng với những trận đánh ở nhà Xôva, chợ Đồng Xuân, trường Trần Nhật Duật, hoàn thành nhiệm vụ tiêu hao sinh lực địch và giam chân một lực lượng lớn quân đội Pháp ở Hà Nội. Đêm 17-2 các chiến sĩ quyết tử Thủ đô được lệnh bí mật rút khỏi Hà Nội vượt sông Hồng sang vùng tự do ở Vĩnh Phú một cách an toàn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','02', 'Lê Hữu Trác, hiệu Hải Thượng Lãn Ông, quê ở tỉnh Hưng Yên, sinh nǎm 1720, mất ngày 17-2-1791. \nÔng đỗ hương cống nhưng không làm quan. Lê Hữu Trác là một đại danh y, nhà vǎn, nhà thơ và nhà tư tưởng lớn thời Lê Mạt.\nCác tác phẩm tiêu biểu của ông là:\nHải Thượng y tông tâm lĩnh khảo cứu về y học và thảo dược học, nêu lên những phương thuốc chữa bệnh, biên soạn trong gần 40 nǎm.\nThượng kinh ký sự là tập bút ký kể lại cuộc hành trình của ông lên Thǎng Long chữa bệnh cho Chúa Trịnh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','03', 'Vào ngày 17-3-1930, tại nhà số 42 phố Nhà Thiếc, Ban Chấp hành lâm thời Đảng bộ Hà Nội (của Đảng Cộng sản Đông Dương) đã được thành lập. \nBan Chấp hành Đảng bộ Hà Nội gồm có ba đồng chí: Đỗ Ngọc Du, Nguyễn Ngọc Vũ và Lều Thọ Nam. Bí thư đầu tiên của Thành ủy Hà Nội là đồng chí Đỗ Ngọc Du.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','03', 'Nhà bảo tàng của Trường Viễn Đông Bác Cổ ở Hà Nội được khởi công tháng 1-1926 và ngày 17-3-1931 được khánh thành. \nCông trình này được coi là một trong những tác phẩm kiến trúc đặc sắc và độc đáo ở Đông Dương. Hiện nay toà nhà này là Viện Bảo tàng lịch sử Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','03', 'Ngày 17-3-1963 Hồ Chủ tịch đến thǎm nhà ǎn khu tập thể Kim Liên.\nBác đi xem nơi chia cơm và thức ǎn, chỉ tay lên chiếc bóng đèn và một góc trần nhà, Bác phê bình: \"Nơi chia thức ǎn sao để bẩn thế này?\". Chị Lan phụ trách nhà ǎn nói: \"Thưa Bác chúng cháu xin sửa chữa ạ.\"\nSau đó Bác đi xuống nhà bếp, nhà tắm, nhà vệ sinh. Trước khi ra về Bác cǎn dặn các nhân viên, cán bộ nhà ǎn Kim Liên:\n- Kim là vàng, Liên là sen - Kim Liên là sen vàng. Đã là hoa sen vàng, các cháu phải phấn đấu sao cho hương thơm toả mỗi ngày một xa.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','03', '17-3-1975, bộ đội ta đã giải phóng hai thị xã Kontum và Plâycu trong chiến dịch Tây Nguyên \nThị xã Tây Nguyên được xây dựng trên bờ sông Đắc Pla, một nhánh của sông Pô Cô, giữa một đồng bằng nhỏ, ở độ cao 525 mét so với mặt biển. Kontum là trung tâm hành chính của Pháp ở Tây Nguyên trước đây.\nThực dân Pháp xây ở đây một nhà tù lớn để giam giữ các chiến sĩ cách mạng.\nCòn thị xã Plâycu được xây dựng giữa cao nguyên đất đỏ, màu mỡ, bằng phẳng, có đồng bào Giarai, có nhiều nông trường trồng cà phê và chè.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','04', 'Ngày nay nǎm 1864, Hồ Huấn Nghiệp bị thực dân Pháp bắt và sát hại.\nÔng sinh nǎm 1829, quê ở xã Yên Định, huyện Bình Dương, tỉnh Gia Định cũ. Ông là sĩ phu tiêu biểu cho chủ nghĩa yêu nước trong những nǎm đầu chống Pháp xâm lược ở Gia Định - Sài Gòn. Hồ Huấn Nghiệp đứng đầu chính quyền kháng chiến ở Tân Bình, kể cả sau khi vua Tự Đức nhượng cho Pháp ba tỉnh miền Đông Nam Kỳ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','04', 'Lưu Quang Vũ sinh ngày 17-4-1948, mất ngày 29-8-1988 do bị tai nạn giao thông.\nÔng quê ở thành phố Đà Nẵng, là con trai nhà thơ Lưu Quang Thuận, là chồng của nhà thơ Xuân Quỳnh.\nLưu Quang Vũ sáng tác nhiều thơ, truyện ngắn song ông nổi tiếng là một nhà viết kịch xuất sắc trong những nǎm 80, một tài nǎng vǎn học được công chúng rất yêu mến và thương tiếc khi qua đời ở tuổi 40 đang sung sức.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','04', 'Ngày 17-4-1946, Syria giành được độc lập sau khi toán quân Pháp cuối cùng rời khỏi đất nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','04', '17-4-1975, quân Khmer đỏ chiếm thủ đô Phnom Pênh và bắt đầu thi hành chính sách diệt chủng, giết chết hơn một triệu người.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','05', 'Ông Thái Phiên sinh nǎm 1882 ở làng Nghệ An, huyện Hoà Vang, nay thuộc thành phố Đà Nẵng.\nÔng là một trong những người đầu tiên theo Tây học, tham gia phong trào yêu nước, chống thực dân Pháp ở miền Trung.\nÔng vận động thanh niên xuất dương du học, ủng hộ Duy Tân hội của Phan Bội Châu. Ông Thái Phiên đã cùng Trần Cao Vân lãnh đạo vận động khởi nghĩa nǎm 1916 và mời vua Duy Tân tham gia khởi nghĩa chống Pháp. Việc bại lộ, ông Thái Phiên và ông Trần Cao Vân bị giặc Pháp bắt vào ngày 17-5-1916, hai ông bị chém ở phía Bắc thành phố Huế. Sau cách mạng tháng Tám thành phố Đà Nẵng có thời gian đổi tên là thành phố Thái Phiên để ghi nhớ tên ông và khích lệ tinh thần yêu nước trong nhân dân tại đây.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','05', 'Từ ngày 17-5-1958, Hồ Chủ tịch đã tới ở và làm việc tại ngôi nhà sàn trong Phủ Chủ tịch cho đến khi qua đời.\nTầng dưới nhà sàn là nơi Bác thường họp với Bộ Chính trị. Tại đây có 3 máy điện thoại, cạnh đó còn úp một chiếc mũ sắt bộ đội. Cửa cầu thang lên gác có chiếc chuông đồng nhỏ để báo cho Bác khi khách đến thǎm. Tầng trên của nhà sàn có 2 phòng nhỏ: phòng làm việc và phòng ngủ. Ở phòng ngủ có chiếc giường gỗ để mộc, 1 bàn gỗ nhỏ và chiếc tủ con đặt cạnh đầu giường.\nĐồ dùng hàng ngày của Bác là hai chiếc quạt (1 quạt giấy và 1 quạt lá cọ), chiếc phích nhỏ, 1 chai nước nguội, chiếc cốc thuỷ tinh, chiếc chổi tre, 1 cái radiô và một chiếc quạt điện.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','05', 'Quân và dân tỉnh Nghệ An đã bắn rơi chiếc máy bay Mỹ thứ 1900 trên miền Bắc nước ta vào ngày 17-5-1967.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','05', 'Nhạc sĩ Pháp Pôn Đuycax sinh ngày 1-10-1865. Ông là đại biểu nổi bật nhất của âm nhạc chủ nghĩa ấn tượng ở Pháp. Ông viết nhiều thể loại âm nhạc đa dạng, phổ biến hơn cả là ôpêra \"Arian và râu xanh\" và bản \"Xkecđô - Học trò phù thuỷ\". Ông mất ngày 17-5-1935 tại Pari.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','05', 'Hǎngri Bácbuýt (Henri Barbusse) là nhà vǎn nổi tiếng của Pháp. Ông giữ vai trò tiên phong trong việc tuyên truyền và phát triển tư tưởng vǎn học theo đường hướng hiện thực XHCN. Ông sinh ngày 17-5-1873 trong một gia đình tiểu trí thức.\nNǎm 23 tuổi Bácbuýt xuất bản tập thơ đầu tay. Khi chiến tranh thế giới thứ nhất bùng nổ ông tham gia quân đội. Là phóng viên mặt trận, ông có nhiều trang tư liệu về chiến tranh, về cuộc sống của người lính. Nǎm 1923 ông gia nhập Đảng Cộng sản Pháp.\nTác phẩm của Bácbuýt chủ yếu đề cập đến những đề tài thời sự. Các tác phẩm \"Những người van nài\" \"Địa ngục\" in đậm tính hiện thực, đi sâu khai thác số phận con người. Nhờ ảnh hưởng Cách mạng tháng Mười Nga, phong trào giải phóng thuộc địa, với các tác phẩm, ông được biết đến như một chiến sĩ đấu tranh cho hoà bình, tự do. Tiểu thuyết \"Khói lửa\" được coi là viên gạch đặt nền móng cho vǎn học XHCN Pháp. Những nǎm gần cuối đời ông còn viết các tác phẩm \"Ánh sáng\", \"Xiềng xích\", \"Giesu Dôla\"...\nÔng qua đời vào ngày 30-8-1935.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','06', 'Đại hội lần thứ 5 của Quốc tế cộng sản, gồm có đại biểu của 49 Đảng cộng sản trên thế giới, đã họp từ ngày 4-6 đến 8-7-1924 tại Mátxcơva.\nĐồng chí Nguyễn Ái Quốc tham dự đại hội với tư cách là đại biểu tư vấn. Tại đây, người đã đọc một bài phát biểu quan trọng, trình bày lập trường, quan điểm của mình và nêu cao vị trí, tầm quan trọng của Cách mạng ở thuộc địa. Hoạt động nổi bật nhất của người trong thời gian này là đấu tranh kiên trì, liên tục, dũng cảm chống những khuynh hướng sai lầm đối với Cách mạng ở thuộc địa. Cuối cùng Người nêu lên trách nhiệm của Quốc tế cộng sản đối với phong trào nông dân các nước thuộc địa.\nNhững đóng góp của đồng chí Nguyễn Ái Quốc với Quốc tế cộng sản cũng là những vấn đề cơ bản thuộc về chiến lược, sách lược và phương pháp của Cách mạng Việt Nam. Đó là bước chuẩn bị rất quan trọng về chính trị và tư tưởng để tiến tới thành lập một Đảng Cộng sản ở Việt Nam')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','06', 'Ngày 17-6-1929, hơn 20 đại biểu các tổ cộng sản mới được tổ chức ở Bắc Kỳ đã họp ở số nhà 312 phố Khâm Thiên - Hà Nội, quyết định thành lập Đông Dương cộng sản Đảng. Hội nghị đã thông qua Chính cương, Tuyên ngôn, Điều lệ Đảng, lấy cờ đỏ búa liềm làm cờ của Đảng, thừa nhận đường lối của Quốc tế cộng sản, và nhận định: trước hết phải làm cách mạng phản đế, sau đó làm cách mạng xã hội chủ nghĩa, thực hiện chuyên chính vô sản.\nHội nghị đã quyết định xuất bản tờ báo \"Búa liềm\", \"Bônsêvích\" và \"Cờ cộng sản\".\nSau khi thành lập, Đông Dương cộng sản Đảng đã tích cực đi vào quần chúng công - nông, tuyên truyền chủ nghĩa Mác - Lênin, xây dựng và phát triển tổ chức, ra sức lãnh đạo quần chúng đấu tranh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','06', 'Nguyễn Thái Học sinh nǎm 1901, quê ở Thổ Tang, Vĩnh Tường, Vĩnh Yên. Lúc còn trẻ ông theo học trường Cao đẳng sư phạm và Cao đẳng thương mại Đông Dương tại Hà Nội. Khi còn ngồi trên ghế nhà trường, ông viết thư gửi nhà cầm quyền Pháp đòi một số yêu sách về xã hội, chính trị và đề nghị cải tổ nền hành chính thuộc địa, ban hành tự do ngôn luận.\nNǎm 1927, Nguyễn Thái Học thành lập quốc dân Đảng và được bầu làm trưởng đảng. Chủ trương của Đảng này là dùng bạo lực để giành lại quyền độc lập dân tộc. Bị giặc khủng bố, Nguyễn Thái Học phải lui vào hoạt động bí mật và tổ chức cuộc khởi nghĩa 10-2-1930. Mục đích cuộc khởi nghĩa là tấn công vào các cơ sở của Pháp ở Yên Bái, Hưng Hoá, Lâm Thao, Hải Dương, Kiến An..., nhưng bị thất bại. Ông và một số chiến sĩ bị bắt.\nNgày 17-6-1930 - 29 tuổi, Nguyễn Thái Học bị Pháp đưa lên đoạn đầu đài của Yên Bái. Trước khi bị chém, ông mỉm cười ngâm thơ bằng tiếng Pháp:\n\"Chết vì tổ quốc\nCái chết inh quang\nLòng ta sung sướng\nTrí ta nhẹ nhàng\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','06', 'Nguyễn Khắc Hiếu, hiệu là Tản Đà, sinh 1888 ở làng Khê Thượng, huyện Ba Vì, tinh Hà Tây, qua đời ngày 17-6-1939 tại Hà Nội.\nTrên vǎn đàn công khai của vǎn học Việt Nam hơn 30 nǎm đầu thế kỷ XX, Tản Đà nổi lên như một ngôi sao sáng, vừa độc đáo, vừa dồi dào nǎng lực sáng tạo. Là một cây bút phóng khoáng, xông xáo trên nhiều lĩnh vực, đi khắp đất nước, nhà thơ, nhà vǎn Tản Đà đã để lại nhiều tác phẩm bao gồm nhiều thể loại. Nhiều thế hệ người Việt Nam được hun đúc lòng yêu nước khi đọc bài thơ \"Thề non nước\" của ông. Những tác phẩm nổi tiếng của Tản Đà có: Khối tình con I, Khối tình con II, Giấc mộng con, Giấc mộng lớn, Tản Đà xuân sắc...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','06', 'Ngày 17 tháng 6 nǎm 1947, Chủ tịch Hồ Chí Minh ký sắc lệnh số 56/SL thành lập trường Ngoại ngữ, nhằm mục đích đào tạo cán bộ ngoại ngữ cho các ngành trong nước, đáp ứng yêu cầu kháng chiến. Đây cũng là trường Ngoại ngữ đầu tiên của ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','06', 'Tô Ngọc Vân là hoạ sĩ, bút danh Tô Tử, Ái Mỹ, sinh ngày 15-12-1906 tại Xuân Cầu, Vân Giang, Hưng Yên. \nÔng tốt nghiệp trường cao đẳng Mỹ thuật Đông Dương khoá II (1926-1931). Từ nǎm 1931 ông cộng tác với các báo \"Nhân loại\", \"Phong hoá\", \"Ngày nay\", \"Thanh Nghị\". Ông từmg dạy ở trường trung học Nông Pênh và dạy ở trường cao đẳng Mỹ thuật Đông Dương.\nTừ sau Cách mạng Tháng Tám, ông tích cực tham gia kháng chiến chống Pháp, phụ trách Trường Mỹ thuật Việt Bắc.\nÔng được xem là người có công đầu trong chất liệu sơn dầu ở Việt Nam. Tranh của ông từng triển lãm ở Sài Gòn, Hà Nội và một số nước như Ba Lan, Liên Xô, Hunggari, Rumani...\nÔng cũng là người có nhiều công sức đào tạo các thế hệ hoạ sĩ Việt Nam .\nNgày 17-6-1954, ông hy sinh ở Đa Khê, vùng gần sát chiến trường Điện Biên Phủ. Khi đó, ông mới 48 tuổi. Đánh giá về tài nǎng và đóng góp của ông đối với nền hội hoạ Việt Nam nói chung và hội hoạ cách mạng nói riêng, Nhà nước ta đã trao tặng ông giải thưởng quốc gia Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','06', 'Ngày 17-6-1966 Bộ Vǎn hoá phối hợp với tổng công đoàn Việt Nam, Trung ương Đoàn thanh niên Lao động Việt Nam, Trung ương Hội liên hiệp phụ nữ Việt Nam, Bộ Giáo dục, Tổng cục chính trị quân đội nhân dân Việt Nam phát động phong trào \"Cất cao tiếng hát sản xuất, chiến đấu chống Mỹ, cứu nước\".\nMục đích của phong trào nhằm góp phần tuyên truyền giáo dục đường lối chính sách của Đảng, giáo dục lòng cǎm thù đế quốc Mỹ và bè lũ tay sai, nâng cao lòng yêu nước và yêu chủ nghĩa xã hội, bồi dưỡng chủ nghĩa anh hùng cách mạng, truyền thống đấu tranh bất khuất của dân tộc ta. Phong trào này được phát động trong hai nǎm 1966-1967. Mặc dù bị đế quốc Mỹ dùng không quân, hải quân đánh phá ác liệt, nhân dân ta vừa chiến đấu vừa sản xuất, vừa đẩy mạnh các hoạt động vǎn nghệ, thực hiện \"Tiếng hát át tiếng bom\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','07', 'Trần Cảnh là vua mở nghiệp nhà Trần, miếu hiệu Thái Tông. Ông sinh ngày 17-7-1218 quê ở Tức Mạc, Thiên Trường, Mỹ Lộc, Nam Định. Ông lên ngôi Vua từ nǎm 1225 do vợ là Lý Chiêu Hoàng trao nghiệp nhà Lý cho ông dưới sự giúp đỡ, sắp đặt của người chú là Thái sư Trần Thủ Độ.\nTriều đại ông, việc chính trị, vǎn hoá, tôn giáo đều cực thịnh. Chính ông cũng chuyên tâm nghiên cứu Phật học và sùng mộ đạo Phật nhưng không xem thường cái học của các trường phái khác. Học thuật nước nhà do đó phát triển. Nǎm 1257, triều Trần đã đánh thắng quân Nguyên Mông xâm lược, để lại tiếng vang trong lịch sử dân tộc\nĐất nước hoà bình, nhân dân an cư lạc nghiệp, phần nào giúp cho thư thái trong tâm hồn. Do đó ông có đủ thì giờ lo tu niệm, trước tác nhiều kinh sách về Phật giáo.\nSau khi lên làm vua được 33 nǎm, Trần Cảnh mất vào ngày 4-5-1277.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','07', 'Nguyễn Vǎn Cẩm qua đời tại Tahiti ngày 17-7-1929.\nÔng sinh nǎm 1875, tại huyện Hưng Hà, tỉnh Thái Bình, nổi tiếng thông minh, học giỏi từ nhỏ, được vua Tự Đức gọi là Kỳ Đồng (nghĩa là cậu bé kỳ lạ). Các sĩ phu yêu nước đề cao Kỳ Đồng và nêu thành một thần tượng cứu nước để tập hợp lực lượng. Để đối phó lại, thực dân Pháp đưa Kỳ Đồng sang học ở Angêri để tách ra khỏi phong trào và mong biến Kỳ Đồng thành tay sai của chúng. Nhưng sau gần 10 nǎm học tập, có bằng tú tài Tây, trở về nước nǎm 21 tuổi, Kỳ Đồng từ chối làm công chức cho Pháp, chỉ nhận việc lập đồn điền ở Yên Thế liên hệ với phong trào của Đề Thám. Đồn điền của Kỳ Đồng đã trở thành cǎn cứ chống Pháp của nghĩa quân Yên Thế.\nTháng 9-1897, thực dân Pháp đã bắt Kỳ Đồng ở trong cǎn cứ. Đầu nǎm 1898, ông bị đầy đi biệt xứ.\nKỳ Đồng còn sáng tác thơ vǎn và viết một vở kịch ba hồi bằng tiếng Pháp nhan đề: \"Những mối tình của người hoạ sĩ già trên quần đảo Mackidơ\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','07', 'Ngày 17-7-1962, Hồ Chủ tịch đã kiểm tra đê ở Hà Nội, Hà Đông, Sơn Tây. Tại một điếm canh đê ở Hà Nội, Người đã nói với nhân dân: \"Trước kia tỉnh nào biết tỉnh ấy, bây giờ phải lo chung. Cán bộ và đồng bào các tỉnh gần nhau phải phối hợp chặt chẽ, tích cực giúp đỡ nhau chuẩn bị phòng và chống bão lụt...\"\nCùng ngày, Bác đã đến thăm công trường xây dựng đập tràn của đập Đáy và trạm bơm Đan Hoài. Tại đập Đáy, Bác đã chủ trì một cuộc họp bàn tại chỗ để đẩy mạnh thi công sửa chữa đập, ở trong một điếm canh đê')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','07', 'Nhà hoạt động tình báo Phạm Ngọc Thảo bị địch sát hại ngày 17-7-1965.\nÔng sinh nǎm 1922, quê ở tỉnh Bến Tre, tốt nghiệp tú tài ở Sài Gòn, theo học ngành công chính. Trong kháng chiến chống Pháp, ông tham gia quân đội, trở thành cán bộ chỉ huy ở chiến trường tây Nam Bộ.\nSau hiệp định Giơnevơ, ông ở lại miền Nam, dạy học ở Vĩnh Long thuộc địa phận giám mục Ngô Đình Thục (anh ruột Ngô Đình Diệm), quen thân với gia đình ông họ Ngô. Thục tin và phục Phạm Ngọc Thảo nên giới thiệu ông với Ngô Đình Diệm. Do đó ông làm việc trong quân đội Sài Gòn. Cuối nǎm 1963, ông Phạm Ngọc Thảo được thǎng đại tá, sau đó làm tuỳ viên vǎn hoá của Đại sứ Việt Nam cộng hoà tại Mỹ. Đầu nǎm 1965, ông bị gọi về nước vì chính quyền Sài Gòn phát hiện ông hoạt động tình báo.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','07', 'Thanh Tịnh là nhà vǎn, nhà thơ, tên thật là Trần Vǎn Ninh, sinh ngày 12-12-1911 quê ở xã Dương Nỗ, huyện Phú Vang, tỉnh Thừa Thiên. Ông được học chữ nho đến nǎm 11 tuổi. Nǎm 1933, ông là người dẫn khách du lịch. Sau đó ông vừa dạy học vừa viết vǎn.\nSáng tác đầu tay của Thanh Tịnh là truyện ngắn \"Cha làm trâu, con làm ngựa\". Dần dà người ta biết tên ông bởi một tâm hồn đa cảm, tinh tế; ông còn được biết đến như một cây bút chữ tình, đằm thắm nhân hậu, có tài. Các tác phẩm tiêu biểu là: \"Hậu chiến trường\", \"Quê mẹ\", \"Chị và em\", \"Ngậm ngải tìm trầm\", \"Sức mồ hôi\", \"Những giọt nước biển\", \"Đi giữa một mùa sen\", \"Thơ ca Thanh Tịnh...\"\nÔng là Ủy viên Ủy ban Trung ương Hội liên hiệp Vǎn học nghệ thuật Việt Nam, Ủy viên Ban Chấp hành Hội Nhà vǎn Việt Nam khoá II, Ủy viên cố vấn Hội Nhà vǎn Việt Nam.\nSau 42 nǎm cầm bút, Nhà vǎn lão thành - Đại tá Thanh Tịnh từ trần ngày 17-7-1988 tại Hà Nội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','07', 'Ngày 17-7-1871, Viện Hàn lâm khoa học Pháp đã đón nhận phát minh đi-na-mô (máy phát điện một chiều) của Gramme, người Bỉ.\nĐi-na-mô của Gramme được các nhà kỹ thuật Đức hoàn thiện vào nǎm 1873.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','07', 'Nhiếp Nhĩ sinh nǎm 1911 tại Vân Nam, Trung Quốc, mất ngày 17-7-1935 tại Nhật Bản. Ông viết ôpêra \"Bão tố trên sông Dương Tử\" và nhiều bài hát quần chúng, trong đó có bài \"Hành khúc nghĩa dũng quân\", viết nǎm 1934 và nǎm 1949 trở thành Quốc ca của nước Cộng hoà nhân dân Trung Hoa.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','07', 'Từ ngày 17-7 đến ngày 2-8-1945 đã diễn ra Hội nghị cao cấp ba cường quốc Liên Xô, Mỹ, Anh ở Pốtxđam (nước Đức). Hội nghị đã ký kết hiệp ước về việc giải quyết vấn đề Đức, mà nội dung cơ bản là: Quy định các nguyên tắc và biện pháp nhằm dân chủ hoá và hoà bình hoá nước Đức, như tiêu diệt tận gốc chủ nghĩa quân phiệt và chủ nghĩa phát xít, xét xử tội phạm chiến tranh, thực hiện các quyền tự do dân chủ đối với nhân dân Đức.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','08', 'Ngày 17-8-1966, dân quân quận Phù Yên tỉnh Nghĩa Lộ bắn 3 phát súng trường xuyên thủng chiếc ghế ngồi của tên giặc lái máy bay Mỹ, góp phần xứng đáng cùng bộ đội Pháp cao xạ bắn rơi một chiếc F4 Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','08', 'Thực hiện nghị quyết của Bộ chính trị Trung ương Đảng, từ đêm 17-8 đến 30-9-1968, quân và dân ta ở miền Nam lại mở đợt tiến công và nổi dậy lần thứ ba trong nǎm 1968. Ta đã đánh vào 27 thành phố, hơn 100 thị trấn, quận lỵ, chi khu quân sự, 17 sân bay, 3 kho và 6 Bộ tư lệnh sư đoàn của địch.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','08', 'Giôn Giacốp Bêzêliút sinh ngày 20-8-1779 tại Thụy Điển. Ông học đại học Y khoa. Luận án tốt ghiệp về dùng nước khoáng chữa bệnh đã khiến ông nghiêng sang hoá học.\nBêzêliút đã đưa ra những quan điểm để thiết lập lý thuyết điện hoá học. Chính ông đã chia các nguyên tố làm hai loại: Loại dương điện là kim loại và âm điện lá á kim. Những nghiên cứu của ông đóng vai trò lớn trong việc hình thành \"Thuyết nguyên tử trong hoá học\".\nLà một trong những nhà bác học lớn nhất của thời đại, bằng nhiều phát minh, Bêzêliút làm giàu cho nhiều ngành khác nhau của hoá học. Những khái niệm quan trọng gắn liền với tên tuổi như: Hoá học, hữu cơ, xúc tác, tính thù hình, hiện tượng đồng phân ... Ký hiệu các nguyên tố mà chúng ta dùng ngày nay là do ông đề nghị.\nBêzêliút mất ngày 17-8-1848 ở cương vị Chủ tịch Viện Hàn lâm khoa học Thụy Điển.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','08', 'Bútlêrốp sinh ngày 15-9-1828, tại một thị trấn nhỏ nước Nga. Thuở nhỏ, ông đã ham mê quan sát cây cỏ, sưu tập côn trùng và các thực nghiệm về hiện tượng vật lý và hoá học.\nNǎm 16 tuổi ông theo học hoá học ở trường Đại học Kazan. Nǎm 26 tuổi ông đã nhận được học vị Tiến sĩ hoá học. Nǎm 1861 ông đưa ra những luận điểm của lý thuyết cấu tạo hoá học của các hợp chất hữu cơ.\nBútlêrốp có vai trò quan trọng trong việc lập nên trường phái đầu tiên các nhà hoá học hữu cơ ở Nga. Ông mất ngày 17-8-1886 khi đang khảo sát về nông nghiệp ở vùng Kazan.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','09', 'Trần Thuyên, vua thứ 4 nhà Trần, miếu hiệu Anh Tông, sinh ngày 17-9-1276, quê làng Tức Mặc, huyện Mỹ Lộc, tỉnh Nam Định.\nÔng lên ngôi lúc 17 tuổi. Khi làm vua ông đặc biệt ưu đãi các cựu thần có công trong cuộc đánh đuổi quân Nguyên và trân trọng sử dụng các bậc nhân tài.\nÔng ở ngôi 21 năm (1293-1314) theo gương các vua trước mà truyền ngôi cho con, rồi về chăm việc tu Phật, sáng tác thơ ca.\nNgày 16-9-1320, ông mất, hưởng dương 44 tuổi. Có soạn tập \"Thuỷ vân tuỳ bút ngoại tập\". Nay chỉ còn 12 bài thơ ghi chép trong \"Việt âm thi tập\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','09', 'Nhà thơ Nguyễn Huy Hổ, tự Cách Như, hiệu Liên Pha, sinh ngày 17-9-1783. Ông là con của Nguyễn Huy Tự (tác giả truyện thơ \"Hoa Tiên\") và cháu gọi Nguyễn Du bằng chú.\nTừ năm 20 tuổi, ông chán cảnh loạn lạc cuối đời Hậu Lê nên ở ẩn đọc sách, cày ruộng. Đến năm 39 tuổi, vua Minh Mạng triệu dụng ông, bổ làm Linh Đài lang (một chức thuộc toà Khâm Thiên giám chuyên về tiên văn, lịch số). Từ đây ông cũng thường được nhà vua và quan trong triều vời đến làm thuốc chữa bệnh, nổi tiếng danh y, danh sĩ. Ông còn sáng tác văn học. Tác phẩm nổi tiếng của ông là tập truyện thơ \"Mai đình mộng ký\".\nNguyễn Huy Hổ mất ngày 8-10-1841, thọ 58 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','09', 'Ngày 17-9-1954, Hội đồng Chính phủ ra quyết định thành lập Ủy ban Quân chính thành phố Hà Nội do Thiếu tướng Vương Thừa Vũ, Tư lệnh sư đoàn Quân tiên phong làm Chủ tịch và bác sĩ Trần Duy Hưng làm Phó chủ tịch.\nỦy ban Quân chính có nhiệm vụ tiếp thu và quản lý thành phố. Hội đồng Chính phủ đã công bố chính sách đối với thành thị mới được giải phóng, đối với các tôn giáo, cán bộ và nhân viên khi vào công tác ở thành phố mới được giải phóng. Bộ Tổng tư lệnh quân đội đã ra lệnh cho các đơn vị đang tiếp quản Hà Nội phải triệt để chấp hành các chính sách và kỷ luật do Chính phủ đề ra, phải luôn luôn nâng cao cảnh giác, sẵn sàng đập tan mọi âm mưu khiêu khích của bọn phá hoại. Sư đoàn Quân tiên phong được Trung ương Đảng và Chính phủ giao nhiệm vụ tiếp quản thủ đô Hà Nội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','09', 'Nhà toán học Riman sinh ngày 17-9-1826 trong một làng nhỏ ở Đức và qua đời năm 1866, lúc ông mới 40 tuổi.\nNăng khiếu toán học của Riman bộc lộ từ lúc lên 6 tuổi. Cậu giải được tất cả các bài toán do cha cậu ra và còn tự đặt ra những bài toán khó để đố các anh em cậu.\nNăm 19 tuổi, Riman học đại học ở Gớttinhghen rồi chuyển sang học ở Béclin để được làm quen với những vấn đề mới mẻ của toán học.\nNăm 25 tuổi, Riman bảo vệ luận án tiến sĩ. Riman đã cùng với Côsi xây dựng được một lý thuyết chính xác về tích phân. Năm 34 tuổi, Riman được mời tham gia Viện Hàn lâm khoa học Pháp.\nTên của Riman được đặt cho một miệng núi lửa trên mặt trăng')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','10', 'Lý Bôn tức Lý Bí, quê ở Long Hưng, tỉnh Thái Bình, sinh ngày 17-10-503. Ông là người tài kiêm văn võ, được tôn là thủ lĩnh địa phương.\nTháng giêng năm 542, Lý Bí lãnh đạo nhân dân khởi binh tấn công quân Lương đô hộ nước ta . Chưa đầy ba tháng, Lý Bí đã chiếm được hầu hết các quận huyện và thành Long Biên.\nTháng 2 năm 544, Lý Bí xưng Hoàng đế, đặt tên nước là Vạn Xuân, đóng đô ở Long Biên.\nNăm 545, nhà Lương lại đưa quân sang đánh, ông tổ chức xây thành, đắp lũy để chống cự, nhưng sức giặc mạnh, ông lui quân về Tam Nông (nay thuộc tỉnh Phú Thọ) và qua đời tại đây vào năm 548.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','10', 'Đào Duy Từ sinh năm 1572 quê ở làng Hoa Trai, Ngọc Sơn, Tĩnh Gia, Thanh Hoá.\nÔng thông kinh sử, tinh thông lý số và binh thư đồ trận được chúa Sãi Nguyễn Phúc Nguyên trọng dụng, phong làm Nội tán, Năm 1630, ông đề xuất việc đắp luỹ Trường Dục ở huyện Phong Lộc, Quảng Bình. Năm sau ông lại đắp thêm một luỹ nữa từ cửa bể Nhật Lệ đến núi Đâu Mâu, thị xã Đồng Hới, Quảng Bình (tục gọi là Luỹ Thầy). Ông viết bộ binh thư \"Hổ trướng khu cơ\" và hai khúc ngân \"Ngoạ long cương vãn\" dài 136 câu lục bát được phổ biến trong lịch sử văn học.\nÔng mất ngày 17-10-1634, thọ 62 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','10', 'Cao Bá Quát sinh ngày 17-10-1808 tại làng Phú Thị, huyện Gia Lâm, Hà Nội.\nLà người có tài nhưng bị sự ghen ghét của quan trưởng nên ông chỉ đỗ cử nhân. Bất bình với triều đình về nhiều mặt, năm 1854, ông tham gia phong trào lãnh đạo nông dân khởi nghĩa do Lê Huy Cự làm thủ lĩnh, nổi dậy ở Mỹ Lương (vùng Mỹ Đức, Chương Mỹ, Tỉnh Hà Tây hiện nay). Cuộc khởi nghĩa thất bại, Cao Bá Quát hy sinh tháng giêng năm 1855.\nCao Bá Quát là một nhà thơ lớn. Ông để lại 1353 bài thơ chữ Hán. Thơ của ông thể hiện tình cảm thiết tha gắn bó với quê hương, với người thân, sự đồng cảm với những người nghèo khổ, niềm tự hào với lịch sử dân tộc và thái độ phê phán mạnh mẽ đối với triều đình nhà Nguyễn.\nNgười đương thời đã suy tôn ông là \"Thánh Quát\". Ông nổi tiếng về chữ đẹp, còn để lại bút tích đến ngày nay.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','10', 'Từ ngày 17 đến ngày 23-10-1986, tại Cung văn hoá Lao động Việt - Xô đã tiến hành Đại hội Đảng bộ Hà Nội lần thứ X. Có 709 đại biểu thay mặt cho hơn 150 nghìn Đảng viên về dự.\nCác đại biểu đã bầu ra Ban Chấp hành Đảng bộ Hà Nội khoá X gồm 51 uỷ viên chính thức, 14 uỷ viên dự khuyết, do đồng chí Nguyễn Thanh Bình làm Bí thư Thành uỷ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','10', 'Phrêđêric Sôpanh là nhạc sĩ pianô, nhà soạn nhạc nổi tiếng Ba Lan, sinh năm 1819 và từ trần ngày 17-10-1849 tại Pari.\nSôpanh học đàn pianô từ nhỏ. 19 tuổi, ông đã bắt đầu sáng tác âm nhạc. 20 tuổi ông rời Ba Lan sang sống ở nước Pháp làm thầy dạy nhạc.\nNhững tác phẩm chính của Sôpanh có nhiều loại: cho dàn nhạc, cho nhạc thính phòng và chủ yếu cho đàn pianô. Những bản nhạc của ông có tính lãng mạn dịu dàng, buồn man mác. Ông đã kết hợp truyền thống cổ điển với dân ca Ba Lan. Một số bản nhạc ông đã nói lên sự phẫn nộ, căm uất và thương nhớ tổ quốc Ba Lan bị nô dịch.\nSôpanh là người cách tân phương pháp biểu diễn pianô trong lĩnh vực hoà âm và phối khí.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','11', 'Nghe tin mấy tỉnh miền Nam bị bão lụt, gây thiệt hại về tài sản và tính mạng của nhân dân, ngày 17-11-1964, Hồ Chủ tịch đã gửi thư cho đồng bào các tỉnh ở đó. Trong thư Người viết:\n\"Máu chảy ruột mềm, được tin tức đó, tôi và toàn thể đồng bào miền Bắc rất là đau xót, như muối sát vào lòng.\nHội Chữ thập đỏ của nước Việt Nam dân chủ cộng hoà chuẩn bị gửi tặng đồng bào bị nạn một số gạo, vải và thuốc, gọi là no đói có nhau, sẻ cơm nhường áo\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','11', 'Giu Vanđen Vônđen (Joost Vanden Vondel) là nhà thơ và nhà viết kịch Hà Lan. Ông sinh ngày 17-11-1587.\nTác phẩm thơ nổi tiếng của ông là \"Bàn chải cọ\" (1630). Với ngòi bút trào phúng, tập thơ đả kích xã hội sâu sắc. Ông được đánh giá là \"Thi bá của Hà Lan\". Ông là nhà vǎn mang tư tưởng nhân vǎn tư sản tiến bộ, có nhiều đóng góp với nghệ thuật sân khấu Hà Lan và châu Âu thế kỷ XVII, XVIII.\nÔng mất ngày 30-5-1679.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','11', 'Nicôla Áppe sinh ngày 17-11-1749 tại Salôngsun Macnơ nước Pháp.\nÔng đã tiến hành nhiều thí nghiệm về vai trò của nhiệt để bảo quản thức ǎn. Nǎm 1790, ông khám phá: nếu đun thức ǎn tới 100 độ C trong những nồi hoặc thùng đậy chặt kín trong một thời gian nào đó, thì có thể bảo quản chúng được lâu.\nTừ đó Nicôla Áppe là người đầu tiên trên thế giới đã chế tạo đồ hộp bảo quản thức ǎn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','11', 'Kênh đào Xuyê (Suez) xuyên qua eo biển Xuyê thuộc lãnh thổ Ai Cập, nối giữa Địa Trung Hải và Hồng Hải, được bắt đầu khởi công ngày 25-4-1859 và hoàn thành ngày 17-11-1869. Kênh là điểm giao thông trọng yếu nhất giữa Đại Tây Dương và Thái Bình Dương.\nKênh dài 162,5 km, rộng 135 mét, sâu 13 mét. Tàu thuyền qua kênh chỉ mất 14 tiếng đồng hồ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','11', 'Ngày 17-11-1939 phát xít Hít le đã đóng cửa các trường đại học ở Tiệp Khắc và bắt giam nhiều sinh viên yêu nước vào các trại tập trung, tra tấn cực kỳ dã man. Nhân dân Tiệp Khắc và thế giới cǎm phẫn đấu tranh phản đối. Để ghi nhớ cuộc đấu tranh bất khuất của sinh viên Tiệp Khắc, lên án tội ác của chủ nghĩa phát xít, và để động viên lực lượng hùng hậu của sinh viên thế giới đấu tranh cho hoà bình, tự do và tiến bộ, Hội đồng sinh viên quốc tế gồm đại biểu sinh viên chống phát xít họp ở Luân Đôn (Anh) nǎm 1941 đã lấy ngày 17-11 làm ngày \"Quốc tế sinh viên\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','12', 'Thiền sư Mãn Giác, tên thật là Lý Trường, sinh nǎm 1052, viên tịch ngày 17-12-1096.\nThông hiểu cả Nho giáo và Phật giáo nên lúc 20 tuổi ông đã được chọn vào cung dạy Hoàng tử. Ít lâu sau, ông đi tu được sư Quảng Trí truyền tâm ấn, nối truyền thế hệ thứ 8 dòng thiền Quan Bích, có nhiều học trò theo học.\nVua Lý Nhân Tông và Hoàng hậu rất trọng đãi ông, cho xây cất một ngôi chùa để ông trụ trì, thường gọi ông là Trưởng lão, hay mời đến hỏi han giáo lý và bàn việc nước.\nThiền sư Mãn Giác còn để lại một bài kệ nổi tiếng có tên là \"Cáo tật thị chúng\" được dịch ra quốc âm như sau:\n\"Xuân ruổi trǎm hoa rụng\nXuân tới trǎm hoa cười\nTrước mặt, việc đi mãi\nTrên đầu già đến rồi!\nĐừng tưởng xuân tàn, hoa rụng hết\nĐêm qua sân trước một nhành mai\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','12', 'Nhà yêu nước Trần Xuân Soạn, sinh nǎm 1849, quê ở Thanh Hoá.\nNǎm 1885, kinh thành Huế thất thủ, ông cùng Đại thần Tôn Thất Thuyết đưa vua Hàm Nghi ra Hà Tĩnh, rồi về Thanh Hoá chỉ đạo kháng chiến.\nKhi nghĩa quân suy yếu, ông sang Trung Quốc, tìm gặp Tôn Thất Thuyết ở Long Châu nhằm tổ chức các toán quân về hoạt động ở biên giới.\nÔng mất ở Long Châu ngày 17-12-1923.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','12', 'Từ 7h đến 12h ngày 17-12-1946, máy bay thám thính Pháp bay dò xét bầu trời thành phố Hà Nội.\nVào lúc 10h, chúng đem xe ủi đất và cần trục tới phá các ụ chiến đấu của anh em tự vệ phố Lò Đúc. Cùng lúc đó, lính Pháp đến phố Hàng Bún phá các ụ chiến đấu của anh em tự vệ và bắn xả vào đồng bào ta.\nĐến 15h, lính pháp ở trong thành nã đại bác vào phố Hàng Bún, Yên Ninh làm sập những dẫy nhà, hàng trǎm người bị giết hại.\nĐây là hành động khiêu khích man rợ nhất của quân Pháp ở Hà Nội trong đợt khiêu khích của chúng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '17','12', 'Thiếu tướng, giáo sư, bác sĩ Đỗ Xuân Hợp sinh nǎm 1906 tại Hà Nội và qua đời ngày 17-12-1985.\nNǎm 26 tuổi ông đã là trợ lý giảng dạy bộ môn giải phẫu trường Đại học Y khoa Hà Nội.\nTrong cuộc kháng chiến chống Pháp, bác sĩ Đỗ Xuân Hợp đã có nhiều đóng góp cho ngành quân y, từ cấp cứu chiến thương, điều trị thương binh, phụ trách bệnh viện đến tổ chức đào tạo cán bộ.\nÔng đã viết nhiều sách và có nhiều công trình nghiên cứu về y học. Nổi bật nhất là bộ sách giáo khoa về giải phẫu học (gồm 4 tập) có giá trị lớn cả về thực tiễn mà ông đã biên soạn trong gần 20 nǎm.\nGiáo sư Đỗ Xuân Hợp được tặng danh hiệu Anh hùng lực lượng vũ trang, và được Nhà nước truy tặng Giải thưởng Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','12', 'Hǎmphri Đâyvi (Humphry Davy) sinh ngày 17-12-1778 tại nước Anh. Ông bắt đầu sự nghiệp nghiên cứu khoa học từ nǎm 20 tuổi.\nTừ nǎm 1800 đến 1806 ông nêu lên được nguyên lý cơ bản của một ngành khoa học mới là điện khí hoá học mà ông là người sáng lập. Nǎm 1808 ông tìm ra phương pháp điện phân muối của các kim loại. Sau đó nhờ phương pháp điện phân, ông chứng minh được bản chất nguyên tố Clo.\nÝ kiến của ông về vai trò quan trọng của muối khoáng trong dinh dưỡng của thực vật trở thành những ý kiến cơ bản trong nông hoá học. Ông còn là tác giả của chiếc đèn khí an toàn dùng cho thợ mỏ mang tên ông.\nÔng mất ngày 29-5-1829.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','12', 'Ngày 17-12-1965, Hội nghị đoàn kết đầu tiên giữa châu Á, châu Phi và châu Mỹ Latinh được tổ chức tại La Habana.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','12', '17-12-1989, lần đầu tiên sau 29 nǎm, người dân Brazil lại được đi bầu cử và Fernando Collor de Mello đã trúng cử Tổng thống nước này.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '17','12', 'Ngày này năm 1991, Boris Yeltsin và Mikhail Gorbachov đồng ý giải tán Liên bang Xô Viết và tuyên bố thành lập Khối Thịnh vượng chung vào ngày đầu nǎm mới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','01', 'Ngày 18-1-1077, quân Tống do Quách Quỳ và Triệu Tiết chỉ huy tiến đến sông Như Nguyệt (tức sông Cầu ở Bắc Ninh ngày nay). Tại đây, quân dân nhà Lý do Lý Thường Kiệt chỉ huy đã tiêu diệt phần lớn quân tác chiến của Tống (8 vạn tên) và một phần lớn quân tiếp lương, phục vụ (7 vạn tên), buộc chúng phải rút lực lượng còn lại về nước, thừa nhận nền độc lập của nước ta. Chiến thắng Như Nguyệt kết thúc vào đầu tháng 3 nǎm 1077. Ở đây lần đầu tiên đã xuất hiện một phương thức kết thúc chiến tranh với giặc phương Bắc: trong thế thắng, ta vẫn chủ động giảng hoà, mở đường cho giặc rút về nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','01', 'Đầu nǎm 1950, Ban chỉ huy Mặt trận Hà Nội giao nhiệm vụ đánh sân bay Bạch Mai cho tiểu đoàn 108.\nSân bay Bạch Mai nằm trong thành phố Hà Nội dài gần 2000 mét, rộng 30 mét, được bao bọc bằng những hàng rào dây thép gai, ao hồ và hào sâu. Lực lượng bảo vệ sân bay gồm một đại đội lính Âu - Phi và một trung đội lính dù. Chúng quanh sân bay có nhiều đèn pha và lô cốt. Ngày cũng như đêm, xe bọc thép, ôtô và binh lính thay nhau tuần tiễu, canh gác nghiêm ngặt.\nRạng sáng ngày 18-1-1950, bộ đội ta đã bí mật vào sân bay đặt mìn, phá huỷ và đốt cháy 25 máy bay, 60 vạn lít xǎng dầu và 32 tấn vũ khí của Pháp ở sân bay Bạch Mai. Chính phủ đã tặng thưởng Huân chương Quân công hạng ba cho tiểu đoàn 108.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','01', 'Ngày 18-1-1964 khánh thành đợt I Nhà máy điện Uông Bí (ở tỉnh Quảng Ninh).\nĐây là một nhà máy điện có công suất vào loại lớn nhất miền Bắc lúc đó, do Liên xô giúp ta xây dựng. Nhà máy này bắt đầu được xây dựng từ ngày 19-5-1961. Đợt I, nhà máy có công suất 24 nghìn kilôoét, với các thiết bị điều khiển tự động, đốt lò bằng than phun.\nCùng với Nhà máy điện Uông Bí, hệ thống đường dây điện cao thế 110 Kilôvôn Uông Bí - Hải Phòng, các trạm biến thế lớn An Lạc, Đông Anh cũng đã xây dựng xong.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','01', 'Ngày 18-1-1969, đã diễn ra phiên họp đầu tiên của Hội nghị Pari về Việt Nam, tại phòng họp trung tâm của hội nghị quốc tế Pari. Bốn đoàn đại biểu gồm Việt Nam Dân chủ cộng hoà, Mặt trận giải phóng dân tộc miền nam Việt Nam, Mỹ và chính quyền Sài Gòn đã bàn về việc chuẩn bị cho phiên họp toàn thể lần thứ nhất.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','02', 'Từ ngày 18 đến 21-2-1973 Quốc hội khoá 4 họp kỳ thứ ba biểu dương thắng lợi vĩ đại của dân tộc ta. Sau lời khai mạc, Quốc hội đã nghe báo cáo về thắng lợi của cuộc kháng chiến chống Mỹ cứu nước và việc ký kết Hiệp định chấm dứt chiến tranh lập lại hoà bình ở Việt Nam tại Pari. Sau nhiều tham luận các đại biểu đã thông qua nghị quyết về việc ký kết Hiệp định chấm dứt chiến thanh lập lại hoà bình ở Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','02', 'Ngày 18-2-1979, Hiệp ước hoà bình hữu nghị và hợp tác giữa Việt Nam và Campuchia cùng các Hiệp định quan trọng khác đã được ký kết tại thủ đô Phnôm Pênh. Trong hiệp ước có ghi, hai bên cam kết ủng hộ và giúp đỡ lẫn nhau về mọi mặt và bằng mọi hình thức cần thiết nhằm tǎng cường khả nǎng bảo vệ độc lập chủ quyền thống nhất, toàn vẹn lãnh thổ và công cuộc lãnh đạo hoà bình của nhân dân mỗi nước, chống mọi âm mưu và hành động phá hoại của lực lượng đế quốc và phản động quốc tế.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '18','02', 'Michelangelo, nhà điêu khắc, nhà thơ, nhà kiến trúc nổi tiếng, sinh ngày 6-3-1475. Các tác phẩm nổi tiếng của ông có tượng cẩm thạch \"Tình mẫu tử\", tượng \"Đức mẹ Viege và chúa hài đồng\", pho tượng đá hoa cương \"David\" cao 5,5 mét, pho tượng \"Những người nô lệ\", đặc biệt là bức bích hoạ đồ sộ tuyệt mỹ \"Ngày phán xét cuối cùng\"... Ông là kiến trúc sư của nhiều nhà thờ thánh, các công trình xây dựng cầu, thư viện, lǎng mộ. Sáng tác thơ ca của ông được liệt vào hàng những tập thơ hay nhất Italia thời Phục Hưng.\nVề nghệ thuật, ông luôn hướng tới tiêu chuẩn cái đẹp, đưa vào tác phẩm của mình tính nhân vǎn nhuần nhụy của nghệ thuật cổ Hy Lạp. Ông qua đời ngày 18-2-1564.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '18','02', 'Alêcxanđrô Vônta, sinh ngày 18-2-1745, mất nǎm 1827.\nÔng là nhà vật lý học người Italia đã phát minh ra điện phân, điện nghiệm, tụ điện, đồng hồ đo điện thế, pin điện. Tên ông được đặt cho đơn vị điện thế: Volt, ký hiệu V.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','03', 'Ngày 18-3-1962 đã khánh thành khu công nghiệp ở thành phố Việt Trì (tỉnh Phú Thọ).\nKhu công nghiệp này gồm có nhà máy hoá chất, nhà máy giấy, nhà máy đường và nhà máy điện, đánh dấu bước phát triển của công cuộc xây dựng chủ nghĩa xã hội ở miền Bắc trong kế hoạch 5 nǎm lần thứ nhất.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','03', 'Ngày 18-3-1965, phong trào phụ nữ \"Ba đảm đang\" do Bác Hồ đặt tên được Trung ương Hội liên hiệp phụ nữ Việt Nam phát động.\nLúc đó đang diễn ra cuộc kháng chiến chống Mỹ cứu nước, nhiều nam giới đi chiến đấu, ở hậu phương lực lượng chủ yếu là phụ nữ. Nội dung phong trào phụ nữ \"Ba đảm đang\" gồm có:\n1. Phụ nữ đảm đang sản xuất và công tác thay cho chồng, con đi chiến đấu.\n2. Phụ nữ đảm đang việc gia đình cho chồng, con, anh em tòng quân và phục vụ lâu dài trong quân đội.\n3. Phụ nữ đảm đang phục vụ chiến đấu, tham gia dân quân, tự vệ, luyện tập quân sự để sẵn sàng chiến đấu, phục vụ bộ đội, công an, dân quân tự vệ khi chiến đấu.\nPhong trào \"Ba đảm đang\" đã lôi cuốn hàng triệu phụ nữ nước ta vào cuộc kháng chiến chống Mỹ cứu nước và xây dựng chủ nghĩa xã hội ở miền Bắc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','03', 'Ngày 18-3-1975, Bộ Chính trị Trung ương Đảng nhận định: \"Thắng lợi to lớn có ý nghĩa chiến lược của ta đánh dấu một bước rất mới trong cục diện chung, một bước suy sụp mới của Mỹ - Ngụy. Địch đang thực hiện co cụm chiến lược, có thể co cụm ở Đà Nẵng và Cam Ranh...\"\nBộ Chính trị hạ quyết tâm hoàn thành kế hoạch giải phóng miền Nam ngay trong nǎm 1975, nhiệm vụ trước mắt là nhanh chóng tiêu diệt quân đoàn 1 của địch, không cho chúng rút về Sài Gòn, giải phóng Huế, Đà Nẵng và các tỉnh Trung Bộ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','03', 'Ngày 18-3-1975, sư đoàn 320 của ta đuổi kịp đội hình rút chạy của địch trên đường số 7 và tổ chức tiến công thị xã Cheo Reo. Sau 6 giờ chiến đấu liên tục và quyết liệt, ta giải phóng thị xã Cheo Reo. Đây là huyện lỵ của huyện A Dun Pa, tỉnh Gia Lai. Bộ phận còn lại trong tập đoàn rút chạy của địch đứng trước nguy cơ bị tiêu diệt hoàn toàn. Chúng buộc phải bỏ lại toàn bộ vũ khí nặng, xe cộ và khí tài, mở các đường tắt chạy bộ qua rừng, cố thoát khỏi khu vực đường số 7. Con đường \"tuỳ nghi di tản\" này trở thành \"đường chết\" của quân ngụy. Cùng ngày 18-3, ta còn giải phóng thị xã Phú Bổn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '18','03', 'Ngày 18-3-1848, tại Béclin đã nổ ra cuộc cách mạng tư sản, dẫn tới việc thành lập một chính phủ gồm những người tư sản và công chức tự do vào ngày 20-3-1848. Chính phủ này bắt buộc vua Phổ phải công bố hiến pháp.\nTháng 4-1848, Mác và Ǎngghen ở Đức đã phát động đấu tranh, đề ra nhiệm vụ cấp thiết nhất là: đánh đổ chính phủ quân chủ, tiêu diệt tất cả tàn tích của chế độ nông nô, thành lập một nước Đức cộng hoà - dân chủ - thống nhất.\nMác và Ǎngghen cho rằng: Tiến hành cách mạng đến cùng là điều kiện cần thiết nhất để chuẩn bị cho cuộc đấu tranh giành chủ nghĩa xã hội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '18','03', 'Giữa thế kỷ XIX nền công nghiệp Pháp phát triển chưa từng có nhưng cũng mở đầu cho sự bóc lột của giai cấp tư sản đối với nhân dân lao động. Trong khi đó Phổ bao vây Pari. Trước tình hình này nhân dân Pari cǎm phẫn nổi dậy lật đổ nền đế chế thứ hai và đòi lập chế độ cộng hoà.\nSáng 18-3-1871 quân đội chính phủ Pháp tiến công tìm cách cướp vũ khí của quân đội vệ quốc gồm những người lao động. Vì vậy, dân chúng Pari nhất tề nổi dậy phá tan âm mưu khiến chúng hoảng sợ bỏ chạy về Véc - xay. Quân đội vệ quốc chiếm lĩnh các công sở. Lá cờ đỏ phấp phới tung bay trên nóc toà thị chính Pari. Ngày 18-3 là ngày thắng lợi của nền cộng hoà của những người lao động. Uỷ ban Trung ương quân đội vệ quốc Pari trở thành chính phủ thực sự của công xã Pari được thành lập.\nSự ra đời của công xã Pari đánh dấu một giai đoạn mới trong cuộc đấu tranh của giai cấp công nhân chống lại giai cấp tư sản Pháp. Những điều mà Công xã Pari đặt nền móng là mong mỏi của hàng triệu người trên thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '18','03', 'Ngày 18-3-1970, tại Cǎmpuchia đã nổ ra cuộc đảo chính do lực lượng cánh hữu Lonnon Xirich Matắc tiến hành, lật đổ Quốc trưởng Nôrôđôm Xihanúc trong lúc ông đi chữa bệnh ở nước Pháp.\nHọ tuyên bố khủng hoảng chính trị nghiêm trọng ở Cǎmpuchia và vội vã triệu tập cuộc họp giữa Quốc hội và Hội đồng Vương quốc. Hội nghị ra quyết định phế truất ông Xihanúc ra khỏi chức vụ Quốc trưởng, thành lập chế độ \"Cộng hoà Khơme\" và cử Cheng Heng làm Quốc trưởng - Họ tuyên bố xoá bỏ các quyền tự do dân chủ, theo Hiến pháp trước đây, bắt giam những người chống đối. Cuộc đảo chính này là sự vận dụng \"học thuyết Nichxơn\" của Cǎmpuchia và đưa đất nước này vào quỹ đạo cuộc chiến tranh xâm lược thực dân kiểu mới của Mỹ trên toàn bán đảo Đông Dương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','04', 'Để chuẩn bị cho cuộc đàm phán chính thức, phái đoàn chính phủ Việt Nam và Pháp đã họp hội nghị trù bị ở Đà Lạt ngày 18-4-1946.\nPhái đoàn Chính phủ Việt Nam dân chủ cộng hoà do đồng chí Võ Nguyên Giáp dẫn đầu. Trong hội nghị, phái đoàn ta đã tích cực đấu tranh vạch trần âm mưu của thực dân Pháp định xoá bỏ hiệp định sơ bộ 6-3 để lập lại chế độ thuộc địa ở Việt Nam. Phái đoàn ta kiên quyết giữ vững lập trường \"Hoà bình trong tự do bình đẳng phù hợp với hiệp định sơ bộ 6-3 chứ không phải hoà bình trong nô lệ\". Sau gần một tháng đấu tranh, hội nghị bế tắc vì thái độ ngoan cố của phái đoàn Pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','04', 'Ngày 18-4-1970, chính phủ Việt Nam Dân chủ cộng hoà ra tuyên bố nghiêm khắc cảnh báo tập đoàn phản động Lonnon Xirich Matắc đã gây ra những tội ác dã man đối với Việt kiều ở Cǎmpuchia. Bản tuyên bố viết \"Chính phủ Việt Nam Dân chủ cộng hoà cực lực lên án và tố cáo trước dư luận thế giới âm mưu độc ác và những tội ác cực kỳ man rợ của tập đoàn Lonno Xirich Matắc đối với kiều dân Việt Nam ở Cǎmpuchia\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','04', 'Từ thành phố Đà Lạt, trung đoàn 812 của ta nhanh chóng cơ động xuống Bình Thuận. Trung đoàn này cùng tiểu đoàn 200C và lực lượng địa phương tiêu diệt chi khu quân sự Thiện Giáo (Ma Lâm) là một vị trí quan trọng trên tuyến phòng thủ Tây Bắc thị xã Phan Rang. Du kích và nhân dân nổi dậy diệt địch, phá tan hệ thống đồn bốt và các ấp Tân Hưng, An Phú, Bình Lâm. Quân địch ở tiểu khu Bình Thuận điên cuồng chống trả. Chúng cho pháo binh, máy bay liên tục mém bom đánh phá dọc theo tuyến vành đai đông - bắc Phan Thiết, tập trung 5 tiểu đoàn bảo an liên tục phản kích. Quân đoàn 2 của ta tiến đến Xara, phối hợp với lực lượng vũ trang địa phương tiến công thị xã Phan Thiết. Từ các hướng, các mũi, quân ta tiến công vào các mục tiêu then chốt, các cơ quan chỉ huy đầu não của địch. Ngày 18-4-1975, thị xã Phan Thiết và tỉnh Bình Thuận được giải phóng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','04', 'Ngày 18-4-1977, Hội đồng chính phủ ban hành điều lệ về đầu tư của nước ngoài ở nước Cộng hoà xã hội chủ nghĩa Việt Nam.\nBản điều lệ gồm 27 điều quy định về nguyên tắc chung, hình thức đầu tư, quyền lợi và nghĩa vụ của bên nước ngoài, thủ tục xin đầu tư vào Việt Nam, giải thể và thanh lý các xí nghiệp, công ty có vốn đầu tư của nước ngoài, việc xử lý các vụ tranh chấp giữa các xí nghiệp, công ty có vốn đầu tư của nước ngoài và các điều khoản thi hành Điều lệ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '18','04', 'Ngay sau khi Cách mạng tháng Mười thành công, liên quân 14 nước đế quốc tiến hành can thiệp hòng bóp chết cách mạng non trẻ. Hạm đội của nhiều nước đế quốc đã tiến vào và bao vây nước Nga. Ngày 18-4-1919, thuỷ thủ và binh lính Pháp trên chiến hạm Phơrǎngxơ tuyên bố chống lệnh chiến đấu của bọn chỉ huy, đưa ra khẩu hiệu \"Không chiến tranh với nước Nga\". Đồng chí Tôn Đức Thắng, lúc đó là thợ máy trên chiến hạm được cử làm người kéo cờ đỏ biểu thị thái độ phản đối sự can thiệp vào nước Nga Xô Viết. Cuộc binh biến đã lan rộng ra toàn hạm đội Pháp và buộc bọn chỉ huy phải cho tàu về cǎn cứ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '18','04', 'Ngày 18-4-1955, Hội nghị Á Phi đã khai mạc tại Bǎngđung Indônêsia, 29 đoàn đại biểu chính phủ các nước Á Phi là thành viên chính thức tới dự hội nghị. Thành phần đại biểu gồm nhiều vị đứng đầu chính phủ các nước Á Phi. Đoàn đại biểu Việt Nam Dân chủ cộng hoà tham dự hội nghị do đồng chí Phạm Vǎn Đồng dẫn đầu. Chương trình nghị sự của hội nghị Á Phi gồm 5 mục:\n- Hợp tác kinh tế\n- Hợp tác vǎn hoá\n- Quyền con người và quyền tự quyết \n- Vấn đề các dân tộc phụ thuộc\n- Đẩy mạnh phong trào hoà bình và hợp tác quốc tế.\nHội nghị tuyên bố: Chủ nghĩa thực dân với mọi hình thức biểu hiện của nó, là một tai hoạ, cần chấm dứt mau chóng. Việc áp bức và bóc lột là một sự phủ nhận những quyền cơ bản của con người, trái với hiến chương Liên hiệp quốc ngǎn cản sự nghiệp hoà bình thế giới và sự hợp tác quốc tế. Hội nghị lên án những chính sách và hành động phân biệt chủng tộc ở nhiều vùng rộng lớn châu Phi và nhiều nơi trên thế giới xâm phạm quyền con người, phủ nhận phẩm giá con người.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','05', 'Ngày 18-5-1960 khánh thành Nhà máy cao su Sao vàng, Nhà máy thuốc lá Thǎng Long, Nhà máy xà phòng Hà Nội; Cùng với nhà máy cơ khí Hà Nội và một số nhà máy khác đã hình thành khu công nghiệp Thượng Đình, khu công nghiệp quy mô đầu tiên ở Hà Nội. Từ trước ngày giải phóng (1954), Hà Nội là một thành phố chủ yếu là dịch vụ và tiêu thụ. Kế hoạch 5 nǎm lần thứ nhất đã khẳng định Hà Nội phải là trung tâm vǎn hoá, chính trị và kinh tế ở miền Bắc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','05', 'Vào ngày 18-5-1961, Công ước Quốc tế đã được thông qua tại Hội nghị viên của Liên hiệp quốc tế về quan hệ ngoại giao giữa các nước trên thế giới và miễn trừ ngoại giao. Đến nǎm 1985 đã có 143 nước tham gia Công ước Viên 1961 về quan hệ ngoại giao, trong đó có Việt Nam (từ nǎm 1980).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','05', 'Ngày 18-5-1964, ta đã khánh thành cầu Hàm Rồng (ở Thanh Hoá) và đoạn đường sắt Hàm Rồng - Vinh dài 156km.\nCầu Hàm Rồng trên sông Mã được xây dựng tại một vị trí có địa chất phức tạp. Hai bên đầu cầu là hai trái núi. Lòng sông khó thi công. Bằng trí thông minh và tinh thần sáng tạo, đội ngũ công nhân ngành cầu non trẻ Việt Nam đã khắc phục khó khǎn tiến hành xây dựng đảm bảo kỹ thuật và tiến độ. Trong những nǎm có chiến tranh phá hoại của giặc Mỹ, quân và dân ta đã bắn rơi 100 máy bay của chúng trên bầu trời Hàm Rồng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','05', 'Ngày 18-5-1978, thực hiện Hiệp định vận chuyển hàng không giữa hai nước Việt-Pháp, chuyến bay đầu tiên của Hãng hàng không dân dụng Pháp đã đến sân bay Tân Sơn Nhất, thành phố Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','05', 'Nhạc sĩ Đỗ Nhuận, sinh nǎm 1922 và qua đời ngày 18-5-1991. Ông đã tu nghiệp tại Nhạc viện Tchaikôpxki (Liên Xô), nguyên là Tổng thư ký Hội Nhạc sĩ Việt Nam khoá 1 và khoá 2. Ông đã sáng tác từ trước nǎm 1945, với hàng trǎm bài hát, trong đó có nhiều bài nổi tiếng như: Côn Đảo, Du kích ca, Nhớ chiến khu, Áo mùa đông, Đâu có giặc là ta cứ đi, Giải phóng Điện Biên, Du kích sông Thao, Việt Nam quê hương tôi, Thanh niên vui mở đường, Trai anh hùng - gái đảm đang, Trông cây lại nhớ tới người, Đường bốn mùa xuân...\nĐỗ Nhuận là nhạc sĩ đầu tiên viết Ôpêra với các tác phẩm: Cô Sao, Người tạc tượng. Ngoài ra ông còn viết một số ôpêret và tác phẩm khí nhạc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '18','05', 'Ngày 18-5-1969, tại Xtốckhôm, Thủ đô Vương quốc Thụy Điển, hội nghị quốc tế ủng hộ Việt Nam khai mạc. 300 đại biểu của 50 nước và 21 tổ chức quốc tế đã hoan nghênh lập trường của ta, đòi Mỹ phải rút quân ra khỏi Việt Nam, chấm dứt chiến tranh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','06', 'Nguyễn Trọng Khâm, nhà hoạt động cách mạng, nhà thơ, bút danh Xuân Thuỷ, sinh ngày 2-9-1912 tại xã Xuân Phương, Từ Liêm, Hà Nội.\nLúc còn nhỏ, ông học tại Hà Nội, tham gia các tổ chức yêu nước chống Pháp. Từ nǎm 1932 ông tích cực hoạt động dưới sự lãnh đạo của Đảng lãnh dạo Đông Dương, bị Pháp bắt giam nhiều lần nhưng vẫn kiên trì đấu tranh trong tù cũng như khi được trả tự do. Sau Cách mạng Tháng Tám, ông lần lượt được cử giữ nhiều chức vụ qaun trọng trong Đảng và Chính phủ. Ông là Ủy viên Trung ương Đảng các khoá 2,3 và 4 (từ nǎm 1951 đến 1981) và nǎm 1968 được bầu vào Ban Bí thư Trung ương Đảng. Nǎm 1968, ông là Bộ trưởng của Chính phủ, được cử làm trưởng đoàn đại biểu Chính phủ dự hoà đàm về chiến tranh Việt Nam với Mỹ tại Pari, đưa đến ký hiệp định Pari chấm dứt chiến tranh tại Việt Nam nǎm 1973. Đầu nǎm 1980, ông làm Chủ tịch hữu nghị Việt - Xô, Phó chủ tịch kiêm Tổng Thư ký Ủy ban bảo vệ hoà bình thế giới của Việt Nam , Phó chủ tịch kiêm tổng Thư ký Hội đồng nhà nước Việt Nam.\nDo công lao của mình, ông được Đảng và Nhà nước Việt Nam tặng thưởng nhiều huân chương cao quý, trong đó có huân chương Hồ Chí Minh. Ông đã xuất bản các tập thơ: \"Thơ Xuân Thuỷ\" (1974) và \"Đương xuân\" (1979). Ông mất ngày 18-6-1985 tại Hà Nội, thọ 73 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','06', 'Cục Vận tải thuộc Bộ Quốc phòng được thành lập ngày 18-6-1949, trong tình hình cuộc kháng chiến chống thực dân Pháp đang mở rộng. Từ đó đến nay, ngày 18-6 trở thành ngày truyền thống của bộ đội vận tải.\nSau khi thành lập, Cục Vận tải đã tổ chức ngay lực lượng phục vụ chiến dịch biên giới. Trong những nǎm 1950-1953, bộ đội vận tải đã phục vụ 6 chiến dịch lớn: Trần Hưng Đạo, Hoàng Hoa Thám, Quang Trung, Hoà Bình, Tây Bắc và Thượng Lào.\nTrong chiến dịch Điện Biên Phủ, bộ đội vận tải có 26 vạn dân công tham gia với trên 3 triệu ngày công. Cuộc tổng tiến công và nổi dậy 1975, bộ đội vận tải đã xử dụng trên 1 vạn xe vận tải quân sự và một số đoàn xe vạn tải hàng hoá.\nCác đợn vị vận tải cơ giới đường bộ và đường thuỷ thực hiện khẩu hiệu \"Thần tốc, táo bạo\" trong vận chuyển. Tháng 8-1968, Hồ Chủ tịch tặng cho ngành xe vận tải quân đội lá cờ mang dòng chữ \"Yêu xe như con, quý xǎng như máu, vượt mọi khó khǎn hoàn thành nhiệm vụ\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','06', 'Phong trào thi đua \"Ba nhất\", nẩy mầm từ đại đội hai pháo binh, đoàn Vinh Quang, một đơn vị huấn luyện, trước thuộc loại kém nhưng sau một thời gian kiên quyết phấn đấu chống tư tưởng bảo thủ, trì trệ, ra sức khiêm tốn học tập, quyết tâm vươn lên làm chủ khoa học kỹ thuật nên chuyển thành đơn vị tiên tiến và là đơn vị đầu tiên đạt danh hiệu \"Ba nhất\" vào ngày 18-6-1960. Nội dung \"Ba nhất\" là: nhất về huấn luyện quân sự, rèn luyện kỹ thuật; nhất về mặt gương mẫu, kỷ luật; nhất về lao động và sản xuất.\nPhong trào thi đua \"Ba nhất\" đã lôi cuốn các đơn vị trong toàn quân, mở rộng ra cả những đơn vị dân quân, thắt chặt thêm tình đoàn kết giữa bộ đội thường trực và lực lượng hậu bị, củng cố khối đoàn kết công - nông - binh.\nHồ Chủ tịch đã nói:\n\"Công nhân phất cao ngọn cờ Duyên Hải\nNông dân phất cao ngọn cờ Đại Phong\nQuân đội anh hùng phất cao ngọn cờ \"Ba nhất\".\nCông - nông - binh đại thi đua, đại đoàn kết, chủ nghĩa xã hội nhất định thành công, Bắc - Nam nhất định sẽ thống nhất, non sông một nhà\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '18','06', 'Ngày 18-6-1815 đã diễn ra trận Oatéclô, gần thành phố Bruyxen (nước Bỉ). Tham chiến có quân Pháp với gần 72 nghìn người, dưới sự chỉ huy trực tiếp của Napôlêông Bônapactơ và liên quân Anh - Phổ có 68 nghìn người, do tướng Oenlinhtơn cầm đầu. \nQuân đội của Napôlêông đã bị quân Anh - Phổ đánh bại hoàn toàn. Thất bại ở trận Oateclô buộc Napôlêông phải thoái vị lần thứ hai (ngày 22-6-1815), sau 100 ngày trở lại làm vua nước Pháp. Ông bị đầy ra đảo Xanh Hê Len (Đại Tây Dương) và chết ở đó nǎm 1821.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '18','06', 'Nhân dân Bungari và nhân dân thế giới vô cùng biết ơn và kính trọng Ghêoocghi Đimitơrốps, người con vĩ đại của giai cấp công nhân, vị lãnh tụ và người thầy của cách mạng Bungari, người xây dựng nước Bungari XHCN, nhà hoạt động lỗi lạc của phong trào cộng sản quốc tế.\nĐimitơrốps sinh ngày 18-6-1882 trong một gia đình công nhân cách mạng. Ông bắt đầu hoạt động từ nǎm 15 tuổi. Nǎm 20 tuổi ông gia nhập Đảng xã hội dân chủ. Sau đó, ông được bầu làm Ủy viên Trung ương Đảng.\nSau cách mạng tháng Mười nga, ông có công lớn trong việc thống nhất Đảng xã hội dân chủ Bungari, Nǎm 1923 ông lãnh đạo cuộc khởi nghĩa chống phát xít. Ông tích cực hoạt động trong Ban Chấp hành Quốc tế cộng sản và góp phần dự thảo những vǎn kiện có tính chất cương lĩnh. Nǎm 1935 ông được bầu làm Tổng bí thư quốc tế cộng sản. Sau khi lật đổ chính quyền phát xít, Bungari tuyên bố thành lập nước cộng hoà nhân dân, Đimitơrốp được bầu làm Chủ tịch Quốc hội và Chủ tịch nước. Vận dụng sáng tạo chủ nghĩa Mác-Lênin, ông đã góp phần quan trọng vào việc phát triển lý luận của chủ nghĩa cộng sản khoa học, nhất là lý luận về chế độ dân chủ nhân dân và con đường đi lên chủ nghĩa xã hội ở Bungari.\nNgày 2-7-1949 Đimitơrốp qua đời, để lại cho tất cả những người cộng sản và lao động trên thế giới một niềm thương tiếc vô hạn, một tấm gương sáng chói về dũng khí, phẩm chất của một người cộng sản kiên định sáng tạo.\nVề Đimitơrốp, còn có sự kiện rất đáng chú ý và nổi tiếng thế giới, là ông đã hùng biện tự bào chữa cho mình một cách thắng lợi và cho những người cộng sản ở toà án của bọn phát xít Đức.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','07', 'Ngày 18-7-1963 Thủ tướng Chính phủ ra Nghị định ban hành Điều lệ quy định các danh hiệu và tiêu chuẩn thi đua để động viên mọi người, mọi tập thể lao động ra sức phấn đấu nâng cao nǎng suất, chất lượng lao động hoàn thành toàn diện và vượt mức kế hoạch nhà nước, ra sức phấn đấu trở thành những tập thể xuất sắc về mọi mặt.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','07', 'Ngày 18-7-1973 Hoa Kỳ đã ra thông báo \"hoàn thành\" việc tháo gỡ và làm mất hiệu lực bom mìn đã thả xuống các vùng biển, sông của nước Việt Nam Dân chủ cộng hoà, và rút hết phương tiện và lực lượng ra khỏi vùng biển nước ta như hiệp định Pari quy định. Nhưng trên thực tế, Chính phủ Hoa Kỳ đã tìm mọi cách trì hoãn, không chịu thực hiện nghĩa vụ của họ nên chỉ tháo gỡ được rất ít trong số hàng vạn bom mìn đã thả xuống các vùng sông biển của nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','07', 'Ngày 18-7-1977 tại Viêng Chǎn (Thủ đô Cộng hoà dân chủ nhân dân Lào). Cộng hoà xã hội chủ nghĩa Việt Nam và Cộng hoà dân chủ nhân dân Lào đã ký Hiệp ước hữu nghị và hợp tác, nhằm củng cố và tǎng cường tình đoàn kết, mối quan hệ đặc biệt giữa hai dân tộc cũng như mối quan hệ hợp tác, giúp đỡ lẫn nhau về an ninh quốc phòng của mỗi nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '18','07', 'Thackơrê Uylimơ Mếchpixơ (Thackeray William Makepeace) là nhà vǎn lớn nước Anh. Ông sinh ngày 18-7-1811 tại Ấn Độ. Ông say mê vǎn học và bắt đầu sáng tác khi học đại học. Nǎm 1948 ông cho ra đời tiểu thuyết \"Hội chợ phù hoa\" phê phán gay gắt thói xấu xa của bọn quý tộc, tư sản Anh. Với tác phẩm này ông lập tức trở thành một trong những nhà tiểu thuyết nổi tiếng nhất nước Anh.\nTiểu thuyết của Thackơrê chứa đầy yếu tố hài hước, dí dỏm, dễ gây xúc cảm. Ngòi bút của ông đã góp phần phơi bày sự thật của xã hội thượng lưu. Nghệ thuật thể loại tiểu thuyết của ông vừa có thiên hướng tình cảm, vừa chứa đựng tư tưởng phê phán xã hội. Bằng ngòi bút phê phán sắc sảo, giàu tính trữ tình, ông đã để lại cho nhân loại nhiều tác phẩm bất hủ, xứng đáng là người đại diện ưu tú của vǎn học Anh thế kỷ XIX. Ông mất ngày 23-11-1864 khi mới 53 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','08', 'Sau 15 ngày đêm mưa liên tục, cơn lũ nǎm 1945 đã đạt đến đỉnh cao vào ngày 18-8-1945 và đạt mức nước lịch sử. Ngay trong ngày 18-8 nước lũ đã làm vỡ hàng loạt đê: đê Quảng Cự, đê Diêm Xuân (VĩnhTường, Vĩnh Yên); đê hữu ngạn sông Cầu; đê Đông Lao sông Đáy (Hà Đông); đê Bất Bạt sông Bo (Sơn Tây); đê Hưng Nhân sông Hồng (Thái Bình); đê Hồng Hà (Việt Trì); đê Vĩnh Bảo và Thạch Trù (Hải Dương); đê tả ngạn sông Kinh Thày (Hải Dương).\nTrước đó, ngày 17-8, đê sông Thao, sông Lô vỡ. Các đê sông Hồng vỡ ngày 18-8, đê sông Phó Đáy vỡ ngày 20-8, đê Thái Bình vỡ ngày 21-8. Đê vỡ làm úng lụt 1/3 diện tích cấy cày ở Bắc Bộ. Những vụ vỡ đê do mưa lũ, mưa lớn, còn do bộ máy quản lý đê bị tê liệt và do bọn phản động phá hoại đã làm trầm trọng thêm nạn đói vốn đã hoành hành từ trước, làm chết hai triệu người, và đặt chính quyền Cách mạng được lập ra sau các vụ vỡ đê vài ngày trước đó những khó khǎn cực kỳ nghiêm trọng để phục hồi sản xuất, ổn định đời sống nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','08', 'Từ tháng 5-1965 Mỹ huy động 8.000 lính thuỷ đánh bộ và hàng trǎm máy bay phản lực ném bom, máy bay lên thẳng và tàu chiến tiến hành càn quét thôn Vạn Tường, huyện Bình Sơn, Quảng Ngãi, một thôn nhỏ thuộc xã Bình Hải, chiều dài không quá 6 km, rộng không quá 3 km. Trong hai ngày chiến đấu quyết liệt, quân giải phóng đã tiêu diệt 919 tên Mỹ, phá huỷ 22 xe bọc thép, bắn rơi 13 máy bay.\nNếu trận ấp Bắc đã mở đầu cao trào diệt quân Nguỵ - đội quân được Mỹ tổ chức, huấn luyện, trang bị và chỉ huy, thì trận Vạn Tường mở đầu cao trào diệt quân Mỹ xâm lược. Trận Vạn Tường chứng tỏ quân và dân miền Nam hoàn toàn có khả nǎng thắng Mỹ về quân sự trong chiến tranh cục bộ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','09', 'Ngày 18-9-1946, đơn vị cuối cùng với quân Tưởng đã rút khỏi thành phố Hải Phòng. Dân tộc ta bớt được một kẻ thù nguy hiểm.\nTrước đó một năm, quân đội Tưởng vào Việt Nam để tiếp nhận sự đầu hàng của quân đội Nhật Bản, đồng thời chúng còn âm mưu \"diệt Công, cầm Hồ\", giúp đỡ bọn tay sai \"Việt Quốc\", \"Việt Cách\" gây bạo loạn, lật đổ Chính phủ ta. Nhưng âm mưu của bọn chúng đã bị thất bại.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','09', 'Ngày 18-9-1976, Hội đồng Chính phủ ra Nghị định thành lập thành phố Đà Lạt trực thuộc tỉnh Lâm Đồng.\nĐà Lạt là một trung tâm du lịch và an dưỡng nổi tiếng. Nhiệt độ trung bình từ 10 đến 20 độ. Thành phố là một \"viện bảo tàng thác nước\" với các thác nổi tiếng như Cam Ly, Đa Tam La, Prenn, Pônggua... Đà Lạt còn có những hồ mang tên thật gợi cảm: Xuân Hương, Than Thở, Đa Thành, Đa Thiện...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '18','09', 'Lêônác Âylơ (Leonnhard Euler) là nhà toán học bậc thầy của châu Âu, Ông sinh ngày 15-4-1707 tại Thuỵ Sĩ. Ông làm việc chủ yếu ở Viện Hàn lâm khoa học Beclin Đức. Chính ông là người có công dìu dắt nhà khoa học Nga Lômônôsốp.\nCả cuộc đời ông đều cống hiến cho khoa học, ngày đêm miệt mài nghiên cứu và sáng tạo, tham gia công tác lãnh đạo giới toán học. Ông để lại 416 công trình. Toàn bộ công trình của ông đều được in thành sách 85 quyển cỡ lớn với gần 40.000 trang. Mọi người đã biết đến ông qua: Đường thẳng Âylơ, đường tròn Âylơ về liên hệ giữa số đỉnh, cạnh và mặt trong một đa diện lồi.\nNgày 18-9-1783 nhà thiên tài toán học Lêonác Âylơ ngừng làm toán và cũng là ngày ông từ trần.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','10', 'Ngày 18-10-1961, tại thị xã Phủ Lý, Bộ Giáo dục đã phát động phong trào thi đua \"Dạy tốt, học tốt\" học tập trường phổ thông cấp II Bắc Lý trong toàn ngành giáo dục.\nTrường cấp II Bắc Lý thuộc huyện Lý Nhân, tỉnh Nam Hà, là trường dẫn đầu trong phong trào thi đua \"Dạy tốt, học tốt\", của ngành giáo dục. Nhà trường luôn luôn nắm vững mục tiêu đào tạo con người toàn diện, thực hiện phương châm kết hợp học tập với lao động sản xuất, học đi đôi với hành, gắn nhà trường với xã hội, thực hiện khẩu hiệu \"Tất cả vì chủ nghĩa xã hội, tất cả vì học sinh thân yêu\".\nUỷ ban Thường vụ Quốc hội đã tặng thưởng Huân chương Lao động hạng nhất cho trường phổ thông cấp II Bắc Lý.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','10', 'Hiệp định tín dụng được Chính phủ Việt Nam và Ngân hàng phát triển châu Á (ADB) ký kết chính thức ngày 18-10-1996 với số tiền tương đương 50 triệu đôla Mỹ.\nDự án này nhằm tăng thu nhập của nông dân ở nông thôn, tạo nhiều việc làm, củng cố hệ thống tài chính nông thôn.\nADB hàng năm đã tài trợ cho Việt Nam từ 300 đến 500 triệu đôla Mỹ thông qua các dự án các chương trình và đi kèm là các khoản trợ giúp kỹ thuật. Trong 3 năm (từ 1993-1996) ADB cho Việt Nam vay hơn 1 tỷ đôla.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '18','10', 'Tômát Anva Êđixơn (Thomas Alva Adison) sinh năm 1847 tại nước Mỹ, qua đời ngày 18-10-1931.\nÊđixơn là tác giả của các phát minh: Máy điện báo tải hai, đa tải, máy đĩa hát, bóng đèn điện có dây tóc, xe điện, xe hoả chạy điện. Máy chiếu phim câm và phim nói, máy tách sắt ra khỏi quặng bằng từ trường v.v...\nÔng đã đăng ký hơn 1.200 phát minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','11', 'Ngày 18-11-1930, Ban thường vụ Trung ương Đảng ra chỉ thị thành lập Hội phản đế đồng minh gửi cho các cấp uỷ Đảng và toàn thể đảng viên. Ban chỉ thị nêu đường lối chiến lược và sách lược của Đảng, nêu nhiệm vụ chống đế quốc và chống phong kiến phải kết hợp chặt chẽ, và xác định công nhân và nông dân là hai động lực chính của Cách mạng.\nVới bản chỉ thị này, Trung ương Đảng đã tạo điều kiện thuận lợi cho các cấp uỷ phát triển mạnh mẽ Hội phản đế đồng minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','11', 'Ngày 18-11-1964, đại đội 3 pháo cao xạ đã bắn rơi một chiếc máy bay RF101 và 2 chiếc T28 của Mỹ ở miền Tây tỉnh Quảng Bình.\nTrong trận chiến đấu này, hình ảnh chính trị viên Nguyễn Viết Xuân với câu nói động viên chiến sĩ \"Nhằm thẳng quân thù, bắn\" đã biểu lộ lòng cǎm thù giặc sâu sắc và ý chí kiên quyết tiêu diệt địch.\nAnh Nguyễn Viết Xuân sinh nǎm 1934 ở xã Ngũ Kiên, huyện Vĩnh Tường, tỉnh Vĩnh Phúc. Anh được Nhà nước truy tặng danh hiệu Anh hùng lực lượng vũ trang nhân dân. Hà Nội có một trường học nội trú cho con liệt sĩ mang tên Nguyễn Viết Xuân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '18','11', 'Nhà thơ Pôn Êluya tên thật là Pôn Ơgien Granliden, sinh nǎm 1895 tại Pari, từ trần ngày 18-11-1952.\nÔng đã tham gia chiến tranh ở Tây Ban Nha chống phát xít Frǎngcô. Trong chiến tranh thế giới lần thứ hai, ông tham gia chống phát xít Đức. Nǎm 1942, giữa lúc Đảng cộng sản Pháp bị tổn thất lớn, Pôn Êluya xin vào Đảng.\nCuộc đời và sự nghiệp của ông tiêu biểu cho con đường của nhiều nhà thơ và nghệ sĩ phương Tây, từ các trường phái vǎn nghệ suy đồi và sự nổi loạn ảo tưởng đã đến với vǎn nghệ Cách mạng và chủ nghĩa cộng sản.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','12', 'Nhà vǎn, nhà soạn kịch Vi Huyền Đắc sinh ngày 18-12-1899 quê ở Trà Cổ (tỉnh Quảng Ninh). Mất nǎm 1976 tại Hà Nội.\nVi Huyền Đắc viết tới 20 vở kịch. Trước nǎm 1945 là thời kỳ viết sung sức nhất của ông. Nhiều vở kịch có giá trị như: Uyên ương, Hoàng Mộng Điệp, Kim tiền, Ông Ký Cóp, Thành Cát Tư Hãn, Từ Hy thái hậu.\nÔng là một trong vài nhà viết kịch có tên tuổi trong thời kỳ hình thành nền kịch nói Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','12', 'Ngày 18-12-1954, Chủ tịch Hồ Chí Minh đã nói chuyện với học sinh các trường trung học Chu Vǎn An, Nguyễn Trãi, Trưng Vương (Hà Nội).\nBác Hồ cǎn dặn: Các học sinh phải học để xứng đáng là người chủ nước nhà. Nhiệm vụ của thanh niên là học tập để: Yêu Tổ quốc, yêu nhân dân, yêu khoa học, yêu lao động, yêu đạo đức. Học để phụng sự Tổ quốc, phụng sự nhân dân, làm cho dân giàu nước mạnh. Học phải đi đôi với hành, phải thi đua giữa các trường, các lớp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','12', 'Để thực hiện âm mưu gây sức ép buộc Hà Nội phải trở lại Hội nghị Pari, chính quyền Níchxơn điên cuồng mở cuộc tập kích chiến lược bằng B52 và nhiều loại máy bay khác vào thủ đô Hà Nội và thành phố cảng Hải Phòng vào đêm 18-12-1972.\nChúng đã tập trung một nửa lực lượng không quân chiến lược và hầu hết lực lượng không quân chiến thuật mà chúng có ở Đông Nam Á để tiến hành cuộc tập kích này. Nhưng quân và dân ta cảnh giác cao, chiến đấu giỏi, đã thắng trận đầu hết sức giòn giã, bắn rơi 7 máy bay Mỹ trong đó có 3 chiếc B52 và 1 chiếc F111, bắt sống nhiều giặc lái.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','12', 'Ngày 18-12-1974, Bộ Chính trị Trung ương Đảng lao động Việt Nam họp hội nghị mở rộng. Tham dự Hội nghị còn có các đồng chí lãnh đạo các chiến trường, các đồng chí trong quân uỷ Trung ương và đồng chí Phó Tổng tham mưu trưởng phụ trách tác chiến. Nghị quyết của hội nghị nói rõ: \"Động viên nỗ lực lớn nhất của toàn Đảng, toàn dân, toàn quân ở hai miền trong thời gian 1975-1976\". Bộ Chính trị đưa ra phương án cực kỳ quan trọng là: Nếu thời cơ đến vào đầu hoặc cuối 1975 thì lập tức giải phóng miền Nam trong nǎm 1975.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','12', 'Tại kỳ họp thứ 7 Quốc hội khoá VI. Hiến pháp của Cộng hoà xã hội chủ nghĩa Việt Nam đã được thông qua vào 15 giờ 25 phút ngày 18-12-1980.\nHiến pháp của nước Cộng hoà xã hội chủ nghĩa Việt Nam gồm 12 chương, 147 điều. Là sự kế thừa và phát triển các hiến pháp 1946 và 1959 trong điều kiện mới, Hiến pháp tổng kết và xác định những thành quả đấu tranh cách mạng của nhân dân Việt Nam trong nửa thế kỷ qua, thể hiện ý chí nguyện vọng của nhân dân Việt Nam, bảo đảm bước phát triển rực rỡ trong thời gian tới. Là luật cơ bản của Nhà nước, Hiến pháp quy định chế độ chính trị, kinh tế, văn hoá xã hội, quyền nghĩa vụ cơ bản của công nhân, cơ cấu tổ chức nguyên tắc hoạt động của các cơ quan Nhà nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '18','12', 'Nhà thơ Xuân Diệu sinh 1916, quê ở Hà Tĩnh, qua đời ngày 18-12-1985 ở Hà Nội.\nÔng nổi tiếng là một nhà thơ tài nǎng trong phong trào Thơ Mới, từ khi xuất bản tập \"Thơ Thơ\" (nǎm 1938) và tập \"Phấn thông vàng\"(1939).\nÔng đã đóng góp cho nền vǎn học nước nhà một khối lượng sáng tác to lớn: 13 tập thơ, 5 tập bút ký, 16 tập nghiên cứu phê bình vǎn học, 6 tác phẩm dịch thơ nước ngoài.\nỞ mặt nào, tác phẩm của ông cũng in đậm hình ảnh một Xuân Diệu - nhà thơ của mùa xuân và tuổi trẻ, luôn luôn sôi nổi tình yêu, dạt dào tình đời.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '18','12', 'Ngày 18-12-1984, Hoàng thân Xuphanuvông, Chủ tịch nước cộng hoà dân chủ nhân dân Lào ký quyết định tặng thưởng Huân chương Sao vàng quốc gia Lào cho quân đội nhân dân Việt Nam, vì những đóng góp to lớn đối với Cách mạng Lào và góp phần thắt chặt mối quan hệ đặc biệt Việt - Lào.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','01', 'Ngày 19-1-1947, tại cǎn cứ địa Việt Bắc đã khai mạc Hội nghị quân sự lần thứ nhất. Hội nghị đề ra kế hoạch triệt để tiêu thổ kháng chiến, phá hoại đường xá, nhà cửa, làm vườn không nhà trống, xây dựng các đội quân đặc biệt như: đội cảm tử diệt xe tǎng, đội đánh địa lôi... để ngǎn chặn bước tiến của quân Pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','01', 'Ngày 19-1-1997, Hoàng Thị Minh Hồng - Phóng viên của báo Đầu tư và ngân hàng cùng 35 thanh niên của 25 nước trên thế giới tiến hành cuộc thám hiểm Nam Cực do UNESCO tổ chức.\nTại Nam Cực, với lòng dũng cảm và ý chí phi thường của phụ nữ Việt Nam, Minh Hồng đã cắm được lá cờ đỏ sao vàng, Quốc kỳ Việt Nam lên xứ sở đầy tuyết phủ này, chứng tỏ sức mạnh của tuổi trẻ Việt Nam.\nTháng 11-1997, Hoàng Thị Minh Hồng đã được nhận bằng khen của Thủ tướng Chính phủ về thành tích này của chị.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '19','01', 'Giêm Oát (James Watt) - nhà kỹ thuật và sáng chế thiên tài Anh. Ông sinh ngày 19-1-1736. Nǎm 1765 ông phát minh cải tiến máy hơi nước hoàn chỉnh. Phát minh này góp phần cải tiến đầu máy Xe lửa hoàn thiện hơn. Ông là hội viên Hội Hoàng gia Luân Đôn, là Viện sĩ Viện Hàn lâm khoa học Pari, Pháp. Ông qua đời ngày 19-8-1819. Tên ông được dùng để đặt cho đơn vị công suất, đó là đơn vị Oát. Nǎm 1932, Viện Hàn lâm khoa học Anh đã đặt ra \"Huy chương vàng Giêm Oát\" để làm giải thưởng cho các nhà khoa học có công lớn trong ngành chế tạo máy.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '19','01', 'Pôn Xêdan (Paul Cezanne) - hoạ sĩ thiên tài Pháp, sinh ngày 19-1-1839. Ông có nhiều thành công rực rỡ trong việc vẽ tĩnh vật, cụ thể bức \"Đĩa hoa quả\" và \"Chiếc đồng hồ bằng cẩm thạch đen\" là những tác phẩm danh tiếng nhất. Những kiệt tác cuối đời là \"Người chơi bài\" và \"Tắm\".\nTranh của Xêdan kết hợp giữa chủ nghĩa ấn tượng với nghệ thuật cổ điển. Ông có ảnh hưởng sâu sắc đến nền nghệ thuật châu Âu thế kỷ XX và được coi là cha đẻ khơi nguồn cho hội hoạ hiện đại. Ông mất ngày 23-10-1906.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','02', 'Từ ngày 19 đến ngày 28-2-1952, quân đội ta đã chặn đánh và phá tan cuộc tấn công của thực dân Pháp vào Đồng Tháp Mười.\nTrong trận càn này, địch đã huy động 3.000 quân, 75 xe lội nước, 39 tàu chiến và có cả máy bay yểm hộ. Ta chặn đánh các mặt, tiêu diệt 780 tên, làm bị thương 790 tên, phá huỷ 3 tàu chiến, 4 xe lội nước, lực lượng còn lại của địch phải rút chạy.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','02', 'Từ ngày 19 đến ngày 22-2-1974, đã diễn ra Hội nghị đại biểu Đoàn thanh niên lao động Hồ Chí Minh. Có 383 đại biểu thay mặt cho 2 triệu 80 vạn đoàn viên về dự, trong đó có 15 anh hùng chiến sĩ thi đua; Hội nghị nghe và thảo luận báo cáo phương hướng, nhiệm vụ Đoàn TNLĐ trong tình hình mới. Chủ tịch Quốc hội đã gắn Huân chương Hồ Chí Minh của Quốc hội trao tặng phong trào thanh niên và Đoàn thanh niên Lao động Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','02', 'Ngày 19-2-1977, Cung thiếu nhi Hà Nội, (tiền thân là Câu lạc bộ thiếu nhi) được khánh thành.\nCung có một ngôi nhà 6 tầng, hai ngôi nhà 2 tầng, một rạp Khǎn quàng đỏ 500 chỗ ngồi, một sân pa-tanh, một sân ôtô điện và đặc biệt có nhà truyền thống Bác Hồ với thiếu nhi Thủ đô.\nHàng nǎm Cung thiếu nhi Hà Nội thu hút từ 8.000 đến 1 vạn em đến sinh hoạt và học tập các bộ môn: điện ảnh, âm nhạc, vǎn học, tin học, khoa học và kỹ thuật, võ thuật, thể thao, tiếng nước ngoài v.v...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '19','02', 'Nicôlai Côpécnixếch (Nicolas Copermicees), nhà thiên vǎn học vĩ đại Ba Lan, sinh ngày 19-2-1473. Nǎm 1525, trong một tác phẩm kinh tế học ông đã phát minh ra một qui luật tiền tệ. Nǎm 1530 tác phẩm \"Chuyển động quay của các thiên thể\" của ông đã trình bày học thuyết cho rằng mặt trời là trung tâm vũ trụ, Trái đất cùng các hành tinh khác quay xung quanh mặt trời. Chúng ta cảm thấy mặt trời và các vì sao quay xung quanh trái đất, chính là vì trái đất chuyển động quanh trục của nó một lần trong ngày đêm. Học thuyết Nhật tâm của ông đã giáng một đòn nặng nề vào quan điểm \"Vai trò của Thượng đế sáng tạo ra thế giới\" của giáo hội Thiên chúa giáo. Ông mất ngày 4-5-1543. Trên mộ chí nơi an nghỉ của nhà bác học vĩ đại người ta ghi một dòng chữ đầy ý nghĩa: \"Người đã giữ lại Mặt trời và đẩy Trái đất di chuyển\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '19','02', 'Nhà lãnh đạo Trung Quốc - Đặng Tiểu Bình sinh nǎm 1904 tại Tứ Xuyên, lúc còn trẻ từng sang học ở Pháp, Liên Xô. Về nước, ông công tác tại cơ quan Trung ương Đảng, trải qua nhiều chức vụ Nhà nước và Đảng - lên tới chức Tổng Bí thư. Ông cũng nhiều lần mất hết chức vụ rồi lại được phục hồi (1966-1976)\nNgày 19-2-1997, ông từ trần tại Bắc Kinh, thọ 93 tuổi. Ông là một nhà lãnh đạo lão thành, một trong những người chủ chốt mở ra phương hướng mở cửa của Trung Quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','03', 'Ngày 19-3-1950 Đế quốc Mỹ cho 2 tàu chiến Anđécxơn và Xtíchken cập bến Sài Gòn, chuẩn bị thao diễn lực lượng để uy hiếp tinh thần đồng bào ta. Cả Sài Gòn tức giận, hàng vạn đồng bào Sài Gòn xuống đường tuần hành thị uy hô vang các khẩu hiệu \"Phản đối viện trợ Mỹ\", \"Đả đảo Bảo Đại\", \"Hồ Chí Minh muôn nǎm\". Đồng bào giương cao cờ đỏ sao vàng và khẩu hiệu diễu qua các phố chính dẫn xuống bến tàu. Bị cảnh sát ngǎn lại, hàng vạn người dồn về toà thị chính xé cờ Mỹ, cờ Pháp, cờ bù nhìn và ảnh Bảo Đại. Cũng hôm đó quân ta nã súng cối vào các tàu đậu trên sông. Trước sức mạnh của quần chúng, 2 tàu chiến Mỹ đã lặng lẽ rút neo cút khỏi Sài Gòn. Đây là cuộc biểu tình chống Mỹ đầu tiên của nhân dân ta.\nTừ đó ngày 19-3 hằng nǎm được gọi là ngày Toàn quốc chống Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','03', 'Từ những tổ đội đặc công trong kháng chiến chống Pháp, bộ đội đặc công đã trưởng thành nhanh chóng, lập công xuất sắc trong cuộc kháng chiến chống Mỹ. Nhân dịp Bộ đội Đặc công được xác định chính thức là một binh chủng của Quân đội nhân dân Việt Nam, ngày 19-3-1967, Hồ Chủ tịch đã đến thǎm bộ đội đặc công. Người đã biểu dương thành tích của các chiến sĩ đặc công và tặng bộ đội đặc công 16 chữ vàng: Đặc biệt tinh nhuệ. Anh dũng tuyệt vời. Mưu trí táo bạo. Đánh hiểm thắng lớn. Nǎm 1976 Nhà nước ta đã tuyên dương Đơn vị Anh hùng lực lượng vũ trang cho Binh chủng Đặc công.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','03', 'Ngày 19-3-1975, tỉnh Quảng Trị được giải phóng trong cuộc tổng tiến công và nổi dậy của mùa xuân đại thắng. \nTỉnh Quảng Trị có diện tích 4585km2, số dân hơn 50 vạn người. Tỉnh lỵ là thị xã Đông Hà. Tỉnh này có 6 huyện là: Hương Hoá, Vĩnh Linh, Gio Linh, Triệu Phong, Hải Lǎng, Cam Lộ và thị xã Quảng Trị.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','03', 'Từ ngày 19 đến ngày 21-3-1975, quân ta tiếp tục truy kích, tiêu diệt lực lượng của quân ngụy rút chạy khỏi Tây Nguyên. \nNgày 24-3, số địch còn lại bị tiêu diệt và bắt sống toàn bộ ở Củng Sơn. Chiến dịch Tây Nguyên đến đây kết thúc thắng lợi.\nTrong chiến dịch này, ta thu và phá huỷ 110 máy bay, 17 nghìn súng các loại, hơn 760 máy thông tin, 1095 xe quân sự, thu toàn bộ kho tàng, thiết bị chỉ huy, cơ sở sửa chữa của quân đoàn 2 ngụy ở Tây Nguyên.\nLần đầu tiên trong lịch sử chiến tranh ở Đông Dương một quân đoàn và một quân khu của địch bị tiêu diệt chỉ trong một chiến dịch - Toàn bộ vùng cao nguyên có vị trí chiến lược hết sức quan trọng trên chiến trường miền Nam được giải phóng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '19','03', 'Phrêđêrich Giôliô Quyri (Frédéric Joliot Curie) sinh ngày 19-3-1900. Qua đời nǎm 1958. Ông là nhà bác học nổi tiếng người Pháp, nhà hoạt động xuất sắc của phong trào hoà bình thế giới. Ông cùng với vợ là bà Iren Quyri đã tìm ra tính phóng xạ nhân tạo dùng trong y học, nông nghiệp, tìm ra nơtơrôn và nghiên cứu về phản ứng dây chuyền để thu nǎng lượng nguyên tử.\nNǎm 1935, ông Giôliô Quyri và bà Iren Quyri được nhận giải thưởng Noben về hoá học. Trong chiến tranh thế giới thứ hai, ông Giôliô Quyri tích cực tham gia phong trào chống phát xít, nǎm 1942 gia nhập Đảng Cộng sản Pháp.\nTháng 3-1956 khi bà Iren Quyri qua đời, Hồ Chủ tịch đã gửi điện chia buồn tới giáo sư Giôliô Quyri.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','04', 'Ngày 19-4-1947, thành lập Xí nghiệp cơ khí Trần Hưng Đạo (hiện nay là Nhà máy Cơ khí Trần Hưng Đạo). Đây là xí nghiệp đầu tiên của ngành công nghiệp quốc doanh Việt Nam do Ban kinh tế Trung ương Đảng thành lập ở Việt Bắc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','04', 'Ngày 19-4-1948, bộ đội chủ lực khu 9 đánh địch ở Tầm Vu (tỉnh Cần Thơ). Ta tiêu diệt hoàn toàn 24 xe quân sự, thu một khẩu pháo 105 ly và 100 súng trường của địch.\nĐây là một chiến thắng có tiếng vang lớn ở Nam Bộ trong những nǎm đầu kháng chiến chống giặc Pháp xâm lược.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','04', 'Từ ngày 19-4 đến ngày 1-5-1968, với một lực lượng hơn một vạn quân \"tinh nhuệ\" gồm 2 lữ đoàn \"kỵ binh bay\", 1 lữ đoàn dù Mỹ, 1 chiến đoàn dù và 1 trung đoàn nguỵ cùng 4-5 tiểu đoàn công binh, pháo binh và pháo các loại, đế quốc Mỹ đã mở cuộc hành quân vào vùng Aso, A Lưới (Thừa Thiên). Chúng tập trung một số lớn máy bay lên thẳng và dùng máy bay B52 bắn phá liên tục hàng tuần để mở đường. Nhưng trước sự phản công mạnh mẽ của quân dân Thừa Thiên, giặc Mỹ thất bại thảm hại, quân ta diệt hơn 2.200 địch (hầu hết là Mỹ) bắn rơi và phá huỷ hơn 100 xe quân sự.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '19','04', '19-4-1890, một người Pháp tên là Ađê (Ader) đã đǎng ký phát minh máy bay.\nCũng vào thời đó, một người Đức tên là ốtthô Liliênthan (Lilinethal) đã tập lái tàu lượn bay liệng để thu được những số liệu cho phép ông chuyển sang bay trên máy bay có động cơ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '19','04', 'Ngày 19-4-1961, Mỹ đã cho đội quân đánh thuê đổ bộ lên bãi biển Hirông của Cuba.\nDưới sự chỉ huy trực tiếp của tổng tư lệnh Phiđen Caxtơrô, quân và dân Cuba đã đánh tan bọn xâm lược trong 72 giờ, bắt sống 1.200 tù binh, bắn rơi 10 máy bay và bắn chìm 1 số tàu đổ bộ của Mỹ.\nChiến thắng Hirông chứng tỏ Cuba đã tự bảo vệ được mình và sau chiến thắng lịch sử này, Cuba tuyên bố tiến lên chủ nghĩa xã hội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','05', 'Ngày 19-5-1890, Chủ tịch Hồ Chí Minh đã sinh ra ở xã Kim Liên, huyện Nam Đàm, tỉnh Nghệ An và Người từ trần vào ngày 2-9-1969 tại Hà Nội.\nHồ Chủ tịch là người đầu tiên truyền bá chủ nghĩa Mác - Lênin vào nước ta, sáng lập ra Đảng Cộng sản Việt Nam và nước Việt Nam Dân chủ cộng hoà.\nHồ Chủ tịch là lãnh tụ vĩ đại của giai cấp công nhân và nhân dân ta, là chiến sĩ quốc tế vô sản và giải phóng dân tộc kiệt xuất, là nhà vǎn hoá lỗi lạc, một biểu tượng đạo đức vô song.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','05', 'Tại Hội nghị Ban Chấp hành Trung ương lần thứ 8, theo đề nghị của đồng chí Nguyễn Ái Quốc, ngày 19-5-1941 Hội nghị quyết định thành lập Việt Nam độc lập Đồng Minh (gọi tắt là Việt Minh). Mặt trận có nhiệm vụ giúp đỡ nhân dân Lào và Cǎmpuchia thành lập mặt trận của mình. Các tổ chức của Việt Minh sẽ là Hội cứu quốc. Hội nghị cũng đã nhận định: \"Cuộc cách mạng Đông Dương sẽ có khởi nghĩa vũ trang từng địa phương và mở đường cho cuộc Tổng khởi nghĩa giành thắng lợi\". Sự ra đời của mặt trận Việt Minh đã hoàn chỉnh sự chuyển hướng chỉ đạo chiến lược và sách lược của Đảng, trực tiếp góp phần quyết định thắng lợi của Cách mạng tháng 8-1945.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','05', 'Nhà máy xe lửa Gia Lâm (Hà Nội) là một nhà máy có truyền thống đấu tranh của giai cấp công nhân nước ta và là một doanh nghiệp lớn của ngành đường sắt có Anh hùng lao động Ngô Gia Khảm. Ngày 19-5-1955 Hồ Chủ tịch đã về thǎm nhà máy. Người thân mật hỏi thǎm tình hình lao động của anh chị em công nhân. Người cǎn dặn phải lao động tích cực và tiết kiệm vì đồng bào miền Nam ruột thịt.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','05', 'Sư đoàn Phòng không Hà Nội được thành lập ngày 19-5-1965 với nhiệm vụ bảo vệ vùng trời Thủ đô yêu quí.\nSư đoàn Phòng không Hà Nội (còn gọi là sư đoàn 361) được Hồ Chủ tịch và nhân dân Thủ đô quan tâm chǎm sóc. Bác đã về thǎm 7 lần ở các trận địa Dốc Vân, Quảng Bá, đập Đáy - Phùng và Yên Nghĩa.\nSư đoàn 361 đã lập được nhiều chiến công xuất sắc trong cuộc kháng chiến chống Mỹ cứu nước: bắn rơi 594 máy bay Mỹ (trong đó có 25 chiếc B52). \nSư đoàn Phòng không Hà Nội đã được tuyên dương Anh hùng lực lượng vũ trang nhân dân (nǎm 1976), 5 trung đoàn, 9 tiểu đoàn, đại đội và 6 cán bộ, chiến sĩ được tuyên dương Anh hùng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','05', 'Bảo tàng Hồ Chí Minh tại thủ đô Hà Nội được bắt đầu xây dựng từ ngày 31-8-1985. Và khánh thành đúng dịp kỷ niệm 100 nǎm ngày sinh nhật Bác. Bảo tàng có diện tích trưng bày về cuộc đời, sự nghiệp Cách mạng của Chủ tịch Hồ Chí Minh là 4000 mét vuông và 400 mét vuông dành cho triển lãm chuyên đề. Hệ thống kho cơ sở rộng gần 1000 mét vuông, lưu giữ và bảo quản 10 vạn hiện vật, tài liệu; Có thư viện chuyên đề với hơn 15 nghìn bảng sách.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','05', 'Ngày 19-5-1996, nhà vǎn Mạc Phi từ trần. Ông tên thật là Lưu Huy Hoà sinh nǎm 1928 tại Vân Nam (Trung Quốc), vào Đảng Cộng sản Đông Dương nǎm 1946, đã từng phục vụ trong công an và quân đội. \nMạc Phi sống ở Tây Bắc nhiều nǎm và ông đã làm đẹp cho Tây Bắc bằng các tác phẩm của mình. Đáng chú ý nhất là bộ tiểu thuyết \"Rừng động\", và ông còn sưu tầm, dịch ra tiếng việt nhiều truyện thơ dân gian các dân tộc ít người, giá trị nhất là \"Xống chụ Xon Xao\" (Tiễn dặn người yêu) của dân tộc Thái.\nVề hưu ở Hà Nội, ông tiếp tục viết nhiều truyện và đã để lại những ấn tượng riêng về Mạc Phi trong lòng bạn đọc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','06', 'Nhà thơ Lưu Trọng Lư sinh ngày 19-6-1912, quê huyện Bố Trạch, tỉnh Quảng Bình, qua đời ngày 10-8-1991 tại Hà Nội.\nTừ nǎm 1932, Lưu Trọng Lư và một số người khác khởi xướng phong trào Thơ mới. Cùng với Thế Lữ, Phạm Huy Thông, ông đã góp phần làm cho Thơ mới thắng thế.\nTrước Cách mạng Tháng Tám, Lưu Trọng Lư đã xuất bản tập thơ \"Tiếng thu\" (1939) và các tập vǎn xuôi \"Người Sơn nhân\", \"Khói lam chiều\", \"Con voi già của Vua Hàm Nghi\".\nTừ nǎm 1946, ông tiếp tục hoạt động vǎn học, lần lượt cho ra các tập thơ: \"Toả sáng đôi bờ\" (nǎm 1959), \"Người con gái sông Gianh\" (nǎm 1966), \"Từ đất này\" (nǎm 1971), tập hồi ký \"Mùa thu lớn\" (nǎm 1978) và nhiều vở kịch.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','06', '\"Tâm tâm xã\" là một tổ chức cách mạng gồm những nhà cách mạng như Phạm Hồng Thái, Lê Hồng Phong, Hồ Tùng Mậu, Lê Hồng Sơn... Mục đích của tổ chức này là khôi phục quyền độc lập và chủ trương bạo động. Tổ chức được thành lập ở Quảng Châu và cử người liên lạc với sĩ phu và vận động thanh niên ra nước ngoài học tập, nhưng chưa có cơ sở quần chúng trong nước. Để thức tỉnh đồng bào, \"Tâm tâm xã\" cử đồng chí Phạm Hồng Thái tổ chức vụ đánh bom định giết toàn quyền Đông Dương Méclanh.\nTháng 6-1924 Toàn quyền Đông Dương Méclanh sang Nhật bản để điều đình với Nhật trục xuất các nhà cách mạng Việt Nam. Trên dường về, Méclanh cùng đoàn tuỳ tùng dừng lại ở Quảng Châu, dự tiệc đêm 18-6-1924 tại khách sạn Vitoria ở phía Bắc thành phố Sa Điện. Không bỏ lỡ cơ hội, dù biết khó khǎn nguy hiểm, Phạm Hồng Thái giả làm phóng viên nhà báo vào khách sạn, liệng một quả bom nhỏ ngay giữa tiệc. Bom nổ, một số nhân vật Pháp và ngoại quốc chết tại chỗ, nhưng Méclanh chỉ bị thương nhẹ.\nGiữa lúc báo động, Phạm Hồng Thái chạy thoát ra ngoài. Bọn cảnh vệ đuổi theo bắn rát, ông gieo mình xuống dòng Châu Giang hy sinh trong đêm 18 rạng ngày 19-6-1924, khi mới 28 tuổi.\nTrong lúc cuộc đấu tranh giữa con đường cách mạng và con đường cải lương đang diễn ra gay gắt, tiếng bom của Phạm Hồng Thái đã có ý nghĩa chính trị to lớn. Nó có tác dụng thức tỉnh nhân dân ta, kêu gọi nhân dân đứng dậy đấu tranh cách mạng và cảnh cáo mạnh mẽ bọn đế quốc xâm lược.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','06', 'Ngày 19-6-1937, tờ báo \"Sông hương tục bản\" ra số đầu tiên tại Huế. Đây là tờ báo của những người cộng sản, cơ quan công khai của Đảng và do đồng chí Phan Đǎng Lưu trực tiếp chỉ đạo. Từ số đầu tiên và liên tục trong nhiều số đầu, tờ \"Sông hương tục bản\" giành để vận động cho những đại biểu của Đảng ra tranh cử Viện dân biểu Trung Kỳ. Đồng thời tờ báo cũng đề cập tới hàng loạt vấn đề thời sự nóng hổi như Đông Dương đại hội, chống thuế...\nVới những nội dung cách mạng trên nên thực dân Pháp tìm mọi cách để bóp chết tờ báo. Không muốn thay đổi mục đích, những nhà báo cộng sản đã cho tờ \"Sông hương tục bản\" ra số cuối cùng vào ngày 14-7-1937, và tuyên bố tờ báo tự đình bản sau khi đã hoàn tất sứ mạng của mình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','06', 'Từ ngày 27-3-1948, Ban thường vụ Trung ương Đảng đã ra chỉ thị, nêu rõ: \"Mục đích thi đua ái quốc là làm sao cho kháng chiến mau thắng lợi, kiến quốc chóng thành công\". Nhân dịp kỷ niệm 1.000 ngày kháng chiến, ngày 19-6-1948 Hồ Chủ tịch viết lời kêu gọi: \"Bổn phận của người Việt Nam, bất kỳ sĩ, nông, công thương, binh; bất kỳ làm việc gì, đều cần phải thi đua: làm cho mau, làm cho tốt, làm cho nhiều. Mỗi người Việt Nam bất kỳ trẻ già, trai gái; bất kỳ giàu nghèo, lớn, nhỏ đều phải trở nên một chiến sĩ đấu tranh trên một mặt trận: quân sự, chính trị, kinh tế, vǎn hoá. Thực hiện khẩu hiệu \"Toàn dân kháng chiến, toàn diện kháng chiến\". Hồ Chủ tịch nêu mục đích thi đua ái quốc là: Diệt giặc đói khổ; Diệt giặc dốt nát; Diệt giặc ngoại xâm. Cách làm là \"Dựa vào lực lượng của dân, tinh thần của dân\". Kết quả của thi đua ái quốc là: \"toàn dân sẽ đủ ǎn đủ mặc. Toàn dân sẽ biết đọc biết viết. Toàn bộ đội sẽ đầy đủ lương thực, khí giới để giết giặc ngoại xâm. Toàn quốc sẽ thống nhất, độc lập hoàn toàn\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','06', 'Chế Lan Viên tên thật là Phan Ngọc Hoan - một trong những tài nǎng vǎn học lớn của đất nước - tạ thế tại thành phố Hồ Chí Minh ngày 19-6-1989.\nÔng xuất thân trong một gia đình viên chức. Lớn lên giữa \"Gió Lào râm ran rách xé\" và sắn khoai Cam Lộ, Quảng trị. Làm thơ lúc 12 tuổi ở huyện lỵ An Nhơn, nhưng phải đến 16, 17 tuổi xuống Quy Nhơn học trung học, Chế Lan Viên mới thành một người làm thơ thực sự. Chế Lan Viên đã gặp một người anh về thơ là Hàn Mặc tử, và thành lập \"Trường thơ loạn\".\nNǎm 1939, Chế Lan Viên ra Hà Nội học rồi nhảy vào Sài Gòn làm báo, sau đó ra Thanh Hoá và lại lộn về Huế dạy học. Suốt dọc dài của cuộc kháng chiến, Chế Lan Viên trǎn trở lột xác, tìm đường đi cho thơ. Tập \"Gửi các anh\" (1955) không mấy thành công là một thể nghiệm, để rồi \"Ánh sáng và phù sa\" (1960) là một mốc quan trọng vững chãi cắm trên lịch trình thơ của ông.\nVào cuộc trường chinh chống Mỹ cho tới ngày từ giã cõi thế, Chế Lan Viên vừa tham gia lãnh đạo Hội Nhà vǎn Việt Nam vừa làm thơ, là đại biểu Quốc hội các khoá 4, 5, 6, 7 và các hoạt động đối ngoại với nhiều tiếng vang trên các diễn đàn vǎn học quốc tế ở Liên Xô, Pháp, Nam Tư, Tây Đức. Chế Lan Viên khoẻ khoắn xây đắp được những \"Tam đảo - Ba Vì thơ\", trong đó có tập \"Hoa ngày thường, chim báo bão\" (1967) và nhiều tập thơ nổi tiếng khác.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '19','06', 'Paxcan, nhà toán học, triết học Pháp sinh ngày 19-6-1623.\nPaxcan mồ côi mẹ từ khi mới lên 3 tuổi. Bố ông là một người yêu toán học, điều đó đã sớm gây cho ông lòng ham mê và khát vọng nghiên cứu toán học. Do sức khoẻ, ông không được bố hướng dẫn, ông phải tự mình tìm hiểu môn khoa học này. Ông vẽ ra các hình và cố giải thích được tính chất của chúng. Nǎm 12 tuổi ông mới được tham gia nhóm nghiên cứu toán. Nǎm 1640 ông công bố luận vǎn: \"Về tiết diện hình nón\", trong đó có định lý Paxcan. Ông đã rút ra được gần 400 hệ quả từ định lý của mình.\nCũng nǎm 17 tuổi, Paxcan đã chế tạo được một chiếc máy tính làm được bốn phép tính số học. Ông còn là một trong những người sáng lập ra môn \"Thuỷ tinh học\" và là một trong những người đặt viên gạch đầu tiên cho lý thuyết xác suất, một ngành toán học hiện đại có nhiều ứng dụng thực tế. \nÔng mất vào ngày 19-8-1962 khi mới 39 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '19','06', 'Ngày 19-6-1975, Việt Nam và New Zealand thiết lập quan hệ ngoại giao ở cấp đại sứ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','07', 'Tổng thống Pháp ra sắc lệnh thành lập thành phố Hà Nội gồm phần lớn đất đai 2 huyện Thọ Xương và Vĩnh Thuận, ngày 19-7-1888. Về tổ chức hành chính, đứng đầu là một viên Đốc lý do thống sứ Bắc Kỳ đề cử và toàn quyền Đông Dương bổ nhiệm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','07', 'Sau khi cuộc kháng chiến toàn quốc bùng nổ, chiến tranh lan rộng ra nhiều vùng. Đời sống của chiến sĩ, nhất là những chiến sĩ bị thương gặp nhiều khó khǎn, thiếu thốn. Trong tình hình ấy, Đảng và Nhà nước ta đã quyết định nhiều chính sách quan trọng về công tác thương binh liệt sĩ, góp phần ổn định đời sống tinh thần và vật chất của thương binh, gia đình liệt sĩ. Ngày 19-7-1947, Hội đồng Chính phủ quyết định thành lập cơ quan Nhà nước của ta phụ trách công tác thương binh - đó là Bộ Thương binh và cựu binh.\nBộ Thương binh và cựu binh có nhiệm vụ: Phát triển phong trào ủng hộ thương binh, xây dựng chính sách thương tật, hưu bổng, trợ cấp cho gia đình liệt sĩ, tìm việc làm cho thương binh; tổ chức các trại thương binh, an dưỡng cho thương binh; lập xưởng chế tạo dụng cụ chuyên môn cho thương binh...\nNhững nǎm về sau Bộ Thương binh và cựu binh đổi tên là Bộ Thương binh. Và sau này Bộ Thương binh sáp nhập với Bộ Lao động để thành Bộ Lao động - thương binh và xã hội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','08', 'Từ 15 - 8 - 1945 xứ Uỷ Bắc Kỳ trực tiếp chỉ thị cho Hà Nội khởi nghĩa và thành lập uỷ ban khởi nghĩa. Hà Nội tràn ngập không khí cách mạng. Sáng ngày 19-8-1945 hơn 10 vạn đồng bào Hà Nội xuống đường tuần hành thị uy. Một cuộc mít tinh lớn ở Quảng trường nhà hát lớn chuyển thành khởi nghĩa giành chính quyền. Lực lượng quần chúng nhanh chóng chiếm Phủ Khâm Sai, toà thị chính thành phố, trại bảo an và các công sở quan trọng khác.\nCùng ngày với Hà Nội, nhân dân các tỉnh Yên Bái, Thái Bình, Phú Yên, Thanh Hoá, Khánh Hoà cũng vùng lên khởi nghĩa và giành thắng lợi nhanh chóng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','08', 'Cùng với cuộc tổng khởi nghĩa 19-8-1945 ở Hà Nội, lực lượng công an nhân dân Việt Nam đã chính thức được thành lập.\nSinh ra và lớn lên trong bão táp cách mạng, lực lượng công an nhân dân đã góp công lớn trong việc bảo vệ chính quyền nhân dân non trẻ chống thù trong giặc ngoài. Đêm 13-7-1946 lực lượng công an nhân dân Thủ đô đã đập tan âm mưu phản cách mạng định lật đổ chính quyền do bọn phản động \"Việt Quốc\", \"Việt Cách\" tổ chức. Các sào huyệt của chúng ở 80 Quan Thánh, 132 Bùi Thị Xuân, 7 Ôn Như Hầu bị phá tan. Những vụ trừng trị ác ôn ở phố Hàng Vải, Lò Đúc, Phố Huế đã làm bọn giặc kinh hồn khiếp vía.\nNgày nay, lực lượng Công an nhân dân càng thấm nhuần 6 lời dạy của Chủ tịch Hồ Chí Minh, ra sức nâng cao tinh thần cảnh giác, tiến công địch trên mọi mặt, nỗ lực xây dựng và phát triển lực lượng, dựa vào sức mạnh tổng hợp, xây dựng chủ nghĩa xã hội, bảo vệ vững chắc Tổ quốc Việt Nam. Với thành tích xuất sắc, lực lượng Công an nhân dân Việt Nam đã được Nhà nước tặng thưởng Huân chương Sao Vàng và nhiều danh hiệu cao quý khác.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','08', 'Ngày 19-8-1961, đồng chí Nông Vǎn Nhúng (tức Xuân Trường) được Thủ tướng Chính phủ ra quyết định số 337/TTG cấp bằng Tổ quốc ghi công.\nĐây là liệt sĩ đầu tiên của Quân đội nhân dân Việt Nam.\nĐồng chí Nhúng người dân tộc Tày, quê ở xã Sóc Hà, huyện Hà Quảng, tỉnh Cao Bằng, tham gia Cách mạng từ nǎm 1942, là chiến sĩ Đội Việt Nam tuyên truyền giải phóng quân.\nĐồng chí hy sinh tối mồng 4 tháng 2 nǎm 1945 trong trận đánh đồn Đồng Mu, trận đánh thứ ba của đội Việt Nam tuyên truyền giải phóng quân (sau trận Phai Khắt, Nà Ngần). Khi đó đồng chí là tiểu đội trưởng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','08', 'Ngày 19-8-1963, Bảo tàng vǎn hoá các dân tộc Việt Nam (ở thành phố Thái Nguyên) đã mở cửa phục vụ người xem.\nBảo tàng này có diện tích sử dụng 4.000m2, trong đó có 2.000m2 dành cho trưng bày. Sau phần mở đầu giới thiệu khái quát 54 dân tộc cư trú trên đất nước Việt Nam - được phân thành tám nhóm ngôn ngữ, thuộc các ngữ hệ Nam Á, Nam Đảo, Hán - Tạng, Tày - Thái - là các phần nói về đặc trưng vǎn hoá của nhóm Việt Mường, Tày Thái, Mông Dao, Môn - Khme, Hán - Tạng. Mỗi nhóm dân tộc được trình bày theo trình tự: Nguồn gốc lịch sử, địa vực cư trú, nhà cửa, hình thái kinh tế, đặc trưng vǎn hoá vật chất, truyền thống vǎn hoá tinh thần.\nBảo tàng vǎn hoá các dân tộc Việt Nam đang lưu giữ hơn 10 nghìn hiện vật, trong đó có nhiều sưu tập quý như trang phục các dân tộc, công cụ sản xuất, sǎn bắt, hái lượm, nghề thủ công cổ truyền, nghệ thuật dân gian, nhạc cụ dân tộc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','09', 'Duy Tân tức Nguyễn Phúc Vĩnh San, vua thứ 11 nhà Nguyễn, sinh ngày 19-9-1900\nÔng làm vua từ năm 1907 đến năm 1916. Duy Tân là nhà yêu nước, có tinh thần dân tộc. Năm 1916, ông cùng Thái Phiên và Trần Cao Vân trong Việt Nam Quang Phục hội, dựa vào số binh lính bị mộ sang Pháp để tổ chức khởi nghĩa ở Huế và miền Nam Trung Kỳ. Công việc bị bại lộ, Duy Tân bị thực dân Pháp đày sang đảo Rêuyniông (châu Phi). Duy Tân là người ham học có ý chí. Trong chiến tranh thế giới thứ hai, ông gia nhập quân đội đồng minh chống phát xít. Cuối năm 1945, ông mất do tai nạn máy bay ở châu Phi. Đến tháng 4-1987, hài cốt Duy Tân được đưa từ đảo Rêuyniông về thành phố Huế, cải táng bên cạnh vua cha Thành Thai.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','09', 'Nhà văn Nguyễn Đình Lạp có bút danh Yến Đình sinh ngày 19-9-1913, quê ở Bạch Mai - Hà Nội.\nÔng chuyên viết tiểu thuyết xã hội, nổi tiếng trước Cách mạng Tháng Tám. Đến cuộc kháng chiến chống Pháp. Ông tích cực tham gia hội văn nghệ, nhiệt thành đóng góp tâm huyết trên mặt trận văn hoá, văn nghệ. Các tác phẩm của ông là: \"Ngoại ô\" (năm 1941), \"Ngõ hẻm\" (1943). Ngoài ra còn một số truyện ngắn và bài biên khảo đăng trên báo.\nÔng mất ngày 24-4-1952, hưởng dương 39 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','09', 'Ngày 19-9-1954, Hồ Chủ tịch đã nói chuyện với các cán bộ, chiến sĩ đại đoàn 308 trước khi về tiếp quản Thủ đô tại Đền Hùng (Phú Thọ) trong cuộc nói chuyện Bác đã nói: \"Bộ đội ta đánh giặc giỏi, nhưng làm sao phải chiếm được lòng dân, để dân tin cậy, đó là điều quan trọng. Các chú phải lo tiếp quản Thủ đô cho chu đáo... phải bảo vệ tài sản trong Thành phố. Bác cháu ta gặp nhau ở đây tuy tình cờ nhưng lại rất có ý nghĩa: Ngày xưa, các vua Hùng đã có công dựng nước. Ngày nay, Bác cháu ta phải cùng nhau giữ lấy nước\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','09', 'Ngày 19-9-1981, Xí nghiệp liên doanh Dầu khí Việt - Xô bắt đầu thăm dò, khai thác dầu và khí ở thềm lục địa phía Nam nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '19','09', 'Cônxtantin Êđuaôvich Xiôncốpxki sinh năm 1857 và qua đời ngày 19-9-1935 tại Liên Xô.\nCác nhà khoa học thế giới công nhận Xiôncốpxki là ông tổ ngành du hành vũ trụ.\nXuất thân là nhà giáo, từ năm 28 tuổi, ông đã quyết hiến trọn đời mình cho công cuộc nghiên cứu về hàng không vũ trụ. Ngay từ năm 1898, ông đã viết cuốn \"Thám hiểm các khoảng không vũ trụ bằng máy bay phản lực\".\nCách mạng Tháng Mười Nga (năm 1917) đã chắp cánh cho Xiôncốpxki phát huy hết tài năng. Toàn bộ công trình của ông gồm hơn 150 tác phẩm và ông trở thành viện sĩ Viện Hàn lâm khoa học Liên Xô.\nĐáng tiếc sau khi ông qua đời 22 năm, những ước mơ của ông mới thành hiện thực. Năm 1957, vệ tinh nhân tạo đầu tiên của Liên Xô mới được phóng vào vũ trụ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','10', 'Ngày 19-10-1946, khai mạc Hội nghị Quân sự toàn quốc của Đảng.\nHội nghị nhận định: \"Không sớm thì muộn Pháp sẽ đánh mình và mình cũng nhất định phải đánh Pháp\".\nHội nghị khẳng định: Tuy ta kém về kỹ thuật, vũ khí nhưng với tinh thần dẻo dai, bền bỉ, ta nhất định sẽ thắng. Ta cần tăng cường công tác xây dựng Đảng, công tác chính trị của Vệ quốc đoàn, đẩy mạnh việc xây dựng lực lượng vũ trang để sẵn sàng chiến đấu.\nĐây là hội nghị quân sự lớn nhất của Đảng ta từ sau khi giành được chính quyền.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','10', 'Ngày 19-10-1962, Đoàn đại biểu Mặt trận dân tộc giải phóng miền Nam do giáo sư Nguyễn Văn Hiếu dẫn đầu ra thăm miền Bắc, 12 ngày ở trên đất Bắc, đoàn được Hồ Chủ tịch, các đồng chí lãnh đạo Đảng, Nhà nước tiếp thân mật, được các đoàn thể và nhân dân miền Bắc đón tiếp nồng nhiệt. Trong buổi tiếp đoàn ngày 20-10-1962, Hồ Chủ tịch đã nói: \"Hình ảnh của miền Nam yêu quý ở trong trái tim tôi\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','10', 'Ngày 19-10-1973 Chính phủ Cách mạng lâm thời cộng hoà miền Nam Việt Nam công bố chính sách dân tộc gồm 8 điểm:\n1- Thực hiện bình đẳng dân tộc, đoàn kết các lực lượng dân tộc trong đại gia đình các dân tộc Việt Nam.\n2- Ra sức bảo tồn và phát triển các dân tộc anh em\n3- Thực hiện đầy đủ quyền tự do dân chủ....\n4- Tôn trọng tiếng nói, chữ viết của các dân tộc...\n5- Tôn trọng phong tục, tập quán tín ngưỡng, tôn giáo...\n6- Chăm lo quyền lợi về ruộng đất, nương rẫy cho đồng bào...\n7- Ra sức phát triển y tế, vệ sinh phòng bệnh, chữa bệnh bảo vệ bà mẹ trẻ em.\n8- Chăm lo đào tạo, bồi dưỡng đông đảo đội ngũ cán bộ của dân tộc anh em.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','10', 'Nguyễn Văn Huyên sinh năm 1908, quê ở Hà Tây, mất ngày 19-10-1975.\nHồi nhỏ ông học ở Hà Nội, sau sang Pháp học đại học (từ 1926-1935). Ông là người Việt Nam đầu tiên nhận bằng tiến sĩ văn chương tại trường đại học Xoócbon (Pari)\nTrở về nước, ông dạy học, nghiên cứu di sản văn hoá dân tộc. Ông là Uỷ viên Hội đồng nghiên cứu khoa học Đông Dương (từ 1941-1945). Từ năm 1938, ông đã tham gia hội truyền bá quốc ngữ.\nNgay sau Cách mạng tháng Tám (1945), Ông Nguyễn Văn Huyên hoạt động sôi nổi trong lĩnh vực giáo dục văn hoá.\nTừ năm 1946 đến năm qua đời - 1975, ông là Bộ trưởng Bộ Giáo dục của nước Việt Nam dân chủ cộng hoà.\nSự nghiệp giáo dục của nước Việt Nam dân chủ cộng hoà gắn liền với tên tuổi và sự nghiệp của Bộ trưởng Nguyễn Văn Huyên trong suốt gần 30 năm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','10', 'Nữ sĩ Lê Liễu Huê, bút danh Ái Lan, sinh năm 1910, quê ở tỉnh Quảng Trị, mất ngày 19-10-1976 tại thành phố Hồ Chí Minh.\nBà làm thơ viết báo, cộng tác với nhiều báo từ năm 1928 đã xuất bản tập thơ \"Trên đường\" (năm 1953)\nBà Ái Lan - Lê Liễu Huê là người nhiệt tình yêu nước, yêu văn hoá dân tộc. Khi đất nước thống nhất, bà là cố vấn Hội Văn nghệ giải phóng thành phố Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','10', 'Trong dịp đoàn đại biểu Đảng và Nhà nước do đồng chí Trường Chinh dẫn đầu sang thăm chính thức nước Cộng hoà Cuba, ngày 19-10-1982, Hiệp ước hữu nghị và hợp tác Việt Nam - Cuba đã được ký kết tại thủ đô Lahabana. Hiệp ước vạch ra những phương hướng cơ bản nhằm phát triển mối quan hệ hai nước trong sự nghiệp xây dựng chủ nghĩa xã hội và trong cuộc đấu tranh chung vì hoà bình, độc lập dân tộc, dân chủ và chủ nghĩa xã hội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','11', 'Nhà thơ Nguyễn Nhược Pháp sinh nǎm 1914, quê ở làng Phượng Vũ, huyện Thường Tín, tỉnh Hà Tây, qua đời ngày 19-11-1938.\nCác bài phóng sự, truyện ngắn, kịch nói của ông đều có giá trị nghệ thuật, song nổi tiếng vẫn là tập thơ \"Ngày xưa\" (in nǎm 1935) với bài thơ \"Chùa Hương\".\nTrong cuốn \"Thi nhân Việt Nam\" nhà phê bình vǎn học Hoài Thanh có một nhận xét: \"Thơ in ra rất ít mà được người ta yêu mến rất nhiều, tưởng không ai bằng Nguyễn Nhược Pháp\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','11', 'Ngày 19-11-1946, Chủ tịch Hồ Chí Minh đã đến thǎm nhà thương Đồn Thuỷ (nay là bệnh viện Quân đội Trung ương 108). Người ân cần thǎm hỏi bệnh binh, thương binh và động viên anh em yên tâm điều trị.\nTrước đó, Bác Hồ cũng đã đến thǎm các thầy thuốc và bệnh binh nhà thương Đồn Thuỷ vào tháng 2-1946, và lần thứ ba vào tháng 6-1955.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','11', 'Ngày 19-11-1958, ký hiệp định thương mại chính thức đầu tiên giữa Việt Nam và Campuchia.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','11', 'Ngày 19-11-1967, lần đầu tiên không quân Việt Nam bắn rơi một máy bay EB 66 của Mỹ. Biên đội Mig 21 của phi công Vũ Ngọc Đỉnh và Nguyễn Đǎng Kính đã lập chiến công này.\nEB 66 là loại máy bay trinh sát điện tử hiện đại, chức nǎng chính là gây nhiễu, làm giảm hiệu lực chiến đấu của các lực lượng phòng không và không quân của ta. Nó còn trinh sát, phát hiện tần số và vị trí các đài ra-đa, làm vô hiệu hoặc mục tiêu cho máy bay cường kích đánh phá huỷ diệt.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','11', 'Ngày 19-11-1984, tiến hành lễ bàn giao công trình Nhà máy Xi mǎng Bỉm Sơn (Thanh Hoá) do Liên Xô viện trợ thiết bị toàn bộ và cử chuyên gia giúp Việt Nam xây dựng. Đây là công trình lớn đầu tiên được hoàn thành kể từ khi hai nhà nước Việt Nam - Liên Xô ký Hiệp ước hợp tác và hữu nghị ngày 3-11-1978.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '19','11', 'Phranxơ Pêthơ Sube (Franz Peter Sehubert) là nhạc sĩ tài ba và rất danh tiếng trong nửa đầu thế kỷ XIX của Áo. Ông sinh ngày 31-11-1797. Những ca khúc hay nhất của ông: Erlkonig, Gretehen am Spinrnad, Am meer, Du Bist die Rube, Ave Maria...\nÔng còn là tác giả của 200 tác phẩm và có 634 thi ca ngắn, 8 bản hoà tấu, 2 khúc mở màn, 16 xô nát và nhiều khúc mixa, nhiều nhạc kịch và nhiều bản nhạc viết cho đàn piano. Ông qua đời ngày 19-11-1828.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '19','11', 'Ngày 19-11-1990, các nhà lãnh đạo của NATO và của khối VASAVA tuyên bố kết thúc chiến tranh lạnh thông qua việc ký kết Hiệp ước về các lực lượng thông thường và phá huỷ các kho vũ khí trong chiến tranh lạnh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '19','11', 'Anna Dêgơc sinh ngày 19-11-1900 và qua đời nǎm 1983. Bà là nhà vǎn nổi tiếng của Cộng hoà dân chủ Đức trước đây.\nBà vào Đảng cộng sản khi còn là sinh viên. Là người sáng lập Viện Hàn lâm nghệ thuật Đức, bà còn là một nhà tiểu thuyết lớn của Đức và thế giới.\nNhững tác phẩm xuất sắc của Anna Dêgơc đã được dịch sang tiếng Việt có: Cây thập tự thứ bảy, Những người chết còn trẻ mãi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','12', 'Nhà soạn kịch Vũ Đình Long sinh ngày 19-12-1896 quê ở tỉnh Hà Tây, qua đời nǎm 1960 tại Hà Nội.\nTừ nǎm 1936, ông xuất bản nhiều sách báo.\nSau Cách mạng tháng Tám, ông gia nhập Hội vǎn hoá Cứu quốc. Nǎm 1954 ông được bầu làm Uỷ viên Ban Chấp hành Hội nghệ sĩ sân khấu Việt Nam.\nVũ Đình Long đã sáng tác nhiều vở kịch có giá trị, và nổi tiếng với vở \"Chén thuốc độc\" được công diễn ở Hà Nội nǎm 1921. Vở kịch này đã đi vào vǎn học sử và sân khấu Việt Nam với tư cách mở đầu cho kịch nói nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','12', 'Ngày 19-12-1946, tại làng Vạn Phúc thuộc thị xã Hà Đông (Hà Tây) Hồ Chủ tịch đã viết lời kêu gọi toàn quốc kháng chiến chống thực dân Pháp:\nTrong lời kêu gọi có đoạn:\n\"Chúng ta muốn hoà bình, chúng ta phải nhân nhượng. Nhưng chúng ta càng nhân nhượng, Thực dân Pháp càng lấn tới, vì chúng quyết tâm cướp nước ta một lần nữa.\nKhông! chúng ta thà hy sinh tất cả, chứ nhất định không chịu mất nước, nhất định không chịu làm nô lệ!...\"\nĐáp lời kêu gọi của Hồ Chủ tịch, toàn dân, toàn quân Việt Nam đã cầm vũ khí đánh giặc Pháp, cứu nước và sau hơn 9 nǎm chiến đấu trường kỳ đã giành được thắng lợi, kết thúc cuộc kháng chiến chống thực dân Pháp bằng chiến thắng Điện Biên Phủ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','12', 'Chiều 19-12-1946 Bộ trưởng Bộ Quốc phòng kiêm Tổng chỉ huy quân đội quốc gia Võ Nguyên Giáp ra lệnh cho lực lượng vũ trang: \"Giờ chiến đấu đã đến!\" và giao mệnh lệnh nổ súng cho các đơn vị. Đúng 20 giờ 03 phút đèn hiệu thành phố vụt tắt, pháo đài Láng nổ súng hiệu lệnh tổng tiến công. 21h bản mệnh lệnh toàn quốc kháng chiến được phát trên Đài Tiếng nói Việt Nam. Toàn quốc theo mệnh lệnh của Trung ương Đảng và Chính phủ xiết chặt đội ngũ, mài sắc ý chí, đồng loạt nổ súng chiến đấu, mở đầu cho một giai đoạn mới, giai đoạn kháng chiến chống thực dân Pháp trên phạm vi cả nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','12', '19-12-1972, Việt Nam và Madagascar thiết lập mối quan hệ ngoại giao ở cấp đại sứ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '19','12', 'Ngày 19-12-1994, Đảng và Nhà nước ta đã tổ chức trọng thể lễ trao tặng danh hiệu vinh dự Nhà nước: \"Bà mẹ Việt Nam anh hùng\" lần thứ nhất tại Phủ Chủ tịch ở thủ đô Hà Nội.\n59 \"Bà mẹ Việt Nam anh hùng\" tiêu biểu cho mọi miền đất nước, thay mặt gần 2 vạn Bà mẹ được phong tặng danh hiệu lần đầu tiên mời về dự.\nCác vị lãnh đạo cao nhất của Đảng và Nhà nước tặng hoa và trao huy hiệu \"Bà mẹ Việt Nam anh hùng\" đến từng bà mẹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '19','12', 'Ngày 19-12-1948, Anh và Trung Quốc ký hiệp ước về việc trao trả Hồng Kông cho Trung Quốc vào nǎm 1997.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '19','12', '19-12-1955, Quốc hội Sudan tuyên bố độc lập.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '19','12', 'Ngày 19-12-1966 thành lập Ngân hàng phát triển châu Á (Viết tắt theo tiếng Anh là ADB)\nĐây là tổ chức tín dụng quốc tế liên chính phủ của các nước ở châu Á và Thái Bình Dương. Đến nǎm 1990 ADB có 47 nước thành viên, trong đó có Việt Nam. Trụ sở chính của ADB đóng ở Manila (Philippin).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '19','12', 'Ngày 19-12-1984 Đảng và Nhà nước Cộng hoà nhân dân Campuchia đã tặng Huân chương Ǎng Co - Huân chương cao quý nhất của Campuchia cho quân đội nhân dân Việt Nam. Đây là sự ghi nhận xứng đáng của nhân dân Campuchia đối với quân đội nhân dân Việt Nam, những người anh em đã kịp thời giúp đỡ nhân dân và lực lượng cách mạng Campuchia thoát khỏi hoạ diệt chủng do bè lũ Pônpốt gây ra, góp phần đưa đất nước Campuchia xây dựng xã hội phồn vinh, hạnh phúc và hữu nghị.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '19','12', 'Ngày này năm 1997, Tổng thống Hàn Quốc Kim Dae Jung đề nghị đàm phán với nước Cộng hoà Dân chủ Nhân dân Triều tiên, một trong những bước đi đầu tiên trong tiến trình đàm phán giữa hai nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','01', 'Rạng sáng ngày 20-1-1785, quân Xiêm tấn công quân Tây Sơn ở Mỹ Tho, Gia Định. Nguyễn Huệ dử quân địch lọt vào trận địa mai phục ở sông Rạch Gầm - Xoài Mút. Pháo binh Tây Sơn bất ngờ nhả đạn vào các thuyền chiến của địch. Giữa lúc quân địch đang hốt hoảng, rối loạn thì quân thuỷ bộ của ta từ các vị trí mai phục xông ra tiêu diệt chúng. Toàn bộ chiến thuyền của địch bị đánh đắm. Gần 4 vạn quân Xiêm bị giết chết tại trận. Trận thắng Rạch Gầm - Xoài Mút do Nguyễn Huệ chỉ huy đã đập tan âm mưu can thiệp của quân xâm lược Xiêm và trừng trị đích đáng hành động bán nước của bọn Nguyễn Ánh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','02', 'Từ ngày 20 đến 28-2-1957, Đại hội vǎn nghệ toàn quốc lần thứ hai, gần 500 đại biểu các ngành vǎn nghệ trên miền Bắc tham dự. Đại hội đã đấu tranh mạnh mẽ với luận điệu phản động \"Nhân vǎn - Giai phẩm\". Vạch rõ ranh giới bạn, thù chống lại âm mưu phá hoại của kẻ địch và bọn tay sai.\nĐại hội quyết định lập Hội Liên hiệp Vǎn học nghệ thuật và các hội riêng của từng ngành vǎn học, nghệ thuật để thay thế cho tổ chức vǎn nghệ cũ. Trong BCH Hội Liên hiệp Vǎn học nghệ thuật do Đại hội bầu ra gồm 55 người, có 10 ghế dành cho vǎn nghệ sĩ miền Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','02', 'Ngày 20-2-1959, Chủ tịch Hồ Chí Minh đi thǎm công trường xây dựng công trình thuỷ lợi Bắc - Hưng - Hải.\nĐến cống Xuân Quan, Bác Hồ xem xét từng hạng mục công trình và nghe ban chỉ huy công trường báo cáo về tình hình thi công.\nCùng ngày 20-2-1959, Bác Hồ còn đến thǎm làng gốm Bát Tràng (ở huyện Gia Lâm, Hà Nội) và hợp tác xã thủ công nghiệp ở đó. Bác cǎn dặn xã viên \"phải cố gắng thi đua sản xuất tốt, hạ giá thành, thu nhập của mỗi xã viên phải cao hơn người làm ǎn riêng lẻ\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','02', 'Ngày 20-2-1963, khánh thành đường hàng không Việt - Lào nối liền Thủ đô Hà Nội với Thủ đô Viêng Chǎn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '20','02', 'Ngày 20-2-1943, tại làng Paricutin (Mêhicô), núi lửa hoạt động cao tới 180 mét đã chồn vùi cả làng Paricutin.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','03', 'Vào ngày 20-3-1926, (có tài liệu nói là ngày 23-3-1926) báo Nước Nam Trẻ ra số đầu tiên, cũng là số duy nhất. Báo mang tiêu đề \"Diễn đàn giải phóng dân tộc\", xuất bản tại Sài Gòn. Điều đáng chú ý là khuynh hướng chống chính quyền thực dân. Cũng tại số báo này có đǎng lại một bài trên báo Le Paria (Người cùng khổ) ký tên Nguyễn Ái Quốc: \"chủ nghĩa đế quốc Pháp ở vùng biển Viễn Đông\". Báo còn trích đǎng bài của báo Nhân đạo của Đảng Cộng sản Pháp. Báo Nước Nam Trẻ ngay số đầu đã chứng tỏ là một tờ báo tiến bộ, vì lẽ đó đã bị chính quyền thực dân ra lệnh đình bản và thu hồi giấy phép.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','03', 'Ngày 20-3-1929, công nhân xưởng sợi Nam Định tiến hành cuộc bãi công đòi tǎng lương và phản đối chủ đàn áp. Cuộc bãi công kéo dài hơn một tuần đã khiến chủ nhà máy phải nhượng bộ. Kết quả giới chủ đã tǎng 20% lương cho công nhân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','03', 'Trong cao trào Xô Viết Nghệ Tĩnh, tổ chức Nông hội đỏ phát triển mạnh, đến tháng 3-1931 đã có tới 64.000 hội viên và trở thành nòng cốt của phong trào đấu tranh nông dân. Tuy nhiên, Nông hội đỏ ở một số nơi đã bộc lộ sai lầm hữu khuynh, có nguy cơ để bọn phú nông lũng đoạn. Ngày 20-3-1931, Ban thường vụ Trung ương Đảng cộng Sản Đông Dương đã ra chỉ thị về vấn đề chỉnh đốn Nông hội đỏ. Bản chỉ thị nhắc nhở các cấp ủy địa phương quán triệt chủ trương củng cố khối bần cố nông, đoàn kết với trung nông và bảo đảm quyền lãnh đạo của giai cấp vô sản.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','03', 'Ngày 20-3-1951, mở đầu chiến dịch Hoàng Hoa Thám. Trong chiến dịch này quân ta đã huy động 7 trung đoàn đánh vào phòng tuyến địch trên đường số 18 (khu vực từ Phả Lại đến Uông Bí). Trong thời gian hơn nửa tháng, quân ta đã tiêu diệt được một phần binh đoàn cơ động thứ 7 và phá vỡ một mảng hệ thống phòng ngự của địch trên đường số 18, 20, 21. Ta thu được 409 súng các loại, phá huỷ 49 xe cơ giới, 6 xe tǎng và thiết giáp.\nĐịch bị tiêu diệt 14 vị trí lớn nhỏ và bị bức rút 3 vị trí ở vùng mỏ giàu có là Uông Bí, Mạo Khê, Tràng Bạch.\nĐây là một trong những chiến dịch quy mô lớn đầu tiên của bộ đội ta đánh vào phòng tuyến Boongke kiên cố của Đờlát Đờtátxinhi ở Bắc Bộ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','03', 'Ngày 20-3-1967, trừng phạt pháo binh Mỹ từ bờ Nam sông Bến Hải bắn sang miền Bắc, bộ đội pháo binh ở Vĩnh Linh đã bắn pháo sang Cồn Tiên, diệt 1070 tên Mỹ, phá huỷ 17 đại bác, 57 xe quân sự, 5 máy bay lên thẳng, và 1 kho xǎng. Đây là một trận đánh táo bạo, bí mật, bất ngờ, khiến quân Mỹ phải thú nhận thất bại. Đồng thời chứng tỏ khả nǎng giáng trả kịp thời và chính xác của pháo binh ta. Cho thấy pháo binh Việt Nam đã phát triển mạnh cả về trang bị và cách đánh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '20','03', 'Ngày 20-3-1943, đồng minh Anh - Mỹ tấn công Đức ở Bắc Phi.\nTừ tháng 4 đến tháng 11 nǎm 1941, quân phát xít Đức đánh đuổi quân Anh đến biên giới Ai Cập. Sau khi thất bại ở Mátxcơva tháng 12-1941, Hítle phải rút binh lực ở Bắc Phi về tǎng cường cho mặt trận ở Liên Xô. Mặt khác, Mỹ cũng có ý đồ đổ bộ vào Bắc Phi để tranh giành ảnh hưởng với quân Anh ở khu vực này.\nNgày 20-3-1943, quân Mỹ và Anh mở cuộc tấn công quân Đức ở Bắc Phi dồn quân Đức đến bán đảo Bon ở phía Bắc Tuynidi. Không đủ sức chống cự, ngày 13-5-1943 quân Đức phải hạ khí giới đầu hàng. Chiến sự ở Bắc Phi chấm dứt.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '20','03', '20-3-1956, Tunisia được trao trả độc lập hoàn toàn theo nghị định ký với Pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '20','03', 'Ngày 20-3-1970, tổ chức hợp tác vǎn hoá và kỹ thuật (gọi tắt là ACCT) của nước sử dụng tiếng Pháp được thành lập tại Niamây (Thủ đô nước Nigiê). Đến nay ACCT tập hợp được 41 quốc gia.\nHoạt động của ACCT nhằm thúc đẩy sự phát triển và phổ biến nền vǎn hoá dân tộc của các nước thành viên, đẩy mạnh giao lưu và hợp tác vǎn hoá khoa học, kỹ thuật theo phương châm \"đoàn kết, bình đẳng, bổ sung\". Các dự án của ACCT tập trung vào các lĩnh vực nông nghiệp, lâm nghiệp, nǎng lượng, thông tin, khoa học kỹ thuật, giáo dục, đào tạo, truyền thông, môi trường, ngoại ngữ, vǎn hoá dân tộc.\nViệt Nam gia nhập ACCT từ nǎm 1979 và đã được tổ chức này hợp tác và giúp đỡ về vǎn hoá và kỹ thuật. Ngày 20-3 hàng nǎm được coi là ngày Quốc tế nói tiếng Pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '20','03', '20-3-1990, Namibia giành độc lập từ Nam Phi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '20','03', '10h15 ngày 20-3-2003 : Tổng thống Mỹ George W. Bush và Thủ tướng Anh Tony Blair tuyên bố giai đoạn đầu của chiến dịch giải giáp Iraq bắt đầu mặc dù chưa có sự đồng ý của Liên hợp quốc. Trước đó, tối 17/3, Tổng thống George Bush đã ra tối hậu thư cho Tổng thống Irắc Saddam Hussein rằng ông này phải chấp nhận sống lưu vong sau 48 giờ, nếu không sẽ phải hứng chịu toàn bộ sức mạnh quân sự của Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','04', 'Nhà thơ Tản Đà - tên thật là Nguyễn Khắc Hiếu sinh ngày 20-4-1889 và mất nǎm 1931. Ông quê ở làng Khê Thượng, huyện Bất Bạt, tỉnh Sơn Tây cũ (nay là tỉnh Hà Tây). Quê ông là một làng nhỏ ven sông Đà, dưới chân núi Tản Viên. Sự hoà quyện hữu tình non nước quê hương đã tạo nên bút danh thi sĩ Tản Đà.\nSinh thời tài nǎng của Tản Đà thật phong phú. Ông làm thơ viết vǎn, làm báo, dịch chữ hán, soạn kịch - ở lĩnh vực nào ông cũng thành công. Nhưng có lẽ đỉnh cao tài nǎng của ông chính là thơ. Ông sống nghèo túng và đi nhiều nơi trên đất nước. Ông mất lúc 51 tuổi và để lại các tác phẩm chính như: Giấc mộng lớn, Giấc mộng con, Thề non nước, Vương Thuý Kiều chú giải tân truyện, Liêu trai chí dị...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','04', 'Ngày 20-4-1931, đồng chí Nguyễn Ái Quốc viết \"Thư gửi Ban chấp hành Trung ương Đảng\". Trong bức thư này, Người đã đề nghị một loạt các biện pháp nhằm đáp ứng những yêu cầu của cao trào cách mạng như: Thành lập Ban phụ trách tuyên truyền giáo dục, tǎng cường phát triển Đảng, tǎng cường thành phần công nhân cũng như hoạt động của các tổ chức quần chúng. Đặc biệt quan trọng là Người đã đề ra việc thành lập Hội phản đế đồng minh thành một Mặt trận dân tộc rộng rãi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','04', 'Ngày 20-4-1963. Hồ Chủ tịch đã về thǎm xí nghiệp X40 (nay là Công ty may 40).\nSau khi đi xem nơi sản xuất, Bác nói chuyện với công nhân, cán bộ ở đây Bác khen xí nghiệp đã có nhiều thành tích và kinh nghiệm về công tác phát triển Đảng, cǎn dặn mọi người thi đua sản xuất, tǎng nǎng xuất lao động, nâng cao chất lượng phục vụ các mặt hàng phục vụ quân đội, lãnh đạo xí nghiệp phải chǎm lo đời sống của công nhân viên chức.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','04', 'Ngày 20-4-1972, quân và dân Quảng Bình đã bắn rơi một máy bay F4. Đây là chiếc máy bay Mỹ thứ 3.500 bị bắn rơi trên miền Bắc nước ta.\nTrong hai lần chống chiến tranh phá hoại của Mỹ đối với miền Bắc, quân và dân Quảng Bình đã bắn rơi tất cả 700 máy bay Mỹ, trong đó có 3 chiếc B52 và 1 chiếc F111, là tỉnh đứng đầu bảng vàng lập công của miền Bắc; ngoài ra còn 79 lần bắn chìm, bắn cháy, bắn bị thương tàu chiến và tàu biệt kích của dịch.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','04', 'Ngày 20-4-1976, Báo Nhân dân, cơ quan Trung ương của Đảng Cộng sản Việt Nam bắt đầu được in tại thành phố Hồ Chí Minh để kịp phát hành kịp thời và rộng rãi ở miền Nam trong thời gian cùng ngày.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','04', 'Thượng tướng Trần Vǎn Trà sinh nǎm 1919 tại làng Tịnh Long, huyện Sơn Tịnh, tỉnh Quảng Ngãi và từ trần ngày 20-4-1996 tại thành phố Hồ Chí Minh. \nLà một cán bộ quân sự xuất sắc, thượng tướng Trần Vǎn Trà đã có nhiều cống hiến vào hai cuộc kháng chiến bảo vệ và giải phóng đất nước, được chiến sĩ, đồng bào cả nước yêu mến.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','05', 'Đạo Thiên chúa được truyền giáo vào Việt Nam từ nửa sau thế kỷ XVI. Cho đến đầu thế kỷ XX Giáo hội Việt Nam đã hình thành một số địa phận như Hải Phòng, Bắc Ninh, Phát Diệm, Lạng Sơn... Tuy nhiên ảnh hưởng của Vaticǎng còn bị hạn chế. Để tǎng cường quyền lực của mình, từ nǎm 1923 giáo hoàng đã cử tổng giám mục Lơri sang điều tra về giáo hội Việt Nam. Và ngày 20-5-1925 giáo hoàng Piô-9 đã tấn phong chức Khâm sứ đầu tiên ở Đông Dương cho giám mục Agiuti. Nhưng mãi tới nǎm 1933 Linh mục Nguyễn Bá Tòng mới là người Việt Nam đầu tiên được thụ phong giám mục.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','05', 'Trước tình hình một số quần chúng và Đảng viên dao động do bị địch khủng bố, Xứ ủy Trung Kỳ đã chủ trương \"Thanh Đảng\" nhằm loại trừ những phần tử phản bội, tuy nhiên chủ trương này đã bộc lộ sai lầm tả khuynh. Ngày 20-5-1931 Ban Chấp hành Trung ương Đảng Cộng sản Đông Dương đã chỉ thị cho Xứ ủy Trung Kỳ. Bản chỉ thị đề cập một loạt vấn đề có tính nguyên tắc và lý luận xây dựng Đảng nhằm uốn nắn sai lầm của Xứ ủy Trung Kỳ, tǎng cường sức chiến đấu của Đảng và quần chúng.\nCuối tháng 5-1942 từ Pác Pó, đồng chí Nguyễn Ái Quốc chuyển dịa điểm hoạt động xuống khu Lam Sơn (huyện Hoà Anh và huyện Nguyên Bình tỉnh Cao Bằng). Tại đây, Người đã mở lớp huấn luyện về lý luận và công tác Đảng cho cán bộ địa phương, chuẩn bị lực lượng cán bộ cho khu giải phóng sau này.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','05', 'Ngày 20-5-1948 ta mở hội nghị cán bộ Trung ương lần thứ 4 (miền Bắc - Đông Dương); Hội nghị đã nghe báo cáo thảo luận và ra nghị quyết về 6 vấn đề cụ thể: Về kế hoạch quân sự mùa hè dự đoán địch tiến công nhiều hơn phòng ngự để hỗ trợ việc lập chính phủ bù nhìn. Do đó kế hoạch đối phó của ta là tích cự bổ sung bộ đội, phát triển dân quân, cải thiện dân sinh. Hội nghị đề ra kế hoạch cụ thể về kinh tế - xã hội và vǎn hoá. Về công tác trong vùng địch kiểm soát và chiếm đóng là một bộ phận rất quan trọng trong toàn bộ công tác của Đảng. Hội nghị vạch rõ hướng tuyên truyền, cổ động, công việc tổ chức, tranh đấu, vấn đề cán bộ, vấn đề dân sinh, về công tác Việt Minh và Liên Việt và cuối cùng là tổ chức trong Đảng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '20','05', 'Crixtốp Côlông (Columbus Christopher) là nhà hàng hải Itali, phục vụ triều đình Tây Ban Nha, thực hiện chuyến thám hiểm đầu tiên qua Đại Tây Dương và tìm ra châu Mỹ. Ông sinh nǎm 1451.\nSinh thời, Côlông thường có những suy nghĩ táo bạo và lãng mạn, luôn mơ ước vượt trùng dương tới những miền đât xa lạ. Nǎm 1476 ông đề xuất dự án vượt đại dương với vua Bồ Đào Nha, nhưng không được chấp thuận. Ông sang Tây Ban Nha và được vua Tây Ban Nha cấp kinh phí để thực hiện cuộc thám hiểm.\nNgày 3-8-1492, Côlông cầm đầu đoàn thuyền tìm đường sang Ấn Độ. Đoàn thuyền đến được quần đảo Bahama, Cuba, Haiti. Tháng 3-1493, đoàn thuyền trở về Tây Ban Nha, ông được triều đình và nhân dân đón tiếp trọng thể. Ông được vua phong phó vương và các thuộc địa ở Tân Lục Địa.\nSau đó Côlông còn thực hiện 3 chuyến thám hiểm nữa. Ông đã khám phá ra hầu hết các đảo trên quần đảo Ǎngti và cả bờ biển trung Mỹ. Nhưng số vàng bạc của cải ông mang về cho vua Tây Ban Nha quá ít ỏi nên không được trọng vọng. Ngày 20-5-1506, ông mất tại một thành phố nhỏ ở miền Bắc Tây Ban Nha trong sự nghèo khổ và lãng quên.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '20','05', 'Ônôrê đờ Bandắc, nhà vǎn hiện thực lớn của nước Pháp sinh ngày 20-5-1799. Ông sinh ra và lớn lên tại thành phố Tua nước Pháp. Sau khi tốt nghiệp đại học luật khoa ông làm thông sự ở toà án. Sau thấy mình có thiên hướng viết vǎn, ông chuyển sang viết vǎn. Ông làm việc hết sức cần cù. Ông viết di viết lại, sửa chữa nhiều lần những trang bản thảo của mình. Ông cũng dành nhiều công sức nghiên cứu, quan sát cuộc sống và đọc sách tham khảo. Trong hơn 20 nǎm cặm cụi, ông đã viết tới 96 cuốn, tập hợp thành bộ sách mang tên \"Tấn trò đời\".\n\"Tấn trò đời\" là bức tranh rộng lớn, đa dạng, miêu tả trung thực sinh động xã hội Pháp ở nửa đầu thế kỷ XIX. Bandắc đã lột trần những thủ đoạn làm giàu của giai cấp tư sản. Đối với giai cấp tư sản giàu có là hình ảnh đáng thương của những người bình dân chỉ mong muốn một cuộc sống yên ổn mà không được.\nTrong kho tàng đồ sộ này, các tiểu thuyết đặc biệt xuất sắc là \"Miếng da lừa\", \"Ơgiêni Grǎngđê\", \"Lão Gôriô\", \"Vỡ mộng\", \"Trời không có mắt\"...\nBandắc mất vào ngày 18-8-1850')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '20','05', 'Nhân dân Cǎmpuchia lấy ngày 20 tháng 5 hàng nǎm là ngày cǎm thù chế độ diệt chủng Pônpốt.\nTừ ngày lên cầm quyền (tháng 4-1975) đến ngày bị lật đổ (tháng 1-1979), trong 3 nǎm 8 tháng, 20 ngày, tập đoàn Pônpốt đã giết hại hơn 3 triệu người Cǎmpuchia, làm cho hơn 140 nghìn người bị tàn phế, hơn 200 nghìn trẻ bị mồ côi, hơn 600 nghìn ngôi nhà bị phá huỷ. Cơ cấu xã hội, nền vǎn hoá dân tộc, cơ sở kinh tế bị đảo lộn đến tận gốc rễ. Lý trí, tình cảm, nhân phẩm của con người bị chà đạp thô bạo nhất. Lịch sử dân tộc Cǎmpuchia bị đẩy lùi hàng thập kỷ.\nTội ác của tập đoàn Pônpốt bị toàn thế giới lên án, cǎm ghét và phỉ nhổ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','06', 'Từ ngày 20 đến 24-6-1867, chỉ trong 5 ngày, quan quân triều đình Huế để mất 3 tỉnh Vĩnh Long, An Giang và Hà Tiên.\nCòn nhớ, nǎm 1862, triều đình Huế đã ký hiệp ước nhượng hẳn cho Pháp 3 tỉnh miền đông (Gia Định, Định Tường, Biên Hoà). Sau đó miền Bắc muốn chiếm nốt 3 tỉnh miền Tây bằng cách tiêu diệt các cuộc khởi nghĩa còn lại ở 3 tỉnh miền Đông. Còn triều đình Huế muốn chuộc lại 3 tỉnh miền Đông bằng thương thuyết, không dựa trên sức mạnh chiến đấu của nhân dân, thậm chí còn cô lập nghĩa quân, truy nã những người khởi nghĩa, tiếp tay cho Pháp. \nSau khi mất 3 tỉnh miền Tây, Phan Thanh Giản, một quan triều đình Huế được giao nhiệm vụ thương thuyết với Pháp, đã nhịn ǎn 17 ngày, rồi uống thuốc tự tử. Vua Tự Đức muốn xoá tội của mình nên tước hết chức của Phan Thanh Giản và đục bỏ tên ông trên bia tiến sĩ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','06', 'Nhạc sĩ Vǎn Chung, tên thật là Mai Vǎn Chung, sinh ngày 20-6-1914, quê ở tỉnh Hải Dương, qua đời nǎm 1984 tại Hà Nội.\nÔng là một trong những nhạc sĩ đàn anh của các thế hệ nhạc sĩ Việt Nam.\nNhạc sĩ Vǎn Chung bắt đầu sáng tác từ nǎm 21 tuổi, trong đó có nhiều ca khúc nổi tiếng như: \"Tiếng sáo chǎn trâu\", \"Bóng ai qua thềm\", \"Hồ xuân và thiếu nữ\", \"Hò dân cày\", \"Vào Đông Khê\", \"Gái thôn Đoài - trai thôn Thượng\", \"Quê tôi giải phóng\", \"Ba cô gái đảm\", \"Lúa cấy thẳng hàng\"... và một số ca khúc dành cho thiếu nhi như: \"Lỳ và sáo\", \"Đêm sao\", \"Lượn tròn lượn khéo\", \"Trǎng theo em rước đèn\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','06', 'Ngày 20-6-1979, Bộ Ngoại giao ta đã ra tuyên bố về vấn đề người Việt Nam chạy ra nước ngoài. Bản tuyên bố đã vạch rõ nguyên nhân về một số người Việt Nam chạy ra nước ngoài làm cho hậu quả của cuộc chiến tranh xâm lược và chế độ thực dân mới của đế quốc Mỹ trước đây, của những âm mưu thủ đoạn cực kỳ nham hiểm của các thế lực thù địch với Việt Nam. Bản tuyên bố cũng nêu rõ chủ trương và chính sách đúng đắn của Chính phủ ta về vấn đề người Việt Nam chạy ra nước ngoài.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '20','06', 'Chuyến bay đầu tiên trên thế giới từ Liên Xô sang Mỹ không đỗ dọc đường được hoàn thành vào ngày 20-6-1937, bằng máy bay ANT - 25 cánh đỏ do Valeri Tscalôp, Anh hùng Liên Xô lái cất cánh từ Mátxcơva ngày 18-6-1937, bay vượt qua bắc cực sang Mỹ trong 27 giờ. Nǎm sau Tscalôp hy sinh trong khi bay thử nghiệm một loại máy bay khu trục mới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','07', 'Luật sư Trịnh Đình Thảo sinh ngày 20-7-1901 tại Hà Đông. Thời trẻ, ông học ở Hà Nội rồi du học bên Pháp. Tốt nghiệp cử nhân vǎn chương, cao học kinh tế và thương mại, tiến sĩ luật khoa, nǎm 1929 ông về nước, làm luật sư tại toà Thượng thẩm Sài Gòn hơn 40 nǎm. Trong các lần bào chữa các vụ án chính trị, ông tận tình bênh vực những người yêu nước nên được đồng bào quý mến, giới trí thức kính trọng. Nǎm 1945 ông là Bộ trưởng trong nội các Trần Trọng Kim. Cách mạng Tháng Tám thành công, ông trở lại với nghề luật sư.\nSau nǎm 1945 ông tham gia hoạt động nội thành Sài Gòn. Nǎm 1968 ông ra chiến khu tham gia thành lập \"Liên minh các lực lượng dân tộc, dân chủ, hoà bình Việt Nam\" nhằm động viên chấm dứt chiến tranh.\nÔng từng giữ các chức vụ; Chủ tịch Liên minh các lực lượng dân tộc, dân chủ hoà bình Việt Nam, Phó chủ tịch Hội đồng cố vấn chính phủ lâm thời cộng hoà miền Nam Việt Nam, Uỷ viên Đoàn chủ tịch Uỷ ban Trung ương Mặt trận Tổ quốc Việt Nam. Đại biểu Quốc hội nước Cộng hoà xã hội chủ nghĩa Việt Nam. Ông đã được Nhà nước tặng thưởng nhiều huân chương cao quý.\nNgày 31-3-1986 ông mất tại thành phố Hồ Chí Minh thọ 85 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','07', 'Cuốn \"Tự chỉ trích\" do đồng chí Nguyễn Vǎn Cừ với bút danh là Trí Cường, Tổng Bí thư Đảng cộng sản Đông Dương viết tại Hà Nội, ở một gác xép trên sân thượng nhà bǎng Đông Dương (nay là Ngân hàng Quốc gia Việt Nam) và được nhà xuất bản Dân Chúng phát hành ngày 20-7-1939. Cuốn sách nhằm chỉ đạo kịp thời đảng viên và quần chúng trong tình hình mới. Cuốn sách đề cập đến các vấn đề: Nguyên tắc phê và tự phê của Đảng, một số bài học rút ra từ các cuộc vận động tuyển cử, đấu tranh bảo vệ đường lối Mặt trận Dân chủ chống tả và hữu khuynh, tóm tắt đường lối chiến lược và sách lược của Mặt trận Dân chủ.\nĐây là một tác phẩm lý luận tổng kết kinh nghiệm chủ yếu của Mặt trận Dân chủ Đông Dương và có ý nghĩa chỉ đạo thực tiễn trong phong trào Cách mạng Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','07', 'Ngày 20-7-1954 Hội nghị Giơnevơ về Đông Dương kết thúc. Các bản hiệp định đình chiến ở Việt Nam, Campuchia và Lào đã được ký kết. Các nước tham gia Hội nghị thông qua Bản tuyên bố chung thừa nhận: Tôn trọng độc lập, chủ quyền, thống nhất và toàn vẹn lãnh thổ của Việt Nam, Campuchia và Lào, quy định quân đội Pháp phải rút khỏi các nước Đông Dương và ở mỗi nước Đông Dương sẽ tổ chức Tổng tuyển cử tự do để thống nhất đất nước.\nBản tuyên bố chung có ghi rõ: ở Việt Nam \"đường ranh giới về quân sự chỉ có tính chất tạm thời, không để coi như là một biên giới chính trị hoặc lãnh thổ\", và quy định thời hạn dứt khoát về việc tổ chức tổng tuyển cử thống nhất nước Việt Nam vào tháng 7-1956.\nHội nghị Giơnevơ thành công là thắng lợi to lớn của nhân dân Đông Dương, nhân dân yêu chuộng hoà bình thế giới, đồng thời là đòn giáng mạnh vào âm mưu mở rộng và kéo dài chiến tranh Đông Dương của Đế quốc Mỹ. Hội nghị Giơnevơ thắng lợi, hoà bình được lập lại ở Đông Dương, nhân dân miền Bắc được hoàn toàn giải phóng, đó là một bước ngoặt quan trọng đối với Cách mạng Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','07', 'Ngày 20-7-1959, theo sáng kiến của Hội đồng đoàn kết nhân dân Á Phi họp ở Lơ Ke (Ai Cập) là ngày nhân dân thế giới ủng hộ nhân dân Việt Nam đấu tranh thống nhất Tổ quốc. \"Ngày Việt Nam\" đầu tiên đã được tổ chức của trên hai mươi nước tổ chức trọng thể. Từ đó cứ đến ngày 20-7 hàng nǎm, ngày kỷ niệm ký kết Hiệp định Giơnevơ, nhân dân thế giới lại tổ chức \"Ngày Việt Nam\" để ủng hộ nhân dân ta trong cuộc đấu tranh thống nhất đất nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','07', 'Ngày 20-7-1962 Chủ tịch Hồ Chí Minh ký lệnh công bố hai pháp lệnh \"quy định nhiệm vụ, quyền hạn\" và \"quy định chế độ cấp bậc sĩ quan và hạ sĩ quan cảnh sát nhân dân Việt Nam\". Ngày đó trở thành ngày truyền thống của lực lượng Cảnh sát nhân dân.\nDưới sự lãnh đạo của Đảng, được sự giúp đỡ đùm bọc của nhân dân, sự phối hợp của các ngành, các đoàn thể quần chúng, lực lượng Cảnh sát nhân dân sát cánh với Quân đội nhân dân đã hoàn thành tốt nhiệm vụ và không ngừng trưởng thành về nhiều mặt. Đã tiến hành đồng bộ nhiều biện pháp tiến công nhằm trấn áp bọn tham ô, trộm cắp, buôn lậu, lưu manh côn đồ... và bài trừ các tệ nạn xã hội, quản lý tốt trật tự công cộng. Kế thừa và phát huy truyền thống tốt đẹp của Công an nhân dân, các lực lượng Cảnh sát nhân dân đã được Quốc hội và Chính phủ tuyên dương danh hiệu Anh hùng lực lượng vũ trang nhân dân và tặng thưởng huân chương Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','07', 'Nguyễn Đổng Chi, nhà nghiên cứu vǎn hoá, sinh ngày 6-1-1915 tại xã Hậu Lộc, Can Lộc, Hà Tĩnh.\nÔng là Giáo sư tận tình, nhiệt tình với việc vun bồi nền vǎn hoá Việt Nam, từng tham gia hoạt động cứu quốc trước nǎm 1945. Sau Cách mạng Tháng Tám, ông từng giữ các chức vụ: Chủ tịch Hội Vǎn nghệ Cứu quốc Nghệ An, Giám đốc Nhà xuất bản Dân chủ mới - Liên khu 4, Viện trưởng Viện Hán Nôm và là Chuyên viên ở Viện Vǎn hoá dân gian, Uỷ viện chấp hành Hội Vǎn nghệ dân gian. Trong thời gian làm việc ở Viện Sử, ông nghiên cứu phong trào nông dân trong lịch sử và từng phát hiện di chỉ đồ đá núi Đọ.\nCác tác phẩm chính của ông gồm có: \"Việt Nam cổ vǎn học sử\", \"Mọi KonTum\", \"Hát dặm Nghệ Tĩnh\", \"Lược khảo về thần thoại Việt Nam\", \"Kho tàng chuyện cổ tích Việt Nam\", \"Sơ khảo lịch sử vǎn học Việt Nam\", \"Thời đại Hùng Vương\", \"Góp phần tìm hiểu phong trào nông dân Việt Nam trong lịch sử xã hội phong kiến\", \"Địa chí vǎn hoá dân gian Nghệ Tĩnh\". v.v...\nÔng mất ngày 20-7-1984, thọ 69 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '20','07', 'Ngày 20-7-1969, hai nhà du hành vũ trụ Mỹ - Amxtrong và Anđriu - lần đầu tiên đặt chân lên mặt trǎng trên con tàu vũ trụ Apollo 11. Từ mặt trǎng vọng về trái đất câu nói nổi tiếng của Amxtrong \"Một bước đi của con người ở đây là bước nhảy vọt khổng lồ của nhân loại\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','08', 'Trương Định (tức Trương Công Định) sinh nǎm 1820 tại Quảng Ngãi. Nǎm 1850 hưởng ứng chính sách khẩn hoang của Nguyễn Tri Phương, ông đứng ra chiêu mộ dân nghèo khai hoang lập ấp. Vì có công, ông được triều đình Huế phong chức Quảng cơ, hàm lục phẩm. Khi giặc Pháp chiếm đánh thành Gia Định, ông đem nghĩa quân chặn giặc nên được thǎng chức Phó Lãnh binh và cùng chiến sĩ xây dựng cǎn cứ chống Pháp ở Gò Công. Triều đình Huế cắt ba tỉnh miền Đông Nam Kỳ cho Pháp, ông cùng nhân dân chống lại. Nghĩa quân và nhân dân vùng Gò Công, Tân An, Mỹ Tho... tôn xưng ông là \" Bình Tây Đại Nguyên Soái\". Thực dân Pháp nhiều lần huy động quân lực bao vây Gò Công, mặt khác tìm mọi cách dụ ông ra hàng.\nTừ ngày 26-2-1863 Pháp tấn công mạnh vào cǎn cứ nghĩa quân. Ngày 20-8-1864, trong một trận đánh, sau khi bị bắn gẫy xương sống, ông rút gươm tự sát, không chịu để giặc bắt sống. Cái chết của ông là một mất mát lớn lao cho lực lượng kháng chiến lúc bấy giờ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','08', 'Đồng chí Tôn Đức Thắng là nhà hoạt động nổi tiếng của Đảng và Nhà nước Việt Nam, một chiến sĩ xuất sắc của phong trào công nhân quốc tế, người tiêu biểu cho chính sách đại đoàn kết của Đảng và Hồ Chủ tịch.\nĐồng chí sinh ngày 20-8-1888 trong một gia đình nông dân nghèo ở làng Mỹ Hoà Hưng, tỉnh Long Xuyên. Nǎm 1910, sau mấy nǎm học nghề ở trường bách nghệ, đồng chí làm công nhân một xưởng máy của Pháp ở Sài Gòn. Nǎm 1926, đồng chí tham gia Việt Nam thanh niên cách mạng đồng chí Hội và nǎm sau được cử vào Ban Chấp hành kỳ bộ Nam Kỳ.\nCuối nǎm 1929, đồng chí bị thực dân Pháp bắt giam ở khám lớn Sài Gòn, rồi đầy ra Côn Đảo cho tới khi Cách mạng tháng Tám nǎm 1945 thành công.\nTừ nǎm 1955, đồng chí Tôn Đức Thắng là Chủ tịch Uỷ ban Trung ương Mặt trận Tổ quốc Việt Nam, từ nǎm 1960 là Phó là Phó chủ tịch nước Việt Nam dân chủ cộng hoà.\nSau khi Chủ tịch Hồ Chí Minh qua đời, đồng chí được Quốc hội bầu làm Chủ tịch nước từ cuối nǎm 1969 cho đến khi đồng chí từ trần ngày 30-3-1980 taị Hà Nội, hưởng thọ 92 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','08', 'Nhà vǎn, nhà giáo, nhà nghiên cứu vǎn học Hoàng Ngọc Phách, bút hiệu Song An, sinh ngày 20-8-1896. Quê quán: Xã Tùng Ảnh, huyện Đức Thọ, Hà Tĩnh. Ông sinh trưởng trong một gia đình nhà nho yêu nước, cha là một sĩ phu Cần Vương.\nTừ nǎm 1922 đến 1926: Học cao đẳng sư phạm Hà Nội\nTừ nǎm 1926 đến 1945: dạy học nhiều nơi. Thời gian này Hoàng Ngọc Phách là Trưởng ban truyền bá chữ quốc ngữ ở Bắc Ninh và tham gia Việt Minh ở đây.\nSau Cách mạng, từ 1947 đến 1948: Làm giám đốc Giáo dục khu XI. Từ 1951 đến 1954: Giám đốc trường Cao đẳng sư phạm Trung ương. Từ nǎm 1959: về Viện Vǎn học làm công tác sưu tầm, hiệu đính, nghiên cứu các tác phẩm vǎn học Việt Nam cận đại.\nNhà vǎn Hoàng Ngọc Phách mất ngày 24-11-1973 tại Hà Nội.\nTác phẩm chính: \"Tố Tâm\", tiểu thuyết (1925), \"Thời thế với vǎn chương\" Tập 1; \"Đâu là chân lý\" tập 2; \"Thơ vǎn Nguyễn Khuyến\" (1957), \"Thơ vǎn Cách mạng và yêu nước\" (1858-1940), \"Giai thoại vǎn học Việt Nam\" v.v...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','08', 'Ngày 20-8-1941, hoàn thành xây dựng cầu chữ Y ở Sài Gòn. Công trình được xây dựng từ nǎm 1937, khởi công chính thức vào ngày 13-12-1938, do Công ty Công xưởng và Công trình công chính đảm nhiệm. Cầu có 3 nhánh tạo thành chữ Y. Tổng cộng có độ dài 913m, độ cao cách mặt nước là 6,3m, bảo đảm cho tàu bè xà lan đi lại dưới sông. Xây dựng cầu phải sử dụng 800 tấn thép, 4.000m3 bê tông.\nChiếc cầu này không những gắn chặt với đời sống người dân Sài Gòn mà còn ghi lại nhiều chiến tích của quân giải phóng trong cuộc kháng chiến chống đế quốc Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','08', 'Theo thoả thuận của phe đồng minh chiến thắng Phát xít, ngày 20-8-1945, hai mươi vạn quân Tưởng do tướng Lư Hán làm tư lệnh, đem theo bọn phản động tay sai trong các tổ chức \"Việt Nam Quốc dân Đảng\", \"Việt Nam cách mạng đồng minh hội\", \"Phục quốc Đảng\" kéo vào miền Bắc Việt Nam. Hôm sau, chúng đến Lạng Sơn và chỉ trong một tháng đã chiếm đóng các vị trí từ vĩ tuyến 16 trở ra. Chúng ra sức quấy rối, phá hoại, lập các trạm kiểm soát, tự quyền khám xét, bắt giữ người, ngǎn cản sự đi lại của nhân dân ta. Còn bọn tay sai, dựa vào quân Tưởng, chúng ngang ngược lập chính quyền phân liệt ở một số nơi, tổ chức bắt cóc, ám sát, gây rối về chính trị và tổ chức xã hội. Chúng tổ chức những cuộc biểu tình phản cách mạng, kích động bạo loạn. Về thực chất, quân Tưởng không làm tròn chức nǎng giải giáp quân Nhật bại trận mà toan tính lập chính quyền tay sai hòng bóp chết chính quyền nhân dân non trẻ của ta. Song, với đường lối đấu tranh khôn khéo của Chính phủ Hồ Chí Minh, quân Tưởng phải rút về nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','08', 'Ngày 20-8-1948, Chủ tịch Hồ Chí Minh ký sắc lệnh về việc thưởng \"Huân chương Kháng chiến\" cho những người Việt Nam có công với quân đội hoặc các tổ chức quốc phòng và kháng chiến.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','08', 'Ngày 20-8-1958, tổ bay gồm Đinh Tôn - lái chính, Hoàng Liên - phụ lái và Đinh Huy Cận - dẫn đường, đã cất cánh bay lên bầu trời Hà Nội. Ngày ấy được coi là ngày đầu tiên phi công Việt Nam bay lên bầu trời Tổ quốc.\nAnh Đinh Tôn quê ở tỉnh Bình Định, có nhiều thành tích xuất sắc trong chiến đấu với máy bay Mỹ, đã được Nhà nước tuyên dương Anh hùng lực lượng vũ trang nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','08', 'Đại hội cháu ngoan Bác Hồ toàn quốc lần thứ nhất được tổ chức từ ngày 20 đến 23-8-1981 tại Hà Nội, nhằm \"Biểu dương những cống hiến của tuổi nhỏ trong sự nghiệp xây dựng và bảo vệ Tổ quốc xã hội chủ nghĩa\".\nDự đại hội có 410 đại biểu trong đó có 305 cháu ngoan Bác Hồ thay mặt cho 3 triệu cháu ngoan Bác Hồ, 53.000 chi đội mạnh trong cả nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','09', 'Ngày 20-9-1961, lần đầu tiên ở Việt Nam đã thực hiện tốt thủ thuật \"mổ gan khô\" (lúc cắt gan không chảy máu) tại bệnh viện Hữu nghị Việt Đức (Hà Nội). Phương pháp mổ gan khô của Giáo sư Tôn Thất Tùng thành công là một cống hiến quan trọng cho sự nghiệp phát triển của nền y học Việt Nam và thế giới. Các hội nghị khoa học quốc tế đã gọi phương pháp này là \"phương pháp Việt Nam\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','09', 'Từ ngày 20 đến 23-9-1971 đã diễn ra đại hội anh hùng, chiến sĩ thi đua các lực lượng vũ trang nhân dân giải phóng Đông Nam Bộ và Sài Gòn.\nTham dự đại hội có hàng trăm đại biểu của các đơn vị chủ lực, bộ đội địa phương. Đại hội nêu cao chủ nghĩa anh hùng cách mạng trong giai đoạn đánh thắng \"Việt Nam hoá chiến tranh\" của đế quốc Mỹ.\nĐại hội đón mừng quyết định của Chính phủ Cách mạng lâm thời cộng hoà miền Nam Việt Nam tuyên dương danh hiệu Anh hùng các lực lượng vũ trang nhân dân giải phóng cho 16 cá nhân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '20','09', 'Tại phiên họp lần thứ 32 của đại hội đồng Liên hiệp quốc ở Niu Oóc (Mỹ) ngày 20-9-1977, Việt Nam được chính thức công nhận là hội viên thứ 149 của Liên hiệp quốc.\nViệc Việt Nam vào Liên hiệp quốc là một sự kiện có ý nghĩa lịch sử, phản ánh sự biến đổi sâu xa của thời đại chúng ta, theo xu thế thất yếu của lịch sử mở ra từ Cách mạng Tháng Mười Nga. Sự kiện này đã tăng thêm ảnh hưởng của các nước xã hội chủ nghĩa của các dân tộc đang đấu tranh để củng cố và hoàn thiện nền độc lập dân tộc của mình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('1', '20','10', 'Ngày Phụ nữ Việt Nam')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','10', 'Hội Phụ nữ phản đế Việt Nam được thành lập ngày 20-10-1930. Đó là tổ chức tiền thân của Hội Liên hiệp phụ nữ Việt Nam.\nHội Liên hiệp phụ nữ Việt Nam tập hợp rộng rãi các tầng lớp phụ nữ, đấu tranh giải phóng dân tộc, giải phóng giai cấp và giải phóng phụ nữ theo đường lối của Đảng, Đảng chỉ rõ: nếu giải phóng dân tộc, giải phóng giai cấp mà chưa giải phóng phụ nữ thì Cách mạng mới chỉ là một nửa.\nĐồng chí Nguyễn Thị Minh Khai là chủ tịch đầu tiên của Hội Liên hiệp phụ nữ Việt Nam.\nBác Hồ kính yêu đã từng đánh giá: Giang sơn gấm vóc Việt Nam là do phụ nữ Việt Nam, trẻ cũng như già dệt thêu mà thêm tốt đẹp, rực rỡ\".\nTrong hai cuộc kháng chiến và xây dựng đất nước, phụ nữ nước ta có nhiều cống hiến to lớn xuất sắc. Hội hiệp phụ nữ Việt Nam đã được Nhà nước ta tặng thưởng Huân chương Hồ Chí Minh và Huân chương Sao vàng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','10', 'Nhà văn Nguyễn Minh Châu sinh ngày 20-10-1930. Quê quán ở xã Sơn Hải, huyện Quỳnh Lưu, tỉnh Nghệ An. Năm 1950, đang học chuyên khoa trường Huỳnh Thúc Kháng, ông tình nguyện vào bộ đội, viết văn từ 1954. Năm 1960 về công tác tại tạp chí \"Văn nghệ Quân đội\" cho đến ngày mất. Ông là Uỷ viên Ban Chấp hành Hội Nhà văn khoá III.\nTác phẩm: \"Cửa sông\" - tiểu thuyết (1967-1968); \"Những vùng trời khác nhau\" - tập truyện ngắn (1970), \"Dấu chân người lính\" tiểu thuyết 1972-1974-1984), \"Những ngày lưu lạc\" (1981), \"Người đàn bà trên chuyến tàu tốc hành\" (1983), \"Cỏ lau\" (1988) v.v.... Cuộc đời Nguyễn Minh Châu là cuộc đời của người chiễn sĩ tay súng tay bút chiến đấu không mệt mỏi, là cây bút sung sức tài năng, luôn trăn trở tìm tòi đổi mới cho văn học, cả lý luận và sáng tác. Truyện của Nguyễn Minh Châu là chuyện của đời thường, nghiêng về chính luận, tinh tế và duyên dáng ẩn chứa đạo đức sâu sắc và mang tính dự báo cao.\nNguyễn Minh Châu mất ngày 23 tháng 1 năm 1989.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','10', 'Ngày 20-10-1950, Báo \"Quân đội nhân dân\" ra số đầu tiên. Báo do báo \"Vệ quốc quân\" và báo \"Quân du kích\" sát nhập. Chủ nhiệm kiêm chủ bút ông Lê Liêm. Thư ký toà soạn là ông Lưu Văn Lợi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','10', 'Ngày 20-10-1995, tại 36 phố Lý Thường Kiệt, Hà Nội đã khánh thành bảo tàng Phụ nữ Việt Nam.\nĐây là kiến trúc hiện đại có diện tích 4.500 mét vuông.\nBảo tàng phụ nữ Việt Nam là trung tâm nghiên cứu về phụ nữ, nơi giữ gìn, bảo quản tài liệu, hiện vật và các bộ sưu tầm hiện vật, thể hiện vai trò của phụ nữ trong tiến trình lịch sử - văn hoá của dân tộc Việt Nam..\nBảo tàng cũng là trung tâm hoạt động văn hoá, truyền thống kiến thức về gia đình và xã hội cho phụ nữ.\nBảo tàng còn là nơi giao lưu văn hoá giữa phụ nữ Việt Nam và phụ nữ thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('1', '20','11', 'Ngày Nhà giáo Việt Nam')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','11', 'Mờ sáng ngày 20-11-1873, quân Pháp do Gácniê chỉ huy đánh vào thành Hà Nội.\nNguyễn Tri Phương, Tổng đốc Hà nội, đốc thúc quân sĩ chống cự nhưng không giữ nổi Thành trước sức tấn công mạnh mẽ của giặc Pháp. Thành Hà Nội bị giặc chiếm đóng, Nguyễn Tri Phương bị trúng đạn vào bụng, bị giặc bắt, ông nhịn ǎn mà chết, không hàng giặc.\nNgay sau đó, trong lúc triều đình Huế đang hoang mang, bị động thì Gácniê đưa quân đi đánh chiếm nhiều nơi ở miền Bắc. Không đầy một tháng, các tỉnh, thành ở đồng bằng sông Hồng lần lượt lọt vào tay giặc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','11', 'Tháng 8-1957, Hội nghị quốc tế các nhà giáo họp tại Vácsava (Ba Lan) đã thông qua bản Hiến chương các nhà giáo và quyết định lấy ngày 20-11 hàng nǎm là \"Ngày quốc tế hiến chương các nhà giáo\".\nNgày 20-11-1958, Ngày quốc tế hiến chương các nhà giáo được tổ chức lần đầu tiên ở miền Bắc nước ta. Đến ngày 28-9-1982, Hội đồng Bộ trưởng ra Quyết định số 167, lấy ngày 20-11 làm \"Ngày nhà giáo Việt Nam\". Quyết định này có ý nghĩa đặc biệt quan trọng, thể hiện quan điểm của Đảng, Nhà nước về vị trí, vai trò của nhà giáo trong sự nghiệp đào tạo lớp người mới xây dựng chủ nghĩa xã hội và bảo vệ Tổ quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','11', 'Ngày 20-11-1984 Hội đồng Bộ trưởng ra Nghị định số 153-HĐBT quy định các danh hiệu Nhà giáo nhân dân và Nhà giáo ưu tú để tặng những nhà giáo và những người quản lý giáo dục có cống hiến xuất sắc đối với sự nghiệp đào tạo thế hệ trẻ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','11', 'Ngày 20-11-1991, ngành điện lực nước ta đã khởi công xây dựng Nhà máy Thuỷ điện Thác Mơ, nay thuộc huyện Phước Long, tỉnh Bình Phước, và đã hoàn thành công trình vào cuối nǎm 1994.\nNhà máy này có 2 tổ máy với công suất 150 mêgaoat và sản lượng điện hàng nǎm là 610 triệu kilôoat giờ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '20','11', 'Xenma Lagéclốp (Salme Legerlof) là nhà nữ vǎn sĩ danh tiếng Thụy Điển, sinh ngày 20-11-1859. Vốn là nhà giáo, bà viết nhiều tiểu thuyết, truyện ngắn lãng mạn, đậm màu sắc dân tộc.\nCác tác phẩm bày tỏ lòng phẫn nộ đối với chiến tranh, chống chủ nghĩa phân biệt chủng tộc, chủ nghĩa phát xít. Nǎm 1919, bà được tặng giải Noben và được bầu làm Viện sĩ Viện Hàn lâm Thụy Điển. Bà mất ngày 16-3-1940.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '20','11', 'Ngày 20-11-2000, Quốc hội Philippines mở phiên toà luận tội đầu tiên ở châu Á, yêu cầu Tổng thống Joseph Estrada phải đối chất trước toà vì những lời buộc tội tham nhũng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','12', 'Nguyễn Tri Phương, Đại thần triều Nguyễn, Tổng đốc thành Hà Nội, quê ở tỉnh Thừa Thiên.\nNgày 19-11-1873, quan Pháp đanh úp thành Hà Nội, ông bị trọng thương, được lính pháp cứu chữa nhưng khảng khái từ chối và nói rằng: \"Bây giờ nếu ta chỉ gắng lay lắt mà sống, sao bằng thung dung chết vì việc nghĩa\".\nSau đó ông tuyệt thực gần một tháng và mất vào 20-12-1873, thọ 73 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','12', 'Ông Đỗ Ngọc Du sinh ngày 20-12-1907 tại thị xã Hải Dương, quê chính ở làng Tả Thanh Oai, huyện Thanh Trì, Hà Nội.\nSau khi được kết nạp vào Việt Nam Thanh niên cách mạng đồng chí Hội (nǎm 1926) ở Quảng Châu, ông trở về nước hoạt động, tham gia sáng lập Đông Dương cộng sản Đảng, được bầu làm Bí thư xứ uỷ Bắc Kỳ kiêm Bí thư Thành uỷ Hà Nội (nǎm 1929)\nSau ngày hợp nhất Đảng (3-2-1930) ông được Đảng cử sang liên hệ với Đảng cộng sản Trung Quốc và hoạt động phong trào Việt kiều yêu ước ở Thượng Hải.\nTháng 6-1931 thực dân Pháp bắt ông Đỗ Ngọc Du ở Thượng Hải và đưa về Việt Nam, lưu đày ở Sơn La, Côn Đảo, nǎm 1936 ông được thả tự do. Những nǎm tháng tù đày, ông bị bệnh lao quá nặng nên mất tháng 1-1938.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','12', 'Nhà vǎn Phan Tứ, tên thật là Lê Khâm, sinh ngày 20-12-1930, quê ở tỉnh Quảng Nam, qua đời nǎm 1995.\nNǎm 1950 ông nhập ngũ, từng ở chiến trường Hạ Lào. Nǎm 1954 ông tập kết ra Bắc. Nǎm 1961 trở về Nam, ông công tác Tuyên huấn ở Liên khu V.\nPhan Tứ viết nhiều thể loại, nhưng chủ yếu là tiểu thuyết. Các tác phẩm có tiếng: Bên kia biên giới, Trước giờ nổ súng, Về làng, Trại ST 18. Thành công hơn cả hai tập tiểu thuyết: Gia đình má Bảy (nǎm 1968), Mẫn và tôi (nǎm 1972).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','12', 'Hồi 8 giờ tối ngày 19-12-1960, tại một vùng giải phóng miền Đông Nam Bộ, đại biểu các giai cấp, các Đảng phái các tôn giáo, các dân tộc toàn miền Nam đã họp Đại hội để thành lập Mặt trận dân tộc giải phóng miền Nam Việt Nam. Một giờ sáng ngày 20-12, Mặt trận dân tộc giải phóng miền Nam Việt Nam đã long trọng tuyên bố thành lập. Mặt trận công bố chương trình hoạt động gồm 10 điểm, mà nội dung cơ bản là đánh đổ chế độ thuộc địa trá hình của đế quốc Mỹ và chế độ độc tài Ngô Đình Diệm, nhằm xây dựng một miền Nam Việt Nam độc lập, dân chủ, hoà bình thống nhất nước nhà.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '20','12', 'Ngày 20-12-1963, khánh thành lò cao số 1 khu gang thép Thái Nguyên. Trong phong trào thi đua hướng về miền Nam thân yêu, cán bộ và công nhân khu gang thép Thái Nguyên đã dũng cảm vượt khó khǎn, hoàn thành lò cao số 1 trước thời hạn, lấy thành tích chào mừng ngày thành lập Mặt trận giải phóng dân tộc miền Nam Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '20','12', 'Ngày 20-12-1991, Hội nghị Alma Ata tuyên bố thành lập Cộng đồng các quốc gia có chủ quyền (SNG). Có nguyên thủ 11 nước cộng hoà thuộc Liên Xô tham dự gồm: Âudơbekixtan, Ucraina, Agiécbaidan, Bêlorútxia, Agiécbaidan, Acmênia, Cadắcxtan, Kiếcghidixtan, Mônđavia, Tuốcmenixtan, Tadikixtan và Gruzia tham gia với tư cách quan sát viên, Liên bang Xô Viết chấm dứt hoạt động (1922-1991) từ đó.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','01', 'Vơlađimia Ilích Lênin sinh nǎm 1870, từ trần ngày 21-1-1924. Ông là người sáng lập Nhà nước xã hội chủ nghĩa đầu tiên trên thế giới, nhà lý luận mác xít và hoạt động cách mạng thực tiễn kiệt xuất, người thầy của giai cấp công nhân và dân tộc bị áp bức toàn thế giới. Lênin đặc biệt chú ý và đưa ra nhiều luận điểm quan trọng về phong trào đấu tranh của các dân tộc thuộc địa và phụ thuộc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','01', 'Ngày 21-1-1946, tại thủ đô Hà Nội, Chủ tịch Hồ Chí Minh đã tiếp và trả lời câu hỏi của các nhà báo nước ngoài. Người nói: \"Tôi tuyệt nhiên không ham muốn công danh phú quý chút nào. Bây giờ phải gánh vác chức Chủ tịch là vì đồng bào uỷ thác thì tôi phải gắng sức làm, cũng như một người lính vâng lệnh của quốc dân ra trước mặt trận. Tôi chỉ có một ham muốn, ham muốn tột bậc, là làm sao cho nước nhà được hoàn toàn độc lập, dân ta được hoàn toàn tự do, đồng bào ai cũng có cơm ǎn, áo mặc, ai cũng được học hành\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','01', 'Trước áp lực của ta, Mỹ bị động tǎng cường lực lượng đến Khe Sanh (tỉnh Quảng Trị) và xây dựng một cứ điểm mạnh.\nNgày 21-1-1968, Quân giải phóng bắt đầu vây hãm quân địch ở Khe Sanh. Từ đó đến ngày 9-7-1968, ta đã mở 4 đợt tấn công địch. Chúng phải rút chạy ở Khe Sanh. Ta đã loại khỏi vòng chiến đấu 17 nghìn tên địch (trong đó có 13 nghìn tên Mỹ), bắn rơi và phá huỷ 480 máy bay, 120 xe quân sự, 65 đại bác và súng cối cỡ lớn, 55 kho xǎng và đạn dược, thu hàng nghìn súng, hàng trǎm tấn đồ dùng quân sự và lương thực, giải phóng hoàn toàn Hướng Hoá, Khe Sanh.\nChiến thắng này là một thắng lợi có ý nghĩa chiến lược của ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','01', 'Ngày 21-1-1976 đã thành lập Uỷ ban nhân dân cách mạng thành phố Sài Gòn - Gia Định do đồng chí Võ Vǎn Kiệt, Uỷ viên ban chấp hành Trung ương Đảng lao động Việt Nam làm Chủ tịch, thay cho Uỷ ban quân quản thành phố')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','02', 'Ngày 21-2-1950 khai mạc Đại hội lần thứ nhất Đoàn thanh niên cứu quốc và Liên đoàn thanh niên Việt Nam tại Việt Bắc. Hơn 400 đại biểu của Bắc - Trung - Nam (cả những vùng và thành phố bị tạm chiếm) về dự, nêu cao quyết tâm \"Tất cả cho tiền tuyến, tất cả để chiến thắng\". Đại hội đã đặt cơ sở thống nhất tư tưởng và hành động cho thanh niên, định rõ nhiệm vụ và địa vị của thanh niên trong giai đoạn chuyển mạnh sang tổng phản công. Tại đại hội đồng chí Trường Chinh, Tổng Bí thư Đảng đã nói chuyện về \"Lý tưởng của thanh niên Việt Nam\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','02', 'Nhà soạn kịch Lưu Quang Thuận sinh nǎm 1921 tại thành phố Đà Nẵng, qua đời ngày 21-2-1981.\nTừ nǎm 1937 ông vào sống ở Gia Định bắt đầu sáng tác vǎn học đǎng trên các báo ở Sài Gòn. Nǎm 1943, ông trở ra Hà Nội, viết báo, làm thơ, soạn kịch. Từ nǎm 1946, ông tham gia kháng chiến chống Pháp, có sáng kiến thành lập tạp chí Sân khấu và nhà xuất bản Hoa Lư ở Hà Nội. Sau đó làm việc ở đài Tiếng nói Việt Nam tại chiến khu Việt Bắc. \nLưu Quang Thuận là tác giả nhiều vở kịch thơ được công chúng tán thưởng như: Yến Ly, Lê Lai đổi áo, Kiều Công Tiễn, Hoàng Hoa Thám, Nàng Si Ta, Tấm Cám, Mối tình Điện Biên và các tập thơ: Tóc thơm, Việt Nam yêu dấu, Lời thân ái, Mừng đất nước, Cảm ơn thời gian...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','02', 'Nhà thơ, nhà báo Nguyễn Mỹ sinh ngày 21-2-1936, quê ở tỉnh Phú Yên, hy sinh nǎm 1971 ở tỉnh Quảng Nam.\nÔng đi bộ đội từ nǎm 16 tuổi, rồi tập kết ra miền Bắc, nổi tiếng với bài thơ \"Cuộc chia ly màu đỏ\" (nǎm 1964). Đến nǎm 1968, trở về miền Nam, làm phóng viên mặt trận, hoạt động ở Ban tuyên truyền vǎn nghệ khu V. \nNgoài làm thơ, ca dao, Nguyễn Mỹ còn vẽ, sáng tác nhạc, viết bút ký.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '21','02', 'Ngày 21-2-1973 Hiệp định Viêng Chǎn về hoà bình và hoà hợp dân tộc ở Lào được ký kết giữa chính quyền Viêng Chǎn thân Mỹ và Mặt trận Lào yêu nước do những người Cách mạng Lào lãnh đạo. Việc ký kết Hiệp định Viêng Chǎn đã đồng thời kết thúc chiến tranh xâm lược của đế quốc Mỹ đối với đất nước Triệu voi, mở ra con đường mới cho nhân dân các bộ tộc Lào. Thắng lợi này cũng là thắng lợi của phong trào cách mạng của nhân dân Đông Dương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '21','02', 'Ngày 21-2-1984, nhà vǎn lừng danh thế giới Mikhain Sôlôkhốp, qua đời.\nÔng sinh nǎm 1905 ở tỉnh Rôxtôp của nước Nga. Khi Cách mạng tháng Mười bùng nổ và nội chiến, ông tham gia Hồng quân, đi khắp vùng sông Đông và những nǎm tháng bão táp đó đã in sâu trong tâm trí nhà vǎn. Hai tác phẩm nổi tiếng của ông mà bạn đọc rất yêu mến là các bộ tiểu thuyết \"Sông Đông êm đềm\" và \"Đất vỡ hoang\" đều được đưa lên màn ảnh. Riêng \"Sông Đông êm đềm\" được dịch ra hàng chục thứ tiếng, được coi là một trong những tác phẩm cổ điển ưu tú nhất của nền vǎn học thế giới hiện đại.\nNhà vǎn Mikhain Sôlôkhốp đã được giải thưởng Nôben về vǎn học nǎm 1965.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '21','02', 'Havớt Oantơ Phlôri (Sir Howrd Walter Florey) sinh nǎm 1898, mất ngày 21-2-1968. \nPhlôri là nhà bệnh lý học người Anh, nghiên cứu khoa học ở trường đại học Cambrítgiơ và Xépphin, làm giáo sư ở ốtxpho nǎm 1935. Ông cùng với Chên và Abraham trích li và tinh lọc Penicillin (do Phlêminh tìm thấy nǎm 1928) để làm thuốc kháng sinh. Nǎm 1945, Phlôri cùng với Phlêminh và Chên được nhận giải Nôben về sinh y học.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','03', 'Nhà giáo dục Ca Vǎn Thỉnh sinh ngày 21-3-1902 tại huyện Mỏ Cày, tỉnh Bến Tre. Sau khi tốt nghiệp cao đẳng sư phạm, ông được bổ nhiệm làm giáo sư, rồi làm đốc học Bến Tre. Suốt hơn mười nǎm liền, ông nghiên cứu vǎn học và sử học. Tháng 8-1945, ông tham gia thành lập chính quyền ở Bến Tre, sau làm ủy viên ủy ban kháng chiến hành chính Nam Bộ. Khi tập kết ra Bắc, ông chuyển sang làm công tác ngoại giao và từ nǎm 1959 làm giám đốc Thư viện Khoa học xã hội Trung ương.\nSau ngày 30-4-1975, ông Ca Vǎn Thỉnh được cử làm Viện trưởng Viện Khoa học xã hội miền Nam tại thành phố Hồ Chí Minh cho đến ngày nghỉ hưu.\nÔng có nhiều tác phẩm khảo cứu, dịch thuật có giá trị.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','03', 'Giữa lúc ở Sài Gòn và Nam Kỳ đang sôi động phong trào đấu tranh dân chủ, một số nhà yêu nước như: Nguyễn An Ninh, Trần Huy Liệu... và một số người Pháp tiến bộ đã chủ trương tổ chức một cuộc mít tinh bày tỏ thái độ đối với nhà cầm quyền thực dân. Cuộc mít tinh tiến hành vào ngày 21-3-1926, tại Xóm Lách (Sài Gòn). Tiếng vang của cuộc mít tinh đã tự phát hình thành một tổ chức mang tên là Đảng Thanh niên. \nRất tiếc tổ chức này chỉ sôi nổi thời gian đầu và mục tiêu đấu tranh lại không rõ ràng. Sau cuộc vận động đình công thất bại, cũng như một số nhân vật của tổ chức bị bắt - Đảng Thanh niên chấm dứt hoạt động. Điều ghi nhận ở đây là dấu hiệu của phong trào quần chúng và sự đòi hỏi sớm có chính Đảng tiền tiến lãnh đạo.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','03', 'Nhằm hạn chế tác động của lũ sông Hồng. Từ nǎm 1926 chính quyền thực dân Pháp đã cho nghiên cứu đề án xây dựng một con đập trên sông Đáy dùng để phân lũ sông Hồng, đồng thời bảo đảm tưới tiêu cho diện tích sản xuất thuộc địa phận Hà Đông, Phủ Lý. Sau ba mùa khô thi công, ngày 21-3-1937, công trình đập Đáy được khánh thành. Mặc dù chính quyền Pháp ra sức tuyên truyền về khả nǎng cũng như chức nǎng của con đập này nhưng trong thực tế đập Đáy đã từng bị mất tác dụng hoặc hiệu quả thấp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','03', 'Ngày 21-3-1975, quân đội ta mở đầu chiến dịch Huế - Đà Nẵng.\nTrước nguy cơ bị tiêu diệt để ngǎn chặn cuộc tiến công của ta, địch gấp rút điều chỉnh thế bố trí lực lượng, thực hiện ý đồ co cụm chiến lược. Từ ngày 15 đến ngày 20 tháng 3, chúng đưa 2 lữ đoàn (369 và 258), sư đoàn lính thuỷ đánh bộ từ Trị Thiên vào Quảng Đà, thay cho 2 lữ đoàn dù (1 và 30 rút vào Sài Gòn). Những ngày sau đó, do sức ép ngày càng tǎng của ta, địch rục rịch rút bỏ Huế, âm mưu đưa lực lượng về giữ Đà Nẵng.\nBám sát tình hình, chủ động mở rộng tiến công, quân đoàn 2 và các đơn vị thuộc 2 quân khu 5 và Trị Thiên đã nhanh chóng kiên quyết chuyển từ thế tiếp xúc với hệ thống phòng ngự của địch sang thế bao vây, chia cắt, làm rối loạn đội hình địch, chặn đường rút chạy và tiêu diệt chúng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '21','03', 'Ngày 21-3-1685, là ngày sinh của Iôhan Dêbaxchian Bách (Johann Sebastian Bach), nhạc sĩ sáng tác và nghệ sĩ đàn đai phong cầm thiên tài người Đức. Sáng tác của ông mang tính nhân đạo có nguồn gốc dân gian, dân tộc, đã thâu tóm mọi thành tựu của nền âm nhạc châu Âu thời bấy giờ, và mở ra một thời kỳ mới trong âm nhạc thế giới. Ông là người đã hoàn thiện hệ thống điều hoà trong âm nhạc tạo cho âm nhạc một bước phát triển lớn lao. Di sản sáng tác của ông thật khổng lồ bao gồm nhiều thể loại, để lại một dấu ấn không phai mờ trong nền âm nhạc thế giới. Trong tiếng Đức thì Bach có nghĩa là con suối. Nhưng ông đâu chỉ là Suối - ông là Đại Dương - như thời đại ông đã gọi. Ông mất nǎm 1750.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '21','03', 'Napôlêông là nhà chính trị và quân sự nổi tiếng trong lịch sử nước Pháp. Trong Cách mạng tư sản Pháp ông được Nhà nước Pháp phong cấp tướng khi mới 24 tuổi. Sau khi làm cuộc đảo chính, nǎm 1804 Napôlêông tự phong là Hoàng đế, thâu tóm toàn bộ quyền lập pháp và hành pháp. Dưới thời ông đã hoàn thành việc củng cố chế độ nhà nước Tư sản, hình thành những nền tảng cơ bản của Luật tư sản. Ngày 21-3-1804 Bộ luật dân sự được ban hành. Nó nổi tiếng với tên gọi \"Bộ luật Napôlêông\". Bộ luật đã khẳng định quyền tư hữu củng cố quyền thống trị kinh tế và chính trị của giai cấp tư sản. Các quyền tự do công dân đã được Bộ luật quy định một cách cụ thể. Mọi cơ cấu của hệ thống luật phong kiến đã bị xoá bỏ. Bộ luật thể hiện những nguyên tắc tạo điều kiện thúc đẩy tính chủ động và việc kinh doanh tư hữu tư bản chủ nghĩa. Angghen coi bộ luật Napôlêông là \"Bộ luật tư bản hiện đại nhất, mà cơ sở của nó là những thành quả xã hội của cuộc Cách mạng tư sản Pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '21','03', 'Ra đời vào đầu những nǎm 50, Anphata, thành viên chủ yếu của tổ chức giải phóng Palextin (PLO) ngày càng phát triển. Ngày 21-3-1968, với vài trǎm chiến sĩ du kích Palextin thuộc lực lượng Anphata đã bẻ gẫy cuộc tiến công lớn của hơn mười nghìn quân Itxraen có máy bay và xe tǎng yểm trợ khi chúng tấn công vào Karame. Nhờ đó uy tín của Anphata càng tǎng.\nTháng 2 nǎm 1969, tại khoá họp lần thứ 5 hội đồng dân tộc Palextin, lãnh tụ của Anphata, ông Araphát được bầu làm ủy viên ban chấp hành PLO.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '21','03', 'Lịch sử phát triển nhân loại lấy làm hổ thẹn khi trên thế giới vǎn minh còn tồn tại chế độ phân biệt chủng tộc. Một dạng nguy hiểm của tệ phân biệt chủng tộc là chủ nghĩa Xiôn - chủ nghĩa Apácthai. Đồng thời đây cũng là nguồn gốc đau thương hàng trǎm nǎm mà người da đen Châu Phi phải gánh chịu. Chế độ sử dụng người da đen Châu Phi làm nô lệ ra đời sau khi người ta thực hiện khai thác Tân thế giới (châu Phi). Trước sức phát triển của chế độ loài người, trước xu thế tiến bộ chiến thắng chủ nghĩa phát xít, đại hội đồng Liên Hiệp quốc đã ra quyết định nhằm thủ tiêu tư tưởng phản khoa học, phản động và các chính sách ô nhục này. Hàng nǎm cứ đến ngày 21-3 nhân loại tiến bộ lấy làm ngày quốc tế chống phân biệt chủng tộc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','04', 'Huỳnh Thúc Kháng là một nhà học giả thời trẻ có tên là Hạnh, tự giới Sanh, hiệu Minh Viên, quê ở Thạch Bình, tổng Tiên Giang Thượng, tỉnh Quảng Nam (nay là huyện Tiên Phước, tỉnh Quảng Nam).\nĐậu tiến sĩ nǎm 1904 nhưng ông không làm quan, nhiệt thành lo nước, thương dân, kết bạn thân tình với Phan Bội Châu, Phan Chu Trinh, Trần Quý Cáp, ông bị bắt nǎm 1908 và 1921 mới được trả tự do. Ông là một trong các nhân vật lãnh dạo phong trào Duy Tân nǎm 1908. Nǎm 1926 ông được cử làm Viện trưởng Viện dân biểu Trung Kỳ.\nTrong 3 nǎm hoạt động trong Viện, ông cương quyết tranh đấu trong nghị trường, rồi nhân chống lại viên khâm sứ Pháp, ông từ chức và sáng lập ra tờ báo Tiếng dân. Làm Chủ nhiệm kiêm Chủ bút báo này tại Huế nǎm 1927-1943. Sau Cách mạng tháng Tám, ông nhận chức Bộ trưởng Bộ Nội vụ chính phủ Liên hiệp Kháng chiến Việt Nam Dân chủ cộng hoà và khi Chủ tịch Hồ Chí Minh sang Pháp đàm phán, ông được trao quyền Chủ tịch nǎm 1946. Ông cũng là một sáng lập viên và là Hội trưởng Hội Liên hiệp quốc dân Việt Nam (Liên Việt). Bắt đầu cuộc Toàn quốc kháng chiến, ông được Chính phủ đặc phái vào liên khu V công tác, và sau đó do tuổi già sức yếu, ông bị mất tại Quảng Ngãi ngày 21-4-1947, thọ 71 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','04', 'Ngô Tất Tố là một nhà vǎn nhà báo lớn có rất nhiều bút danh, ông sinh nǎm 1894. Khoảng nǎm 1927-1929 ông vào Nam cộng tác với các báo rồi trở ra Bắc, chuyên tâm sáng tác, nghiên cứu vǎn học, sử học, triết học, nổi tiếng trong làng báo, làng vǎn trong thời 1930-1935.\nÔng mất vào tháng 4-1954, ông đã để lại nhiều tác phẩm vǎn học nổi tiếng như: Tắt đèn viết nǎm 1939, Việc làng nǎm 1941, Lều chõng viết nǎm 1941, Thi vǎn bình chú viết nǎm 1941.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','04', 'Từ 21 đến ngày 30-4-1959, tại Nhà hát thành phố Hà Nội đã tiến hành Hội nghị cán bộ toàn Đảng bộ Hà Nội (có ý nghĩa như Đại hội lần thứ nhất của Đảng bộ thành phố).\nDự hội nghị này có 236 đại biểu thay mặt cho 12 nghìn đảng viên.\nNgày 25-4-1959, Chủ tịch Hồ Chí Minh đến thǎm và nói chuyện với các đại biểu. Người nói: \"Hà Nội phải trở thành thủ đô xã hội chủ nghĩa\" và \"Đảng Bộ Hà Nội gương mẫu sẽ góp một phần vào công cuộc đưa miền Bắc tiến lên chủ nghĩa xã hội, làm cơ sở vững mạnh để đấu tranh giành thống nhất nước nhà\".\nHội nghị đã bầu Ban Chấp hành Đảng bộ Hà Nội gồm có 30 đồng chí, do đồng chí Trần Danh Nguyên làm Bí thư Thành ủy.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','04', 'Ngày 21-4-1975, quân ta giải phóng thị xã Xuân Lộc và tỉnh Long Khánh cũ. 7 giờ sáng trung đoàn 4 (sư đoàn 6) phối hợp với nhân dân và công nhân ở \"Sở ông Quế\", đánh tan tiểu đoàn 2 chiến đoàn 43 sư 18 ngụy khi chúng đang trên đường tháo chạy.\n8 giờ sáng, ngụy quân ngụy quyền thuộc tỉnh Long Khánh tan rã hoàn toàn. Tuyến phòng thủ Xuân Lộc bị đập tan, cửa ngõ tiến quân vào Sài Gòn đã mở.\nTrước những thắng lợi to lớn của ta ở chiến trường miền Nam, chính quyền Pho - Kitxinhgiơ ra lệnh di tản. Một lực lượng lớn không quân, hải quân Mỹ gồm 35 tàu chiến, có 4 chiếc tàu sân bay và hàng trǎm máy bay hoạt động nhộn nhịp trong một cuộc hành quân rút chạy hốt hoảng bắt đầu từ ngày 21-4-1975.\nCùng ngày đó, Nguyễn Vǎn Thiệu tuyên bố rút khỏi cái gọi là \"Tổng thống Việt Nam cộng hoà\".\nMỹ đưa Trần Vǎn Hương, một tay sai đắc lực khác của Mỹ lên thay Thiệu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','04', 'Nguyễn Thành Châu (tức Nǎm Châu) sinh nǎm 1906, quê ở Mỹ Tho (tỉnh Tiền Giang), mất ngày 21-4-1978. \nÔng bước vào sân khấu từ nǎm 15 tuổi. Trải qua 50 nǎm tận tụy yêu nghề, ông có nhiều đóng góp xây dựng và phát triển nghệ thuật cải lương nước nhà. Ông là nhà viết kịch bản, kiêm đạo diễn, diễn viên xuất sắc trong giới nghệ sĩ sân khấu cải lương.\nNghệ sĩ Nǎm Châu đã soạn gần 50 vở cải lương có giá trị. Ông được Nhà nước tặng danh hiệu Nghệ sĩ nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','05', 'Để chuẩn bị cho Tổng khởi nghĩa giành chính quyền, từ tháng 2 nǎm 1945, Ban thường vụ Trung ương Đảng quyết định thành lập các chiến khu Cách mạng, tới tháng 5-1945 sau hội nghị các bí thư tỉnh ủy và ủy viên quân sự các tỉnh Thanh Hoá, Hoà Bình, Ninh Bình và Hà Nam, Chiến khu Hoà-Ninh-Thanh chính thức được thành lập. Tại các cǎn cứ của chiến khu, những đơn vị vũ trang hình thành và tham gia chiến đấu gây thanh thế cho Cách mạng.\nChiến khu Hoà-Ninh-Thanh trở thành bàn đạp cho cuộc Tổng khởi nghĩa tháng 8-1945 ở các tỉnh nói trên.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','05', 'Ngày 21-5-1954 ba nghìn công nhân Nhà máy sợi Nam Định cùng nhân dân thành phố tổ chức mít tinh lớn và biểu tình tuần hành phản đối thực dân Pháp, can thiệp Mỹ âm mưu mở rộng, kéo dài chiến tranh Đông Dương và ủng hộ Đoàn đại biểu nước Việt Nam Dân chủ cộng hoà ở Hội nghị Giơnevơ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','05', 'Phối hợp với Sài Gòn, từ ngày mùng 8 đến ngày 21-5-1968, ta đã tiến công đồng loạt vào các tuyến phòng thủ xung quanh Sài Gòn như Đức Hoà, Bến Lộc (Long An) Tân Thời Hiệp (Gia Định), Biên Hoà và các thành phố, thị xã vùng đồng bằng sông cửu Long như Cần Thơ, Mỹ Tho, Bến Tre, Châu Đốc, Trà Vinh, Vĩnh Long, Sa Đéc, Cà Mau. Đồng thời chúng ta đã chặn đánh các cuộc hành quân phản kích của địch ra Long An, Tây Ninh, Thủ Dầu Một, Biên Hoà.\nTrên mặt trận đường 9 - Trị Thiên ở Huế, sau khi rút ra ngoài, quân ta tiệp tục vây hãm thành phố, bao vây cụm cứ điểm Mỹ ở Khe Sanh, và chặn đánh các cuộc hành quân của Mỹ Ngụy ở đồng bằng và rừng núi. Theo con số thống kê được trong các cuộc tấn công đồng loạt này ta đã tiêu diệt và bắt sống 72.000 tên địch, bắn rơi và phá huỷ 1.000 máy bay, bắn cháy hơn 2100 xe quân sự, hơn 120 tàu chiến của địch.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','05', 'Từ ngày 21-5 đến ngày 15-11-1972, Liên quân Lào - Việt Nam mở chiến dịch phòng ngự cánh đồng Chum - Xiêng Khoảng, nhằm đánh bại cuộc tấn công lấn chiếm của gần 80 tiểu đoàn phái hữu Lào và quân Thái Lan được Mỹ chi viện.\nĐến ngày 5-11-1972 quân đội phái hữu Lào đã rút hết khỏi phía nam cánh đồng Chum.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','06', 'Ngày 21-6-1925, tờ báo \"Thanh niên\" do lãnh tụ Nguyễn Ái Quốc sáng lập ra số đầu tiên, phát hành tại Quảng Châu (Trung Quốc).\nĐể ghi nhớ công ơn của Chủ tịch Hồ Chí Minh, người sáng lập báo chí Cách mạng Việt Nam, Ban Bí thư Trung ương Đảng quyết định lấy ngày 21-6 là \"Ngày báo chí Việt Nam \". Hàng nǎm, đến ngày đó thì tổ chức kỷ niệm nhằm phát huy truyền thống tốt đẹp của báo chí Cách mạng, nêu cao vai trò của báo chí và trách nhiệm của nhà báo đối với xã hội, làm cho báo chí ngày càng có tác dụng to lớn đối với sự nghiệp cách mạng của nhân dân ta. Với tinh thần đó, ngày 21-6-1985, \"Ngày báo chí Việt Nam\" được tổ chức lần đầu tiên ở nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','06', 'Bộ đội không quân của ta đã bắn rơi tại Thanh Hoá chiếc máy bay không người lái đầu tiên của giặc Mỹ vào ngày 21-6-1965.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','06', 'Từ ngày 21-6 đến 4-7-1977 đã diễn ra Hội nghị Ban Chấp hành Trung ương Đảng lần thứ hai, khoá IV. Hội nghị khẳng định những thành công và những kinh nghiệm tốt của nền công nghiệp hợp tác hoá, đồng thời cũng chỉ rõ những khuyết điểm nghiêm trọng trong việc chỉ đạo nông nghiệp.\nHội nghị xác định: phải xác lập quan hệ sản xuất xã hội chủ nghĩa trong cả nước. Cùng với việc củng cố và phát triển mạnh nông trường quốc doanh trên hai miền, phải củng cố hợp tác xã sản xuất nông nghiệp ở miền Bắc và tiến hành cải tạo xã hội chủ nghĩa đối với nông nghiệp miền Nam. Hội nghị ra nghị quyết \"Tập trung các lực lượng cả nước, thực hiện thắng lợi nhiệm vụ phát triển nông nghiệp\". Nghị quyết nêu lên tình hình nông nghiệp và sự lãnh đạo nông nghiệp trong thời gian qua, phương hướng nhiệm vụ phát triển nông nghiệp trong giai đoạn mới, những chủ trương, biện pháp lớn và tǎng cường sự chỉ đạo của Đảng và Nhà nước trong nông nghiệp.\nĐây là một vǎn kiện quan trọng nhằm cụ thể hoá nghị quyết Đại hội lần thứ IV của Đảng về đường lối phát triển nông nghiệp trong 5 nǎm 1976-1980.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '21','06', 'Mácxơ Vônphơ (Max Wolf) sinh ngày 21-6-1863, tại nước Đức, qua đời ngày 3-10-1932. Ông là nhà thiên vǎn học, người đầu tiên dùng máy ngắm nổi khám phá 228 tiểu hành tinh và một sao chổi mang tên ông.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '21','06', 'Rimxki Coócxacốp từ trần ngày 21-6-1908 tại Pêtecbua (nước Nga). Ông sinh ngày 6-3-1844 tại vùng Nôvôgôrốt.\nNǎng khiếu âm nhạc của Coócxacốp được gia đình chǎm sóc, từ khi còn nhỏ tuổi đã học pianô. Sau khi tốt nghiệp trường hải quân, ông đã đi vòng quanh thế giới trong 3 nǎm. Những ấn tượng tốt đẹp của cuộc du lịch nay được ghi lại trong nhiều tác phẩm của ông.\nÔng là người sáng tác, là giáo sư âm nhạc, chỉ huy giàn nhạc giao hưởng, đã từng biểu diễn ở Pari và Bruxen. Khi cuộc cách mạng Nga nǎm 1905 nổ ra, ông đứng về phía những người cách mạng.\nRimxki Coócxacốp để lại một kho tàng âm nhạc lớn lao gồm nhiều thể loại.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','08', 'Phùng Chí Kiên sinh nǎm 1901, quê ở Mỹ Quang Thượng, huyện Diễn Châu, tỉnh Nghệ An. Ông xuất thân trong một gia đình nông dân, giác ngộ Cách mạng từ lúc trẻ tuổi. Nǎm 1926 ông sang Quảng Châu, dự lớp huấn luyện đầu tiên do Nguyễn Ái Quốc tổ chức, rồi vào học trường Võ bị Hoàng Phố. Tại đây ông trực tiếp tham gia cuộc khởi nghĩa Quảng Châu ngày 12-12-1927 do Đảng cộng sản Trung Quốc tổ chức.\nNǎm 1931 ông sang Matxcơva học tại trường đại học Phương Đông, nhưng giữa đường bị phát xít Nhật bắt giam. Ra tù ông tiếp tục sang Liên Xô học tập. Nǎm 1934 ông về Hồng Kông cùng Lê Hồng Phong, Hà Huy Tập chuẩn bị triệu tập Đại hội đại biểu toàn quốc Đảng cộng sản Đông Dương. Tại Đại hội, ông được bầu vào ban chấp hành Trung ương Đảng, phụ trách công tác Đảng ở nước ngoài. Nǎm 1936, ông bị bắt ở Hồng Kông rồi bị trục xuất. Ông trở lại Trung Quốc vận động thành lập Việt Nam độc lập đồng minh Hội, phá âm mưu \"Hoa quân nhập Việt\" của Quốc dân Đảng.\nNǎm 1941 ông về Cao Bằng tực tiếp chỉ đạo khu cǎn cứ Bắc Sơn. Sau đó bị bắt. Ngày 21-8-1941 ông bị giặc Pháp chặt đầu rồi bêu ở cầu Ngân Sơn để trấn áp tinh thần cách mạng của nhân dân. Sau Cách mạng tháng Tám, ông được hội đồng chính phủ truy phong quân hàm cấp tướng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','08', 'Hội nghị tuyên huấn toàn dân lần thứ nhất họp ngày 21-8-1951. Hội nghị thống nhất nhận thức về vai trò trọng yếu của công tác giáo dục chính trị, lãnh đạo tư tưởng trong quân đội. Nghị quyết của hội nghị nêu rõ :\"Việc lãnh đạo tư tưởng hiện nay phải đặt thành nhiệm vụ hết sức quan trọng trong quân đội\". Trọng tâm lãnh đạo tư tưởng lúc này là rèn luyện cho cán bộ chiến sĩ có một cơ sở tư tưởng, chính trị vững chắc, nâng cao ý thức giác ngộ giai cấp và dân tộc, xây dựng tư tưởng kháng chiến lâu dài, gian khổ và lòng tin tưởng nhất định thắng lợi')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','08', 'Ngày 21-8-1959, tại huyện Long Mỹ (tỉnh Cần Thơ), nơi Mỹ Diệm làm điểm xây dựng trại tập trung mang tên là \"Khu trù mật\" đã nổ ra cuộc đấu tranh quyết liệt. Mặc dù Mỹ Diệm cho quân lính cưỡng ép 12.000 gia đình phải vào các khu tập trung những kết qủa thì dồn được vài chục gia đình trong số hàng vạn gia đình ở Long Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','08', 'Từ 21 đến 24-8-1965, liên tiếp trong bốn ngày, hơn 2.000 sinh viên thanh niên Huế tập trung giữa thành phố để phản đối chế độ quân dịch, và dùng loa phóng thanh đọc một bản tuyên ngôn đòi \"lật đổ chính phủ quân sự Thiệu Kỳ\"... Ngày 28-8, hơn 4.000 học sinh, sinh viên Huế lại biểu tình đòi lật đổ Thiệu - Kỳ. Đêm hôm ấy thanh niên ở thành phố Đà Nẵng đi xe đạp, xe máy khắp phố kêu gọi đấu tranh, và sáng hôm sau có 3.000 học sinh bãi khoá và nhiều cửa hiệu đóng cửa ủng hộ thanh niên. Để hưởng ứng cuộc đấu tranh của thanh niên, đồng bào Phật giáo ở Huế, Đà Nẵng tổ chức những \"đêm không ngủ\". Ở Sài Gòn, ngày 29-8, học sinh, sinh viên cử một đoàn đại biểu đến gặp Nguyễn Hữu Có, \"Tổng uỷ chiến tranh\" ngụy, lên án chế độ quân dịch.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','08', 'Trưa ngày 21-8-1967, máy bay Mỹ đánh phá Hà Nội. Đại đội 92 bộ đội hoá học đã thả khói che kín nhà máy điện Yên Phụ, làm cho địch không đánh trực tiếp được vào nhà máy. Đây là trận đánh đầu tiên địch sử dụng bom điều khiển từ xa nhưng mục tiêu (nhà máy điện Yên Phụ) vẫn được bảo vệ an toàn. Đây cũng lần đầu tiên bộ đội hoá học dùng màn khói che mắt quân thù để bảo vệ một cơ sở sản xuất quan trọng ở Thủ đô, có sự hiệp đồng với bộ đội pháo phòng không.\nTrong cuộc chiến đấu chống chiến tranh phá hoại của giặc Mỹ đối với miền Bắc nước ta, bộ đội hoá học còn sử dụng màn khói để bảo vệ đài phát thanh Mễ Trì, cầu Đuống, cầu Đáp Cầu, cầu Kỳ Lừa, cầu Đa Phúc, đập Thác Huống, trạm biến thế điện Bala - Bông Đỏ, cầu Phủ Lý, cầu Hàm Rồng, nhà máy thuỷ điện Thác Bà, nhà máy điện Việt Trì.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','09', 'Từ ngày 21 đến 22-9-1946, cuộc thi trẻ em khoẻ và đẹp lần đầu tiên ở nước ta đã được tổ chức tại Ấu Trĩ viên (Nay là Cung Thiếu nhi Hà Nội). Có 300 trẻ em dưới 30 tháng tuổi đã dự thi, chia làm 3 nhóm:\n- Nhóm A từ lọt lòng đến 6 tháng\n- Nhóm B từ hơn 6 tháng đến 12 tháng\n- Nhóm C Từ hơn 12 tháng đến 30 tháng.\nĐáng chú ý hầu hết những em được giải thưởng đều được nuôi bằng sữa mẹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','09', 'Nhà báo, nhà nhiếp ảnh Đinh Thuý hy sinh ngày 21-9-1967 tại mặt trận Trảng Dầu (nay thuộc tỉnh Tây Ninh).\nAnh tên thật là Bùi Đình Tuý, sinh năm 1914, quê ở xã Cảnh Dương, huyện Quảng Trạch, tỉnh Quảng Bình.\nAnh ra Hà Nội học nghề ảnh và nghề vẽ. Sau đó vào Sài Gòn làm thợ vẽ cho một hãng chiếu bóng ở tại đây anh đã bị địch bắt do hoạt động yêu nước. Sau khi trốn khỏi nhà tù, anh tham gia phong trào Việt Minh đặc khu Sài Gòn - Chợ Lớn. Sau Cách mạng Tháng Tám 1945, Đinh Thuý được phân công phụ trách công tác nhiếp ảnh của Sở Thông tin Sài Gòn, làm phóng viên báo \"Cảm tử\". Trong những năm kháng chiến chống Pháp, anh là phóng viên ảnh tại mặt trận. Năm 1954, anh tập kết ra Bắc, công tác tại Thông tấn xã Việt Nam. Năm 1961, được cử sang Cộng hoà Dân chủ Đức học ảnh màu và năm 1962 là người đầu tiên xây dựng cơ sở ảnh màu và chụp ảnh màu ở Hà Nội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','09', 'Ngân hàng thế giới công nhận nước Cộng hoà xã hội chủ nghĩa Việt Nam là hội viên chính thức ngày 21-9-1976.\nNgân hàng thế giới (viết tắt là W.B) ra đời từ tháng 7-1944 nhưng chính thức hoạt động từ ngày 25-6-1946.\nNó được coi là chiếc cầu nối giữa thị trường tài chính quốc tế với các nước nghèo và đang phát triển. Nó cung cấp cho các nước này các khoản tín dụng theo chương trình với các điều kiện ưu đãi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','10', 'Ngày 21-10-1956, tướng Nguyễn Sơn từ trần tại Hà Nội. Ông tên thật là Vũ Nguyên Bác, sinh năm 1908 ở làng Kiêu Kị, huyện Gia Lâm - Hà Nội.\nThuở nhỏ, ông học ở Hà Nội. Nam 1926 ông sang Trung Quốc tham dự lớp huấn luyện ở Quảng Châu do Nguyễn Ái Quốc tổ chức. Sau đó ông được cử vào học trường quân sự Hoàng Phố. Nguyễn Sơn đã tham gia cuộc Vạn lý trường chinh của Hồng quân Trung Quốc, trải qua nhiều công tác: huấn luyện chính trị, quân sự, báo chí văn nghệ trong Bát lộ quân, là ủy viên chính phủ công - nông Xô viết Trung Quốc. Năm 1945 về Việt Nam, ông được giao nhiệm vụ Chủ tịch Uỷ ban kháng chiến miền Nam, Cục trưởng Cục quân huấn - Bộ quốc phòng (tháng giêng năm 1947), và Tư lệnh kiêm Chính ủy Liên khu V, Liên khu IV. Ở những nơi trên, ông đã góp phần to lớn vào việc xây dựng lực lượng chiến đấu chính quy và tự vệ cho những năm đầu của cuộc kháng chiến. Tháng giêng 1948, ông được phong quân hàm thiếu tướng quân đội nhân dân Việt Nam. Năm 1951, ông trở lại Trung Quốc, công tác ở Bộ Tổng tham mưu Quân giải phóng nhân dân Trung Quốc, và tham gia cuộc kháng chiến Mỹ - Việt Triều. Tướng Nguyễn Sơn viết nhiều tài liệu phổ biến các tri thức quân sự.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','10', 'Nhà văn Võ Huy Tâm sinh năm 1926 ở tỉnh Nam Định, qua đời ngày 21-10-1996 tại Quảng Ninh.\nSau Cách mạng tháng Tám, ông lần lượt làm cán bộ công đoàn, cán bộ Đảng ở Bắc Giang.\nCuối năm 1948, ông được cử đi xây dựng phong trào vùng mỏ Hòn Gai, lúc đó đang bị thực dân Pháp chiếm đóng. Ông vừa làm thợ vừa tham gia lãnh đạo công nhân đấu tranh chống giặc và bọn chủ mỏ. Thực tế cuộc đấu tranh của thợ mỏ đã thôi thúc ông viết thành công cuốn tiểu thuyết \"Vùng mỏ\", tác phẩm được tặng giải nhất Giải thưởng văn nghệ 1951-1952. Với tác phẩm này, lần đầu tiên người công nhân cách mạng đã trở thành nhân vật trung tâm của nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '21','10', 'Anphôngxơ đơ Lamáctin (Alphonse de Lamartine) là nhà thơ lãng mạn Pháp, nhà hoạt động ngoại giao và chính trị. Ông sinh ngày 21-10-1790.\nThơ ông giàu tâm trạng đã ảnh hưởng sâu sắc đến các nhà thơ nhiều thế hệ nước Pháp và ngay cả các nhà thơ trong phong trào Thơ Mới Việt Nam (1930-1945).\nNội dung thơ ông được phản ánh bằng những lời thơ duyên dáng, nhạc thơ êm ái du dương, có chiều sâu tâm hồn. Nghệ thuật thơ ông đã có nhiều đóng góp quan trọng trong sự phát triển nghệ thuật thơ ca của nhân loại. Ông mất ngày 28-2-1869.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '21','10', 'Nhà khoa học người Thuỵ Điển Anphrét Bécna Nôben (Alfred Bernhard Nobel) sinh ngày 21-10-1833.\nNăm 1867, ông đã phát minh ra chất đi-na-mít, chất nổ có hiệu quả và dễ sử dụng đầu tiên, có thể dùng mở đường hầm xuyên núi.\nÔng sống độc thân, chuyên tâm nghiên cứu khoa học. Khi qua đời năm 1896, Nôben để lại chúc thư: Đề nghị lập một quỹ lấy từ tiền lời của tài sản của ông để hàng năm trao giải thưởng cho các nhà bác học, nhà văn, nhà hoạt động xã hội, kinh tế có cống hiến xuất sắc cho loài người. Giải thưởng này mang tên Nôben.\nGiải Nôben được lập ra từ năm 1901.\nTrị giá mỗi giải từ 11 nghìn đến 30 nghìn bảng Anh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','11', 'Anh Lý Tử Trọng, còn có tên là Huy, sinh nǎm 1914, quê Hà Tĩnh, là một chiến sĩ cộng sản trẻ tuổi, được Bác Hồ đào tạo trong Thanh niên cách mạng đồng chí hội ở Quảng Châu (Trung Quốc). Mùa thu nǎm 1929, anh về Sài Gòn hoạt động. Trong cuộc mít tinh kỷ niệm khởi nghĩa Yên Bái (ngày 9-2-1931) do ta tổ chức, để bảo vệ cán bộ đang diễn thuyết, Lý Tử Trọng đã bắn chết tên trùm mật thám ở Sài Gòn và bị giặc Pháp bắt giam.\nTrước toà án đế quốc, anh nói: \"Con đường của thanh niên chỉ có thể là con đường cách mạng\". Lý Tử Trọng hy sinh khi mới 17 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','11', 'Bác sĩ Hoàng Tích Trí sinh nǎm 1903 và mất ngày 21-11-1958.\nÔng là nhà nghiên cứu vi trùng học và các bệnh truyền nhiễm, dịch tễ. Bác sĩ Hoàng Tích Trí là đại biểu Quốc hội khoá I và là Bộ trưởng Y tế đầu tiên trong Chính phủ nước Việt Nam dân chủ cộng hoà, có nhiều đóng góp vào sự nghiệp chǎm lo, bảo vệ sức khoẻ nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','11', 'Ngày 21-11-1979, Chủ tịch nước Tôn Đức Thắng đã ký lệnh công bố \"Pháp lệnh về bảo vệ, chǎm sóc và giáo dục trẻ em\" (Được Uỷ ban Thường vụ Quốc hội thông qua ngày 14-11-1979).\nBản Pháp lệnh đã khẳng định: \"Trẻ em là nguồn hạnh phúc của gia đình, là tương lai của đất nước, là người sẽ tiếp tục sự nghiệp tổ tiên, gánh vác mọi công việc xây dựng và bảo vệ Tổ quốc xã hội chủ nghĩa\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','11', 'Ngày 21-11-1991, Việt Nam và Mỹ thảo luận bình thường hoá quan hệ ngoại giao giữa hai nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '21','11', 'Vônte tên thật là Phrǎngxoa Mari Anrê, sinh ngày 21-11-1694 tại Pari, thủ đô nước Pháp và qua đời nǎm 1778.\nVônte không chỉ là nhà vǎn, mà còn là nhà triết học, nhà hoạt động xã hội. Viện sĩ Viện Hàn lâm Pháp. Ông là ngọn cờ đầu của phong trào Ánh sáng Pháp thế kỷ XVIII, có ảnh hưởng đến cuộc cách mạng tư sản Pháp 1789, đến vǎn học và triết học châu Âu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '21','11', 'Cay xỏn Phômvihản sinh ngày 13-12-1920 tại Xavanakhet - Lào. Ông học đại học Luật ở Hà Nội, tham gia phong trào học sinh chống thực dân Pháp và phát xít Nhật ở Việt Nam. Nǎm 1948 trở về nước. Ông lãnh đạo phong trào kháng chiến chống pháp ở Đông bắc Lào. Nǎm 1949, ông là Tư lệnh quân đội Lào Itxala.\nNǎm 1950 giữ chức Phó chủ tịch Mặt trận Lào yêu nước. Tháng 2-1972, Cay xỏn được bầu làm Tổng Bí thư Ban Chấp hành Trung ương và đến Đại hội Đảng lần thứ V (3-1991) được giữ chức Chủ tịch Ban Chấp hành Trung ương Đảng nhân dân cách mạng Lào. Ngày 2-12-1975, nước Cộng hoà dân chủ nhân dân Lào ra đời, Cay xỏn Phômvihản được cử làm Chủ tịch Hội đồng Bộ trưởng nước Cộng hoà dân chủ nhân dân Lào và đầu nǎm 1991 được bầu làm Chủ tịch nước.\nÔng mất ngày 21-11-1992 tại thủ đô Viên Chǎn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '21','12', 'Ngày 21-12-1941 trên báo Độc Lập số 113, đồng chí Nguyễn Ái Quốc viết bài \"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '21','12', 'Ngày 21-12-1954, Chủ tịch Hồ Chí Minh đến thǎm nhà máy điện Yên Phụ và nhà máy điện Bờ Hồ (nay là Công ty điện lực Hà Nội).\nỞ hai nhà máy, Bác Hồ đã thǎm hỏi sức khoẻ, tình hình sinh hoạt, công việc của công nhân, cán bộ. Bác nói:\n\"Bác thay mặt Chính phủ khen ngợi và cảm ơn các cô, các chú. Bây giờ nhà máy thuộc về các cô, các chú, tất cả các cô, các chú phải đoàn kết chặt chẽ hǎng hái thi đua để xứng đáng là người chủ của nhà máy\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '21','12', 'Từ ngày 21 đến ngày 26-12-1965 đã diễn ra Hội nghị Ban Chấp hành Trung ương Đảng lần thứ 12. Hội nghị đã định ra nhiệm vụ, chủ trương, phương châm thích hợp với tình hình mới, động viên cả nước kiên quyết đánh bại cuộc chiến tranh xâm lược của đế quốc Mỹ\nHội nghị đề ra nhiệm vụ: \"Động viên lực lượng của cả nước kiên quyết đánh bại cuộc chiến tranh xâm lược của đế quốc Mỹ trong bất cứ tình huống nào để bảo vệ miền Bắc, giải phóng miền Nam, hoàn thành cách mạng dân tộc, dân chủ trong cả nước...\"\nNghị quyết Hội nghị Trung ương lần thứ 12 là một vǎn kiện lịch sử quan trọng về lý luận cũng như thực tiễn trong cuộc đấu tranh Cách mạng của nhân dân ta chống đế quốc Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '21','12', 'Ngày 21-12-1972. Chính phủ Việt Nam dân chủ cộng hoà ra \"Tuyên bố về bước leo thang chiến tranh mới của chính quyền Níchxơn\" với quy mô và mức độ tàn phá chưa từng thấy và vạch trần thủ đoạn lật lọng xảo quyệt của chính phủ Mỹ không chịu ký Hiệp định \"chấm dứt chiến tranh và lập lại hoà bình ở Việt Nam\" như đã thoả thuận.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('4', '21','12', 'Giǎng Raxin (Jean Racine) là nhà soạn kịch cổ điển vĩ đại nước Pháp. Ông sinh ngày 21-12-1639.\nCó thể chia sự nghiệp sáng tác của ông làm 3 giai đoạn. Giai đoạn đầu (1664-1665) với các tác phẩm như: Ǎngđrômác, Những kẻ sính kiện cáoExter, Atali thực sự là một tuyệt tác viên mãn của nền bi kịch cổ điển Pháp.\nBi kịch của ông đã dung hoà được một cách tuyệt vời giữa các mặt tương phản của cuộc sống, xung đột và trữ tình, bi thiết và hùng tráng, và dịu dàng lắng đọng. Ông được coi là người đã đưa bi kịch cổ điển Pháp sánh ngang với bất cứ nền bi kịch nào của chủ nghĩa cổ điển châu Âu.\nÔng mất ngày 21-4-1699.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('4', '21','12', 'Gustavơ Flôbe (Gustave Flaubert) là nhà vǎn hiện thực lớn nước Pháp. Ông sinh ngày 21-12-1821. Sáng tác đầu tay của ông là \"Xmar\" (1849). Bảy nǎm sau ông ra đời tiểu thuyết \"Bà Bôvary\" (1856) và ngay lập tức gây chấn động dư luận đương thời. Ngoài tác phẩm trên ông còn có các tác phẩm \"Xalambô\" (1862), \"Giáo dục tình cảm\" (1869) \"Ba truyện\"(1887), \"Buva và Pecuysê\".\nÔng là một nhà vǎn lao động nghiêm túc. Ông có công trực tiếp dìu dắt nhà vǎn Môpátsǎng ở những bước đi đầu tiên. Tầm vốc của tác phẩm nghệ thuật đã đưa ông vào hàng những đỉnh cao của nền vǎn học thế giới. Ông mất nǎm 1880.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('4', '21','12', 'Ngày 21-12-1972, Đông Đức và Tây Đức ký hiệp định chính thức chấm dứt hai thập kỷ thù địch trong thời kỳ chiến tranh lạnh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','01', 'Cuối nǎm 1884, các tổ chức yêu nước các tỉnh xung quanh và trong thành phố Sài Gòn dự tính khởi nghĩa, sẽ bất ngờ đánh vào cơ quan đầu não của Pháp, nhập cuộc với phong trào Cần Vương chống Pháp đang dấy lên trong cả nước. Đêm 22-1-1885, nghĩa quân đốt Khám lớn và một số công sở, nhưng sự phối hợp khởi nghĩa giữa các lực lượng bị trục trặc. Đề đốc Nguyễn Vǎn Bường, người cầm đầu cuộc khởi nghĩa bị bắt.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','01', 'Ngày 22-1-1962, Nhà máy đường Vạn Điểm, nhà máy đường lớn nhất miền Bắc lúc bấy giờ, được khánh thành. Ngoài việc sản xuất đường, nhà máy còn sản xuất thêm bột giấy từ bã mía, phân bón từ bùn mía và vôi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','01', 'Nhà vǎn Phùng Quán sinh tháng 1-1932 tại xã Thuỷ Dương, huyện Hương Thuỷ, Thừa Thiên - Huế.\nTham gia kháng chiến chống Pháp ở trong quân đội với tư cách là thiếu sinh quân chiến trường Bình Trị Thiên khói lửa. Sau hoà bình lập lại - 1954, Phùng Quán chuyển sang công tác vǎn hoá, theo dõi phong trào sáng tác vǎn nghệ quần chúng của Bộ Vǎn hoá.\nTác phẩm: \"Vượt Côn Đảo\" (tiểu thuyết), \"Tuổi thơ dữ dội\" (tiểu thuyết 3 tập) - giải thưởng hàng nǎm của Hội nhà vǎn - đã dựng thành phim \"Tiếng hát trên địa ngục Côn Đảo\"...Ông mất ngày 22-1-1995 tại Hà Nội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '22','01', 'Gótthôn ephraiem Lexinh (Gotthold ephraim Lessing) - nhà viết kịch, nhà phê bình lý luận vǎn học nổi tiếng Đức thế kỷ 18; ông là người đại diện cho phong trào \"Ánh sáng Đức\", sinh ngày 22-1-1729.\nNǎm 19 tuổi ông đã viết vở hài kịch \"Chàng học giả trẻ tuổi\". Ông có các vở kịch \"Cô Xara Xǎmpxơ\", \"Mina phon Banhem\", vở bi kịch \"Êmilia Galôtti\", vở kịch thơ \"Nathan, người thông thái\"... Ông còn có những sáng tác phê bình và lý luận như \"Những thư về vǎn hoá\", \"Laokun hay những ranh giới của hội hoạ và thơ\"... Các tác phẩm của ông phản ánh tư tưởng chống phong kiến, chống tôn giáo phản động, thể hiện lòng tin vào sự phát triển của con người tự do, vào lý trí và nhân đạo. Ông qua đời ngày 15-2-1781.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '22','01', 'Giogiơ Gođơn Bairơn (Goerge Gordon Byron) sinh ngày 22-1-1788, qua đời nǎm 1824. Ông là nhà thơ lớn của nước Anh thế kỷ XIX. Là một người yêu nước nồng nàn, yêu tự do, dân chủ, ông kịch liệt chống chế độ phong kiến, khuôn sáo đạo đức giả của xã hội thượng lưu. Tác phẩm nổi tiếng của Byron là tập thơ trường thiên \"Cuộc du hành của Childe Harold\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '22','01', 'Lép Đaviđơvich Lanđâu (Lev Davidovich Landau), nhà vật lý nổi tiếng Nga, người sáng lập trường phái vật lý, lý thuyết Liên Xô, sinh ngày 22-1-1908.\nNǎm 18 tuổi ông đã đǎng tác phẩm khoa học đầu tiên. Nǎm 1937 ông phát minh ra \"Hiện tượng từ tính Landau\" nổi tiếng. Ông được trao tặng giải thưởng Lênin, ba lần được giải thưởng quốc gia, là Viện sĩ Viện Hàn lâm khoa học. Nǎm1954 được tặng danh hiệu Anh hùng lao động, được tặng giải Nôbel vật lý. Ông qua đời ngày 1-4-1968.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','02', 'Từ ngày 22 tháng 2 đến ngày 13-4-1967, quân và dân miền Đông Nam Bộ đã đánh bại cuộc hành quân Gianxơn Xiti, gồm 45.000 quân, thuộc 8 lữ đoàn Mỹ vào vùng bắc tỉnh Tây Ninh nhằm tiêu diệt cơ quan đầu não của ta.\nTa loại khỏi vòng chiến đấu 14.000 tên địch, phá huỷ hơn 1.000 xe quân sự, 90 khẩu pháo, phá huỷ và bắn rơi 167 máy bay. Tướng Mỹ Lêman, chỉ huy cuộc hành quân này bị cách chức. Đập tan cuộc hành quân Gianxơn Xiti, ta đã giáng thêm một đòn chí tử vào kế hoạch phản công chiến lược mùa khô lần thứ hai của Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','02', 'Ngày 22-2-1983, Hội nghị cấp cao ba nước Đông Dương họp tại Viên Chǎn nhằm tǎng cường quan hệ đoàn kết chiến đấu, tuyên bố ủng hộ sự thoả thuận giữa Cộng hoà xã hội chủ nghĩa Việt Nam và Cộng hoà nhân dân Campuchia về việc rút quân tình nguyện Việt nam về nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','02', 'Nhà thơ, nhà giáo dục Võ Liêm Sơn sinh nǎm 1888, quê ở tỉnh Hà Tĩnh, mất ngày 22-2-1949.\nÔng làm giáo sư Hán vǎn và quốc vǎn ở trường Quốc học Huế. Nǎm 1926, ông gia nhập Đảng Tân Việt. Nǎm 1930, ông bị bắt Pháp bắt giam. Ra tù, ông sáng tác thơ vǎn kêu gọi lòng yêu nước. Nǎm 1944, ông tham gia Mặt trận Việt Minh, đến nǎm 1948 làm Chủ tịch Mặt trận Liên Việt liên khu IV. \nCác tác phẩm chính của Võ Liêm Sơn gồm có: \"Hài vǎn\" châm biếm chế độ thực dân. \"Cô lâu mộng\" (truyện dài), \"Ngắm non Hồng\" (tập thơ). Phê bình Vǎn hoá Đông Tây (dịch thuật).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '22','02', 'Phrêđêrich Sôpanh sinh ngày 22-2-1810, từ trần nǎm 1849. Ông là nhạc sĩ pianô, nhà soạn nhạc nổi tiếng của Ba Lan và thế giới.\nTác phẩm của ông có nhiều loại, nhưng chủ yếu cho đàn pianô. Những bản nhạc của ông có tính chất lãng mạn, dịu dàng, buồn man mác. Ông đã kết hợp âm nhạc truyền thống cổ điển với dân ca Ba Lan. Một số bản nhạc của ông đã nói lên sự phẫn nộ, cǎm uất và sự thương nhớ Tổ quốc Ba Lan bị nô dịch. Sôpanh là người cách tân phương pháp biểu diễn pianô trong lĩnh vực hoà âm và phối khí.\nSau khi Sôpanh qua đời, trái tim của ông được đưa về lưu giữ trên mảnh đất quê hương ông - Tổ quốc Ba Lan.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '22','02', 'Haienrich Ruđônphơ Hécxơ nhà vật lý vĩ đại người Đức, người có công tìm ra sóng điện từ và hiệu ứng quang điện, sinh ngày 22-2-1857.\nNǎm 1887, ông công bố công trình \"Những dao động điện rất nhanh\" và chế tạo thành công máy phát dao động điện cao tần gọi là bộ rung Hécxơ và một bộ cộng hưởng để phát hiện những giao động điện. Nǎm 1891 ông khám phá ra nhiều tính chất của tia tử ngoại, nghiên cứu điện động lực các môi trường chuyển động. Các công trình của ông đã góp phần vào việc chế tạo ra vô tuyến điện.\nÔng mất ngày 1-1-1894. Để tưởng nhớ ông, người ta dùng tên ông để đặt cho đơn vị tần số điện \"Hécxơ\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','03', 'Vào ngày này của nǎm 30, hơn 500 công nhân nhà máy diêm Bến Thuỷ tiến hành bãi công đòi tǎng lương, giảm giờ làm, được nghỉ trưa, chống đuổi thợ. Trước xu thế của phong trào công nhân Nghệ Tĩnh đang dâng cao, giới chủ đã nhượng bộ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','03', 'Báo Vệ quốc quân là cơ quan tuyên truyền và giáo dục bộ đội của quân đội ta. Báo này ra hàng tuần, đã ra số đầu tiên vào ngày 22-3-1947.\nĐến nǎm 1959, báo Vệ quốc quân và báo Quân du kích sáp nhập thành báo Quân đội nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','03', 'Sau thất bại ở Điện Biên Phủ và trên chiến trường Đông Dương, thực dân Pháp phải ký hiệp định Giơnevơ thừa nhận chủ quyền độc lập toàn vẹn lãnh thổ Việt Nam, Lào, Cǎmpuchia nhưng đế quốc Mỹ đã trực tiếp can thiệp vào Lào, lập ra chính phủ cực hữu, cải tổ quân sự và tǎng cường viện trợ cho bọn tay sai.\nTrước yêu cầu của tình hình và do sự trưởng thành của cách mạng Lào, ngày 22-3-1955, các chiến sĩ cách mạng Lào, đảng viên của Đảng Cộng sản Đông Dương trước đây, đã tiến hành thành lập Đảng Nhân dân cách mạng Lào.\nNhững người Cộng sản Lào và Đảng NDCM Lào đã có những cống hiến quan trọng vào thắng lợi quyết định của sự nghiệp đấu tranh chống xâm lược, giải phóng đất nước và xây dựng CNXH ở nước CHDCND Lào, cũng như sự nghiệp cách mạng chung của ba nước anh em Việt Nam - Lào - Cǎmpuchia trên bán đảo Đông Dương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','03', 'Ngày 22-3-1961, Đại hội Đại biểu Toàn quốc lần thứ ba của Đoàn họp tại Hà Nội. Về dự Đại hội có 655 đại biểu thay mặt cho 80 vạn đoàn viên và hàng triệu thanh niên các mặt trận, kinh tế, vǎn hoá, xã hội xây dựng quân đội, củng cố quốc phòng. Đại hội đã phát động \"Phong trào xung phong tình nguyện vượt mức kế hoạch 5 nǎm lần thứ nhất\". Đây là một phong trào lớn được đông đảo đoàn viên tích cực hưởng ứng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','03', '22-3-1973, Việt Nam và Bỉ thiết lập quan hệ ngoại giao ở cấp đại sứ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '22','03', 'Ngày 22-3-1946, Anh công nhận nền độc lập của Jordan, nước trước đây là thuộc địa của Anh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','05', 'Nhà vǎn Ngô Vǎn Triện, bút danh Trúc Khê, sinh ngày 22-5-1901 tại xã Xuân Phương, huyện Từ Liêm, Hà Nội. \nÔng là người nhiệt thành yêu nước, nǎm 26 tuổi đã chủ trương đánh thực dân Pháp để giành độc lập cho nước nhà. Ông đã tham Việt Nam quốc dân Đảng do Nguyễn Thái Học làm đảng trưởng. Cuộc khởi nghĩa Yên Bái thất bại, ông bị bắt giam ở Hoả Lò (Hà Nội). Ra tù, ông chuyên sáng tác vǎn học và mở \"Trúc khê thư cục\". Ông chưa kịp lên Việt Bắc để tham gia kháng chiến chống thực dân Pháp thì qua đời nǎm 1947.\nCác tác phẩm chính của Trúc Khê có: Tập thơ Chợ chiều, các tiểu thuyết Trǎm lạng vàng, Nét ngọc, Đò chiều, một số truyện danh nhân và nhiều cuốc sách biên thảo, dịch thuật.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','05', 'Ngày 22-5-1946, Hồ Chủ tịch ký sắc lệnh xác định \"Vệ quốc đoàn là Quân đội quốc gia của nước Việt Nam Dân chủ cộng hoà\". Biên chế của Quân đội thống nhất theo từng trung đoàn, tiểu đoàn, đại đội, có chính trị viên từ trung đội trở lên.\nHồ Chủ tịch dạy: \"Trung với nước, hiếu với dân là một bổn phận thiêng liêng, một trách nhiệm nặng nề, nhưng cũng là vinh dự của người chiến sĩ trong đạo quân quốc gia đầu tiên của nước ta\".\nTrong thời gian này, Vệ quốc đoàn được chấn chỉnh tổ chức thành 25 chi đội ở Nam Bộ và 30 trung đoàn ở Bắc Bộ, Trung Bộ, tổng số quân lên tới gần chục vạn. Du kích và tự vệ cũng phát triển lên tới hàng triệu người.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','05', 'Ngày 22-5-1946 Hội liên hiệp Quốc dân Việt Nam gọi tắt là Hội Liên Việt đã được thành lập. Chủ tịch Hồ Chí Minh được suy tôn là hội trưởng danh dự. Cụ Huỳnh Thúc Kháng là Hội trưởng, ngoài ra BCH Hội còn có 6 thành viên khác. Hội nhằm đoàn kết tất cả mọi người Việt Nam yêu nước không phân biệt đảng phái, giai cấp xu hướng chính trị, tôn giáo, tín ngưỡng, dân tộc trong một mặt trận chung, nhằm thực hiện mục tiêu xây dựng nước Việt Nam độc lập, thống nhất, dân chủ và giàu mạnh. Mặt trận Việt Minh lúc đó là một thành viên của Liên Việt.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','05', 'Ngày 22-5-1993, Chính phủ ta ra nghị định về việc thành lập Trung tâm Khoa học xã hội và Nhân vǎn quốc gia.\nTrung tâm này là cơ quan khoa học trực thuộc Chính phủ. Chức nǎng của Trung tâm là: Nghiên cứu những vấn đề khoa học xã hội và nhân vǎn trọng điểm của Nhà nước, cung cấp luận cứ khoa học để các cơ quan lãnh đạo định ra đường lối, chính sách xây dựng và phát triển đất nước, xây dựng và phát huy tiềm lực khoa học xã hội và nhân vǎn của cả nước.\nTrung tâm khoa học xã hội và nhân vǎn quốc gia gồm có: Viện triết học, Viện xã hội học, Viện nghiên cứu Nhà nước và Pháp luật, Viện kinh tế học, Viện kinh tế thế giới, Viện sử học, Viện dân tộc học, Viện vǎn học, Viện nghiên cứu vǎn hoá dân gian, Viện ngôn ngữ học, Viện nghiên cứu Hán Nôm, Viện nghiên cứu Đông Nam Á, Viện nghiên cứu tôn giáo, Viện tâm lý học, Viện khoa học xã hội tại thành phố Hồ Chí Minh, Nhà xuất bản khoa học xã hội, Viện thông tin khoa học xã hội, Tạp chí khoa học xã hội Việt Nam (bằng tiếng Anh).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '22','05', 'Ngày 22-5-1972 Tổng thống Mỹ Nichxơn chính thức đi thǎm Liên Xô, tiến hành hội đàm với tổng Bí thư L. Brêgiơnép. Nhân cuộc đi thǎm, hai bên đã ký nhiều hiệp ước về kinh tế, khoa học - kỹ thuật, vǎn hoá và bảo vệ môi trường. Quan trọng nhất là hiệp ước hạn chế vũ khí chiến lược SALT, đánh dấu giai đoạn hoà hoãn giữa hai siêu cường trên thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','06', 'Nguyễn Gia Thiều sinh nǎm 1741 và qua đời ngày 22-6-1798. Ông quê ở xã Ngũ Thái, huyện Thuận Thành, tỉnh Bắc Ninh.\nSinh ra trong một gia đình quý tộc, thuở bé ông được vào học trong phủ chúa Trịnh. Ông hiểu biết nhiều ngành nghệ thuật như âm nhạc, hội hoạ, kiến trúc, trang trí, làm quan được phong tước hầu. Nǎm 1786, nhà Tây Sơn đưa quân ra Bắc Hà diệt họ Trịnh và nǎm 1789, Nguyễn Huệ đại phá quân Thanh, lập ra triều đại mới. Nguyễn Gia Thiều được mời ra cộng tác, nhưng ông từ chối. Ông trở về quê, hằng ngày uống rượu tiêu sầu, sống như một người bất đắc chí. \nNguyễn Gia Thiều sáng tác nhiều thơ chữ Hán, nổi tiếng hơn cả là tập thơ chữ Nôm có tên là: \"Cung oán ngâm khúc\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','06', 'Nguyễn Thông, nhà thơ, sinh ngày 22-6-1827 ở Phú Ngãi Trị, Vàm Cỏ, tỉnh Long An.\nNǎm 1849 ông đỗ cử nhân. Nǎm 1856 ông tham gia biên soạn bộ \"Khâm Định nhân sự kim giám\". Khi giặc Pháp chiếm Gia Định, ông về Nam đánh giặc. Nǎm 1862 ông nhận chức đốc học Vĩnh Long. Thời gian này ông tích cực giúp nhân dân trong việc giáo dục. Nǎm 1871, ông bị triều đình xử oan. Sau được minh oan và được thǎng Hàn lâm viện trứ tác, lãnh chức Tư nghiệp Quốc Tử Giám. Tại đây ông kiểm tra lại bộ \"Khâm Định Việt sử thông giám cương mục\". Nǎm 1881 ông được triều đình sung chức Điện nông phó sứ kiêm chức Đốc học Bình Thuận.\nCác tác phẩm chính của Nguyễn Thông là : \"Khâm Định nhân sự kim giám\", \"Dương chính lục\", \"Việt sử thông giám cương mục khảo lược\", \"Kỳ xuyên thi sao\", \"Kỳ xuyên vǎn sao\", \"Ngoa du sào tập\".\nÔng mất ngày 7-7-1884, thọ 57 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','06', 'Ngày 22-6-1953 Chính phủ Việt Nam dân chủ cộng hoà ban hành chính sách dân tộc. Chính sách dân tộc dự trên đường lối chung là đoàn kết các dân tộc trên nguyên tắc bình đẳng, tương trợ để kháng chiến, kiến quốc, giúp đỡ nhau tiến bộ, phát tiển mọi mặt: chính trị, quân sự, kinh tế, vǎn hoá, tiến tới thành lập những khu dân tộc tự trị.\nChính sách dân tộc ra đời đã góp phần tǎng thêm sức mạnh đoàn kết kháng chiến của các dân tộc trên đất nước Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','06', 'Ngày 22-6-1965, đế Quốc Mỹ và bè lũ tay sai đã giết hại anh Trần Vǎn Đang, người thợ điện 24 tuổi. Cũng như Nguyễn Vǎn Trỗi và Lê Độ, Trần Vǎn Đang đã nêu cao khí phách và tinh thần không nao núng trước bạo tàn. Trước khi chết, anh nói thẳng vào mặt quân thù: \"Tao chết nhưng hàng vạn, hàng triệu đồng bào sẽ đứng lên chống lại chúng bay\", và hô lớn: \"Hồ Chí Minh muôn nǎm \", \"Đả đảo đế quốc Mỹ!\", \"Việt Nam muôn nǎm!\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '22','06', 'Rạng sáng ngày 22-6-1941, phát xít Đức bất thình lình mở cuộc tấn công trên khắp biên giới phía Tây của Liên Xô, trải dài từ biển Ban Tích đến biển Đen. Do yếu tố bất ngờ và chiếm ưu thế hơn hẳn về lực lượng và kinh nghiệm chiến đấu, quân Đức nhanh chóng tiến sâu vào lãnh thổ Liên Xô. Đến cuối tháng 10-1941, mũi phía Bắc đã bao vây Lêningrát, mũi trung tâm tiến sát cách Mátxcơva 20 kilômét, mũi phía Nam tới Rôxtôp bên bờ biển đen.\nMặc dù khó khǎn ban đầu những nhân dân Liên Xô đã kháng cự dũng cảm, ngoan cường nên chỉ trong hai tháng đầu quân phát xít Đức đã tổn thất vô cùng nặng nề.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '22','06', 'Ngày 22-6-1940, Chính phủ Pêtanh của Pháp đã phải ký hiệp định đình chiến với Đức. Theo hiệp định này, Pháp bị tước vũ trang hai phần ba nước Pháp bị Đức chiếm đóng, trong đó có thủ đô Pari; vùng Andat và Loren của Pháp phải sáp nhập vào Đức; Pháp phải nuôi toàn bộ quân đội chiếm đóng Đức.\nTrước khi ký hiệp định này, Pháp bị thất bại nặng nề trong trận Đoongkeccơ (Dunkerque) (từ ngày 26-5 đến ngày 4-6-1940) rồi \"bỏ ngỏ\" thủ đô Pari. Chính phủ Pháp bỏ chạy về Tua (ngày 10-6) và ngày 16-6-1940, Râynô từ chức, Pêtanh lên cầm đầu chính phủ Pháp đã xin đầu hàng Đức.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','07', 'Dưới sự chỉ đạo của đồng chí Nguyễn Vǎn Cừ, Hà Huy Tập, ngày 22-7-1938, tờ báo \"Dân chúng\" ra số đầu tiên tại Sài Gòn. Tham gia toà soạn báo còn có Nguyễn Vǎn Nguyễn, Nguyễn Vǎn Kỉnh, Nguyễn Thị Lựu, Nguyễn Vǎn Trân...\nTờ \"Dân chúng\" không những là cơ quan tuyên truyền đường lối chính sách của Đảng, mà còn là trung tâm chỉ đạo và tổ chức phong trào đấu tranh của quần chúng đòi ân xá chính trị phạm, chống thuế... Tờ báo có uy tín lớn trong quần chúng. Số lượng phát hành ban đầu khoảng 3.000 bản sau lên tới 15.000 bản. Lo sợ trước ảnh hưởng lớn của tờ báo, đã hai lần thực dân Pháp kiếm cớ giữ những người làm báo, khám xét toà soạn. Song nhân dân đã tổ chức 28 cuộc biểu tình và quyên góp tài chính cho báo. Tờ báo tồn tại cho đến khi chiến tranh thế giới lần thứ II bùng nổ, tổng cộng ra được 80 số.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','07', 'Ngày 22-7-1946 Đảng xã hội Việt Nam được thành lập. Đảng xã hội Việt Nam ra đời nhằm tập hợp những trí thức Việt Nam yêu nước muốn sát cánh cùng toàn dân bảo vệ thành quả Cách mạng Tháng Tám, bảo vệ nền độc lập của Tổ quốc. Đảng xã hội Việt Nam hoàn toàn nhất trí và triệt để ủng hộ đường lối Cách mạng do Đảng cộng sản Việt Nam đề ra, coi việc thực hiện đường lối này là nhiệm vụ chính trị hàng đầu của mình.\nNgay từ khi ra đời, Đảng xã hội Việt Nam đã tự nguyện ra nhập Hội Liên hiệp Quốc dân Việt Nam, do Chủ tịch Hồ Chí Minh sáng lập để mở rộng khối đại đoàn kết dân tộc. Đảng đã tuyên bố giải tán sau khi hoàn thành sứ mệnh của mình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','07', 'Vào lúc 0 giờ ngày 22-7-1954, Bộ Tư lệnh quân đội nhân dân Việt Nam ra lệnh ngừng bắn trên toàn chiến trường Việt Nam. Quân đội nhân dân Việt Nam cùng toàn dân kết thúc thắng lợi cuộc kháng chiến chống thực dân Pháp và can thiệp Mỹ. Từ đây, Quân đội nhân dân Việt Nam chuyển sang một giai đoạn mới - giai đoạn cùng nhân dân cả nước bước vào xây dựng chủ nghĩa xã hội trên miền Bắc và đấu tranh đòi thống nhất Tổ quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','07', 'Tổ máy thứ nhất của công trình phát điện tua bin khí Tiền Hải (Thái Bình) có công suất 17 KW đã chính thức phát điện phục vụ sản xuất và đời sống nhân dân, vào ngày 22-7-1981. Đây là công trình đầu tiên ở nước ta khai thác, sử dụng khí thiên nhiên dưới lòng đất.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','07', 'Ngày 22-7-1992 Việt Nam tham gia \"Hiệp ước Bali\" - Hiệp ước hữu nghị và hợp tác Đông Nam Á (ASEAN) và trở thành quan sát viên của tổ chức này.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '22','07', 'Tại Bǎng Cốc ngày 22 và ngày 23-7-1994 đã diễn ra Hội nghị ngoại trưởng lần thứ 27 các nước ASEAN. ở cuộc họp này, lần đầu tiên có sự tham gia của 10 nước Đông Nam Á bao gồm sáu nước hội viên ASEAN, hai nước quan sát viên Việt Nam và Lào cùng khách mời là Campuchia và Mianma.\nVấn đề được dư luận quốc tế quan tâm trong hội nghị là việc Việt Nam gia nhập ASEAN. Hội nghị chính thức thông báo, ASEAN nhất trí hoan nghênh Việt Nam sẵn sàng gia nhập ASEAN, coi đó là một đóng góp quan trọng vào việc xây dựng Đông Nam Á hoà bình, ổn định, hợp tác, hữu nghị và phồn vinh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','08', 'Được thắng lợi của khởi nghĩa giành chính quyền thành công ở Hà Nội (19-8) cổ vũ, nhân dân địa phương hǎng hái vùng lên với khí thế cách mạng sục sôi. Tại thị xã Cao Bằng, tối 22-8-1945 một đơn vị quân giải phóng tiến vào thị xã phối hợp với các lực lượng quần chúng buộc Nhật giao nộp vũ khí, giải tán chính quyền tay sai và thành lập Uỷ ban nhân dân lâm thời. Tại thị xã Hưng Yên, trước sự ngoan cố của bọn ngụy quyền, lực lượng quần chúng đã biểu tình thị uy khiến bộ máy chính quyền tan rã và lập nên chính quyền cách mạng ngày 22-8-1945. Cũng trong ngày 22-8 nhân dân các tỉnh lỵ khác như Kiến An, Tân An đã nổi dậy giành chính quyền về tay cách mạng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '22','08', 'Ngày 22-8-1976 lần đầu tiên trạm tự động Luxna 24 của Liên Xô (cũ) đã lấy được một số mẫu đất từ mặt trǎng về trái đất. Những mẫu đất đá này cho phép các nhà khoa học nghiên cứu nhằm phát triển khoa học cơ bản và ứng dụng các ngành kinh tế.\nNgày 22-8-1990, Quốc hội Cộng hoà Dân chủ Đức thông qua quyết định: Cộng hoà dân chủ Đức sáp nhập Cộng hoà Liên bang Đức vào ngày 3-10-1990.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','09', 'Nhà thơ Hàn Mặc Tử còn có tên là Nguyễn Trọng Trí, sinh ngày 22-9-1912 tại Lệ Mỹ, thị xã Đồng Hới, tỉnh Quảng Bình.\nTừ năm 1930, 1931 ông làm thơ và bắt đầu có tiếng tăm. Năm 1932 ông vừa làm việc ở sở Đạc điền Quy Nhơn vừa làm thơ đăng ở tuần báo \"Phụ nữ Tân Văn\". Năm sau, ông xin thôi việc, rồi vào Sài Gòn, giữ trang văn chương ở các báo \"Sài Gòn\", \"Công luận\", \"Tân Thời\". Được một năm, ông trở về Quy Nhơn, rồi mắc bệnh phong, điều trị tại nhà khá lâu không khỏi, ông bị cưỡng bức vào nhà thương Quy Hoà. Các tập thơ xuất sắc của ông là \"Gái quê\", \"Thơ Hàn Mặc Tử\".\nÔng mất ngày 11-11-1940 khi mới 28 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','09', 'Nguyễn Đỗ Cung là hoạ sĩ xuất sắc trong thế hệ hoạ sĩ tạo hình nước ta. Ông là con cụ tú Nguyễn Đỗ Mục, một học giả nổi tiếng vào thời quốc ngữ đang phát triển. Nguyễn Đỗ Cung sinh năm 1912. Ông học tại trường Cao đẳng Mỹ thuật Đông Dương, và sớm có danh trong những năm 1935, 1936. Ông nhiệt thành yêu nước, tham gia cách mạng, tích cực hoạt động và trải qua các nhiệm vụ: Uỷ ban thường vụ Hội Mỹ thuật Việt Nam, Uỷ viên ban chấp hành Hội liên hiệp Văn học nghệ thuật Việt Nam, Viện trưởng Viện Mỹ thuật, đại biểu Quốc hội.\nÔng mất ngày 22-9-1977 tại Hà Nội, thọ 65 tuổi, nhận xét đánh giá con người và sự nghiệp ông, hoạ sĩ Nguyễn Văn Tỵ viết: \"Trong bão táp của sáng tạo nghệ thuật, trong cái phức tạp và gian khổ của công tác cách mạng. Nguyễn Đỗ Cung là một mũi thép sắc nhọn, một cây thông vút ngọn lên cao. Vô tư, liêm khiết, vì mọi người. Nguyễn Đỗ Cung vẫn giữ được trên tranh và trong tâm hồn tính dân tộc đó\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','09', 'Từ ngày 22 đến 29-9-1985 đã diễn ra Đại hội Thể dục thể thao toàn quốc lần thứ nhất tại Hà Nội. Có 1.250 cán bộ, vận động viên, huấn luyện viên, trọng tài đã tham dự Đại hội. 42 đoàn vận động viên tham gia diễu hành và một chương trình đồng diễn thể dục với quy mô lớn, với hàng ngàn người tham gia được tổ chức trong buổi khai mạc.\nKết quả đại hội: 27 đoàn được nhận 125 bộ Huy chương các loại, lập 19 kỷ lục quốc gia mới về môn điền kinh, bơi và bắn súng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','10', 'Nguyên phi Ỷ Lan tên thật là Lê Thị Yến, từ trần ngày 22-10-1117.\nBà quê ở làng Thổ Lỗi, nay thuộc xã Dương Xá, huyện Gia Lâm Hà Nội.\nLà một người phụ nữ chịu khó học hỏi, bà được triều thần khâm phục về sự hiểu biết uyên thâm trên mọi lĩnh vực và có tài cai trị đất nước.\nNăm 1069, vua Lý Thánh Tông cầm quân đi đánh giặc Chiêm Thành, bà Ỷ Lan được trao quyền nhiếp chính. Năm 1072, vua Lý Thánh Tông đột ngột băng hà, Ỷ Lan trở thành Hoàng thái hậu nhiếp chính vì lúc đó Lý Nhân Tông mới 7 tuổi.\nNăm 1077, nhà Tống đưa quân sang xâm lược nước ta. Thái hậu Ỷ Lan đã cùng với Thái uý Lý Thường Kiệt và thái sư Lý Đạo Thành điều khiển triều đình, huy động sức người, sức của cho cuộc kháng chiến, chỉ trong vòng mấy tháng, quân giặc Tống đã phải rút chạy về nước.\nSau khi Thái hậu Ỷ Lan qua đời, nhân dân ta vô cùng thương tiếc, lập đền thờ ở nhiều nơi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','10', 'Nguyễn Mậu Kiến sinh năm 1819 ở tỉnh Thái Bình. Ông đỗ tiến sĩ và làm quan đến chức Trung nghị đại phu. Khi thực dân Pháp xâm lược nước ta, ông đứng về phía chủ chương chiến dấu chống lại quân Pháp để bảo vệ đất nước, dâng sớ lên vua kể tội bọn quan lại đầu hàng giặc. Vì thế, ông bị cách chức. Sau năm 1874 Nguyễn Mậu Kiến mộ nghĩa binh, chuẩn bị khởi nghĩa ở Hưng Hoá, nhưng giữa chừng bị bệnh, mất ngày 22-10-1879.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','10', 'Ngày 22-10-1954, sau 3 tháng chuẩn bị khẩn trương, công trường khôi phục đường sắt Hà Nội - Lạng Sơn làm lễ khởi công. Anh chị em dân công thuộc nhiều thành phần dân tộc khác nhau ở các tỉnh miền xuôi, miền núi và thanh niên các thành phố mới giải phóng đã tình nguyện làm việc ở công trường này, tham gia hàn gắn vết thương chiến tranh, khôi phục nền kinh tế quốc dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','10', 'Giáo sư Cao Xuân Huy sinh năm 1900 tại tỉnh Nghệ An và qua đời ngày 22-10-1983 tại thành phố Hồ Chí Minh.\nSinh ra trong một gia đình có truyền thống văn hoá, giáo sư Cao Xuân Huy sớm hình thành và kiên định một bản lĩnh khoa học. Quan niệm chủ toàn nhìn thế giới của ông là một cấu trúc nhất thể, vĩnh hằng bao gồm các thành tố đa dạng và tương phản. Tư tưởng phương Đông, đặc biệt trong triết học Lão, Trang và Phật giáo Đại thừa vốn mang tính chủ toàn. Những kiến giải sâu sắc, độc đáo của triết gia Cao Xuân Huy về triết học Đông -Tây nói chung và Việt Nam nói chung còn có ý nghĩa phương pháp luận.\nTác phẩm \"Tư tưởng phương Đông gợi những điểm nhìn tham chiếu\" của ông được bạn đọc trong nước và ngoài nước đánh giá cao.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','10', 'Ngày 22-10-1990, Hội nghị Ngoại trưởng các nước cộng đồng châu Âu (EC) quyết định thiết lập quan hệ ngoại giao chính thức với Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '22','10', 'Giôn Rít sinh ngày 22-10-1887 tại thành phố Poótơlan (Hoa Kỳ).\nÔng là nhà văn, nhà báo, chiến sĩ cộng sản Mỹ. \nMùa hè năm 1917, ông sang nước Nga, được chứng kiến đầy đủ những diễn biến của Cách mạng tháng Mười. Trở về nước Mỹ, ông viết cuốn \"Mười ngày rung chuyển thế giới\" nhằm nói cho nhân dân Mỹ hiểu sự thật về nước Nga và cuộc cách mạng vô sản vĩ đại. Tác phẩm ra mắt bạn đọc năm 1919 và sau đó được dịch ra nhiều thứ tiếng.\nCũng năm 1919, Giôn Rít lại san Mátxcơva để làm việc ở quốc tế Cộng sản nhằm tiến tới sự thống nhất của hai đảng Cộng sản ở Mỹ. Ít lâu sau ông bí mật trở về Niu oóc, nhưng bị lộ phải quay trở lại nước Nga Xô - Viết và đã qua đời tại đây năm 1920.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','11', 'Nguyễn Trường Tộ sinh nǎm 1828, quê Hưng Nguyên, Nghệ An. Ngoài chữ Hán ông còn học chữ Pháp và chữ Quốc ngữ. Nǎm 1858, ông sang Pháp, ba nǎm sau mới về nước. Người Pháp đã có ý dùng ông làm tay sai, nhưng giàu lòng yêu nước, ông từ chối không nhận mọi chức tước.\nỞ ẩn nơi quê nhà, ông lần lượt gửi lên triều đình nhiều bài điều trần giá trị, đề nghị chính quyền cải cách chính trị, kinh tế, vǎn hoá, khoa học, giáo dục. Ngoài 14 bản trần tình về quốc kế dân sinh, ông còn có nhiều bài thơ hay được truyền tụng.\nÔng mất ngày 22-11-1871.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','11', 'Giáo sư Phạm Huy Thông sinh ngày 22-11-1916, quê ở tỉnh Hưng Yên, mất nǎm 1988 tại Hà Nội.\nNǎm 1937, ông sáng Pháp học tập, đậu tiến sĩ luật khoa và thạc sĩ sử - địa. Trên đất Pháp, ông hǎng hái hoạt động trong giới Việt kiều yêu nước.\nNǎm 1952, Chính phủ Pháp trục xuất ông về nước và quản chế tại Sài Gòn. Đầu nǎm 1954, ông lại tham gia đấu tranh chính trị và là Tổng thư ký phong trào hoà bình Sài Gòn - Chợ Lớn.\nNǎm 1956 đến nǎm 1967, ông làm Hiệu trưởng trường đại học Sư phạm Hà Nội. Từ nǎm 1968 là Viện trưởng Viện Khảo cổ học, Phó chủ nhiệm Uỷ ban khoa học xã hội. Nǎm 1987, giáo sư Phạm Huy Thông được Viện Hàn lâm khoa học Cộng hoà dân chủ Đức bầu làm viện sĩ.\nPhạm Huy Thông còn là nhà thơ, làm thơ từ nǎm 18 tuổi. Trong phong trào Thơ Mới, ông được chú ý với các tập thơ và kịch thơ: Yêu đương, Anh Nga, Tần Ngọc, Tiếng địch trong sông Ô.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','11', 'Ngày 22-11-1945, Chủ tịch Hồ Chí Minh ký sắc lệnh tổ chức Hội đồng nhân dân và Uỷ ban hành chính các cấp. Sắc lệnh quy định cách thức tổ chức chính quyền dân chủ nhân dân trong cả nước. Chính quyền ở mỗi địa phương sẽ có hai cơ quan: Thay mặt cho dân là Hội đồng nhân dân, do phổ thông đầu phiếu bầu ra, và vừa thay mặt cho dân, vừa đại diện cho Chính phủ là Uỷ ban hành chính, do Hội đồng nhân dân đề cử. Sắc lệnh quy định nhiệm vụ quyền hạn của Hội đồng nhân dân và ủy ban hành chính mỗi cấp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '22','11', 'Giắc Lơnđơn (Jack London) là nhà vǎn hiện thực Mỹ. Ông sinh ngày 12-11-1876.\nThời sinh viên ông đã cho đǎng một số truyện ngắn. Tác phẩm \"Tiếng gọi nơi hoang dã\" đã có tiếng vang. Truyện \"Tình yêu cuộc sống\" đã đưa ông vào hàng những nhà vǎn được công chúng rộng rãi hết sức yêu mến. Nǎm 1907 nhà vǎn cho ra đời tiểu thuyết \"Gót sắt\". Đây là tác phẩm viễn tưởng thể hiện những tư tưởng ước mơ xã hội của ông.\nGiắc Lơnđơn là nhà vǎn được quần chúng yêu mến và là nhà vǎn có tác phẩm được biết đến rộng rãi nhất trong thời đại của ông. Ông mất ngày 22-11-1916.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('1', '22','12', 'Ngày Thành lập Quân đội nhân dân Việt Nam')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','12', 'Nhà vǎn Chu Vǎn tên thật là Nguyễn Vǎn Chử, sinh ngày 22-12-1922, quê ở Thái Bình. \nTừ nǎm 1950, ông phụ trách toà soạn báo Cứu Quốc - Liên khu III. Từ nǎm 1958 là Trưởng ty Vǎn hoá tỉnh Nam Định, sau đó ông phụ trách Hội vǎn nghệ tỉnh Hà Nam Ninh.\nChu Vǎn thật sự khẳng định tài nǎng của mình bằng tiểu thuyết \"Bão biển\" (xuất bản nǎm 1969) viết về một vùng nông thôn công giáo ven biển miền đồng bằng Bắc Bộ trong những nǎm bước vào xây dựng chủ nghĩa xã hội.\nChu Vǎn có một bút pháp hiện thực chặt chẽ, vừa có thể khắc hoạ bức tranh xã hội, vừa đi xâu vào những số phận cá nhân và những tính cách nhân vật.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','12', 'Tháng 12-1944, lãnh tụ Hồ Chí Minh ra chỉ thị thành lập Đội Việt Nam tuyên truyền giải phóng quân. Chỉ thị nhấn mạnh: \"Tên Đội Việt Nam Tuyên truyền Giải phóng quân nghĩa là chính trị quan trọng hơn quân sự. Nó là đội tuyên truyền..., đồng thời nó là khởi điểm của giải phóng quân, nó có thể đi suốt từ Nam chí Bắc...\"\nNgày 22-12-1944, tại khu rừng thuộc huyện Nguyên Bình (Cao Bằng), Đội Việt Nam Tuyên truyền Giải phóng quân đã chính thức làm lễ thành lập, gồm 3 tiểu đội với 34 chiến sĩ được lựa chọn từ những chiến sĩ Cao-Bắc-Lạng, do đồng chí Võ Nguyên Giáp trực tiếp chỉ huy. Đây là đơn vị chủ lực đầu tiên của lực lượng vũ trang cách mạng và là tiền thân của Quân đội nhân dân Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','12', 'Ngày 22-12-1946 Ban Thường vụ Trung ương Đảng ra chỉ thị toàn dân kháng chiến. Bản chỉ thị vạch rõ: đánh phản động và thực dân Pháp xâm lược giành độc lập, thống nhất; đoàn kết chặt chẽ toàn dân, thực hiện toàn dân kháng chiến, đoàn kết với Miên, Lào và thân thiện với các dân tộc yêu chuộng tự do hoà bình, liên hiệp với nhân dân Pháp chống phản động thực dân Pháp.\nTiếp đó, đồng chí Trường Chinh đã viết một loại bài: \"Kháng chiến nhất định thắng lợi\", giải thích đường lối kháng chiến của Đảng đǎng trên báo Sự Thật, sau xuất bản thành tác phẩm \"Kháng chiến nhất định thắng lợi\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','12', 'Bảo tàng Quân đội nhân dân Việt Nam ở đường Điện Biên Phủ, Hà Nội thành lập ngày 22-12-1959. Tại đây trưng bày có hệ thống và khái quát quá trình xây dựng, trưởng thành trong chiến đấu của lực lượng vũ trang Việt Nam từ nǎm 1930 cho đến nay.\nCác phòng trưng bày gồm các phần:\n- Quá trình hình thành lực lượng vũ trang cách mạng (từ 1930-1945) và cuộc kháng chiến chống Pháp (1946-1954). \n- Lực lượng vũ trang trong cuộc kháng chiến chống Mỹ (từ 1955 đến 1975).\n- Lực lượng vũ trang cùng nhân dân xây dựng và bảo vệ Tổ quốc xã hội chủ nghĩa Việt Nam (từ 1976 đến nay).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '22','12', 'Ngày 22-12-1964 ngành đường sắt nước ta hoàn thành việc đóng mới đầu máy xe lửa kiểu Tự Lực, mang tên Nguyễn Vǎn Trỗi.\nĐây là chiếc đầu máy xe lửa đầu tiên do cán bộ, công nhân nhà máy xe lửa Gia Lâm (Hà Nội) và Tổng cục Đường sắt thiết kế, chế tạo với sự giúp đỡ của nhiều xí nghiệp và cơ quan các ngành khác.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '22','12', 'Ngày 22-12-1988, Tại diễn đàn Liên Hợp Quốc, Nam Phi, Cuba và Angola ký hiệp định đem lại nền độc lập cho Namibia, vốn là thuộc địa trước đây của Đức trong thế chiến I.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','01', 'Ngày 23-1-1961, Hội nghị Ban Chấp hành Trung ương Đảng Lao động Việt Nam đã quyết định thành lập Trung ương Cục miền Nam.\nTrung ương Cục là một bộ phận của Ban Chấp hành Trung ương Đảng chỉ đạo trực tiếp miền Nam, chủ yếu là Nam Bộ trong tình hình giao thông liên lạc có nhiều khó khǎn, đáp ứng đòi hỏi mới của cuộc chiến tranh. Còn khu V và các tỉnh Quảng Trị, Thừa Thiên vẫn trực thuộc Trung ương. \nThực tiễn Cách mạng ở miền Nam đã chứng minh việc thành lập Trung ương Cục là một chủ trương đúng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','01', 'Ngày 23-1-1973, đã diễn ra lễ ký tắt Hiệp định về chấm dứt chiến tranh, lập lại hoà bình ở Việt Nam tại Pari giữa cố vấn đặc biệt Lê Đức Thọ, đại diện cho Việt Nam dân chủ cộng hoà và tiến sĩ Hênri Kitxingiơ đại diện cho Hoa Kỳ.\nĐây là một thắng lợi to lớn của dân tộc ta, kết quả của quá trình đấu tranh đầy hy sinh, gian khổ, nhưng vô cùng anh dũng, vẻ vang của quân và dân ta trên cả 3 mặt trận quân sự, chính trị và ngoại giao.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','01', 'Đồng chí Trần Quốc Hoàn, tên thật là Nguyễn Trọng Cảnh, sinh ngày 23-1-1916 ở tỉnh Nghệ An, qua đời nǎm 1986. Trong cuộc đời của mình, đồng chí đã công tác nhiều nǎm ở Hà Nội và ngành công an. Tháng 12-1946, đồng chí được cử làm phái viên của Trung ương, hoạt động bí mật ngay trong lòng Hà Nội bị địch chiếm. Nǎm 1951 được bầu vào Ban chấp hành Trung ương Đảng, trực tiếp phụ trách Bộ Công an (nay là bộ nội vụ), đến nǎm 1954 kiêm Bí thư Thành uỷ Hà Nội. Nǎm 1972, được bầu vào Bộ Chính trị. Nǎm 1982 là Trưởng ban Dân vận Trung ương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','01', 'Võ Thị Sáu sinh nǎm 1935, quê ở quận Đất Đỏ, tỉnh Bà Rịa cũ (nay là tỉnh Bà Rịa - Vũng Tàu).\nTừ nǎm 14 tuổi, Sáu đã tham gia kháng chiến, dùng lựu đạn giết chết một tên quan ba Pháp và làm bị thương nhiều tên khác. Sau đó, chị ở lại Bà Rịa làm nhiệm vụ điều tra tình hình địch và liên lạc tiếp tế cho chiến khu. Chị là công an xung phong quận Đất Đỏ.\nNǎm 1950, chị về làng định xử tội một tên tay sai đại gian ác của địch nhưng đã bị giặc Pháp bắt.\nSuốt trong gần ba nǎm, chúng đã giam chị ở nhiều nhà tù, cùm kẹp, tra tấn rất dã man, có khi lại ngon ngọt dụ dỗ mong chị khai báo tổ chức và cán bộ cách mạng. Nhưng trước sau, chị can đảm chịu đựng, không để lộ bí mật. Ngày 23-1-1952, kẻ thù đã xử bắn chị Võ Thị Sáu tại Côn Đảo.\nNǎm 1993, Nhà nước ta đã truy tặng chị Võ Thị Sáu danh hiệu lực Anh hùng lượng vũ trang nhân dân và Huân chương Chiến công hạng nhất.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','02', 'Lý Càn Đức sinh ngày 23-2-1066, lúc lên ngôi mới 6 tuổi, hiệu là Lý Nhân Tông. \nĐến tuổi trưởng thành, nhà Vua trực tiếp lo việc nước, có thực tài, được ca ngợi là vị vua sáng suốt. \nTriều đại ông có chiến công rực rỡ: đập tan cuộc xâm lược của nhà Tống. Ông chú trọng vǎn hoá, giáo dục, mở các khoa thi kén chọn nhân tài và ưu đãi trọng vọng các nhà sư.\nLý Nhân Tông từ trần nǎm 1128.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','02', 'Đại hội Công đoàn Việt Nam lần thứ 2 họp ở Hà Nội từ 23 đến 27-2-1961. Đại hội tổng kết những kinh nghiệm chính của công tác công Đoàn trong thời gian 1950-1961 và cǎn cứ vào đường lối chủ trương của Đảng, xác định vai trò và chức nǎng của Công đoàn trong giai đoạn mới của Cách mạng nhằm đẩy mạnh các mặt hoạt động của Công đoàn, bảo đảm hoàn thành thắng lợi những nhiệm vụ mới. \nĐại hội đã thông qua nghị quyết: \"Tập trung lực lượng thực hiện nhiệm vụ trung tâm công nghiệp hoá xã hội chủ nghĩa, xây dựng miền Bắc làm cơ sở đấu tranh thống nhất nước nhà\". \nĐại hội đã thông qua điều lệ mới của Tổng Công đoàn Việt Nam, bầu ra Ban Chấp hành mới của Tổng Công đoàn và nhất trí tán thành đổi tên Tổng Liên đoàn Lao động Việt Nam thành Tổng Công đoàn Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '23','02', 'Hồng quân Xô viết thành lập ngày 23-2-1918, đây là lực lượng vũ trang đã bảo vệ vững chắc nhà nước Xô viết (1918-1920), chiến thắng chủ nghĩa phát xít (1940-1945), góp phần tích cực vào công cuộc xây dựng và bảo vệ Liên bang Xô viết trải qua quá trình chiến đấu và chiến thắng trở thành Quân đội Cách mạng có sức mạnh vĩ đại. Là lực lượng góp phần duy trì hoà bình thế giới và tham gia xây dựng quân đội Cách mạng các nước anh em.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '23','02', 'Gutenbéc (Gutenberg) sinh nǎm 1394, mất ngày 23-2-1468.\nÔng là chủ nhà in người Đức. \nÔng từng làm các nghề gọt đá quý, làm gương, rồi phát minh ra phương pháp in bằng những con chữ rời (in typô), tại Strasbourg nǎm 1440, được hoàn chỉnh ở Mayence nǎm 1448.\nTác phẩm đầu tiên được in typô là cuốn kinh thánh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '23','02', 'Cáclơ Phriđrích Gauxơ (Carl Friedrich Gauss), nhà toán học vĩ đại Đức, sinh ngày 30-4-1777. Vào nǎm 19 tuổi, ông đã giải được phương trình X17-1 = 0 và đưa ra cách dựng đa giác đều 17 cạnh bằng thước kẻ và com pa. Ông là người có tài tính nhẩm và là người đi tiên phong trong hình học phi Euclide (Ơclit). Ông mất ngày 23-2-1855.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '23','02', 'Ghêoóc Phriđich Henđen (Georg Friedrich Handel), nhà soạn nhạc Đức, sinh ngày 23-3-1685. Nǎm 1705, vở Opera đầu tiên của ông ra đời và thành công rực rỡ. Sau đó ông ra nước ngoài biểu diễn và nhập quốc tịch Anh. Tại Anh, ông được làm giám đốc nhiều nhà hát và từng là giám đốc Viện Hàn lâm Âm nhạc Hoàng gia. Nǎm 1752, ông bị mù nhưng vẫn tiếp tục sáng tác. Những sáng tác của ông gồm có 40 vở Opera và nhiều thánh ca, đặc biệt có hơn 30 oratorio, trong đó nổi tiếng hơn cả là oratorio Esther (1732). Kiệt tác nhất là tác phẩm \"Đấng cứu thế\" và \"The Water Music\".\nÔng qua đời ngày 14-4-1759.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '23','02', 'Ngày 23-2-1987 nhà thiên vǎn học C.Matxen tại đài quan sát thiên vǎn đặt tại Chilê và nhà thiên vǎn học Isentơn tại đài thiên vǎn Los Campanass (Mỹ) phát hiện vụ nổ khổng lồ của sao siêu mới Xupenôvaê 1987 A, thuộc tinh vân Majenlan ngoài Thiên hà, cách chúng ta 160.000 nǎm ánh sáng. Đây là hiện tượng thiên vǎn hiếm có mà ba thế kỷ rưỡi, từ 1604, mới lại thấy.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','03', 'Tàu \"Anbexaro\" thuộc loại tàu đi biển dài 85m, rộng 12m, trọng tải 3100 tấn đã được xưởng đóng tàu Ba Son (Sài Gòn ) đóng xong và hạ thuỷ cho chạy thử ngày 23-3-1922 đây là chiếc tàu thuỷ lớn nhất và trang bị khá hiện đại đầu tiên ở Đông Dương. Nó chứng tỏ đội ngũ công nhân đóng tàu Việt Nam có đủ khả nǎng đóng mới tàu đi biển.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','03', 'Ngày 23-3-1931, hơn 400 công nhân hãng Xôcôny ở nhà Bè (Sài Gòn) bãi công đòi tǎng lương, giảm giờ làm và chống đánh đập. Cuộc đấu tranh do Xứ ủy Nam Kỳ và Thành ủy Sài Gòn chỉ đạo trong đó có các đồng chí Võ Vǎn Tần, Lê Vǎn Lương. Thực dân đã điều lính đến đàn áp và vấp phải sự chống trả quyết liệt của công nhân. Cuộc đấu tranh này làm chấn động dư luận Sài Gòn và cả dư luận Pháp. Quốc tế Công hội đỏ đã cử một phái đoàn sang Đông Dương điều tra và đứng ra bào chữa cho các chiến sĩ công nhân bị toà án truy tố.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','03', 'Ngày 23-3-1973, Chính phủ Việt Nam Dân chủ Cộng hoà và Chính phủ Cộng hoà Italia quyết định thiết lập quan hệ ngoại giao ở mức đại sứ đặc mệnh toàn quyền.\nItalia là một nước có vị trí địa lý nằm trên bán đảo Apennin giáp Pháp, Thụy Sĩ, Áo, Nam Tư và Địa Trung Hải. Italia là một nước nghèo tài nguyên thiên nhiên, chỉ có tiềm nǎng về thuỷ điện, đá hoa cương; có diện tích: 301 nghìn 200 km2, dân số 55.810 nghìn người. Ngôn ngữ Italia là ngôn ngữ chính. Italia là thành viên của Liên hiệp quốc nǎm 1955, ngoài ra, còn là thành viên của nhiều tổ chức quốc tế khác, trong đó có khối quân sự Bắc Đại Tây Dương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '23','03', 'Đại hội đồng LHQ lần thứ 47 họp tháng 11-1922 đã quyết định tuyên bố ngày 22-3 hằng nǎm là \"ngày thế giới về nước\" và đề nghị các quốc gia hãy giành ngày này cho các hoạt động cụ thể phù hợp với tình hình của mỗi nước. Chủ đề của \"ngày thế giới về nước\" được xác định là \"nước sạch cho mọi người, mọi ngành\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','04', 'Phan Vǎn Trường - Nhà yêu nước, luật sư, từng tham gia các hoạt động dân chủ ở Pháp và Sài Gòn. Ông quê ở xã Đông Ngạc, huyện Từ Liêm thành phố Hà Nội. Đã đậu tiến sĩ Luật khoa ở Pháp và làm luật sư tại Pari rồi ít lâu sau trở về nước về sinh sống tại Sài Gòn. Ông liên hệ mật thiết với Phan Chu trinh, Nguyễn An Ninh. Thường bênh vực quyền lợi của nhân dân nên Phan Vǎn Trường được mọi người quý mến. Ông cũng là một trong những người Việt Nam đầu tiên tìm hiểu chủ nghĩa Mác. Tờ báo L\\'Annam do ông làm chủ bút có Đǎng Tuyên ngôn Đảng Cộng sản cùng loạt bài công kích chủ nghĩa đế quốc. Ông mất ngày 23-4-1933, khi 58 tuổi. Được tin ông từ trần, Phan Bội Châu rất cảm động và có đôi câu đối điếu ông. Trong đó có câu: \"Một kiếp thương tâm\" ám chỉ Phan Vǎn Trường cả cuộc đời đau đáu nỗi đau chung là mất nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','04', 'Bộ đội pháo binh ta đã bắn chìm cả một tốp 3 tàu biệt kích của địch ở vùng biển Quảng Bình vào ngày 23-4-1966. Trong 6 nǎm, từ nǎm 1966 đến nǎm 1972, pháo binh đã bắn cháy hoặc bắn bị thương 32 tàu chiến Mỹ ở ngoài biển thuộc các tỉnh Quảng Bình, Nghệ An, Vĩnh Linh, Thanh Hoá và Hải Phòng. \nTrong chiến công trên, đội nữ pháo binh xã Ngư Thuỷ, huyện Lệ Thuỷ, tỉnh Quảng Bình đã 4 lần bắn cháy tàu chiến Mỹ, được Bác Hồ gửi thư khen và tặng huy hiệu của Người')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','04', 'Ngày 23-4-1975, tỉnh Bình Tuy cũ đã được hoàn toàn giải phóng. \nSau thắng lợi ở Phan Thiết, các lực lượng vũ trang Bình Tuy chủ động nổ súng đánh chiếm cǎn cứ 6 và 13 của địch. Từ đêm 22-4-1975, quân ta đánh chiếm khu vực Láng Gòn. quân địch ở thị xã Hàm Tân bị hoàn toàn tan vỡ, xô nhau tháo chạy ra cửa biển Tân Lý. \nVới thắng lợi của cuộc hành quân thần tốc, đột phá mở đường vào phía nam, tiến công tiêu diệt địch ở Phan Rang, Phan Thiết, Hàm Tân, giải phóng các tỉnh Ninh Thuận, Bình Thuận, Bình Tuy, các lực lượng vũ trang của ta đã góp phần quan trọng làm thất bại biện pháp phòng ngự từ xa của địch ở hướng đông bắc Sài Gòn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '23','04', 'Sêchxpia, nhà thơ, nhà soạn kịch, vǎn hào lớn của tư tưởng nhân vǎn trong trào lưu vǎn hoá phục hưng Anh. Ông sinh ngày 23-4-1564. \nQuá trình sáng tác kịch của Sêchxpia có thể chia làm ba giai đoạn. Giai đoạn đầu khi lực lượng tiến bộ chống chế độ phong kiến đang giành được một số thắng lợi, ông viết một số hài kịch như: Giấc mộng đêm hè, Người lái buôn thành Vơnidơ... và một số kịch lịch sử. Sau là một số vở bi kịch như: Rômêô và Giuyliel. Giai đoạn thứ hai, thời kỳ chủ nghĩa tư bản thắng thế, nhưng lại làm bǎng hoại đạo đức xã hội. Giai đoạn này ông sáng tác những vở bi kịch nói lên sự thất vọng về chính trị và phản ánh sự xung đột giữa cá nhân với xã hội như vở Hǎmlét, Ôtenlô, Vua Lia. Giai đoạn thứ ba, ông trở lại khuây khoả với những bi kịch hoang đường tuy nội dung vẫn mang tính chất xã hội và có phê phán, tư duy sâu sắc. \nSêchxpia đã nói lên nguyện vọng, hoài bão của nhân dân, đề cao quyền tự do của con người, đòi quyền sống, quyền yêu đương, quyền được hạnh phúc trong các vở kịch của mình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '23','04', 'Một tay đua xe đạp Hy Lạp tên là Kênlôpơlớt (Kanellopoulos) lái máy bay Deudalus, đạp chân, đã cất cánh từ H\\'eraklion, phái bắc đảo Creta, để bay 118 kilômet trong 3 giờ 55 phút tới đảo Santorin. Vào ngày 23-4-1988.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','05', 'N\\'Trang Long sinh nǎm 1870, quê ở Bu N\\'Trang (nay thuộc huyện Đắc Nông, tỉnh Đắc Lắc).\nCuối nǎm 1911, thực dân Pháp đưa quân đến bình định các tỉnh ở Tây Nguyên, gây nhiều tội ác với đồng bào các dân tộc. Với lòng cǎm thù giặc sôi sục, N\\'Trang Long đã lãnh đạo nhân dân các dân tộc đánh giặc Pháp trong suốt 24 nǎm. Cuối cùng ông bị địch bắt và hy sinh ngày 23-5-1935. Tấm gương anh dũng của ông đã để lại trong lòng nhân dân các dân tộc Tây Nguyên những tình cảm tốt đẹp. Ở thành phố Hồ Chí Minh có đường phố mang tên ông.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','05', 'Ngày 23-5-1950 tại cǎn cứ địa Việt Bắc, Hội hữu nghị Việt Xô được thành lập. Lúc đó, cuộc kháng chiến chống thực dân Pháp vừa giành được thắng lợi quan trọng, quân và dân ta vừa phá được thế bao vây của địch mở đường nối liền đất nước Việt Nam trẻ tuổi vơí bạn bè nǎm châu. Vị Chủ tịch đầu tiên của Họ là đồng chí Tôn Đức Thắng, người chiến sĩ cách mạng đã kéo cờ phản chiến trên hạm đội Pháp ở Bắc Hải, bảo vệ thành quả Cách mạng tháng Mười, người chiến sĩ tiêu biểu cho tình đoàn kết chiến đấu giữa nhân dân Liên Xô và nhân dân Việt Nam.\nSự ra đời của Hội hữu nghị Việt Xô đánh dấu một giai đoạn mới trong quan hệ giữa hai nước. Cũng từ đây sự hợp tác Việt Xô trong nhiều lĩnh vực được mở ra với quy mô rộng khắp. Hội đã vinh dự đóng góp một phần trong sự nghiệp củng cố và phát triển tình hữu nghị anh em giữa hai Đảng, Chính phủ và nhân dân hai nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','05', 'Việt Nam và vương quốc Tây Ban Nha đã thiết lập quan hệ ngoại giao giữa hai nước vào ngày 23-5-1977.\nTây Ban Nha ở tây nam châu Âu, có diện tích 507.600 km<>sup2, số dân 39 triệu 622 nghìn người, thủ đô là thành phố Mađrit.\nTây Ban Nha là thành viên của Liên hiệp quốc của cả khối Bắc đại tây dương (NATO).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','05', 'Ngày 23-5-1983 ta đã thành lập vườn quốc gia Cát Bà (ở huyện Cát Hải, thành phố Hải Phòng).\nVườn này rộng 600 hecta, bảo tồn hệ sinh thái rừng nhiệt đới nguyên sinh với hơn 600 loài thực vật, có nhiều cây quí như báng, gội nếp, sǎng lẻ, kim giao, cùng nhiều loài động vật có giá trị như voọc đầu trắng, voọc quần đùi, cầy giông, khỉ mặt đỏ, khỉ đuôi lợn...\nỞ vườn quốc gia Cát Bà đã phát hiện được nhiều di tích vǎn hoá Hạ Long và dấu vết của người Việt cổ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','06', 'Từ ngày 23 đến ngày 27-6-1952, hội nghị lần thứ ba Ủy ban Liên Việt toàn quốc họp tại Việt Bắc. Trên diễn đàn hội nghị, đồng chí Trường Chinh đọc tham luận: \"Củng cố mặt trận dân tộc thống nhất và ý kiến về sửa đổi vài điểm trong 10 điểm ghi nhớ của Hội viên Liên Việt\". Hội nghị ra quyết định về những công tác cụ thể của mặt trận. những công tác nổi bật là: \"Thực hiện kế hoạch sản xuất tiết kiệm; định chính sách cụ thể của mặt trận để xúc tiến việc vận động đồng bào tôn giáo và vận động các dân tộc miền núi; sửa đổi vài điểm trong 10 điều ghi nhớ của Hội viên Liên việt; đẩy mạnh công tác của Mặt trận ở vùng sau lưng địch...\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','06', 'Theo quyết định của Trung ương Đảng và Chính phủ, ngày 23-6-1953, lớp chỉnh quân chính trị cho cán bộ trung cao cấp do Tổng quân ủy trực tiếp phụ trách, khai mạc tại Việt Bắc.\nHồ Chủ tịch đã huấn thị cho lớp học và nêu rõ: \"Mục đích chỉnh quân là làm cho quân đội ta thành một quân đội nhân dân Cách mạng quyết chiến quyết thắng\".\nKết quả của lớp chỉnh huấn là cán bộ đã nâng cao nhận thức tư tưởng về những vấn đề quan trọng như: Đường lối cách mạng; Trường kỳ kháng chiến, tự lực cánh sinh; Lập trường giai cấp, tổ chức kỷ luật... Lớp chỉnh quân là một thắng lợi chính trị có ảnh hưởng sâu rộng, nó xây dựng cơ sở tư tưởng rất cần thiết cho cuộc kháng chiến.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','06', 'Ngày 23-6-1979, đoàn học sinh Việt Nam đi dự kỳ thi toán quốc tế lần thứ 21 tổ chức tại Luân Đôn (Anh). Cả 4 học sinh Việt Nam đều được giải thưởng. Học sinh Lê Bá Khánh Trình đạt giải nhất với số điểm tối đa là 40 điểm trên 40. Ba học sinh còn lại đều đạt giải nhì.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','06', 'Ngày 23-6-1994, Quốc hội nước Cộng hoà xã hội chủ nghĩa Việt Nam khoá 9, kỳ họp thứ nǎm đã thông qua Bộ luật lao động. Bộ luật này có hiệu lực từ ngày 01-01-1995.\nNội dung chủ yếu của Bộ luật Lao động quy định: Việc làm, học nghề, hợp đồng lao động, thoả ước lao động tập thể, tiền lương, thời giờ làm việc, thời giờ nghỉ ngơi, kỷ luật lao động, trách nhiệm vật chất, an toàn lao động, vệ sinh lao động, những quy định riêng đối với lao động nữ, những quy định riêng đối với người lao động chưa thành niên và 1 số lao động khác, bảo hiểm xã hội, công đoàn, giải quyết tranh chấp lao động, quản lý Nhà nước về lao động, xử lý vi phạm pháp luật lao động...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '23','06', 'Ngày 23-6-1888, ban đồng ca công nhân thành phố Linlơ (nước Pháp) đã biểu diễn \"Quốc tế ca\" lần đầu tiên. Từ nǎm 1918 đến nǎm 1943, \"Quốc tế ca\" là quốc ca của Liên Xô.\n\"Quốc tế ca\" là bài ca chính thức của giai cấp vô sản trên thế giới. Lời của Pôchiê và nhạc của Đêgâytơ, cả hai đều là người Pháp. Ơgien Pôchiê là nhà thơ lớn nhất của vǎn học Công xã Pari (tháng 6-1871).\nHồ Chủ tịch là người đầu tiên dịch \"Quốc tế ca\" ra tiếng Việt. Nhiều chiến sĩ cách mạng Việt Nam đã hát vang \"Quốc tế ca\" khi bước ra pháp trường của thực dân Pháp, trước lúc hy sinh, để cổ vũ những người khác tin tưởng ở ngày mai tươi sáng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','07', 'Khúc Thừa Dụ quê ở xã Cúc Bồ, huyện Ninh Thanh, tỉnh Hải Dương đã dấy binh đánh đuổi bọ đô hộ nhà Đường, mở đầu nền tự chủ của đất nước ta. Ông là người đã một thời trị nước, an dân nhưng cũng chỉ xưng là Tiết Độ Sứ. Ông mất ngày 23-7-907, con là Khúc Hạo lên thay.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','07', 'Tạ Quang Bửu nhà hoạt động khoa học sinh ngày 23-7-1910 ở Nam Hoành, Nam Đàn, Nghệ An. Thuở nhỏ ông học ở Quảng Nam, Huế, tốt nghiệp ông được cấp học bổng du học ở Pháp, Anh. Ra trường ông được về dạy tại trường Quốc học Huế.\nÔng chuyên nghiên cứu toán học lý thuyết và toán học ứng dụng vào sinh vật, vật lý học, hoá học, ông đậu chứng chỉ hạng ưu của vǎn bằng cử nhân toán học khi ông du học bên Pháp. Ông luôn luôn quan tâm đến thời cuộc, vận mệnh dân tộc. Cách mạng Tháng Tám thành công ông đã có mặt trong hàng ngũ kháng chiến chống Pháp. Trong kháng chiến chống Pháp ông từng là Thứ trưởng Bộ Quốc phòng. Tại hội nghị Giơnevơ tháng 7-1954 ông là thành viên của phái đoàn chính phủ Việt Nam, thay mặt Bộ Quốc phòng ký các vǎn bản về quân sự với Pháp. Sau ngày tiếp quản Thủ đô ông tham gia đào tạo cán bộ khoa học kỹ thuật trên cương vị Hiệu trưởng trường đại Học Bách khoa Hà Nội, Bộ trưởng Bộ Đại học và Trung học chuyên nghiệp. Do có công lớn, ông được Nhà nước Việt Nam và nước ngoài tặng nhiều danh hiệu và huân chương cao quý.\nTạ Quang Bửu mất ngày 21-8-1986 tại Hà Nội, thọ 76 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','07', 'Hồ Tùng Mậu sinh nǎm 1896, quê ở Quỳnh Đôi, Nghệ An. Xuất thân trong một gia đình yêu nước, từ nǎm 1816 ông cùng một số đồng chí cách Mạng sang Xiêm và Trung Quốc lo việc cứu nước. Nǎm 1923 ông cùng Lê Hồng Sơn thành lập \"Tâm tâm xã\", một tổ chức có xu hướng Cộng sản và tham gia trong vụ ám sát toàn quyền Méclanh ở Sa Điện trong nǎm 1924. Nǎm 1925, qua những lần tiếp xúc với Nguyễn Ái Quốc ông trở thành một cán bộ xuất sắc trong tổ chức \"Việt Nam thanh niên Cách mạng đồng chí Hội\".\nNǎm 1926 ông ra nhập Đảng cộng sản Trung Quốc, bị chế độ Tưởng Giới Thạch bắt giam nhiều lần và lần cuối bị kết án chung thân đưa về nước. Suốt 12 nǎm tù đày và trải qua nhiều nhà lao đến tháng 3-1943 ông vượt ngục về hoạt động ở Trung bộ. Nǎm 1946 ông được cử làm Chủ tịch Uỷ ban Kháng chiến hành chính liên khu 4. Nǎm 1949 ông làm Tổng thanh tra Chính phủ, rồi được bầu vào Ban Chấp hành Trung ương Đảng và làm Hội trưởng Hội Việt Trung hữu nghị.\nNgày 23-7-1951 trên đường đi công tác ông đã hy sinh vì máy bay địch oanh tạc. Ông được nhà nước truy tặng Huân chương Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '23','07', 'Ngày 23-7-1962, Hiệp định về nền hoà bình, trung lập và hoà hợp dân tộc ở Lào đã được ký kết tại Giơnevơ (Thụy Sĩ). Hiệp định này quy định: Thành lập ở Lào chính phủ liên hiệp và trung lập gồm ba diện phái (Phuma, Bunmin, và Pathét Lào) nhưng chỉ tồn tại được 8 tháng sau khi ký, vì sự phá hoại của các lực lượng phản động bên trong nước Lào và của Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '23','07', 'Vào ngày 23-7-1972, vệ tinh Erts-1 (Mỹ) phóng lên, có thể xem như vệ tinh \"sinh thái\" đầu tiên. Nó nặng 930 kg đặt ở độ cao 930 km trên một quỹ đạo tròn - Cứ 18 ngày, nó lại bay qua trên cùng một điểm, các bộ cảm biến, camêra và hồng ngoại của nó cho phép quan sát được sự tiến triển của những dạng ô nhiễm khác nhau những bệnh tật cây cối, những sự lan tràn của côn trùng v.v...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '23','07', 'Hồi 21 giờ 33 phút ngày 23-7-1980 (theo giờ Mátxcơva) tại sân bay vũ trụ Baicônua, Liên Xô đã phóng tàu vũ trụ Liên hợp 37 do đoàn tphi hành quốc tế điều khiển gồm: Chỉ huy con tàu - hai lần anh hùng Liên Xô, phi công nhà du hành vũ trụ Liên Xô Gorơbátcô và nhà du hành, nghiên cứu vũ trụ Phạm Tuân.\nSau 8 ngày bay trong vũ trụ, ngày 31-7, hai nhà du hành vũ trụ đã trở về trái đất an toàn.\nChuyến bay vũ trụ này chứng minh được khả nǎng của chúng ta ở lĩnh vực này và là biểu tượng tuyệt đẹp của tình hữu nghị và sự hợp tác Việt Nam - Liên Xô. Nhà nước ta tặng thưởng đồng chí Phạm Tuân danh hiệu Anh hùng lao động và Huân chương Hồ Chí Minh, đồng chí Gorơbátcô danh hiệu Anh hùng lao động, Huân chương Lao động hạng nhất.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','09', 'Cầu Long Biên bắc qua sông Hồng ở Hà Nội được chính thức khởi công xây dựng từ tháng 9-1898 và khánh thành vào tháng 2-1902, thời Pháp thuộc nó có tên là cầu Đume (tên của Toàn quyền Đông Dương lúc đó), nhưng đồng bào ta quen gọi là cầu Sông Cái.\nCầu do kỹ xư nổi tiếng Épphen thiết kế, do người Pháp và người Việt cùng thi công - dài 1680 mét, có tới 20 bệ xây, trụ và mố. Xe lửa đi ở giữa, ôtô và xe đi hai bên. Ở đầu thế kỷ XX, cầu Long Biên là một trong những cây cầu lớn của thế giới.\nCầu Long Biên giữ vị trí quan trọng nối liền tả ngạn và hữu ngạn sông Hồng. Trong những năm chiến tranh và trong xây dựng hoà bình cầu Long Biên luôn luôn là tình cảm, là niềm tự hào, là biểu tượng trong lòng mỗi người dân Hà Nội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','09', 'Đúng 0 giờ ngày 23-9-1945 thực dân Pháp được sự giúp đỡ của quân Anh - Ấn đã nổ súng đánh chiếm hàng loạt mục tiêu quan trọng trong thành phố Sài Gòn, bắt đầu cuộc chiến tranh xâm lược lại Đông Dương. Chúng hy vọng nhanh chóng bình định Nam Bộ để làm bàn đạp chiếm tiếp Miền Trung và Miền Bắc Việt Nam.\nRạng sáng 23-9, Hội nghị liên tịch ở Xứ ủy, Ủy ban nhân dân, Ủy ban kháng chiến và đại diện của Tổng bộ Việt Minh đã họp cấp tốc tại 107 đường Cây Mai (Chợ Lớn) hạ quyết tâm chiến đấu và thông qua bản hiệu triệu của Ủy ban kháng chiến Nam Bộ. Kể từ ngày đó - ngày 23-9-1945, nhân dân Nam Bộ đứng lên kháng chiến với lời nguyền \"Mùa thu này ngày 23, ta đi lên theo tiếng kêu sơn hà nguy biến...\" và xứng đáng với danh hiệu \"Nam Bộ thành đồng đi trước về sau\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','09', 'Đội Thể Công nay là Đoàn TDTT Quân đội thuộc Tổng cục Chính trị nay trực thuộc Cục Huấn luyện chiến đấu - Bộ Tổng tham mưu, được thành lập ngày 23-9-1954.\nĐoàn Thể dục thể thao Quân đội gồm các đội bóng đá, bóng chuyền, bắn súng, bóng bàn, thể dục dụng cụ, điền kinh, bóng rổ, bơi lội, xe đạp, vật tự do, Karate, Têcônđô...\nĐây là một trong những trung tâm mạnh của phong trào thể dục thể thao Việt Nam, đặc biệt ở các bộ môn bắn súng, bóng đá (15 lần vô địch toàn quốc)...\nTrên 60 vận động viên được phong cấp kiện tướng, nhiều vận động viên đạt danh hiệu vận động viên cấp 1.\nNhiều vận động viên và các đội mạnh của đoàn tham gia đội tuyển quốc gia trong các giải thể thao khu vực, quôc tế và thi đấu hữu nghị đạt thành tích xuất sắc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','09', 'Ngày 23-9-1976, Việt Nam gia nhập Ngân hàng phát triển châu Á (viết tắt là ADB).\nNgân hàng này thành lập năm 1966, ngoài một số nước châu Á còn có một số nước ở lục địa khác.\nMục tiêu ngân hàng phát triển châu Á là thúc đẩy việc phát triển kinh tế và thương mại ở các nước đang phát triển ở châu Á, tăng cường sự hợp tác trong khu vực.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','10', 'Ngày 23-10-1896 thành lập trường Quốc học Huế. \nNhiều học trò của trường sau này đã trở thành danh nhân văn hoá, những nhà cách mạng tiền bối, những nhà khoa học lớn, các nhà văn, hoạ sĩ, nhạc sĩ nổi tiếng của nước ta. Những tên tuổi tiêu biểu như: Hồ Chí Minh, Trần Phú, Phạm Văn Đồng, Võ Nguyên Giáp, Nguyễn Chí Diểu, Tố Hữu, Tạ Quang Bửu, Hải Triều, Nguyễn Khánh Toàn, Xuân Diệu, Huy Cận,v.v.... làm rạng danh nhà trường.\nTừ năm 1975 đến nay, có nhiều học sinh Quốc học Huế đoạt giải trong các kỳ thi quốc tế.\nTừ năm 1995, trường Quốc học Huế là một trong ba trường trong cả nước được Bộ Giáo dục và đào tạo chọn làm trường phổ thông trung học chất lượng cao.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','11', 'Tào Mạt tên thật là Nguyễn Duy Thục sinh ngày 23-11-1930 tại xã Hữu Bằng, huyện Thạch Thất, tỉnh Hà Tây, qua đời nǎm 1993 tại Hà Nội.\nVới tư cách là nhà sân khấu, ông đã viết hơn 20 vở kịch, chèo, nổi tiếng hơn cả là bộ chèo lịch sử \"Bài ca giữ nước\" gồm 3 vở: \"Lý thánh Tông tuyển hiền\", \"Ỷ Lan nhiếp chính\", \"Lý Nhân Tông kế nghiệp\". Tác phẩm này không nhằm minh hoạ lịch sử mà khai thác những vấn đề tư tưởng và quan niệm đạo đức ý nghĩa thời sự đối với người xem đương thời. \"Bài ca giữ nước\" đạt tới giá trị cao về tư tưởng và nghệ thuật, có sức rung động lòng người, có thể coi là tác phẩm tiêu biểu nhất của sân khấu chèo trong 30 nǎm gần đây.\nTào Mạt đã được Nhà nước phong tặng danh hiệu Nghệ sĩ nhân dân và truy tặng Giải thưởng Hồ Chí Minh nǎm 1996.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','11', 'Đêm 22 rạng ngày 23-11-1940, Đảng bộ Đảng cộng sản Đông Dương Nam Kỳ đã phát động cuộc khởi nghĩa ở hầu khắp các tỉnh Nam Kỳ. Quân khởi nghĩa đã triệt hạ nhiều đồn bốt giặc, phá nhiều đường giao thông, thành lập chính quyền nhân dân và toà án cách mạng ở nhiều vùng trong các tỉnh Mỹ Tho, Gia Định, Bạc Liêu. Trong cuộc khởi nghĩa, lá cờ đỏ sao vàng xuất hiện lần đầu tiên.\nThực dân Pháp và tay sai cho máy bay dội bom và bắn phá nhiều làng mạc, đốt nhà, cướp của giết người. Chỉ trong 40 ngày, riêng ở 4 tỉnh Gia Định, Long Xuyên, Mỹ Tho, Cần Thơ đã có gần 6000 vụ bắt người, Giặc Pháp đã lấy giây thép xâu tay nhiều người vào nhau, rồi bắt đứng phơi nắng đến chết khô; hoặc chất họ lên sà lan đưa ra nhấn chìm ngoài biển cả.\nCơ sở Đảng bị tổn thất nặng, nhưng một số cán bộ và nghĩa quân đã kịp thời rút vào rừng chấn chỉnh đội ngũ để chờ cơ hội hoạt động trở lại.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','11', 'Ngày 23-11-1945, Hồ Chủ tịch đã ký Sắc lệnh thành lập Ban Thanh tra đặc biệt, là tổ chức tiền thân của Thanh tra Nhà nước hiện nay.\nThanh tra là tai mắt của Nhà nước, là bạn thân của dân. Ngành thanh tra xây dựng đội ngũ cán bộ viên chức trong sạch, vững mạnh, cần, kiêm, liêm, chính, chí công, vô tư, xứng đáng với lòng tin yêu của Đảng, Nhà nước và nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','11', 'Ngày 23-11-1946. Đại hội đại biểu Hội hồng thập tự Việt Nam lần thứ nhất họp tại Vân Đình (nay thuộc tỉnh Hà tây). Các đại biểu nhất trí bầu Hồ Chủ tịch làm Hội trưởng danh dự, Bác sĩ Vũ Đình Tụng, một trí thức công giáo yêu nước, có uy tín trong xã hội, được bầu vào Hội trưởng Hội thập tự.\nTại Đại hội lần thứ ba của Hội (nǎm 1955), Hội Hồng thập tự Việt Nam đổi tên thành Hội Chữ thập đỏ Việt Nam.\nNgày 23-11 được coi là ngày ra đời của Hội Chữ thập đỏ Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '23','11', 'Alêchxây Nikôlaêvich Tôixtôi - Nhà vǎn Nga, sinh ngày 10-1-1882\nTác phẩm nổi tiếng là các bộ tiểu thuyết lớn \"Pie đại đế\", \"Con đường đau khổ\" với các tập \"Hai chị em\", \"Nǎm 1918\", \"Buổi sáng ảm đạm\".\nCác tác phẩm của A.N Tôxtôi thấm đượn tinh thần yêu nước, tư tưởng lạc quan tươi sáng thể hiện truyền thống vǎn hoá Nga sâu sắc, với vǎn phong cổ điển mẫu mực. Ông là người có công lớn trong việc tạo dựng và phát triển thể loại tiểu thuyết sử thi Nga và kịch lịch sử Nga đến độ hoàn mỹ. Ông mất ngày 23-11-1945.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','12', 'Ngày 23-12-1972. Mỹ huy động 50 lần chiếc máy bay chiến thuật đánh ngoại thành Hà Nội, từ Mai Dịch đến trạm Trôi (Hoài Đức, tỉnh Hà Tây). Đêm đó, có 33 chiếc B52 đánh Đồng Mỏ và Bắc Giang, 41 chiếc máy bay chiến thuật đánh Yên Viên, Giáp Bát, Đa Phúc, Sơn Đồng và các sân bay Nội Bài, Yên Bái. Hướng biển có 7 máy bay Mỹ đánh Uông Bí, Phà Rừng, Đồ Sơn, Sở Dầu, và sân bay Kiến An, ta bắn rơi 4 máy bay Mỹ, trong đó Hải Phòng bắn rơi 2 chiếc B52.\nCùng ngày, Uỷ ban Thường vụ Quốc hội đã tuyên dương công trạng quân dân miền Bắc đánh rất giỏi, thắng rất to và đang làm thất bại cuộc tập kích chiến lược bằng không quân của đế quốc Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '23','12', 'Phối hợp chiến đấu với thủ đô Hà Nội trong trận \"Điện Biên Phủ trên không\", từ 23 đến 29-12-1972, dân quân tự vệ tỉnh Hoà Bình kiên trì bao vây, lùng sục suốt 7 ngày đêm trong rừng sâu, bắt sống được giặc lái, và bắn rơi một máy bay lên thẳng đến cứu tên giặc lái đó.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '23','12', 'Ngày 23-12-1964, Một trận gió xoáy ở Ceylon, Sri Lanka làm 2000 người thiệt mạng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '23','12', 'Ngày này năm 1972, ở Nicaragua xảy ra một loạt các cuộc động đất làm 7000 thiệt mạng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '23','12', 'Anđrây Nicôlaiêvich Tupôlép sinh nǎm 1888, mất ngày 23-12-1972.\nÔng là Tổng công trình sư chế tạo máy bay nổi tiếng của Liên Xô trước đây.\nÔng còn là chuyên gia khí động lực học, lập ra Viện thuỷ khí động lực học, đã thiết kế hơn 100 kiểu máy bay quân sự như ANT-20, phi cơ lớn nhất nǎm 1933, TU104, phản lực cơ đầu tiên chở khách.\nTupôlép là Viện sĩ hàn lâm khoa học, đã được nhiều giải thưởng quốc gia trong đó có giải thưởng Lênin.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '23','12', '23-12-1990, Slovenia bỏ phiếu để vùng Tây bắc Cộng hoà Nam Tư trở thành một quốc gia độc lập.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','01', 'Bùi Ngọc Dương sinh nǎm 1943, lớn lên và trưởng thành ở Hà Nội.\nNǎm 1966, anh tốt nghiệp kỹ sư xây dựng, sau đó vào quân đội, và cùng đồng đội vào miền Nam chiến đấu.\nTrong một trận đấu tại mặt trận Khe Sanh (tỉnh Quảng Trị), anh dẫn đầu trung đội công binh mở đường xông lên đánh thẳng vào sở chỉ huy Mỹ. Không cho đồng đội biết tay mình đã bị thương, Bùi Ngọc Dương vẫn chỉ huy trung đội chiến đấu. Lại bị thương vào chân, anh cố hết sức mình để không ngã, rồi tựa lưng vào thành công sự tiếp tục chỉ huy trận đánh, cho đến lúc kiệt sức, ngã xuống. Hôm ấy là ngày 24-1-1968.\nBùi Ngọc Dương là La Vǎn Cầu thời đánh Mỹ, anh đã được tuyên dương Anh hùng lực lượng vũ trang tháng 11-1969.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','01', 'Ngày 24-1-1977, Chính phủ nước Cộng hoà xã hội chủ nghĩa Việt Nam đã ra nghị định thành lập 10 khu rừng cấm. Bao gồm: Đền Hùng (Lâm Thao - Phú Thọ), Pác Bó (Hà Quảng - Cao Bằng), Tân Trào (Sơn Dương-Tuyên Quang), Đảo Ba Mùn (Cẩm Phả - Quảng Ninh), Ba Bể (Chợ Rã - Bắc Cạn), núi Ba Vì (Ba Vì - Hà Tây), núi Tam Đảo (Lập Thạch - Vĩnh Phúc, huyện Sơn Dương - Tuyên Quang, huyện Đại Từ - Thái Nguyên), bán đảo Sơn Trà (Đà Nẵng), khu rừng thông ba lá quanh thành phố Đà Lạt (Lâm Đồng).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '24','01', 'Heinrich Geissler sinh nǎm 1814, qua đời ngày 24-1-1879. Ông là nhà cơ khí và vật lý người Đức, đã chế tạo dụng cụ thuỷ tinh dùng trong phòng thí nghiệm, triển lãm máy bơm thuỷ ngân đầu tiên đạt độ chân không 1/1000 milimet (nǎm 1857), sáng chế các loại ống thuỷ tinh (mang tên ông ), chứa khí loãng dùng nghiên cứu tác dụng của ánh sáng do dòng điện phát ra.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','02', 'Ngày 24-2-1959, Chủ tịch Hồ Chí Minh đã về thǎm công trường xây dựng 3 nhà máy: Cao su Sao Vàng, Xà phòng Hà Nội và Thuốc lá Thǎng Long ở Hà Nội.\nPhát biểu ý kiến với công nhân, cán bộ, Bác Hồ nói: \"Giai cấp công nhân là giai cấp lãnh đạo cách mạng, muốn xứng đáng là giai cấp lãnh đạo thì phải gương mẫu trong công tác. Muốn tiến lên chủ nghĩa xã hội, muốn nước nhà mau chóng thống nhất, muốn được tự do sung sướng, mọi người phải khắc phục khó khǎn, thi đua tǎng gia sản xuất, thực hành tiết kiệm\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','02', 'Thiền sư Nguyễn Khánh Hỉ sinh nǎm 1067 tại kinh thành Thǎng Long và viên tịch ngày 24-2-1142.\nÔng ǎn chay niệm Phật từ thuở nhỏ, thọ giới ở nhiều chùa. Về sau, ông trụ trì chùa Từ Liêm, và trở thành người đứng đầu thế hệ 14 dòng Thiền Nam phương.\nDo uyên thâm kiến thức và có uy tín nên thiền sư Nguyễn Khánh Hỉ được vua Lý Thần Tông (128-1137) rất trọng đãi, phong ông làm Tǎng Lục rồi Tǎng Thống.\nTác phẩm chính của ông là \"Ngộ đạo ca thi tập\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','02', 'Nguyễn Nghiêm sinh nǎm 1903 tại tỉnh Quảng Ngãi. Ông gia nhập Việt Nam thanh niên Cách mạng đồng chí Hội. Đến nǎm 1930, ông vào Đảng Cộng sản Việt Nam và được đề cử làm Bí thư đầu tiên của Tỉnh uỷ Quảng Ngãi.\nHưởng ứng phong trào Xô Viết Nghệ Tĩnh, ông lãnh đạo cuộc khởi nghĩa ở làng Tân Hội (quê ông). Nhân dân kéo đến chiếm huyện đường Đức Phổ, bắt những kẻ có nợ máu với dân và xử tội. Giặc Pháp và bọn tay sai đàn áp cuộc khởi nghĩa và ra lệnh truy nã ông.\nNgày 24-2-1931, giặc bắt ông ở phủ Tư Nghĩa tỉnh Quảng Ngãi. Chúng dùng nhiều đòn tra tấn dã man nhưng không khuất phục được ông. Đến tháng 4-1931, chúng chém đầu ông ở bên sông Trà Khúc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '24','02', 'Rôbớt Phuntơn (Robert Fulton) người Mỹ, nhà phát minh ra tàu thuỷ chạy bằng hơi nước, sinh nǎm 1765. Sau nhiều nǎm nghiên cứu, thử nghiệm, ngày 11-8-1807 chiếc tàu mang tên Clermont chạy bằng hơi nước do ông chế tạo chạy ngược dòng sông Hudson tốc độ 5 dặm một giờ đã thành công. Sau đó ông còn đóng được 17 chiếc tàu chạy bằng hơi nước, một tàu phóng ngư lôi và một chiếc phà tự hành. Nǎm 1814 ông được Chính phủ Mỹ giao nhiệm vụ đóng một chiếc tàu chiến chạy bằng hơi nước và được hạ thuỷ vào năm 1815. Ông mất ngày 24-2-1815.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '24','02', 'Tuyên ngôn của Đảng Cộng sản là tác phẩm do C.Mác và Ph.Ăngghen viết từ tháng 12-1847 đến tháng 1-1848. Được xuất bản lần đầu tiên tháng 2-1848 ở Luân Đôn (Anh). Và ngày 24-2-1848, Tuyên ngôn của Đảng Cộng sản được chính thức công bố, sau đó được dịch ra nhiều thứ tiếng và xuất bản ở nhiều nước trên thế giới.\nTuyên ngôn của Đảng Cộng sản đánh dấu sự ra đời của chủ nghĩa cộng sản khoa học. Như V.I.Lênin đã chỉ rõ, nó đã trình bày một cách hoàn chỉnh và có hệ thống học thuyết về chủ nghĩa xã hội khoa học của C.Mác. Nó là cương lĩnh chiến đấu của giai cấp vô sản toàn thế giới.\nỞ Việt Nam, Tuyên ngôn của Đảng Cộng sản đã sớm được truyền bá rộng rãi, nhất là từ khi có Đảng của giai cấp công nhân và nhân dân lao động nước ta trong sự nghiệp đấu tranh giành độc lập dân tộc và đi lên chủ nghĩa xã hội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '24','02', 'Nicôlai Ivanơvich Lôbachepxki nhà toán học vĩ đại Nga, sinh ngày 1-12-1792. Cống hiến to lớn của ông là nǎm 1826 đã chứng minh Định đề Ơcơlít không đúng trong trường hợp mặt cầu. Ông đưa ra một tiên đề khác: \"Trong mặt phẳng qua 1 điểm ở ngoài đường thẳng cho trước có ít nhất hai đường thẳng không cắt đường thẳng đã cho\". Ông còn có các công trình toán học như: \"Các nguyên lý mới trong hình học với lý thuyết song song hoàn toàn\", \"Các nguyên lý hình học\", \"Hình học ảo\". Phát minh của ông tạo ra một bước chuyển biến rất lớn trong hình học và triết học. Hiện nay nó là cơ sở của nhiều lý thuyết mới của vật lý và thiên vǎn học.\nÔng qua đời ngày 24-2-1856')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '24','02', 'Côngxtantin Alếchxanđôvich Phêđin nhà vǎn Nga, viện sĩ Viện Hàn lâm khoa học Liên Xô, Anh hùng lao động, sinh ngày 24-2-1892.\nCác tác phẩm của ông đều thấm đượm tình cảm và tâm hồn Nga. Không ít tác phẩm là đề tài phản ánh thời kỳ đấu tranh Cách mạng và dựng lại nhiều chân dung nhà vǎn cổ điển. Các tác phẩm tiêu biểu có \"Thành phố và nǎm tháng\", \"Những niềm vui đầu tiên\", \"Nhà vǎn, nghệ thuật, thời đại\", \"Goocki với chúng tôi\"... Các tác phẩm của ông được chuyển thể lên sân khấu, dựng phim và dịch ra hơn 20 thứ tiếng trên thế giới. Ông qua đời nǎm 1977 tại Mátcơva.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','03', 'Báo La Lutte (Tranh đấu) ra số đầu tiên tại Sài Gòn ngày 24-3-1913 giữa không khí sôi động của cuộc tranh cử vào Hội đồng thành phố Sài Gòn.\nMặc đầu thành phần chủ chốt của tờ báo có một số phần tử Tơrốtxkit nhưng do nội dung chủ yếu tập trung đấu tranh chống các ứng cử viên thân chính quyền thực dân, nên về cǎn bản tờ báo chĩa mũi nhọn đả kích vào bọn thực dân và bọn tay sai, hướng về phía quyền lợi quần chúng lao động. Tờ báo đã dành được ảnh hưởng đối với đông đảo quần chúng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','03', 'Ngày 24-3-1926, nhà ái quốc Phan Chu Trinh qua đời và dấy lên phong trào để tang ông.\nPhan Chu Trinh hiệu là Tây Hồ, sinh ngày 9-9-1872 tại Tam Kỳ, tỉnh Quảng Nam. Ông từng được triều đình Huế bổ làm quan. Song ông có ý thức dân tộc nên đã gặp gỡ những người cùng chí hướng như: Huỳnh Thúc Kháng, Trần Quý Cáp, Lương Vǎn Can, Đề Thám, Phan Bội Châu. Ông bị chính quyền thực dân Pháp bắt nhiều lần. Nǎm 1911 ông sang Pháp và hoạt động trong phong trào yêu nước Việt kiều. Cùng Phan Vǎn Trường lập Hội người Việt Nam yêu nước tại Pháp. Ông có quan hệ gần gũi với Nguyễn Ái Quốc. Ngày 28-5-1925 ông về nước và ngày 24-3-1926 ông đột ngột qua đời tại Sài Gòn. Cái chết của ông gây xúc động lớn và ngày 4-4-1946, tại Sài Gòn có tới 14 vạn người tham dự đám tang của ông. Từ trong sự kiện này tinh thần yêu nước của quần chúng được thức tỉnh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','03', 'Nhà vǎn Hải Triều viết bài \"Nghệ thuật vị nghệ thuật hay nghệ thuật vị nhân sinh?\". Bài báo được đǎng trên tờ Đời mới ngày 24-3-1935, để trả lời bài viết của nhà vǎn Thiếu Sơn - \"Hai cái quan niệm vǎn học\" (đǎng trên Tiểu thuyết thứ bảy số 38 nǎm1935) đã mở màn cho một cuộc tranh luận sôi nổi về đường lối vǎn hoá giữa hai quan điểm: Nghệ thuật vị nghệ thuật và Nghệ thuật vị nhân sinh.\nCuộc tranh luận đã thu hút nhiều cây bút, nhiều tờ báo và trở thành một sự kiện có ý nghĩa đối với lịch sử Vǎn hoá và góp phần truyền bá những quan điểm cơ bản của chủ nghĩa Mác-Lênin vào đời sống vǎn hoá dân tộc; phê phán quan điểm phi vô sản, lạc hậu và lẩn trốn thực tiễn đấu tranh của dân tộc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','03', 'Ngày 24-3-1959, Hồ Chủ tịch cùng với Tổng thống Ấn Độ, ông Ragiǎngđơra Praxát đến thǎm nhà máy cơ khí Hà Nội, xã Định Công (huyện Thanh Trì), trường Đại học Tổng hợp Hà Nội, chùa Quán Sứ và chùa Một Cột.\nHiện nay, cạnh chùa Một Cột còn một cây bồ đề do Tổng thống Ấn Độ tặng Chủ tịch Hồ Chí Minh trong dịp bác sang thǎm nước này.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','03', 'Ngày 24-3-1963, mở đầu phong trào \"nghìn việc tốt\" thực hiện 5 điều Bác Hồ dạy trong thiếu nhi Việt Nam. Đây là một sáng kiến của Liên đội thiếu niên xã Tam Sơn, huyện Từ Sơn, tỉnh Hà Bắc. Sáng kiến này đã được thiếu niên, nhi đồng nhiều địa phương ở miền Bắc nhiệt liệt hưởng ứng, có tác dụng thúc đẩy các em rèn luyện mình trở thành con ngoan trò giỏi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','03', 'Ngày 24-3-1975, thị xã Tam Kỳ (tỉnh Quảng Nam cũ) được giải phóng.\nThị xã này vốn là một cǎn cứ quân sự lớn của Mỹ, có hệ thống bố phòng kiên cố - Trước sự tấn công của ta quân địch đã tǎng thêm lực lượng phòng thủ.\nTừ ngày 23-3, trung đoàn Ba Gia được tǎng cường 2 tiểu đoàn và 10 xe tǎng, xe bọc thép, đánh hướng chủ yếu, đột phá tuyến ngǎn chặn của địch từ Suối Đá đến bờ bắc sông Tam Kỳ.\nBan chỉ huy sư đoàn 2, liên đoàn 12 biệt động quân ngụy và ngụy quyền (tiểu khu Quảng Tín) hốt hoảng tháo chạy - Từ các bàn đạp đã chiếm được, quân ta tiến nhanh vào trung tâm thị xã Tam Kỳ.\nCùng ngày 24-3-1975, ta còn giải phóng thị xã Gia Nghĩa.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '24','03', 'Cho đến nửa cuối thế kỷ XIX, bệnh lao vẫn là cǎn bệnh huỷ hoại sức khoẻ con người, cướp đi rất nhiều sinh mạng. Ở châu Âu và châu Mỹ, cứ 7 người thì có 1 người chết do bệnh lao. Nhà bác học Rôbe Kốc (người Đức) đã công bố tìm ra cǎn nguyên gây bệnh lao. Để ghi nhớ thành quả này, gắn liền với công lao của ông, người ta gọi vi trùng gây bệnh lao là Kốc.\nNhững nǎm 50 của thế kỷ XX, thuốc chữa bệnh lao có hiệu quả đã lưu hành rộng rãi trên toàn cầu và đến nay, bệnh lao không phải là thứ bệnh nan y mà có thể chữa chạy đem lại cuộc sống lành mạnh cho người bệnh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '24','03', 'Pôpôv là người Nga, sinh nǎm 1859, qua đời nǎm 1906.\nNǎm 1895, ông đã phát minh ra anten. Là một phụ tá giáo sư trường dạy về ngư lôi, ông đã sử dụng các phương pháp của Branly và Lodge để phát hiện những cơn giông ở xa. Pôpôp nhận thấy rằng độ nhậy sẽ tǎng lên khi cǎng 1 sợi dây thẳng đứng để thu sóng do chớp gây ra. Do đó chiếc cột thu lôi đã là anten đầu tiên.\nNhờ Pôpôp mà có được sự liên lạc vô tuyến điện sử dụng morse (điện tín) đầu tiên qua một khoảng cách 250 mét vào ngày 24-3-1986')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '24','03', 'Nguyễn Bửu Lân tức Thành Thái, vua thứ 10 nhà Nguyễn sinh nǎm 1789, ở ngôi vua từ nǎm 1889 đến đầu nǎm 1907. Thành Thái là người yêu nước và có tinh thần dân tộc, chống lại thực dân Pháp. Vì thế, nǎm 1907, ông bị thực dân Pháp đày sang đảo Rêuyniông (châu Phi). Đến nǎm 1947, được đưa về nước nhưng buộc phải sống ở Sài Gòn. Ông mất ngày 24-3-1954.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '24','03', '24-3-1998, Liên minh châu Âu công bố 11 quốc gia thành viên đủ điều kiện tham gia vào khối đồng tiền chung châu Âu euro, đồng tiền sẽ được sử dụng thay thế cho đồng tiền riêng của các quốc gia.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','04', 'Trần Anh Tông tên thật là Trần Thuyên, là vua thứ tư của triều Trần và là một nhà vǎn.\nÔng có óc canh tân, chịu sửa lỗi lầm, mở mang việc học hành, sử dụng người tài nên kinh tế nước ta lúc đó được phát triển, vǎn học thịnh vượng.\nVua Trần Anh Tông thuận gả công chúa Huyền Trân cho Chế Môn là vua Chiêm Thành, để đổi lấy châu Ô và châu Rí. Tác phẩm chính của ông là Thuỷ Vân tuỳ bút.\nÔng từ trần ngày 24-4-1320 hưởng dương 44 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','04', 'Nguyễn Nghiêm xuất thân trong một gia đình nho học truyền thống. Trước nǎm 1930 hoạt động trong tổ chức Công Ái ở Quảng Ngãi, rồi ra nhập Việt Nam Thanh niên Cách mạng đồng chí Hội. Phong trào Xô Viết Nghệ Tĩnh bùng nổ, ông là người trực tiếp chỉ đạo cuộc khởi nghĩa phát đi từ làng Tân Hội (quê ông), ngày 24-2-1931 giặc bắt ông ở phủ Từ Nghĩa cùng tỉnh, chúng dùng nhiều đòn tra tấn dã man nhưng không khuất phục được ông, chúng đã tuyên án tử hình ông ngày 24-4-1931 lúc đó ông mới 28 tuổi. Nguyễn Nghiêm là một trong những người cộng sản kiên cường góp phần xương máu ngay tại quê hương mình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','04', 'Với mục tiêu đòi tự do báo chí và vận động thành lập tổ chức báo chí thống nhất trên toàn quốc, ngày 24-4-1937, trên 200 người làm báo đã tới dự Hội nghị báo giới Bắc kỳ họp tại Hội quán thể thao (nay là Câu lạc bộ quân đội). Tại Hội nghị, Hải Triều đã trình bày việc thành lập một mặt trận thống nhất báo giới. Hội nghị đã cử ra một ban biên tập gồm 7 người (trong đó có Trần Huy Liệu, Đào Duy Kỳ, Nguyễn Đức Kính) và Uỷ ban quản trị (trong đó có Võ Nguyên Giáp, Phan Tử Nghĩa, Nguyễn Mạnh Chất). Các cơ quan này có nhiệm vụ giao thiệp với báo chí Trung Kỳ, và sẽ giải tán khi thực hiện được việc thành lập một tổ chức chung của báo chí cả nước. Cũng từ đây phong trào bị thực dân Pháp chú ý, kèm theo hàng loạt những cuộc khủng bố nhằm vào các tờ báo cách mạng; Song mặt trận báo chí dân chủ vẫn trở nên sôi động làm thực dân Pháp lâm vào thế lúng túng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','04', 'Đồng chí Hà Huy Tập sinh ngày 24-4-1906 tại tỉnh Hà Tĩnh, hy sinh ngày 28-8-1941 tại Hóc Môn, tỉnh Gia Định cũ.\nNǎm 1927, đồng chí vào Sài Gòn, vừa dạy học vừa hoạt động cách mạng; cuối nǎm 1928 sang Trung Quốc.\nTừ nǎm 1929 được vào học ở trường đại học Phương Đông của Quốc tế Cộng sản ở Matxcơva. Sau khi tốt nghiệp đại học Phương Đông, tháng 4 nǎm 1034, đồng chí về tới Ma Cao, cùng các đồng chí khác chuẩn bị cho Đại hội đại biểu toàn quốc lần thứ nhất của Đảng. Tại Đại hội này vào cuối tháng ba nǎm 1935 ở Ma Cao, đồng chí Hà Huy Tập đã đọc báo cáo tổng kết phong trào cách mạng Đông Dương và 5 nǎm hoạt động của Đảng. Sau tháng 7 -1936, đồng chí cùng cơ quan Trung ương Đảng rời Trung Quốc về Sài Gòn, trực tiếp lãnh đạo phong trào cách mạng trong nước.\nTại Hội nghị Trung ương Đảng mở rộng họp từ cuối tháng 8 đến đầu tháng 9-1937 tại Bà Điểm, Gia Định, đồng chí Hà Huy Tập đọc báo cáo kiểm điểm sự lãnh đạo của Đảng từ sau Đại hội lần thứ nhất đến nǎm 1937, nêu bật thành công của Đảng là đã khôi phục được hệ thống tổ chức trong cả nước.\nGiữa tháng 3 năm 1938, đồng chí Hà Huy tập bị bắt - Ngày 25-3-1941, toà án của thực dân Pháp mở tại Sài Gòn đã tuyên án tử hình đồng chí Hà Huy Tập cùng với nhiều đồng chí lãnh đạo khác của Đảng vì tội chịu trách nhiệm về cuộc Khởi nghĩa Nam Kỳ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','04', 'Hội nghị cấp cao của nhân dân Đông Dương họp trong hai ngày 24, 25-4-1970 nhằm động viên nhân dân 3 nước Đông Dương tǎng cường đoàn kết nhất trí hết lòng ủng hộ lẫn nhau, kiên trì và đẩy mạnh chiến đấu, đánh bại đế quốc Mỹ và bè lũ, tiến lên giành thắng lợi hoàn toàn. Đoàn đại biểu nước Việt Nam Dân chủ cộng hoà đi dự hội nghị do Thủ tướng Phạm Vǎn Đồng dẫn đầu. Đoàn đại biểu nhân dân Việt Nam do luật sư Nguyễn Hữu Thọ, Chủ tịch Đoàn chủ tịch Uỷ ban Trung ương mặt trận dân tộc giải phóng miền Nam, Chủ tịch Hội đồng cố vấn Chính phủ cách mạng lâm thời miền Nam Việt Nam dẫn đầu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','04', 'Ngày 24-4-1972, Quân giải phóng Tây Nguyên tiến công như vũ bão vào toàn bộ quân địch ở tuyến phòng thủ Đắc Tô - Tân Cảnh, lập một chiến công vang dội: Chỉ trong 6 giờ đã tiêu diệt cǎn cứ lớn này - một cǎn cứ có lực lượng cơ động mạnh, có hệ thống hoả lực và pháo binh dày đặc, lại được sự yểm hộ rất cao của máy bay Mỹ, diệt gọn sư đoàn bộ binh ngụy số 22.\nNhư vậy tuyến phòng thủ kiên cố của địch ở Tây Nguyên đã bị chọc thủng, phía Bắc tỉnh Komtum được giải phóng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','04', '1976: Việt Nam và Costa Rica thiết lập mối quan hệ ngoại giao ở cấp đại sứ')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '24','04', 'Lênin từ Thụy Sĩ trở về Nga vào ngày 24-4-1917 tại hội nghị toàn Nga lần thứ bảy đã trình bày \"Luận cương tháng tư\". Đây là một bản cương lĩnh quan trọng vạch ra đường lối lãnh đạo cách mạng trong giai đoạn mới. Bản luận cương đã đưa ra khẩu hiệu \"Tất cả chính quyền về tay Xô Viết\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '24','04', 'Theo sáng kiến của Liên đoàn thanh niên dân chủ thế giới, ngày 24-4 hàng nǎm được coi là \"Ngày đoàn kết quốc tế của thanh niên\".\nLiên đoàn thanh niên dân chủ thế giới được thành lập từ tháng 10-1945 tại Đại hội thanh niên thế giới ở Luân Đôn (nước Anh). Tổ chức này đấu tranh chống chủ nghĩa đế quốc, vì hoà bình dân chủ, độc lập dân tộc vì lợi ích sống còn và quyền lợi của thế hệ trẻ. Hiện nay, Liên đoàn tập hợp được 250 tổ chức thanh niên của hơn 100 nước. Hội liên hiệp Thanh niên Việt Nam đã ra nhập Liên đoàn thanh niên dân chủ thế giới từ nǎm 1949.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','05', 'Lý Bôn còn gọi là Lý Bí quê ở Long Hưng, Thái Bình.\nÔng từng làm quan với nhà Lương khi nước ta bị chúng đô hộ. Nhà giàu có, tài gồm vǎn võ, ít lâu ông cáo quan lui về quê nhà, nuôi chí đánh đuổi giặc. Nhân Thứ sử Giao Châu là Tiêu Tư tàn ác, ông dấy quân đánh đuổi Tiêu Tư, thu phục Thǎng Long. Rồi tiến đánh Lâm Ấp, chiêu an dân chúng, tự xưng Nam Đế, đặt hiệu nước là Vạn Xuân, hiệu nǎm là Thiên Đức trong nǎm 554.\nNhà Lương lại sai tướng Dương Phiêu và Trần Bá Tiên đem quân xâm lược. Ông chống cự nhiều trận, bị thua, rút quân về đóng ở Hồ Điển Triệt (huyện Lập Thạch). Quân Lương tiến công, ông thất thế chạy vào động Khuất Liêu rồi bệnh mất ngày 24-5-548.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','05', 'Đồng chí Hoàng Vǎn Thụ sinh nǎm 1906, người dân tộc Tày, quê ở xã Nhân Lý, Vǎn Yên, Lạng Sơn.\nĐồng chí Hoàng Vǎn Thụ thoát ly gia đình rất sớm, sang Trung Quốc tìm đường cứu nước. Nǎm 1929, đồng chí được kết nạp vào Đông Dương Cộng sản đảng và được cử về nước gây cơ sở cách mạng ở Lạng Sơn và ở nước ngoài. Nǎm 1937, đồng chí được cử trực tiếp chỉ đạo phong trào cách mạng ở Cao Bằng, Bắc Cạn, Lạng Sơn,. Sau đó đồng chí vào Xứ uỷ Bắc Kỳ, chỉ đạo phong trào công nhân Hà Nội, Hải Phòng, Uông Bí, Hòn Gai... và được cử làm Bí thư Xứ ủy. \nĐồng chí Hoàng Vǎn Thụ được cử vào ban thường vụ Trung ương, sau đó được cử vào tổng bộ lâm thời mặt trận Việt Minh. Đồng chí có nhiều cống hiến quan trọng, góp phần giữ vững và phát triển lực lượng Cách mạng, trực tiếp chỉ đạo, củng cố phong trào Bắc Sơn, duy trì phát triển các đội du kích.\nTháng 8-1943, đồng chí bị thực dân Pháp bắt ở Hà Nội. Trong ngục, đồng chí luôn nêu cao khí tiết, lòng tin và tinh thần lạc quan. Đồng chí đã dành những ngày còn lại ngắn ngủi của mình để truyền lại kinh nghiệm đấu tranh cho các đồng chí khác. Ngày 24-5-1944, đồng chí bị đế quốc Pháp xử bắn. Trước khi chết, đồng chí hô lớn những khẩu hiệu tỏ lòng tin tưởng sắt đá vào thắng lợi cuối cùng của Cách mạng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','05', 'Ngày 24-5-1945 đã khai mạc Hội nghị dân quân du kích toàn quốc lần thứ nhất.\nMục đích của Hội nghị nhằm thống nhất việc tổ chức dân quân tự vệ và du kích trong cả nước. Trong thư gửi hội nghị, Hồ Chủ tịch đã viết: \"Dân quân tự vệ và du kích là lực lượng của toàn dân tộc, là một lực lượng vô địch, là một bức tường sắt của Tổ quốc. Vô luận kẻ địch hung bạo thế nào, hễ động vào lực lượng đó, bức tường đó, thì địch nào cũng phải tan rã\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','05', 'Ngày 24-5-1962 tại phiên toà xử án của Mỹ Diệm, giáo sư Lê Quang Vịnh, người thanh niên 27 tuổi đã nói thẳng vào bọn bán nước và cướp nước: \"Tôi tiếc là không giết được kẻ cầm đầu bọn xâm lược\", và hô lớn: \"Đả đảo Ngô Đình Diệm\" \"Đả đảo luật phát xít\". Cả bốn thanh niên bị kết án tại phiên toà đều tuyên bố không xin xỏ một điều gì đối với bọn cướp nước và bán nước.\nSau đó, Lê Quang Vịnh, Lê Hồng Tư và một số người nữa bị đày đi Côn Đảo. Suốt 13 nǎm sống trong nhà Tù Mỹ Ngụy nhưng các anh vẫn không ngừng đấu tranh chống lại bọn đao phủ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '24','05', 'Ngày 24-5-1540, là ngày sinh của Uyliơm Ghinbớt (William Ginber), người có những công trình nghiên cứu có tính chất mở đầu về hiện tượng từ và đã được đánh giá là nhà khoa học lỗi lạc của nước Anh. Ông là một trong những người đầu tiên đã chấp nhận phương pháp thực nghiệm theo nghĩa hiện đại.\nTác phẩm chính của ông là \"De Magnete\" trình bày nghiên cứu về các vật thể có từ tính và hút điện. Ông kết luận rằng kim nam châm sở dĩ nằm theo phương Nam - Bắc và chúc xuống phía dưới là vì trái đất đã tác dụng như một thanh nam châm. Những từ \"Điện\", \"Sự hút điện\", \"Lực điện\", \"Cực từ\" là do ông đưa ra. Ông tán thành quan điểm về sự quay của Trái Đất xung quanh mặt trời. Ông khẳng định các định tính không phải đều ở cách xa Trái Đất như nhau và cho rằng các hành tinh được giữ trên quỹ đạo của chúng ta nhờ một loại lực từ.\nÔng mất ngày 30-11-1603.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '24','05', 'Sôlôkhốp, nhà vǎn hiện đại Nga sinh ngày 24-5-1905. Nǎm 15 tuổi ông đã tham gia Cách mạng, đấu tranh cho chính quyền Xô Viết. Sau đó ông trở về quê sáng tác vǎn học. Những tác phẩm của ông phản ánh sâu sắc đời sống của nhân dân Nga, nhất là vùng sông Đông như: \"Những câu chuyện sông Đông\", \"Thảo nguyên xanh\". Bộ tiểu thuyết \"Sông Đông êm đềm\" là bức tranh vĩ đại về cuộc đấu tranh gay gắt giữa thế giới cũ và sự trưởng thành lớn mạnh của thế giới mới. Ông đã nhận giải thưởng Quốc gia với tác phẩm này. Nǎm 27 tuổi ông xuất bản bộ tiểu thuyết \"Đất vỡ hoang\" viết về công cuộc cải cách nông thôn.\nTrong cuộc chiến tranh thế giới lần thứ hai ông có các tác phẩm \"Khoa học cǎm thù\", \"Họ chiến đấu vì Tổ quốc\", \"Số phận con người\".\nSôlôkhốp là người lao động sáng tạo có nhiều cống hiến to lớn cho vǎn học Xô Viết. Nǎm 1965 ông nhận giải thưởng Nôbel về vǎn học. Nǎm 1967 ông được phong tặng danh hiệu anh hùng LĐXHCN. Ông còn là một nhà hoạt động xã hội chính trị nhiệt thành. Ông là Viện sĩ Viện Hàn Lâm khoa học, đại biểu Xô Viết tối cao, Uỷ viên Ban Chấp hành Trung ương Đảng. Ông mất vào ngày 21-2-1984.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','06', 'Theo sáng kiến của Phân hội thuỷ thủ người Việt Nam trong Công hội thuỷ thủ, ngày 24-6-1936, 60 thuỷ thủ Việt Nam làm trên các tàu buôn của Pháp đã họp hội nghị tại Mácxây đưa ra bản yêu sách với giới chủ. Nội dung của yêu sách là: Tuần lễ làm 40 giờ, tǎng lương, cải thiện bữa ǎn, thành lập các phân hội thuỷ thủ ở Hải Phòng, Sài Gòn. Ngoài ra bản yêu sách còn bổ sung một số khẩu hiệu chính trị như tự do hội họp và báo chí bằng tiếng Việt, đại xá tù chính trị, thủ tiêu cơ quan mật thám chuyên theo dõi người Việt.\nSự kiện này là kết quả và đỉnh cao của các cuộc đấu tranh của thuỷ thủ làm trên các tàu buôn của Pháp trên tuyến đường nối với Đông Dương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','06', 'Ngày 24-6-1962 khánh thành nhà máy Suýp-pe phốt phát Lâm Thao (tỉnh Phú Thọ), một xí nghiệp hoá chất lớn do Liên Xô giúp ta xây dựng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','06', 'Viện Bảo tàng Mỹ thuật (nhà số 66 phố Nguyễn Thái Học, Hà Nội) bắt đầu đón khách tham quan từ ngày 24-6-1966.\nViện đã sưu tầm, tập hợp, hệ thống hoá hiện vật, và góp phần đáng kể vào công tác giới thiệu lịch sử nghệ thuật tạo hình của nước ta.\nNgười xem có thể theo dõi quá trình phát sinh, phát triển của các bộ môn kiến trúc, điêu khắc, hội hoạ, mỹ thuật ở Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','06', 'Từ ngày 24-6 đến ngày 3-7-1976 Quốc hội nước Việt Nam thống nhất họp kỳ thứ nhất tại thủ đô Hà Nội.\nSau diễn vǎn khai mạc của đồng chí Nguyễn Hữu Thọ, đồng chí Trường Chinh đọc báo cáo về tình hình và kết quả cuộc tổng tuyển cử ngày 25-4-1976. Đồng chí Lê Duẩn - Bí thư thứ nhất Ban Chấp hành Trung ương Đảng Lao động Việt Nam đã đọc bản báo cáo chính trị về tình hình và nhiệm vụ của Cách mạng Việt Nam, về nhiệm vụ của Nhà nước Việt Nam thống nhất.\nQuốc hội đã thông qua một số nghị quyết quan trọng như: Lấy tên nước là Cộng hoà xã hội chủ nghĩa Việt Nam. Quốc kỳ nền đỏ, ở giữa có ngôi sao vàng 5 cánh. Quốc huy hình tròn, nền đỏ ở giữa có ngôi sao vàng 5 cánh, xung quanh có bông lúa, ở dưới có bánh xe rǎng cưa và dòng chữ Cộng hoà xã hội chủ nghĩa Việt Nam. Thủ đô là Hà Nội. Quốc ca là bài \"Tiến quân ca\". Thành lập Ủy ban dự thảo Hiến pháp nước Cộng hoà xã hội chủ nghĩa Việt Nam. Chính thức đặt tên thành phố Sài Gòn - Gia Định là thành phố Hồ Chí Minh. Và thành lập 6 ủy ban của Quốc hội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','06', 'Hoạ sĩ Bùi Xuân Phái qua đời ngày 24-6-1988. Ông sinh ngày 1-9-1921. Ông vẽ nhiều đề tài và là hoạ sĩ của Hà Nội 36 phố phường với những sinh hoạt bình dị của người thủ đô. Trong 40 nǎm lao động sáng tạo, Hoạ sĩ Bùi Xuân Phái để lại hàng ngàn tác phẩm và nhiều minh hoạ trên sách báo.\nTranh của ông được trưng bày trong Bảo tàng Mỹ thuật Việt Nam và bảo tàng của nhiều nước. Ông được đánh giá cao về nét độc đáo rất Hà Nội, và giới hội hoạ đã gọi ông một cách trìu mến \"Phố Thái\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','06', 'Từ ngày 24-6 đến ngày 27-6-1991, tại hội trường Ba Đình Hà Nội đã tiến hành Đại hội đại biểu toàn quốc lần thứ VII Đảng cộng sản Việt Nam. Dự đại hội có 1.176 đại biểu thay mặt cho 2.155.002 Đảng viên trong cả nước.\nĐại hội đã thông qua \"Cương lĩnh xây dựng đất nước trong thời kỳ quá độ lên chủ nghĩa xã hội\", nội dung cơ bản của chiến lược ổn định và phát triển kinh tế - xã hội của nước ta đến nǎm 2000; Báo cáo chính trị của Ban Chấp hành Trung ương (khoá 6) về đánh giá kết quả thực hiện Nghị quyết đại hội VI và phương hướng nhiệm vụ chủ yếu 5 nǎm 1991-1995; Báo cáo xây dựng Đảng và sửa đổi điều lệ Đảng, và thông qua điều lệ mới của Đảng.\nĐại hội đã bầu Ban Chấp hành Trung ương khoá VII gồm 146 đồng chí. Ban Chấp hành Trung ương đã bầu ra Bộ Chính trị gồm 13 đồng chí, Ban Bí thư gồm 13 đồng chí. Đồng chí Đỗ Mười được bầu làm Tổng bí thư Trung ương Đảng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','07', 'Ngày 24-7-1968, mười nữ thanh niên xung phong đã bị bom Mỹ vùi ở Ngã ba Đồng Lộc (Hà Tĩnh).\nTrong kháng chiến chống Mỹ, ngã ba này là một cửa ngõ quan trọng để vào hệ thống đường Hồ Chí Minh. Máy bay Mỹ đến đây đánh phá ác liệt, có ngày hơn 10 trận. Tổ thanh niên xung phong gồm mười cô gái từ 17 đến 22 tuổi do Võ Thị Tần làm tổ trưởng được giao nhiệm vụ lấp hố bom, sửa đường, cắm tiêu, gỡ bom, hướng dẫn xe đi qua Ngã ba Đồng Lộc.\nNgã ba Đồng Lộc trở thành một địa chỉ lịch sử, ghi đậm chiến công của lực lượng thanh niên xung phong về sự hy sinh tuổi thanh xuân của mình cho sự nghiệp giải phóng miền Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','07', 'Đất là tài nguyên quốc gia vô cùng quý giá, là tư liệu sản xuất đặc biệt, là thành phần quan trọng hàng đầu của môi trường sống, và là địa bàn phân bố các khu dân cư, xây dựng các cơ sở kinh tế, vǎn hoá, xã hội, an ninh và quốc phòng\".\nVới ý nghĩa đó ngày 24-7-1993, Chủ tịch nước đã công bố Luật Đất đai. Luật quy định chế độ quản lý, sử dụng đất đai, quyền và nghĩa vụ của người sử dụng đất. Luật gồm có 7 chương và 89 điều.\nCũng ngày này, Luật Thuế sử dụng đất nông nghiệp cũng được công bố. Luật gồm 5 chương, 16 điều. Mục đích của Luật là để khuyến khích sử dụng đất nông nghiệp có hiệu quả, thực hiện công bằng hợp lý sự đóng góp của tổ chức, cá nhân sử dụng đất nông nghiệp vào ngân sách Nhà nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '24','07', 'Sécnưsépxki là nhà vǎn, nhà phê bình, nhà dân chủ cách mạng Nga. Ông sinh ngày 24-7-1828. Ông là người thông minh ham học. Mới 16 tuổi đã thông thạo nhiều tiếng: Hy Lạp, La Tinh, Anh, Pháp, Đức, Do Thái, Ba Tư, Ba Lan ... và có một kiến thức vǎn hoá thế giới sâu sắc, phong phú.\nNǎm 25 tuổi Sécnưsépxki bảo vệ tiến sĩ khoa học, công khai hoạt động cách mạng, trở thành nhà báo, nhà vǎn, nhà dân chủ cách mạng lãnh đạo phong trào cách mạng Nga những nǎm 60. Ông là nhà triết học duy vật lỗi lạc. Ông chủ trương lật đổ chế độ Nga hoàng bằng bạo lực đưa nước Nga đi lên chủ nghĩa xã hội. Các tác phẩm kinh tế, chính trị, triết học của ông đều toát lên tinh thần đấu tranh kiên cường chống lại chế độ cũ. Công trình nghiên cứu \"Quan hệ thẩm mỹ của nghệ thuật với hiện thực\" đã đề ra nguyên tắc mỹ học cơ bản của sáng tác với hiện thực. Cuốn tiểu thuyết nổi tiếng \"Làm gì\" đã kêu gọi nhân dân Nga xây dựng một xã hội chủ nghĩa không tưởng. Tác phẩm đã ảnh hưởng lớn đến phong trào đấu tranh giai cấp ở Nga và thế giới.\nSécnưsépxki qua đời ngày 29-10-1889.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '24','07', 'Ngày 24-7-1997: Sau 290 nǎm hợp nhất, Chính phủ Anh trao cho Scotland quyền lập pháp, thu thuế, và có tiếng nói riêng tại Liên minh châu Âu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','08', 'Ngày 24-8-1958, khánh thành sân vận động Hàng Đẫy. Đây là một sân vận động hiện đại đầu tiên ở miền Bắc, xây dựng trên sân bãi Hàng Đẫy cũ của Hà Nội.\nTrên nền của sân vận động Hàng Đẫy, thành phố Hà Nội đã xây dựng lại và lấy tên là sân vận động Hà Nội. Đây là một sân vận động có tiêu chuẩn quốc tế và đã diễn ra nhiều hoạt động thể thao của Hà Nội và Quốc gia, quốc tế.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','08', 'Cục Xǎng dầu Tổng cục Hậu cần được thành lập ngày 24-8-1968.\nThành tích lớn nhất của ngành là thiết kế, xây dựng các đường ống dẫn dầu phục vụ cho các chiến trường ở tiền tuyến miền Nam.\nTháng 6-1968 đoạn đường ống đầu tiên dài 42 km qua Nam Đàn, Đồng Lộc, đã được lắp xong. Tháng 11 nǎm ấy, tuyến đường ống kéo dài 230 km xuống phía Nam, kịp thời vận hành xǎng cung cấp cho các đoàn xe \"xẻ dọc Trường Sơn đi cứu nước\".\nTháng 5-1972, bộ đội xǎng dầu đã phối hợp với các ngành, các địa phương nhanh chóng thi công hệ thống kho và 6 tuyến đường ống từ biên giới Lạng Sơn, Quảng Ninh về Hà Nội, hình thành một mạng lưới ống dẫn liên hoàn kịp tiếp nhận một khối lượng lớn xǎng dầu chi viện cho chiến trường. Trong cuộc tổng tiến công và nổi dậy đại thắng mùa xuân nǎm 1975, bộ đội xǎng dầu đã cung cấp 96 vạn tấn nhiên liệu cho các loại phương tiện cơ giới quân sự, góp phần vào thắng lợi vĩ đại của dân tộc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '24','08', 'Alếcxǎngđrơ Đuyma (Dumas Alesandre (père)), còn gọi là \"Đuyma bố\" sinh ngày 24-8-1802 và qua đời nǎm 1870, là nhà vǎn nổi tiếng của nước Pháp thế kỷ 19.\nTrở thành nhà vǎn chuyên viết tiểu thuyết lịch sử và phiêu lưu \"Đuyma bố\" có sở thích viết về những tình huống bất ngờ, vốn quen thuộc với sân khấu và kịch bản. Trong các sự kiện làm nền cho nhiều tiểu thuyết của mình, nhà vǎn chú ý đến cái quá khổ, ngoại lệ và khác thường.\nBạn đọc Việt Nam rất yêu mến các tác phẩm của ông như: \"Ba chàng ngự lâm pháo thủ\", \"Bá tước Môngtê Cristô\", \"Nữ hoàng Mácgô\"...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '24','08', 'Ngày 24-8-1991, Goócbachốp tuyên bố từ chức Tổng bí thư Đảng Cộng sản Liên Xô. Ông ta yêu cầu Ban Chấp hành Trung ương Đảng cộng sản Liên Xô tự giải tán; ký sắc lệnh đình chỉ các hoạt động chính trị trong quân đội và các cơ quan an ninh Nhà nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '24','09', 'Nhạc sĩ Italia, Vineenzô Bellini sinh ngày 3-1-1801. Ông là nhạc sĩ tiêu biểu cho xu hướng tìm kiếm những giai điệu êm dịu trong nền nhạc kịch Italia. Tính hùng tráng ở nhiều bài hát trong nhạc kịch của ông đã có tác động kích thích cao trào cách mạng của quần chúng Italia vào những năm 30 của thế kỷ.\nÔng mất ngày 24-9-1835 tại Pari.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','10', 'Ngày 24-10-1953, công nhân bến tàu Angiê và Ôrăng ở nước Angiêri đấu tranh kiên quyết không chịu khuân vác vũ khí xuống 2 chiếc tàu của Pháp để chở sang Việt Nam.\nTừ tháng 6-1953, công nhân bến tàu Ôrăng đã giữ trọn lời hứa: thà chịu đói chứ không chịu khuân vác vũ khí xuống tàu cho thực dân Pháp chở sang xâm lược Việt Nam.\nĐây là một hành động dũng cảm của công nhân Angiêri thể hiện sự đồng tình ủng hộ cuộc kháng chiến của nhân dân ta chống thực dân Pháp xâm lược.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','10', 'Quân đoàn 1 - Binh đoàn quyết thắng, được thành lập ngày 24-10-1973. Đây là binh đoàn chủ lực cơ động đầu tiên của quân đội nhân dân Việt Nam.\nBinh đoàn gồm những sư đoàn có bề dày lịch sử và lập nhiều chiến công vẻ vang hợp thành, như sư đoàn 308 (Sư đoàn quân tiên phong) là Sư đoàn chủ lực đầu tiên của quân đội ta được thành lập 8-1949, Sư đoàn 312 thành lập từ năm 1950 và Sư đoàn 320B.\nTrong cuộc tổng tiến công và nổi dậy mùa xuân 1975, Quân đoàn 1 vừa làm nhiệm vụ dự bị chiến lược bảo vệ miền Bắc, hậu phương lớn của cả nước, vừa thực hiện cuộc hành quân thần tốc, hoàn thành xuất sắc nhiệm vụ trong chiến dịch Hồ Chí Minh, góp phần cùng toàn quân, toàn dân giải phóng hoàn toàn miền Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '24','10', 'Hiến chương Liên hiệp quốc do Liên Xô, Mỹ, Anh và Trung Quốc soạn thảo đã được thông qua tháng 6-1945 tại hội nghị Xan Phranxixcô gồm đại diện 50 nước, và có hiệu lực từ ngày 24-10-1945. Ngày 24-10 hàng năm được gọi là Ngày Liên hiệp quốc.\nLiên hiệp quốc là tổ chức quốc tế của các nước độc lập và có chủ quyền, với mục đích giữ gìn, củng cố hoà bình, an ninh quốc tế, phát triển sự hợp tác giữa các quốc gia thành viên.\nCộng hoà xã hội chủ nghĩa Việt Nam là thành viên của Liên hiệp quốc từ tháng 9-1977.\nTrong những năm qua, các tổ chức của liên hiệp quốc như PAM, UNICEF, FAQ, UNDP, UNFPA, UNESCO, IMF, WB đã hợp tác, giúp đỡ Việt Nam phát triển kinh tế, văn hoá, khoa học, giáo dục, y tế, công tác dân số v.v....')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','11', 'Giáo sư Nguyễn Vǎn Hiếu sinh ngày 24-11-1922 tại tỉnh Cà Mau, từ trần nǎm 1991.\nLà người sớm giác ngộ cách mạng, ông đã tham gia đấu tranh giành chính quyền tháng 8 nǎm 1945. Từ nǎm 1954, ông hoạt động trong các đơn vị vũ trang chống lại chính quyền Ngô Đình Diệm. Từ nǎm 1960, Giáo sư Nguyễn Vǎn Hiếu là Tổng thư ký Uỷ ban mặt trận dân tộc giải phóng miền Nam. Sau ngày đất nước thống nhất (1975), giáo sư Nguyễn Vǎn Hiếu giữ chức Bộ trưởng Bộ Vǎn hoá thông tin, và Uỷ viên Đoàn chủ tịch Uỷ ban Trung ương mặt trận Tổ quốc Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','11', 'Ngày 24-11-1946, Đại hội Vǎn hoá toàn quốc lần thứ nhất được khai mạc trọng thể tại nhà hát thành phố Hà Nội, với sự tham gia của hơn 200 đại biểu. Trong diễn vǎn khai mạc, Chủ tịch Hồ Chí Minh nêu bật nhiệm vụ của nền vǎn hoá mới là phải lấy hạnh phúc của đồng bào, lấy sự nghiệp đấu tranh của dân tộc làm nội dung phản ánh. Người còn nói: \"Tôi mong muốn chúng ta đem vǎn hoá lãnh đạo quốc dân để thực hiện độc lập tự cường và tự chủ\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '24','11', 'Ông Branly người Pháp, tiến sĩ khoa học, bác sĩ y khoa, giáo sư vật lý ở Viện nghiên cứu Thiên chúa giáo Pari đã khám phá ra phương pháp dò sóng vô tuyến nǎm 1888.\nSau hai nǎm nghiên cứu và hoàn thiện, ngày 24-11-1890 ông Branly đã giới thiệu trước Viện hàn lâm khoa học Pari chiếc máy dò sóng vô tuyến của ông, gọi là ống mạt giũa. Chiếc máy này nhạy tới mức có thể ghi được sóng ở cách xa vài chục mét qua các bức tường. Môn điện báo vô tuyến điện ra đời từ đấy.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '24','12', 'Tiến sĩ Phạm Vǎn Nghị sinh ngày 24-12-1805, quê ở làng Tam Đǎng, huyện Ý Yên, tỉnh Nam Định, mất nǎm 1881. Ông đỗ hoàng giáp nǎm 1838, từng làm quan và dạy học trong nhiều nǎm.\nNǎm 1858 giặc Pháp đánh chiếm Đà Nẵng. Ông tổ chức đội Nghĩa Dũng vào Nam chiến đấu. Nhưng khi đội quân Nghĩa Dũng vào đến Huế thì vua Tự Đức ra lệnh phải rút về. Ông rút quân về Phong Doanh, lập cǎn cứ ở An Hoà và tổ chức chiến đấu ở Nam Định, Ninh Bình. Phong trào kháng Pháp của Phạm Vǎn Nghị tuy thất bại nhưng đã gây cho địch nhiều tổn thất và thổi bùng lên ngọn lửa đấu tranh giành độc lập.\nPhạm Vǎn Nghị còn sáng tác nhiều thơ mang nặng tình nhân ái và lòng tự cường dân tộc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','01', 'Ngày 25-1-1969, đã khai mạc phiên họp toàn thể lần thứ nhất của Hội nghị bốn bên về Việt Nam ở Pa-ri.\nTrong phiên họp trưởng đoàn đại biểu Mặt trận dân tộc giải phóng miền Nam Việt Nam Trần Bửu Kiếm nêu lên lập trường 5 điểm của Mặt trận. Bộ trưởng Xuân Thuỷ, Trưởng đoàn Việt Nam dân chủ cộng hoà đã nêu rõ tình hình Việt Nam, đường lối chống Mỹ cứu nước đúng đắn và những thắng lợi kỳ diệu của chính phủ Việt Nam dân chủ cộng hoà, Mặt trận dân tộc giải phóng miền Nam, của toàn thể nhân dân Việt Nam và nhấn mạnh ý chí sắt đá của nhân dân ta kiên quyết chiến đấu cho đến khi giành được độc lập và tự do nếu Mỹ vẫn ngoan cố theo đuổi chiến tranh xâm lược.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','01', 'Ngày 25-1-1984, Chủ tịch Hội đồng Bộ trưởng ra quyết định số 44/CT tặng danh hiệu Nghệ sĩ nhân dân và Nghệ sĩ ưu tú cho 189 nghệ sĩ các ngành nghệ thuật biểu diễn. Đây là đợt xét tặng danh hiệu Nghệ sĩ đầu tiên ở nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','01', 'Ngày 25-1-1991, đền Đô được công nhận là di tích lịch sử vǎn hoá.\nNgôi đền này xây dựng từ thế kỷ thứ XI trên đất làng Đình Bảng, huyện Tiên Sơn, tỉnh Bắc Ninh, là nơi phát tích của nhà Lý, nơi thờ 8 vị vua triều Lý (nên còn gọi là đền Lý Bát Đế).\nLễ hội đền Đô được tổ chức hàng nǎm từ ngày 15 đến 17 tháng 3 âm lịch để tưởng nhớ vua Lý Thái Tổ đǎng quang (ngày 15-3 nǎm Canh Tuất - 1010)')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '25','01', '25-1-1915, Nhà phát minh ra điện thoại, Alexander Graham Bell, đã khánh thành dịch vụ điện thoại xuyên lục địa đầu tiên của Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '25','01', 'Hội nghị Ngoại trưởng 4 nước Liên Xô, Anh, Pháp và Mỹ họp từ ngày 25-1 đến ngày 18-2-1954 tại Béclin (Thủ đô của Cộng hòa dân chủ Đức).\nĐoàn Liên Xô nêu ra 3 vấn đề để bàn bạc:\n1- Biện pháp làm dịu tình hình cǎng thẳng trong quan hệ quốc tế.\n2- Nước Đức và nhiệm vụ bảo đảm an ninh châu Âu\n3- Hiệp ước về thống nhất nước Áo.\nDo lập trường phản động của Mỹ và các nước phương Tây nên vấn đề thứ hai và thứ ba được nêu ra trong hội nghị không đi đến nghị quyết gì.\nCác đại biểu tham dự hội nghị Béclin đã thảo luận việc triệu tập hội nghị Giơnevơ bàn vấn đề Triều Tiên và việc lập lại hoà bình ở Đông Dương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','02', 'Từ ngày 25 đến 28-2-1943, Ban Thường vụ Trung ương Đảng Cộng sản Đông Dương đã triệu tập Hội nghị tại Võng La (Đông Anh, Phúc Yên) với sự tham dự của các đồng chí Trường Chinh, Hoàng Vǎn Thụ, Hoàng Quốc Việt...\nNghị quyết Hội nghị đề cập một cách toàn diện các công tác chuẩn bị như: Tǎng cường tổ chức Đảng, xây dựng cǎn cứ địa, phát triển lực lượng vũ trang... Đặc biệt Hội nghị đã thông qua \"Đề cương vǎn hoá Việt Nam\", một vǎn kiện quan trọng về đường lối vǎn hoá của Đảng do Tổng Bí thư Trường Chinh soạn thảo, vǎn kiện này đã góp phần mở ra một mặt trận trên lĩnh vực vǎn hoá, góp phần thu hút các lực lượng trí thức, các nhà vǎn hoá yêu nước trong sự nghiệp chống lại kẻ thù dân tộc và đặt nền móng xây dựng nền vǎn hoá mới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','02', 'Ngày 25-2-1944, Trung đội Cứu quốc quân thứ Ba được thành lập tại khu rừng Khuổi Kịch (Châu Sơn Dương, Tuyên Quang). Trung đội gồm 24 chiến sĩ, hoạt động trên địa bàn Đại Từ, Bắc Cạn, Tuyên Quang, Vĩnh Yên. Đồng chí Hoàng Quốc Việt thay mặt Trung ương Đảng làm lễ công nhận đơn vị.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','02', 'Ngày 25-2-1966, tại Hà Nội, những người làm công tác sử học đã tiến hành Đại hội thành lập Hội khoa học lịch sử Việt Nam.\nHội khoa học lịch sử Việt Nam là một hội nghề nghiệp có hệ thống từ Trung ương đến các địa phương. Từ ngày ra đời đến nay, Hội khoa học lịch sử Việt Nam đã đóng góp to lớn vào công tác nghiên cứu, sưu tầm, giảng dạy bộ môn lịch sử. Nhiều công trình khoa học được công bố có giá trị lịch sử và thực tiễn cao. Nhiều nhà sử học đã đóng góp vào việc lưu giữ và biên soạn các bộ sách sử có giá trị.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','02', 'Nhà soạn kịch Đào Tấn, sinh 25-2-1845 tại quê làng Vĩnh Thịnh, xã Phúc Lộc, tỉnh Bình Định.\nNǎm Đinh Mão 1867, ông đỗ cử nhân, làm Tổng đốc Nghệ An, sau vinh thǎng Hiệp tá Đại học sĩ. Vì chống tên Việt gian đại gian ác Nguyễn Thân, nên bị cách chức lui về quê nhà ở ẩn khoảng nǎm 1902.\nÔng nổi tiếng thanh liêm, công bình, được hầu hết sĩ phu trọng vọng, lại giỏi vǎn chương, thích soạn tuồng hát. Chính ông sáng lập ra bộ môn hát bội ở Bình Định. Tại quê nhà ông có lập một trường dạy kịch nghệ gọi là Học bộ đình.\nCác tác phẩm của ông gồm tập: Mộng mai thi tồn, Mộng mai từ lục, Mộng mai ngâm thảo, Mộng mai vǎn sao đều viết bằng chữ Hán, còn hầu hết là các vở tuồng xuất sắc: Quần trân hiếu thoại, Tứ quốc lai vương... Đào Tấn là nhà soạn tuồng cao nhất về chất lượng cũng như về số lượng của bộ môn nghệ thuất sân khấu Việt Nam từ trước tới nay.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','02', 'Nhà vǎn Phạm Duy Tốn sinh nǎm 1881, quê ở tỉnh Hà Tây, qua đời ngày 25-2-1924.\nÔng tốt nghiệp trường thông ngôn, làm việc ở toà thống sứ Bắc Kỳ, rồi làm ở Ngân hàng Đông Dương. Ông còn viết báo ở Hà Nội và Sài Gòn.\nHai truyện ngắn của ông Sống chết mặc bay (nǎm 1918) và Con người Sở Khanh diễn tả cảnh đen tối, thối nát của xã hội thuộc địa nửa phong kiến được coi là tác phẩm mở đường cho thể loại truyện ngắn ở miền Bắc nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','03', 'Ngày 25-3-1928, Toàn quyền Đông Dương ra nghị định thành lập Hội đồng nghiên cứu khoa học Đông Dương nhằm mục đích: Thu nhập, khảo cứu và thông báo cho các cơ quan hữu quan để áp dụng vào thực tiễn những thành tựu nghiên cứu khoa học cơ bản hoặc khoa học ứng dụng. Đồng thời, thay mặt cho giới khoa học Đông Dương tham dự các hội nghị khoa học của nước Pháp hoặc quốc tế. Hội đồng gồm những nhà chuyên môn người Pháp hoặc người bản xứ, với số lượng không quá 40 thành viên. Và đặt dưới sự chỉ đạo của Viện Hàn lâm khoa học các thuộc địa của Pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','03', 'Ngày 25-3-1930, hơn 4000 công nhân nhà máy sợi Nam Định đã tiến hành bãi công. Cuộc bãi công do Tỉnh ủy Nam Định trực tiếp chỉ đạo và kéo dài đến 16-4-1930. Do được tổ chức chặt chẽ, được sự ủng hộ của nhân dân trong tỉnh và của công nhân ở Hà Nội, Hải Phòng... đã buộc bọn chủ phải nhượng bộ, đáp ứng những yêu sách của công nhân. Cuộc bãi công này có ảnh hưởng mạnh mẽ đến phong trào công nhân cả nước và được coi là tiêu biểu nhất kể từ sau khi Đảng Cộng sản Đông Dương được thành lập.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','03', 'Công binh là một trong những binh chủng được thành lập sớm nhất của quân đội ta: Ngày 25-3-1946.\nTrong kháng chiến chống Pháp binh chủng công binh đã hoàn thành xuất sắc nhiệm vụ phục vụ chiến đấu, lập nhiều chiến công to lớn, được Chủ tịch Hồ Chí Minh thưởng cờ \"Mở đường thắng lợi\". Trong kháng chiến chống Mỹ, bộ đội công minh đã hiệp đồng chặt chẽ, anh dũng chiến đấu và phục vụ chiến đấu. Với quyết tâm \"Xẻ dọc Trường sơn đi cứu nước\" bộ đội công binh mở đường và giữ vững con đường chiến lược nối liền Bắc, Nam, con đường Hồ Chí Minh, lập nên một kỳ công trong lịch sử. Ở hậu phương bộ đội công binh đã giữ vững mạch máu giao thông, xây dựng các công trình quốc phòng và nhiều công trình quan trọng khác trong đó có Lǎng Chủ tịch Hồ Chí Minh. Binh chủng đã phá núi mở đường, xây dựng phòng tuyến chiến đấu ở biên giới Tây Nam và biên giới phía Bắc, góp phần làm tròn nghĩa vụ quốc tế với Campuchia.\nBinh chủng công binh đã được Quốc hội và Chính phủ tuyên dương là đơn vị Anh hùng các lực lượng vũ trang nhân dân Việt Nam và tặng thưởng huân chương Hồ Chí Minh cao quý.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','03', 'Ngày 25-3-1950, mở đầu chiến dịch mùa xuân ở Nam Bộ nhằm tiêu diệt sinh lực địch và thu hẹp phạm vi chiếm đóng của địch. Trong toàn chiến dịch, ở Trà Vinh, ta diệt 650 tên, bắt sống 125 tên, gọi hàng 184 tên, san bằng 30 lô cốt; ở Sóc Trǎng, diệt và bức rút 38 vị trí; ở sài Gòn - Chợ Lớn diệt 494 tên địch.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','03', 'Ngày 25-3-1966 Chủ tịch Hồ Chí Minh đã tới thǎm và nói chuyện với Đại hội thi đua \"Đảm bảo giao thông vận tải, quyết tâm đánh thắng giặc Mỹ xâm lược\". Người cǎn dặn \"Giao thông vận tải rất quan trọng, quan trọng đối với chiến đấu, đối với sản xuất, đối với đời sống nhân dân, nó rất quan trọng nên ta phải cố gắng...\". \nThực hiện lời dạy của Chủ tịch Hồ Chí Minh kính yêu, toàn ngành giao thông vận tải đã vươn lên mạnh mẽ, khắc phục mọi khó khǎn gian khổ hy sinh để làm tròn nhiệm vụ trên mặt trận khôi phục và đảm bảo giao thông vận tải trong suốt thời kỳ chiến tranh phá hoại của giặc Mỹ. Đối mặt với bom đạn, mìn, thuỷ lôi của địch, các chiến sĩ lái tàu, xe, đường sắt, đường bộ, đường sông, đường biển, các thợ cầu, đường đã giữ vững mạch máu giao thông, thông suốt trong mọi tình huống góp phần vào sự nghiệp xây dựng đất nước.\nNgày Chủ tịch Hồ Chí Minh đến thǎm và nói chuyện với ngành giao thông vận tải đã được lấy làm ngày truyền thống của ngành.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','03', 'Ngày này nǎm 1975, tỉnh Quảng Ngãi được giải phóng.\nTừ sáng ngày 24-3, trung đoàn 576 pháo binh của ta nã vào các mục tiêu địch. Hai tiểu đoàn (403 và 406) của đặc công quân khu 5, tiểu đoàn 7 bộ đội địa phương tỉnh được sự chi viện của xe tǎng, xe bọc thép, đồng loạt đánh các mục tiêu ở vùng ven và tiến vào thị xã.\nỞ phía Bắc, trung đoàn 94 bộ đội tỉnh cùng lực lượng vũ trang huyện tiến công các vị trí phòng thủ của liên đoàn 11 biệt động quân nguỵ, cắt đường số 1 (đoạn từ Sơn Tịnh đi Bình Sơn), chặn đường rút chạy của địch về Chu Lai.\nHơn 4000 tên địch còn lại bỏ thị xã Quảng Ngãi tháo chạy. Đoàn xe địch gồm hơn 200 chiếc chạy theo đường số 1 về hướng Chu Lai. Trung đoàn 94 và lực lượng vũ trang địa phương đã tổ chức trận địa mai phục, diệt gọn cánh quân này, bắt 3500 tên, diệt 550 tên, thu và phá huỷ 206 xe quân sự.\nTừ nhiều hướng, bộ đội ta tiến vào chiếm các mục tiêu trong thị xã Quảng Ngãi.\nChiều và đêm ngày 24, rạng sáng ngày 25-3 quân và dân các huyện trong tỉnh Quảng Ngãi đồng loạt tiến công, kết hợp nổi dậy đánh chiếm quận lỵ, giải phóng các xã.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '25','03', '25-3-1957, sáu quốc gia châu Âu ký kết Hiệp ước Rome thành lập Cộng đồng kinh tế châu Âu EEC. Các nước ký kết bao gồm Bỉ, Pháp, CHLB Đức, Italia, Hà Lan và Luxembourg.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','04', 'Hoàng Diệu sinh nǎm 1828, đỗ cử nhân phó bảng. Ông làm tri huyện, tri phủ, án sát, bố chính một số tỉnh. Ông nổi tiếng công minh và thanh liêm. Suốt 30 nǎm làm quan nhiều nơi, cảnh nhà vẫn thanh bạch.\nNǎm 1880, ông làm Tổng đốc Hà Ninh, lãnh chức Thượng thư Bộ Binh. Khi đại tá Pháp Hǎngri Rivie (Henri Rivière) đem quân ra Bắc, lấy cớ bảo vệ sinh mạng và tài sản Pháp kiều; ông bất bình, chuẩn bị đề phòng và sẵn sàng đối phó mọi bất trắc, đột biến do Pháp âm mưu gây ra.\nNgày 25-4-1882 Rivière đưa tối hậu thư với 3 điều: Phá các phòng thủ trong thành; Giải giới binh lính; Các quân trong thành phải trình diện. Sau đó quân Pháp sẽ vào thành kiểm kê. Hoàng Diệu tiếp tối hậu thư, phẫn uất sai người đi điều đình. Nhưng không đợi trả lời, quân Pháp tấn công vào thành. Trước hoả lực của địch, ông quyết liệt đối phó. Ông chỉ huy quân sự ở Cửa Bắc. Một số quân sĩ bỏ chạy. Một mình Hoàng Diệu vào hành cung, thảo tờ di biểu, rồi ra trước võ miếu dùng khǎn bịt đầu thắt cổ tự tử.\nCái chết bất tử của ông đã để lại trong lòng người Hà Nội và cả nước một nỗi niềm thương tiếc vô hạn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','04', 'Ngày 25-4-1920, một nhóm trí thức trẻ nước ta đưa lên sân khấu nhà hát Hà Nội vở kịch nói Người bệnh tưởng của Môlie, một kịch gia nổi tiếng của nước Pháp, thế kỷ XVII - Đây là 1 sự kiện mở đường cho ngành kịch nói nước ta. Được kích thích bằng vở hài kịch đó, nhà giáo Vũ Đình Long soạn vở kịch Chén thuốc độc và công diễn ngày 20-10-1921 cũng tại nhà hát Hà Nội, được người xem nhiệt liệt tán thưởng. Đó là vở kịch nói đầu tiên và buổi diễn kịch nói đầu tiên của sân khấu Hà Nội và Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','04', 'Ngày 25-4-1955, vùng mỏ Quảng Ninh được hoàn toàn giải phóng khỏi ách đô hộ của thực dân Pháp. Ngày nay tỉnh Quảng Ninh có diện tích 5.938 km2, số dân gần 1 triệu người, với thành phố Hạ Long là trung tâm của tỉnh, hai thị xã Cẩm Phả, Uông Bí và 10 huyện. Quảng Ninh nổi tiếng bởi các loại than và Vịnh Hạ Long, nơi mà Unesco đã công nhận là di sản thiên nhiên của')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '25','04', 'Ngày 25-4-1965, địch giết hại anh Lê Độ ở Đà Nẵng.\nCũng như anh Nguyễn Vǎn Trỗi, Lê Độ trước khi chết tỏ ra hiên ngang, bất khuất. Đi giữa hai hàng lưỡi lê của địch, anh hô vang các khẩu hiệu: \"Đả đảo đế quốc Mỹ và bọn tay sai!\", \"Việt Nam muôn nǎm!\", \"Hồ Chủ tịch muôn nǎm!\". Lê Độ tức Lê Vǎn Dậu sinh nǎm 1941, quê ở thôn Mỹ Thị, quận 3 thành phố Đà Nẵng. Anh nhập ngũ ngày 15-1-1963, lúc hy sinh là chiến sĩ biệt động thành phố Đà Nẵng.\nNgày 20-5-1965, Uỷ ban Trung ương Mặt trận dân tộc giải phóng miền Nam truy tặng huân chương Thành Đồng hạng nhì cho liệt sĩ Lê Độ.\nNgày 23-7-1997, anh được Nhà nước truy tặng danh hiệu Anh hùng lực lượng vũ trang nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '25','04', 'Bác sĩ Lê Đình Thám sinh nǎm 1897, quê ở huyện Điện Bàn, tỉnh Quảng Nam, qua đời ngày 25-4-1969 tại thủ đô Hà Nội.\nSau khi học hết bậc thành chung ở Huế, ông ra Hà Nội học tiếp và tốt nghiệp đại học y khoa, rồi lại trở về làm việc ở bệnh viện thành phố Huế. Ông là một trong những người thành lập Hội Phật giáo ở Huế.\nNǎm 1948, ông được cử giữ chức Chủ tịch Uỷ ban kháng chiến hành chính Nam Trung Bộ. Nǎm 1949, được điều động lên chiến khu Việt Bắc, lần lượt giữ các chức vụ: Chủ tịch Uỷ ban bảo vệ hoà bình thế giới của Việt Nam, ủy viên Đoàn Chủ tịch Uỷ ban Trung ương Mặt trận Tổ quốc Việt Nam, Phó Hội trưởng Hội Phật giáo Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '25','04', 'Ngày 25-4-1976 nhân dân ta ở 38 tỉnh thành phố từ cao nguyên Đồng Vǎn đến mũi Cà Mau hân hoan đi bầu quốc hội chung của cả nước. Đây thực sự là ngày hội lớn của dân tộc ta, ngày hội thống nhất non sông.\nCả nước có 98,77% cử tri đi bỏ phiếu, 492 ứng cử viên trúng cử trong đó có 80 đại biểu là cán bộ chính trị, 98 đại biểu là trí thức và nhân sĩ dân chủ, 13 đại biểu tôn giáo...\nThành công của cuộc tổng tuyển cử bầu Quốc hội chung của cả nước khẳng định chân lý mà Hồ Chủ Minh đã khẳng định \"Nước Việt nam là một - dân tộc Việt Nam là một, sông có thể cạn, núi có thể mòn, song chân lý đó không bao giờ thay đổi\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '25','04', 'Ngày 25-4-1978, Hội đồng Chính phủ ra Quyết định về việc thống nhất tiền tệ trong cả nước, phát hành tiền ngân hàng mới, thu đổi tiền ngân hàng cũ ở miền Bắc và miền Nam.\nThi hành Quyết định trên, ngày 3-5-1978, 38 tỉnh thành phố trong cả nước đã đổi tiền theo đúng kế hoạch đã định và cả nước ta chỉ lưu hành một loại tiền thống nhất của nước Cộng hoà xã hội chủ nghĩa Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '25','04', 'Ngày 25-4-1991, Thủ tướng Chính phủ đã ký quyết định thành lập Viện Bỏng quốc gia mang tên danh y Lê Hữu Trác.\nTrụ sở chính thức của Viện hiện nay ở xã Tân triều, huyện Thanh Trì, Hà Nội.\nTừ khi thành lập đến nay, Viện đã cấp cứu, chữa bỏng cho gần 5 nghìn người.\nGiám đốc đầu tiên của Viện Bỏng quốc gia là Thiếu tướng Giáo sư Lê Thế Trung. Anh hùng lực lượng vũ trang có hai bằng tiến sĩ khoa học về y học hiện đại và y học cổ truyền.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('4', '25','04', 'Piốt Ilich Traicôpxki đã sinh ra ở Vikinxcơ, miền Uran trong một gia đình kỹ sư mỏ, ngày 25-4-1840. Ông được chǎm sóc và phát triển nǎng khiếu âm nhạc từ nhỏ. Nǎm 19 tuổi, tốt nghiệp trường luật, làm việc ở bộ luật pháp nhưng ông vẫn dành nhiều thời gian cho âm nhạc, chơi đàn pianô. Nǎm 22 tuổi, Traicôpxki học ở nhạc viện Pêtecbua. Sau 3 nǎm học tập, tốt nghiệp với huy chương vàng, sau đó là giáo sư Nhạc viện Matxcơva - Ông đã đi biểu diễn ở nhiều nước.\nTraicôpxki không những là nhạc sĩ Nga vĩ đại mà còn là nhạc sĩ lớn của thế giới. Sáng tác của ông chiếm một vị trí đặc biệt trong nền âm nhạc Nga và châu Âu cuối thế kỷ XIX.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('4', '25','04', 'Ngày 25-4-1945, Hội nghị quốc tế thành lập Liên hiệp quốc được khai mạc tại thành phố Sanyraneicô (Mỹ) có 50 nước được mời tham dự, đó là những nước đã tuyên chiến với chủ nghĩa phát xít. Vấn đề chủ yếu của chương trình nghị sự là soạn thảo chi tiết và thông qua hiến chương Liên hiệp quốc, sau hai tháng làm việc, Hiến chương được thông qua.\nMục đích của việc thiết lập Liên hiệp quốc đã khẳng định ngay trong lời nói đầu của Hiến chương:\n- Phòng ngừa cho các thế hệ tương lai khỏi thảm hoạ của chiến tranh.\n- Khẳng định lại lòng tin vào những quyền cơ bản của con người.\n- Thiết lập điều kiện cho phép duy trì công bằng và sự tôn trọng đối với các nghĩa vụ phải thực hiện theo các thoả ước và các luật quốc tế.\n- Thúc đẩy sự tiến bộ xã hội')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('4', '25','04', 'Đúng vào lúc tình hình thế giới cǎng thẳng do đế quốc Mỹ và đồng minh phát động cuộc chiến tranh lạnh chống Liên Xô và các nước xã hội chủ nghĩa khác, một số nhân vật nổi tiếng đã họp Đại hội thế giới các nhà trí thức và đưa ra bản kêu gọi nhân dân các nước đấu tranh cho hoà bình.\nBản kêu gọi đó dẫn đến Đại hội lần thứ nhất các chiến sĩ hoà bình họp vào ngày 25-4-1949 đồng thời tại Pari (Thủ đô nước Pháp) và Praha (Thủ đô nước cộng hoà xã hội chủ nghĩa Tiệp Khắc cũ) gồm đại biểu của 72 nước. Phong trào hoà bình thế giới ra đời tại đại hội này, và nhà bác học vật lý nguyên tử nổi tiếng người Pháp Giôliô Quyri được bầu làm Chủ tịch Đại hội.\nÔng có câu nói bất hủ: \"Hoà bình là sự nghiệp của nhân dân các nước. Không riêng một quốc gia nào hay một cá nhân nào mà tất cả phải đoàn kết lại mới có thể bảo vệ được hoà bình, ngǎn chặn chiến tranh\".\nMùa thu nǎm 1949, những Uỷ ban hoà bình đã được thành lập tại 70 nước. Trong khi đó, khắp nǎm châu đã liên tiếp có những đại hội, hội nghị, mít tinh, biểu tình đấu tranh cho hoà bình. Hình ảnh con chim bồ câu trắng của hoạ sĩ Picátxô đã trở thành biểu tượng của phong trào hoà bình thế giới.\nUỷ ban bảo vệ hoà bình thế giới và phong trào hoà bình của nhân dân Việt Nam là một bộ phận và là một thành viên chính thức của phong trào, Uỷ ban của nước ta đã có tiếng nói và hoạt động thiết thực đóng góp tích cực vào phong trào chung.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('4', '25','04', 'Ngày 25-4-1980, Alêgiô Cácpǎngtiê qua đời. Ông là nhà vǎn lớn của Cuba và Châu Mỹ Latinh, sinh nǎm 1904 tại La Habana trong một gia đình trí thức. \nÔng viết nhiều tiểu thuyết, nổi tiếng hơn cả là cuốn Thế kỷ ánh sáng (nǎm 1962). Cuốn tiểu thuyết lịch sử này khẳng định con đường phát triển của Châu Mỹ Latinh chỉ có thể là con đường đấu tranh của bản thân quần chúng ở châu lục này, chứ không thể là sự du nhập Cách mạng tư sản từ phương Tây.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','05', 'Công chúa Lê Ngọc Hân sinh ngày 25-5-1770 là con gái có tài sắc hơn cả trong số các con gái của vua Lê Hiển Tông. \nNhằm thắt chặt quan hệ giữa nhà Lê với quân Tây Sơn, nhà vua đã gả Ngọc Hân cho Nguyễn Huệ vào nǎm 1786. Lễ thành hôn đã được tổ chức trọng thể ở kinh đô Thǎng Long. Sau đó, Ngọc Hân đã về Phú Xuân sống với vua Quang Trung, gắn bó đời mình với sự nghiệp của người anh hùng áo vải cờ đào, bằng một sự đồng cảm đặc biệt.\nSau khi vua quang Trung bǎng hà, nǎm 1792, hoàng hậu Ngọc Hân đã làm một bài thơ dài \"Ai tư vãn\", ca ngợi công đức của Nguyễn Huệ - Bà qua đời ngày 4-12-1799.\nỞ quận Hai Bà Trưng, Hà Nội có một phố và một trường học mang tên Lê Ngọc Hân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','05', 'Bùi Huy Bích là danh sĩ đời Lê Mạt người làng Định Công, sang cư ngụ ở làng Thịnh Liệt, Thanh trì, Hà Nội.\nNǎm 1762 Đời Lê Hiển Tông ông đỗ Hương Cống. Nǎm 1769, 25 tuổi ông đỗ tiến sĩ được bổ làm Hiệu lý Viện Hàn lâm, rồi thǎng làm thị chế, kế được thụ chức Thiên sai tri hộ phiên, kiêm chức Đông các hiệu thư.\nNǎm 1777 ông làm Đốc đồng Nghệ An. Về sau kiêm chức Tả thị lanh bộ lại, Hành tham tụng. Ông làm quan chính trực, không cầu cạnh, từ lời nói đến việc làm là khuôn mẫu cho người đời. Bùi Huy Bích để lại các tác phẩm như \"Bích câu tiền hậu\", \"Nghệ An tập thơ\", \"Thái Liên\", \"Tôn Am vấn cáo\", \"Hoàng việt thi tuyển\", \"Quốc triều chính đại lục\", \"Lịch triều thi sao\"...\nSách lịch Đại Danh hiền Phổ còn chép rằng: \"Ông bình vǎn thì sang sảng, phong độ uy nghi, lúc nào cũng ung dung nhàn nhã. Mỗi khi ông dự tế trong Quốc Tử Giám thì người ta đua nhau đến xem\". Vua Gia Long có vời ông ra làm quan, nhưng ông từ chối.\nBùi Huy Bích mất ngày 25-5-1818.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','05', 'Theo đề nghị của đồng chí Trường Chinh, Xứ ủy Bắc Kỳ Đảng Cộng sản Đông Dương vận động thành lập một tổ chức công khai chống nạn mù chữ. Tháng 5-1938 các đồng chí Trần Huy Liệu, Đặng Thai Mai, Võ Nguyên Giáp cùng với một số trí thức nhân trí tiến bộ đứng ra lập \"Hội truyền bá quốc ngữ\" do Nguyễn Vǎn Tố, một trí thức có tên tuổi và tiến bộ làm Hội trưởng. Ngày 25-5-1938, Hội truyền bá Quốc ngữ chính thức ra mắt tại trụ sở Hội thể dục thể thao Hà Nội.\nTrong thời gian ngắn, nhờ sự chỉ đạo của tổ chức Đảng, tinh thần ham học của quần chúng và nỗ lực của các hội viên gồm những trí thức, học sinh, sinh viên yêu nước, phong trào truyền bá quốc ngữ mau chóng chở thành một phong trào quần chúng có tổ chức và thu được những kết quả tốt đẹp. Phong trào tiếp tục phát triển và sau Cách mạng tháng Tám 1945, trở thành lực lượng nòng cốt cho phong trào diệt dốt, Bình Dân học vụ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','05', 'Ngày 25-5-1954, Đảng và Chính phủ ta quyết định tiến hành đợt 1 cải cách ruộng đất ở 47 xã thuộc các huyện Đại Từ, Đồng Hỷ, Phú Bình (tỉnh Thái Nguyên) và 6 xã thuộc huyện Nông Cống (tỉnh Thanh Hoá). Đợt 1 này kết thúc vào ngày 20-9-1954.\nTrước khi tiến hành đợt 1, ta đã thí nghiệm cải cách ruộng đất ở 6 xã thuộc huyện Đại Từ (tỉnh Thái Nguyên) trong 2 tháng để rút kinh nghiệm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','05', 'Ca Lê Hiến, nhà thơ, bút danh Lê Anh Xuân sinh ngày 5-6-1940 quê ở Vàm Nước Trong, xã Tân Bình, Mỏ Cày, Bến Tre.\nNǎm 1952 ông thoát ly gia đình tham gia xếp chữ ở nhà in Trịnh Đình của Sở Giáo dục Nam Bộ. Nǎm 1954 ông tập kết ra Bắc và tốt nghiệp khoa sử trường Đại học Tổng hợp, làm giảng viên. Ít lâu sau được cử đi học nước ngoài nhưng ông xin được về quê hương chiến đấu. Cuối nǎm 1964 về Nam trong đoàn cán bộ giáo dục công tác ở Tiểu ban Tuyên huấn Trung ương Cục rồi chuyển về công tác ở Hội Vǎn nghệ giải phóng.\nCa Lê Hiến đã sáng tác các tập thơ \"Tiếng gà gáy\", \"Hoa dừa\", \"Trường ca Nguyễn Vǎn trỗi\", trong đó có bài thơ nổi tiếng \"Dáng đứng Việt Nam\".\nÔng hy sinh vào ngày 25-5-1968 ở vùng phụ cận Sài Gòn. Lúc đó ông 28 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '25','05', 'Tổ chức thống nhất châu Phi (gọi tắt là OAU) được thành lập vào ngày này 25-5-1963 tại Ali Abêba (nước Êtiôpia) có hơn 50 nước thành viên.\nHoạt động của OAU nhằm củng cố sự đoàn kết và phối hợp hành động của các nước châu Phi, đấu tranh chống chủ nghĩa thực dân cũ và mới, giúp đỡ nhau phát triển kinh tế. Cơ quan tối cao của OAU là Hội nghị những người đứng đầu nhà nước và Chính phủ. Trụ sở của OAU đặt tại Ali Abêba.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','06', 'Ngày 25-6-1952, Hội nghị cung cấp toàn quân lần thứ nhất được triệu tập. Đến thǎm và nói chuyện với hội nghị, Chủ tịch Hồ Chí Minh đã cǎn dặn:\n\"Một bát gạo, một đồng tiền, một viên đạn, một viên thuốc, một tấc vải phải đi thẳng đến chiến sĩ\". Người cǎn dặn các cán bộ cung cấp phải yên tâm công tác. \"Trong bộ máy Cách mạng, từ người quét nhà, nấu ǎn cho đến Chủ tịch nước đều là làm đầy tớ cho dân... không có việc sang, việc hèn. Công việc gì làm tròn, bổ ích cho kháng chiến, cho nhân dân đều là công việc sang, công việc gì bên ngoài có vẻ loè loẹt mà không làm tròn đều là công việc xấu\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','06', 'Từ đầu nǎm 1962, cuộc đấu tranh vũ trang ở miền Nam Việt Nam phát triển mạnh. Các đơn vị đặc công, biệt động đã hoạt động tốt, đánh nhiều trận có hiệu suất cao vào kho tàng, cơ quan đầu não, trên các trục đường giao thông huyết mạch của địch. Lớn nhất là trận đánh vào kho xǎng Tân Sơn Nhất ngày 25-6-1962, đốt cháy 6 triệu lít, gây chấn động mạnh ở thành phố Sài Gòn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','06', 'Ngày 25-6-1965, lần đầu tiên đế quốc Mỹ cho nhiều máy bay phản lực mon men xâm phạm vùng trời Hà Nội. Chưa kịp gây tội ác, chúng đã bị quân và dân Hà Nội đánh rất mãnh liệt. Bộ đội Phòng không bảo vệ Thủ đô lập chiến công đầu tiên của Hà Nội; hạ một máy bay phản lực Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','06', 'Từ ngày 25-6 đến 4-7-1981 Quốc hội khoá VII họp kỳ thứ nhất.\nQuốc hội đã tập trung thảo luận và thông qua 4 đạo luật lớn về tổ chức các cơ quan cao nhất của Nhà nước ta. Tiếp đó Quốc hội đã bầu các cơ qaun lãnh đạo cao nhất nước ta bao gồm: Đồng chí Trường Chinh là Chủ tịch Hội đồng nhà nước, Chủ tịch Quốc hội là luật sư Nguyễn Hữu Thọ, Chủ tịch Hội đồng bộ trưởng là đồng chí Phạm Vǎn Đồng.\nQuốc hội cũng đã bầu các thành viên Hội đồng Chính phủ, Hội đồng Quốc phòng, Hội đồng dân tộc và Ủy ban Thường trực của Quốc hội. Quốc hội còn ra tuyên bố hưởng ứng lời của Xô Viết tối cao Liên Xô ngày 23 tháng Tư gửi Quốc hội và nhân dân các nước trên thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '25','06', 'Ngân hàng thế giới (viết tắt là WB) ra đời từ tháng 7 nǎm 1944 nhưng chính thức hoạt động từ ngày 25-6-1946. \nNgân hàng thế giới được coi là chiếc cầu nối giữa thị trường tài chính quốc tế với các nước nghèo và đang phát triển. Nó cung cấp cho các nước này các khoản tín dụng theo chương trình với các điều kiện ưu đãi. Việt Nam là hội viên chính thức của Ngân hàng thế giới từ ngày 21-9-1976.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','07', 'Ngày 25-7-1938 xuất bản tác phẩm \"Vấn đề dân cày\" của các đồng chí Trường Chinh và Võ Nguyên Giáp. Tác phẩm ký bằng bút danh Qua Ninh và Vân Đình phát hành tại Hà Nội.\nVận dụng lý luận của nghĩa Mác - Lênin, đường lối của Đảng và thực tiễn khảo sát đời sống nông thôn, các tác giả đặt vấn đề nghiên cứu thực trạng nông thôn Việt Nam dưới ách áp bức thực dân và phong kiến, đề cập đến vần đề ruộng đất và dân cày. Cuốn sách phê phán nhận thức quan điểm sai lầm đối với dân cày và vạch rõ vị trí của người dân cày trong cách mạng Việt Nam. Cuốn sách lên tiếng tố cáo chính sách phản động của đế quốc phong kiến về ruộng đất, tô thuế, nạn vay lãi nặng... và nêu lên yêu sách của dân cày Đông Dương trước Mặt trận nhân dân Pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','07', 'Nhà vǎn Nguyễn Huy Tưởng qua đời vào ngày 25-7-1960. Ông sinh nǎm 1912 ở xã Dục Tú, huyện Đông Anh, Hà Nội. Học tập ở Hải Phòng, làm thư ký nhà Đoan, rồi về Hà Nội Viết vǎn để ký thác tấm lòng với đất nước. Ông có sở trường về đề tài lịch sử, đã viết các tiểu thuyết: \"Đêm hội Long Trì\", \"An Tư công chúa\", kịch \"Vũ Như Tô\", \"Cột đồng mã viện\" - Sau Cách mạng tháng Tám và trong kháng chiến chống Pháp ông viết kịch \"Bắc Sơn\", \"Những người ở lại\", \"Kỹ sư Cao Lạng\" - Hoà bình lập lại, ông viết \"Truyện Anh Lực\", \"Bốn nǎm sau\", \"Luỹ Hoa\" và \"Sống mãi với Thủ đô\"\nNguyễn Huy Tưởng là nhà vǎn người Hà Nội và có nhiều tác phẩm về Hà Nội có giá trị.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '25','07', 'Người đầu tiên thực hiện chuyến bay vượt biển Mǎngsơ là Lui Blêriô, một người Pháp, vào ngày 25-7-1909.\nĐể thực hiện chuyến bay dài 38 kilômét nối liền bờ biển nước Pháp với bờ biển nước Anh, Blêriô đã mất tới 37 phút. Ông đã sử dụng một chiếc máy bay dài 8 mét, nặng 300 kilôgam, có một chiếc cánh quạt bằng gỗ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '25','07', 'Vào ngày 25-7-1947, Festival đầu tiên của thanh niên và sinh viên thế giới khai mạc ở Praha, thủ đô của Tiệp Khắc cũ. Đây là ngày hội của tuổi trẻ các nước đấu tranh cho hoà bình và tiến bộ xã hội.\nNhững nǎm đó, Festival của thanh niên và sinh viên thế giới tiếp tục được tổ chức ở Buđapét (nǎm 1949), Béc-Lin (nǎm 1951), Bucaret (nǎm 1954), Vácsava (nǎm 1955), Mátxcơva (nǎm 1957), Viên (nǎm 1959), Henxinki (nǎm 1962), Xôphia (nǎm 1968), nǎm 1997 tại La Habana Cuba.\nTrong các liên hoan đó, nước ta đều cử đoàn đại biểu thanh niên sinh viên ưu tú của mình đi dự.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '25','07', 'Em bé ống nghiệm đầu tiên đã ra đời ở bệnh viện Oldham (nước Anh) vào ngày 25-7-1978. Em tên là Lui Braonơ. Những \"người cha khoa học\" của Lui là bác sĩ Xtitơ và Êđuốt. Họ cho thụ thai trong ống nghiệm một trích trứng của người vợ với tinh trùng của người chồng. Sau đó họ cấy lại phôi thu được vào trong dạ con của người vợ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','08', 'Ngày 21-8-1945, Tổng bí thư Đảng cộng sản Đông Dương - Trường Chinh - và nhiều Uỷ viên Trung ương Đảng đã về tới Hà Nội. Ngày 25-8, Chủ tịch Hồ Chí Minh từ Tân Trào về tới làng Gạ (Từ Liêm, ngoại thành Hà Nội). Tại đây, ngày 26-8, Người đã nghe các đồng chí Võ Nguyên Giáp, Trần Đǎng Ninh báo cáo tình hình khởi nghĩa ở Hà Nội. Chiều 26-8, Người vào nội thành ở và làm việc tại số nhà 48 Hàng Ngang. Tại đây, Người đã chủ toạ phiên họp đầu tiên của Thường vụ Trung ương Đảng bàn việc bắt tay vào những nhiệm vụ cấp bách của Nhà nước cách mạng. Và cũng tại địa điểm lịch sử này, từ ngày 28-8 đến 31-8, Người soạn thảo vǎn kiện lịch sử :\"Tuyên ngôn độc lập\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','08', 'Với sự giúp đỡ của Tổng bộ Việt Minh và các đoàn thể Cứu quốc, Bộ Giáo dục đã triệu tập Đại hội giáo giới toàn quốc trong ba ngày (từ 25 đến 27-8-1946) tại Nhà hát lớn Hà Nội. Đây là cuộc họp mặt đầu tiên gồm đủ các đại biểu giáo giới thuộc các cấp học khắp cả ba miền Bắc, Trung, Nam. Các giáo chức đã trao đổi những vấn đề thuộc về quan điểm, lý luận giáo dục mới của nước Việt Nam là: dân tộc, khoa học và đại chúng.\nĐại hội bầu ra Ban Chấp hành Liên đoàn Giáo giới Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','08', 'Ngày 25-8-1966, quân giải phóng Thủ Dầu Một chặn đánh nhiều tiểu đoàn thuộc sư đoàn bộ binh số 1 của Mỹ ở Bông Trang - Lò Gạch (quận Bến Cát). Sau 12 giờ liên tục chiến đấu, quân giải phóng đã đẩy lùi 34 đợt tiến công của địch, diệt một tiểu đoàn bộ binh Mỹ, tiêu hao nặng 2 tiểu đoàn khác, giết và làm bị thương hơn 700 tên, phá huỷ 14 xe tǎng, xe bọc thép và bắn rơi 4 máy bay.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','09', 'Nhà nghiên cứu văn học, Giáo sư Đặng Thai Mai bút hiệu Thanh Tuyền, sinh ngày 15-12-1902 quê ở Lương Điền, Thanh Chương, Nghệ An.\nÔng xuất thân trong một gia đình khoa bảng, có truyền thống yêu nước. Năm 1928 tốt nghiệp Cao đẳng Sư phạm, ông được bổ dạy ở trường Quốc học Huế. Vì tham gia \"Đảng Tân Việt\" ông bị bắt hai lần. Năm 1936 ông là Hội viên Hội Truyền bá chữ quốc ngữ và ứng cử vào Viện Dân biểu Trung Kỳ.\nÔng là người đầu tiên viết về lý luận văn học theo quan điểm mác xít và nổi tiếng ngay với tác phẩm \"Văn học khái luận\". Ông còn viết về thân thế sự nghiệp các nhà văn hào lớn Trung Quốc qua các tác phẩm của Lỗ Tấn, Tào Ngu mà ông dịch ra tiếng Việt. Ngoài ra ông còn có các tác phẩm \"Tạp văn trong văn học Trung quốc hiện đại\", \"Lôi Vũ\", \"Chủ nghĩa nhân văn dưới thời kỳ văn hoá phục hưng\", \"Thơ văn Phan Bội Châu\", \"Thơ văn cách mạng Việt Nam đầu thế kỷ XX\".\nÔng còn giữ nhiều chức vụ như: Bộ trưởng Bộ Giáo dục, Chủ tịch Uỷ ban kháng chiến tỉnh Thanh Hoá, Chủ tịch Hội liên hiệp Văn học nghệ thuật, Viện trưởng Viện Văn học Việt Nam, Đại biểu quốc hội. Ông mất ngày 25-9-1984 tại Hà Nội. Ông được tặng huân chương Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '25','09', 'Tômơt Han Moocgan (Thomas Hunt Morgan) sinh ngày 25-9-1866 tại Mỹ. Năm 20 tuổi ông tốt nghiệp đại học. Năm 24 tuổi đã nhận học vị tiến sĩ khoa học.\nLúc đầu Moocgan nghiên cứu về phôi sinh học thực nghiệm, sau đó sang vấn đề di truyền. Năm 1910 ông công bố công trình \"Nhân tố di truyền\". Năm 1915 ông xuất bản cuốn \"Cơ chế của di truyền học Mendenl\" để chứng minh sự đúng đắn của học thuyết. Năm 1926 ông có tác phẩm \"Học thuyết về gen\" để trình bày rõ và sâu về cơ sở vật chất nhiễm sắc thể và gen của tính di truyền.\nÔng là viện sĩ của các viện hàn lâm Nga, Mỹ. Ông được giải thưởng Noben về sinh lý học năm 1933. Ông mất năm 1945, thọ 79 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '25','09', 'Lỗ Tấn là nhà văn Cách mạng nổi tiếng của Trung Quốc. Ông sinh ngày 25-9-1881 tại tỉnh Triết Giang.\nNăm 18 tuổi, ông đến Nam Kinh học hàng hải và mỏ. Tiếp thu tư tưởng mới và trong ông hình thành quan điểm \"Sinh mệnh lớp sau bao giờ cũng có ý nghĩa hơn, hoàn thiện hơn lớp trước\". Sau ông học y ở Nhật Bản nhưng lại chuyển sang làm văn nghệ. Cách mạng Tháng Mười đã rung động sâu sắc tâm hồn ông. Ông viết các tác phẩm \"Nhật ký người điên\", \"Gào thét\", \"Bàng hoàng\" và hàng loạt bài lên án chủ nghĩa đế quốc. Là giáo sư trường đại học, ông lãnh đạo sinh viên lập các nhóm văn học; ông trở thành người thầy của nền văn học vô sản Trung Hoa.\nÔng mất ngày 9-10-1936. Khi tiễn đưa ông, dân chúng và giới văn nghệ sĩ đã phủ lên quan tài ông một lá cờ thêu ba chữ \"Hồn Dân Tộc\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '25','09', 'Êrich Maria Rơmác là nhà văn Đức, sinh ngày 22-6-1898. Các tác phẩm của ông thể hiện sâu sắc tinh thần đấu tranh không mệt mỏi cho hoà bình và hạnh phúc của con người. Bằng cách kể chuyện điêu luyện và hấp dẫn, với óc quan sát tinh tế và sắc sảo, tiểu thuyết của ông đã gây hứng thú cho đông đảo độc giả nhất là khi ông bầy tỏ nỗi xúc động đau đớn về thân phận dân tộc cũng như nhân loại. Chính vì vậy ông được coi là nhà văn hiện đại tiêu biểu của Đức. Các tác phẩm chính của ông là: \"Phía Tây không có gì lạ\", \"Khải hoàn môn\", \"Tia lửa sống\", \"Thời gian để sống và thời gian để chết\", \"Đường về\"... ông mất ngày 25-9-1970 tại Thuỵ Sĩ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','11', 'Lương Vǎn Can sinh ngày 25-11-1854 quê ở làng Nhị Khê, huyện Thường Tín tỉnh Hà Tây, đỗ cử nhân, nên người đương thời gọi cụ là cụ cử Can.\nLương Vǎn Can và Nguyễn Quyền cùng với Nguyễn Quyền cùng với các nhà nho yêu nước đã thành lập trường Đông Kinh Nghĩa Thục ở Hà Nội tháng 3-1907. Đây là một trường học kiểu mới, truyền bá tư tưởng yêu nước, mở mang dân trí, phát triển dân sinh, nhằm giải phóng dân tộc. Trường này bị đóng cửa sau 9 tháng hoạt động. Nǎm 1913, thực dân Pháp tìm cớ kết tội Lương Vǎn Can, với mức án 10 nǎm tù, đưa đi an trí ở Phnôm Pênh (Campuchia).\nNǎm 1921, ông được tha trước hạn, trở về Hà Nội, và qua đời tại nhà số 4 Hàng Đào nǎm 1927, thọ 73 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','11', 'Bùi Viện quê ở xã An Ninh, huyện Tiền Hải, tỉnh Thái Bình, sinh nǎm 1839 và mất ngày 25-11-1878.\nSo với các nho sĩ yêu nước đương thời, ông là người nhìn xa trông rộng, luôn coi trọng việc làm thực tế.\nVề ngoại giao, ông Bùi Viện là người Việt Nam đầu tiên đến Hoa Kỳ và gặp tổng thống Hoa Kỳ để đặt quan hệ giữa hai nước.\nÔng còn là người tổ chức và chỉ huy đội tuần dương quân từ dân chúng, bảo vệ vùng biển và nhân dân vùng ven biển phía bắc.\nBùi Viện xứng đáng được tôn vinh là nhà canh tân đất nước ta ở thế kỷ XIX.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','11', 'Ngày 25-11-1945, Trung ương Đảng ra chỉ thị: \"Kháng chiến và kiến quốc\".\nVǎn kiện quan trọng này đã xác định cách mạng nước ta vẫn là Cách mạng giải phóng dân tộc, kẻ thù chính của nhân dân ta lúc này là thực dân Pháp xâm lược, phải tập trung ngọn lửa đấu tranh vào chúng.\nBốn nhiệm vụ của nhân dân ta lúc này là: Củng cố chính quyền, chống thực dân Pháp xâm lược, bài trừ nội phản, cải thiện đời sống của nhân dân. Nhiệm vụ bao trùm là củng cố chính quyền. Để củng cố chính quyền cách mạng lúc này, phải thực hiện đồng thời hai nhiệm vụ chiến lược là:\n- Quyết tâm kháng chiến chống thực dân Pháp xâm lược\n- Ra sức xây dựng chế độ mới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','11', 'Học giả Trần Vǎn Giáp sinh nǎm 1898 tại Hà Nội, quê ở huyện Thanh Miện, tỉnh Hải Dương, qua đời ngày 25-11-1973.\nNǎm 1916, ông làm việc ở Trường Viễn đông Bác Cổ. Ít lâu sau ông sang Pháp học và tốt nghiệp trường Cao đẳng thực hành Xoócbon, Viện cao học Hán học và Trường vǎn hoá Hán học. Về nước ông lại tiếp tục làm việc ở trường Viễn đông Bác Cổ.\nSau Cách mạng Tháng Tám (1945) và trong thời kỳ kháng chiến chống Pháp, ông công tác ở Bộ Giáo dục.\nNǎm 1954, ông làm việc ở Ban Nghiên cứu Vǎn - Sử - Địa, Viện Sử học, viết nhiều công trình lịch sử, vǎn học có giá trị.\nCác tác phẩm chính của học giả Trần Vǎn Giáp có:\n- Phật giáo Việt Nam (viết bằng tiếng Pháp)\n- Lược sử khoa cử Việt Nam\n- Lược truyện các tác giả Việt Nam\n- Tìm hiểu kho sách Hán Nôm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '25','11', 'Do đấu tranh để giải phóng Cuba, nǎm 1955 Phiđen Caxtơrô bị trục xuất sang Mêhicô. Tại đây, ông tập hợp những thanh niên là người Cuba yêu nước, quyên góp tiền mua sắm vũ khí, luyện tập quân sự.\nNgày 25-11-1956, Phiđen cùng 81 chiến sĩ đã từ Mêhicô đáp tàu \"Granma\" vượt biển trở về Tổ quốc. Sau 7 ngày lênh đênh trên mặt biển, các chiến sĩ đã đặt chân lên Tổ quốc Cuba, anh đã vượt qua vòng vây của địch, rút về vùng núi Xiêra Maêxtơra hiểm trở về xây dựng cǎn cứ địa cách mạng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('1', '25','12', 'Lễ Giáng sinh')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','12', 'Nhà thờ lớn Hà Nội được xây dựng trên khu đất cao, vốn là chân tháp Báo Thiên của Thǎng Long xưa có từ giữa thế kỷ XI.\nVào dịp lễ giáng sinh 25-12-1887, các tín đồ Đạo thiên chúa đã tổ chức khánh thành nhà thờ lớn Hà Nội.\nNhà thờ này thiết kế theo kiểu kiến trúc Gôtích rất thịnh hành trong thế kỷ XII và thời Phục hưng ở châu Âu. Hai gác chuông hai bên cao 30 mét với những trụ đá to nặng ở 4 góc. Trên đỉnh nhà thờ là cây thánh giá bằng đá.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','12', 'Từ ngày 25 đến ngày 30-12-1920, đồng chí Nguyễn Ái Quốc tham dự Đại hội Đảng xã hội Pháp. Tại Đại hội, đồng chí đã bỏ phiếu tán thành việc Đảng xã hội Pháp gia nhập Quốc tế 3. Đảng xã hội Pháp được xây dựng thành một Đảng mác xít. Đồng chí đã trở thành người cộng sản Việt Nam đầu tiên và tham gia sáng lập Đảng cộng sản Pháp. Sự kiện này đánh giá một bước ngoặt lớn trong hoạt động chính trị của Người, đi từ chủ nghĩa yêu nước truyền thống đến chủ nghĩa Mác-Lênin theo con đường cách mạng vô sản.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','12', 'Từ ngày 25-12-1950 đến 17-1-1951, Bộ tổng tư lệnh QĐNDVN sử dụng đại đoàn 308, đại đoàn 312, nǎm trung đoàn cùng lực lượng vũ trang địa phương mở chiến dịch Trung du (còn gọi là chiến dịch Trần Hưng Đạo) nhằm tiêu diệt lực lượng địch, phát triển chiến tranh du kích, giữ vững thế chủ động chiến lược trên chiến trường Bắc Bộ.\nSau gần một tháng chiến đấu, ta đã tiêu diệt và làm bị thương 5.000 tên địch, diệt 30 vị trí, 40 tháp canh, thu nhiều vũ khí, giải phóng một số vùng ở Vĩnh Yên, Phúc Yên, mở rộng vùng tự do từ Hải Ninh đến sát Tiên Yên, Móng Cái.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','12', 'Ngày 25-12-1951, Chủ tịch Hồ Chí Minh đã ký sắc lệnh thành lập Sở Vận tải trực thuộc Bộ Giao thông công chính. Ngày này được coi là ngày thành lập ngành vận tải ô tô Việt Nam.\nTrong kháng chiến chống Pháp, ngành vận tải ô tô đã tích cực phục vụ chiến dịch Điện Biên Phủ nǎm 1954. Sau đó ngành đã góp phần đắc lực vận chuyển hàng hoá, hành khách, kịp thời chi viện cho miền Nam đánh giặc Mỹ, đồng thời hoàn thành nhiệm vụ quốc tế với các nước bạn Lào và Campuchia.\nNgành vận tải ô tô nước ta đã được Nhà nước trao tặng Huân chương độc lập hạng ba, tuyên dương hai tập thể Anh hùng và 11 Anh hùng lao động.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '25','12', 'Hoàng Quốc Việt tên thật là Hạ Bá Cang, sinh nǎm 1905, quê ở tỉnh Bắc Ninh, mất ngày 25-12-1992 tại Hà Nội.\nÔng gia nhập Việt Nam Thanh niên cách mạng đồng chí Hội từ nǎm 1928. Nǎm 1930, ông là Ủy viên Ban Chấp hành Trung ương lâm thời Đảng Cộng sản Việt Nam. Nǎm 1941 là Uỷ viên Ban Thường vụ Trung ương Đảng. Nǎm 1951 là Uỷ viên Bộ Chính trị, Chủ tịch Tổng liên đoàn Lao động Việt Nam. Nǎm 1960 là Uỷ viên Ban Chấp hành Trung ương Đảng, Viện trưởng Viện Kiểm sát nhân dân tối cao. Nǎm 1961 là Chủ tịch Tổng liên đoàn Việt Nam. Nǎm 1977 là Chủ tịch Đoàn chủ tịch Uỷ ban Trung ương Mặt trận Tổ quốc Việt Nam.\nÔng Hoàng Quốc Việt là một nhà cách mạng được nhân dân yêu mến và kính trọng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '25','12', 'Hrixtô Bôterơ (Hristo Botere) - nhà thơ, anh hùng dân tộc Bungari, sinh ngày 25-12-1847.\nDi sản thơ ca của ông được coi là đỉnh cao của thơ trữ tình Bungari, có nội dung cách mạng yêu nước, cảm xúc sâu sắc, ngôn ngữ gần với thi ca dân gian. Ông có các bài thơ nổi tiếng như: \"Khúc bi tráng\", \"Du kích\", \"Đấu tranh\"...\nTừ nǎm 1875 ông là người lãnh đạo cách mạng dân chủ Bungari. Ngày 17-3-1876, từ Rumani ông dẫn một đội quân vượt sông Đanuýp trở về Bungari tham gia chiến đấu. Ông đã anh dũng hy sinh trong một trận giáp chiến vào ngày 2-5-1876, lúc ông mới 29 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '25','12', '25-12-1991, Tổng Bí thư cuối cùng của Liên bang Nga từ chức. Nước Nga chuyển sang thể chế mới, thể chế cộng hoà.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','01', 'Để xây dựng thí điểm các đoàn thể cứu quốc của Việt Minh ở Cao Bằng, đồng chí Nguyễn Ái Quốc chủ trương mở lớp huấn luyện cán bộ Việt Minh do Người trực tiếp phụ trách. Đây là lớp huấn luyện đầu tiên gồm có 40 học viên học tại làng Nậm Quang và Ngằm Tảy (hương Bình Vân, Tĩnh Tây, Trung Quốc). Lớp học kết thúc vào ngày 26-1-1941.\nĐiểm độc đáo của phương pháp huấn luyện của Người tại lớp học là sự kết hợp rất chặt giữa lý luận và thực tiễn, học và hành. Kết thúc lớp huấn luyện, 40 cán bộ Cao Bằng đã trở về ngay địa phương mình đi vào quần chúng nhân dân tuyên truyền, giác ngộ và tổ chức các đoàn thể cứu quốc của Việt Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','01', 'Ngày 26 tháng giêng nǎm 1981, Nhà nước ta trao tặng danh hiệu Anh hùng lao động và Huân chương Hồ Chí Minh cho trung tá Lêônnít Pôpốp, nhà du hành vũ trụ, chỉ huy trên tổ hợp Chào mừng 6 - Liên hợp 36 và kỹ sư Aborariumin, Anh hùng phi công vũ trụ Liên Xô vì đã có cống hiến xuất sắc vào công việc chinh phục vũ trụ, góp phần thực hiện thành công chuyến bay vũ trụ Xô - Việt trên tổ hợp quỹ đạo nghiên cứu vũ trụ khoa học Chào mừng 6 - Liên hợp 36 - Liên hợp 37, và đã thể hiện tinh thần dũng cảm, khí phách anh hùng trong chuyến bay.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','01', 'Chí sĩ Nguyễn Thần Hiến, sinh nǎm 1856 tại tỉnh Kiên Giang.\nNǎm 1905, hưởng ứng phong trào Đông Du, ông đã góp nhiều công sức thành lập \"Khuyến du học hội\" nhằm vận động học sinh sang Nhật Bản học tập, đồng thời tổ chức nhiều cơ sở yêu nước tại Việt Nam. Từ nǎm 1910 đến nǎm 1913, ông thường qua lại Nhật Bản, Hồng Kông, Thượng Hải, cùng các đồng chí mua sắm vũ khí, gửi về nước cho các lực lượng vũ trang khởi nghĩa. \nNǎm 1913, ông Nguyễn Thần Hiến bị mật thám Pháp bắt ở Hồng Kông rồi chúng đưa ông về giam ở Hà Nội. Sau mấy tháng bị cực hình, ông tuyệt thực, đến ngày 26-1-1914, ông mất tại nhà lao Hoả Lò.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','01', 'Vuốc Xtêphanôvich Caradich (Vuk Stefanovich Karadzic) - nhà vǎn, nhà bác học lớn Serbia, sinh ngày 26-10-1787.\nÔng là nhà sưu tầm, hệ thống hoá các sáng tác dân gian. Nǎm 1814 ông xuất bản tuyển tập ca dao và dân ca Secbia. Nǎm 1836 ông sưu tầm tập hợp in Tuyển tập tục ngữ phương ngôn, sưu tầm bộ Dân ca Secbia gồm 9 tập. Ông còn là nhà phê bình vǎn học, góp phần thúc đẩy mạnh sự hình thành chủ nghĩa lãng mạn tích cực trong vǎn học Secbia. Ông mất ngày 26-1-1864.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','01', 'Êđuốt Gienơ (Edward Jenner) bác sĩ người Anh, sinh nǎm 1749, qua đời ngày 26-1-1823.\nÔng là người phát minh phương pháp chủng đậu: chủng ở da tinh độc của bệnh đậu, tạo ra tính miễn dịch ở người đối với bệnh đậu mùa.\nJenner viết nhiều sách liên quan đến các công trình nghiên cứu của ông, được người đương thời rất trọng vọng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','01', '26-1-1950, Ấn Độ tuyên bố là một nước Cộng hoà. Rajendra Prasad lên nắm chức vụ Tổng thống.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','02', 'Ngày 26-2-1969, Quân giải phóng tiến công cǎn cứ địch ở Đồng Dù. Đồng Dù như tấm cửa sắt giữ phía bắc Sài Gòn. Để bảo vệ cǎn cứ này, Mỹ đã thiêu huỷ cả một vùng rộng lớn gồm 7 xã, lập hàng rào dây thép gai, hoả lực mạnh và một máy móc điện tử tinh vi nhằm phát hiện bóng người.\nSau 40 phút chiến đấu, Quân giải phóng Gia Định đã biến cǎn cứ này thành mồ chôn bọn lính \"Tia chớp nhiệt đới\" Mỹ, diệt 1.270 tên, đốt cháy 4 kho đạn, một kho xǎng, phá huỷ 125 máy bay các loại, 179 xe quân sự.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','02', 'Từ ngày 26-2 đến ngày 2-3-1973, tại Pari đã diễn ra Hội nghị quốc tế về Việt Nam. Hội nghị có đại biểu 4 bên tham gia, 5 nước lớn (Liên Xô, Trung Quốc, Anh, Pháp, Mỹ), 4 nước trong Uỷ ban Giám sát và Kiểm soát quốc tế (Ba Lan, Hunggari, Canađa, Indônêxia) và Tổng Thư ký Liên hiệp quốc với tư cách là quan sát viên.\nHội nghị đã ký Định ước xác nhận và cam kết tôn trọng các vǎn bản của Hiệp định Pari. Giá trị của Hiệp định Pari tǎng thêm, vị trí của việt nam Dân chủ Cộng hoà và Chính phủ Cách mạng lâm thời miền Nam được đề cao.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','02', 'Danh tướng Trần Bình Trọng sinh nǎm 1259, quê ở làng Bảo Thái, huyện Thanh Liêm, tỉnh Hà Nam.\nÔng có huân công, được phong tước Bảo Nghĩa Vương.\nKhi quân Nguyên sang xâm lược nước ta, kinh đô Thǎng Long thất thủ, ông lĩnh nhiệm vụ ở lại Thiên Trường để ngǎn chặn đạo quân của Thoát Hoan, trong khi Hưng Đạo Vương đưa vua Trần về Hải Dương chuẩn bị lực lượng chống ngoại xâm.\nNgày 26-2-1285, tướng Trần Bình Trọng chỉ huy một cánh quân chống quân Nguyên ở bãi Tức Mặc, và bị giặc bắt. Quân Nguyên dụ ông đầu hàng sẽ được phong vương, ông khảng khái trả lời: \"Ta thà làm quỷ nước Nam, chứ không thèm làm vương đất Bắc\". Giặc đã sát hại ông khi ông mới 26 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','02', 'Phạm Thái hiệu là Chiêu Lỳ, người xã Yên Thường, huyện Gia Lâm, Hà Nội. Sinh ngày 26-2-1777, qua đời nǎm 1813.\nÔng là con một võ tướng thời Lê Cảnh Hưng. Khi quân Tây Sơn ra Bắc, Phạm Thái tham gia phù Lê, chống Tây Sơn. Bị thất bại và bị truy nã.\nÔng đã viết: \"Sơ kính tân trang\" là một tập truyện thơ bằng chữ nôm. Tác phẩm này độc đáo về nhiều phương diện. Cốt truyện dựa trên cơ sở cuộc đời thực của tác giả. Cảnh vật thiên nhiên và cuộc sống xã hội trong \"Sơ kính tân trang\" đều có giá trị chân thực lịch sử. Phạm Thái cũng đã đưa vào tác phẩm một phong cách bút pháp và ngôn ngữ tự do, phóng khoáng, mang màu sắc hiện đại.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','02', '26-2-1973, Việt Nam thiết lập quan hệ ngoại giao với Australia')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','02', 'Vichto Huygô (Victor Marie Hugo) sinh ngày 26-2-1802, qua đời nǎm 1885.\nÔng là nhà vǎn, nhà thơ, nhà soạn kịch lớn của nước Pháp và thế giới ở thế kỷ XIX.\nCác cuốn tiểu thuyết Những người khốn khổ, Nhà thờ Đức Bà Pari của ông được bạn đọc nhiều nước ưa thích và đã được dựng thành phim. Tập thơ Trừng phạt của Vichto Huygô cũng rất nổi tiếng.\nÔng là người sáng lập ra trường phái vǎn học lãng mạn, là người theo chế độ cộng hoà dân chủ, đấu tranh mạnh mẽ chống chế độ quân chủ, phải sống lưu vong 19 nǎm. \nVichto Huygô được bầu vào Viện Hàn lâm và Quốc hội nước Pháp. Hài cốt của ông được đưa vào lǎng mộ danh nhân Pǎngtêông.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','03', 'Cuối tháng 3 nǎm 1931, Trung ương Đảng Cộng sản Đông Dương đã triệu tập Hội nghị toàn thể lần thứ hai tại Sài Gòn, dưới sự chủ toạ của Tổng bí thư Trần Phú. Hội nghị đã đề ra những nhiệm vụ cấp bách và tǎng cường thành phần công nhân trong Đảng. Cũng tại Hội nghị này, nhận thấy vai trò của lực lượng thanh niên trong sự nghiệp cách mạng nên đã đề ra quyết định \"Cần kíp tổ chức ra Cộng sản thanh niên Đoàn\" và chỉ thị cho các tổ chức Đảng ở các địa phương quan tâm đến việc xây dựng tổ chức Đoàn thanh niên. Từ đó Đoàn TNCS Đông Dương ra đời. Qua các giai đoạn của Cách mạng, tổ chức này trải qua các tên gọi như: Đoàn thanh niên dân chủ, Đoàn thanh niên phản đế, Đoàn thanh niên cứu quốc, Đoàn thanh niên lao động và ngày nay là Đoàn thanh niên Cộng sản Hồ Chí Minh. Cùng đất nước, các thế hệ thanh niên Việt Nam đã làm tròn sứ mệnh lịch sử của mình. Từ ngày 23 đến ngày 25-5-1961, Đại hội đại biểu toàn quốc lần thứ ba của Đoàn lấy ngày 26-3 - một ngày trong thời gian diễn ra Hội nghị Trung ương tháng 3-1931 - làm kỷ niệm thành lập đoàn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','03', 'Ngày 26-3-1955 Quốc hội khoá I đã ra quyết định về \"tự do tín ngưỡng\". Đây là sự thể hiện đường lối đúng đắn, đại đoàn kết dân tộc của Đảng ta từ những ngày đầu thành lập.\nTừ khi thành lập, Đảng của giai cấp vô sản lãnh đạo nhân dân ta tiến hành đấu tranh giải phóng dân tộc, thống nhất đất nước. Sự đoàn kết toàn dân không phân biệt tôn giáo, dân tộc đã kết thành sức mạnh làm nên chiến thắng. Trong đấu tranh cách mạng, nhiều đoàn thể, nhiều tổ chức tôn giáo đã tham gia tích cực và đóng góp xứng đáng. Tôn trọng tự do tín ngưỡng đã trở thành một trong những mục tiêu hành động của toàn dân Việt Nam ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','03', 'Ngày 26-3-1965, bộ đội pháo cao xạ đã bắn rơi 12 chiếc máy bay Mỹ tại tỉnh Hà Tĩnh.\nĐây là một trận thắng oanh liệt của binh chủng pháo cao xạ trong cuộc chiến đấu đánh trả chiến tranh phá hoại của đế quốc Mỹ đối với miền Bắc nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','03', 'Từ các đơn vị độc lập ở chiến trường Tây Nguyên, ngày 26-3-1975 đã thành lập Quân đoàn 3.\nCác đơn vị của quân đoàn đã kinh qua cuộc kháng chiến chống thực dân Pháp và được tôi luyện 10 nǎm trên chiến trường Tây Nguyên.\nMùa xuân nǎm 1975, các đơn vị của quân đoàn đã lập chiến công xuất sắc ở Buôn Ma Thuột, tiêu diệt nhiều sinh lực địch, đánh chiếm sân bay Tân Sơn Nhất và bộ tổng tham mưu ngụy.\nQuân đoàn còn hoàn thành xuất sắc nhiệm vụ bảo vệ biên giới Tây Nam và nghĩa vụ quốc tế ở Cǎmpuchia được nước bạn thưởng huân chương Ǎngco. \nNǎm 1980 Quân đoàn 3 binh đoàn Tây Nguyên được Nhà nước tuyên dương là Đơn vị Anh hùng lực lượng vũ trang.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','03', 'Trong cuộc Tổng tiến công và nổi dậy mùa xuân nǎm 1975, trên chiến trường Quảng Trị - Thừa Thiên, sau khi đập tan tuyến phòng thủ vòng ngoài của địch, pháo binh của ta tập chung bắn phá các cǎn cứ địch ven thành phố Huế. Các mũi bộ binh nhanh chóng bao vây chia cắt địch, chặt đứt mọi đường rút lui của chúng tháo chạy về biển hòng tìm lối thoát. Quân và dân Thừa Thiên - Huế đã xốc tới truy kích địch. Đồng bào trong tỉnh nổi dậy phối hợp chặt chẽ với lực lượng vũ trang tiến công địch ở các chi khu quân sự và nhanh chóng đập tan bộ máy kìm kẹp, giành quyền làm chủ ở tất cả các quận. Nhiều đơn vị lính ngụy đã mang súng trở về với nhân dân.\nNgày 26-3-1975 thành phố Huế đã được giải phóng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','03', 'Ngày 26-3-1483 là ngày sinh của Raphaen, danh hoạ Italia. Tác phẩm của ông thể hiện đầy đủ tính nhân vǎn trong nghệ thuật. Ông là người trung thành với truyền thống hiện thực trong bất kỳ tác phẩm nào. Đó là những hình tượng của con người hoàn chỉnh, con người lý tưởng, hoà hợp nhịp nhàng với kiến trúc và phong cảnh bao quanh. Ông cũng là một trong những người đã góp phần làm cho Rôma trở thành trung tâm vǎn hoá lúc bấy giờ với việc trang trí điện Vaticǎng. Tuy mất sớm (khi mới 37 tuổi) Raphaen đã để lại một số lượng rất lớn tác phẩm. Trong đó nổi tiếng nhất là bức hoạ Đức mẹ ở nhà thờ Xichxtơ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','03', 'Nhà vǎn Pháp Vichto Huygô sinh ngày 26-3-1802 và qua đời ngày 22-5-1885. \nÔng là nhà vǎn lớn nhất của chủ nghĩa lãng mạn của nước Pháp. Là người sáng lập ra trường phái lãng mạn, ông đã viết hơn 20 tập thơ, nhiều tiểu phẩm nổi tiếng như: Những người khốn khổ, Nhà thờ Đức bà Pari.\nLà một người cộng hoà, dân chủ, ông đã đấu tranh mạnh mẽ chống chế độ quân chủ, phải sống lưu vong 19 nǎm. Nhà vǎn Vichto Huygô được bầu vào Viện Hàn lâm và Quốc hội nước Pháp. Khi qua đời, thi hài của ông được đưa vào lǎng danh nhân Pǎngtêông ở Thủ đô Pari.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','03', '26-3-1979 là ngày ký kết Hiệp ước Ai-cập - Israel, mang lại nền hoà bình đầu tiên giữa một nước A-rập và một nước Do Thái, chấm dứt 30 nǎm chiến tranh giữa hai nước này.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','04', 'Hội nghị Giơnevơ là hội nghị Bộ trưởng ngoại giao của một số nước, họp từ ngày 26-4 đến 21-7-1954 tại Giơnevơ (Thụy Sĩ).\nHội nghị được triệu tập theo chủ trương của Hội nghị Béclin nǎm 1954. Chương trình thảo luận của hội nghị gồm 2 vấn đề: Giải quyết vấn đề Triều Tiên và lập lại hoà bình ở Đông Dương.\nDo thái độ ngoan cố của Mỹ và các nước chư hầu đã tham gia chiến tranh Triều Tiên, cuộc thảo luận về vấn đề Triều Tiên không thu được kết quả.\nĐến ngày 8-5-1954, đúng 1 ngày sau chiến thắng Điện Biên Phủ, vấn đề chấm dứt chiến tranh lập lại Hoà Bình ở Đông Dương chính thức được hội nghị Ginevơ thảo luận. Phái đoàn ta do đồng chí Phạm Vǎn Đồng làm trưởng đoàn tham gia hội nghị với tư thế đoàn đại biểu của một dân tộc chiến thắng.\nTuyên bố chung của Hội nghị và Hiệp định đình chiến ở Đông Dương được ký kết vào ngày 21-7-1954.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','04', '26-4-1955, Học viện Hải quân đã được thành lập, ban đầu có tên là Trường Huấn luyện bờ biển, rồi Trường Sĩ quan hải quân. Đến ngày 3-4-1993, trường được nâng cấp thành Học viện Hải quân với các nhiệm vụ: Đào tạo sĩ quan hải quân có trình độ đại học, bổ túc sĩ quan chỉ huy hải quân có trình độ trên đại học và nghiên cứu khoa học.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','04', 'Ngày 26-4-1962 Bộ Chính trị Trung ương Đảng ra nghị quyết về cuộc vận động \"nâng cao ý thức trách nhiệm, tǎng cường quản lý kinh tế tài chính, cải tiến kỹ thuật, chống tham ô, lãng phí, quan liêu\", gọi tắt là cuộc vận động \"ba xây, ba chống\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','04', 'Ngày 26-4-1964, các cử tri ở miền Bắc đã bầu cử đại biểu Quốc hội khoá II.\nTổng số đại biểu được bầu là 366. Tổng số người ứng cử là 488. Tổng số cử tri là 8 triệu 775.002\nTrong tổng số 366 đại biểu trúng cử có 60 đại biểu các dân tộc ít người; 62 đại biểu là phụ nữ; 71 đại biểu là công nhân; 90 đại biểu là nông dân; 18 đại biểu là quân nhân; 98 đại biểu làm công tác khoa học kỹ thuật, giáo dục, vǎn nghệ; 8 đại biểu là người lãnh đạo các tôn giáo, 3 đại biểu là tư sản dân tộc.\nNhư vậy 366 đại biểu khoá này cộng với 89 đại biểu miền Nam được kéo dài nhiệm kỳ, tổng số đại biểu khoá III là 455 vị.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','04', 'Ngày 26-4-1976, Thủ tướng Chính phủ đã có quyết định thành lập Viện nghiên cứu hạt nhân trực thuộc Uỷ ban khoa học và kỹ thuật Nhà nước. Nǎm 1979, Viện nghiên cứu hạt nhân được đặt dưới sự chỉ đạo trực tiếp của Thủ tướng Chính phủ và nǎm 1984 đổi tên thành Viện Nǎng lượng nguyên tử quốc gia. Từ nǎm 1993, Viện chuyển về Bộ Khoa học - Công nghệ và môi trường và lấy tên là Viện Nǎng lượng nguyên tử Việt Nam.\nViện có nhiệm vụ nghiên cứu sử dụng nǎng lượng nguyên tử trong các ngành kinh tế quốc dân và trong đời sống.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','04', 'Ngày 26-4-1981, cử tri cả nước ta đã bầu đại biểu Quốc hội khoá 7.\nTổng số đại biểu được bầu là 496.\nNhiệm kỳ khoá này từ tháng 6 nǎm 1981 đến tháng 6 nǎm 1987. Có 12 kỳ họp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','04', 'Nước cộng hoà Nam Phi là một quốc gia lớn ở Châu Phi và từng là thuộc địa của Anh. Đã nhiều thế kỷ, nhân dân các bộ tộc Nam Phi bị bọn thực dân và sau là chính quyền của người da trắng thiểu số kỳ thị và bóc lột thậm tệ. Chủ nghĩa Apacthai ở đây được bộc lộ rõ nét nhất, phản động nhất và thiếu vǎn minh nhất. Nhân dân các bộ tộc Nam Phi dưới sự lãnh đạo của Tổ chức nhân dân Tây Nam Phi (ANC) đã kiên trì đấu tranh giành quyền tự do, quyền bình đẳng thực sự.\nNgày 26-4-1994, đã diễn ra cuộc bầu cử dân chủ không phân biệt mầu da lần đầu tiên trong lịch sử nước cộng hoà Nam Phi. Ông Nexơn Manđêla lãnh tụ ANC đã được bầu làm Tổng thống và là vị Tổng thống da đen đầu tiên ở quốc gia này.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','05', 'Từ ngày 26 đến ngày 30-5-1956 tại Hà Nội đã diễn ra Đại hội Phụ nữ toàn quốc lần thứ hai.\nTham dự Đại hội có 425 đại biểu thuộc mọi tầng lớp tôn giáo, dân tộc, xu hướng chính trị. Đại hội vui mừng đón tiếp các đoàn đại biểu của Liên đoàn Phụ nữ dân chủ quốc tế và đoàn đại biểu phụ nữ một số nước xã hội chủ nghĩa. Đại hội đã kiểm điểm công tác từ đại hội lần thứ nhất 1950 và đề ra nhiệm vụ của phụ nữ trong giai đoạn cách mạng và bầu BCH Trung ương Hội Liên hiệp phụ nữ Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','05', 'Ngày 26-5-1957, Hồ Chủ tịch đã tới sân vận động Hàng Đẫy (ở phố Trịnh Hoài Đức, Hà Nội) xem trận đấu bóng đá giữa hai đội Việt Nam và Cǎmpuchia.\nTừ nǎm 1946 đến 1961, Hồ Chủ tịch đã tới sân vận động này 8 lần xem các hoạt động thể dục thể thao, động viên mọi người rèn luyện thân thể để có sức khoẻ học tập, lao động và bảo vệ Tổ quốc. Bản thân Hồ Chủ tịch đã nêu tấm gương rèn luyện thể dục thể thao như Người đã nói: \"Tự tôi ngày nào cũng tập\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','05', 'Cán bộ, công nhân viên Việt Nam và Liên Xô của Xí nghiệp Liên doanh dầu khí Việt Xô đã phát hiện thấy dầu tại thềm lục điạ Việt Nam vào ngày 26-5-1984.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','06', 'Nhằm đối phó với phong trào đấu tranh của công nhân, ngày 26-6-1943, Toàn quyền Đông Dương ra Nghị định cho phép giới chủ thầu được tuyển mộ các nhân viên mật vụ theo dõi hoạt động của công nhân. Mục đích của nhân viên mật vụ là ngǎn chặn công nhân đình công. Những nhân viên này khi nhận việc phải làm tuyên thệ trước toà án và được Thanh tra lao động cấp một loại thẻ đặc biệt.\nĐây là một hình thức mới nữa của thực dân Pháp nhằm giám sát mọi hoạt động chống đối của công nhân Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','06', 'Ngày 26-6-1996, Tổng Công ty bưu chính viễn thông Việt Nam đưa mạng thông tin di động Vinafone, mạng thông tin di động GSM thứ hai đi vào hoạt động song song với mạng Mobifone của VMS đã có trước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','06', 'Hội nghị quôc tế dẫn đến thành lập Liên hiệp quốc được khai mạc tại thành phố Sanphranxixcô (Mỹ). Có 50 nước được mời tham dự. Đó là những nước đã tuyên chiến với chủ nghiã phát xít. Vấn đề chủ yếu của chương trình nghị sự là soạn thảo chi tiết và thông qua Hiến chương Liên hiệp quốc (26-6-1945).\nTrong lời nói đầu của bản Hiến chương đã nói rõ mục đích của Liên hiệp quốc là: \"Phòng ngừa cho cac thế hệ tương lai khỏi thảm hoạ của chiến tranh... Khẳng định lại lòng tin vào những quyền cơ bản của con người, vào nhân phẩm và giá trị của các cá hhân, vào quyền bình đẳng của nam nữ và các dân tộc lớn hay nhỏ. Thiết lập những điều kiện cho phép duy trì sự công bằng và sự tôn trọng đối với các nghĩa vụ phải thực hiện dựa theo các thoả ước và các luật quốc tế. Thúc đẩy sự tiến bộ xã hội với mức sống cao hơn trong sự tự do rộng rãi hơn\".\nChính vì vậy, từ nǎm 1945 trở đi, ngày 26-6 được kỷ niệm hàng nǎm và được gọi là ngày Hiến chương Liên hiệp quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','07', 'Chiêu Minh Vương Trần Quang Khải danh tướng nhà Trần, sinh nǎm 1214 quê ở Tức Mặc, Thiên Trường, Nam Định.\nÔng học nhiều, biết rộng, có tài vǎn chương, giỏi việc quân sự. Nǎm 1371 ông được cử giữ chức Tướng quốc Thái Uý, rồi thǎng đến Thái sư. Trong cuộc đánh đuổi quân Nguyên Mông xâm lược, ông cùng Trần Quốc Tuấn và các chiến hữu lập được những chiến tích vẻ vang. Bài thơ chiến thắng khải hoàn \"Tụng Giá Hoàng kinh sư\" trở thành bản hùng ca dân tộc.\nĐoạt giáo Chương Dương độ\nCầm hồ Hàm Tử quan\nThái bình tu nỗ lực\nVạn cổ thử giang san.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','07', 'Ngày 26-7-1936 đã diễn ra Hội nghị Ban chấp hành Trung ương Đảng Cộng sản Đông Dương tại Thượng Hải (Trung Quốc).\nHội nghị họp dưới sự lãnh đạo chủ toạ của Ủy viên Ban chấp hành Quốc tế cộng sản - Lê Hồng Phong, nhằm triển khai tinh thần nghị quyết Đại hội 7 Quốc tế Cộng sản, bổ sung cho nghị quyết của đại hội Đảng lần thứ nhất - tạo ra một bước chuyển biến sâu sắc của cách mạng Đông Dương trong tình hình mới. Hội nghị chủ trương thành lập Mặt trận Nhân dân phản đế rộng rãi, đấu tranh cho các mục tiêu dân chủ, cơm áo và hoà bình. Hội nghị nhấn mạnh đến việc thay đổi hình thức tổ chức đấu tranh, triển khai các tổ chức và đấu tranh hợp pháp, bán hợp pháp bên cạnh phương thức bí mật, đấu tranh chống những biểu hiện tả khuynh và hữu khuynh trong Đảng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','07', 'Bécna Sô là nhà vǎn Anh, gốc Ailen. Ông sinh ngày 26-7-1856. Nǎm 20 tuổi ông đến Luân Đôn, và đi vào con đường hoạt động xã hội trở thành nhà phê bình nhạc kiêm phê bình vǎn học. Rồi ông chuyển sang viết kịch. Các vở kịch của ông đã mau chóng thu hút được sự ngưỡng mộ của khán giả.\nBécna Sô là nhà soạn kịch Anh vĩ đại, đã cống hiến cho sân khấu Anh và sân khấu thế giới những những tác phẩm xuất sắc. Tác phẩm của ông là lời tố cáo đanh thép chủ nghĩa tư bản độc ác, là tư tưởng đấu tranh cho con người, cho dân tộc. Ông là nhà cách tân sân khấu lớn của thế kỷ 20. Nǎm 1925 ông được nhận giải thưởng Nôben về vǎn học. Ông mất ngày 2-11-1950, thọ 96 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','07', 'Thuật ngữ \"chiến tranh lạnh\" do Barút, một chuyên gia vạch kế hoạch nguyên tử của Mỹ đặt ra và lần đầu tiên xuất hiện trên báo chí Mỹ ngày 26-7-1947.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','07', 'Ngày 26-7-1953, hơn 130 thanh niên yêu nước do Phiđen Caxtơrô lãnh đạo tấn công pháo đài Môncađơ mở đầu cuộc đấu tranh của nhân dân Cuba nhằm lật đổ chế độ độc tài quân sự Batista.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','07', 'Ngày 26-7-1961, Đảng của giai cấp công nhân Cuba thành lập trên cơ sở thống nhất ba tổ chức cách mạng: \"Phong trào 26 tháng Bảy\", \"Đảng xã hội chủ nghĩa nhân dân Cuba\", \"Phong trào 13 tháng Ba\" thành Tổ chức Cách mạng thống nhất được củng cố lại, lấy tên là Đảng thống nhất Cách mạng xã hội chủ nghĩa và đến 3-1-1965, đổi tên thành Đảng Cộng sản Cuba.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','08', 'Ngày 26-8-1945 Chính phủ lâm thời họp. Theo đề nghị của Hồ Chủ tịch, một Chính phủ thống nhất toàn quốc thể hiện chính sách đoàn kết rộng rãi, bao gồm những đại biểu các đảng phái yêu nước và một số nhân sĩ không đảng phái có danh tiếng được thành lập. Nhiều Uỷ viên Tổng bộ Việt Minh trong chính phủ lâm thời yêu cầu được rút lui nhường chỗ cho những người không phải Việt Minh.\nCùng ngày này, Hồ Chủ tịch đã chủ toạ phiên họp đầu tiên của Thường vụ Trung ương Đảng. Hội nghị quyết định ngày ra mắt của Chính phủ lâm thời cũng là ngày nước Việt Nam chính thức công bố quyền độc lập và thiết lập chính thể dân chủ cộng hoà.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','08', 'Ngày 26-8-1946, 57 người thay mặt cho các tầng lớp nhân dân ở Sài Gòn ký tên vào bản quốc nghị gửi lên Quốc hội và Chính phủ Trung ương \"Xin đổi tên Thành phố Sài Gòn thành Thành phố Hồ Chí Minh, để tượng trưng cho sự chiến đấu hy sinh và cương quyết trở về với Tổ quốc của dân Nam Bộ\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','08', 'Tại đại hội lần thứ 5 Bộ trưởng ngoại giao các nước không liên kết họp tại thủ đô Lima (Pêru) từ ngày 25 đến ngày 30-8-1975, Việt Nam dân chủ cộng hoà được công nhận là thành viên của khối các nước không liên kết ngày 26-8-1975.\nHội nghị kết thúc với bản tuyên bố Lima và nhiều nghị quyết quan trọng. Trong đó hội nghị nhiệt liệt hoan nghênh thắng lợi to lớn và rực rỡ của nhân dân ba nước Đông Dương, hoan nghênh chiến thắng lịch sử của nhân dân Việt Nam anh hùng trong cuộc đấu tranh vì độc lập tự do, đánh bại hoàn toàn cuộc xâm lược của đế quốc Mỹ, khẳng định tình đoàn kết với nhân dân Việt Nam, Lào, Campuchia.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','08', 'Tuyên ngôn nhân quyền và dân quyền của Pháp với tiêu chí \"Tự do - Bình đẳng - Bác ái\", được công bố ngày 26-8-1789.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','08', 'Ǎngtoan Lôren Lavoadiê (Antoine Laurent de Lavoisier) sinh ngày 26-8-1943. Nǎm 17 tuổi, ông theo học luật trường đại học tổng hợp Pari. Nǎm 21 tuổi ông đã trở thành tiến sĩ luật. Nǎm 22 tuổi, ông công bố công trình khoa học đầu tiên về độ tan của thạch cao, sự mất nước của nó khi nung và khả nǎng tái hợp của nó với nước. Sau đó ít lâu ông được bầu vào Viện Hàn lâm khoa học Pháp.\nTrong thời gian từ 1778 đến 1782, ông nghiên cứu nông nghiệp và đưa ra nhiều cải tiến có giá trị. Ông nghiên cứu bản chất của nhiệt, chế tạo ra nhiệt lượng kế, đo nhiệt nung, nhiệt cháy của các chất. Ông xác định thành phần của nước, cơ chế phân huỷ và tổng hợp nước. Ông đã định ra quy tắc thống nhất về cách gọi tên các hợp chất hoá học. Nǎm 1789 ông hoàn thành một tác phẩm quan trọng là \"Các biến đổi cơ bản của hoá học\".\nLà một nhà hoạt động xã hội rộng lớn, lại là một nhà quý tộc nên ông trở thành đối tượng của cách mạng tư sản Pháp. Ngày 8-5-1794 ông bị xử chém.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','09', 'Sau sự kiện ngày 23-9-1945 - quân Pháp gây hấn ở Nam Bộ, ngày 26-9-1945 qua Đài phát thanh, Chủ tịch Hồ Chí Minh gửi thư kêu gọi đồng bào Nam Bộ anh dũng kháng chiến chống thực dân Pháp xâm lược, trong thư có đoạn viết: \"Tôi tin và đồng bào cả nước đều tin vào lòng kiên quyết ái quốc của đồng bào Nam Bộ... \"Thà chết tự do còn hơn sống nô lệ\". Tôi chắc và đồng bào Nam Bộ cũng chắc rằng Chính phủ và đồng bào toàn quốc sẽ hết sức ủng hộ những chiến sĩ và nhân dân hiện đương hy sinh đấu tranh để giữ vững nền độc lập của nước nhà\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','09', 'Để chi viện cùng đồng bào Nam Bộ kháng chiến chống thực dân Pháp xâm lược, ngày 26-9-1945, đoàn quân Nam Tiến đầu tiên xuất phát từ ga Hà Nội. Đoàn gồm có 3 đại đội Bắc Sơn, Bắc Cạn và Hà Nội được tổ chức thành một chi đội do đồng chí Hoàng Thơ là chỉ huy. Ít ngày sau, đoàn quân này đã tham gia chiến đấu ở mặt trận Sài Gòn. Sau đó, suốt hai tháng, hầu như ngày nào cũng có quân Nam Tiến, hầu như chuyến xe lửa nào cũng chở quân Nam Tiến.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','09', 'Ivan Pêtrôvich Paplốp là nhà sinh lý học Nga vĩ đại, người sáng tạo học thuyết di vật về hoạt động thần kinh cấp cao. Ông sinh ngày 26-9-1849 trong một gia đình linh mục nghèo.\nNăm 1904, ông là người Nga đầu tiên được nhận giải thưởng Noben dành cho công trình nghiên cứu sinh lý học. Ông là người phát triển trường phái tổng hợp. Thông qua học thuyết về phản xạ có điều kiện và hệ thống tín hiệu thứ hai, ông đã chứng minh rằng ý thức con người được hình thành trên những cơ sở vật chất. Ông để lại cho nhân loại một di sản khổng lồ: Đó là hàng trăm công trình nghiên cứu lớn nhỏ không những đặt nền móng cho bộ môn sinh lý học hiện đại, mà còn có giá trị cho một loạt lĩnh vực nghiên cứu khác của khoa học.\nÔng được bầu là Viện sĩ của Viện Hàn lâm và nhiều Hội khoa học khác của Thụy Điển, Đan Mạch, Áo, Pháp, Anh, Đức, Mỹ.\nÔng mất ngày 27-2-1936.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','09', 'Nhạc sĩ người Hunggari, Bác Tốc sinh ngày 25-3-1881 tại miền Bắc Rumani.\nÔng là một trong những nhạc sĩ sáng tác lỗi lạc nhất của thế kỷ XX. Ông có nhiều cống hiến lớn lao trong việc đổi mới ngôn ngữ âm nhạc trên cơ sở nhạc dân gian để tạo ra một phong cách riêng với những đường nét làn điệu hấp dẫn, những tiết tấu độc đáo, những hoà âm táo bạo.\nÔng mất ngày 26-9-1945 tại Niu Oóc. Năm 1955 ông được truy tặng Giải thưởng Hoà bình thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','09', 'Danh hoạ Từ Bi Hồng sinh ngày 19-7-1895 tại tỉnh Hồ Giang, Trung Quốc.\nÔng có biệt tài vẽ ngựa. Sau ông còn thăng hoa rực rỡ trong nhiều lĩnh vực khác. Chính vì vậy mà ông được tôn vinh là một nhà cách mạng nghệ thuật kiệt xuất trong sự nghiệp đào tạo mỹ thuật. Ông còn là một nhà giáo dục xuất sắc, nhà yêu nước và hoạt động xã hội đầy nhiệt huyết.\nSau Cách mạng, Từ Bi Hồng được bổ nhiệm làm Viện trưởng Học viện Mỹ thuật Trung ương, Chủ tịch Hiệp hội Mỹ thuật gia Trung Quốc, Đại biểu Quốc hội. Ông được coi là một điểm sáng rực rõ của tài hoa giao thoa nghệ thuật Đông Tây, mang lại sự chấn hưng nghệ thuật đương thời Trung Quốc. Ông mất ngày 26-9-1953.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','10', 'Ngày 26-10-1967, với hai sư đoàn bộ binh số 7 và 9 cùng một số đơn vị kỹ thuật yểm trợ, quân giải phóng miền Nam đã mở đầu chiến dịch Lộc Ninh.\nSau hơn một tháng tiến công quân Mỹ - Nguỵ, các đơn vị quân giải phóng đã đánh 60 trận (có hai trận cấp sư đoàn) loại khỏi vòng chiến đấu 5400 tên địch (trong đó có 2900 tên Mỹ) bắt 617 tên, phá huỷ 103 xe quân sự, 63 khẩu pháo, thu 172 súng các loại.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','10', 'Nhạc sĩ người Italia Dominico Xcáclát sinh ngày 26-10-1685 tại Napôli. Ông được coi là người tiền thân về sáng tác Xônát trong âm nhạc.\nÔng mất ngày 23-7-1757 tại Mađrít.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','10', 'Êvarít Galoa (Évaiste Galois), sinh ngày 26-10-1811 ở Bualaren gần thủ đô Pari.\nÔng là nhà toán học thiên tài, người được cơ sở cho đại số hiện đại.\nNăm 1832, mới 21 tuổi, Galoa bị chết trong một trận quyết đấu. Đêm hôm trước, Galoa ghi lại trên 60 trang giấy các phát minh chủ yếu của mình. Phải 40 năm sau, thế giới toán học mới hiểu hết được lý thuyết Galoa.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','11', 'Nguyễn Vǎn Thoại, còn gọi là Thoại Ngọc Hầu, sinh ngày 26-11-1761, quê ở huyện Điện Bàn, tỉnh Quảng Nam, là một nhà doanh điền lớn.\nNǎm 1818 ông đốc suất dân binh đào kênh Đông Xuyên ở Long Xuyên, sau mang tên ông là kênh Thoại Hà. Tiếp đó, nǎm 1820, ông được lệnh của Vua Minh Mạng cho đào một con kênh nối liền Châu Đốc với Hà Tiên, dài gần 100 km mang tên vợ ông là kênh Vĩnh Tế. Đây là một đường thuỷ tiện lợi giao thông, đồng thời làm sạch phèn, tưới nước ngọt cho một diện tích lớn trồng cây ở Hà Tiên và Rạch Giá.\nÔng Thoại Ngọc Hầu mất nǎm 1829.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','11', 'Ngày 26-11-1946, Chủ tịch Hồ Chí Minh đã ký sắc lệnh quy định tổ chức ngành thương mại nước ta. Từ đó đến nay, ngành thương mại đã không ngừng phấn đấu, trưởng thành, phục vụ tốt các nhiệm vụ chính trị, kinh tế, xã hội của đất nước qua các giai đoạn cách mạng, ngày càng thể hiện vai trò quan trọng đối với nền kinh tế quốc dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','11', 'Ngày 26-11-1963, dòng điện của Nhà máy điện Uông Bí (Quảng Ninh) đã hoà nhập với lưới điện miền Bắc nước ta.\nNhà máy này được xây dựng từ giữa nǎm 1961, có công suất 48.000 kilôoat.\nCuối nǎm 1973, Nhà máy điện Uông Bí được Nhà nước tuyên dương là đơn vị Anh hùng lao động. Đến cuối nǎm 1977, Nhà máy hoàn thành việc xây dựng mở rộng, nâng công suất lên 153.000 kilôoát')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','11', 'Ngày 26-11-1974, khởi công xây dựng cầu Thǎng Long bắc qua sông Hồng ở Hà Nội.\nSau gần 10 nǎm, đến tháng 5-1984, cây cầu này mới được xây dựng xong. \nPhần cầu chính trên sông dài 1688 mét gồm 15 nhịp thép - Nếu tính cả cầu dẫn thì phần đường sắt dài 5.500 mét, đường ôtô dài 3.000.\nCầu Thǎng Long là cây cầu lớn nhất và hiện đại nhất của Thủ đô Hà Nội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','11', 'Ngày 26-11-1979, Ban Bí thư Trung ương Đảng ra chỉ thị số 83CT/TW về việc phát thẻ Đảng viên.\nViệc phát thẻ Đảng viên là công tác quan trọng, có ý nghĩa giáo dục chính trị sâu sắc để nâng cao hơn nữa ý thức Đảng, tinh thần phấn đấu cách mạng, thúc đẩy cuộc \"vận động xây dựng Đảng vững mạnh và trong sạch\", đưa công tác đảng viên vào nền nếp, ngǎn ngừa kẻ địch và phần tử xấu chui vào Đảng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','11', 'Ngày 26-11-1982, khánh thành Xí nghiệp Liên hợp Giấy Vĩnh Phú, nay là Công ty Giấy Bãi Bằng ở Phong Châu tỉnh Phú Thọ.\nĐây là công trình hợp tác giữa Việt Nam và Thụy Điển, có thiết bị hiện đại, dây chuyền sản xuất khép kín.\nSản phẩm của Công ty Giấy Bãi Bằng gồm các loại giấy in, giấy viết, giấy photocopy, giấy kẻ ngang, giấy máy vi tính và giấy vở học sinh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','12', 'Ngày 26-12-1961, Nhà nước đã có quyết định đầu tiên về việc \"sinh đẻ có hướng dẫn\"\nTháng 5-1997, Thủ tướng Chính phủ đã ra quyết định lấy ngày 26-12 hàng nǎm làm ngày \"Dân số Việt Nam\" nhằm đẩy mạnh công tác tuyên truyền, vận động các tầng lớp nhân dân tham gia thực hiện chương trình dân số và kế hoạch hoá gia đình để góp phần nâng cao chất lượng cuộc sống của từng người, từng gia đình và toàn xã hội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','12', 'Ngày 26-12-1963, Nhà máy điện Thái Nguyên, một trong những hệ thống quan trọng của khu liên hiệp gang thép Thái Nguyên được khánh thành.\nĐây là nhà máy hoàn toàn cơ khí hoá, đốt lò bằng than phun, gồm 42 công trình lớn nhỏ. Công suất của nhà máy này là 24.000 kilôoát cung cấp điện cho khu gang thép, mỏ sắt Trại Cau và thành phố Thái Nguyên')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','12', 'Ngày 26-12-1971, quân và dân các tỉnh Thanh Hoá, Nghệ An, Quảng Bình cảnh giác cao, đã bắn rơi 5 máy bay Mỹ, bắt sống giặc lái. Trong số này có chiếc máy bay Mỹ thứ 100 do quân dân Hàm Rồng bắn rơi. Máy bay Mỹ đã đánh phá cầu Hàm Rồng hơn 500 trận, trút xuống đây hơn 7 vạn tấn bom đạn. Nhưng cầu Hàm Rồng vẫn hiên ngang vắt qua dòng sông Mã để các đoàn xe của ta đi qua, chi viện cho miền Nam tiền tuyến lớn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '26','12', 'Ngày 26-12-1972. Mỹ huy động 52 lần chiếc máy bay chiến thuật đánh khu vực Đông Anh (Hà Nội). Về ban đêm có 105 lần chiếc B52 với 120 lần chiếc máy bay chiến thuật yểm hộ đánh cùng lúc vào 3 khu vực: Hà Nội, Hải Phòng và Thái Nguyên. Ở Hà Nội, máy bay B52 huỷ diệt phố Khâm Thiên làm 473 người chết và bị thương, phá huỷ và làm hỏng nặng gần 2.000 ngôi nhà.\nQuân và dân ta anh dũng chiến đấu bắn rơi 10 máy bay Mỹ trong đó có 8 chiếc B52 (4 chiếc B52 rơi tại chỗ). \nHành động của Mỹ khi dùng B52 đánh vào khu dân cư là một tội ác dã man nhất, tàn bạo nhất, nhưng cũng cho thấy khả nǎng về sức mạnh của không lực Hoa Kỳ đã sụp đổ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','12', 'Alegiô Capơnti (Alejo Carpentie) - nhà vǎn lớn Cuba và châu Mỹ Latinh - sinh ngày 26-12-1904, ở thủ đô Lahabana.\nÔng theo học âm nhạc và kiến trúc, tham gia làm báo từ nǎm 18 tuổi. Vì ký tên vào bản tuyên bố chống chế độ độc tài nên ông bị bắt giam. Khi được tự do, ông xin cư trú tại Pari. Các tác phẩm chính của ông là các tiểu thuyết \"Những dấu ấn đã mất\" (1953), \"Thế kỷ ánh sáng\" (1962), \"Luận về phương pháp\" (1973), và một số công trình khảo cứu \"Âm nhạc Cuba\"(1946)...\nCác tác phẩm của ông thể hiện những sáng tạo tinh thần đặc sắc, gợi mở, đặc trưng cho nền vǎn học Mỹ Latinh hiện đại. Ông là một trong những đại diện ưu tú khơi mở và giới thiệu nền vǎn học Mỹ Latinh với thế giới, đồng thời là người đặt nền móng cho nền vǎn học Mỹ Latinh hiện đại.\nÔng mất nǎm 1890.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '26','12', 'Ngày 26-12-2004, một trận động đất mạnh nhất trong vòng 40 năm qua với cường độ tới 8,9 độ Rích-te xảy ra ở ngoài khơi đảo Xumatơra (Inđônêxia) đã gây ra những đợt sóng thần hoành hành suốt dọc bờ biển nhiều nước châu Á, làm hơn 230.000 người bị thiệt mạng tại các nước Ấn Độ, Indonesia, Sri Lanka, Thái Lan, Malaysia, Maldives, Bangladesh, Myanmar. Hàng chục nghìn người vẫn bị mất tích, trong đó có nhiều khách du lịch. Trận sóng thần này được các chuyên gia đánh giá là thảm hoạ thiên nhiên tồi tệ nhất trong lịch sử đương đại.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','01', 'Từ ngày 19 tháng 12 - 1946, quân dân Hà Nội chiến đấu bảo vệ từng cǎn nhà, từng góc phố, chống lại sự xâm lược của quân đội Pháp. Nhân dịp tết Đinh Hợi 1947, ngày 27 tháng giêng nǎm đó, Hồ Chủ tịch đã gửi thư cho Trung đoàn Thủ đô. Người viết:\n\"Cùng các chiến sĩ Trung đoàn Thủ đô yêu quí! Các em là đội quyết tử. Các em quyết tử để cho Tổ Quốc quyết sinh. Các em là đại biểu cái tinh thần tự tôn tự lập của dân tộc ta mấy nghìn nǎm để lại, cái tinh thần quật cường đó đã kinh qua Hai Bà Trưng, Lý Thường Kiệt, Trần Hưng Đạo, Lê Lợi, Quang Trung, Phan Đình Phùng, Hoàng Hoa Thám, truyền lại cho các em. Nay các em gan góc tiếp tục cái tinh thần bất diệt đó, để truyền lại cho nòi giống Việt Nam muôn đời về sau.\nCác em hǎng hái tiến lên, lòng Già Hồ, lòng Chính phủ và lòng toàn thể đồng bào luôn luôn ở bên cạnh các em\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','01', 'Từ ngày 27-1 đến 4-2-1954 đã diễn ra chiến dịch Bắc Tây Nguyên. Toàn bộ chiến địch, quân ta tiêu diệt 8 cứ điểm lớn, trong đó có vị trí kiên cố nhất là Mǎng Đen, bức rút 6 vị trí khác, diệt 2000 sinh lực địch. Quân ta giải phóng toàn tỉnh Kon Tum rộng lớn gồm 16.000 km2 và 20 vạn dân. Thị xã Kon Tum giải phóng ngày 5-2-1954.\nChiến thắng tạo điều kiện cho bộ đội địa phương ở tỉnh Bình Định, Phú Yên, Khánh Hoà phối hợp tích cực chiến đấu phá chiến dịch Átlǎng của địch, tiêu diệt nhiều sinh lực địch.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','01', 'Vào lúc 11 giờ (giờ Pari) ngày 27-1-1973, hiệp định về chấm dứt chiến tranh, lập lại hoà bình ở Việt Nam đã được chính thức ký kết tại Trung tâm các hội nghị quốc tế ở Pari, giữa các Bộ trưởng ngoại giao thay mặt chính phủ Việt Nam dân chủ cộng hoà, Chính phủ cách mạng lâm thời Cộng hoà miền Nam Việt Nam, Hoa Kỳ và chính quyền Sài Gòn. Hiệp định nêu rõ: Hoa Kỳ và các nước khác tôn trọng độc lập, chủ quyền thống nhất, toàn vẹn lãnh thổ của nước Việt Nam. Hoa Kỳ chấm dứt sự dính líu về quân sự và can thiệp vào công việc nội bộ của miền Nam Việt Nam, tôn trọng quyền tự quyết và bảo đảm các quyền tự do dân chủ của nhân dân miền Nam. Nhân dân miền Nam Việt Nam tự quyết định tương lai chính trị của mình, thông qua tổng tuyển cử thật sự tự do và dân chủ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','01', 'Ngày 27-1-1992, vườn quốc gia Bến En (ở huyện Như Xuân, tỉnh Thanh Hoá) thành lập.\nBến En có hồ chứa nước rộng tới 4.000 hécta, sâu hàng chục mét. Trên mặt nước nhô lên 24 hòn đảo lớn nhỏ. Các đảo có rừng cây, hoa lá, nhiều giống chim thú sống tự nhiên và do người chǎn thả, nuôi dưỡng.\nBến En không những là khu bảo tồn các nguồn gien động vật, thực vật hiếm quý, phục vụ cho nghiên cứu khoa học, mà còn là một khu tham quan, du lịch, nghỉ mát hấp dẫn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','01', 'Phạm Phú Thứ, sinh ngày 27-1-1820, quê ở tỉnh Quảng Nam, qua đời nǎm 1880. Nǎm 23 tuổi đã đỗ tiến sĩ, làm quan dưới triều nhà Nguyễn đến chức Thượng thư.\nNǎm 1865, ông đi sứ sang Pháp làm Phó trưởng đoàn của phái đoàn do Phan Thanh Giản dẫn đầu để thương lượng chuộc 3 tỉnh miền Đông Nam Kỳ.\nSau khi ở Pháp về, nhiều lần ông tâu xin nhà vua cải cách việc chính trị, giáo dục của nước ta. Ông còn xin phép in, xuất bản những sách khoa học thường thức của phương Tây để đưa vào dạy ở các trường học. Đáng tiếc là phần lớn những đề nghị của ông đều bị thế lực bảo thủ trong triều đình cự tuyệt.\nVǎn thơ và các sớ tấu của Phạm Phú Thứ hiện còn được lưu giữ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','01', 'Đmitri Ivanovich Menđêlêev - nhà hoá học lớn người Nga, sinh ngày 27-1-1834.\nNǎm 1869, ông phát minh ra Định luật tuần hoàn Menđêlêev. Trên cơ sở đó ông đã xây dựng hệ thống bảng tuần hoàn các nguyên tố hoá học gọi là bảng tuần hoàn Menđêlêev. Ông đã hệ thống hoá những chi thức tản mạn về hiện tượng đồng hình, nhờ đó đã phát triển môn địa hoá học. Ông phát hiện độ sôi tối hạn, xây dựng thuyết Hđrát hoá của dung dịch. Ông đề xuất thuyết nguồn gốc vô cơ của dầu mỏ. Ông còn nghiên cứu chế tạo thuốc súng không khói, nghiên cứu khí tượng học, hoàn thiện kỹ thuật đo lường, đề xuất phương pháp khai thác dầu mỏ và nhiều quy trình sản xuất hóa chất. Ông mất ngày 20-1-1907.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','01', '27-1-1967, hơn 60 nước đã ký Hiệp ước cấm sản xuất và lưu hành vũ khí hạt nhân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('1', '27','02', 'Ngày Thầy thuốc Việt Nam')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','02', '27/2 /1932: Uỷ ban trung ương của Quốc tế cộng sản gửi thư cho các đảng cộng sản ở Pháp, Trung Quốc và Ấn Độ, yêu cầu họ giúp đỡ và hợp tác với các phong trào cách mạng ở Đông Dương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','02', 'Ngày 27/2 /1973, một hội nghị quốc tế về Việt nam được tổ chức ở Paris. Tham gia hội nghị có đại diện 4 bên kí kết Hiệp định Pari về Việt Nam: Chính phủ Việt Nam dân chủ cộng hòa; Chính phủ cách mạng lâm thời cộng hòa miền nam Việt Nam; Chính quyền Sài Gòn; Chính phủ Mỹ; và các nước ở nhiều khu vực trên thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','02', 'Nhà vǎn Nguyễn Tử Siêu sinh ngày 27-2-1887 ở tỉnh Hà Tây và qua đời nǎm 1965 ở Hà Nội \nÔng còn dạy học và nghiên cứu đông y. Lúc đầu ông viết loại truyện tâm lý xã hội sau chuyển sang viết tiểu thuyết lịch sử như: Tiếng sấm đêm đông, Vua Bố Cái, Lê Đại Hành, Đinh Tiên Hoàng, Việt - Thanh chiến sử, Trần - Nguyên chiến kỷ, Vua bà Triệu Ẩu, Hai bà đánh giặc, những cuốn tiểu thuyết ấy làm sống lại những trang sử vẻ vang chống xâm lǎng của dân tộc, khêu gợi lòng yêu nước, niềm tự hào về quá khứ chói lọi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','02', 'Ivan Petrôvich Pavlốp, nhà sinh lý học, thầy thuốc, nhà tư tưởng lỗi lạc Nga và thế giới ở thế kỷ XX. Ông sinh ngày 26-9-1849. Trong hơn 60 nǎm hoạt động khoa học ông đã có những phát hiện quí báu về sinh lý học và y học. Nǎm 1904 ông được tặng giải thưởng Nôben dành cho các công trình nghiên cứu của ông về phản xạ có điều kiện và hệ thống tín hiệu thứ, tức ngôn ngữ, ông đã chứng minh rằng ý thức con người được hình thành trên cơ sở vật chất.\nÔng qua đời ngày 27-2-1936, để lại cho nhân loại một di sản khoa học khổng lồ. Đó là hàng trǎm công trình nghiên cứu khoa học lớn nhỏ, nó không chỉ đặt nền móng cho ngành sinh học hiện đại mà góp phần to lớn cho mọi lĩnh vực nghiên cứu khoa học tự nhiên khác.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','02', 'Henri Uốtduốt Loongphelâu (HeryWadswonth Longfellow), nhà thơ Mỹ, sinh ngày 27-2-1807. Ông là nhà thơ nổi tiếng thông thạo hàng chục ngoại ngữ và cổ ngữ châu Âu.\nSáng tác của ông gồm nhiều thể loại: Thơ, tiểu thuyết, tiểu luận, kịch, bút ký... Các sáng tác trộn lẫn nhiều khuynh hướng và phong cách, lãng mạn và hiện thực, bác học và dân gian, tình cảm và giáo luận. Tập truyện thơ nổi tiếng nhất của ông là Bài ca về Hainôthơ (1855). Ông có công trong việc dịch thuật và giới thiệu nhiều nền thơ ca của các dân tộc khác với dân chúng Mỹ. Ông còn biên soạn bộ sách đồ sộ gồm 31 tập mang tự đề Thơ ca mọi miền. Ông qua đời ngày 24-3-1882.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','03', 'Sau khi Quốc tế 2 phản bội, tình hình thế giới đòi hỏi giai cấp công nhân phải thành lập tổ chức Cách mạng của mình. \nNgày 27-3-1919, tại Mátxcơva, Lênin đã chủ trì Đại hội thành lập Quốc tế Cộng sản (Quốc tế 3) có sự tham dự của đại diện 30 Đảng cộng sản và các tổ chức cánh tả trên thế giới với khẩu hiệu: \"Vô sản tất cả các nước và các dân tộc bị áp bức, liên hiệp lại\". Nǎm 1920, tại Đại hội Tua của Đảng xã hội Pháp, đồng chí Nguyễn Ái Quốc cùng một số đảng viên khác đã tán thành gia nhập Quốc tế 3 và Người trở thành một trong những người sáng lập Đảng Cộng sản Pháp. Quốc tế Cộng sản ra đời đã trở thành chỗ dựa tinh thần quan trọng cho Cách mạng Việt Nam. Đảng Cộng sản Đông Dương là một chi bộ của Quốc tế Cộng sản. Đồng chí Lê Hồng Phong là ủy viên Trung ương Quốc tế Cộng sản.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','03', 'Từ ngày 27 đến 31-3-1935 tại Ma Cao (Trung Quốc), 13 đại biểu đại diện cho 600 đảng viên thuộc các Đảng bộ trong nước và các tổ chức Đảng ở nước ngoài đã tiến hành Đại hội đại biểu toàn quốc lần thứ nhất của Đảng Cộng sản Đông Dương. Đại hội đã phân tích tình hình quốc tế và xác định nhiệm vụ của Đảng là: Củng cố phát triển Đảng, thâu phục quảng đại quần chúng, chống chiến tranh đế quốc, ủng hộ Liên Xô và cách mạng Trung Hoa. Đại hội cũng thông qua nghị quyết về vận động công nhân, nông dân và thanh niên... Ban Chấp hành Trung ương Đảng đã được bầu gồm các đồng chí Hà Huy Tập, Phùng Chí Kiên, Hoàng Đình Giang... Đại hội lần thứ nhất mang ý nghĩa lịch sử quan trọng là đã khôi phục lại hệ thống tổ chức Đảng từ Trung ương xuống cơ sở. Thống nhất sự chỉ đạo chuẩn bị điều kiện cho phong trào Cách mạng Đông Dương chuyển sang giai đoạn mới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','03', 'Nhằm thúc đẩy hoạt động báo chí cách mạng và tập hợp lực lượng báo chí tiến bộ thành một mặt trận thống nhất, Đảng Cộng sản Đông Dương đã tích cực vận động tiến tới thành lập một tổ chức các nhà báo tiến bộ đấu tranh cho quyền tự do ngôn luận và các quyền dân chủ khác.\nNgày 27-3-1937, trong khuôn khổ một cuộc họp công khai, Hội nghị báo giới Trung Kỳ đã khai mạc tại Đông pháp lữ quán 1 - Huế với 70 đại biểu của các báo Trung Kỳ và đại biểu báo chí cách mạng Bắc Kỳ. (Đồng chí Võ Nguyên Giáp đại biểu cho báo Rassemblement và đồng chí Hà Huy Giáp của báo Tiếng Trẻ). Hội nghị đã kêu gọi lập một mặt trận thống nhất của những người làm báo Đông Dương và yêu cầu được tự do xuất bản và thành lập Hội Ái hữu báo giới Trung Kỳ. Sự kiện này đã mở đầu cho cuộc vận động mở hội nghị báo giới ở các địa phương khác.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','03', 'Ngày 27-3 hằng nǎm được nước ta chọn là \"Ngày Thể thao Việt Nam\".\nNgày này cách đây 52 nǎm, 27-3-1946, Chủ tịch Hồ Chí Minh đã viết một bài nhan đề \"Sức khoẻ và thể dục\" đǎng trên báo Cứu Quốc - Người viết:\n\"Giữ gìn dân chủ, xây dựng nước nhà, gây đời sống mới, việc gì cũng cần có sức khoẻ mới làm thành công. Mỗi một người dân yếu ớt, tức là cả nước yếu ớt, mỗi một người dân khoẻ mạnh tức là cả nước mạnh khoẻ.\nVậy nên luyện tập thể dục, bồi bổ sức khoẻ là bổn phận của mỗi một người yêu nước. Việc đó không tốn kém, khó khǎn gì. Gái trai, già trẻ ai cũng nên làm và ai cũng làm được. Mỗi ngày lúc ngủ dậy, tập một ít thể dục - ngày nào cũng tập thì khí huyết lưu thông, tinh thần đầy đủ, như vậy là sức khoẻ\" - Bác Hồ viết tiếp: \"Dân cường thì quốc thịnh - Tôi mong đồng bào ta ai cũng gắng tập thể dục - Tự tôi, ngày nào tôi cũng tập\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','03', 'Ngày 27-3-1964 Hồ Chủ tịch triệu tập Hội nghị chính trị đặc biệt tại Hội trường Ba Đình. Đây là một hội nghị Diên Hồng của thời đại mới. Người đã báo cáo với Hội nghị những thành tích to lớn của nhân dân ta trong mười nǎm qua, về tình hình hiện tại và nhiệm vụ trước mắt.\n\"Sự nghiệp cách mạng là một sự nghiệp lâu dài và gian khổ, song nhất định thắng lợi. Mọi người chúng ta bất kỳ ai làm việc gì, ở cương vị nào, đều phải là những chiến sĩ dũng cảm của sự nghiệp vẻ vang ấy\".\nNgười kêu gọi \"Mỗi người làm việc bằng hai để đền đáp lại đồng bào miền Nam ruột thịt\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','03', 'Đại hội Đại biểu lần thứ 5 của Đảng Cộng sản Việt Nam họp từ ngày 27-3-1982 tại Thủ đô Hà Nội.\nĐại hội khẳng định toàn Đảng, toàn dân phải ra sức phấn đấu làm tốt hai nhiệm vụ chiến lược: Xây dựng thành công chủ nghĩa xã hội, và sẵn sàng chiến đấu, bảo vệ vững chắc Tổ quốc Việt Nam xã hội chủ nghĩa. Đại hội đề ra 4 mục tiêu kinh tế và xã hội tổng quát của những nǎm 80. Nội dung chính của công nghiệp hoá - xã hội chủ nghĩa trong chặng đường trước mắt là tập trung phát triển nông nghiệp, coi nông nghiệp là mặt trận hàng đầu. Đại hội quyết định đổi mới cơ chế quản lý kinh tế và kế hoạch hoá, thực hiện làm chủ ở ba cấp: Trung ương, địa phương và cơ sở.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','03', 'Ngày 27-3 là Ngày Quốc tế sân khấu. Lịch sử xã hội phát triển loài người ghi nhận loại hình nghệ thuật sân khấu ra đời từ rất lâu. Nó bao gồm các thể loại kịch và cả nghệ thuật múa rối. Ở châu Âu kịch nói phát triển mạnh, đặc biệt ở vào thế kỷ XVI với tên tuổi của Sếchxpia (người Anh) và thế kỷ XVIII với Sinlơ (người Đức). Các tác phẩm ở thời kỳ này thường mang đậm chất bi xen lẫn hùng, như Hǎm lét, Âm mưu và tình yêu...\nĐối với Việt Nam, nghệ thuật hát chèo là nghệ thuật truyền thống đối với dân tộc. Nó vừa độc đáo vừa gần gũi trong quần chúng nhân dân. Được phổ biến ở vùng châu thổ sông Hồng. Hiện nay nghệ thuật sân khấu Việt Nam đã phong phú bởi nhiều loại hình khác nhau cùng cấu thành bộ môn sân khấu Việt Nam hiện đại và đậm đà bản sắc dân tộc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','04', 'Ngày 27-4-1904 Toàn quyền Đông Dương ra nghị định thiết lập chương trình giáo dục hệ Pháp - Việt ở Bắc Kỳ. Chương trình này chủ yếu sử dụng tiếng Pháp. Mục đích nhằm loại bỏ nền \"Hán học\" ở Bắc Kỳ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','04', 'Hội Lao động giải phóng miền Nam Việt Nam ra đời vào ngày 27-4-1961 và gia nhập Mặt trận DTGPMNVN. Về sau, Hội đổi tên là \"Liên hiệp Công đoàn giải phóng miền Nam Việt nam\". Hội ra lời kêu gọi gửi anh chị em công nhân lao động toàn miền Nam, trong cả nước và lao động thế giới nhân dịp ngày Quốc tế Lao động 1.5. Hưởng ứng lời kêu gọi của Hội, công nhân trong các xí nghiệp, đồn điền sôi nổi đấu tranh đòi quyền lợi kinh tế, các quyền tự do dân chủ. Nhiều cuộc mít tinh kỷ niệm ngày 1.5 được tổ chức.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','04', 'Hồ Chủ tịch đã đến thǎm trường mẫu giáo Sao Sáng ở nhà số 5 phố Nguyễn Thượng Hiền, Hà Nội, ngày 27-4-1962.\nBác bế một cháu nhỏ nhất trên tay, hôn cháu. Các cháu quây quần bên bác. Cháu nào cũng muốn được Bác bế, được ngồi gần Bác để hôn lên má Bác, sờ râu Bác. Rồi các cháu lại thi nhau kể chuyện cho Bác nghe. Bác cǎn dặn các cháu phải biết giữ vệ sinh, phải vâng lời cô giáo, đi nắng phải đội mũ để giữ gìn sức khoẻ.\nCác cháu còn múa và hát. Bác khen các cháu múa đẹp, hát hay, và cho quà từng cháu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','04', 'Ngày 27-4-1969 Miền Bắc sôi nổi bầu cử HĐND huyện, xã và các cấp tương đương. 99,08% tổng số cử tri đã đi bầu cử để chọn ra đại biểu của 298 HĐND khu phố, huyện, thị xã, 5851 HĐND xã, thị trấn.\nHồ Chủ tịch đã đi bầu cử HĐND khu phố Ba Đình tại hòm phiếu số 6 thuộc đơn vị 4 tiểu khu 1 đặt tại nhà Thuyền Hồ Tây. Đây là lần cuối cùng Hồ Chủ tịch đi bầu cử HĐND các cấp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','04', '27-4-1972, quân ta mở cuộc tiến công đợt 2 vào khu vực phòng ngự của địch ở Đông Hà, Ái Tử, La Vang.\nSau 5 ngày chiến đấu, ta đã tiêu diệt cụm Đông Hà - Ái Tử, giải phóng toàn tỉnh Quảng Trị và Động Tranh, trực tiếp uy hiếp địch ở Thừa Thiên - Huế.\nĐây là lần đầu ta giải phóng Quảng Trị. Sau đó, địch tái chiến và đến nǎm 1975 Quảng Trị lại được giải phóng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','05', 'Ngày 27-5-1936, Đảng bộ nhà tù Côn Đảo phát động cuộc đấu tranh đòi thả tù chính trị. Đại diện cho các tù nhân tạm giam tại nhà tù Côn Đảo, đồng chí Phạm Hùng đã trao cho bọn giám ngục yêu sách của cuộc đấu tranh đòi thực hiện \"đại xá tù chính trị\" mà Chính phủ nhân dân Pháp đã đề ra. Cuộc đấu tranh đã tập hợp hơn một ngàn tù nhân tham gia và bất chấp mọi thủ đoạn khủng bố của kẻ thù. Cuộc đấu tranh kéo dài và sau ba ngày tuyệt thực của tù nhân, bọn thực dân đã phải lùi bước. Hơn 200 chiến sĩ Cách mạng đã lần lượt trở về đất liền. Cuộc đấu tranh có tiếng vang lớn và nhận được sự ủng hộ rộng rãi của nhân dân cả nước cũng như của các lực lượng tiến bộ nước Pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','05', 'Tại kỳ họp thứ 10 quốc hội khoá 1 ngày 27-5-1959 đã ra quyết định đưa nông thôn miền Bắc đi theo con đường hợp tác hoá nông nghiệp tiến lên chủ nghĩa xã hội.\nQuốc hội nhận định: đó là khâu chính trong toàn bộ công cuộc cải tạo xã hội chủ nghĩa ở miền Bắc và nhất trí tán thành những quy định về mục đích, yêu cầu, đường lối giai cấp, phương châm, nguyên tắc, những chính sách cụ thể về phương pháp hoạt động hợp tác hoá nông nghiệp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','05', 'Điều lệ phạt vi cảnh đã được Chính phủ ban hành ngày 27-5-1977. Điều lệ gồm 33 điều quy định những hành vi phạm pháp vi cảnh, quyền hạn phạt vi cảnh và quyền khiếu nại của người bị phạt.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','05', 'Từ ngày 27-5-1994, ngành điện lực nước ta bắt đầu vận hành đường dây tải điện 500 kilôvôn Bắc Nam. Công suất truyền tải điện cao nhất đạt 630 mêgaóat, góp phần chấm dứt tình trạng thiếu điện trầm trọng ở miền Trung và miền Nam trước đây. Về lâu dài, hệ thống tải điện 500 kilôvôn sẽ tạo điều kiện thuận lợi cho việc khai thác tối ưu các nhà máy điện trong cả nước và mở ra khả nǎng mới để hợp tác, trao đổi nǎng lượng trong tương lai với các nước láng giềng, nhất là các nước trong lưu vực sông Mê Công.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','05', 'Giaoaháclan Nêru (Neghru Jawaharlan) sinh nǎm 1889 và từ trần ngày 27-5-1964.\nÔng là nhà hoạt động chính trị nổi tiếng, lãnh tụ của phong trào giải phóng dân tộc Ấn Độ - Gia nhập Đảng Quốc đại (nǎm 1912), là Tổng thư ký (nǎm 1929) rồi trở thành Chủ tịch của Đảng này. Nǎm 1946, ông tham gia thành lập Chính phủ lâm thời Ấn Độ, giữ chức Phó Thủ tướng kiêm Bộ trưởng Ngoại giao. Từ nǎm 1947, Ấn Độ tuyên bố độc lập, ông giữ chức Thủ tướng.\nG.Nêru là một trong những người sáng lập \"Phong trào không liên kết\", là người bạn lớn thân thiết của nhân dân Việt Nam, đã ủng hộ chúng ta trong cuộc kháng chiến chống thực dân Pháp và đế quốc Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','06', 'Ngày 27-6-1908, anh em bồi bếp và binh lính người Việt thuộc Trung đội Công nhân pháo thủ Hà Nội đã tổ chức đầu độc binh lính Pháp đóng trong thành để phối hợp với các lực lượng nghĩa quân được bố trí ở bên ngoài theo một kế hoạch trước, nhằm tiên công đánh chiếm thành Hà Nội. Lịch sử gọi đây là vụ \"Hà thành đầu độc\".\nCuộc bạo động diễn ra như sau: Trong bữa ǎn tối, toàn bộ binh lính Pháp thuộc Trung đoàn pháo binh và Trung đoàn bộ binh đóng trong Thành bị anh em bồi bếp và binh lính người Việt dùng cà độc đầu độc. Song anh em chưa kịp bắn súng hiệu cho nghĩa quân bên ngoài thì đã bị thực dân Pháp tước hết vũ khí, tống giam. Một số chiến sĩ trong vụ này bị xử chém.\nTuy cuộc mưu chiếm lại thành Hà Nội không thành, nhưng vụ \"Hà thành đầu độc\" đã làm chấn động dư luận. Bon thực dân Pháp ở Hà Nội rất hoảng sợ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','06', 'Ngày 27-6-1927 Tỉnh bộ Hà Nội của Thanh niên Cách mạng đồng chí Hội ra đời tại nhà số 11 phố Tô Hiến Thành, Hà Nội. Ngôi nhà này còn là nơi in tài liệu. Khi Đảng Cộng sản Đông Dương được thành lập, báo \"Búa liềm\" của Đảng mỗi kỳ phát hành vài trǎm số đều in ở đây cùng với nhiều truyền đơn khác.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','06', 'Ngày 27-6-1950, Tổng thống Hoa Kỳ Truman tuyên bố tǎng cường viện trợ quân sự Pháp trong cuộc chiến tranh ở Việt Nam, bao gồm việc gửi các đoàn cố vấn quân sự và cung cấp vũ khí. Khoản viện trợ đầu tiên trị giá 15 triệu đô la. Từ đây, Mỹ trực tiếp can thiệp vào cuộc chiến tranh xâm lược ở Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','06', 'Ngày 27-6-1968 Quân và dân miền Bắc bắn rơi chiếc máy bay thứ 3000 của giặc. Chủ tịch Hồ Chí Minh gửi thư khen toàn thể đồng bào, chiến sĩ và cán bộ: \"Nam Bắc hai miền đều đánh giỏi! Quân và dân miền Bắc dũng cảm không những bắn rơi nhiều máy bay Mỹ mà thắng cả Mỹ trên các mặt giao thông vận tải, xây dựng kinh tế, giữ vững đời sống nhân dân... Qua hơn ba nǎm chống Mỹ cứu nước, miền Bắc xã hội chủ nghĩa càng thêm vững vàng và luôn luôn hết lòng làm tròn nghĩa vụ đối với đồng bào Việt Nam ruột thịt\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','06', 'Ngày 27-6-1950, Hội đồng Bảo an Liên hiệp quốc, không có mặt Liên Xô, đã ra Nghị quyết mở đầu cho việc Mỹ và đồng minh của họ can thiệp vào nội chiến ở Triều Tiên.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','06', 'Ngày 27-6-1954, nhà máy điện nguyên tử đầu tiên trên thế giới ở Liên Xô được đưa vào hoạt động.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('1', '27','07', 'Ngày Thương binh Liệt sĩ')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','07', 'Ngày 27-7-1947, đại biểu của tổng bộ Việt Minh. Trung ương Hội Phụ nữ cứu quốc, Trung ương Đoàn thanh niên cứu quốc, Cục Chính trị quân đội Quốc gia Việt Nam, Nha thông tin tuyên truyền và một số địa phương đã tập họp tại Đại Từ (Bắc Thái) để bàn về công tác thương binh, liệt sĩ và thực hiện chỉ thị của Hồ Chủ tịch chọn một ngày làm ngày \"Thương binh\". Sau khi cân nhắc về nhiều mặt, Hội nghị nhất trí đề nghị Trung ương lấy ngày 27-7-1947 làm ngày \"Thương binh toàn quốc\". Chiều ngày 27-7-1947, một cuộc mít tinh quan trọng tổ chức tại huyện Đại Từ (Bắc Thái) có 2000 người tham gia. Ban Tổ chức ngày \"Thương binh toàn quốc\" đã trịnh trọng đọc thư của Hồ Chủ tịch. Trong thư Người cho biết đã ủng hộ một chiếc áo lụa của Hội Phụ nữ gửi biếu Người, một tháng lương và một bữa ǎn trưa của các nhân viên trong Phủ Chủ tịch. Từ nǎm 1947, ngày \"Thương binh\" đã được tổ chức trọng thể thường kỳ hàng nǎm. Sinh thời nǎm nào vào dịp này, Hồ Chủ tịch đều có thư và quà gửi cho anh em thương binh và các gia đình liệt sĩ. Tháng 7-1954, sau thắng lợi Điện Biên Phủ, Đảng và Nhà nước ta đã quan tâm giải quyết vấn đề liệt sĩ, gia đình liệt sĩ và công tác thương binh. Trên ý nghĩa đó, từ nǎm 1955 ngày \"Thương binh toàn quốc\" được đổi thành ngày \"Thương binh liệt sĩ\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','07', 'Những tội ác chiến tranh tàn bạo của Đế quốc Mỹ đã làm bùng lên những đợt sóng mạnh mẽ và rộng rãi trên thế giới ủng hộ Việt Nam, chống đế quốc Mỹ xâm lược, kiên quyết đòi Mỹ chấm dứt chiến tranh xâm lược, rút không điều kiện toàn bộ quân Mỹ về nước.\nTrong bối cảnh đó, ngày 20-7-1972, Hội nghị các Đảng Cộng sản và công nhân châu Âu đoàn kết với nhân dân Việt Nam đã họp tại Pari, Dự hội nghị có 27 đoàn đại biểu, trong đó có các đoàn đại biểu các nước xã hội chủ nghĩa Đông Âu, các đoàn đại biểu các Đảng Cộng sản và công nhân các nước Tây Âu và Bắc Âu. Hội nghị đã thông qua một bản tuyên bố quan trọng về vấn đề Việt Nam, kịch liệt lên án chính quyền Níchxơn, xâm lược miền Nam Việt Nam và leo thang chiến tranh chống nước Việt Nam Dân chủ cộng hòa, gây ra nhiều tội ác cực kỳ tàn bạo đối với nhân dân Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','07', 'Lécmôntốp là nhà thơ, nhà viết kịch, nhà tiểu thuyết Nga nổi tiếng. Ông sinh ngày 15-10-1814 trong một gia đình quý tộc. Ông có tưởng tượng tự do, có ý thức sâu sắc về sứ mạng của nhà thơ trước vận mạng của nhân dân. Các bài thơ của ông gieo hy vọng vào lòng dân tộc như: \"Cái chết của nhà thơ\", \"cách buồm\", \"Người tù\", \"Vĩnh biệt nước Nga dơ bẩn\". Đặc biệt tiểu thuyết nổi tiếng \"Người anh hùng của thời đại chúng ta\" là một đỉnh cao của nền vǎn xuôi Nga.\nThơ vǎn Lécmôntốp cháy bỏng lời hiệu triệu đấu tranh, đem lại niềm tin và tình thương yêu nhân loại. Thơ ông là một trong những tiếng thơ trữ tình nồng thắm của nền vǎn học cổ điển vĩ đại và trường tồn của nước Nga. Ông bị sát hại trong một cuộc đấu súng do âm mưu của những kẻ vốn cǎm ghét nhà thơ vào ngày 27-7-1841.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','07', 'Ngày 27-7-1866, cáp điện báo vượt Đại Tây Dương đầu tiên đã được lắp đặt thành công.\nPhinđơ, người Mỹ, là người khởi xướng ra công trình này. Ông dành toàn bộ tài sản của mình cho công việc đó.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','07', 'Hiệp định đình chiến ở Triều Tiên đã được ký kết tại Bàn-môn-điếm ngày 27-7-1953. Các bên tham gia ký kết hiệp định gồm có: Quân đội Nam Triều Tiên và Quân đội Mỹ, Quân đội Bắc Triều Tiên và Chí nguyện quân Trung Quốc. Theo Hiệp định này, tất cả các hoạt động quân sự ở đất nước Triều Tiên đều đình chỉ, giới tuyến được ấn định là vĩ tuyến 38. Uỷ ban giám sát việc thi hành hiệp định được thành lập bao gồm: Ba Lan, Tiệp Khắc, Thụy Sĩ và Thụy Điển.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','08', 'Chính phủ đã cho phép ngành Bưu điện in và phát hành bộ tem bưu chính đầu tiên của nước ta, theo sắc lệnh số 172/SL ngày 27-8-1946.\nBộ tem đầu tiên này in chân dung Chủ tịch Hồ Chí Minh, gồm 5 mẫu, màu và giá khác nhau. Tem in trên giấy tàu bạch. Hoạ sĩ Nguyễn Sáng đã thiết kế, thể hiện thành công những đường nét đặc sắc, khắc hoạ được hình ảnh Bác Hồ, người khai sinh ra nước Việt Nam dân chủ cộng hoà.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','08', 'Ngày 27-8-1995, bắt đầu xây dựng nhà máy xi mǎng Bút Sơn (tỉnh Hà Nam).\nKhi hoàn thành, nhà máy có thể sản xuất 1,4 triệu tấn xi mǎng một nǎm. Nhà máy xi mǎng Bút Sơn là công trình đầu tiên thực hiện theo phương thức: ta tự huy động vốn các nguồn, tự chịu trách nhiệm về xây dựng và áp dụng quy chế đấu thầu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','08', 'Lope Felix Carpio là nhà thơ, tác giả sân khấu thiên tài người Tây Ban Nha. Ông sinh nǎm 1562, đời tư nhuốm nhiều bi kịch.\nNgoài các tác phẩm thơ ca và vǎn xuôi gồm 21 tập được viết nhiều thể loại như trường ca thôn dã, trường ca hài hước, thần thoại, phiêu lưu tiểu thuyết, truyện ngắn... ông còn viết 1.800 vở kịch. Về thơ ca, đáng kể có \"Thời đại hoàng kim\". Thơ trữ tình của ông phát huy và nâng cao truyền thống thơ ca dân gian và một bước tiến của thơ ca Tây Ban Nha. Ông trở thành người sáng lập ra nền sân khấu dân tộc, qua cuốn sách lý luận sân khấu: \"Bàn về những nguyên tắc mới của nghệ thuật hài kịch trong thời đại chúng ta\". Kịch của ông gần gũi với người dân, đề cao trí thông minh, lòng dũng cảm chống lại áp bức bóc lột, như các vở: \"Ngôi sao thành Xêvida\", \"Con chó người làm vườn\", \"Trừng phạt không trả thù\"... Cuộc đời và sự nghiệp của ông có ảnh hưởng đến cuộc đấu tranh của các tầng lớp tiến bộ cũng như sự phát triển vǎn học Tây Ban Nha. Ông mất ngày 27-8-1635.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','08', 'Phơriđrich Hêghen - triết gia nổi tiếng người Đức, người đề xướng ra phép biện chứng trong sự phát triển sự vật, sinh ngày 27-8-1770. Ông học triết học tại Chủng viện Thần học của Hội Giáo hội Tin lành. Sau khi tốt nghiệp, ông dạy đại học.\nCác tác phẩm nổi tiếng của ông là: \"Khoa học của lôgic\", \"Cơ sở triết học\", \"Từ điển Bách khoa các khoa học về triết học\", \"Nguyên lý triết học của luật pháp\", \"Mỹ học\", \"Lôgic\"... Với các tác phẩm của mình, tên tuổi ông được truyền tụng trong các giờ giảng về triết học. Triết học của ông đã có ảnh hưởng lớn đến nền vǎn hoá thế giới ở thế kỷ 19 và 20. Tư tưởng triết học của ông phân định rõ hai mặt: Phép biện chứng phát triển trên cơ sở duy tâm và hệ thống triết học duy tâm khách quan. Phép biện chứng của ông là mặt tiến bộ trong triết học, là một thành quả vĩ đại của triết học cổ điển Đức. Mác và Ǎngghen đã tiếp thu có phê phán phép biện chứng duy tâm của Hêghen, đề ra phép biện chứng duy vật.\nNǎm 1831, dịch tả lan tràn nước Đức. Hêghen đã mắc bệnh và qua đời ngày 14-11 nǎm đó.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','08', 'Lơ Coócbuýtsiê là nhà kiến trúc lỗi lạc, nhà quy hoạch thành phố và lý luận kiến trúc hiện đại. Ông sinh nǎm 1887, người gốc Thuỵ Sĩ, nhưng gần như cả đời làm việc ở Pháp và nhiều nước trên thế giới.\nTừ những nǎm 20 của thế kỷ này, ông hào hứng nghiên cứu quy hoạch thành phố: \"Thành phố hiện đại\" (1922); \"Thành phố tươi sáng\" (1935), và mặt bằng thành phố Alger. Ông chủ trương kiến trúc và quy hoạch thành phố phải phụ thuộc vào yêu cầu sử dụng và tuân thủ chức nǎng thực tiễn. Trên tinh thần đó, ông đã hoàn thành các đồ án cho nhiều đất nước như: Phương án Cung Xô Viết ở Mátxcơva, 1928; Bảo tàng nghệ thuật hiện đại Pari, 1935; Nhà bảo tàng Tôkyô; trụ sở Liên hợp quốc ở Niu Oóc York 1952.\nLơ Coócbuýtsiê không những là nhà kiến trúc, nhà quy hoạch mà còn là nhà xã hội học, nhà vǎn, nhà thơ trữ tình, hoạ sĩ, nhà điêu khắc. Tài nǎng của ông được đánh giá như nghệ thuật phục hưng Italia. Ông qua đời ngày 27-8-1965.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','09', 'Nhận thấy sự tan rã của bộ máy chính quyền thực dân Pháp sau khi phát xít Nhật nhảy vào Đông Dương, Đảng bộ Bắc Sơn phát động quần chúng nổi dậy cướp chính quyền.\n20 giờ ngày 27-9-1940, hơn 600 quân khởi nghĩa gồm đủ các dân tộc Tày, Dao, Nùng, Kinh đã tấn công đồn Mỏ Nhài (Châu lỵ Bắc Sơn). Sau khi chiếm lỵ, Ban chỉ huy khởi nghĩa tuyên bố xoá bỏ chính quyền cũ. Các ngày 28 và 29-9 quân khởi nghĩa tiếp tục tấn công vào đánh tan quân Pháp ở đèo Canh Tiêm, Sập Di. Thực dân Pháp cùng phát xít Nhật thỏa hiệp với nhau đàn áp cuộc khởi nghĩa, đưa quân chiếm lại các đồn. Đồng chí Trần Đăng Ninh được Xứ ủy cử lên lãnh đạo phong trào và hướng cuộc nổi dậy vào mục tiêu xây dựng lực lượng chiến đấu lâu dài. Giữa tháng 10-1940, Ban chỉ huy khu căn cứ Bắc Sơn được thành lập và quyết định thành lập Du kích Bắc Sơn vào 13-10-1940. Ngày 28-10 quần chúng Cách mạng tổ chức mít tinh ở Vũ Lăng.\nCuộc khởi nghĩa Bắc Sơn tuy thất bại nhưng để lại bài học quý báu về khởi nghĩa vũ trang, xây dựng chính quyền, đặt nền móng cho việc xây dựng căn cứ địa cách mạng. Đội du kích Bắc Sơn sau này phát triển thành Việt Nam Cứu quốc quân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','09', 'Ngày 27-9-1950, tổ điệp báo A13 của công an Hà Nội đã phối hợp với công an Thanh Hóa để tiến hành một nhiệm vụ quan trọng.\nNhiệm vụ này được giao cho các đồng chí Hoàng Đạo, Kim Sơn, Nguyễn Thị Lợi và Chu Duy Kính. Với tinh thần dũng cảm và mưu trí, họ đã làm nổ tung chiếc tàu chiến Amiô Đanhvin trên vùng biển Sầm Sơn, 200 lính và sĩ quan Pháp cùng hàng trăm tấn vũ khí bị tiêu diệt và phá huỷ. Chị Nguyễn Thị Lợi đã hy sinh - chị được Nhà nước truy tặng Huân chương Quân công hạng ba và danh hiệu \"Anh hùng lực lượng vũ trang nhân dân\".\nVới chiến công này, ta đã đập tan âm mưu của thực dân Pháp và bọn tay sai muốn xây dựng \"chiến khu quốc gia\", đánh chiếm vùng tự do Thanh - Nghệ Tĩnh, phá tan mưu đồ lôi kéo, mua chuộc những người kháng chiến \"li khai\" chính phủ Việt Minh, trở về với chính phủ quốc gia \"bù nhìn\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','09', 'Ngày 27-9-1975 tại Mêhicô, Tổ chức Du lịch thế giới ra đời. Tổ chức Du lịch thế giới (OMT) luôn cố gàng làm cho các quốc gia hiểu biết nhau, gần nhau và duy trì mối quan hệ quốc tế tốt đẹp. Tổ chức đã thực hiện những chương trình hành động bao gồm việc nghiên cứu các đề tài du lịch, hướng dẫn nghiệp vụ du lịch, cấp học bổng đào tạo cán bộ du lịch cho các nước thành viên...\nNgành Du lịch Việt Nam đã trở thành thành viên chính thức của Tổ chức Du lịch thế giới từ năm 1981.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','09', 'Tại kỳ thi kiến trúc quốc tế thứ tư tổ chức tại Xôphia (Bungari) ngày 27-9-1987, đồ án kiến trúc \"Tồn tại hay không tồn tại\" của một nhóm kiến trúc sư trẻ Việt Nam đã được giải thưởng lớn và Huy chương vàng.\nBản đồ án kiến trúc lấy ý từ câu triết lý sâu sắc của Hămlet, nhân vật bi kịch của Sếchxpia, đưa ra giải pháp về nhà ở đối với người dân chài ở miền Trung nước ta. Cuối năm 1987, nhóm 4 kiến trúc sư Vũ Anh Tuấn, Vũ Văn Tân, Lê Thị Kim Dung và Nguyễn Bắc Vũ lại giành giải nhất tại kỳ thi quốc tế về kiến trúc ở Ba Lan với đồ án \"Không gian Ali Baba\" đưa ra giải pháp nhà ở tại khu 36 phố phường Hà Nội, \"giúp nó tồn tại như một quá khứ thuộc về tương lai\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','09', 'Ngày 27-9-1989, Chính phủ ta đã quyết định thành lập Uỷ ban quốc gia phòng nạn mù chữ. \nUỷ ban này có nhiệm vụ chỉ đạo công cuộc thống nạn mù chữ cho người lớn tuổi và phổ cập giáo dục tiểu học cho trẻ em trong độ tuổi.\nVới sự tham gia của 12 ngành, đoàn thể, Uỷ ban quốc gia chống nạn mù chữ chỉ đạo một mạng lưới rộng khắp từ tỉnh đến huyện, quận, xã, phường trong cả nước, tiếp tục truyền thống của bình dân học vụ, bổ túc văn hóa trong mấy chục năm qua, cùng với cộng đồng quốc tế quyết tâm thực hiện mục tiêu thế giới bước vào thế kỷ XXI không còn nạn mù chữ do UNESCO khuyến cáo và Liên hiệp quốc phát động.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','10', 'Nguyễn Trung Trực là lãnh tụ cuộc khởi nghĩa nhân dân chống Pháp ở Long An, Rạch Giá, sinh năm 1837 ở Tân An.\nNăm 1861, hưởng ứng hịch Cần Vương, ông chiêu mộ nhân dân nổi dậy đánh phá các đồn Pháp ở Tân An, tổ chức phục kích đốt tàu Hy Vọng của Pháp trưa 10-12-1861 trên sông Nhật Tảo. Sau đó, ông rút quân về Phú Quốc lập căn cứ. Tháng 10-1968, để bảo toàn lực lượng trước sự bao vây của Pháp, ông đã tự nộp mình cho giặc. Thống soái Nam Kỳ lúc đó dụ hàng nhưng ông trả lời: \"Thưa Pháp soái, chúng tôi chắc rằng chừng nào ngài trừ hết cỏ trên mặt đất thì chừng đó ngài mới may ra trừ diệt được những người ái quốc của xứ sở này\".\nGiặc Pháp đem ông ra hành hình ở chợ Rạch Giá ngày 27-10-1868. Lúc đó ông mới 31 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','10', 'Ngày 27-10-1929, Ban Chấp hành Quốc tế cộng sản gửi thư cho các nhóm cộng sản ở Đông Dương về vấn đề thành lập Đảng cộng sản Đông Dương.\nBức thư phân tích sẵn mâu thuẫn của xứ Đông Dương, cao trào Cách mạng trên đường quốc tế. Vấn đề thành lập Đảng cộng sản Đông Dương, bí thư chỉ rõ: \"Nhiệm vụ quan trọng hơn hết và tuyệt đối cần kíp của tất cả những người cộng sản Đông Dương là sáng lập một Đảng Cách mạng của giai cấp vô sản... ở Đông Dương thì chỉ có đảng ấy là tổ chức cộng sản mà thôi\". Bức thư còn đề ra phương pháp thống nhất các nhóm cộng sản, Phương thức thành lập Ban Chấp hành, phương thức hoạt động, sự liên lạc và giúp đỡ các Đảng anh em, sản xuất sách báo bí mật của Đảng, tổ chức bộ máy liên lạc bí mật của Đảng...\nBức thư của Quốc tế cộng sản là một văn kiện góp phần quan trọng vào việc thống nhất các tổ chức cộng sản để thành lập Đảng ta ngày 3-2-1930.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','10', 'Ngày 27-10-1967, Chủ tịch đoàn Chủ tịch Uỷ ban Mặt trận Trung ương giải phóng miền Nam gửi thư tới Hồ Chủ tịch. Trong thư, đồng chí Nguyễn Hữu Thọ cảm ơn Hồ Chủ tịch và chính phủ nước Việt Nam dân chủ cộng hoà đã ủng hộ cương lĩnh chính trị của Mặt trận, và đồng chí đã khẳng định: \"Quân và dân miền Nam quyết trả thù cho đồng bào Thủ đô và cả nước, cùng miền Bắc ruột thịt tiến lên đánh thắng hoàn toàn giặc Mỹ xâm lược, thống nhất Tổ quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','10', 'Ngày 27-10-1994, Thủ tướng Ítxraen Y.Rabin và thủ tướng Gioócđam A.S.Magiali đã chính thức ký hiệp ước hoà bình giữa hai nước, chấm dứt 46 năm hận thù. Đây là chặng thứ ba đi tới hoà bình ở Trung Đông, sau Hiệp định Ítxraen - Ai Cập (1978) và Hiệp định Israel - PLO (1993)')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','11', 'Cụ Nguyễn Sinh Sắc (là thân sinh của Chủ tịch Hồ Chí Minh) sinh nǎm 1862 tại làng Kim Liên, huyện Nam Đàn, tỉnh Nghệ An, từ trần ngày 27-11-1929.\nCụ đỗ phó bảng và làm quan trong triều Nguyễn. Vốn có tinh thần yêu nước, khảng khái cụ không phục tùng bọn thực dân Pháp nên bị cách chức. Cụ vào sinh sống ở thị xã Cao Lãnh, tỉnh Đồng Tháp. Dù bị mật thám Pháp theo dõi, cụ vẫn liên lạc với những người yêu nước chống Pháp. Hàng ngày cụ xem mạch, kê đơn thuốc, chữa bệnh cho đồng bào, được bà con rất quý trọng.\nTại phường 4, thị xã Cao Lãnh, còn ngôi mộ cụ Phó bảng Nguyễn Sinh Sắc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','11', 'Ngày 27-11-1961 Thủ tướng Phạm Vǎn Đồng ký Nghị định \"Phê chuẩn các phương án chữ Tày - Nùng, chữ Thái và chữ Mèo\". Từ đây, đồng bào các dân tộc Tày, Nùng, Thái, Mèo có chữ viết riêng của dân tộc mình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','11', 'Alếch xǎnngđrơ Douyma (Alexandare Dumas) (con) là nhà viết tiểu thuyết, kịch gia Pháp sinh ngày 28-7-1824.\nCác tác phẩm của ông mang tính phê phán hiện thực nhằm cải tạo phong tục cũ nát của giai cấp tư sản. Ông được coi là một trong những người đổi mới sân khấu hiện đại Pháp. \nÔng mất ngày 27-11-1895. Tiểu thuyết \"Trà hoa nữ\" của ông được mọi người rất ưa thích.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','11', 'Ngày 27-11-1843, Bain, người Anh đã đǎng ký phát minh \"Phương pháp thu được các ảnh ở xa nhờ điện\".\nHệ thống tái tạo một ảnh cố định ở xa là hệ thống báo trước của máy truyền ảnh và truyền hình.\nNǎm 1924, phòng thí nghiệm Bell đã truyền bằng điện những tấm ảnh từ xa.\nĐến nǎm 1925, nhà nghiên cứu Pháp Bơlanh đã chế ra loại máy truyền ảnh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','11', 'Iugin Glátxtâu Âu Nin (Eugènc Gladtone O\\'NEILL) một trong những nhà viết kịch lớn nhất của Mỹ, sinh ngày 16-10-1888.\nNǎm 1963, ông được tặng giải thưởng Noben vǎn học với vở \"Con khỉ lông lá\". Các tác phẩm của ông phản ánh trật tự xã hội tư bản suy tàn, bênh vực người lao động, và ông có những tác phẩm đầu tiên trong nền vǎn học Mỹ lên án chủ nghĩa phân biệt chủng tộc. Ông qua đời ngày 27-11-1953.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','12', 'Nhà vǎn Doãn Kế Thiện sinh ngày 27-12-1891, quê ở huyện Quốc Oai, tỉnh Hà Tây, mất nǎm 1965.\nÔng đã gắn bó cả tuổi trẻ với sự nghiệp với cố đô Thǎng Long - Hà Nội. Ông còn làm thơ, viết vǎn, viết báo, và là nhà Hà Nội học đầu tiên của Thủ đô.\nVới các bút danh Tú Sơn, Long Thành, Sơn Vân, Sở Bảo, nhà vǎn Doãn Kế Thiện còn để lại nhiều tác phẩm: Hà nội cũ (1943), Cổ tích và thắng cảnh Hà Nội (1959), Danh nhân Việt Nam, cùng nhiều các bài báo bài thơ, dịch thơ Đường, thơ Tống. Toàn bộ sáng tác của ông toát lên cái nhìn của một nhà nho, một nhân sĩ trí thức đi theo Cách mạng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '27','12', 'Ngày 27-12-1996, Thủ tướng Chính phủ đã phê duyệt \"Tổng thể đô thị khu công nghiệp và cảng bến nước sâu Chân Mây\" (ở tỉnh Thừa Thiên - Huế)\nVịnh này có lợi thế để xây dựng một cảng nước sâu phục vụ cho việc vận chuyển hàng hoá giữa các tỉnh Quảng Bình, Quảng Trị và Thừa Thiên - Huế. Nằm trong cụm cảng vùng trọng điểm miền Trung, cảng Chân Mây còn đóng vai trò điều phối khối lượng hàng hoá chu chuyển thích hợp cho vùng Trung Bộ. Dự án xây dựng khu đô thị khu công nghiệp và cảng bến nước Chân Mây được xem là một đột phá khẩu trong phát triển kinh tế - xã hội của Thừa Thiên - Huế, đưa tỉnh này vươn lên hoà nhập cùng quá trình phát triển chung của đất nước.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','12', 'Iôhannét Kêplơ (Johannes Kepler) - Nhà thiên vǎn nổi tiếng Đức, là một trong những người đặt nền móng cho thiên vǎn học hiện đại - sinh ngày 27-12-1571.\nÔng đi sâu nghiên cứu hình học, thiên vǎn học và sáng tác ra một loại sách lịch, trong đó có bàn về chiêm tinh. Ông đã chứng minh được ba định luật quan trọng, sau này mang tên ông, về sự chuyển động của hành tinh. Ông viết các cuốn: \"Tân thiên vǎn\", \"Sự hoà hợp của vũ trụ\", có giá trị khoa học cao. Ông thiết lập ngành quang hình học, hoàn thiện khoa giải phẫu mắt.\nÔng mất ngày 15-11-1630.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','12', 'Lu-i Paxtơ (Louis Pasterur) sinh ngày 27-12-1822, qua đời nǎm 1895.\nLà tiến sĩ vật lý và hoá học người Pháp, ông trở nên nổi tiếng khi công bố một báo cáo khoa học về tinh thể học.\nTừ nǎm 1862, ông tập trung nghiên cứu vi sinh vật. Các nghiên cứu của ông về bệnh dại đã gây nên những cuộc bút chiến mạnh mẽ và dẫn tới việc điều chế được vắc xin phòng chống bệnh dại.\nViện nghiên cứu về vệ sinh dịch tễ ở thủ đô Pari đã được mang tên ông. Từ Viện này, trên khắp châu lục đã hình thành các viện Paster quốc gia. Công lao và cống hiến khoa học của ông giúp cho loài người tránh được một số bệnh nan y.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '27','12', '27-12-1950, Mỹ và Tây Ban Nha nối lại quan hệ kể từ cuộc nội chiến Tây Ban Nha những nǎm 1930.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','01', 'Ngày 28-1-1941, lãnh tụ Nguyễn Ái Quốc vượt qua cột mốc 108 ở biên giới Việt Nam - Trung Hoa, trở về Tổ quốc sau 30 nǎm hoạt động cách mạng ở nước ngoài. Người về ở làng Pác Bó, huyện Hà Quảng, tỉnh Cao Bằng. Tại đây, Người đã bắt tay vào việc chuẩn bị Hội nghị Ban Chấp hành Trung ương Đảng lần thứ VIII.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','01', 'Từ ngày 28-1 đến ngày 10-2-1954 quân đội ta phối hợp với bộ đội Phathét Lào mở chiến dịch Thượng Lào (gồm có Nậm Hu, Mường Khoa, Phong-xa-lì, Mường Sài nhằm đánh lạc hướng địch, tưởng ta bỏ ý định tấn công Điện Biên Phủ, đồng thời bao vây chia cắt Điện Biên Phủ với Thượng Lào, tạo điều kiện giải phóng một vùng đất đai quan trọng trên đất Lào.\nKết thúc chiến dịch Thượng Lào, địch bị diệt 17 đại đội (có 1 tiểu đoàn Âu Phi bị diệt gọn), ta giải phóng tỉnh Phong-xa-lì. Phòng tuyến Nậm Hu, \"con đường liên lạc chiến lược\" của địch với Điện Biên Phủ bị đập tan. Cǎn cứ kháng chiến của nhân dân Lào được mở rộng gồm 10 nghìn kilômét vuông, khu giải phóng Sầm Nưa của bạn nối liền với vùng Tây Bắc của ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','01', 'Sáng ngày 28 tháng Chạp nǎm Bính Thân (tức là ngày 28-1-1957) đoàn đại biểu xã Nhật Tân huyện Từ Liêm đã mang cây đào đẹp nhất đến biếu Hồ Chủ tịch.\nSau khi thǎm hỏi việc chuẩn bị đón tết, đời sống nhân dân trong xã, Bác Hồ cǎn dặn \"ǎn tết phấn khởi nhưng phải nhớ tiết kiệm\". Bác tặng mỗi người một thiếp chúc mừng nǎm mới và nói: \"Xin cảm ơn các cụ và các đồng chí đã đem tặng tôi cây đào để tôi vui tết. Nhưng sang nǎm, thôi không đem tặng tôi nữa, tôi sẽ tự trồng cây đào này để có hoa chơi tết\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','01', 'Ngày 28-1-1977, Uỷ ban Thường vụ Quốc hội nước Cộng hoà xã hội chủ nghĩa Việt Nam đã quyết định đặt Huân chương Hữu nghị để tặng thưởng cho những tổ chức và cá nhân nước ngoài đã có công giúp nhân dân ta trong sự nghiệp xây dựng và bảo vệ Tổ quốc xã hội chủ nghĩa Việt Nam và phát triển tình hữu nghị với nhân dân Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','01', 'Guyxtavơ Epphen - Kỹ sư người Pháp đã thiết kế và chỉ đạo xây dựng tháp Epphen. Công trình được khởi công ngày 28-1-1887, đến ngày 31-3-1889 (tức là sau 2 nǎm 2 tháng 5 ngày) thì xây dựng xong. Tháp cao 320 mét, trọng lượng toàn bộ của tháp là 9.700 tấn. Tháp Epphen đã trở thành biểu tượng của thủ đô Pari - một kỳ quan thực sự.\nGuyxtavơ Epphen còn là tác giả của rất nhiều công trình kim loại ở nhiều nước, đặc biệt là các cây cầu. Người ta gọi ông là \"Thuật sĩ của vật liệu sắt\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','01', 'Hôxê Mácti sinh ngày 28-1-1853, từ trần ngày 19-5-1895. Ông là nhà vǎn và anh hùng dân tộc Cuba. Nǎm 15 tuổi đã tham gia chống thực dân Tây Ban Nha xâm lược nước Cuba. Ông đã bị bắt và bị đày ra nước ngoài hai lần. Nǎm 1895, ông trở về nước, lại chỉ huy nghĩa quân chiến đấu, rồi tử trận. Những bài báo và thơ vǎn của ông đều tập trung vào chủ đề cuộc đấu tranh giải phóng, cảnh giác với nguy cơ đế quốc Mỹ. Tác phẩm nổi tiếng nhất của ông là tập \"Thơ tự do\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','02', '28-2-1949 - mở đầu chiến dịch Lao - Hà (Lao Cai - Hà Giang).\nĐể phối hợp với hướng chính ở Đông Bắc, ngày 18-3 ta tiêu diệt vị trí phố Lu (Lào Cai) và 11 vị trí xung quanh Hoàng Xu Phì (Hà Giang). Toàn bộ chiến dịch từ 28-2-1949 đến 18-3-1949 ta đã giải phóng được một vùng rộng lớn, cô lập Hoàng Xu Phì. Mở rộng thêm 3000 km2 vùng cǎn cứ địa.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','02', 'Ngày 28-2-1962, địch mở chiến dịch lớn đánh vào vùng U Minh Hạ (Cà Mau) nhằm dồn 60 ngàn dân vào \"Ấp chiến lược\". Quân dân U Minh đã dùng đấu tranh vũ trang và đấu tranh chính trị để chống lại. Hàng ngày có từ 300 đến 500 người kéo trụ sở tề quận, đồn bốt, tố cáo tội ác của địch. Đi đôi với đấu tranh chính trị, ta đã tấn công diệt những nơi địch sơ hở như Đầm Dơi, Cái Nước, bức rút 4 cǎn cứ khác. Tính chung, suốt thời gian chiến dịch, địch bị đánh 725 trận, chết 572 tên, bị thương 558 tên.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','02', 'Từ ngày 28-2 đến 20-3-1969 Đoàn đại biểu Mặt trận dân tộc giải phóng miền Nam Việt Nam do bác sĩ Phùng Vǎn Cung, Phó chủ tịch Đoàn chủ tịch Uỷ ban Trung ương Mặt trận dân tộc giải phóng miền Nam dẫn đầu ra thǎm miền Bắc. Trong tình nghĩa ruột thịt Bắc Nam, nhân dân Thủ đô Hà Nội và toàn miền Bắc vô cùng sung sướng và nhiệt liệt hoan nghênh Đoàn đại biểu Mặt trận dân tộc giải phóng miền Nam, những đại biểu yêu quý của tiền tuyến lớn anh hùng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','02', 'Ngày 28-2-1977, Tổng cục Hàng không dân dụng Việt Nam đã ra quyết định thành lập Sân bay quốc tế Nội Bài ở huyện Sóc Sơn, Hà Nội.\nĐến đầu nǎm 1978, sân bay chính thức mở cửa hoạt động. Hiện nay sân bay có các nhà ga khách quốc tế đi, đến, khách trong nước và một nhà khách VIP.\nSân bay quốc tế Nội Bài có tổng diện tích hơn 10.000 mét vuông, bảo đảm nǎng lực thông qua khoảng 1,5 triệu hành khách một nǎm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','02', 'Lainớt Pôlinh (Linu Pauling), nhà hoá học vĩ đại Mỹ, sinh ngày 28-2-1901. Ông là một trong những nhà khoa học đầu tiên sử dụng công cụ mới đó là tinh thể học tia X để xác định cấu trúc phân tử. Công trình của ông về \"bản chất của các liên kết hoá học kể cả những khái niệm về cộng hưởng và lai tạo\" đã làm thay đổi tận gốc bộ môn hoá học. Với công trình về \"liên kết hoá học\", ông được tặng giải Noben hoá học 1954.\nÔng còn là một chiến sĩ đấu tranh cho hoà bình - cấm vũ khí hạt nhân, chống chiến tranh. Vì vậy ông được tặng giải Noben hoà bình 1962. Và ông cũng là người độc nhất từ xưa đến nay nhận 2 giải thưởng Noben mà không phải chia xẻ với ai. Ông qua đời ngày 19-8-1994.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','03', 'Ngày 28-3-1967, Tổng Thư ký Liên hiệp quốc U Than đề xuất một thoả ước ngừng bắn toàn diện tại Việt Nam sau các cuộc hội đàm hoà bình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','03', 'Vǎn hào Nga Macxim Gorki là người mở đầu trào lưu vǎn hoá xã hội lỗi lạc. Chủ tịch đầu tiên của hội Nhà vǎn Liên Xô. Ông sinh ngày 28-3-1868. Thời thơ ấu ông đã phải làm nhiều việc để kiếm sống và tự học, tự trang bị cho mình một vốn kiến thức vǎn hoá đa dạng, đặc biệt là vǎn học Nga và phương Tây. Sau Cách mạng tháng Mười ông đảm nhiệm nhiều trọng trách trong lĩnh vực vǎn hoá - xã hội.\nSáng tác của Gorki gồm nhiều thể loại. Nổi tiếng nhất là Tự thuật ba tập: \"Thời thơ ấu\", \"Kiếm sống\" và \"Những trường đại học của tôi\". Nét đặc biệt xuyên suốt sáng tác của ông là mô tả con người với tấm lòng đầy nhân ái và trân trọng kêu gọi con người hãy có hành động cao thượng, xứng đáng. Gorki đã kết hợp được truyền thống phân tích xã hội một cách nghiêm ngặt của vǎn học hiện thực phương tây thế kỷ XIX với chủ nghĩa lãng mạn mới để sáng lập ra trào lưu vǎn học xã hội chủ nghĩa - một trào lưu vǎn học lớn nhất và đặc trưng cho thế kỷ XX.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','03', 'Guglielmo Marconi là người Italia, sinh nǎm 1874 và ông qua đời 1937.\nÔng đã nghiên cứu các sóng điện tử và sau một nǎm thử nghiệm ở ngôi biệt thự của gia đình, ở Bologne nǎm 1895, lúc 21 tuổi, ông đã truyền thành công buổi liên lạc bằng vô tuyến điện trong khoảng cách 2400 mét.\nNgày 28-3-1899, Marconi đã thực hiện buổi phát điện báo đầu tiên giữa hai địa điểm cách nhau 50 kilômét.\nSau đó, ngày 12-12-1901, với sự hợp tác của Fleming (người Anh), Marconi đã tiến hành buổi liên lạc đầu tiên bằng điện báo vô tuyến qua Đại Tây Dương ở 2 địa điểm cách nhau 3400 kilômét.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','04', 'Ngày 28-4-1956, thực dân Pháp buộc phải rút quân khỏi miền Nam nước ta, sau buổi lễ hạ cờ Pháp trước phủ Cao Uỷ ở Sài Gòn chấm dứt vĩnh viễn chế độ thực dân cũ và cuộc xâm lược kéo dài 98 nǎm trên đất nước Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','04', 'Để củng cố quốc phòng, giữ gìn hoà bình, bảo vệ thành quả cách mạng và sự nghiệp lao động; nhằm phát huy truyền thống của nhân dân, nâng cao ý thức quốc phòng, phổ cập ý thức quân sự trong nhân dân; ngày 28-4-1960 Chủ tịch Hồ Chí Minh đã ban hành sắc lệnh công bố Luật nghĩa vụ quân sự.\nLuật nghĩa vụ quân sự đã đáp ứng được sự mong mỏi của nhân dân lao động để bảo vệ Tổ quốc. Luật đã xác định: \"Nghĩa vụ quân sự là nghĩa vụ vẻ vang của công dân Việt Nam đối với Tổ quốc\".\nNhiều nǎm nay việc thực hiện Luật nghĩa vụ quân sự đã trở thành quyền lợi và nghĩa vụ của mỗi người dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','04', 'Ngày 28-4-1964, Hồ Chủ tịch đã về thǎm Nhà máy bóng đèn phích nước Rạng Đông (nay là Công ty Rạng Đông).\nThực hiện lời cǎn dặn của Bác, Công ty đã sản xuất bóng đèn và phích nước có chất lượng cao, có thể cạnh tranh với hàng cùng loại của nước ngoài, được người tiêu dùng tín nhiệm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','04', 'Ngày 28-4-1975, một biên đội gồm 5 máy bay A37 do phi công Nguyễn Vǎn Lục chỉ huy, phi công Nguyễn Thành Trung (người đã ném bom dinh Độc lập ngày 8 tháng 4) dẫn đường cất cánh từ sân bay thành sơn (Phan Rang) bay về phía Sài Gòn. Vượt qua mạng lưới ra đa cảnh giới của địch, biên đội đã ném bom chính xác vào khu vực để máy bay ở sân bay Tân Sơn Nhất, phá huỷ 24 máy bay diệt 200 binh lính và nhân viên ngụy trực chiến ở sân bay. Tiếng bom nổ ở Tân Sơn Nhất làm rung chuyển cả thành phố Sài Gòn. Quân địch kinh hoàng vì bất ngờ. Pháo cao xạ và không quân của chúng không kịp phản ứng. Việc di tản bằng máy bay có cánh cố định từ sân bay Tân Sơn Nhất bị tê liệt. Mỹ buộc phải tổ chức \"Chiến dịch di tản liều mạng\" bằng máy bay lên thẳng. Bộ đội không quân anh hùng đã lập thêm một chiến công làm rạng rỡ truyền thống của quân chủng, dùng máy bay lấy được của địch đánh địch. Phi đội máy bay lập chiến công ngày 28-4 được mang tên \"Phi đội quyết thắng\".\nCùng ngày 28-4-1975, ta đã giải phóng Bà Rịa.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','04', 'Ngày 28-4-1984, ta khởi công xây dựng công trình chính tuyến đập và tuyến nǎng lượng của Nhà máy thuỷ điện Trị An trên sông Đồng Nai.\nNhà máy này đã được xây dựng xong nǎm 1990, có công suất 420MW, sản lượng điện 1 tỷ 700 triệu KW giờ một nǎm.\nNhà máy thuỷ điện Trị An là nhà máy lớn thứ hai của nước ta hiện nay, sau Nhà máy thuỷ điện Hoà Bình trên Sông Đà.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','04', 'Môrisơ Tôsê (Maurice Thoez) sinh ngày này nǎm 1900, qua đời nǎm 1964.\nÔng là nhà hoạt động chính trị của nước Pháp và của phong trào cộng sản, công nhân quốc tế. Tháng 3-1919, ông vào Đảng Xã hội Pháp và tích cực vận động để thành lập Đảng Cộng sản Pháp. Ông đã giữ nhiều chức trách trong ĐCS Pháp: Uỷ viên Trung ương Đảng (nǎm 1924), Uỷ viên Bộ Chính trị (nǎm 1925), Tổng Bí thư Đảng (từ nǎm 1930 đến nǎm 1964).\nMôrisơ Tôsê là người bạn thân thiết của nhân dân Việt Nam. Cùng với Đảng Cộng sản Pháp, ông kịch liệt lên án cuộc chiến tranh xâm lược của thực dân Pháp ở Việt Nam và Đông Dương, ủng hộ nhân dân ta trong cuộc kháng chiến chống Pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','04', 'Ngày 28-4-1975, Quốc hội Mỹ đã công bố những con số quân Mỹ bị thương vong ở Việt Nam như sau:\n- 56.245 lính bị chết, 303.640 lính bị thương, 1.350 lính mất tích\n- 15% quân số nghiện ma tuý.\n- 3 vạn lính đào ngũ chạy ra nước ngoài, 25 vạn thanh niên không chịu đǎng ký nghĩa vụ quân sự.\nSau chiến tranh xâm lược Việt Nam, 70 vạn binh lính và sĩ quan mắc bệnh thần kinh, tự sát hoặc trốn ra sống trong rừng. Hậu quả chiến tranh còn ảnh hưởng cả một thế hệ thanh niên Mỹ trong lứa tuổi từ 20 đến 25 gọi là \"thế hệ sau Việt nam\" và ảnh hưởng sâu sắc tới tâm lý xã hội Mỹ, tạo thành \"Hội chứng Việt Nam\". 26% cựu chiến binh Mỹ sau khi từ Việt Nam trở về sử dụng thuốc phiện.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','05', 'Vào ngày 28-5-1840 một trí tuệ, tài nǎng Việt Nam xứng đáng gọi là nhà bác học của dân tộc: Phan Huy Chú - đã qua đời.\nÔng quê ở Hà Tĩnh. Mặc dù chỉ đỗ hai lần tú tài, thực học, thực tài của ông vẫn được khẳng định. Nǎm 1821, Minh Mệnh cho mời ông vào Huế giữ chức Biên tu Hàn lâm viện. Sau đó ông được cử đi sứ sang Trung Quốc. Nǎm 1828, làm Phủ thừa tỉnh Thừa Thiên, rồi thǎng Hiệp trấn Quảng Nam. Sau lại đi sứ sang Trung Quốc và lúc về bị cách chức. Nhưng sau khi đi phục vụ một số đoàn Inđônêxia về ông được cử làm chức Tư vụ Bộ công, nhưng đã ngán cảnh quan trường, ông vin cớ đau yếu xin về dạy học cho đến lúc mất. \nĐứng về phương diện vǎn hoá, Phan Huy Chú có những cống hiến rất lớn ông đã để lại một loạt các tác phẩm: Hoàng Việt dư trí, Hải trình chí lược, Lịch đại điển yếu thông, Dương trình ký kiến... Đặc biệt bộ Lịch triều hiến chương loại chí đã nâng ông lên hàng nhà bác học. Cho mãi đến nay giới học thuật vẫn khen ngợi đó là công trình bách khoa.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','05', 'Cao Xuân Huy, giáo sư chuyên về lịch sử tư tưởng triết học Phương Đông sinh ngày 28-5-1900 tại Diễn Châu, Nghệ An.\nÔng xuất thân trong một gia đình nho học và làm quan nên từ bé đã hiếu học. Nǎm 1925 tốt nghiệp trường cao đẳng sư phạm Hà Nội sau đó đi dạy học ở Sài Gòn, Huế và chuyên nghiên cứu triết học. Sau Cách mạng Tháng Tám, ông làm hiệu trưởng trường trung học Nguyễn Xuân Ôn ở Nghệ An. Từ 1949-1959 ông phụ trách giảng dạy triết học cổ đại Phương Đông trong các trường ở vùng kháng chiến và các trường đại học ở Hà Nội. Nǎm 1958, ông được phong giáo sư, rồi Trưởng ban Vǎn học cổ đại Việt Nam, làm giáo sư chính lớp đại học Hán Nôm ở Hà Nội. Sau đó tiếp tục dạy các lớp chuyên tu sau đại học; đã đào tạo được một thế hệ trẻ có khả nǎng nghiên cứu Cổ học nước nhà.\nÔng đã để lại cho đời một số giáo trình cổ đại xuất sắc về Kinh dịch, Luận ngữ, Mạnh tử, Bách gia chư tử. Ông mất ngày 22-10-1983.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','05', 'Hơn 200 công nhân xưởng sửa chữa ôtô Avia (Hà Nội) đã bãi công dưới sự lãnh đạo của đồng chí Ngô Gia Tự và Công hội nhà máy vào ngày 28-5-1929, họ đòi chủ xưởng tǎng lương, không đánh đập, không đuổi công nhân đã tham gia bãi công.\nCuộc bãi công nhận được sự ủng hộ của công nhân các nhà máy diêm, nhà máy điện, nhà máy in (ở Hà Nội), nhà máy sợi Nam Định, nhà máy than ở Mạo Khê, nhà máy Carông, nhà máy chai (ở Hải Phòng) và của nông dân Thái Bình, Nam Định. Đến ngày 10-6-1929, cuộc bãi công của công nhân xưởng Avia kết thúc thắng lợi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','05', 'Ngày 28-5-1946, theo sáng kiến của Hồ Chủ tịch, Hội liên hiệp quốc dân Việt Nam gọi tắt là Liên Việt được thành lập để mở rộng khối đoàn kết toàn dân, bắt tay với các đảng phái chính trị và các nhân sĩ dân chủ và thân sĩ yêu nước.\nHội Liên Việt gồm mặt trận Việt Minh, các tố chức, các đảng phái và cá nhân yêu nước chưa tham gia mặt trận Việt Minh. Đảng Xã hội Việt Nam và hội Liên hiệp phụ nữ Việt Nam cùng tham gia Hội Liên Việt.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','05', 'Sau Cách mạng Tháng Tám, để bảo vệ chính quyền nhân dân - Quân đội quốc gia Việt Nam đã từng bước ổn định và củng cố. Đồng chí Võ Nguyên Giáp được giao nhiệm vụ là Tổng chỉ huy.\nNgày 28-5-1948 tại một địa điểm thuộc khu cǎn cứ địa Việt Bắc đã diễn ra lễ thụ phong Đại tướng cho đồng chí Võ Nguyên Giáp. Thay mặt chính phủ, Chủ tịch Hồ Chí Minh xúc động tuyên bố trước bàn thờ Tổ quốc: \"Nhân danh Chủ tịch nước VNDCCH trao cho chú chức vụ Đại tướng để chú điều binh khiển tướng làm tròn sứ mệnh mà quốc dân giao phó\". Buổi lễ giản dị mà trang nghiêm. Cũng trong dịp này một số vị lãnh đạo quân sự khác cũng được thụ phong cấp tướng như: Trung tướng Nguyễn Bình và các Thiếu tướng: Nguyễn Sơn, Lê Thiết Hùng, Hoàng Sâm, Hoàng Vǎn Thái, Chu Vǎn Tấn, Vǎn Tiến Dũng, Lê Hiến Mai, Trần Đại Nghĩa, Trần Tử Bình.\nCùng quân đội anh hùng, các tướng lãnh đầu tiên ấy đã xứng đáng với sự tin cậy của nhân dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','05', 'Ngày 28-5-1981, Viện Lịch sử quân sự Việt Nam và ngành lịch sử quân sự nước ta được thành lập.\nTừ khi thành lập, Viện đã tập trung vào nhiệm vụ tổng kết chiến tranh, viết lịch sử hai cuộc kháng chiến chống Pháp và chống Mỹ, nghệ thuật quân sự. Từ nǎm 1992, Viện đã thực hiện được những công trình cấp nhà nước.\nCác quân khu, quân chủng, binh chủng, quân đoàn, các học viện, nhà trường và hầu hết các tỉnh, thành phố đã có lịch sử địa phương và đơn vị. Toàn quân đã biên soạn được lịch sử hơn 800 trận đánh.\nViện Lịch sử quân sự còn được giao trách nhiệm làm trung tâm đào tạo sau đại học về lịch sử quân sự, chủ trì biện soạn giáo trinh thống nhất ba cấp về lịch sử quân sự cho các học viện, nhà trường trong quân đội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','06', 'Từ ngày 28-6 đến 27-7-1946, Nha Bình dân học vụ đã mở lớp đào tạo cán bộ bình dân học vụ cốt cán cho các dân tộc ít người. \n75 đại biểu của 14 dân tộc đã dự lớp đào tạo này.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','06', 'Ngày 28-6-1949 là ngày mở đầu chiến dịch ở Tây Nguyên. Lần đầu tiên quân ra mở một chiến dịch dài ngày ở cả 3 tỉnh Công Tum, Gia Lai, Đắc Lắc mà hướng chính là tỉnh Gia Lai. Qua chiến dịch ta đã gây được cơ sở quần chúng và phát động chiến tranh nhân dân trong các vùng đồng bào Thượng, như: dọc đường 21, M\\'đrắc, Cheo Reo, dọc đường 19, Công Tum, An Khê, Mang Giang, Gia Hội, Ca Nắc, Công Plông.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','06', 'Tháng 2-1951, Đại hội đại biểu lần thứ hai của Đảng Cộng sản Đông Dương quyết định tách Đảng thành 3 Đảng ở 3 nước Việt Nam, Lào, Cǎmpuchia để phù hợp với tình hình phát triển mới và việc lãnh đạo Cách mạng ở mỗi nước được sát hợp và thuận lợi. \nThi hành quyết định trên, ngày 28-6-1951 Ban cán sự Đảng bộ Cǎmpuchia đa triệu tập Đại hội thành lập Đảng nhân dân Cách mạng Khơme, thông qua Cương lĩnh, điều lệ và bầu lãnh đạo của Đảng. \nKế thừa truyền thống của Đảng nhân dân Cách mạng Khơme và Đảng nhân dân Cách mạng Cǎmpuchia, Đảng nhân dân Cǎmpuchia ngày nay giữ vững và phát huy tình cảm anh em 3 nước trên bán đảo Đông Dương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','06', 'Từ ngày 28-6 đến 1-7-1996, tại Hội trường Ba Đình (Hà Nội) đã tiến hành Đại hội đại biểu toàn quốc lần thứ 8 Đảng Cộng sản Việt Nam. \nVề dự có 1.198 đại biểu và một số đoàn khách quốc tế. Đại hội đã thảo luận và thông qua các vǎn kiện quan trọng: Báo cáo chính trị; Phương hướng, nhiệm vụ kế hoạch phát triển kinh tế - xã hội 5 nǎm (từ 1996 đến nǎm 2000); Điều lệ Đảng (có bổ sung, sửa đổi) và nghị quyết của Đại hội. \nCác đại biểu đã bầu Ban chấp hàng Trung ương Đảng khoá 8 gồm 170 uỷ viên, do đồng chí Đỗ Mười làm Tổng bí thư. Đại hội đại biểu toàn quốc lần thứ 8 Đảng cộng sản Việt Nam đánh dấu bước ngoặt, chuyển đất nước ta sang thời kỳ công nghiệp hoá hiện đại hoá, xây dựng nước Việt Nam độc lập, dân chủ giàu mạnh, xã hội công bằng vǎn minh theo định hướng xã hội chủ nghĩa.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','06', 'Giáo sư Nguyễn Đình Tứ sinh nǎm 1932 tại Hà Tĩnh, từ trần ngày 28-6-1996 tại Hà Nội. \nGiáo sư đã có nhiều cống hiến đối với sự nghiệp Cách mạng của Đảng và nhân dân ta, đặc biệt trong lĩnh vực nghiên cứu khoa học nǎng lượng nguyên tử. \nĐòng chí Nguyễn Đình Tứ đã từng giữ các chức vụ quan trọng: Chủ nhiệm Ủy ban Khoa học - Kỹ thuật của Quốc hội, Bộ trưởng Bộ Đại học và Trung học chuyên nghiệp, Trưởng ban Khoa giáo Trung ương, Bí thư Trung ương Đảng, Ủy viên Bộ Chính trị Ban Chấp hành Trung ương Đảng cộng sản Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','07', 'Ngày 28-7-1929 tại số nhà 15 Hàng Nón - Hà Nội, Hội nghị đại biểu Công hội đỏ Bắc Kỳ lần thứ nhất đã họp. Đồng chí Nguyễn Đức Cảnh, thay mặt ban trị sự lâm thời Bắc Kỳ lao động liên hiệp Tổng Công hội báo cáo về tình hình phong trào đấu tranh của gia cấp công nhân Việt Nam. Hội nghị quyết định thành lập Tổng Công hội đỏ Bắc Kỳ, thảo luận và thông qua Điều lệ và chương trình hoạt động của Hội, quyết định xuất bản tờ báo \"Lao động\" làm cơ quan tuyên truyền, và tạp chí \"Công hội đỏ\" làm cơ quan lý luận của Hội. Hội nghị bầu ra Ban Chấp hành lâm thời do đồng chí Nguyễn Đức Cảnh phụ trách. Tiếp sau đó các Tổng Công hội đỏ ở Trung Kỳ, Nam Kỳ được thành lập. Cuối 1929 Hội nghị đại biểu các Tổng Công hội đỏ địa phương đã quyết định thống nhất tổ chức và bầu ra Ban Chấp hành chính thức của Tổng Công hội đỏ Bắc Kỳ.\nTừ đó qua các thời kỳ cách mạng, Công hội đỏ có nhiều tên gọi khác nhau: Công hội đỏ (1929-1935), Nghiệp đoàn ái hữu (1939-1941), Công nhân cứu quốc (1941-1945), Tổng liên đoàn Lao động Việt Nam (từ 20-7-1946), Tổng công đoàn Việt Nam (từ 27-2-1961) và Tổng liên đoàn Lao động Việt Nam (từ 17-10-1988). Ngày 25-6-1983 Bộ Chính trị Ban Chấp hành Trung ương quyết định lấy ngày 28-7-1929, ngày họp Đại hội thành lập Công hội đỏ đầu tiên làm ngày thành lập Công đoàn Việt Nam. Đại Hội đại biểu công đoàn toàn quốc lần thứ 5 vào tháng 11-1983 đã nhất trí thông qua nghị quyết lấy ngày 28-7-1929 làm ngày thành lập Công đoàn Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','07', 'Nhà vǎn, Nhà sử học, nhà hoạt động Cách mạng Trần Huy Liệu. Ông sinh nǎm 1901 quê ở Vân Cát, Vụ Bản, Nam Định.\nTừ nǎm 1924 ông vào Nam cộng tác với một số báo và làm chủ bút tờ \"Đông Pháp thời báo\". Nǎm 1927, ông bị kết án tù vì có chân trong các tổ chức yêu nước. Trong tù ông tự nguyện đứng vào hàng ngũ những người cộng sản. Nǎm 1935, ông bị trục xuất ra Bắc. Nǎm 1939, lại bị bắt. Đến 3-1945, ông tham gia phá trại giam Nghĩa Lộ, vượt ngục trở về Hà Nội hoạt động. Nǎm 1945, ông được bầu làm Phó chủ tịch Uỷ ban dân tộc giải phóng. Rồi làm Bộ trưởng Bộ tuyên truyền, và được cử thay mặt Chính phủ cùng với Nguyễn Lương Bằng và Cù Huy Cận vào Huế chấp nhận sự thoái vị của Bảo Đại. Sau đó, ông lần lượt giữ các chức vụ: Chính trị cục Trưởng, Bí thư Tổng bộ Việt Minh, Chủ tịch Hội Vǎn hoá cứu quốc, Trưởng ban nghiên cứu vǎn sử địa, Viện trưởng Viện Sử học, Phó chủ nhiệm uỷ ban khoa học xã hội Việt Nam.\nCác tác phẩm chính của ông là: \"Một bầu tâm sự\", \"Ngục trung ký sự\", \"Lịch sử Cách mạng cận đại Việt Nam\", \"Lịch sử 80 nǎm chống Pháp\", \"Phong trào Cách mạng Việt Nam qua thơ vǎn\", \"Nguyễn Trãi\"...\nÔng mất ngày 28-7-1969.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','07', 'Ngày 28-7-1995, nước Cộng hoà xã hội chủ nghĩa Việt Nam gia nhập hiệp hội các nước Đông Nam Á (Viết tắt là ASEAN).\nTổ chức này thành lập nǎm 1967 với 5 thành viên là: Inđônêxia, Malaixia, Philippin, Thái Lan và Singapo.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','07', 'Lutvích Phơbách nhà duy vật nổi tiếng cổ điển Đức. Sinh ngày 28-7-1804 trong một gia đình luật sư. Ông tốt nghiệp triết học ở Trường Đại học tổng hợp và tham gia phái Hêghen trẻ, nhưng sau đó tách khỏi và xây dựng một hệ thống triết học duy vật của mình.\nCác tác phẩm triết học nổi tiếng của ông là \"Suy nghĩ về cái chết và sự bất tử\", \"Những nguyên lý của triết học tương lai\", \"Về bản chất Đạo Cơ đốc\". Theo ông con người là sản phẩm của tự nhiên, nhận thức con người là nền tảng và chìa khoá để nhận thức thế giới. Chỉ có thể giải quyết vấn đề cơ bản của triết học về quan hệ tinh thần và vật chất trên cơ sở nhận thức con người. Ông coi tôn giáo là sự thể hiện, sự mềm yếu, bất lực của con người đối với những điều kiện xã hội. Các quan điểm triết học của Phơbách có ảnh hưởng rất lớn đối với các nhà sáng lập ra Chủ nghĩa Cộng sản khoa học sau này.\nPhơbách mất ngày 13-9-1872.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','07', 'Ngày 28-7-1914 Áo - Hung tuyên chiến với Secbia, mở đầu cuộc chiến tranh thế giới thứ nhất (1914-1918) giữa phe Liên Minh (Đức, Áo, Hung, Thổ Nhĩ Kỳ...) với phe Hiệp ước (Anh, Pháp, Nga, sau thêm Mỹ, Nhật...).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','08', 'Đồng chí Nguyễn Hữu Tiến sinh nǎm 1901 ở làng Lũng Xuyên, huyện Duy Tiên, tỉnh Hà Nam, bị giặc Pháp đem bắn ở Hóc Môn, tỉnh Gia Định cũ, vào ngày 28-8-1941 cùng với một số các đồng chí lãnh đạo cuộc khởi nghĩa Nam Kỳ.\nTừ nǎm 1927, Nguyễn Hữu Tiến được kết nạp vào Việt Nam Thanh niên cách mạng đồng chí Hội. Nǎm 1929 đồng chí vào Đông Dương Cộng sản Đảng. Tháng 5-1931, đồng chí bị mật thám địch bắt tại Hà Nội. Sau đó đồng chí bị kết án 20 nǎm khổ sai, 20 nǎm quản thúc, đầy đi nhà tù Sơn La, đến tháng 12-1933 chuyển ra Côn Đảo. Tháng 4-1935, đồng chí vượt ngục, về hoạt động ở Sài Gòn, Chợ Lớn, phụ trách cơ quan ấn loát của Đảng. Đến cuối tháng7-1940, đồng chí bị địch bắt tại cơ quan báo của Đảng, Đồng chí Nguyễn Hữu Tiến là người đã vẽ lá cờ đỏ sao vàng và lần đầu tiên lá cờ này xuất hiện trong cuộc khởi nghĩa Nam Kỳ ngày 23-11-1940. Trong kỳ họp Quốc hội khoá I, các đại biểu đã quyết định lấy lá cờ đỏ có ngôi sao vàng ở giữa do Nguyễn Hữu Tiến vẽ làm Quốc kỳ của nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','08', 'Tại Bà Điểm, Hóc Môn, ngày 28-8-1941, thực dân Pháp đã xử bắn các đồng chí: Nguyễn Vǎn Cừ (sinh ngày 9-7-1912). Tổng Bí thư của Đảng; Nguyễn Thị Minh Khai (sinh ngày 1-1-1910) - Xứ uỷ viên Nam Kỳ, Bí thư Thành uỷ Sài Gòn; Phan Đǎng Lưu (sinh ngày 5-5-1902) - Uỷ viên Trung ương Đảng; Võ Vǎn Tần (sinh 1894) - Uỷ viên Trung ương Đảng; Hà Huy Tập - Uỷ viên Trung ương Đảng.\nTrước họng súng của kẻ thù, các đồng chí bình tĩnh, không chịu bịt mắt và hô lớn những khẩu hiệu tỏ lòng tin tưởng sắt đá vào thắng lợi cuối cùng của cách mạng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','08', 'Ngày 28-8-1945, hai chi đội giải phóng quân đầu tiên tiến vào Hà Nội. Buổi duyệt binh đầu tiên của quân đội cách mạng quần áo còn nhiều kiểu, nhiều màu, vũ khí còn thô sơ, nhưng được nhân dân Hà Nội khâm phục và trìu mến đón mừng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','08', 'Bộ Tài chính là một trong những bộ được thành lập đầu tiên cùng với sự ra đời của Chính phủ lâm thời Việt Nam dân chủ cộng hoà ngày 28-8-1945.\nBộ trưởng đầu tiên của Bộ Tài chính là đồng chí Phạm Vǎn Đồng.\nLúc chính quyền mới về tay nhân dân ta (sau Cách mạng tháng Tám), trong quỹ của Ngân khố Trung ương chỉ có vẻn vẹn 1.250.000 đồng Đông Dương, trong đó có 580.000 đồng là tiền hào rách nát chờ tiêu huỷ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','08', 'Thực hiện nghị quyết của hội nghị cán bộ Trung ương Đảng tháng 1/1949 về việc rút các đại đội độc lập về để cùng với các tiểu đoàn tập trung, xây dựng thành những binh đoàn chủ lực, ngày 28-8-1949, Đại đoàn Quân tiên phong (tức sư đoàn 308) được thành lập ở Đồn Du (Thái Nguyên).\nTiền thân của đại đoàn là trung đoàn 308 (trung đoàn này được tổ chức sau chiến dịch Việt Bắc 1947, gồm các đơn vị của trung đoàn vệ binh 147 và trung đoàn Thủ đô) và một số tiểu đoàn có thành tích chiến đấu ở các chiến khu. Đây là đại đoàn chủ lực, cơ động đầu tiên của quân đội nhân dân Việt Nam đang được mang danh hiệu \"Quân Tiên phong\" .')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','08', 'Giôhan Vôngang Gớt sinh ngày 28-8-1749, trong một gia đình giàu có ở thành phố Phrǎng phuốc bên sông Mainơ (nước Đức) và mất vào nǎm 1832.\nLúc nhỏ ông được bố và gia sư dạy chữ, nhiều tiếng nước ngoài. Từ nǎm 1765 đến 1768, ông học khoa Luật ở trường đại học Laixích, học thêm cả hội hoạ và vǎn học.\nGớt là nhà vǎn hào vĩ đại nhất trong lịch sử vǎn học nước Đức và là một trong những vǎn hào lỗi lạc của thế giới. Bạn đọc Việt Nam yêu mến ông qua vở kịch: Phaoxtơ, tiểu thuyết \" Những nǎm học nghề của Vinhem Maixtơ\", \"Nỗi đau của chàng Véctơ\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','08', 'Tuốcghênhép là nhà vǎn hiện thực nổi tiếng nước Nga, sinh ngày 28-8-1818.\nÔng đã đem đến cho vǎn học Nga, cho tiểu thuyết Nga một phong cách trữ tình, lãng mạn và cao thượng đầy chất thơ. Với cảm xúc tinh tế và khả nǎng nắm bắt tinh thần của thời đại một cách nhạy bén, tác phẩm của ông đã phản ánh sâu sắc đời sống tinh thần của xã hội Nga vào những nǎm 1840-1870. Ông có cống hiến to lớn cho sự phát triển của ngôn ngữ vǎn học Nga thế kỷ XIX.\nCác tác phẩm nổi tiếng của ông là: \"Bút ký người đi sǎn\" (1847); \"Ru đin\" (1855); \"Mối tình đầu\" (1860), \"Ngày hôm trước\" (1860), \"Cha và con\" (1862).\nÔng mất ngày 2-9-1883.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','08', 'Lép Nicôlaiêvich Tônxtôi sinh ngày 28-8-1828 và qua đời nǎm 1910, là nhà vǎn vĩ đại của người Nga và thế giới.\nTrong hơn 60 nǎm cầm bút, ông đã để lại một di sản vǎn học đồ sộ gồm nhiều tiểu thuyết, kịch vǎn chính luận, nhật ký, thư từ, và Toàn tập Tônxtôi, gồm 90 tập.\nXuất thân từ tầng lớp đại quý tộc, đến cuối đời, Tônxtôi đã lên án sự bóc lột cùng lối sống xa hoa của giai cấp này và tự nguyện làm luật sư cho triệu triệu dân cày. Các tác phẩm chính của ông gồm: \"Phục sinh\", \"Anna Karêninna\", \"Chiến tranh và hoà bình\", \"Đức cha Xécghi\"...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','08', 'Lui Paxtơ (Louis Pasteur) từ trần ngày 28-8-1895. Ông sinh nǎm 1822 ở Jura, nước Pháp.\nPaxtơ học tập ở trường trung học Bơdǎngxông trước khi được nhận vào trường Sư phạm Pari nǎm 1843. Là tiến sĩ vật lý và hoá học, ông trở nên nổi tiếng khi công bố một báo cáo khoa học về tinh thể học. Từ nǎm 1862, ông tập trung nghiên cứu vi sinh vật. Các nghiên cứu của ông về bệnh dại đã gây nên những cuộc bút chiến mạnh mẽ và đã dẫn tới việc điều chế được vắc xin phòng ngừa bệnh dại, khiến ông càng nổi tiếng trên thế giới\nỞ thủ đô Hà Nội có một vườn hoa mang tên Paxtơ và có tượng của ông.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','08', 'Cônxtantin Ximônốp sinh nǎm 1915 ở Bêtơrôgrát (nước Nga), qua đời ngày 28-8-1979\nÔng là nhà vǎn, nhà thơ, nhà viết kịch Xô Viết nổi tiếng.\nTrong những nǎm chiến tranh ái quốc chống Phát xít Đức xâm lược Liên Xô, ông là phóng viên của báo Sao Đỏ, có mặt ở nhiều chiến dịch quan trọng. Ngoài thơ trữ tình, vở kịch \"Những người Nga\" và truyện \"Ngày và đêm\" của ông là hai tác phẩm nổi tiếng của nền vǎn học Xô Viết trong chiến tranh. Từ lâu, Ximônốp là nhà vǎn gần gũi với bạn đọc Việt Nam qua bài thơ nổi tiếng \"Đợi anh về\" và vở kịch \"Những người Nga\". Ông còn viết nhiều bài thơ thể hiện tình cảm thắm thiết với nhân dân ta trong cuộc kháng chiến chống Mỹ cứu nước. Đó là tập thơ \"Việt Nam mùa đông nǎm bảy mươi\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','09', 'Cuộc khởi nghĩa Hương Sơn (Hà Tĩnh) do Phan Đình Phùng lãnh đạo được phát động từ năm 1885. Qua 10 năm chiến đấu kiên cường, nghĩa quân dựa vào nhân dân và địa thế hiểm trở để đánh địch. Ngày 28-9-1895, Phan Đình Phùng chỉ huy nghĩa quân đánh quân Pháp ở vùng núi Vụ Quang. Cuộc chiến đấu kết thúc với thắng lợi thuộc về nghĩa quân. Chiến thắng Vụ Quang làm nức lòng nhân dân và nghĩa quân. Đây cũng là thắng lợi vang dội trong phong trào chống Pháp giai đoạn cuối thế kỷ XIX ở nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','09', '28-9-1945 Hồ Chủ tịch đã gửi thư kêu gọi \"Sẻ cơm nhường áo\" trong thư có đoạn viết \"... Lúc chúng ta nâng bát cơm mà ăn, nghĩ đến kẻ đói khổ, chúng ta không khỏi động lòng.\nVậy tôi xin đề nghị với đồng bào cả nước, và tôi xin thực hành trước:\nCứ 10 ngày nhịn ăn một bữa, mỗi tháng nhịn ăn 3 bữa. Đem gạo đó (mỗi bữa một bơ) để cứu dân nghèo\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','09', 'Ngày 28-9-1945, quân đội Nhật Bản ở Đông Dương đã tổ chức lễ đầu hàng tại Phủ toàn quyền cũ (ở Hà Nội).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','09', 'Ngày 28-9-1864 thành lập Hội liên hiệp Lao động quốc tế (Còn gọi là Quốc tế thứ nhất).\nHội hoạt động dưới sự chỉ đạo của Mác - Ǎngghen. Và tồn tại đến năm 1876.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','09', 'Lui Paxtơ (Luois Paster) là một trong những nhà bác học có cống hiến to lớn nhất cho nhân loại. Ông sinh ngày 27-12-1822 tại nước Pháp.\nTừ năm 26 tuổi ông đã có một phát minh khoa học về tinh thể học làm cho tên tuổi ông nổi tiếng trong giới khoa học. Sau đó ông nghiên cứu sự lên men của rượu và chứng minh việc diệt trừ men gây bệnh ở rượu là cần đun nóng rượu lên 55 độ C. Từ năm 1868 ông bị liệt nửa người nhưng ông tiếp tục hoàn thành các công trình vĩ đại của mình: Tìm ra nguyên nhân gây ra các bệnh truyền nhiễm ở xúc vật.\nNăm 1873 Paxtơ được bầu vào Viện Hàn lâm Y học Pháp. Năm 1881 được tặng thưởng Huân chương Bắc đẩu Bội tinh và được bầu vào Viện Hàn lâm Pháp Quốc. Và một phát minh to lớn của thời gian này là tìm ra Vắcxin phòng bệnh chó dại. Phát minh này được đánh giá là đã mở đầu cho Y học hiện đại.\nÔng mất ngày 28-9-1895. Chính phủ Pháp đã tổ chức quốc tang để tưởng nhớ ông.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','10', 'Ngày 28-10-1946, kỳ họp thứ 2 quốc hội khoá I khai mạc tại nhà hát Thành phố Hà Nội với 290 đại biểu các tỉnh về dự.\nQuốc hội đã thông qua chủ trương cấp bách để chuẩn bị toàn quốc kháng chiến. Theo đề nghị của đoàn đại biểu Nam Bộ, Quốc hội đã nhất trí bày tỏ sự tin tưởng tuyệt đối của nhân dân cả nước với Chủ tịch Hồ Chí Minh và tôn suy Người là công dân thứ nhất. Tại kỳ họp lịch sử này, bản Hiến pháp đầu tiên của nước Việt Nam dân chủ cộng hoà đã được Quốc hội thông qua. Sau khi Quốc hội thông qua, Chủ tịch Hồ Chí Minh đã ký sắc lệnh công bố bản Hiến pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','10', 'Ngày 28-10-1995, tại kỳ họp thứ 8 Quốc hội nước Cộng hoà xã hội chủ nghĩa Việt Nam khoá IX đã thông qua Bộ luật dân sự. Bộ luật này có hiệu lực từ ngày 1-7-1996. Bộ luật dân sự có 7 phần, 33 chương, 838 điều. Nó là công cụ pháp lý thúc đẩy giao lưu dân sự, tạo môi trường thuận lợi cho sự phát triển kinh tế - xã Hội của đất nước, góp phần bảo đảm cuộc sống cộng đồng ổn định, lành mạnh, giữ gìn và phát huy truyền thống đoàn kết, tương thân, tương ái, thuần phong mỹ tục và bản sắc văn hoá dân tộc đã hình thành trong lịch sử lâu dài xây dựng và bảo vệ Tổ quốc Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','10', 'Ngày 28-10-1996, Thủ tướng Chính phủ đã ký nghị định thành lập quận Tây Hồ, thành phố Hà Nội. \nQuận Tây Hồ được thành lập trên các phường: Bưởi, Thuỵ Khuê, Yên Phụ của quận Ba Đình và các xã Tứ Liên, Nhật Tân, Quảng An, Xuân La, Phú Thượng của huyện Từ Liêm.\nQuận Tây Hồ có diện tích tự nhiên 2042,7 ha và 69.713 nhân khẩu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','10', 'Ivan Sécghêvich Tuốcghêniép là nhà văn người Nga, sinh ngày 28-10-1818 qua đời năm 1883.\nTuy đỗ tiến sĩ triết học nhưng ông lại có năng khiếu văn học, đã sáng tác nhiều tác phẩm có gía trị, vẽ nên bức tranh rộng lớn, sinh động về cuộc sống hiện thực của đất nước Nga trên bước đường phát triển từ năm 40 đến những năm 70 của thế kỷ XIX. Tiêu biểu là: \"Bút ký người đi săn\", \"Người cùng thời\", \"Mối tình đầu\", \"Một tổ quý tộc\", \"Đêm trước\", \"Cha và con\"...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','10', 'Ngày 28-10-1886, Tượng \"Nữ thần tự do\" đặt ở cảng Niu Oóc khánh thành.\nBức tượng cao 46 mét, đặt trên một cái bệ cũng cao 46 mét, nặng 225 tấn. Tượng mang hình một phụ nữ, tay phải giơ cao một bó đuốc, tay trái ôm một tấm thẻ ghi \"Ngày 4 tháng 7 năm 1776\", ngày tuyên bố độc lập của Hoa Kỳ\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','11', 'Ngày 28-11-1959, Chủ tịch Hồ chí Minh đã phát động phong trào trồng cây. Tháng 6-1995 Thủ tướng Chính phủ đã có quyết định lấy ngày 28-11 hàng nǎm là \"Ngày Lâm nghiệp Việt Nam\". Quyết định của Thủ tướng Chính phủ nêu rõ: Việc tổ chức \"Ngày Lâm nghiệp Việt Nam\" hàng nǎm là dịp giáo dục toàn dân ý thức bảo vệ môi trường, tình cảm yêu ngành, yêu nghề trong cán bộ, công nhân viên lâm nghiệp, động viên phong trào thi đua lao động sản xuất, phát triển rừng, sử dụng tài nguyên rừng hợp lý, tiết kiệm, biểu dương, khen thưởng người tốt, địa phương, đơn vị tốt.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','11', 'Ngày 28-11-1983, Chủ tịch Hội đồng Nhà nước Trường Chinh đã ký lệnh công bố Pháp lệnh về việc ban hành công trái xây dựng Tổ quốc.\nMục đích của Pháp lệnh nhằm động viên nguồn vốn trong nhân dân góp phần vào sự nghiệp công nghiệp hoá xã hội chủ nghĩa nước nhà.\nViệc mua công trái thực hiện theo nguyên tắc tự nguyện và tuỳ khả nǎng của mỗi người.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','11', 'Phêrêdrich Ǎngghen sinh ngày 28-11-1820 tại nước Đức và qua đời nǎm 1895.\nÔng là người thầy vĩ đại của giai cấp công nhân. Cùng với Các Mác, Ǎngghen đã xây dựng lý luận chủ nghĩa cộng sản khoa học và đấu tranh cho sự nghiệp giải phóng giai cấp công nhân.\nǍngghen là nhà cách mạng vĩ đại, nhà triết học, nhà kinh tế học, nhà sử học, nhà khoa học tự nhiên và khoa học quân sự, nhà vǎn, nhà ngôn ngữ học. Ông là người sáng lập Quốc tế thứ hai.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','11', 'Alếchxanđrơ Alếchxanđrơvivh Blốc là nhà thơ lớn Nga, sinh ngày 28-11-1880. Từ bé ông có nǎng khiếu về thơ. Ông học luật, ngữ vǎn và lịch sử.\nCác tác phẩm chính của ông là: \"Thơ ca ngợi giai nhân\", \"Bài ca số phận\", \"Tập thơ về nước Nga\", \"Mười hai người \"...Thơ ông thể hiện lòng cǎm ghét thế giới tư bản và niềm tin ở Nga, tin ở lao động, bình đẳng xã hội. Nhiều bài thơ trữ tình của ông được coi là tuyệt tác trong thơ ca Nga. Trong lịch sử vǎn học Nga, ông là một nhà thơ lớn cách tân thuộc tầm cỡ thế giới. Ông mất ngày 7-8-1921')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','11', 'Enricô Phécmi (Enrico Fermi) nhà vật lý nguyên tử và thực nghiệm nổi tiếng Italia, sinh ngày 29-9-1901. Nǎm 1934, ông đã nghiên cứu các phản ứng hạt nhân nguyên tố Urnium bắn phá bởi các Newtron để hình thành các nguyên tố nhân tạo nặng hơn như: Neptunium, Phitonium. Ông được tặng giải thưởng Nôben Vật lý nǎm 1938. Nǎm 1942, ông hoàn thành pin nguyên tử đầu tiên của nhân loại, thực hiện phản ứng dây chuyền hạt nhân có điều khiển. Ông có công đầu trong chế tạo 3 quả bom nguyên tử đầu tiên của Mỹ 1945\nÔng mất ngày 28-11-1954.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','12', 'Phan Đình Phùng sinh nǎm 1844, quê ở tỉnh Hà Tĩnh, mất ngày 28-12-1895.\nÔng đỗ tiến sĩ và làm quan dưới thời vua Tự Đức.\nNǎm 1885, hưởng ứng chiếu Cần Vương của vua Hàm Nghi, ông đã tập hợp nghĩa quân kháng chiến chống thực dân Pháp xâm lược, cǎn cứ ở vùng rừng núi Hương Sơn, Hương Khê, tỉnh Hà Tĩnh.\nCuộc khởi nghĩa Hương Khê do Phan Đình Phùng lãnh đạo kéo dài gần 10 nǎm, tiêu biểu cho phong trào Vǎn Thân chống Pháp ở Bắc Kỳ và Trung Kỳ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','12', 'Nguyễn Thượng Hiền, tự là Mai Sơn, người làng Liên Bạt, huyện Ứng Hoà, tỉnh Hà Tây, sinh nǎm 1868, mất nǎm 1925.\nÔng thông minh, 16 tuổi đã đỗ cử nhân, nǎm sau thi Hội, rồi thi Đình và ông đỗ Đình Nguyên. Nǎm 25 tuổi, ông lại thi đỗ Hoàng Giáp. Vì không muốn ra làm quan, nên sau khi đỗ, ông xin triều đình Huế cho về nghỉ ít nǎm. Nhưng sau đó, ông bị điều ra và phải nhận làm một chức học quan ở Ninh Bình, rồi Nam Định.\nVốn sẵn tấm lòng yêu nước, cǎm thù bọn thực dân nên thời kỳ này ông liên hệ với nhiều sĩ phu yêu nước như các ông Phan Bội Châu, Tǎng Bạt Hổ...và vận động phong trào kháng thuế ở miền Trung nǎm 1908, ông đã bị kết án tử hình cùng với Nguyễn Dữ Hàm, nhưng lúc đó Nguyễn Thượng Hiền đã xuất dương sang Trung Quốc.\nNǎm Mậu Thân (1908) ông cùng với Phan Bội Châu và các đồng chí khác hoạt động cách mạng nhằm chống thực dân ở nước nhà. Suốt thời kỳ ở nước ngoài, ông đã qua Nhật và Xiêm, vận động và tổ chức phong trào cách mạng và vận động du học sinh sang Nhật, sang Trung Quốc để học tập và hoạt động Cách mạng cho nước nhà.\nLà một nhà thơ, vǎn nổi tiếng, ông đã viết rất nhiều bài nội dung chủ yếu nói về tình cảnh mất nước, cǎm thù giặc, kêu gọi đoàn kết chống giặc...\nNǎm Ất Sửu (1925), Phan Bội Châu bị mật thám địch bắt và đem về an trí tại Huế. Tin ấy làm cho Nguyễn Thượng Hiền đau xót vô cùng. Vốn đã yếu, ông ngã bệnh nặng và mất ngày 13-11 nǎm Ất Sửu (tính theo dương lịch là ngày 28-12-1925) tại một ngôi chùa ở Hàng Châu - Trung Quốc, thọ 57 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','12', 'Lương Định Của sinh nǎm 1921, quê ở Sóc Trǎng, qua đời ngày 28-12-1975 tại Hà Nội.\nÔng đỗ tiến sĩ nông học ở Nhật Bản. Nǎm 1954, ông cùng gia đình về miền Nam, rồi ra chiến khu và tập kết ra Bắc. \nÔng đã làm việc ở Viện Khảo cứu nông lâm, Trường đại học Nông nghiệp. Viện Lương thực và cây thực phẩm. Là một nhà di truyền học, Lương Định Của đã lai tạo thành công nhiều giống lúa mới cho nǎng suất cao, ngắn ngày, chịu ngập nước và những cây ǎn cây ǎn quả. Bà con nông dân rất quý mến ông, lấy tên ông đặt cho các giống cây mới như: \"Lúa ông Của\", \"Táo ông Của\", \"Cà chua ông Của\"...\nÔng đã được Nhà nước phong tặng danh hiệu Anh hùng lao động.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '28','12', 'Ngày 28-12-1989, tại kỳ họp thứ 6, Quốc hội nước Cộng hoà XHCN Việt Nam khoá 8 đã thông qua Luật báo chí.\nNội dung chủ yếu của Luật quy định: Quyền tự do báo chí, quyền tự do ngôn luận trên báo chí của công dân; nhiệm vụ và quyền hạn của báo chí; tổ chức báo chí và nhà báo; quản lý Nhà nước về báo chí và xử lý các vi phạm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','12', 'Kỹ sư Guxtavơ Épphen (Gustave Fiffel) mất ngày 28-12-1923, hưởng thọ 91 tuổi. Ông sinh nǎm 1832 ở Dijon (Pháp). Là kỹ sư cơ khí, Épphen thực hiện gian hàng triển lãm máy móc trong cuộc đấu xảo thế giới nǎm 1867 về xây cất cầu cống.\nNǎm 1886 ông đề nghị dựng một cái tháp bằng thép tại Pari. Khi hoàn thành, tháp cao nhất thế giới lúc đó - 300,65 mét đã mang tên ông: tháp Épphen.\nTháp Épphen bây giờ là biểu tượng của thủ đô Pari.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '28','12', 'Xécgây Alếchxanđrơvich Êxênhin (Sergei Alexandrovitsh Esenin) - nhà thơ lớn Nga, sinh ngày 3-10-1895. Từ nǎm 1924 là mốc đánh dấu cao trào sáng tác của ông. Nội dung thơ ca của Êxênhin thấm nhuần tư tưởng xây dựng chủ nghĩa cộng sản. Các tác phẩm lớn của ông gồm có: \"Nước Nga Xô Viết\", \"Thơ tứ tuyệt\", \"Trường ca Anna Xênhêghina\", \"Bài ca về cuộc hành quân vĩ đại\", \"Giai điệu Ba Tư\"...\nÔng được coi là nhà thơ trữ tình tinh tế và thực sự trở thành tài sản quý giá của nước Nga. Ông tự sát ngày 28-12-1925.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','01', 'Tháng giêng nǎm 1258, quân Mông Cổ do tướng Ngột Lương Hợp Thai chỉ huy từ Vân Nam (Trung Quốc) đánh xuống nước Đại Việt. Vua Trần Thái Tông đã đem quân lên chặn giặc ở Bình Lệ Nguyên, bên sông Cà Lồ. Nhưng sau đó, quân ta rút lui để bảo toàn lực lượng trước thế mạnh ban đầu của giặc.\nGiặc đóng ở Thǎng Long trong một toà thành trống không, đã bị khốn đốn vì thiếu lương thực. Ngày 29-1-1258, vua Trần Thái Tông đã đem binh quyền ngược sông Hồng, tiến về Thǎng Long. Quân địch bị đánh bật khỏi kinh thành, theo đường cũ, chúng chạy về Vân Nam.\nNơi quân ta lập chiến công này là ở Đông Bộ Đầu (phía trên cầu Long Biên ngày nay).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','01', 'Nhà thơ Trần Tế Xương sinh nǎm 1870, qua đời ngày 29-1-1907. Ông quê ở Vị Xuyên, tỉnh Nam Định. Mọi người thường gọi là Tú Xương. Phần lớn sáng tác của ông là thơ nôm, viết về con người thực, cảnh sống thực, khá tiêu biểu của một xã hội thực dân, nửa phong kiến. Nghệ thuật trào phúng trong thơ Tú Xương rất sắc sảo, ít có trong vǎn học nước nhà.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '29','01', 'Antôn Pavlôvich Tsêkhốp, nhà vǎn Nga nổi tiếng thế giới, sinh ngày 29-1-1860. Chặng đường sáng tác của ông có thể chia làm 3 giai đoạn.\nGiai đoạn đầu: ông viết nhiều truyện hài hước có nội dung xã hội sâu sắc, phê phán cái xấu xa của cuộc sống như \"Anh béo và anh gầy\". \"Con kỳ nhông\"...\nGiai đoạn hai từ những nǎm 80, ông chuyển sang viết chuyện dài, truyện vừa, tổng kết những cảm nghĩ của ông về đất nước và nhân dân \nGiai đoạn ba, những nǎm 90, ông viết một loạt truyện ngắn bác bỏ thuyết không dùng bạo lực để chống điều ác.\nTruyện của TSêkhốp đơn giản về kết cấu, nội dung xã hội sâu sắc, trau truốt, ngắn gọn về ngôn ngữ. Ông mất ngày 15-7-1904.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '29','01', 'Rônanh Rôlǎng (Romain Rolland), nhà vǎn lớn nước Pháp, sinh ngày 29-1-1866.\nNǎm 1903 ông viết cuốn \"Sân khấu của dân chúng\" và nhiều vở kịch về đề tài cách mạng Pháp. Tiếp đó ông xuất bản bộ tiểu thuyết \"Giǎng Cristop\". Với tác phẩm này ông được trao giải Nobel nǎm 1915. Khi Cách mạng tháng Mười Nga thắng lợi, ông lên tiếng ca ngợi chủ nghĩa cộng sản. Sau đó với cuốn \"Giã từ quá khứ\", \"Mười lǎm nǎm đấu tranh\" ông chủ trương không khoan nhượng chống chủ nghĩa phát xít, kẻ thù của vǎn hoá loài người. Ông còn có tác phẩm ca ngợi những nhà tư tưởng, nhà vǎn lớn của nhân loại và viết tiểu sử danh nhân. Ông còn viết một số công trình nghiên cứu âm nhạc. Ông qua đời ngày 30-12-1944.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','02', '28-2-1949 - mở đầu chiến dịch Lao - Hà (Lao Cai - Hà Giang).\nĐể phối hợp với hướng chính ở Đông Bắc, ngày 18-3 ta tiêu diệt vị trí phố Lu (Lào Cai) và 11 vị trí xung quanh Hoàng Xu Phì (Hà Giang). Toàn bộ chiến dịch từ 28-2-1949 đến 18-3-1949 ta đã giải phóng được một vùng rộng lớn, cô lập Hoàng Xu Phì. Mở rộng thêm 3000 km2 vùng cǎn cứ địa.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','02', 'Ngày 28-2-1962, địch mở chiến dịch lớn đánh vào vùng U Minh Hạ (Cà Mau) nhằm dồn 60 ngàn dân vào \"Ấp chiến lược\". Quân dân U Minh đã dùng đấu tranh vũ trang và đấu tranh chính trị để chống lại. Hàng ngày có từ 300 đến 500 người kéo trụ sở tề quận, đồn bốt, tố cáo tội ác của địch. Đi đôi với đấu tranh chính trị, ta đã tấn công diệt những nơi địch sơ hở như Đầm Dơi, Cái Nước, bức rút 4 cǎn cứ khác. Tính chung, suốt thời gian chiến dịch, địch bị đánh 725 trận, chết 572 tên, bị thương 558 tên.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','02', 'Từ ngày 28-2 đến 20-3-1969 Đoàn đại biểu Mặt trận dân tộc giải phóng miền Nam Việt Nam do bác sĩ Phùng Vǎn Cung, Phó chủ tịch Đoàn chủ tịch Uỷ ban Trung ương Mặt trận dân tộc giải phóng miền Nam dẫn đầu ra thǎm miền Bắc. Trong tình nghĩa ruột thịt Bắc Nam, nhân dân Thủ đô Hà Nội và toàn miền Bắc vô cùng sung sướng và nhiệt liệt hoan nghênh Đoàn đại biểu Mặt trận dân tộc giải phóng miền Nam, những đại biểu yêu quý của tiền tuyến lớn anh hùng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','02', 'Ngày 28-2-1977, Tổng cục Hàng không dân dụng Việt Nam đã ra quyết định thành lập Sân bay quốc tế Nội Bài ở huyện Sóc Sơn, Hà Nội.\nĐến đầu nǎm 1978, sân bay chính thức mở cửa hoạt động. Hiện nay sân bay có các nhà ga khách quốc tế đi, đến, khách trong nước và một nhà khách VIP.\nSân bay quốc tế Nội Bài có tổng diện tích hơn 10.000 mét vuông, bảo đảm nǎng lực thông qua khoảng 1,5 triệu hành khách một nǎm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '29','02', 'Lainớt Pôlinh (Linu Pauling), nhà hoá học vĩ đại Mỹ, sinh ngày 28-2-1901. Ông là một trong những nhà khoa học đầu tiên sử dụng công cụ mới đó là tinh thể học tia X để xác định cấu trúc phân tử. Công trình của ông về \"bản chất của các liên kết hoá học kể cả những khái niệm về cộng hưởng và lai tạo\" đã làm thay đổi tận gốc bộ môn hoá học. Với công trình về \"liên kết hoá học\", ông được tặng giải Noben hoá học 1954.\nÔng còn là một chiến sĩ đấu tranh cho hoà bình - cấm vũ khí hạt nhân, chống chiến tranh. Vì vậy ông được tặng giải Noben hoà bình 1962. Và ông cũng là người độc nhất từ xưa đến nay nhận 2 giải thưởng Noben mà không phải chia xẻ với ai. Ông qua đời ngày 19-8-1994.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','03', 'Ngày 29-3-1938, Hội nghị Ban Chấp hành Trung ương Đảng Cộng sản Đông Dương đã được triệu tập tại làng Tân Thới Nhất (Gia Định) với sự tham dự của các đồng chí Lê Hồng Phong, Nguyễn Vǎn Cừ, Nguyễn Chi Diểu... Nghị quyết của Hội nghị đã đề cập tới chính sách cai trị của chính quyền Đông Dương, phong trào quần chúng và công tác nội bộ Đảng. Bản nghị quyết cũng vạch rõ nhiệm vụ của cách mạng trong giai đoạn mới như: Thực hiện mặt trận thống nhất dân chủ, công tác quần chúng, củng cố tổ chức Đảng, đảm bảo giao thông liên lạc, kết hợp giữa hoạt động bí mật và công khai, đẩy mạnh công tác tuyên truyền, huấn luyện và quan hệ với các đảng phái chính trị khác. Tại Hội nghị này đồng chí Nguyễn Vǎn Cừ được cử làm Tổng Bí thư Đảng Cộng sản Đông Dương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','03', 'Vào ngày 29-3-1945, toàn quyền Nhật ở Đông Dương ra nghị định áp đặt việc toàn cõi Đông Dương thực hiện tính giờ theo múi thứ 9 (Múi giờ mà Nhật Bản thực hiện). Theo đây thời gian được tính ở Đông Dương sẽ nhanh lên 120 phút.\nSau khi phát xít Nhật đại bại, cách tính giờ ở Đông Dương lại trở lại tương ứng múi giờ thứ 7. Trong những nǎm nước nhà bị chia cắt 2 miền, ở miền Bắc Chính phủ ta ra quyết định chính thức tính theo múi thứ 7, ở miền Nam chính quyền Ngụy Sài Gòn lại quy định áp dụng theo múi giờ thứ 8. Từ tháng 5-1975 trên toàn lãnh thổ Việt Nam giờ pháp định được tính thống nhất theo múi giờ thứ 7.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','03', 'Chiều ngày 29-3-1973 tại sân bay Gia Lâm, Hà Nội, ta đã trao trả cho phía Hoa Kỳ những phi công Mỹ cuối cùng bị bắt trong cuộc chiến tranh phá hoại của Mỹ đối với miền Bắc nước ta. Toán phi công Mỹ đầu tiên được ta trao trả vào ngày 12 tháng 2 nǎm 1973.\nTheo hiệp định Pari về Việt Nam ký ngày 27-1-1973 Mỹ cam kết chấm dứt chiến tranh xâm lược Việt Nam, tôn trọng chủ quyền lãnh thổ Việt Nam, rút hết quân Mỹ và chư hầu ra khỏi miền Nam Việt Nam. Vì vậy ngày 29-3-1973 những tên Mỹ cuối cùng phải rút khỏi miền Nam Việt Nam dưới sự kiểm soát của Quân giải phóng và Quân đội nhân dân Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','03', 'Ngày 29-3-1975, chiến dịch Huế - Đà Nẵng đã kết thúc thắng lợi bằng trận quân ta giải phóng Đà Nẵng - thành phố lớn thứ hai của miền Nam.\nChiến dịch này mở đầu ngày 21-3-1975.\nTừ những trận tiến công phối hợp với mặt trận tây Nguyên theo kế hoạch chiến lược thống nhất khi thời cơ lớn xuất hiện quân đoàn 2 và lực lượng vũ trang quân khu Trị Thiên, quân khu 5, dưới sự chỉ huy trực tiếp của Bộ Tổng tư lệnh, đã chuyển hoá mau lẹ, phát triển thành một chiến dịch hợp đồng binh chủng có quy mô lớn. Trong chiến dịch này, quân và dân ta đã tiêu diệt và làm tan rã hoàn toàn quân đoàn 1 và quân khu 1 của địch, đập tan hệ thống phòng thủ kiên cố của chúng, giải phóng 5 tỉnh liền nhau là Quảng Trị, Thừa Thiên, Quảng Đà, Quảng Nam, Quảng Ngãi, trong đó có 2 thành phố lớn là Huế và Đà Nẵng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','04', 'Ngày 29-4-1946 Hồ Chủ tịch ký sắc lệnh qui định công nhân viên chức được hưởng lương ngày nghỉ lễ Quốc tế lao động 1-5. Lần đầu tiên, ngày Hội lao động quốc tế 1-5 được công nhận là ngày Hội chính thức của người lao động Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','04', 'Chiều ngày 29-4-1955 Chủ tịch Hồ Chí Minh đã đến thǎm Đại hội thi đua Thành phố Hà Nội - Người đã tặng huy hiệu cho 35 đại biểu có thành tích thi đua khá nhất.\nĐây là lần đầu tiên Hồ Chủ tịch tặng huy hiệu của Người cho những người tốt làm việc tốt ở Hà Nội.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','04', 'Ngày 29-4-1975, tổng tiến công trên toàn bộ mặt trận, ngǎn chặn và tiêu diệt các tập đoàn chủ lực bên ngoài, thọc sâu vào bên trong, cùng lực lượng tại chỗ đánh chiếm các địa bàn quan trọng ở vùng ven, làm tê liệt hoàn toàn sân bay Tân Sơn Nhất.\nỞ hướng đông, ta tiếp tục phát triển tiến công cǎn cứ Nước Trong, nam cǎn cứ Long Bình, đánh chiếm phần lớn thị xã Vũng Tàu. Binh đoàn thọc sâu của quân đoàn 2 tiến tới cầu xa lộ Đồng Nai.\nQuân đoàn 4 đánh chiến cǎn cứ Yên Thế, tranh chấp Hố Nai, sân bay Biên Hoà.\nỞ hướng Bắc và Tây Bắc, Quân đoàn 1 tiến tới Bắc Lái Thiêu, Quân đoàn 3 chiếm cǎn cứ Đồng Dù, Trảng Bàng, phát triển tiến công trại huấn luyện Quang Trung, Bà Quẹo.\nỞ hướng Tây Nam và Nam, Đoàn 232 chiếm chi khu Đức Huệ thị xã Hậu Nghĩa, có đơn vị tiến vào được Bà Quẹo, Bà Lác.\nTrong ngày 29-4-1975, ta đã giải phóng Biên Hoà, Thủ Dầu Một, Tân An và Vũng Tàu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','05', 'Báo L\\'Avant Gardel (Tiền phong) - cơ quan của lao động và nhân dân Đông Dương đã ra số đầu tiên tại Sài Gòn ngày 29-5-1937.\nBáo in bằng tiếng Pháp, xuất bản không định kỳ và thực tế là cơ quan Trung ương Đảng xuất bản công khai do đồng chí Hà Huy Tập trực tiếp chỉ đạo, Nguyễn Vǎn Nguyễn làm thư ký toà soạn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','05', '29-5-1955, chính quyền và đồng bào phật tử thủ đô đã hoàn thành khôi phục chùa Một Cột, một di tích lịch sử vǎn hoá đã bị địch dùng mìn phá huỷ trước khi rút khỏi Hà Nội. \nChùa Một Cột được xây dựng từ nǎm 1049 thời nhà Lý. Toàn bộ ngôi chùa được đặt trên một cột đá cao 20m trông như một bông hoa sen. Đây là một công trình tiêu biểu của nghệ thuật kiến trúc Việt Nam, thu hút nhiều khách nước ngoài đến xem.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','05', 'Ngày này 29-5-1956, Hồ Chủ tịch đã nói chuyện tại Hội nghị cán bộ làm công tác sửa sai trong cải cách ruộng đất ở ngoại thành Hà Nội.\nNgười nói: \n\"Tôi thay mặt trung ương Đảng và Chính phủ khen ngợi cán bộ và nhân dân các xã ngoại thành Hà Nội đã làm công tác bước hai đạt kết quả khá. Vừa làm tốt việc sửa sai, ngoại thành Hà Nội cần đẩy mạnh thi đua sản xuất thực hành tiết kiệm, ra sức củng cố các tổ chức quần chúng và thi đua làm nghĩa vụ lương thực cho Nhà nước. \nĐể làm tốt những nhiệm vụ trên, các cán bộ sửa sai phải gương mẫu, tránh bao biện làm thay, cán bộ địa phương thì phải cố gằng học tập, không nên ỷ lại vào cán bộ sửa sai\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','05', 'Đồng chí Hoàng Quốc Việt sinh ngày 29-5-1905, và qua đời nǎm 1992 tại Hà Nội, quê ở Đáp Cầu, Bắc Ninh. Đồng chí Hoàng Quốc Việt tên thật là Hạ Bá Cang, tham gia Cách mạng nǎm 20 tuổi. Trước Cách mạng Tháng Tám, đồng chí bị địch bắt giam tại nhà tù Hoả Lò (Hà Nội) và bị đày đi Côn Đảo. Trong gần 70 nǎm hoạt động, đồng chí đã từng giữ nhiều trọng trách: Uỷ viên bộ chính trị Trung ương Đảng, Chủ tịch Tổng Công đoàn, Viện trưởng Viện kiểm sát nhân dân tối cao, Chủ tịch Đoàn chủ tịch Ủy ban trung ương Mặt trận Tổ quốc Việt Nam.\nĐồng chí Hoàng Quốc Việt là một nhà Cách mạng được nhân dân kính trọng và yêu mến.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '29','05', 'Lêôhác Âylơ (Leonhard Euler) đã dùng ký hiệu \"Pi\" vào toán học lần đầu tiên vào ngày 29-5-1736. Ông là nhà toán học nổi tiếng của nước Thụy Sĩ, sinh ngày 15-4-1707 và từ trần ngày 18-9-1783. Âylơ có nhiều công trình nghiên cứu liên quan đến tất cả các ngành toán học đương thời. Tác phẩm \"Chuyên luận đầy đủ về cơ học\" đã áp dụng vào giải tích học và sự chuyển động (nǎm 1736). Ông còn nêu lên Thuyết chuyển động của các hành tinh và sao chổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '29','05', 'Nhạc sỹ Tây Ban Nha Isắc Anbênit sinh ngày 29-5-1860 tại vùng Catalôni. Ông là người sử dụng rất sáng tạo hất liệu âm nhạc dân gian Tây Ban Nha vào nhiều thể loại như ôpêra, nhạc cho dàn nhạc, cho đàn piano, ca khúc.Vì vậy Anbênít có vị trí trong hàng ngũ các nhạc sỹ lớn của thế giới ở cuối thế kỷ XIX sang đầu thế kỷ XX và trở thành mẫu mực noi theo của các thế hệ nhạc sỹ trẻ Tây Ban Nha.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','06', 'Nguyễn Mậu Kiến quê ở xã Vũ Lǎng, huyện Kiến Xương, tỉnh Thái Bình, sinh ngày 29-6-1819 và qua đời ngày 22-10-1879. Sau khi đỗ tiến sĩ, ông làm đến chức Trung nghị đại phu. Khi thực dân Pháp xâm lược nước ta, ông đứng về phái chủ chiến, dâng sớ kể tội bọn quan lại đầu hàng giặc nên ông bị cách chức. Sau hoà ước giữa triều đình Huế và Pháp nǎm 1874, ông mộ nghĩa binh, chuẩn bị khởi nghĩa ở Hưng Hoá nhưng giữa chừng bị bệnh chết.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','06', 'Ngày 29-6-1946, Tổng tham mưu trưởng Hoàng Vǎn Thái đọc quyết định của Bộ Quốc phòng thành lập Đoàn pháo binh Thủ đô gồm 3 pháo đài: Láng, Xuân Canh và Xuân Tảo. Đêm 19-12-1946 mở đầu cuộc kháng chiến toàn quốc chống thực dân Pháp, theo hiệu lệnh, pháo binh ta từ pháo đài Láng bắn vào Thành, nơi đóng quân của quân đội Pháp. Đó cũng là những loạt pháo đầu tiên của pháo binh ta.\nTrong cuộc kháng chiến chống thực dân Pháp, bộ đội pháo binh chiến đấu dũng cảm, gan dạ, mưu trí, lập công xuất sắc và đã vinh dự được Hồ Chủ tịch tặng 8 chữ vàng truyền thống: \"Chân đồng, vai sắt, đánh giỏi, bắn trúng\". Trong kháng chiến chống Mỹ, bộ đội pháo binh anh dũng mưu lược trong tác chiến độc lập cũng như tác chiến hiệp đồng quy mô, nắm vững khoa học kỹ thuật quân sự hiện đại, sáng tạo, đánh thắng trong mọi tình huống khó khǎn phức tạp.\nBinh chủng pháo binh đã được Nhà nước tặng danh hiệu Đơn vị Anh hùng lực lượng vũ trang, cùng nhiều phần thưởng cao quý khác cho cá nhân và các đơn vị trong binh chủng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','06', 'Ngày 29-6-1982, Ủy ban nhân dân thành phố Hà Nội đã ra quyết định thành lập Bảo tàng Hà Nội.\nBảo tàng phản ánh lịch sử dân tộc ta từ thời An Dương Vương xây thành Cổ Loa. Với nhiều hiện vật quý như: kho mũi tên đồng, trống đồng; bộ sưu tập đồ đá, đồ đồng trước công nguyên nói lên nền vǎn minh vùng châu thổ sông Hồng; những vũ khí bằng đồng, bắt sắt; những sưu tập hiện vật của các ngôi mộ cổ thời Bắc thuộc. Đặc biệt là bộ sưu tập hơn 6000 cổ vật đã được các nhà khoa học giám định, xác định niên đại và giá trị mọi mặt, và bộ sưu tập đồ gồm thời Lý - Trần gồm 725 hiện vật từ thế kỷ XI đến thế kỷ XIV. Bảo tàng Hà Nội có hơn 14.000 hiện vật minh chứng cho lịch sử dân tộc, lịch sử Hà Nội. Nhưng đáng tiếc là việc trưng bầy để phục vụ người xem còn chưa được quan tâm đến.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '29','06', 'Vào ngày 29-6-1927, những quan sát thiên vǎn đầu tiên đã tiến hành trên máy bay hai động cơ của hãng hàng không Hoàng gia Anh (British Airway). Các nhà thiên vǎn học đã chụp ảnh nhật thực toàn phần phía trên màn sương mù Luân Đôn.\nKỹ thuật đó sau này đã được sử dụng lại thành công, chủ yếu với máy bay Caraven và máy bay Côngcooc siêu thanh ở Mỹ và ở Pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '29','06', 'Hội đồng tương trợ kinh tế, gọi tắt là SEV, là tổ chức hợp tác quốc tế của các nước Xã hội chủ nghĩa. Mục tiêu của Hội đồng là: bằng con đường phối hợp hoạt động kinh tế, củng cố và phát triển sự hợp tác giữa các nước phát triển một cách có kế hoạch nền kinh tế quốc dân và nâng cao đời sống của nhân dân.\nTại khóa họp lần thứ 32 của Hội đồng tương trợ kinh tế họp tại Bucaret - Rumani từ ngày 27 đến 29-6-1978 đã nhiệt liệt hoan nghênh và nhất trí kết nạp nước Cộng hoà xã hội chủ nghĩa Việt Nam vào hội đồng tương trợ kinh tế. Đây là lần đầu tiên Việt Nam tham gia với tư cách là một thành viên chính thức của tổ chức này, tạo ra môt bước tiến về kinh tế của nước ta, đông thời là một phát triển mới của sự hợp tác và tương trợ kinh tế nước ta với các nước xã hội chủ nghĩa trong Hội đồng tương trợ kinh tế.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','07', 'Ngày 29-7-1936 trên tờ \"Đấu tranh\" số 93 - Nguyễn An Ninh, một trí thức yêu nước có uy tín và có ít nhiều khuynh hướng xã hội chủ nghĩa đã viết bài báo \"Tiến tới một cuộc Đông Dương Đại hội\". Bài báo kêu gọi: \"Hơn bao giờ hết bây giờ là lúc thích hợp để triệu tập một cuộc Đông Dương Đại hội! Ở đó các giai cấp đều có đại diện để thảo ra một bản dân nguyện bao gồm được những yêu sách của các dân tộc Đông Dương\".\nVới vǎn kiện này, chính Nguyễn An Ninh là người đã khởi xướng cho cuộc vận động Đông Dương Đại hội, một phong trào quần chúng rộng khắp cả nước được sự chỉ đạo của những chiến sĩ Cộng sản, đấu tranh cho nguyện vọng đòi các quyền dân chủ cơ bản.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','07', '11 giờ sáng ngày 29-7-1941 tại Pháp, Đô đốc Đáclan, Phó quốc trưởng của chính phủ Pêtanh và Đại sứ Nhật tại Pháp Sôtômatsu Katô đã ký kết một vǎn bản ngoại giao về việc phòng thủ chung Đông Dương.\nHiệp ước cam kết Chính phủ Pháp và Nhật sẽ tương trợ nhau trong việc phòng thủ Đông Dương. Hiệp ước cho phép Nhật sử dụng các sân bay Nha Trang, Đà Nẵng, Biên Hoà, Sài Gòn, Sóc Trǎng... và các hải cảng Đà Nẵng, Cam Ranh và Sài Gòn. Ngay sau khi vǎn bản được ký kết các đơn vị quân Nhật đã kéo vào Sài Gòn. Như vậy, trên thực tế quân đội Nhật đã chiếm đóng trên toàn cõi Đông Dương.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','07', 'Ngày 29-7-1963 Hội nghị ban thường trực Uỷ ban Trung ương Mặt trận dân tộc giải phóng miền Nam quyết định lấy tên bãi biển Hirông - nơi quân đội và nhân dân Cuba đánh bại cuộc đổ bộ xâm lược của Mỹ - đặt tên cho một đơn vị Quân giải phóng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','07', 'Đoàn đại biểu Hội Nhà báo Việt Nam và Đoàn Đại biểu Hội Nhà báo yêu nước và Dân chủ miền Nam Viêt Nam đã họp hội nghị tại thủ đô Hà Nội vào trung tuần tháng 7-1976 để bàn việc thống nhất hai tổ chức hội.\nHội nghị đã quyết định tổ chức thống nhất của những người làm báo trong cả nước mang tên là: Hội Nhà báo Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','08', 'Ngày 13-8-1942, mang tên gọi là Hồ Chí Minh, đồng chí Nguyễn Ái Quốc rời Cao Bằng sang Trung Quốc, với danh nghĩa là đại biểu của Việt Nam Độc lập Đồng minh và Phân bộ Quốc tế phản xâm lược của Việt Nam để tranh thủ sự viện trợ quốc tế. Ngày 29-8, Người bị nhà cầm quyền địa phương của Tưởng Giới Thạch bắt giữ tại thị trấn Túc Vinh (Tĩnh Tây, Quảng Tây, Trung Quốc). Người bị giam giữ, đối xử tàn tệ suốt 13 tháng, bị giải qua hơn 30 nhà tù thuộc 13 huyện tỉnh Quảng Tây. Chính trong thời gian bị cầm tù này, Người đã làm hơn 100 bài thơ chữ Hán, sau đó được tập hợp lại trong một cuốn sách nổi tiếng \"Nhật ký trong tù\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','08', 'Ngày 29-8-1958, Chủ tịch Hồ Chí Minh đã dự hội nghị Bộ Chính trị Trung ương Đảng bàn về kế hoạch mở rộng thành phố Hà Nội. Người nêu rõ: \"Mở rộng thành phố phải cǎn cứ vào thiên thời (mưa, gió, nắng...), địa lợi (địa chất, sông hồ... và nhân hoà (lợi ích của nhân dân, của Chính phủ). Công tác quy hoạch thành phố phải hợp lý, bảo đảm được cả về kinh tế, mỹ quan và quốc phòng, phải có kế hoạch vận động quần chúng tham gia, có ban phụ trách để chịu trách nhiệm, tránh lối làm đại khái, lãng phí\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','08', 'Đáp ứng nguyện vọng tha thiết của toàn dân, Trung ương Đảng, Quốc hội và Chính phủ ta đã quyết định giữ gìn lâu dài thi hài Chủ tịch Hồ Chí Minh và xây dựng lǎng của Người tại Quảng trường Ba Đình lịch sử.\nNgày 2-9-1973 công trình xây dựng lǎng Chủ tịch Hồ Chí Minh được chính thức khởi công. Việc xây dựng lǎng được Liên Xô hết lòng giúp đỡ.\nNgày 29-8-1975, Trung ương Đảng, Quốc hội, Chính phủ và Mặt trận Tổ quốc Việt Nam tổ chức trọng thể lễ khánh thành lǎng Chủ tịch Hồ Chí Minh tại Hội trường Ba Đình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','08', 'Nhà thơ Xuân Quỳnh sinh nǎm 1942, quê ở huyện Hoài Đức, tỉnh Hà Tây, lớn lên ở Hà Nội.\nTừ nǎm 1963, chị làm báo, biên tập viên của nhà xuất bản\nTrước đó Xuân Quỳnh đã làm thơ khi còn là diễn viên múa.\nTác phẩm chính của chị gồm các bài thơ: \"Chồi biếc\", \"Hoa dọc chiến hào\", \"Gió Lào cát trắng\", \"Lời ru trên mặt đất\", \"Sân ga chiều em đi\", \"Hoa cỏ may\".\nThơ Xuân Quỳnh thể hiện một trái tim phụ nữ hồn hậu, chân thành, nhiều lo âu, và luôn da diết khát vọng hạnh phúc đời thường.\nXuân Quỳnh đột ngột vĩnh biệt cuộc đời vào ngày 29-8-1988 trong một tai nạn giao thông.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','08', 'Ngày 29-8-1994, Uỷ ban thường vụ Quốc hội nước ta đã ban hành Pháp lệnh quy định danh hiệu vinh dự Nhà nước \"Bà mẹ Việt Nam anh hùng\".\nTừ cuối nǎm 1994 đến tháng 4 nǎm 1997, Nhà nước đã phong tặng và truy tặng 38.805 \"Bà mẹ Việt Nam anh hùng\", trong đó có 11.048 bà mẹ còn sống. Riêng thủ đô Hà Nội có 637 bà mẹ được Nhà nước phong tặng và truy tặng danh hiệu \"Bà mẹ Việt Nam anh hùng\", trong đó có 131 bà mẹ còn sống.\nNhằm bảo đảm cho các bà mẹ có cuộc sống ổn định về vật chất, thanh thản về tinh thần, cùng với chính sách của Nhà nước, trong cả nước đã dấy lên phong trào phụng dưỡng \"Bà mẹ Việt Nam anh hùng\", \"Áo ấm tặng mẹ\", \"Áo lụa tặng bà\", \"Qùa tặng mẹ\" ...')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','09', 'Trung tướng Nguyễn Bình, tên thật là Nguyễn Phương Thảo sinh năm 1906, quê làng Giai Phạm, tỉnh Hưng Yên. Những năm 30 ông tham gia Quốc dân Đảng, cuộc khởi nghĩa Yên Bái thất bại, ông bị bắt và giam tại nhà tù Hoả Lò (Hà Nội). Ra tù, ông bí mật hoạt động chống Pháp và lập căn cứ ở Đông Triều. Sau Cách mạng Tháng Tám và Nam Bộ kháng chiến, ông được Chính phủ cử vào Nam lo việc thống nhất các lực lượng vũ trang. Nguyễn Bình được phong hàm trung tướng và thực hiện là Tổng chỉ huy chiến trường Nam Bộ. Gan dạ, táo bạo, thông minh và quyết đoán là đặc trưng tiêu biểu trong nhân cách của tướng Nguyễn Bình. Những chiến công buổi đầu của nhân dân Nam Bộ gắn liền với tên ông. Ông được nhân dân kính nể, quân thù khiếp sợ.\nNgày 29-9-1951, trên đường công tác trung tướng Nguyễn Bình đã hy sinh gần Biên giới Việt Nam - Campuchia.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','09', 'Ngày 29-9-1969, Bộ Chính trị Trung ương Đảng Lao động Việt Nam ra chỉ thị về đợt sinh hoạt Chính trị \"Toàn Đảng, toàn dân, toàn quân học tập và làm theo Di chúc của Hồ Chủ tịch\".\nChỉ thị vạch rõ \"Hồ Chủ tịch qua đời nhưng Người đã để lại cho ta một di sản rất quý báu. Đó là sự nghiệp vĩ đại, tư tưởng, đạo đức và tác phong của Người. Nhiệm vụ của toàn Đảng toàn dân ta là đoàn kết chặt chẽ chung quanh Ban Chấp hành Trung ương Đảng, quyết tâm làm theo Di chúc của Hồ Chủ tịch... \"\n\"Mục đích của đợt sinh hoạt chính trị này là: Làm cho mọi người thấy rõ hơn công lao và sự nghiệp vĩ đại của Hồ Chủ tịch đối với Đảng ta, nhân dân ta và đối với cách mạng thế giới, thấy rõ hơn phẩm chất cách mạng cao đẹp trong sáng của Hồ Chủ tịch, từ đó tăng thêm lòng tự hào, phấn khởi tin tưởng và quyết tâm vươn lên tiếp tục sự nghiệp Cách mạng của Hồ Chủ tịch, thực hiện bằng được Di chúc của Người\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','09', 'Ngày 29-9-1981, Hội đồng Bộ trưởng ban hành \"Điều lệ khen thưởng tổng kết thành tích kháng chiến chống Mỹ, cứu nước\". Điều lệ gồm có 11 điều gồm các chương: đối tượng và tiêu chuẩn, thủ tục xét đề nghị khen thưởng và điều khoản thi hành.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','11', 'Phạm Thận Duật sinh nǎm 1825 tại Ninh Bình và qua đời ngày 29-11-1885\nKhi thực dân Pháp xâm lược nước ta, ông được cử thay mặt triều đình đàm phán với Pháp. Ông là đại thần trong phe chủ chiến, cùng với Tôn Thất Thuyết tổ chức đưa vua Hàm Nghi ra Quảng Trị, lập cǎn cứ kháng chiến chống giặc Pháp. Phạm Thận Duật nhận nhiệm vụ ra Bắc bắt liên lạc và tổ chức lực lượng vũ trang. Đi đến đò Hà Trung, ông bị giặc bắt, giải về Huế, rồi đưa ra Côn Đảo. Ông bị ốm và chết ngay trên tàu biển.\nPhạm Thận Duật là một nhà yêu nước và là nhà vǎn hoá của nước ta ở thế kỷ XIX.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','11', 'Nhà báo, nhà thơ Hoàng Lộc sinh nǎm 1922 tại Hải Dương, mất ngày 29-11-1949 tại Phú Thọ.\nCuộc kháng chiến chống Pháp bùng nổ (1946), ông vào quân đội và làm phóng viên mặt trận của các tờ báo \"Xông pha\", \"Bắc Sơn\", \"Vệ quốc quân\". Các bài phóng sự của ông được xuất bản nǎm 1948 trong tập sách nhan đề \"Chặt gọng kìm đường số 4\" phản ánh sinh động cuộc chiến đấu của quân và dân ta trong chiến dịch Việt Bắc thu đông 1947.\nHoàng Lộc được nhiều người biết đến qua bài thơ \"Viếng bạn\" ngắn gọn chân thực, nói lên lòng cǎm thù giặc sâu sắc và tình yêu thương đồng đội thiết tha. Đây là một trong số những bài thơ hay của thơ ca thời kháng chiến chống thực dân Pháp xâm lược.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','11', 'Ngày 29-11-1969, Bộ Chính trị Trung ương Đảng họp ra quyết định về việc \"giữ gìn thi hài Chủ tịch Hồ Chí Minh nguyên vẹn và lâu dài, chống được các biến động có hại của khí hậu, thời tiết, có kế hoạch giữ gìn an toàn\", và xây dựng lǎng ở khu Ba Đình lịch sử với yêu cầu \"Thể hiện được tính cách hiện đại mà vẫn giữ mầu sắc dân tộc, trang nghiêm nhưng giản dị\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','11', '29-11-1975, thiết lập mối quan hệ ngoại giao ở mức đại sứ giữa Việt Nam và nước Cộng hoà Síp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','11', 'Ngày 29-11-1977, Khai mạc Hội nghị Vǎn hoá thông tin vùng các dân tộc ít người. Đại biểu của hơn 60 dân tộc trong cả nước đã về Hà Nội tham dự. Đây là Hội nghị lần đầu tiên bàn về vấn đề vǎn hoá ở các vùng dân tộc ít người.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','11', 'Ngày 29-11-1986, tổ máy số 4, tổ máy cuối cùng của Nhà máy Nhiệt điện Phả Lại được lắp đặt hoàn chỉnh và hoà điện vào lưới điện quốc gia.\nNhà máy Nhiệt điện Phả Lại được xây dựng từ tháng 10-1981, có sản lượng điện 2 tỷ kilôoat giờ một nǎm.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','12', 'Ngày 29-12-1964 khánh thành xưởng luyện cốc và xưởng liên kết, hai công trình lớn và những khâu rất quan trọng có tác dụng to lớn về mặt kinh tế trong toàn bộ dây truyền sản xuất liên hợp của khu gang thép Thái Nguyên. Mẻ than cốc đầu tiên đã được ra lò với chất lượng tốt.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','12', 'Đêm 29-12-1972 diễn ra trận đánh cuối cùng đợt tập kích chiến lược bằng máy bay B52 của giặc Mỹ vào thủ đô Hà Nội và thành phố cảng Hải Phòng.\nNhư vậy, sau 12 ngày đêm chiến đấu liên tục vô cùng dũng cảm và mưu trí, quân và dân miền Bắc đã chiến thắng vẻ vang, bắn rơi 81 máy bay, có 34 chiếc B52, 5 chiếc F111, tiêu diệt và bắt hàng trǎm giặc lái, bắn cháy 9 tàu chiến. Trong chiến thắng chung đó, quân dân thủ đô Hà Nội đã lập nên một \"Điện Biên Phủ trên không\" bắn rơi 23 máy bay B52, 2 chiếc F111 và nhiều máy bay phản lực khác, góp phần đập tan \"thần tượng B52\" của không quân chiến lược Hoa Kỳ và bẻ gẫy âm mưu bè lũ Níchxơn hòng buộc ta trở lại Hội nghị Pari với thế yếu trong cuộc đàm phán.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '29','12', 'Ngày 29-12-1986, Quốc hội khoá 8 đã thông qua Luật hôn nhân và gia đình\nLuật này quy định: Những nguyên tắc của chế độ hôn nhân và gia đình, việc kết hôn, nghĩa vụ và quyền của vợ chồng, nghĩa vụ và quyền của cha mẹ và con, và việc ly hôn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '29','12', 'Trong phiên họp ngày 29 và 30-12-1921 của Đại hội đại biểu toàn quốc lần thứ nhất của Đảng cộng sản Pháp tại thành phố Mácxây, với tư cách tham gia chủ tịch đoàn, đồng chí Nguyễn Ái Quốc đã yêu cầu nghiên cứu và xây dựng một chính sách thuộc địa theo đúng tư tưởng cộng sản chủ nghĩa của Đảng. Để thực hiện việc đó, Người kiến nghị thành lập Ban nghiên cứu thuộc địa của Đảng; Ban đó sẽ khởi thảo chính sách thuộc địa.\nVào nǎm 1922, Ban nghiên cứu thuộc địa của Phân bộ Pháp của Quốc tế cộng sản được thành lập. Trụ sở đóng ở số nhà 120, phố Laphayetơ - Pari.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','01', 'Cuối nǎm 1788, lợi dụng sự cầu cứu của Lê Chiêu Thống, 29 vạn quân Thanh vào chiếm đóng kinh thành Thǎng Long và kiểm soát phần lớn đất Bắc Hà.\nĐúng đêm giao thừa tết Kỷ Dậu 1789, quân Tây Sơn bất ngờ tấn công tiêu diệt đồn tiền tiêu của giặc ở Gián Khẩu. Sau 5 ngày đêm tiến công thần tốc, đạo quân chủ lực của Quang Trung đã phá tan hệ thống phòng chống của địch, tiến về Thǎng Long. Mờ sáng ngày 30-1-1789 (tức mồng 5 tết, quân ta tiêu diệt toàn bộ quân địch ở đồn Ngọc Hồi và đồn Khương Thượng (Đống Đa, Hà Nội ngày nay). Kinh thành Thǎng Long được giải phóng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','01', 'Ngày 30-1-1946, Chủ tịch Hồ Chí Minh ký sắc lệnh thành lập Nha thể dục Trung ương tại Bộ Thanh niên (tiền thân của Tổng cục Thể dục thể thao sau này). Ngày nay được coi là ngày ra đời của ngành thể dục thể thao cách mạng nước ta.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','01', 'Chiều tối 30-1-1957 (giao thừa nǎm Đinh Dậu), Chủ tịch Hồ Chí Minh đã đi chúc tết công nhân nhà máy điện Yên Phụ ở khu tập thể An Dương và đồng bào lao động ở bãi Phúc Xá.\nSau khi ân cần hỏi về mua sắm ǎn tết, Bác Hồ cǎn dặn mọi người ǎn tết vui vẻ, tiết kiệm, sau các ngày nghỉ phải tiếp tục tǎng gia sản xuất. Đối với các cháu nhỏ, Bác Hồ dặn phải vâng lời bố mẹ, kính thầy yêu bạn, chǎm chỉ học tập, ǎn ở sạch sẽ và Bác đã chia kẹo cho các cháu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','01', 'Đêm 30 rạng ngày 31-1-1968 (tết Mậu Thân), quân và dân miền Nam ta nổi dậy tiến công địch ở 64 thành phố, thị xã, thị trấn và nhiều vùng nông thôn ở miền Nam. Trong 45 ngày (từ 30 tháng giêng đến 15-3-1968), ta đã giết, làm bị thương và bắt sống gần 150 nghìn tên địch, trong đó có 43 nghìn tên Mỹ và chư hầu. 1 triệu 600 nghìn đồng bào đã giành được quyền làm chủ ở 100 xã, hơn 600 thôn, ấp được giải phóng.\nTổng tiến công tết Mậu Thân 1968 ở miền Nam và các chiến công của ta ở miền Bắc dẫn tới thay đổi cục diện chiến tranh: Mỹ phải ngừng đánh phá miền Bắc và chấp nhận hoà đàm 4 bên ở Pari.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','01', 'Từ ngày 30-1 đến ngày 23-3-1971, quân dân ta có sự phối hợp chiến dấu của quân dân Lào, đã đánh bại cuộc hành quân lớn của quân Mỹ, Ngụy ở khu vực đường 9 Nam Lào. Ta đã loại khỏi vòng chiến đấu hơn 2 vạn tên Mỹ - Ngụy, phá huỷ 1.100 xe quân sự, hơn 100 khẩu pháo lớn, bắn rơi 55 máy bay lên thẳng, bắt hơn 1000 tù binh, thu hơn 3000 khẩu súng. Quân ngụy Sài Gòn bị tiêu diệt nặng. Lần đầu tiên trong cuộc kháng chiến chống Mỹ, quân đội ta giành thắng lợi trong một chiến dịch phản công quy mô lớn, đánh tiêu diệt quân chủ lực Ngụy.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','03', 'Cuối tháng 3-1929, nhóm những người tích cực trong Việt Nam thanh niên Cách mạng đồng chí Hội ở Bắc Kỳ, nhận thấy sự bức thiết phải thành lập một tổ chức Cộng sản - trong khi Tổng bộ Việt Nam Thanh niên Cách mạng đồng chí Hội ở nước ngoài chưa đặt vấn đề đó ra - nên đã quyết định thành lập tổ chức cộng sản đầu tiên. Tổ chức này gồm 7 người là: Ngô Gia Tự, Nguyễn Đức Cảnh, Trần Vǎn Cung, Trịnh Đình Cửu, Đỗ Ngọc Du, Dương Hạc Đính, Kim Tôn, Tại nhà 5Đ phố Hàm Long, Hà Nội, nhóm cộng sản này đặt ra nhiệm vụ sẽ chỉ đạo sự chuyển hướng của Việt Nam Thanh niên cách mạng đồng chí Hội ở Bắc Kỳ. Sự kiện này có ý nghĩa thúc đẩy mạnh mẽ quá trình thành lập chính đảng Cộng sản phù hợp với yêu cầu của cách mạng Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','03', 'Ngày 30-3-1938, vua Bảo Đại ra đạo dụ tách quần đảo Hoàng Sa khỏi địa hạt tỉnh Nam Ngãi nhập vào tỉnh Thừa Thiên. Đạo dụ ghi rõ: Các cù lao Hoàng Sa thuộc về chủ quyền nước Nam đã lâu đời \nTrước khi thực dân Pháp xâm lược nước ta, hai quần đảo Trường Sa và Hoàng Sa từ lâu đã là bộ phận của lãnh thổ Việt Nam. Khi Pháp xâm lược và biến Việt Nam thành thuộc địa - Chính phủ Pháp là người đại diện cho người Việt Nam thực hiện quản lý 2 quần đảo này. Trong chiến tranh thế giới lần thứ hai, quân Nhật chiếm Trường Sa và Hoàng Sa làm cǎn cứ quân sự. Tháng 9-1951, tại Hội nghị quốc tế họp tại Xanphranxitxcô (Mỹ), Nhật đã long trọng cam kết từ bỏ mọi danh nghĩa và đòi hỏi đối với quần đảo Trường Sa và Hoàng Sa. Cũng tại Hội nghị này đại diện chính quyền Bảo Đại tuyên bố khẳng định chủ quyền việt Nam đối với Trường Sa và Hoàng Sa. Từ khi đất nước thống nhất, quần đảo Hoàng Sa thuộc tỉnh Quảng Ninh và Trường Sa thuộc tỉnh Khánh Hoà.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','03', 'Từ ngày 30-3 đến 24-4-1954 quân ta tấn công đợt 2 vào hệ thống cứ điểm của Pháp ở Điện Biên Phủ tại phía đông quân khu trung tâm Mường Thanh, làm chủ phần lớn dãy đồi phía đông và một bộ phận cánh đồng Mường Thanh. Chiến dịch Điện Biên Phủ đến lúc này đã thể hiện rõ ý chí và khả nǎng cũng như cách đánh của ta. Quân Pháp sa vào thế lúng túng đã phải nhờ đến sự giúp đỡ của cầu hàng không Mỹ làm nhiệm vụ tiếp tế.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','03', 'Tối 30-3-1956, Hồ Chủ tịch đã tới thǎm và nói chuyện với lớp bình dân học vụ khu lao động Lương Yên, Hà Nội.\nHọc viên ở đây là những người từ 16 tuổi trở lên chưa biết chữ, có cả cụ già và các chị có con mọn. Bác Hồ ân cần hỏi han tình hình học tập của các học viên và Người nói:\n\"Đảng và Chính phủ rất mong muốn đời sống của nhân dân, công nhân, công chức và bộ đội ngày càng khá hơn. Đời sống ví như chiếc thuyền. Sản xuất ví như nước. Mực nước lên cao thì con thuyền nổi lên cao.\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','03', 'Vào khoảng 11 giờ sáng ngày 30-3-1965, một sự kiện đáng ghi nhớ trong lịch sử chống ngoại xâm của nước nhà: một chiến sĩ giải phóng quân lái một chiếc xe hơi tới vùng ngã ba đường Võ Di Nguy và Nguyễn Công Trứ (Sài Gòn) dừng lại trước sứ quán Mỹ. Cùng lúc đó một chiến sĩ khác cũng lái một chiếc xe hơi đến, người chiến sĩ thứ nhất nhảy từ xe của mình sang xe này rồi phóng đi. Cảnh sát ngụy bắn theo nhưng không kịp. 35 giây sau đó, một tiếng nổ lớn phát ra từ chiếc xe hơi của anh giải phóng để lại và một đám khói bốc lên cao hơn 9 thước. Chất nổ phá nát tầng dưới của ngôi nhà sứ quán Mỹ làm chết 168 tên Mỹ (có 1 tướng) và 49 tên Mỹ khác bị thương trong đó có phó đại sứ Mỹ Alếchdít Giônxơn; khoảng 20 xe dân dụng và quân sự Mỹ bị phá huỷ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '30','03', 'Ngày 30-3-1746 là ngày sinh của Phơranxitxô Gôia, hoạ sĩ nổi tiếng Tây Ban Nha.\nThời gian đầu Gôia là hoạ sĩ trong cung đình. Tuy phục vụ nhà vua, nhưng với cách nhìn sâu sắc, các tác phẩm của ông lại đả kích quyết liệt bọn quý tộc phong kiến. Ông châm biếm một cách sâu cay những con người cầm vận mệnh đất nước mà chỉ quan tâm đến ǎn chơi hưởng lạc. Khi cuộc cách mạng Tư sản Pháp bùng nổ, ông đã chào đón một cách nhiệt tình, nhưng cũng phê phán cuộc chiến tranh xâm lược của Napôlêông vào Tây Ban Nha. Ông đã vẽ tranh tố cáo của quân xâm lược và ca ngợi tinh thần yêu nước của nhân dân.\nVới khuynh hướng tự do chủ nghĩa, ông đã sáng tạo 14 bức tranh tường mang tên \"Cǎn nhà của người điếc\" được coi như mở đầu nền hội hoạ hiện đại. Ông cũng chú ý đến vẻ kiều diễm nét khêu gợi của phụ nữ, được thể hiện trong những bức chân dung \"Nữ công tước Vxana\" \"Nàng Mada khoả thân\". Do bức tranh khoả thân này ông bị đưa ra toà án giáo hội như một chứng cứ về hành động chống giáo hội. Cuối đời ông phiêu bạt sang Pháp và chết ở nơi đất khách quê người vào nǎm 1828.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('1', '30','04', 'Ngày Giải phóng miền Nam, Thống nhất Đất nước')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','04', 'Ngày 30-4-1969 UBTVQH khoá III ra thông cáo thông qua Điều lệ Hợp tác xã nông nghiệp. Việc thông qua điều lệ này tạo điều kiện củng cố và hoàn thiện quan hệ sản xuất mới ở nông thôn, bảo đảm cho sản xuất nông nghiệp phát triển toàn diện mạnh mẽ và vững chắc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','04', 'Từ ngày 30-4 đến 30-6-1970 Quân giải phóng miền Nam, với tinh thần \"cứu bạn như cứu mình\" đã cùng quân dân Cǎmpuchia chiến đấu dũng cảm, đập tan cuộc hành quân xâm lược Cǎmpuchia của 10 vạn quân Mỹ và nguỵ Sài Gòn loại khỏi vòng chiến đấu 17.000 lính Mỹ nguỵ, giải phóng hoàn toàn 5 tỉnh thuộc vùng Đông Bắc Cǎmpuchia và phần lớn nông dân của 10 tỉnh khác, hình thành một vùng giải phóng rộng lớn với 4 triệu rưỡi dân.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','04', '5 giờ ngày 30-4-1975 quân ta mở đợt tấn công cuối cùng. Vào lúc 10h45, xe tǎng 390 của anh Vũ Đǎng Toàn, Tập Nguyên Phương dẫn đầu lực lượng đột kích thọc sâu của Quân đoàn 2 đánh chiếm dinh Độc Lập. Cán bộ, chiến sĩ Trung đoàn bộ binh 66 Sư đoàn 304 và Lữ đoàn xe tǎng 203 Quân đoàn 2 là những đơn vị đầu tiên cùng một số chiến sĩ biệt động đánh vào sào huyệt địch buộc Tổng thống nguỵ quyền và toàn bộ nội các địch đầu hàng. Cờ giải phóng tung bay trước toà nhà chính của Dinh Độc Lập lúc 11h 30 phút.\nCùng ngày, Bộ Chính trị gửi điện khen quân và dân Sài Gòn - Gia Định, khen ngợi toàn thể cán bộ, chiến sĩ, Đảng viên và đoàn viên thuộc các đơn vị bộ đội chủ lực, bộ đội địa phương, bộ đội tình nguyện, dân quân, tự vệ đã chiến đấu cực kỳ anh dũng, lập chiến công chói lọi, tiêu diệt và làm tan rã lực lượng lớn quân địch, buộc nguỵ quyền Sài Gòn phải đầu hàng không điều kiện, giải phóng Thành phố Sài Gòn - Gia Định, đưa chiến dịch lịch sử mang tên Bác Hồ vĩ đại đến toàn thắng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '30','04', '30-4-1945, sau khi đánh thắng nhiều trận lớn ở các chiến trường châu Âu, Quân đội Liên Xô đã cắm lá cờ chiến thắng trên nóc nhà Quốc hội Đức ở Béclin. Hítle, tên chùm phát xít Đức phải tự sát, chế độ quốc xã cáo chung.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '30','04', 'Tại hội nghị ngày 30-4-1948 tại Bôgôta Thủ đô của Côlômbia, 21 nước cộng hoà ở châu Mỹ đã ký tuyên ngôn thành lập Tổ chức các nước châu Mỹ (viết tắt là OEA).\nHiện nay OEA gồm có 34 nước thành viên\nSau khi cách mạng Cuba thành công, Cuba đã bị loại ra khỏi tổ chức này một cách độc đoán và bất công, do sức ép thù địch của Mỹ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','05', 'Nhà vǎn, nhà nghiên cứu, dịch giả Phan Kế Bính sinh nǎm 1875, mất ngày 30-5-1921. Ông quê tại Hà Nội.\nNǎm 1906 ông đỗ cử nhân, không ra làm quan, sống với nghề dạy học. Ông viết báo từ nǎm 1907, phụ trách phần Hán trong Đǎng Cổ Tùng báo. Rồi lần lượt cộng tác với các báo Đông Dương tạp chí, Trung Bắc tân vǎn.\nÔng đã có nhiều tác phẩm có giá trị như Nam Hải dị nhân, Hưng Đạo đại vương truyện, Việt Nam phong tục, Việt Hán vǎn khảo. Ông cũng là dịch giả các truyện Tam Quốc chí, Đại Nam điển lễ toát yếu, Việt Nam khai quốc chí truyện.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','05', 'Ngày 30-5-1946, nhân dân thủ đô Hà Nội thay mặt cho cả nước mít tinh tiễn Chủ tịch Hồ Chí Minh đi thǎm Pháp với tư cách là thượng khách của chính phủ Pháp. Trước khi lên đường, Hồ Chủ tịch đã nói chuyện với đồng bào cả nước mục đích chuyến đi và 4 điều cần làm để giúp cho cuộc ngoại giao thắng lợi. Đó là: \n1. Đoàn kết chặt chẽ, tránh cho cuộc chia rẽ \n2. Ra sức cần kiệm cho khỏi nạn đói khổ.\n3. Ra sức gìn giữ trật tự, tuyệt đối tuân theo mệnh lệnh của Chính phủ\n4. Đối với kiều dân hữu bang phải tử tế, ôn hoà.\nĐồng thời người còn gửi tới đồng bào Nam Bộ một bức thư tâm huyết với lời khẳng định nổi tiếng: Đồng bào Nam Bộ là dân nước Việt Nam. Sông có thể cạn, núi có thể mòn, song chân lý kia không bao giờ thay đổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','05', 'Ngày 30-5-1946, báo \"Sao vàng\" (kế tục báo \"Quân giải phóng\") là cơ quan tuyên truyền, huấn luyện chiến sĩ của quân ủy hội, ra số đầu tiên.\nToà soạn báo \"Sao vàng\" ở nhà số 28 phố Triệu Quang Phục (nay là phố Hàng Bài) chủ bút là ông Trần Huy Liệu.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '30','05', 'Vônte Phrǎngxoa Mari aruee (Voltaire Francois Marie Arouet) vǎn sĩ, thi sĩ, kịch sĩ, sử gia, triết gia, và đại biểu xuất sắc của triết học thời kỳ Ánh sáng Pháp. Sinh ngày 22-11-1694 ở Pari. Ông tốt nghiệp đại học luật và làm việc ở toà án, sau chuyển sang viết vǎn. Ông sáng tác nhiều thơ ca, truyện ngắn, sử thi, khảo luận lịch sử và triết học. Nǎm 22 tuổi vì sáng tác những bài thơ châm biếm, ông bị bắt giam và trục xuất khỏi nước Pháp. Khi về nước Vônte đã cho xuất bản tập \"Những bức thư triết học\" nổi tiếng. Cuốn sách đả kích sự chuyên quyền của giáo hội, chính sách ngu dân và những tục lệ phong kiến lạc hậu.\nVề chính trị, Vônte tuy chống lại sự độc đoán của chính quyền chuyên chế nhưng lại đặt hy vọng vào những vị vua sáng suốt thấm nhuần triết học. Đối với nhân dân, ông bảo vệ tích cực những người bị áp bức nhưng không đồng ý bạo động. Ông đả kích kịch liệt Giáo hội thiên chúa giáo nhưng lại tuyên bố \"Nếu không có Thượng đế thì phải tạo ra Thượng đế\".\nVônte đã có một vai trò to lớn trong việc truyền bá tư tưởng ánh sáng và có ảnh hưởng Cách mạng mạnh mẽ đối với những phần tử tư sản tiên tiến trong cuộc đấu tranh chống chế độ phong kiến.\nVônte mất ngày 30-5-1778.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '30','05', 'Ghêoocghi Valentinovich Plêkhanôp, nhà hoạt động nổi tiếng của phong trào công nhân Nga và quốc tế, là người đầu tiên tuyên truyền chủ nghĩa Mác vào nước Nga.\nÔng sinh ngày 11-12-1856. Sau khi tốt nghiệp trung học, ông vào học trường sĩ quan lục quân Pêtecxbua, sau đó học ở Học viện Mỏ. Ông gia nhập tổ chức \"Dân tuý\", từng về nông thôn hoạt động Cách mạng bị Chính phủ Nga hoàng truy bức, phải trốn ra nước ngoài. Ở nước ngoài ông có điều kiện tiếp xúc với chủ nghĩa Mác và trở thành người Macxit đầu tiên của nước Nga. Nǎm 1883 ông thành lập nhóm Macxit - nhóm \"Giải phóng lao động\" - để phổ biến chủ nghĩa Mác. Ông còn dịch nhiều tác phẩm của Mác, Ǎngghen ra tiếng Nga và viết nhiều tác phẩm lý luận về chủ nghĩa Mác có giá trị.\nPlêkhanôp từng thay mặt những người phát xít Nga tham gia đại hội thành lập Quốc tế II ở Pari. Ông đứng trên lập trường Macxit, phê phán chủ nghĩa xét lại và trào lưu cơ hội chủ nghĩa khác trong phong trào công nhân quốc tế. Ông không coi trọng đúng mức vai trò của giai cấp nông dân trong cách mạng. Sai lầm đó là nguồn gốc đưa ông tới quan điểm Mensơvích về sau này.\nÔng mất ngày 30-5-1918.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','06', 'Ngày 30-6-1018, thiền sư Vạn Hạnh viên tịch. Ông quê ở xã Đình Bảng, huyện Tiên Sơn, tỉnh Bắc Ninh. Nǎm 21 tuổi ông đã đi tu, đứng đầu hàng thứ 12 dòng Thiền Nam Phương, hiểu sâu giáo lý đạo phật. Ông chủ trương nhập thế, thiền sư Vạn Hạnh đã giúp các vua Lê Đại Hành, Lý Thái Tổ chống ngoại xâm và xây dựng đất nước ta ở thế kỷ X và XI. Thiền sư Vạn Hạnh được tôn là Quốc sư.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','06', 'Được sự giúp đỡ tích cực của Đảng cộng sản Pháp, Đức và do sự hoạt động khéo léo bí mật của mình, đồng chí Nguyễn Ái Quốc đã vượt qua hàng rào bao vây của các nước đế quốc để đến Pêtơrôgrats - Liên Xô vào ngày 30-6-1923, chuẩn bị dự Đại hội lần thứ 5 Quốc tế cộng sản.\nĐây là lần đầu tiên Người đến Liên Xô, quê hương của Cách mạng tháng Mười. Trong thời gian của Liên Xô, người đi thǎm nhiều nơi để tìm hiểu chế độ ưu việt của chính quyền Xô Viết. Người làm việc với Quốc tế cộng sản, viết nhiều bài đǎng báo để trình bày quan điểm của mình về Cách mạng giải phóng dân tộc.\nNhững nǎm về sau, đồng chí Nguyễn Ái Quốc - Chủ tịch Hồ Chí Minh còn đến Liên Xô nhiều lần; nhưng lần đầu tiên đặt chân trên đất nước của Lênin vĩ đại mãi mãi là một ngày đáng ghi nhớ trong suốt đời hoạt động Cách mạng của Người.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','06', 'Đầu nǎm 1944 Đảng ta cử một số cán bộ tiếp xúc với một số trí thức và thanh niên yêu nước, giúp họ thành lập Đảng dân chủ Việt Nam (30-6-1944), một chính đảng Cách mạng của tư sản dân tộc và tiểu sản trí thức yêu nước và tiến bộ.\nĐảng dân chủ Việt Nam ra đời và tham gia vào Mặt trận Việt Minh đã đập tan âm mưu thâm độc của phát xít Nhật định lôi kéo tư sản dân tộc và các tầng lớp trí thức Việt Nam. \nĐảng dân chủ Việt Nam đã cùng đồng hành với dân tộc trong suốt chặng đường cứu nước qua hai cuộc chiến tranh thần thánh của dân tộc, dưới sự lãnh đaọ của Đảng cộng sản Việt Nam, để hoàn thành Cách mạng dân tộc dân chủ, thống nhất đất nước và xây dựng chủ nghĩa xã hội.\nSau khi đất nước thống nhất, nhiệm vụ của Đảng dân chủ Việt Nam đã hoàn thành sự nghiệp vẻ vang của mình và tự nguyện cùng với Đảng xã hội tự giải tán.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','06', 'Nghệ sĩ Nhân dân Nguyễn Nho Tuý sinh nǎm 1910 và qua đời vào ngày 30-6-1977.\nTrước Cách mạng tháng Tám nǎm 1945, ông đã được xếp là 1 trong 5 diễn viên xuất sắc của miền Trung nước ta. Từ sau nǎm 1954, ông đem tài nghệ của mình xây dựng đoàn tuồng Bắc. Ông đã đào tạo được nhiều học trò hiện đang đóng vai trò chủ chốt trên sàn diễn các đoàn tuồng trong cả nước.\nÔng còn là người có công lớn trong việc phục hồi, phát triển nghệ thuật tuồng truyền thống. Nghệ sĩ Nguyễn Nho Tuý đã được truy tặng danh hiệu \"Nghệ sĩ nhân dân\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','06', 'Ngày 30-6-1985 đã diễn ra lễ thông cầu Chương Dương sau 20 tháng thi công và xây dựng. Toàn bộ cầu dài 1.210,95 mét, rộng 19,76 mét. Đây là chiếc cầu lớn thứ hai, sau cầu Thǎng Long, bắc qua sông Hồng trên địa bàn Hà Nội, do cán bộ, công nhân Việt Nam tự thiết kế, thi công.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','06', 'Ngày 30-6-1990, Quốc hội nước ta đã thông qua Bộ luật hàng hải Việt Nam.\nViệc Quốc hội thông qua bộ luật này khẳng định vai trò, vị trí của một ngành kinh tế - kỹ thuật đặc thù, có tiềm nǎng lớn và mang tính chất quốc tế cao. Nó cũng đánh dấu bước ngoặt quan trọng trong lịch sử phát triển của ngành hàng hải nước ta.\nTừ khi có hiệu lực (ngày 1-1-1991) đến nay, Bộ luật hàng hải Việt Nam và các vǎn bản quy phạm pháp pháp luật khác về chuyên ngành được ban hành trong những nǎm gần đây đã thật sự làm tǎng thêm hiệu lực quản lý của nhà nước đối với các hoạt động hàng hải trong phạm vi cả nước, góp phần đẩy nhanh tốc độ phát triển và tǎng cường sự giao lưu, hoà nhập của ngành hàng hải Việt Nam với ngành hàng hải thế giới cũng như các nước trong khu vực Đông Nam Á.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','06', 'Quốc hội nước Cộng hoà xã hội chủ nghĩa Việt Nam khoá 8, kỳ họp thứ bảy, đã thông qua Luật Công đoàn ngày 30-6-1990.\nNội dung chủ yếu Luật này quy định: Quyền và trách nhiệm của Công đoàn, xử lý các vi phạm luật Công đoàn.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','07', 'Trường Đại Học Bách Khoa Hà Nội đã tổ chức lễ tốt nghiệp khoá đầu tiên vào ngày 30-7-1961, khóa này đã đào tạo được 633 kỹ sư công nghiệp thuộc các ngành: Cơ khí, dệt, điện, vô tuyến điện, địa chất, mỏ, luyện kim, xây dựng, cầu đường, thuỷ lợi, hoá chất, thực phẩm.\nĐây là những cán bộ kỹ thuật có trình độ đại học về công nghiệp đầu tiên kể từ sau khi có hoà bình lập lại (nǎm 1954)')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','07', 'Trước việc Đế quốc Mỹ đưa quân viễn chinh ồ ạt vào miền Nam Việt Nam, tiến hành cuộc \"Chiến tranh cục bộ\" chống lại nhân dân ta ngày 3-4-1965, Mặt trận giải phóng miền Nam Việt Nam ra tuyên bố, xác định quyết tâm chống Mỹ: \"Quân và dân miền Nam quyết đánh thắng giặc Mỹ trong bất kỳ tình thế nào, quyết đánh bại cuộc chiến tranh xâm lược của đế quốc Mỹ bất kỳ chúng mở rộng chiến tranh ấy đến mức nào\". Bản tuyên bố còn vạch ra 8 điều kiện dẫn chúng đến thất bại.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','07', 'Ngày 30, 31 tháng 7-1968 đã diễn ra Hội nghị đại biểu lần thứ hai của Mặt trận Liên minh dân tộc, dân chủ và hoà bình Việt Nam. Hội nghị đã thông qua chương trình chính trị: Cứu quốc, kiến quốc và thống nhất đất nước.\nNội dung chính gồm ba phần. Cứu quốc là \"Đoàn kết mọi lực lượng và nhân dân yêu nước, kiên quyết chống chiến tranh xâm lược, đánh đổ chế độ bù nhìn tay sai, thành lập chính phủ liên hiệp, giành độc lập, dân chủ và hoà bình\". Kiến quốc là \"Kiến tạo Nam Việt Nam thành một quốc gia độc lập, tự do, dân chủ, hoà bình, trung lập và thịnh vượng\". Vấn đề thống nhất là \"Chính phủ hai miền Nam - Bắc cùng nhau thương lượng để tiến tới hoà bình thống nhất Tổ quốc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','07', 'Tàu pha sông biển Định An 20 của Công ty Vận tải biển và thuê tàu Việt Nam có sức chở gần 2000 tấn, đã chở lương thực từ cảng Cần Thơ đến cảng Hà Nội ngày 30-7-1984. Đây là con tàu đầu tiên cỡ lớn thực hiện chuyến đi sông - biển - sông, nối liền hai con sông lớn nhất của Việt Nam là con sông Cửu Long với sông Hồng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','08', 'Đêm 30-8 rạng ngày 31-8-1917 300 binh sĩ do Đội Cấn chỉ huy đã phát động khởi nghĩa tại Thái Nguyên. Lương Ngọc Quyến được cử làm cố vấn kiêm Phó tư lệnh.\nNghĩa quân giết chết tên chúa ngục Thái Nguyên, giải phóng toàn bộ tù nhân, trong số đó phần lớn là tù chính trị trong các vụ đề Thám, Duy Tân, Đông Du. Nghĩa quân làm chủ Thái Nguyên suốt bảy ngày. Giặc Pháp đưa viện binh từ Hà Nội lên phản công dữ dội. Đội Cấn nêu cao tinh thần thà chết không hàng giặc, đã tự sát tại khu cǎn cứ Núi Pháo. Vì tàn phế, không đi được, Lương Ngọc Quyến cũng tự sát để nghĩa quân dễ dàng rút lui nhằm bảo tồn lực lượng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','08', 'Chấp hành chủ trương của Tỉnh uỷ Nghệ An phát động phong trào thực dân Pháp thực hiện yêu sách của nông dân, ngày 30-8-1930, Huyện uỷ Nam Đàn lãnh đạo 3.000 nông dân có vũ trang tự vệ biểu tình thị uy lên huyện lỵ, đòi chi huyện giải quyết các yêu sách. Bọn nha lại, lính tráng khiếp sợ đứng im. Quần chúng ập đến phá cửa nhà lao để giải phóng tù chính trị; vào công đường lục hồ sơ các vụ án đem đốt. Tri huyện phải ký tên, đóng ấn vào bản yêu sách của quần chúng, trong đó có lời ghi cam kết: \"Từ nay về sau, tri huyện không được nhiễu hại nhân dân\".\nThừa thắng, quần chúng kéo về làng thị uy, trừng trị bọn hào lý phản động, đồng thời thực hiện những yêu sách của mình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '30','08', 'Rathơphớt (Ernest Rutherford) sinh ngày 30-8-1871, là tác giả của nhiều phát minh mới quan trọng, nhà bác học Anh, đã đề xướng những quan niệm mới cơ bản trong vật lý học.\nNǎm 1919 ông đã đưa lý thuyết về phản ứng hạt nhân đầu tiên được thực hiện trong lịch sử khoa học. Lý thuyết này mở ra khả nǎng biến nguyên tố này thành nhiều nguyên tố khác, từ chì, đồng, sắt có thể qua bắn phá trở thành vàng. Với ý nghĩa ấy, ông đã viết tác phẩm \"Giả kim thuật mới\". Những tác phẩm nổi tiếng khác là: \"Hiện tượng phóng xạ\", \"Lịch sử các tia anpha trong các biến đổi hạt nhân\", \"Bức xạ phát ra từ các chất phóng xạ\".\nÔng được nhận giải thưởng Nôben về hoá học nǎm 1908 và được coi là một trong những nhà bác học lớn nhất của nhân loại. Ông mất ngày 19-10-1937.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '30','08', 'Hǎngri Bácbuýt (Henri Barbusse) là nhà vǎn Pháp, người giữ vai trò tiên phong trong việc tuyên truyền và phát triển tư tưởng vǎn học theo hướng thực hiện xã hội chủ nghĩa ở Pháp, sinh ngày 18-5-1873.\nÔng là người nhiệt thành ca ngợi cách mạng tháng Mười Nga. Nǎm 1923 ông ra nhập Đảng Cộng sản Pháp. Các tiểu thuyết và truyện ký của ông có :\"Những người van nài\", \"Địa ngục\", \"Khói lửa\", \"Ánh sáng\", \"Xiềng xích\", \"Stalin\"... trong số này tiểu thuyết \"Khói lửa\" được coi là viên gạch đặt nền móng cho vǎn học xã hội chủ nghĩa Pháp. Lê-nin khi nói về giá trị vǎn chương Bacbuýt đã nhận định: Ta có thể xem tiểu thuyết \"Khói lửa\" và \"Ánh sáng\" là những khẳng định đặc biệt rõ rệt về sự phát triển ý thức cách mạng của quần chúng đang diễn ra ở khắp nơi...\"\nBacbuýt được mời đi thǎm Liên Xô và qua đời tại Mátxcơva vào ngày 30-8-1935.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','09', 'Lý Chính Thắng tên thật là Nguyễn Đức Huỳnh, sinh năm 1917, quê ở huyện Hương Sơn, tỉnh Hà Tĩnh.\nÔng vào hoạt động cách mạng ở Nam Bộ, tham gia Thành uỷ Sài Gòn - Chợ lớn và gây dựng cơ sở Đảng ở khu vực Đakao.\nTháng 3-1945, ông ra Bắc để xin chỉ thị của Trung ương Đảng, sau đó lại trở về Nam, mang theo nghị quyết về khởi nghĩa vũ trang cho Xứ uỷ Nam Kỳ.\nĐầu năm 1946, ông được nhân dân thành phố Sài Gòn - Chợ Lớn bầu vào Quốc hội khoá I, sau ngày Nam Bộ kháng chiến, ông hoạt động trong ngành giao thông liên lạc tại vùng Sài Gòn bị giặc Pháp chiếm đóng. Giặc bắt, tra tấn cực hình, ông không khai báo, và đã mất tại bệnh viện Chợ Rẫy ngày 30-9-1946.\nNăm 1949, Chủ Tịch Hồ Chí Minh ký sắc lệnh truy tặng Lý Chính Thắng Huân chương Độc lập hạng nhì \"Vì đã lập nhiều thành tích oanh liệt và hy sinh anh dũng\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','09', 'Từ ngày 30-9-1974 đến ngày 8-10-1974 Hội nghị của Bộ Chính trị và Quân uỷ Trung ương họp tại Hà Nội để duyệt kế hoạch tác chiến chiến lược 1975. Hội nghị nhất trí đánh giá tình hình miền Nam như sau:\nQuân Ngụy ngày càng suy yếu. Lực lượng ta mạnh hơn hẳn địch ở miền Nam. Mỹ gặp khó khăn trong nước và thế giới. Ta tạo được một thế liên hoàn, đã tăng cường được lực lượng và dự trù vật chất. Phong trào đòi hoà bình, dân sinh, dân chủ, độc lập dân tộc, đòi lật đổ Thiệu lên cao.\nHội nghị rút ra kết luận: \"Mỹ đã rút khỏi miền Nam thì khó có khả năng nhảy lại vào miền Nam, và dù chúng có thể can thiệp như thế nào đi chăng nữa cũng không thể cứu vãn được nguy cơ sụp đổ của chính quyền Sài Gòn\". Hội nghị nhất trí thông qua dự thảo của Bộ Tổng tham mưu chọn chiến trường Tây Nguyên là hướng chiến trường chủ yếu trong cuộc tiến công lớn và rộng khắp năm 1975.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','09', 'Nhà sử học Văn Tân thật là Trần Đức Lức sinh năm 1913 quê ở Hà Tây, qua đời ngày 30-9-1988 tại thủ đô Hà Nội.\nÔng tham gia hoạt động Cách mạng từ năm 1930 bằng việc viết báo, viết sách.\nLà một nhà nghiên cứu văn học, sử học, Văn Tân để lại một số tác phẩm: \"Vượt ngục\", \"Từ điển Trung - Việt\", \"Văn học trào phúng Việt Nam\", \"Từ điển tiếng Việt\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','09', 'Kiến trúc sư Huỳnh Tấn Phát sinh năm 1913 tại huyện Bình Đại, tỉnh Bến Tre, qua đời ngày 30-9-1989.\nNăm 1933, ông bắt đầu học khoa kiến trúc sư trường Cao đẳng Mỹ thuật Đông Dương ở Hà Nội. Sau 5 năm học tập, ông đỗ thủ khoa và trở về Sài Gòn làm việc. Ông có biệt tài tổ chức không gian và khéo léo khai thác những tinh hoa của di sản kiến trúc truyền thống trong các thiết kế của mình.\nTừ trước Cách mạng Tháng Tám, ông đã tham gia các hoạt động yêu nước chống Pháp - Nhật. Tháng 8-1945, tham gia chính quyền ở Sài Gòn. Trong thời kỳ kháng chiến chống Pháp, ông giữ nhiều chức vụ quan trọng trong chính quyền ở Nam Bộ và đặc khu Sài Gòn - Chợ Lớn.\nSau Hội nghi Giơnevơ, ông được Đảng phân công hoạt động ở nội thành Sài Gòn, được bổ sung vào Thành uỷ. Năm 1959, ông ra vùng giải phóng, phụ trách công tác vận động trí thức, tư sản. Cuối năm 1960 ông được bầu làm Phó chủ tịch kiêm Tổng thư ký Uỷ ban Trung ương Mặt trận dân tộc giải phóng miền Nam. Tháng 6-1969, ông được bầu làm Chủ tịch Chính phủ Cách mạng lâm thời cộng hoà miền Nam.\nTừ năm 1976 đến khi qua đời, Kiến trúc sư Huỳnh Tấn Phát đã giữ chức vụ: Phó Thủ tướng Chính phủ, Phó chủ tịch Hội đồng Nhà nước, Chủ tịch đoàn Chủ tịch Uỷ ban Trung ương Mặt trận Tổ quốc Việt Nam.\nLà một trí thức yêu nước, một chiến sĩ cách mạng có nhiều cống hiến cho đất nước. Kiến trúc sư Huỳnh Tấn Phát đã được thưởng nhiều Huân chương cao quý và Giải thưởng Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','10', 'Ngày 30-10-1962, Hội đồng Chính phủ ra Nghị định thành lập Ngân hàng Ngoại thương nước Việt Nam dân chủ cộng hoà. Tính đến nay, Ngân hàng Ngoại thương Việt Nam đã có quan hệ với 250 ngân hàng các nước trên thế giới.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','10', 'Ngày 30-10-1978, quân dân tự vệ huyện Ba Vì (Hà Tây) được Uỷ ban thường vụ Quốc hội ra quyết định tặng danh hiệu \"Anh hùng lực lượng vũ trang nhân dân\".\nTrong kháng chiến chống thực dân Pháp, quân và dân Ba Vì đã góp phần chiến thắng oanh liệt ở điểm cao 400m và 600m, mở đầu thắng lợi cho chiến dịch hoà bình (năm 1952); có Vật Lại là kháng chiến nổi tiếng. Trong kháng chiến chống đế quốc Mỹ, quân và dân Ba Vì lập chiến công đầu: Bắn rơi 5 máy bay Mỹ, quân và dân Ba Vì còn dùng súng bộ binh góp phần bắn rơi chiếc máy bay Mỹ thứ 400 trên miền Bắc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','10', 'Ngày 30-10-1996, Việt Nam và Xôlômông đã chính thức thiết lập quan hệ ngoại giao ở cấp đại sứ.\nQuần đảo Xôlômông ở phía nam Thái Bình Dương gồm 10 đảo lớn, với diện tích 29.800 km2. Dân số có hơn 36 nghìn người. Thủ đô là thành phố Hôniara.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '30','10', 'Bác sĩ khoa mắt Vladirơ Pôtrôvich Philatốp sinh năm 1875, mất ngày 30-10-1956.\nÔng làm việc ở thành phố Ôđécxa, sau làm Viện trưởng Viện Nhãn khoa Ucraina.\nBác sĩ Philatốp đã ghép giác mạc thành công, nổi tiếng thế giới về phương pháp cấy \"nhau\" dưới da, tạo hưng phấn cho cơ thể, hoặc cho uống cao dịch trích từ \"nhau\", chữa các chứng bệnh loét ở mô mạc.\nÔng là Viện sĩ Viện Hàn lâm Liên Xô từ năm 1944.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','11', 'Ngày 30-11-1946, Chủ tịch Hồ Chí Minh đã ký sắc lệnh số 229 quy định: Các cơ quan quân sự trên toàn cõi Việt Nam đều đặt dưới quyền Bộ Quốc phòng.\nCùng ngày 30-11-1946, Hồ Chủ tịch đã ký sắc lệnh số 230 bổ nhiệm Bộ trưởng Bộ Quốc phòng Võ Nguyên Giáp giữ chức Tổng chỉ huy quân đội toàn quốc.\nHai sắc lệnh trên nhằm thống nhất sự chỉ huy của quân đội ta trong cả nước, phục vụ cho cuộc kháng chiến của nhân dân ta chống thực dân Pháp xâm lược.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','11', 'Ngày 30-11-1982 Hội đồng Nhà nước đã thông qua Pháp lệnh bảo vệ tài liệu lưu trữ quốc gia.\nPháp lệnh gồm 5 chương với 19 điều và khẳng định: Tài liệu lưu trữ quốc gia thuộc sở hữu toàn dân, do Nhà nước quản lý theo nguyên tắc tập trung, thống nhất, không một cơ quan, tập thể hoặc cá nhân nhân nào được chiếm làm của riêng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '30','11', 'Suýp Giônathan (Jonathan Swift) là nhà châm biếm vĩ đại. Ông sinh ngày 30-11-1669 tại Ailen, gốc người Anh.\nCuốn tiểu thuyết bất hủ \"Guliơ du ký\" đã làm ông nổi tiếng trên thế giới. Đây là tác phẩm châm biếm nước Anh nhưng đọc như truyện cổ tích nên được trẻ em thích thú.\nÔng qua đời ngày 19-10-1745.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '30','11', 'Mác Tuên sinh ngày 30-11-1835 và mất nǎm 1910. Ông là nhà vǎn Mỹ, một cây bút giàu chất hài hước, có ảnh hưởng lớn đến vǎn học Mỹ thế kỷ XX.\nTrong tiểu thuyết của Mác Tuên có các nhân vật thổ dân da đỏ, nguyên là những chủ nhân của châu Mỹ và các nhân vật da đen, những nạn nhân đau khổ của chế độ phân biệt chủng tộc Mỹ.\nMác Tuên đã trở thành nhà vǎn nổi tiếng với các tiểu thuyết \"Những cuộc phiêu lưu của Hấc Fin\" (nǎm 1884), được cả bạn đọc trẻ em và người lớn yêu thích.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '30','11', 'Ionescô Ơgennơ (Ionesco Eugene) nhà viết kịch, viện sĩ Viên Hàn lâm Pháp. Ông sinh ngày 30-11-1912.\nCác tác phẩm tiêu biểu của ông là Nữ ca sĩ hói đầu, Những cái ghế, Bài học, Nạn nhân của nghĩa vụ... Ông là một trong những người có đóng góp cho sự cách tân về nghệ thuật, có ảnh hưởng đến sân khấu hiện đại. Các tác phẩn của ông được trình diễn khắp thế giới, được công chúng phương Tây say mê.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '30','11', 'Ngày 30-11-1967, Cộng hoà Nhân dân Nam Yemen giành độc lập từ Anh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','12', 'Ngày 30-12-1922, theo nguyện vọng và vì tình đoàn kết hữu nghị, cũng như sự gắn bó lâu đời giữa các dân tộc, nhằm bảo đảm xây dựng một quốc gia chung đi theo con đường xã hội chủ nghĩa, Liên bang Cộng hoà xã hội chủ nghĩa Xô viết (Liên Xô) được thành lập gồm các nước cộng hoà; Liên bang Nga, Tranxcauxia, Ucraina, Bêlôruxia. Đến nǎm 1941, Liên bang cộng hoà xã hội chủ nghĩa Xô Viết trở thành đại gia đình gồm 15 nước cộng hoà. Nhà nước Liên Xô đã góp phần xây dựng hệ thống chủ nghĩa xã hội chủ nghĩa trên thế giới. Nǎm 1991, Liên Xô tan rã, chấm dứt 69 nǎm tồn tại.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '30','12', 'Ngày 30-12-1966, Chủ tịch Hồ Chí Minh đã đến dự Đại hội Anh hùng chiến sĩ thi đua chống Mỹ cứu nước tại thủ đô Hà Nội.\nTại đây, Hồ Chủ tịch đã nói:\n\"Các cô, các chú được tuyên dương là Anh hùng phải nhận rõ rằng, vinh dự đó là vinh dự chung của tập thể, chứ không chỉ là của riêng từng người. Nếu không có Đảng, không có nhân dân, không có tập thể thì mọi người không trở thành Anh hùng được. Có Anh hùng là vì có tập thể anh hùng, dân tộc anh hùng, Đảng anh hùng\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '30','12', 'Rađiớt Kíplinh (Rudyard Kipling) - nhà viết tiểu thuyết, nhà vǎn Anh, giải thưởng nôbel nǎm 1907, sinh ngày 30-12-1865 tại Ấn Độ.\nÔng vừa viết báo, làm thơ, viết truyện ngắn. Nǎm 23 tuổi đã nổi tiếng với tập thơ đầu tiên \"Departmental Ditties\". Ông là bậc thầy về truyện ngắn, nhất là truyện loài vật viết cho thiếu nhi, như \"Tập sách rừng rậm\", \"Truyện cho trẻ em như thế đấy\", \"Phần thưởng và các nàng tiên\". Về tiểu thuyết, nổi tiếng có \"Ánh nắng lụi tàn\", \"Những thuyền trưởng dũng cảm\", \"Kim\".\nÔng mất ngày 17-1-1936.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '30','12', 'Rômanh Rôlǎng (Romain Rolland) sinh nǎm 1866 và mất ngày 30-12-1944.\nÔng là nhà vǎn hiện thực, nhà nhân vǎn chủ nghĩa của vǎn học hiện đại Pháp. Ông còn là nhà nghiên cứu âm nhạc.\nRômanh Rôlǎng có nhiều tác phẩm, nổi tiếng hơn cả là bộ tiểu thuyết \"Giǎng Crixtôp\" gồm 10 tập. Bộ sách này được dịch ra nhiều thứ tiếng, và ông được giải thưởng Nôbel.\nSuốt cuộc đời mình, Rôlǎng dùng ngòi bút sắc sảo, đầy âm thanh kỳ diệu để đấu tranh cho hạnh phúc loài người, cho giá trị chân chính của con người. Ông thuộc thế hệ những nhà vǎn lớn và tiến bộ của nước Pháp.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','01', 'Sau Cách mạng Tháng Tám, ngân hàng Đông Dương vẫn ở trong tay bọn thực dân Pháp. Ngày 31-1-1946, Chính phủ cho phát hành giấy bạc Việt Nam ở phía nam vĩ tuyến 16. Đến tháng 8, giấy bạc này được lưu hành ra miền Bắc Trung bộ và cuối 1946 được lưu hành trong cả nước. Đồng bào trong cả nước đã nhiệt liệt hoan nghênh \"Giấy bạc cụ Hồ\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','01', 'Lê Thị Riêng sinh nǎm 1925, quê ở Bạc Liêu. Chị tham gia kháng chiến từ nǎm 20 tuổi.\nNǎm 1960, chị được bầu làm uỷ viên Uỷ ban Trung ương Mặt trận dân tộc giải phóng miền Nam và là Phó Hội trưởng Hội Liên hiệp phụ nữ giải phóng miền Nam. Trong một chuyến đi công tác, chị Riêng bị địch bắt vào tháng 5-1967, giam tại nhà lao Biên Hoà. Địch tra tấn rất dã man, chị vẫn không khai báo.\nNgày 31-1-1968, địch đã giết chị Lê Thị Riêng trên đường đến Hồng Bàng, Sài Gòn. Hiện nay ở thành phố Hồ Chí Minh có một đường phố mang tên Lê Thị Riêng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','01', '31-1-1950, Việt Nam Dân chủ Cộng hoà và Cộng hoà Dân chủ Nhân dân Triều Tiên thiết lập quan hệ ngoại giao.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','01', 'Từ ngày 31-1 đến 4-2-1977, Đại hội Mặt trận dân tộc thống nhất Việt Nam đã diễn ra tại thành phố Hồ Chí Minh, gần 500 đại biểu thuộc các tổ chức mặt trận, các chính Đảng và đoàn thể ở các tỉnh và thành phố trong cả nước đã về dự.\nĐại hội nhất trí thống nhất Mặt trận Tổ quốc Việt Nam, Mặt trận dân tộc giải phóng miền Nam Việt Nam và Liên Minh các lực lượng dân tộc dân chủ và hoà bình Việt Nam thành một Mặt trận dân tộc duy nhất, lấy tên là Mặt trận Tổ quốc Việt Nam. Đại hội đã thông qua chương trình và điều lệ của Mặt trận Tổ quốc Việt Nam. Đại hội đã cử ra Uỷ ban Trung ương Mặt trận Tổ quốc Việt Nam gồm 191 vị và Uỷ ban Trung ương Mặt trận đã cử ra Đoàn Chủ tịch gồm 45 vị do đồng chí Hoàng Quốc Việt làm Chủ tịch và đồng chí Tôn Đức Thắng làm Chủ tịch danh dự của Mặt trận Tổ quốc Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '31','01', 'Ngày 31-1-1966, Liên Xô phóng thành công trạm tự động Luna 9. Trạm đã đổ bộ xuống mặt trǎng. Đây là lần đổ bộ vệ tinh đầu tiên xuống mặt trǎng thành công, mở ra bước phát triển cho công tác nghiên cứu bề mặt mặt trǎng cũng như việc nghiên cứu vũ trụ, nhằm phục vụ con người vì mục đích hoà bình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '31','01', '31-1-1968, Nauru, vốn do Anh, Australia và New Zealand cùng kiểm soát từ Chiến tranh thế giới lần thứ nhất, được trao trả độc lập.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','03', 'Ngày 31-3-1968 sau hơn hai tháng tổng tiến công và nổi dậy trên toàn miền Nam, ở Huế, ta đã làm chủ thành phố 25 ngày, thành lập Chính quyền Cách mạng. Ở Sài Gòn - Gia Định, đặc công biệt động tiến công đồng loạt các cơ quan của Mỹ - Ngụy như Toà đại sứ Mỹ, dinh Tổng thống, Bộ Tổng tham mưu, Nha Cảnh sát, Đài phát thanh, Sân bay Tân Sơn Nhất... các lực lượng vũ trang trên toàn miền đồng loạt tiến công nhiều sở chỉ huy quân đoàn, sư đoàn tiểu khu, chi khu quân sự Ngụy, 45 sân bay, nhiều tổng kho bến tàu, cǎn cứ hải quân...\nCuộc tổng tiến công và nổi dậy tết Mậu Thân đã giành được thắng lợi có ý nghĩa chiến lược. Đây là đòn quyết định làm phá sản chiến lược \"chiến tranh cục bộ\" của đế quốc Mỹ, đánh bại ý chí xâm lược của chúng buộc Mỹ phải \"xuống thang chiến tranh\", tạo ra bước ngoặt, mở đầu thời kỳ đi xuống về chiến lược của đế quốc Mỹ trong cuộc chiến tranh xâm lược Việt Nam.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','03', 'Ngày 31-3-1968 Tổng thống Mỹ Giônxơn buộc phải tuyên bố ngừng ném bom miền Bắc từ vĩ tuyến 20 trở ra, cách chức tướng Oétmolen Tư lệnh quân Mỹ ở Việt Nam (22-3), thừa nhận thất bại của chiến lược \"Chiến tranh cục bộ\" và cử người đàm phán với đại diện Chính phủ ta tại Pari.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','03', '31-3-1975, Bộ Chính trị Trung ương Đảng họp tại Hà Nội. Sau khi nghe Quân ủy Trung ương báo cáo chiến thắng của ta trong chiến dịch Tây Nguyên và chiến dịch Huế - Đà Nẵng, Bộ Chính trị đã nhận định: Cuộc Tổng tiến công chiến lược của ta thực tế đã bắt đầu và trong ba tuần đã giành được thắng lợi cực kỳ to lớn. Chúng ta đã tiêu diệt và làm tan rã hơn 35% sinh lực địch, lần đầu tiêu diệt và loại khỏi vòng chiến đấu 2 quân đoàn của địch, hơn 40% binh khí kỹ thuật hiện đại của chúng bị mất. Ta thu và phá hơn 40% cơ sở vật chất và hậu cần của quân ngụy, giải phóng 12 tỉnh với gần 8 triệu người.\nBộ Chính trị quyết định mở cuộc tổng tấn công và nổi dậy giải phóng Sài Gòn trong thời gian sớm nhất, tốt nhất là trong tháng 4 nǎm 1975, không thể để chậm. Tư tưởng chỉ đạo là: \"Thần tốc, táo bạo, bất ngờ, chắc thắng\". Để động viên lực lượng cả nước, Bộ Chính trị thành lập Hội đồng chi viện chiến trường và giao cho thủ tướng Phạm Vǎn Đồng, Ủy viên Bộ Chính trị làm Chủ tịch.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '31','03', 'Sau Đại chiến thế giới lần thứ hai, với sự giúp đỡ và hy sinh to lớn của nhân dân và Hồng quân Liên Xô, hệ thống xã hội chủ nghĩa ra đời. Các nước Đông Âu trở thành thành viên gia đình xã hội chủ nghĩa, và cục diện cũng như lực lượng của cách mạng đã là nhân tố cho hoà bình thế giới. Nhân tố này đã khiến chủ nghĩa đế quốc phải nhìn nhận một cách đầy đủ. Những nǎm 60, Mỹ thực hiện tǎng cường vũ khí hạt nhân và phát động chiến tranh lạnh chống Liên Xô, chống hệ thống xã hội chủ nghĩa, đe doạ trực tiếp hoà bình thế giới. Để đối phó, các nước xã hội chủ nghĩa Đông Âu mà Liên Xô là nòng cốt, đã thành lập Tổ chức hiệp ước Vacsava. Lực lượng quân sự của tổ chức đã làm tròn nhiệm vụ duy trì hòa bình.\nKhi Liên Xô tan rã, hệ thống xã hội chủ nghĩa ở Đông Âu sụp đổ, ngày 31-3-1991 Tổ chức hiệp ước Vacsava chấm dứt hoạt động.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','05', 'Ngày 31-5-1965 dân quân dân tộc Mường ở xã Liên Hoà, huyện Lạc Sơn, tỉnh Hoà Bình lập chiến công đầu tiên cho tỉnh nhà: bắn rơi một máy bay của giặc Mỹ bằng súng trường.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','05', 'Ngày 31-5-1970, hàng nghìn nhà sư ở nhiều tỉnh miền Nam nước ta tuyệt thực 48 giờ, đòi chấm dứt chiến tranh xâm lược của giặc Mỹ, phản đối Nguyễn Vǎn Thiệu công nhận bọn đội lốt nhà sư, đòi trả tự do cho các nhà sư, sinh viên bị bắt, đồng thời tố cáo tình trạng sa sút nghiêm trọng về xã hội và kinh tế ở các vùng ngụy quyền kiểm soát.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','05', 'Quân và dân tỉnh Thừa Thiên đã đánh sập hoàn toàn cụm quân ngụy ở Kô Pung tiêu diệt sở chỉ huy hành quân trung đoàn số 54, hai đại đội bộ binh, hai đại đội háo binh, đánh thiệt hại nặng một đại đội khác, phá huỷ 11 khẩu đại bác.\nChiến thắng Kô Pung ngày 31-5-1971 là một đòn trừng trị đích đáng âm mưu \"Ngǎn chặn từ xa của\" Mỹ, ngụy trong cái gọi là \"cuộc hành quân Lam Sơn 720\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '31','05', 'Jôdep Hayđơn nhà soạn nhạc người Áo, một trong những nhạc sỹ kiệt xuất của trường phái cổ điển Viên, sinh ngày 11-4-1732 và qua đời ngày 31-5-1809. Ông là người đầu tiên hoàn thiện thể loại giao hưởng và loại hoà tấu thính phòng bộ tứ. Ông đã để lại cho đờimột di sản âm nhạc đồ sộ với đủ các thể loại: Ôpêra, giao hưởng, Côngsectô, nhạc thính phòng, hợp xướng, ôratôriô, ca khúc.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','07', 'Đồng chí Nguyễn Đức Cảnh sinh nǎm 1908 quê ở Thụy Hà, Thụy Anh, Thái Bình. Thuở nhỏ học ở Nam Định và tham gia các hoạt động chống Pháp tại địa phương. Nǎm 1925, 1926 tham gia tổ chức truy điệu nhà yêu nước Phan Chu Trinh, đòi ân xá Phan Bội Châu, bị Pháp đuổi khỏi trường học. Từ đó lên Hà Nội sinh sống và hoạt động cách mạng.\nNǎm 1928 đồng chí được cử vào Kỳ bộ Bắc Kỳ của \"Việt Nam Thanh niên Cách mạng đồng chí Hội\". Nǎm 1929, đồng chí là người đứng ra thành lập Tổng Công hội Bắc Kỳ. Ngày 3-2-1930, tham gia thành lập Đảng cộng sản Đông Dương với tư cách là đại biểu Đông Dương Cộng sản Đảng. Cuối nǎm 1930, được cử đi công tác ở Trung Kỳ, rồi được bầu vào Ban thường vụ Xứ uỷ Trung Kỳ, phụ trách tuyên huấn. Cuối nǎm 1931, bị bắt ở Vinh. Ngày 31-7-1932, đồng chí bị thực dân Pháp xử chém tại Hải Phòng, lúc đó đồng chí mới 24 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','07', 'Hội nghị cán bộ Trung ương Đảng Cộng sản Đông Dương lần thứ nhất họp ngày 31-7-1946. Vấn đề trung tâm Hội nghị thảo luận là củng cố và mở rộng mặt trận dân tộc thống nhất. Hội nghị nhận định chính sách xâm lược của thực dân Pháp từ khi có phái đoàn Chính phủ ta sang Pháp để mở cuộc đàm phán chính thức là: \"Về quân sự thì khiêu khích lấn dần, về chính trị thì chia rẽ để chờ thời cơ lật đổ Chính phủ ta. Hội nghị định rõ đường lối cơ bản của Đảng trong giai đoạn này là \"thống nhất bên trong, tìm bạn bên ngoài\" và \" tích cực sẵn sàng về quân sự và chính trị đối phó với những bất trắc có thể xảy ra\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '31','07', 'Đơni Điđơrô (Denis Diđerot) là nhà vǎn, nhà lý luận và phê bình, nhà triết học lớn của nước Pháp từ trần ngày 31-7-1784. Ông cùng với Đalambe, chủ biên bộ \"Bách Khoa toàn thư\" đầu tiên trên thế giới; xác định những quy tắc của thể loại kịch, sáng lập môn phê bình nghệ thuật.\nTác phẩm chính của Điđơrô gồm có: \"Những tư tưởng triết học\", \"Đứa con hoang\", \"Nữ tu sĩ\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '31','07', 'Phriđích Vuêlơ (Frederie Wohler) sinh ngày 31-7-1800. Nǎm 1920 ông vào trường đại học Hâyđenbéc. Hai nǎm sau ông đã điều chế được các dẫn xuất của Axít xyanhiđric và xác định thành phần của nó. Nǎm 1828, ông tổng hợp được một chất hữu cơ từ các chất vô cơ và chính điều này đã giáng một đòn vào học thuyết duy tâm về \"Sinh lực\" và kích thích sự phát triển của hoá học hữu cơ mà chính ông là người sáng lập.\nNǎm 1827, ông tìm ra phương pháp điều chế nhôm kim loại. Những công trình của Vuêơ đóng góp lớn vào việc nghiên cứu tính chất và các phương pháp điều chế những nguyên tố như: Canxi, phốtpho, titan, vanađi, niobi. Ông mất nǎm 1882.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '31','07', 'Prem Chanđơ là nhà vǎn hiện thực lớn của Ấn Độ. Ông sinh ngày 31-7-1880. Ban đầu ông đã viết truyện thiếu nhi, về sau viết khá nhiều truyện ngắn, phê bình vǎn học, dịch truyện.\nCác tác phẩm chính của ông là: \"Đất nước bỏng lửa\" là một bản cáo trạng hùng hồn đối với thực dân Anh. Tiểu thuyết \"Việc nhà, tổ ấm của tình yêu\", \"Gôđan\" là bộ tiểu thuyết kết tinh phong trào yêu nước chống đế quốc của nhân dân Ấn Độ. Ông là chủ tịch đầu tiên của Hội các nhà vǎn Ấn Độ. Ông đã có công làm cho hai ngôn ngữ Urơđu và Hinđu đạt đến một ngữ pháp chuẩn mực, một khả nǎng diễn đạt giản dị, trong sáng. Ông mất nǎm 1936.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '31','07', 'Vào ngày 31-7-1886, Pharen Lixtơ qua đời ở quê hương Hunggari của ông.\nSinh nǎm 1811, Lixtơ là nhà dương cầm điêu luyện, nhà soạn nhạc, nhà lý luận, nhạc trưởng lỗi lạc, nhà lý luận và nhà sư phạm. Từ nǎm 9 tuổi Lixtơ đã đi biểu diễn, nǎm 10 tuổi đã hoà chung với dàn nhạc, nǎm 12 tuổi đến Pari học tập, làm quen với nhiều nhạc sĩ nổi tiếng. Lixtơ thành công nhất trong lĩnh vực viết cho Piano và giao hưởng, thơ giao hưởng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','08', 'Ngày 31-8-1952, quân ta đột kích vị trí Phú Thọ của địch (ở gần Sài Gòn), phá huỷ một kho xǎng 3 triệu lít một kho dầu 2 triệu lít, một kho vũ khí gồm 100 quả bom loại 500 kg và 2 triệu viên đạn, tiêu diệt một đại đội lính Âu Phi')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','08', 'Tháng 8-1961, nhân dịp nhà danh họa Picátxô 80 tuổi Chủ tịch Hồ Chí Minh đã gửi tới người bạn hoạ sĩ bức thư chúc mừng.\nTrong thư Người viết: \"Đồng chí Picátxô thuộc vào những con người luôn luôn trẻ, bởi vì những người ấy sôi nổi trong tâm hồn và một tình yêu say mê cái thiện, cái mỹ, với hoà bình và nhân đạo. Tình yêu ấy đã dẫn dắt Picátxô đên với chủ nghĩa cộng sản, và vì thế, hoạ sĩ mãi mãi giữ được tuổi xuân.\nCon chim bồ câu hoà bình Picátxô vẽ, rất quen thuộc với nhân dân Việt Nam và nhân dân khắp thế giới, đã thể hiện một cách rực rỡ lòng tin mãnh liệt của nhà nghệ sĩ lớn ấy là sự vươn tới hoà bình không gì có thể ngǎn cản nổi của nhân dân các dân tộc\".')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','08', 'Ngày 31-8-1991 Hội đồng Bộ trưởng ra quyết định \"Tổ chức và sắp xếp mạng lưới các trường trong hệ thống giáo dục quốc dân\".\nMục tiêu nguyên tắc của việc sắp xếp lại mạng lưới là:\n1- Việc sắp xếp mạng lưới trường học không nhằm thu hẹp sự phát triển, mà phải tạo điều kiện để tiếp tục mở rộng qui mô, nâng cao chất lượng và hiệu quả của hệ thống giáo dục quốc dân.\n2- Xoá bỏ những bất hợp lý đang tồn tại trong mạng lưới trường học.\n3- Thực hiện đa dạng hoá các hình thức đào tạo chính quy và không chính quy.\n4- Gắn chặt quá trình đào tạo với quá trình sử dụng lực lượng lao động.\n5- Cải tiến mạnh mẽ công tác quản lý giáo dục đào tạo từ cơ sở trong trường học đến toàn ngành, theo hướng đảm bảo tính thống nhất.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '31','08', 'Ngày 31-8-1990, đại diện hai chính phủ Cộng hoà liên bang Đức và Cộng hoà dân chủ Đức ký kết \"Hiệp ước thống nhất\". Các bang của Cộng hoà dân chủ Đức sáp nhập vào Cộng hoà liên bang Đức. Thủ đô của nước thống nhất là Beclin.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','10', 'Cao Thắng sinh năm 1864 quê ở xã Sơn Lễ, huyện Hương Sơn, tỉnh Hà Tĩnh.\nÔng xuất thân trong một gia đình nghèo, nhưng chịu khó học tập, giỏi cả văn võ, thông minh, dũng cảm. Ông ra nhập nghĩa quân của Phan Đình Phùng và trở thành một tướng giỏi.\nKhi cụ Phan Đình Phùng ra Bắc đã giao quyền chỉ huy nghĩa quân cho Cao Thắng. Ông đã góp phần xây dựng lại lực lượng nghĩa quân, chế tạo vũ khí để đánh giặc. Ông làm ra những khẩu súng trường giống như của Pháp năm 1874.\nNgày 31-10-1893, Cao Thắng bị trúng đạn và hy sinh. Khi đó ông mới 29 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','10', 'Lưu Quý Quỳ gốc người xã Minh Hương, huyện Điện Bàn tỉnh Quảng Nam, sinh ngày 31-10-1919.\nTừ năm 1937, tham gia cách mạng, ông công tác qua các ban, ngành: Trợ lý Trưởng Ban Tuyên huấn Trung ương, Uỷ viên Uỷ ban Mặt trận Tổ quốc Việt Nam, Tổng thư ký Hội Nhà báo Việt Nam. Vụ trưởng vụ Báo chí Ban Tuyên huấn Trung ương Đảng.\nÔng viết báo, viết sách nhiều, tích cực hoạt động trên nhiều lĩnh vực, được tặng thưởng Huân chương kháng chiến chống Pháp và chống Mỹ hạng nhất. Huy hiệu Thành đồng Tổ quốc, Huy chương Danh dự Tổ chức quốc tế các nhà báo.\nNhững tác phẩm của ông được hoan nghênh: \"Nước về biển cả\", \"Công chúng mới\", \"Bài thơ Nam Bộ\", \"Miền Nam yêu quý\", \"Tác phong văn nghệ nhân dân\", \"Thực tiễn văn nghệ kháng chiến Nam Bộ\", \"Phút im lặng\"...\nSau cơn bệnh đột ngột khi đang công tác tại nước ngoài, ông từ trần ngày 1-8-1982, thọ 63 tuổi.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '31','10', 'Tướng quân Phrunde sinh ngày 2-2-1885 tại Cộng hoà Kiêcghidia.\nNăm 1906, ông gặp Lênin tại Thụy Điển và từ đó trở thành người đồng chí trung kiên của Lênin. Từ năm 1905 đến Cách mạng tháng Mười, ông hoạt động bí mật, nhiều lần bị bắt, bị đi đày.\nNăm 1924, Ông được cử làm Bộ trưởng dân uỷ phụ trách quân đội và hải quân, làm Phó chủ tịch Hội đồng quân sự cách mạng kiêm Tổng tham mưu trưởng Hồng quân nông - công, kiêm Giám đốc Học viện Quân sự cao cấp.\nNgày 31-10-1925, Ông đã từ trần ở Mátxcơva lúc mới 40 tuổi. Sau khi ông qua đời, Học viện cao cấp Hồng quân được mang tên Phrunde và tên họ của ông được gắn trên tường thành Kremli.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '31','10', 'Ngày 31-10-1952, Mỹ đã cho nổ bom khinh khí đầu tiên trên đảo Ênivetốc trên Thái Bình Dương. Nó đã được Thenlơ (Teller) thiết kế, là kết quả của quá trình nghiên cứu xuất phát từ bom nguyên tử, bắt đầu năm 1949 và hoàn thành vào năm 1951. Tháng 8 năm 1953, Liên Xô cho nổ một quả bom khinh khí tương tự. Tiếp theo là Anh (năm 1957), Trung Quốc (năm 1967) và Pháp (năm 1968).')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '31','10', 'Bà Inđira Ganđi sinh năm 1917 và bị kẻ thù sát hại ngày 31-10-1984, Bà là Thủ tướng Chính phủ Ấn Độ từ năm 1966 đến năm 1977, và từ năm 1980 đến năm 1984, là lãnh tụ xuất sắc của phòng trào Không liên kết, là người bạn thân thiết của nhân dân Việt Nam.\nSau khi bà từ trần, Thủ đô Hà Nội đã đổi tên vườn hoa Chí Linh thành vườn hoa Inđira Ganđi để ghi nhớ tình hữu nghị giữa nhân dân hai nước Việt Nam và Ấn Độ.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','12', 'Phùng Hưng sinh ngày 31-12-736, mất nǎm 790, quê ở xã Đường Lâm, nay thuộc thị xã Sơn Tây, tỉnh Hà Tây.\nTừ giữa thế kỷ VIII, bọn quan đô hộ nhà Đường bắt nhân dân ta đóng thuế rất nặng, ai cũng oán thán. Phùng Hưng đã lãnh đạo nhân dân nổi dậy chống lại chúng. Từ Sơn Tây, nghĩa quân dần dần chiếm được cả vùng rộng lớn xung quanh và thành Tống Bình (tức Hà Nội ngày nay). Ông sắp đặt lại việc cai trị với ý định xây dựng nước nhà độc lập lâu dài. Được 7 nǎm thì Phùng Hưng mất.\nNgày nay ở xã Đường Lâm có đình thờ ông, và ở phố Giảng Võ - Hà Nội còn lǎng mộ của ông.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','12', 'Cao Huy Đỉnh sinh ngày 31-12-1927 quê ở Nghệ An, mất nǎm 1975 tại Hà Nội\nÔng là nhà nghiên cứu vǎn học dân gian xuất sắc, từng làm việc ở Viện Vǎn học, Ban nghiên cứu Đông Nam Á (thuộc Uỷ ban khoa học và xã hội Việt Nam), là Phó tổng thư ký Hội Vǎn nghệ dân gian Việt Nam.\nCác nghiên cứu của ông về \"Người anh hùng làng Gióng\" (nǎm 1969) và \"Tìm hiểu tiến trình vǎn học dân gian Việt Nam\" được đánh giá cao.\nNgoài ra, ông còn có những đóng góp đáng kể trong việc giới thiệu và nghiên cứu vǎn hoá vǎn học Ấn Độ.\nNǎm 1996, Nhà nước truy tặng giải thưởng Hồ Chí Minh cho nhà nghiên cứu vǎn học Cao Huy Đỉnh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','12', 'Ngày 31-12-1954, Chủ tịch Hồ Chí Minh đã đến đặt vòng hoa tại Đài liệt sĩ Ba Đình, Hà Nội.\nĐứng trước hương trầm nghi ngút, Bác Hồ bùi ngùi rút khǎn lau nước mắt.\nThủ tướng Phạm Vǎn Đồng thay mặt Bác đọc điều từ:\n\"Hỡi các liệt sĩ!\nNgày mai là nǎm mới, là ngày đồng bào và bộ đội mừng Chính phủ về Thủ đô. Trong lúc cả nước vui mừng thì mọi người đều thương tiếc các liệt sĩ đã hy sinh vì Tổ quốc, vì dân tộc.\nMáu nóng của các liệt sĩ đã nhuộm lá Quốc kỳ vẻ vang càng thêm thắm đỏ. Tiếng thơm của các liệt sĩ sẽ muôn đời lưu truyền với sử xanh...\"')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','12', 'Nhà thơ Vân Đài tên thật là Đào Thị Minh, sinh nǎm 1903 tại Hà Nội, qua đời ngày 31-12-1964.\nBà làm thơ khá sớm. Nǎm 1943, thơ của bà in trong tập \"Hương Xuân\" cùng với thơ của nữ thi sĩ Hằng Phương, Mộng Tuyết và Anh Thơ.\nTừ Cách mạng Tháng Tám, bà nhanh chóng hoà vào cuộc sống mới, gia nhập bộ đội ngay từ năm đầu kháng chiến chống Pháp. Tình đồng đội, tình quân dân là nguồn cảm hứng trong hàng loạt bài thơ của bà. Sau nǎm 1954, bà chú ý viết về cuộc sống ấm no hạnh phúc ở nông thôn và sự đổi mới của phụ nữ. \"Mùa hái quả\" là tuyển tập thơ tiêu biểu cho hơn 30 nǎm lao động nghệ thuật nữ thi sĩ Vân Đài.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','12', 'Nǎm 1966, ở miền Bắc có chiến tranh ác liệt do Mỹ gây ra và liên tiếp bị thiên tai.\nTrong hoàn cảnh đặc biệt ấy, Thái Bình là tỉnh đầu tiên ở miền Bắc đạt mục tiêu 5 tấn thóc trên một héc ta/một nǎm, trên toàn bộ diện tích 8 vạn héc ta trồng lúa của tỉnh.\nĐây là một thắng lợi to lớn của ngành nông nghiệp đương thời. Hồ Chủ tịch đã gửi thư khen ngợi tỉnh Thái Bình.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','12', 'Nhạc sĩ Hoàng Việt sinh nǎm 1928, quê ở tỉnh Tiền Giang, hy sinh ngày 31-12-1967 ở miền Nam trên đường đi công tác trong cuộc kháng chiến chống Mỹ.\nÔng là tác giả của nhiều bài hát có giá trị nghệ thuật như: Lên ngàn, Lá xanh, Mùa lúa chín, Tình ca. Đặc biệt ông đã sáng tác bản nhạc giao hưởng \"Quê hương\", là bản giao hưởng đầu tiên của nền âm nhạc Việt Nam hiện đại. Nǎm 1996 ông được Nhà nước truy tặng giải thưởng Hồ Chí Minh.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('2', '31','12', 'Ngày 31-12-1976, tại thủ đô Hà Nội và thành phố Hồ Chí Minh đã tổ chức lễ thông xe đường sắt Thống Nhất dài 1730 km. Trong thời gian khôi phục đường sắt Thống Nhất, công nhân, bộ đội và nhân dân ta đã làm mới 626 km đường, trong đó gồm 475 cầu, 520 cống, 150 nhà ga.\nTừ khi khởi công đến lúc nối xong đoạn ray cuối cùng của tuyến đường là 14 tháng.')");
$db->query("INSERT INTO " . $db_config['prefix'] . "_" . $lang . "_" . $module_data . "_rows (catid, day, month, content) VALUES ('3', '31','12', 'Rôbớt Boilơ (Robert Boyle) sinh ngày 25-1-1627 trong một gia đình quý tộc Anh.\nVới công trình đầu tiên - cuốn \"Nhà hoá học hoài nghi\" (1661) - ông đưa ra ý kiến là hoá học có nhiệm vụ trả lời vấn đề thế giới vật chất gồm những gì, thành phần các chất ảnh hưởng như thế nào lên tính chất của chúng. Những phản ứng do ông nghiên cứu và các chất do ông phát minh có ứng dụng rộng rãi trong thực tế. Các dụng cụ thí nghiệm do ông sáng tạo để xác định thể tích của chất khí và chất lỏng, cho đến nay vẫn được sử dụng. Những ý tưởng độc đáo, mới mẻ của ông đã kích thích sự phát triển của khoa học. Lý thuyết về sự phát sáng của phốt pho mà ông đã phác hoạ mãi đến những nǎm 20, 30 của thế kỷ 20 mới được hoàn chỉnh.\nÔng mất ngày 31-12-1691')");
